import{_ as u,o as p,c as t,e as a}from"./app.6bf529a6.js";const s={},o=a('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E94\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E94\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E94\u7AE0</h1><h2 id="\u5341\u7AE5\u5973\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u5341\u7AE5\u5973\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u5341\u7AE5\u5973\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u201C\u90A3\u65F6\uFF0C\u5929\u56FD\u597D\u6BD4\u5341\u4E2A\u7AE5\u5973\uFF0C\u62FF\u7740\u81EA\u5DF1\u7684\u706F\uFF0C\u51FA\u53BB\u8FCE\u63A5\u65B0\u90CE\u3002<sup>2</sup> \u5979\u4EEC\u4E2D\u4E94\u4E2A\u662F\u7CCA\u6D82\u7684\uFF0C\u4E94\u4E2A\u662F\u660E\u667A\u7684\u30023\u7CCA\u6D82\u7684\u62FF\u4E86\u706F\uFF0C\u5374\u6CA1\u6709\u968F\u8EAB\u5E26\u6CB9\uFF1B<sup>4</sup> \u800C\u660E\u667A\u7684\u62FF\u4E86\u706F\uFF0C\u5E76\u4E14\u5728\u58F8\u91CC\u5E26\u4E86\u6CB9\u3002<sup>5</sup> \u56E0\u4E3A\u65B0\u90CE\u8FDF\u5EF6\uFF0C\u5979\u4EEC\u90FD\u6253\u76F9\u7761\u7740\u4E86\u3002<sup>6</sup> \u534A\u591C\u6709\u4EBA\u558A\u8BF4\uFF1A\u65B0\u90CE\u6765\u4E86\uFF0C\u4F60\u4EEC\u51FA\u6765\u8FCE\u63A5\u5427\uFF01<sup>7</sup> \u90A3\u4E9B\u7AE5\u5973\u9042\u90FD\u8D77\u6765\uFF0C\u88C5\u5907\u5979\u4EEC\u7684\u706F\u3002<sup>8</sup> \u7CCA\u6D82\u7684\u5BF9\u660E\u667A\u7684\u8BF4\uFF1A\u628A\u4F60\u4EEC\u7684\u6CB9\uFF0C\u5206\u4E9B\u7ED9\u6211\u4EEC\u5427\uFF01\u56E0\u4E3A\u6211\u4EEC\u7684\u706F\u5FEB\u8981\u706D\u4E86\uFF01<sup>9</sup> \u660E\u667A\u7684\u7B54\u8BF4\uFF1A\u6015\u4E3A\u6211\u4EEC\u548C\u4F60\u4EEC\u90FD\u4E0D\u591F\uFF0C\u66F4\u597D\u4F60\u4EEC\u5230\u5356\u6CB9\u7684\u90A3\u91CC\u53BB\uFF0C\u4E3A\u81EA\u5DF1\u4E70\u5427\uFF01<sup>10</sup> \u5979\u4EEC\u53BB\u4E70\u7684\u65F6\u5019\uFF0C\u65B0\u90CE\u5230\u4E86\uFF1B\u90A3\u51C6\u5907\u597D\u4E86\u7684\uFF0C\u5C31\u540C\u4ED6\u8FDB\u53BB\uFF0C\u5171\u8D74\u5A5A\u5BB4\uFF1B\u95E8\u9042\u5173\u4E0A\u4E86\u3002<sup>11</sup> \u672B\u540E\uFF0C\u5176\u4F59\u7684\u7AE5\u5973\u4E5F\u6765\u4E86\uFF0C\u8BF4\uFF1A\u4E3B\u554A\uFF01\u4E3B\u554A\uFF01\u7ED9\u6211\u4EEC\u5F00\u95E8\u5427\uFF01<sup>12</sup> \u4ED6\u5374\u7B54\u8BF4\uFF1A\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u6211\u4E0D\u8BA4\u8BC6\u4F60\u4EEC\u3002<sup>13</sup> \u6240\u4EE5\uFF0C\u4F60\u4EEC\u8BE5\u9192\u5BE4\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u4E0D\u77E5\u9053\u90A3\u65E5\u5B50\uFF0C\u4E5F\u4E0D\u77E5\u9053\u90A3\u65F6\u8FB0\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u6BD4\u55BB\u6CA1\u6709\u65B0\u5A18, \u56E0\u4E3A\u7AE5\u5973\u8C61\u5F81\u7740\u6559\u4F1A, \u5C31\u662F\u57FA\u7763\u7684\u65B0\u5A18, \u800C\u57FA\u7763\u662F\u5979\u9ED8\u89C2\u4E2D\u7684\u7231\u60C5\u5BF9\u8C61. \u65E7\u7EA6\u91CC, \u9ED1\u591C\u662F\u5BA1\u5224\u7684\u8C61\u5F81, \u56E0\u4E3A\u65B0\uF92C\u4E0D\u5728. \u65B0\uF92C\u7684\u5149\u4E34, \u5C31\u662F\u767D\u663C\u964D\u6765\u7684\u9884\u62A5.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u7B2C\u4E8C\u4E2A\u6BD4\u55BB ( \u6B64\u672C\u5BFC\u8BFB, \u662F\u5C06\u4E0A\u4E00\u7AE0\u6700\u540E\u4E00\u4E2A\u6BB5\u843D\u7684\u6BD4\u55BB\u4E0E\u672C\u7AE0\u7684\u8BF8\u6BD4\u55BB\u4F5C\u4E3A\u4E00\u4E2A\u7CFB\u5217\u6765\u770B, \u800C\u4E8B\u5B9E\u4E0A\u6211\u4EEC\u4E5F\u786E\u5B9E\u53EF\u4EE5\u770B\u5230\u4E0A\u4E00\u7AE0\u4E3B\u9898\u4E0E\u672C\u7AE0\u4E3B\u9898\u7684\u8FDE\u7EED\u6027 ), \u662F\u529D\u8BEB\u4EBA\u4EEC\u4E3A\u4EBA\u5B50\u7684\u6765\u505A\u597D\u51C6\u5907. \u6BD4\u55BB\u4E2D, \u8036\u7A23\u88AB\u5851\u544A\u6210\u65B0\uF92C\u7684\u89D2\u8272 ( \u72B9\u5982: \u739B\u4E5D 15; \u5E76\u53C2\u9605: \u4F9D\u4E94\u56DB 5; \u8036\u4E09\u5341\u4E00 32; \u6B27\u4E8C 16, \u5176\u4E2D\u4E0A\u4E3B\u96C5\u5A01\u662F\u4EE5\u8272\u5217\u7684\u65B0\uF92C ). \u5728\u8036\u7A23\u65F6\u4EE3, \u5A5A\u793C\u5206\u4E3A\u4E24\u4E2A\u9636\u6BB5\u8FDB\u884C, \u9996\u5148\u662F\u5728\u65B0\u5A18\u7684\u7236\u5BB6\u4E3E\u884C\u8BA2\u5A5A\u4EEA\u5F0F, \u65B0\u5A18\u5411\u672B\u6765\u5CB3\u7236\u5448\u4E0A\u7ED3\u5A5A\u7684\u5A5A\u7EA6\u4EE5\u53CA\u8058\u793C; \u65B0\u5A18\u4F1A\u7EE7\u7EED\u5C45\u4F4F\u5728\u7236\u5BB6\u76F4\u5230\u7B2C\u4E8C\u968E\u6BB5, \u5373\u65B0\u5A18\u4E00\u5E74\u540E\u5AC1\u53BB\u592B\u5BB6. \u8FD9\u7B2C\u4E8C\u9636\u6BB5, \u5C31\u662F\u8FD9\u4E2A\u6BD4\u55BB\u63CF\u8FF0\u7684\u968E\u6BB5, \u5F53\u65B0\uF92C\u548C\u65B0\u5A18\u7684\u7236\u4EB2\u5728\u505A\u6700\u540E\u4EA4\u6D89\u65F6, \u7AE5\u5973\u4EEC\u5728\u7B49\u5F85\u65B0\uF92C\u7684\u5230\u6765\uFF0C\u4E00\u65E6\u6700\u540E\u534F\u5B9A\u8FBE\u6210\u5171\u8BC6, \u5A5A\u793C\u7684\u961F\u4F0D\u5C31\u51FA\u53D1\u53BB\u65B0\uF92C\u5BB6, \u5A5A\u5BB4\u5C31\u8981\u5F00\u59CB\u4E86.</p><p>\u8FD9\u91CC, \u6B63\u5728\u7B49\u5F85\u7684 &quot;\u7AE5\u5973&quot; ( \u8BD1\u81EA\u5E0C\u814A\u5B57 Parthenos ) \u90FD\u662F\u65B0\uF92C\u7684\u670B\u53CB; \u65B0\u5A18\u5728\u6545\u4E8B\u4E2D\u4ECE\u672A\u88AB\u63D0\u53CA. Parthenos \u8FD9\u4E2A\u5E0C\u814A\u5B57, \u662F\u6307\u4E00\u4F4D\u9002\u5A5A\u5E74\u9F84 ( \u5341\u4E8C\u5C81\u5DE6\u53F3 ) \u7684 &quot;\u7AE5\u8D1E\u5973\u5B50&quot;. \u660E\u667A\u548C\u7CCA\u6D82\u7AE5\u5973\u7684\u5BF9\u6BD4, \u4EE4\u4EBA\u60F3\u8D77\u90A3\u4F4D &quot;\u5EFA\u9020\u623F\u5C4B\u7684\u4EBA&quot; ( \u739B\u4E03 24 ~ 27 ). \u7AE5\u5973\u4EEC\u5E26\u7684\u7A76\u7ADF\u662F &quot;\u6D78\u6CE1\u7740\u6CB9\u5E03\u6240\u5305\u88F9\u7740\u7684\u706B\u70AC&quot; ( \u8FD9\u901A\u5E38\u662F\u6307\u5F53\u65F6\u7684 &quot;\u706F\u5177&quot; ), \u8FD8\u662F &quot;\u5E26\u6709\u628A\u67C4\u7684\u706F\u82AF\u6CB9\u706F&quot;, \u8FD9\u91CC\u5E76\u6CA1\u6709\u8868\u8FBE\u5F97\u5F88\u6E05\u695A.</p><p>\u739B\u4E94 14 ~ 16 \u63D0\u4F9B\u4E86\u4E00\u4E2A\u7EBF\u7D22, \u53EF\u4EE5\u8BF4\u660E\u4E3A\u4EC0\u4E48\u7AE5\u5973\u4E0D\u80FD\u5206\u4EAB\u5979\u4EEC\u7684\u6CB9: \u706F\u5149\u7B49\u540C\u4E8E\u4ED6\u4EBA\u770B\u5F97\u89C1\u7684\u597D\u884C\u4E3A, \u5E76\u5F15\u9886\u4EBA\u8D5E\u7F8E\u79F0\u9882\u5929\u4E3B. \u540C\u6837, \u739B\u4E03 24 ~ 27 \u4E5F\u6307\u51FA: \u806A\u660E\u4EBA\u662F\u90A3\u4E9B\u542C\u4E86\u8036\u7A23\u7684\u8BDD\u800C\u5B9E\u884C\u7684\u4EBA. \u6B63\u5982\u6BD4\u55BB\u4E2D\u660E\u667A\u7684\u7AE5\u5973, \u5979\u4EEC\u5C31\u662F\u90A3\u4E9B\u5FE0\u5B9E\u5730\u4E3A\u672B\u4E16\u505A\u597D\u51C6\u5907\u7684\u4EBA. \u6CA1\u6709\u4EBA\u53EF\u66FF\u4ED6\u4EBA\u505A\u51C6\u5907. \u672B\u4E16\u5230\u6765\u65F6 ( eschatological moment ), \u4EBA\u53EA\u6709 &quot;\u51C6\u5907\u597D\u4E86&quot;, \u8FD8\u662F &quot;\u6CA1\u6709\u51C6\u5907\u597D&quot; \u4E24\u79CD\u533A\u522B\u800C\u5DF1.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u56DB\u4F4D\u5723\u53F2\u4E2D, \u552F\u6709\u739B\u7AA6\u8BB0\u8F7D\u4E86\u8FD9\u4E2A\u6BD4\u55BB, \u6307\u7684\u662F\u8036\u7A23\u7684\u518D\u5EA6\u6765\u4E34. \u6240\u63CF\u8FF0\u7684\u662F\u4EBA\u4EEC\u671F\u5F85\u590D\u6D3B, \u5E76\u7B49\u5019\u57FA\u7763\u518D\u6B21\u6765\u4E34\u7684\u60C5\u51B5. \u5929\u56FD\u4E0D\u662F\u88AB\u6BD4\u55BB\u4E3A\u5341\u4E2A\u4F34\u5A18, \u800C\u662F\u88AB\u6BD4\u55BB\u4E3A\u90A3\u4E2A\u76DB\u5927\u7684\u5A5A\u5BB4. \u8FD9\u4E2A\u6BD4\u55BB\u7684\u4E2D\u5FC3\u8BAF\u606F\u662F\u8BA9\u4EBA\u5145\u5206\u51C6\u5907\u597D\u81EA\u5DF1.</p><p>\u6BD4\u55BB\u63D0\u5230\u4E24\u4E2A\u4E8B\u5B9E, \u5373\u65B0\u90CE\u8FDF\u8FDF\u672A\u5230, \u548C\u7B49\u5019\u8005\u7761\u7740\u4E86 (\u4E0D\u8B66\u9192). \u90A3\u4E9B\u611A\u8822\u4F34\u5A18\u7684\u611A\u8822\u4E4B\u5904, \u4E0D\u5728\u4E8E\u7761\u7740\u4E86 (\u56E0\u4E3A\u5979\u4EEC\u90FD\u6253\u5428\u513F\u7761\u7740\u4E86). \u800C\u5728\u4E8E\u5979\u4EEC\u6CA1\u6709\u51C6\u5907\u597D\u968F\u65F6\u6267\u884C\u4F7F\u547D. \u5979\u4EEC\u6CA1\u6709\u8003\u8651\u5230\u65B0\u90CE\u53EF\u80FD\u4F1A\u8FDF\u5230, \u56E0\u6B64\u4E5F\u6CA1\u6709\u51C6\u5907\u8DB3\u591F\u7684\u706F\u6CB9. \u90A3\u4E9B\u806A\u660E\u7684\u4F34\u5A18\u62D2\u7EDD\u4E0E\u5979\u4EEC\u5206\u4EAB\u706F\u6CB9, \u8FD9\u662F\u4E00\u79CD\u6BD4\u55BB\u624B\u6CD5, \u544A\u8BC9\u6211\u4EEC, \u8FD9\u79CD\u5FC5\u987B\u7684\u51C6\u5907\u662F\u4E2A\u4EBA\u6027\u7684, \u4E0D\u53EF\u4EE5\u8F6C\u501F. \u540C\u6837, \u53EA\u4F9D\u8D56\u4ED6\u4EBA\u7684\u5FE0\u4FE1\u800C\u81EA\u5DF1\u4E0D\u52AA\u529B, \u662F\u6BEB\u65E0\u7528\u9014\u7684.</p><p>\u65B0\u90CE\u7684\u53CD\u5E94\u8BF4\u660E\u8FD9\u4E9B\u611A\u8822\u7684\u4F34\u5A18\u8C61\u5F81\u4FE1\u5F92\u56E2\u4F53\u4E2D\u7684\u5F92\u6709\u865A\u540D\u3001\u6CA1\u6709\u5168\u5FC3\u5168\u529B\u6295\u5165\u7684\u4EBA, \u6700\u7EC8\u4E0D\u4F1A\u51C6\u5907\u597D\u81EA\u5DF1. &quot;\u6240\u4EE5, \u4F60\u4EEC\u8981\u8B66\u9192&quot; ( 13 ). \u8FD9\u53E5\u8BDD\u662F\u5728\u8B66\u544A\u6211\u4EEC, \u8981\u65F6\u5E38\u4FDD\u6301\u5FE0\u4FE1, \u5E76\u5168\u5FC3\u5168\u529B\u5C06\u8036\u7A23\u7684\u6559\u5BFC\u4ED8\u8BF8\u5B9E\u884C.</p><h2 id="\u5854\u51B7\u901A\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u5854\u51B7\u901A\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u5854\u51B7\u901A\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>14</sup> \u201C\u5929\u56FD\u53C8\u5982\u4E00\u4E2A\u8981\u8FDC\u884C\u7684\u4EBA\uFF0C\u5C06\u81EA\u5DF1\u7684\u4EC6\u4EBA\u53EB\u6765\uFF0C\u628A\u8D22\u4EA7\u6258\u4ED8\u7ED9\u4ED6\u4EEC\uFF1A<sup>15</sup> \u6309\u7167\u4ED6\u4EEC\u7684\u624D\u80FD\uFF0C\u4E00\u4E2A\u7ED9\u4E86\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\uFF0C\u4E00\u4E2A\u7ED9\u4E86\u4E24\u4E2A\uFF0C\u4E00\u4E2A\u7ED9\u4E86\u4E00\u4E2A\uFF1B\u7136\u540E\u52A8\u8EAB\u8D70\u4E86\u3002<sup>16</sup> \u90A3\u9886\u4E86\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\u7684\uFF0C\u7ACB\u523B\u53BB\u7528\u6765\u8425\u4E1A\uFF0C\u53E6\u5916\u8D5A\u4E86\u4E94\u4E2A\u3002<sup>17</sup> \u540C\u6837\uFF0C\u90A3\u9886\u4E86\u4E24\u4E2A\u7684\uFF0C\u4E5F\u8D5A\u4E86\u53E6\u5916\u4E24\u4E2A\u3002<sup>18</sup> \u4F46\u662F\uFF0C\u90A3\u9886\u4E86\u4E00\u4E2A\u7684\uFF0C\u5374\u53BB\u6398\u5F00\u5730\uFF0C\u628A\u4E3B\u4EBA\u7684\u94F6\u5B50\u85CF\u4E86\u3002<sup>19</sup> \u8FC7\u4E86\u591A\u65F6\uFF0C\u4EC6\u4EBA\u7684\u4E3B\u4EBA\u56DE\u6765\u4E86\uFF0C\u4FBF\u4E0E\u4ED6\u4EEC\u7B97\u8D26\u3002<sup>20</sup> \u90A3\u9886\u4E86\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\u7684\u4E0A\u524D\u6765\uFF0C\u5448\u4E0A\u53E6\u5916\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\u8BF4\uFF1A\u4E3B\u554A\uFF01\u4F60\u66FE\u4EA4\u7ED9\u6211\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\uFF0C\u770B\uFF0C\u6211\u8D5A\u4E86\u53E6\u5916\u4E94\u4E2A\u2018\u5854\u51B7\u901A\u2019\u3002<sup>21</sup> \u4E3B\u4EBA\u5BF9\u4ED6\u8BF4\uFF1A\u597D\uFF01\u5584\u826F\u5FE0\u4FE1\u7684\u4EC6\u4EBA\uFF0C\u4F60\u65E2\u5728\u5C11\u8BB8\u4E8B\u4E0A\u5FE0\u4FE1\uFF0C\u6211\u5FC5\u59D4\u6D3E\u4F60\u7BA1\u7406\u8BB8\u591A\u5927\u4E8B\uFF1A\u8FDB\u5165\u4F60\u4E3B\u4EBA\u7684\u798F\u4E50\u5427\uFF01<sup>22</sup> \u90A3\u9886\u4E86\u4E24\u4E2A\u2018\u5854\u51B7\u901A\u2019\u7684\u4E5F\u524D\u6765\u8BF4\uFF1A\u4E3B\u554A\uFF01\u4F60\u66FE\u4EA4\u7ED9\u6211\u4E24\u4E2A\u2018\u5854\u51B7\u901A\u2019\uFF0C\u770B\uFF0C\u6211\u8D5A\u4E86\u53E6\u5916\u4E24\u4E2A\u2018\u5854\u51B7\u901A\u2019\u3002<sup>23</sup> \u4E3B\u4EBA\u5BF9\u4ED6\u8BF4\uFF1A\u597D\uFF01\u5584\u826F\u5FE0\u4FE1\u7684\u4EC6\u4EBA\uFF01\u4F60\u65E2\u5728\u5C11\u8BB8\u4E8B\u4E0A\u5FE0\u4FE1\uFF0C\u6211\u5FC5\u59D4\u6D3E\u4F60\u7BA1\u7406\u8BB8\u591A\u5927\u4E8B\uFF1A\u8FDB\u5165\u4F60\u4E3B\u4EBA\u7684\u798F\u4E50\u5427\uFF01<sup>24</sup> \u968F\u540E\uFF0C\u90A3\u9886\u4E86\u4E00\u4E2A\u2018\u5854\u51B7\u901A\u2019\u7684\u4E5F\u524D\u6765\u8BF4\uFF1A\u4E3B\u554A\uFF01\u6211\u539F\u77E5\u9053\u4F60\u662F\u4E2A\u523B\u8584\u7684\u4EBA\uFF0C\u5728\u4F60\u6CA1\u6709\u4E0B\u79CD\u7684\u5730\u65B9\u6536\u5272\uFF0C\u5728\u4F60\u6CA1\u6709\u6563\u5E03\u7684\u5730\u65B9\u805A\u655B\u3002<sup>25</sup> \u56E0\u4E3A\u6211\u5BB3\u6015\uFF0C\u6240\u4EE5\u6211\u53BB\u628A\u4F60\u7684\u2018\u5854\u51B7\u901A\u2019\u85CF\u5728\u5730\u4E0B\uFF1B\u770B\uFF0C\u4F60\u7684\u4ECD\u8FD8\u7ED9\u4F60\u3002<sup>26</sup> \u4E3B\u4EBA\u56DE\u7B54\u8BF4\uFF1A\u53EF\u6076\u61D2\u60F0\u7684\u4EC6\u4EBA\uFF01\u4F60\u65E2\u77E5\u9053\uFF1A\u6211\u5728\u6CA1\u6709\u4E0B\u79CD\u7684\u5730\u65B9\u6536\u5272\uFF0C\u5728\u6CA1\u6709\u6563\u5E03\u7684\u5730\u65B9\u805A\u655B\uFF1B<sup>27</sup> \u90A3\u4E48\uFF0C\u4F60\u5C31\u8BE5\u628A\u6211\u7684\u94F6\u5B50\uFF0C\u4EA4\u7ED9\u94B1\u5E84\u91CC\u7684\u4EBA\uFF0C\u5F85\u6211\u56DE\u6765\u65F6\uFF0C\u628A\u6211\u8FDE\u672C\u5E26\u5229\u53D6\u56DE\u3002<sup>28</sup> \u6240\u4EE5\uFF0C\u4F60\u4EEC\u628A\u8FD9\u4E2A\u2018\u5854\u51B7\u901A\u2019\u4ECE\u4ED6\u624B\u4E2D\u593A\u8FC7\u6765\uFF0C\u7ED9\u90A3\u6709\u4E86\u5341\u4E2A\u2018\u5854\u51B7\u901A\u2019\u7684\uFF0C<sup>29</sup> \u56E0\u4E3A\u51E1\u662F\u6709\u7684\uFF0C\u8FD8\u8981\u7ED9\u4ED6\uFF0C\u53EB\u4ED6\u5BCC\u88D5\uFF1B\u90A3\u6CA1\u6709\u7684\uFF0C\u8FDE\u4ED6\u6240\u6709\u7684\uFF0C\u4E5F\u8981\u7531\u4ED6\u624B\u4E2D\u593A\u53BB\u3002<sup>30</sup> \u81F3\u4E8E\u8FD9\u65E0\u7528\u7684\u4EC6\u4EBA\uFF0C\u4F60\u4EEC\u628A\u4ED6\u4E22\u5728\u5916\u9762\u7684\u9ED1\u6697\u4E2D\uFF0C\u5728\u90A3\u91CC\u5FC5\u6709\u54C0\u53F7\u548C\u5207\u9F7F\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u73B0\u5728, \u57FA\u7763\u4FE1\u53CB\u4EEC\u8981\u5F97\u5230\u4EE5\u4E0B\u7684\u6559\u8BAD. \u6CE8\u610F: \u8D22\u4EA7\u662F\u6309\u6BCF\u4E2A\u4EBA\u7684\u624D\u80FD\u8D4B\u7ED9\u7684 ( &quot;\u5854\u51B7\u901A&quot; \u539F\u662F\u94B1\u5E01\u7684\u5355\u4F4D, \u56E0\u4E3A\u8FD9\u4E2A\u6BD4\u55BB, \u5B83 ( Talent ) \u5F00\u59CB\u6307 &quot;\u5929\u624D&quot; ) \u800C\u4ED6\u4EEC\u7684\u8D4F\u62A5\u4E0D\u662F\u8D22\u5BCC, \u800C\u662F\u66F4\u5927\u7684\u804C\u8D23. \u5927\u91CF\u7684\u91D1\u94B1\u88AB\u79F0\u4E3A &quot;\u5C11\u8BB8\u4E8B&quot;, \u4E0E\u5929\u4E0A\u7684 &quot;\u8BB8\u591A\u5927\u4E8B&quot; \u76F8\u5BF9, \u56E0\u4E3A\u8FD9\u4E24\u4EF6\u4E8B\u53EF\u80FD\u8C61\u5F81\u8FD9\u4E16\u754C\u7684\u4E00\u5207\u8D22\u7269\u662F\u4E2A\u4FE1\u6258\u57FA\u91D1, \u800C\u5BCC\u4EBA\u5FC5\u987B\u4E3A\u57FA\u7763\u6765\u7BA1\u7406, \u5206\u914D\u5B83\u4EEC.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u6BD4\u55BB\u901A\u5E38\u88AB\u8BA4\u4E3A\u662F\u529D\u514D\u4EBA\u5145\u5206\u4F7F\u7528\u5929\u4E3B\u8D50\u7ED9\u7684\u56E0\u5BA0. \u7136\u800C, talanton ( \u5854\u51B7\u901A ) \u8FD9\u4E2A\u5E0C\u814A\u5B57, \u9664\u4E86\u662F\u8D27\u5E01\u5355\u4F4D\u6216\u91CD\u91CF\u6D4B\u5B9A\u5916, \u5E76\u65E0\u5176\u5B83\u6DB5\u4E49. \u8FD9\u4E2A\u6BD4\u55BB\u4E2D, &quot;\u5854\u51B7\u901A&quot; \u662F\u8868\u793A\u4E00\u5927\u7B14\u7EBF. \u8FD9\u4E2A\u6BD4\u55BB\u6240\u63CF\u8FF0\u7684\u662F: \u4E24\u4E2A\u4EC6\u4EBA\u628A\u4E3B\u4EBA\u59D4\u6258\u7ED9\u4ED6\u4EEC\u7684\u91D1\u94B1, \u7528\u4E8E\u6295\u8D44\u5E76\u83B7\u5F97\u53CC\u500D\u7684\u5229\u6DA6, \u4F7F\u4ED6\u4EEC\u8D62\u5F97\u4E86\u4E3B\u4EBA\u5BF9\u4ED6\u4EEC\u7684\u8BA4\u53EF, \u4E5F\u5206\u4EAB\u4E86\u4E3B\u4EBA\u7684\u559C\u60A6, \u5E76\u88AB\u4E3B\u4EBA\u8D4B\u4E88\u66F4\u5927\u7684\u8D23\u4EFB; \u7136\u800C, \u7B2C\u4E09\u4E2A\u4EC6\u4EBA\u628A\u91D1\u94B1\u57CB\u5728\u5730\u4E0B, \u8FD9\u5728\u53E4\u4EE3\u88AB\u8BA4\u4E3A\u662F\u4FDD\u5B58\u8D35\u91CD\u7269\u54C1\u7684\u6700\u4F73\u65B9\u5F0F, \u4F46\u4ED6\u5374\u5F97\u5230\u4E3B\u4EBA\u82DB\u523B\u7684\u5904\u7F5A.</p><p>\u8036\u7A23\u5E76\u975E\u751F\u6D3B\u5728\u4E00\u4E2A\u8BA4\u4E3A &quot;\u6295\u8D44\u53EF\u589E\u52A0\u8D22\u5BCC&quot; \u7684\u8D44\u672C\u4E3B\u4E49\u793E\u4F1A\u4E2D, \u8FD9\u662F\u7406\u89E3\u8FD9\u4E2A\u6BD4\u55BB\u7684\u5173\u952E. \u5F53\u65F6\u4EBA\u4EEC\u6000\u7740\u7684, \u662F\u6709\u9650\u8D44\u6E90\u7684\u60F3\u6CD5: \u8D22\u5BCC\u662F\u6709\u9650\u7684, \u589E\u52A0\u4E00\u4E2A\u8D22\u5BCC, \u4FBF\u662F\u53E6\u4E00\u4E2A\u4EBA\u7684\u635F\u5931. \u519C\u592B\u7684\u751F\u6D3B\u76EE\u6807, \u662F\u8DB3\u4EE5\u517B\u6D3B\u5BB6\u5EAD. \u4EFB\u4F55\u4EBA\u4E3A\u81EA\u5DF1\u96C6\u805A\u5927\u91CF\u8D22\u5BCC, \u4F1A\u88AB\u89C6\u4E3A\u8D2A\u5A6A\u548C\u90AA\u6076. \u6240\u4EE5, \u5047\u5982\u8FD9\u4E2A\u6BD4\u55BB\u91CC\u7684\u4E3B\u4EBA\u6307\u7684\u4E0D\u662F\u5929\u4E3B\u7684\u8BDD, \u90A3\u4E48, \u7B2C\u4E09\u4E2A\u4EC6\u4EBA\u5C31\u662F\u6700\u503C\u5F97\u5C0A\u656C\u7684\u7684\u4E00\u4F4D\u4E86, \u56E0\u4E3A\u53EA\u6709\u4ED6\u62D2\u7EDD\u4E0E &quot;\u8D2A\u5A6A\u65E0\u6B62\u5883\u7684\u4E3B\u4EBA&quot; \u5408\u4F5C. \u8FD9\u91CC\u662F\u5728\u8B66\u544A\u6709\u94B1\u4EBA\u505C\u6B62\u5265\u524A\u7A77\u4EBA, \u5E76\u9F13\u52B1\u7A77\u4EBA\u8981\u52C7\u6562\u5730\u91C7\u53D6\u884C\u52A8\u63ED\u9732\u8D2A\u5A6A\u7684\u7F6A\u5B7D.</p><p>\u6700\u540E\u4E00\u8282\u76F8\u5F53\u4E25\u8083, \u662F\u5728\u63CF\u8FF0 &quot;\u53CD\u6297\u5BCC\u4EBA\u548C\u5F3A\u6743\u7684\u4EBA&quot;, \u5C06\u4F1A\u53D1\u751F\u7684\u60B2\u60E8\u540E\u91CC: &quot;\u628A\u4ED6\u4E22\u5728\u5916\u9762\u7684\u9ED1\u6697\u4E2D, \u5728\u90A3\u91CC\u5FC5\u6709\u54C0\u53F7\u548C\u5207\u9F7F&quot;. \u8FD9\u4E5F\u5728\u9F13\u52B1\u95E8\u5F92: \u5F53\u4ED6\u4EEC\u9762\u5BF9\u4E0D\u516C\u5E73\u7684\u5236\u5EA6\u65F6, \u8981\u56E2\u7ED3\u4E00\u81F4, \u5171\u540C\u627E\u5BFB\u62B5\u5236\u7684\u65B9\u6CD5. \u673A\u4F1A\u603B\u662F\u6709\u7684, \u56E0\u4E3A\u672B\u65E5\u5C1A\u672A\u5230\u6765.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u6BD4\u55BB\u4E5F\u529D\u5BFC\u4EBA, \u5728\u5929\u4E3B\u5BA1\u5224\u7684\u65F6\u523B\u5230\u6765\u4E4B\u524D, \u5E94\u8BE5\u4FDD\u6301\u5FE0\u4FE1\u548C\u8B66\u9192.</p><p>\u4ECE\u4E0A\u4E0B\u6587\u4E2D\u6211\u4EEC\u53EF\u4EE5\u770B\u51FA, \u8FD9\u4E2A\u6BD4\u55BB\u7684\u91CD\u70B9\u662F\u4EC6\u4EBA\u4EEC\u4EA4\u8D26\u7684\u60C5\u5F62, \u5C24\u5176\u805A\u7126\u4E8E\u90A3\u4E2A\u8FC7\u5206\u8C28\u614E\u7684\u4EC6\u4EBA\u8EAB\u4E0A. \u8036\u7A23\u65A5\u8D23\u90A3\u4E9B\u6447\u6446\u4E0D\u5B9A\u7684\u4EBA, \u4ED6\u4EEC\u63A5\u53D7\u4E86\u5929\u56FD\u7684\u8BAF\u606F, \u540E\u6765\u5374\u8EB2\u5728\u7A7A\u6D1E\u7684\u5B89\u5168\u611F\u4E2D, \u6CA1\u6709\u7ED3\u51FA\u679C\u5B9E.</p><p>\u8036\u7A23\u7684\u95E8\u5F92\u5FC5\u987B\u8981\u4F7F\u5929\u56FD\u7684\u8D44\u4EA7\u5728\u6307\u5B9A\u7684\u65F6\u671F\u5185\u7ED3\u51FA\u679C\u5B9E. \u4E3A\u739B\u7AA6\u800C\u8A00, \u8FD9\u662F\u6307 &quot;\u6559\u4F1A\u7684\u65F6\u671F&quot;. \u90A3\u4E9B\u6CA1\u6709\u7ED3\u51FA\u679C\u5B9E\u7684\u4EBA, \u5373\u4F7F\u4ED6\u4EEC\u80FD\u5C06\u8FD9\u4E9B\u8D44\u4EA7\u4FDD\u5B58\u5F97\u5B8C\u597D\u65E0\u635F, \u4F46\u5728\u6700\u540E\u5BA1\u5224\u65F6\u4ECD\u5C06\u5168\u90E8\u5931\u53BB. \u540C\u6837, \u6559\u4F1A\u7684\u56E2\u4F53\u4E5F\u5FC5\u987B\u8981\u8B66\u60D5\u3001\u5C0F\u5FC3, \u4EE5\u514D\u5C06\u81EA\u5DF1\u7684\u4F7F\u547D\u6CA6\u4E3A\u4E00\u79CD\u6577\u884D\u585E\u8D23\u7684\u81EA\u6EE1\u81EA\u8DB3.</p><h2 id="\u516C\u5BA1\u5224" tabindex="-1"><a class="header-anchor" href="#\u516C\u5BA1\u5224" aria-hidden="true">#</a> \u516C\u5BA1\u5224</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>31</sup> \u201C\u5F53\u4EBA\u5B50\u5728\u81EA\u5DF1\u7684\u5149\u8363\u4E2D\uFF0C\u4E0E\u4F17\u5929\u4F7F\u4E00\u540C\u964D\u6765\u65F6\uFF0C\u90A3\u65F6\uFF0C\u4ED6\u8981\u5750\u5728\u5149\u8363\u7684\u5B9D\u5EA7\u4E0A\uFF0C<sup>32</sup> \u4E00\u5207\u7684\u6C11\u65CF\uFF0C\u90FD\u8981\u805A\u5728\u4ED6\u9762\u524D\uFF1B\u4ED6\u8981\u628A\u4ED6\u4EEC\u5F7C\u6B64\u5206\u5F00\uFF0C\u5982\u540C\u7267\u4EBA\u5206\u5F00\u7EF5\u7F8A\u548C\u5C71\u7F8A\u4E00\u6837\uFF1A<sup>33</sup> \u628A\u7EF5\u7F8A\u653E\u5728\u81EA\u5DF1\u7684\u53F3\u8FB9\uFF0C\u5C71\u7F8A\u5728\u5DE6\u8FB9\u3002<sup>34</sup> \u90A3\u65F6\uFF0C\u541B\u738B\u8981\u5BF9\u90A3\u4E9B\u5728\u4ED6\u53F3\u8FB9\u7684\u8BF4\uFF1A\u6211\u7236\u6240\u795D\u798F\u7684\uFF0C\u4F60\u4EEC\u6765\u5427\uFF01\u627F\u53D7\u81EA\u521B\u4E16\u4EE5\u6765\uFF0C\u7ED9\u4F60\u4EEC\u9884\u5907\u4E86\u7684\u56FD\u5EA6\u5427\uFF01<sup>35</sup> \u56E0\u4E3A\u6211\u997F\u4E86\uFF0C\u4F60\u4EEC\u7ED9\u4E86\u6211\u5403\u7684\uFF1B\u6211\u6E34\u4E86\uFF0C\u4F60\u4EEC\u7ED9\u4E86\u6211\u559D\u7684\uFF1B\u6211\u4F5C\u5BA2\uFF0C\u4F60\u4EEC\u6536\u7559\u4E86\u6211\uFF1A<sup>36</sup> \u6211\u8D64\u8EAB\u9732\u4F53\uFF0C\u4F60\u4EEC\u7ED9\u4E86\u6211\u7A7F\u7684\uFF1B\u6211\u60A3\u75C5\uFF0C\u4F60\u4EEC\u770B\u987E\u4E86\u6211\uFF1B\u6211\u5728\u76D1\u91CC\uFF1B\u4F60\u4EEC\u6765\u63A2\u671B\u4E86\u6211\u3002<sup>37</sup> \u90A3\u65F6\uFF0C\u4E49\u4EBA\u56DE\u7B54\u4ED6\u8BF4\uFF1A\u4E3B\u554A\uFF01\u6211\u4EEC\u4EC0\u4E48\u65F6\u5019\u89C1\u4E86\u4F60\u9965\u997F\u800C\u4F9B\u517B\u4E86\u4F60\uFF0C\u6216\u53E3\u6E34\u800C\u7ED9\u4E86\u4F60\u559D\u7684\uFF1F<sup>38</sup> \u6211\u4EEC\u4EC0\u4E48\u65F6\u5019\u89C1\u4E86\u4F60\u4F5C\u5BA2\uFF0C\u800C\u6536\u7559\u4E86\u4F60\uFF0C\u6216\u8D64\u8EAB\u9732\u4F53\u800C\u7ED9\u4E86\u4F60\u7A7F\u7684\uFF1F<sup>39</sup> \u6211\u4EEC\u4EC0\u4E48\u65F6\u5019\u89C1\u4F60\u60A3\u75C5\uFF0C\u6216\u5728\u76D1\u91CC\u800C\u6765\u63A2\u671B\u8FC7\u4F60\uFF1F<sup>40</sup> \u541B\u738B\u4FBF\u56DE\u7B54\u4ED6\u4EEC\u8BF4\uFF1A\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u51E1\u4F60\u4EEC\u5BF9\u6211\u8FD9\u4E9B\u6700\u5C0F\u5144\u5F1F\u4E2D\u7684\u4E00\u4E2A\u6240\u505A\u7684\uFF0C\u5C31\u662F\u5BF9\u6211\u505A\u7684\u3002<sup>41</sup> \u7136\u540E\u4ED6\u53C8\u5BF9\u90A3\u4E9B\u5728\u5DE6\u8FB9\u7684\u8BF4\uFF1A\u53EF\u5492\u9A82\u7684\uFF0C\u79BB\u5F00\u6211\uFF0C\u5230\u90A3\u7ED9\u9B54\u9B3C\u548C\u4ED6\u7684\u4F7F\u8005\u9884\u5907\u7684\u6C38\u706B\u91CC\u53BB\u5427\uFF01<sup>42</sup> \u56E0\u4E3A\u6211\u997F\u4E86\uFF0C\u4F60\u4EEC\u6CA1\u6709\u7ED9\u6211\u5403\u7684\uFF1B\u6211\u6E34\u4E86\uFF0C\u4F60\u4EEC\u6CA1\u6709\u7ED9\u6211\u559D\u7684\uFF1B<sup>43</sup> \u6211\u4F5C\u5BA2\uFF0C\u4F60\u4EEC\u6CA1\u6709\u6536\u7559\u6211\uFF1B\u6211\u8D64\u8EAB\u9732\u4F53\uFF0C\u4F60\u4EEC\u6CA1\u6709\u7ED9\u6211\u7A7F\u7684\uFF1B\u6211\u60A3\u75C5\u6216\u5728\u76D1\u91CC\uFF0C\u4F60\u4EEC\u6CA1\u6709\u6765\u63A2\u671B\u6211\u3002<sup>44</sup> \u90A3\u65F6\uFF0C\u4ED6\u4EEC\u4E5F\u8981\u56DE\u7B54\u8BF4\uFF1A\u4E3B\u554A\uFF01\u6211\u51E0\u65F6\u89C1\u4E86\u4F60\u9965\u997F\uFF0C\u6216\u53E3\u6E34\uFF0C\u6216\u4F5C\u5BA2\uFF0C\u6216\u8D64\u8EAB\u9732\u4F53\uFF0C\u6216\u6709\u75C5\uFF0C\u6216\u5750\u76D1\uFF0C\u800C\u6211\u4EEC\u6CA1\u6709\u7ED9\u4F60\u6548\u52B3\uFF1F<sup>45</sup> \u90A3\u65F6\uFF0C\u541B\u738B\u56DE\u7B54\u4ED6\u4EEC\u8BF4\uFF1A\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u51E1\u4F60\u4EEC\u6CA1\u6709\u7ED9\u8FD9\u4E9B\u6700\u5C0F\u4E2D\u7684\u4E00\u4E2A\u505A\u7684\uFF0C\u4FBF\u662F\u6CA1\u6709\u7ED9\u6211\u505A\u3002<sup>46</sup> \u8FD9\u4E9B\u4EBA\u8981\u8FDB\u5165\u6C38\u7F5A\uFF0C\u800C\u90A3\u4E9B\u4E49\u4EBA\u5374\u8981\u8FDB\u5165\u6C38\u751F\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u5229\u7528\u65E7\u7EA6\u7684\u610F\u50CF\u6765\u63CF\u7ED8\u6700\u540E\u5BA1\u5224, \u5E76\u5728\u5176\u5185\u5F3A\u8C03\u57FA\u7763\u7684\u5929\u4E3B\u6027. \u8036\u7A23\u73B0\u5728\u6210\u4E3A &quot;\u541B\u738B&quot;, \u8FD9\u4E2A\u79F0\u53F7\u4EE5\u524D\u53EA\u52A0\u5929\u4E3B\u7236\u7684 ( \u4E94 34 ~ 35 ). \u8FD9\u6B21\u5BA1\u7684\u5BF9\u8C61\u662F\u4E00\u5207\u6076\u4EBA\u4E0E\u4E49\u4EBA, \u5FC5\u987B\u4EE5\u5168\u4EBA\u7C7B\u7684\u590D\u6D3B\u4E3A\u5148\u51B3\u6761\u4EF6. \u8036\u7A23, \u4E0A\u4E3B\u7684\u4EC6\u4EBA, \u8BF4\u4ED6\u81EA\u5DF1\u5C31\u662F\u7A77\u4EBA\u548C\u53D7\u82E6\u7684\u4EBA ( \u7531\u6B64\u53EF\u89C1, \u4FDD\u7984\u6240\u8BF4 &quot;\u57FA\u7763\u5965\u4F53&quot; \u7684\u9053\u7406, \u662F\u4ECE\u8036\u7A23\u81EA\u5DF1\u7684\u6559\u8BAD\u53D1\u5C55\u800C\u6765\u7684 ). \u5144\u5F1F\u95F4\u7684\u53CB\u7231\u662F\u6700\u540E\u5BA1\u5224\u7684\u51C6\u7EF3.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u739B\u7AA6\u5723\u53F2\u8BB0\u8FF0\u7684\u6700\u540E\u4E00\u4E2A\u6BD4\u55BB, \u4E5F\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u72EC\u6709\u7684. \u5F53\u4EBA\u5B50\u5149\u8363\u5730\u6765\u4E34\u65F6, \u672B\u4E16\u5BA1\u5224\u65E9\u5DF2\u6765\u4E86 ( 31 \u8282 ). \u8FD9\u4E2A\u60C5\u666F, \u4E0E\u8036\u7A23\u547D\u4EE4\u8FFD\u968F\u8005\u53BB &quot;\u4F7F\u4E07\u6C11\u6210\u4E3A\u7942\u7684\u95E8\u5F92&quot; ( \u739B\u4E8C\u5341\u516B 16 ~ 20 ) \u6709\u5BC6\u5207\u7684\u8054\u7CFB, \u8FD9\u4E2A\u6BD4\u55BB\u5047\u5B9A\u6BD4\u547D\u4EE4\u5DF2\u88AB\u5C65\u884C. \u90A3\u91CC\u7684 &quot;\u4E07\u6C11&quot; ( \u4E8C\u5341\u516B 19 ) \u662F\u5E0C\u814A\u6587 panta ta ethne \u7684\u4E2D\u8BD1\u8BCD, \u610F\u601D\u5C31\u662F\u672C\u6BD4\u55BB\u4E2D\u7684 &quot;\u4E00\u5207\u6C11\u65CF&quot; ( 32 \u8282 ). \u73B0\u5728 &quot;\u4E00\u5207\u6C11\u65CF\u90FD\u88AB\u805A\u96C6\u8D77\u6765&quot;, \u5411\u5929\u4E3B\u4EA4\u8D26.</p><p>\u4E3A\u4EC0\u4E48\u8981\u628A &quot;\u7EF5\u7F8A&quot; \u4E0E &quot;\u5C71\u7F8A&quot; \u5206\u5F00\u6765? \u539F\u6765\u4E0D\u6E05\u695A. \u5176\u5B9E, &quot;\u7EF5\u7F8A&quot; \u4E0E &quot;\u5C71\u7F8A&quot; \u90FD\u6781\u4E3A\u73CD\u8D35. \u6CA1\u6709\u8BC1\u636E\u663E\u793A: \u767D\u5929\u65F6, \u8BA9\u7EF5\u7F8A\u4E0E\u5C71\u7F8A\u4E00\u8D77\u5403\u8349, \u5230\u665A\u4E0A, \u7267\u7F8A\u4EBA\u5374\u8981\u628A\u4ED6\u4EEC\u5206\u5F00. \u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D, \u5728\u672B\u65E5\u628A\u88AB\u9009\u7684\u4E0E\u906D\u5F03\u7684\u5206\u5F00\u7684\u56FE\u50CF\u5F88\u591A ( \u8BF7\u89C1: \u739B\u4E09 12; \u5341\u4E09 24 ~ 30, 47 ~ 50; \u4E8C\u5341\u56DB 40 ~ 41; \u4E8C\u5341\u4E94 1 ~ 13 ). \u56E0\u4E3A\u5927\u591A\u6570\u4EBA\u662F\u53F3\u6487\u5B50, \u53F3\u624B\u6BD4\u8F83\u6709\u529B, \u7075\u6D3B, \u53F3\u4FA7\u4E5F\u5C31\u6210\u4E3A\u949F\u7231, \u53D7\u795D\u798F\u53CA\u8363\u8A89\u7684\u8C61\u5F81.</p><p>\u8036\u7A23\u7684\u56FE\u50CF\u5728\u8FD9\u91CC, \u4ECE\u7267\u7F8A\u4EBA\u8F6C\u6362\u6210\u541B\u738B ( 34 \u8282; \u53C2\u9605: \u739B\u4E8C 2; \u4E8C\u5341\u4E00 5 ). \u540C\u65F6\u5C31\u50CF\u6885\u745F\u5C06 &quot;\u795D\u798F&quot; \u548C &quot;\u8BC5\u5492&quot; \u6446\u5728\u4EE5\u8272\u5217\u5B50\u6C11\u9762\u524D, \u7ED9\u4ED6\u4EEC\u9009\u62E9 ( \u7533\u5341\u4E00 26 ) \u4E00\u6837, \u8036\u7A23\u4E5F\u5C06\u90A3\u4E9B &quot;\u6211\u7236\u6240\u795D\u798F&quot; ( 34 \u8282 ) \u4E0E\u90A3\u4E9B &quot;\u53EF\u5492\u9A82\u7684&quot; ( 41 \u8282 ) \u5206\u5F00, \u8FD9\u662F\u9884\u5148\u5B9A\u597D\u7684, \u800C\u662F\u5929\u4E3B\u5411\u6240\u6709\u53D1\u51FA\u9080\u60C5 ( \u739B\u4E94 45; \u5341\u4E09 3 ~ 9 ), \u63A5\u53D7\u4E0E\u5426\u5168\u51AF\u4E2A\u4EBA\u9009\u62E9. \u63A5\u53D7\u4E86\u9080\u8BF7\u7684\u4EBA, \u8FD9\u4E9B\u4EBA\u7684\u5584\u884C\u662F\u53EF\u89C1\u7684, \u795D\u798F\u548C\u5929\u56FD\u7684\u4EA7\u4E1A\u5728\u671F\u5F85\u7740\u4ED6\u4EEC.</p><p>\u8036\u7A23\u8BF4\u8FC7 &quot;\u5929\u56FD\u7684\u798F\u97F3\u5FC5\u5148\u5728\u5168\u4E16\u754C\u5BA3\u8BB2, \u7ED9\u4E07\u6C11\u4F5C\u8BC1, \u7136\u540E\u7ED3\u5C40\u624D\u4F1A\u6765\u5230&quot; ( \u739B\u4E8C\u5341\u56DB 14 ); \u7531\u8FD9\u53E5\u8BDD\u5F97\u77E5: \u739B\u7AA6\u5723\u53F2\u5F88\u53EF\u80FD\u6B63\u671F\u5F85\u7740\u4F1F\u5927\u798F\u4F20\u4F7F\u547D ( \u739B\u4E8C\u5341\u516B 16 ~ 20 ) \u7684\u5B8C\u6210; \u5230\u90A3\u65F6, \u5305\u62EC\u4EE5\u8272\u5217\u4EBA, \u5916\u90A6\u4EBA\u548C\u57FA\u7763\u5F92\u5728\u5185\u7684\u4E07\u6C11, \u90FD\u5DF2\u542C\u5230\u798F\u97F3, \u5E76\u6839\u636E\u4ED6\u4EEC\u7684\u884C\u4E3A\u5728\u63A5\u53D7\u5BA1\u5224.</p><p>&quot;\u6700\u5C0F\u7684\u5144\u5F1F&quot; ( 40 \u8282 ) \u548C &quot;\u6700\u5C0F\u4E2D\u7684\u4E00\u4E2A&quot; ( 45 \u8282 ), \u5728\u739B\u7AA6\u7684\u773C\u4E2D\u5F88\u53EF\u80FD\u662F\u6307\u6709\u6B64\u9700\u8981\u7684\u57FA\u7763\u5F92\u5F1F\u5144, \u800C\u4E0D\u53EA\u662F\u6307\u4EFB\u4F55\u4E00\u4F4D\u6709\u9700\u8981\u7684\u4EBA. \u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D\u7684 &quot;\u5C0F\u5B50&quot; ( \u739B\u5341\u516B 6 ~ 14 ) \u548C &quot;\u6700\u5C0F\u7684&quot; ( \u739B\u5341\u4E00 11 ), \u662F\u6307\u57FA\u7763\u5F92\u56E2\u4F53\u91CC\u9700\u8981\u5E2E\u52A9\u7684\u5F31\u52BF\u6210\u5458; \u51B5\u4E14, \u8036\u7A23\u66FE\u8BB8\u8BFA\u8981\u7ED9\u4E88\u90A3\u4E9B\u63A5\u5F85\u9700\u8981\u5E2E\u52A9\u7684 &quot;\u5148\u77E5&quot;, &quot;\u4E49\u4EBA&quot;, \u6216 &quot;\u95E8\u5F92\u4E2D\u7684\u5C0F\u5B50&quot; ( \u739B\u5341 41 ~ 42 ) \u7684\u4EBA\u8D4F\u62A5. \u4EBA\u600E\u6837\u63A5\u7EB3\u8036\u7A23, \u662F\u900F\u8FC7\u600E\u6837\u63A5\u7EB3\u4F20\u4F48\u798F\u97F3\u7684\u5BA3\u8BB2\u6765\u505A\u5BA1\u5224\u7684\u4F9D\u636E.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u7BC7\u9053\u7406\u7684\u76EE\u7684\u4E0D\u662F\u4E3A\u63CF\u8FF0\u672B\u65E5\u7684\u4E8B\u4EF6, \u800C\u662F\u5728\u8C06\u8C06\u529D\u5BFC\u4EBA\u51C6\u5907\u597D\u81EA\u5DF1, \u53EA\u6709\u8FD9\u6837, \u624D\u80FD\u591F\u7ECF\u5F97\u8D77\u6700\u540E\u7684\u8003\u9A8C. \u540C\u65F6\u4E5F\u7A81\u51FA\u5176\u6838\u5FC3\u8BAF\u606F, \u5373\u8036\u7A23, \u8FD9\u4F4D\u4EBA\u5B50\u7684\u541B\u738B\u8EAB\u4EFD. \u90A3\u4E9B\u88AB\u63A5\u5230\u5929\u56FD\u91CC\u7684\u4EBA, \u5C31\u662F\u90A3\u4E9B\u4EE5\u5BCC\u6709\u540C\u60C5\u5FC3\u7684\u7231\u5BF9\u5F85\u8FD1\u4EBA\u7684\u4EBA.</p><p>\u5728\u300A\u65E7\u7EA6\u300B\u4E2D\u66FE\u7ECF\u63D0\u5230\u8FD9\u516D\u79CD\u7231\u4EBA\u7684\u65B9\u5F0F ( \u4F9D 58:7 ; \u7EA6 22:6-7 ). \u5728\u8FD9\u91CC, \u8FD9\u4E9B\u65B9\u5F0F\u662F\u7231\u7684\u8BEB\u547D\u7684\u5177\u4F53\u5B9E\u8DF5 ( 22: 37 -40 ) . \u8036\u7A23\u7684\u6559\u5BFC\u7279\u522B\u53CD\u5BF9\u4E70\u5356\u578B\u7684\u5FC3\u6001: \u505A\u67D0\u4E9B\u4E8B\u7684\u52A8\u673A\u7EAF\u7CB9\u662F\u60F3\u83B7\u5F97\u5929\u4E3B\u7684\u8D4F\u62A5, \u81EA\u5DF1\u53EF\u4EE5\u4F7F\u5929\u4E3B\u6CA1\u6709\u5176\u4ED6\u9009\u62E9, \u53EA\u80FD\u8D4F\u62A5\u4ED6\u4EEC. \u56E0\u6B64, \u5373\u4F7F\u5B8C\u6210\u8FD9\u4E9B\u5DE5\u4F5C, \u4E5F\u4E0D\u662F\u4E3A\u4E86\u5929\u4E3B, \u800C\u662F\u53CD\u5BF9\u5929\u4E3B, \u5C31\u7B49\u4E8E\u7ED1\u7740\u5929\u4E3B\u7684\u624B, \u5F3A\u8FEB\u7942\u53BB\u8D4F\u62A5\u8FD9\u4E9B\u4FE1\u5F92. \u8FD9\u5176\u5B9E\u662F\u5C06\u771F\u6B63\u7684\u5B97\u6559\u4FE1\u4EF0\u5B8C\u5168\u626D\u66F2\u4E86.</p><p>\u6240\u4EE5\u8BF4, \u4E00\u4E2A\u4EBA\u65E0\u8BBA\u4EE5\u4F55\u79CD\u5584\u884C\u670D\u52A1\u6709\u9700\u8981\u7684\u8FD1\u4EBA, \u5176\u6700\u540E\u7684\u5224\u51B3\u8FD8\u8981\u53D6\u51B3\u4E8E\u8FD9\u4E9B\u5584\u884C\u80CC\u540E\u7684\u52A8\u673A. \u51FA\u81EA\u771F\u7231\u7684\u6148\u5584\u5DE5\u4F5C, \u4E0D\u4F1A\u53D7\u5230\u4EFB\u4F55\u9650\u5236, \u4E5F\u4E0D\u4F1A\u4E3A\u8FD9\u5DE5\u4F5C\u7684\u4EF7\u503C\u9644\u52A0\u4EFB\u4F55\u6761\u4EF6.</p><p>\u8036\u7A23\u6240\u8BB2\u7684\u8BDD\u9488\u5BF9\u6240\u6709\u7684\u4EBA, \u95E8\u5F92\u4EEC\u6240\u7EC4\u6210\u7684\u6709\u5F62\u53EF\u89C1\u7684\u56E2\u4F53\u6CA1\u6709\u4EFB\u4F55\u533A\u522B. \u5982\u6B64\u8BF4\u660E, \u5728\u7942\u7684\u6559\u4F1A\u4E4B\u5916, \u5929\u4E3B\u7684\u56FD\u5EA6\u4ECD\u7136\u53EF\u80FD\u5B9E\u73B0. \u5730\u4E0A\u7684\u6559\u4F1A\u4E0D\u80FD\u7B49\u540C\u4E8E\u5929\u56FD. \u6559\u4F1A\u662F\u670D\u4F8D\u5929\u56FD\u7684\u8C26\u5351\u4EC6\u4EBA, \u800C\u5929\u56FD\u53EF\u4EE5\u8D85\u8D8A\u6559\u4F1A\u53EF\u89C1\u7684\u8303\u56F4. \u8FD9\u4F7F\u6211\u4EEC\u660E\u767D, \u539F\u6765, \u8BB8\u591A\u4EBA\u6CA1\u6709\u542C\u8BF4\u8FC7\u8036\u7A23\u8FD9\u4E2A\u540D\u5B57, \u4F46\u4ED6\u4EEC\u5B9E\u8DF5\u4E86\u5929\u56FD\u7684\u4EF7\u503C, \u7231\u62A4\u90BB\u4EBA. \u8FD9\u4E5F\u5C31\u662F\u6211\u4EEC\u901A\u5E38\u6240\u8BF4\u7684 &quot;\u533B\u540D\u7684\u57FA\u7763\u5F92&quot;. \u8FD9\u4E00\u5207\u4FBF\u80FD\u51B3\u5B9A\u4ED6\u4EEC\u7684\u6700\u7EC8\u5F52\u5BBF (40\u3001 46). \u8FD9\u4F4D\u5BA1\u5224\u666E\u4E16\u7684\u4E3B, \u9690\u85CF\u4E8E\u6240\u6709\u7684\u7A77\u4EBA\u5F53\u4E2D, \u9690\u85CF\u5728\u6240\u6709\u75DB\u82E6\u7684\u9762\u5BB9\u80CC\u540E:\u800C\u8FD9\u79CD\u9690\u85CF\u7684\u4E8B\u5B9E, \u8981\u5728\u6700\u540E\u7684\u65F6\u523B\u663E\u9732\u4E8E\u4F17\u4EBA\u9762\u524D.</p><p>\u53E6\u5916, \u8036\u7A23\u7684\u8FD9\u4E9B\u6559\u8BAD, \u4E5F\u9488\u5BF9\u90A3\u4E9B\u6CA1\u6709\u5B9E\u9645\u6295\u8EAB\u4E8E\u81EA\u5DF1\u4F7F\u547D\u4E2D\u7684\u57FA\u7763\u5F92, \u4E3A\u7684\u662F\u5C06\u4ED6\u4EEC\u4ECE\u660F\u7761\u4E2D\u5524\u9192, \u8B66\u544A\u4ED6\u4EEC, \u5728\u7942\u7B2C\u4E8C\u6B21\u5230\u6765\u4E4B\u524D, \u6BCF\u4E00\u4E2A\u4EBA\u7684\u547D\u8FD0\u53D6\u51B3\u4E8E\u81EA\u5DF1\u5BF9\u5F85\u6709\u6025\u9700\u8005\u7684\u6001\u5EA6.</p>',44),e=[o];function h(r,q){return p(),t("div",null,e)}var i=u(s,[["render",h],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u4E94\u7AE0.html.vue"]]);export{i as default};
