import{_ as u,o as a,c as s,e}from"./app.6bf529a6.js";const p={},t=e('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u516D\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u516D\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u516D\u7AE0</h1><p>##\u516C\u8BAE\u4F1A\u7684\u9634\u8C0B</p><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u8036\u7A23\u8BB2\u5B8C\u4E86\u8FD9\u4E00\u5207\u8BDD\uFF0C\u4FBF\u5BF9\u4ED6\u7684\u95E8\u5F92\u8BF4\uFF1A<sup>2</sup> \u201C\u4F60\u4EEC\u77E5\u9053\uFF1A\u4E24\u5929\u4EE5\u540E\u5C31\u662F\u903E\u8D8A\u8282\uFF0C\u4EBA\u5B50\u8981\u88AB\u89E3\u9001\uFF0C\u88AB\u9489\u5728\u5341\u5B57\u67B6\u4E0A\u3002\u201D<sup>3</sup> \u90A3\u65F6\uFF0C\u53F8\u796D\u957F\u548C\u6C11\u95F4\u957F\u8001\uFF0C\u90FD\u805A\u96C6\u5728\u540D\u53EB\u76D6\u6CD5\u7684\u5927\u53F8\u796D\u7684\u5EAD\u9662\u5185\uFF0C<sup>4</sup> \u5171\u540C\u8BAE\u51B3\u8981\u7528\u8BE1\u8BA1\u6349\u62FF\u8036\u7A23\uFF0C\u52A0\u4EE5\u6740\u5BB3\u3002<sup>5</sup> \u4F46\u662F\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4E0D\u53EF\u5728\u5E86\u8282\u671F\u5185\uFF0C\u514D\u5F97\u6C11\u95F4\u53D1\u751F\u66B4\u52A8\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5982\u739B\u7AA6\u5728\u524D\u56DB\u7BC7\u9053\u7406\u7684\u7ED3\u5C3E\u6240\u505A\u7684\u4E00\u6837, \u5728\u6B64\u5904\u6CA1\u6709\u8BF4\u53CA\u8036\u7A23\u5F80\u540E\u7684\u884C\u7A0B, \u4F46\u8BF4\u660E\u4E86\u4ED6\u6B63\u51C6\u5907\u6700\u540E\u7684 &quot;\u56DE\u5F52\u7236\u5BB6&quot; ( \u4ED6\u7684\u6B7B\u4EA1\u548C\u590D\u6D3B ). \u4F5C\u8005\u628A\u6211\u4EEC\u7684\u6CE8\u610F\u529B\u5F15\u5230\u5373\u5C06\u4E1A\u4E34\u7684\u72B9\u592A\u903E\u8D8A\u8282, &quot;\u4EBA\u5B50\u8981\u88AB\u89E3\u9001, \u88AB\u9489\u5728\u5341\u5B57\u67B6\u4E0A&quot;.</p><p>\u8FD9\u4F18\u8036\u7A23\u82E6\u96BE\u7684\u8BB0\u8FF0\u662F\u739B\u7AA6\u798F\u97F3\u7684\u9AD8\u6F6E. \u5728\u5B97\u5F92\u4EEC\u4F20\u6559\u7684\u65F6\u5019, \u5B83, \u8FDE\u540C\u8036\u7A23\u590D\u6D3B, \u662F\u7ED9\u7EDD\u671B\u4E16\u754C\u7684\u798F\u97F3\u7684\u4E3B\u65E8. \u6709\u4EBA\u8BA4\u4E3A: \u6700\u521D\u798F\u97F3\u662F\u4EE5\u91C7\u96BE\u53F2\u4F5C\u4ECB\u7ECD, \u662F\u9887\u6709\u89C1\u5730\u7684; \u4ECE\u4E0B\u8FF0\u7684\u4E8B\u5B9E, \u6211\u4EEC\u53EF\u4EE5\u770B\u51FA\u8FD9\u4E2A\u53F2\u8FF9\u5728\u5B97\u5F92\u6559\u4F1A\u7684\u91CD\u8981\u6027: \u56DB\u90E8\u798F\u97F3, \u6700\u521D\u5199\u4F5C\u7684\u65B9\u5411\u5404\u4E0D\u76F8\u540C, \u5374\u5728\u8BB0\u8F7D\u8036\u7A23\u53D7\u96BE\u65F6\u5F7C\u6B64\u975E\u5E38\u63A5\u8FD1. \u739B\u7AA6\u798F\u97F3\u7684\u5927\u7EB2\u4E0E\u9A6C\u5C14\u8C37\u798F\u97F3\u7684\u7C7B\u4F3C. \u4F46\u4ED6\u7528\u4ED6\u81EA\u5DF1\u627E\u6765\u7684\u8D44\u6599\u6765\u89E3\u91CA\u5B83. \u57FA\u672C\u4E0A\u5B83\u542B\u6709 (1) \u5E8F\u8A00, (2) \u53D7\u96BE\u6545\u4E8B, (3) \u8036\u7A23\u7684\u590D\u6D3B\u548C\u5149\u8363.</p><p>\u73B0\u5728, \u53F8\u796D\u957F\u548C\u72B9\u592A\u6559\u7684\u9886\u8896\u4EEC\u5728\u76D6\u6CD5\u7684\u9886\u5BFC\u4E0B, \u8BA1\u5212\u902E\u6355\u8036\u7A23\u5E76\u52A0\u4EE5\u6740\u5BB3. \u4ED6\u4EEC\u51B3\u5B9A\u7B49\u5230\u903E\u8D8A\u8282\u4EE5\u540E, \u56E0\u4E3A\u5BB3\u6015\u5728\u8FD9\u4E2A\u5371\u9669\u7684\u65F6\u523B, \u5404\u5730\u6765\u5230\u8036\u8DEF\u6492\u51B7\u7684\u671D\u5723\u8005\u4F1A\u5F15\u53D1\u66B4\u52A8.</p><h2 id="\u4F2F\u8FBE\u5C3C\u665A\u5BB4" tabindex="-1"><a class="header-anchor" href="#\u4F2F\u8FBE\u5C3C\u665A\u5BB4" aria-hidden="true">#</a> \u4F2F\u8FBE\u5C3C\u665A\u5BB4</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>6</sup> \u8036\u7A23\u6B63\u5728\u4F2F\u8FBE\u5C3C\u765E\u75C5\u4EBA\u897F\u6EE1\u5BB6\u91CC\u65F6\uFF0C<sup>7</sup> \u6709\u4E00\u4E2A\u5973\u4EBA\u62FF\u7740\u4E00\u7389\u74F6\u8D35\u91CD\u7684\u9999\u6DB2\u6765\u5230\u8036\u7A23\u8DDF\u524D\uFF0C\u5012\u5728\u6B63\u5750\u5E2D\u7684\u8036\u7A23\u7684\u5934\u4E0A\u3002 <sup>8</sup> \u95E8\u5F92\u4EEC\u89C1\u4E86\u5C31\u4E0D\u6EE1\u610F\u8BF4\uFF1A \u201C\u4E3A\u4EC0\u4E48\u8FD9\u6837\u6D6A\u8D39\uFF1F<sup>9</sup> \u8FD9\u9999\u6DB2\u539F\u53EF\u5356\u5F97\u8BB8\u591A\u94B1\uFF0C\u65BD\u820D\u7ED9\u7A77\u4EBA\u3002\u201D<sup>10</sup> \u8036\u7A23\u77E5\u9053\u4E86\uFF0C\u5C31\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4F60\u4EEC\u4E3A\u4EC0\u4E48\u53EB\u8FD9\u4E2A\u5973\u4EBA\u96BE\u53D7\uFF1F\u5979\u5728\u6211\u8EAB\u4E0A\u539F\u662F\u4F5C\u4E86\u4E00\u4EF6\u5584\u4E8B\u3002<sup>11</sup> \u4F60\u4EEC\u5E38\u6709\u7A77\u4EBA\u540C\u4F60\u4EEC\u5728\u4E00\u8D77\uFF0C\u81F3\u4E8E\u6211\uFF0C\u4F60\u4EEC\u5374\u4E0D\u5E38\u6709\u3002<sup>12</sup> \u5979\u628A\u8FD9\u9999\u6DB2\u5012\u5728\u6211\u8EAB\u4E0A\uFF0C\u539F\u662F\u4E3A\u5B89\u846C\u6211\u800C\u4F5C\u7684\u3002<sup>13</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u5C06\u6765\u5728\u5168\u4E16\u754C\uFF0C\u8FD9\u798F\u97F3\u65E0\u8BBA\u4F20\u5230\u54EA\u91CC\uFF0C\u5FC5\u8981\u8FF0\u8BF4\u5979\u6240\u4F5C\u7684\u4E8B\uFF0C\u6765\u7EAA\u5FF5\u5979\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u90A3\u65F6\u8036\u7A23\u4F4F\u5728\u57CE\u5916\u4E0D\u8FDC\u4F2F\u8FBE\u5C3C\u7684\u5730\u65B9. \u5728\u4E00\u4E2A\u540D\u53EB\u897F\u6EE1\u8005\u5BB6\u7684\u665A\u5BB4\u4E0A, \u4E00\u4E2A\u5987\u4EBA\u7528\u73CD\u8D35\u7684\u9999\u6DB2\u503E\u5012\u5728\u4ED6\u7684\u5934\u4E0A. \u8DEF\u52A0\u798F\u97F3\u91CC\u4E5F\u6709\u7C7B\u4F3C\u7684\u6545\u4E8B; \u4E3B\u4EBA\u662F\u897F\u6EE1, \u90A3\u4E2A\u5987\u4EBA\u5374\u662F\u4E2A &quot;\u7F6A\u4EBA&quot;, \u5979\u628A\u9999\u6DB2\u5012\u5728\u8036\u7A23\u7684\u811A\u4E0A. \u82E5\u671B\u798F\u97F3\u91CC\u5219\u8BF4\u739B\u5C14\u5927\u662F\u5973\u4E3B\u4EBA, \u5979\u590D\u6D3B\u7684\u5F1F\u5F1F\u62C9\u531D\u7984\u662F\u5BBE\u5BA2, \u800C\u739B\u5229\u4E9A\u7528\u9999\u6DB2 &quot;\u6577\u62B9\u4E86\u8036\u7A23\u7684\u811A&quot;. ( \u63A5\u65B0\u7EA6\u91CC\u7684\u8BB0\u8F7D, \u65E0\u6CD5\u8BC1\u660E\u5979\u5C31\u662F\u739B\u8FBE\u808B\u7EB3 ). \u8036\u7A23\u4E3A\u5979\u7684\u5584\u4E3E\u8FA9\u62A4, \u5C06\u5B83\u89E3\u91CA\u4E3A\u4ED6\u57CB\u846C\u65F6\u5C38\u4F53\u6577\u6CB9\u7684\u5148\u5146, \u5E76\u4E14\u9884\u5148\u58F0\u660E\u8FD9\u9879\u4E3E\u52A8\u8981\u5728\u6559\u4F1A\u7684\u521D\u4F20\u91CC\u5360\u4E00\u5730\u4F4D, \u5B83\u5C06\u88AB &quot;\u4F20\u5E03\u5230\u5168\u4E16\u754C&quot;. \u739B\u7AA6\u548C\u9A6C\u5C14\u8C37\u4E00\u6837, \u4E5F\u8BA4\u4E3A\u72B9\u8FBE\u65AF\u7684\u5356\u4E3B\u662F\u8FD9\u4E00\u4E8B\u4EF6\u7684\u540E\u679C. \u4F46\u739B\u7AA6\u7279\u522B\u63D0\u5230 &quot;\u4E09\u5341\u5757\u94B1\u94F6\u94B1&quot;, \u56E0\u4E3A\u8FD9\u7B14\u94B1\u4EE5\u540E\u8FD8\u6709\u4F5C\u7528.</p><h2 id="\u72B9\u8FBE\u65AF\u901A\u654C\u5356\u4E3B" tabindex="-1"><a class="header-anchor" href="#\u72B9\u8FBE\u65AF\u901A\u654C\u5356\u4E3B" aria-hidden="true">#</a> \u72B9\u8FBE\u65AF\u901A\u654C\u5356\u4E3B</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>14</sup> \u968F\u540E\uFF0C\u90A3\u5341\u4E8C\u4EBA\u4E2D\u4E4B\u4E00\uFF0C\u540D\u53EB\u72B9\u8FBE\u65AF\u4F9D\u65AF\u52A0\u7565\u7684\uFF0C\u53BB\u89C1\u53F8\u796D\u957F\uFF0C<sup>15</sup> \u8BF4\uFF1A\u201C\u6211\u628A\u4ED6\u4EA4\u7ED9\u4F60\u4EEC\uFF0C\u4F60\u4EEC\u613F\u610F\u7ED9\u6211\u4EC0\u4E48\uFF1F\u201D\u4ED6\u4EEC\u7EA6\u5B9A\u7ED9\u4ED6\u4E09\u5341\u5757\u94F6\u94B1\u3002<sup>16</sup> \u4ECE\u6B64\u4ED6\u4FBF\u5BFB\u627E\u673A\u4F1A\uFF0C\u8981\u628A\u8036\u7A23\u4EA4\u51FA\u3002</p></blockquote><h2 id="\u6700\u540E\u665A\u9910" tabindex="-1"><a class="header-anchor" href="#\u6700\u540E\u665A\u9910" aria-hidden="true">#</a> \u6700\u540E\u665A\u9910</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>17</sup> \u65E0\u9175\u8282\u7684\u7B2C\u4E00\u5929\uFF0C\u95E8\u5F92\u524D\u6765\u5BF9\u8036\u7A23\u8BF4\uFF1A\u201C\u4F60\u613F\u610F\u6211\u4EEC\u5728\u54EA\u91CC\uFF0C\u7ED9\u4F60\u9884\u5907\u5403\u903E\u8D8A\u8282\u665A\u9910\uFF1F\u201D <sup>18</sup> \u8036\u7A23\u8BF4\uFF1A\u201C\u4F60\u4EEC\u8FDB\u57CE\u53BB\u89C1\u67D0\u4EBA\uFF0C\u5BF9\u4ED6\u8BF4\uFF1A\u5E08\u5085\u8BF4\uFF1A\u6211\u7684\u65F6\u5019\u8FD1\u4E86\uFF0C\u6211\u8981\u4E0E\u6211\u7684\u95E8\u5F92\u5728\u4F60\u90A3\u91CC\u4E3E\u884C\u903E\u8D8A\u8282\u3002\u201D<sup>19</sup> \u95E8\u5F92\u5C31\u7167\u8036\u7A23\u5429\u5490\u4ED6\u4EEC\u7684\u4F5C\u4E86\uFF0C\u9884\u5907\u4E86\u903E\u8D8A\u8282\u665A\u9910\u3002</p></blockquote><blockquote><p><sup>20</sup> \u5230\u4E86\u665A\u4E0A\uFF0C\u8036\u7A23\u4E0E\u5341\u4E8C\u95E8\u5F92\u5750\u5E2D\u3002<sup>21</sup> \u4ED6\u4EEC\u6B63\u5403\u665A\u9910\u7684\u65F6\u5019\uFF0C\u8036\u7A23\u8BF4\uFF1A\u201C\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4F60\u4EEC\u4E2D\u6709\u4E00\u4E2A\u4EBA\u8981\u51FA\u5356\u6211\u3002\u201D<sup>22</sup> \u4ED6\u4EEC\u975E\u5E38\u5FE7\u95F7\uFF0C\u5F00\u59CB\u5404\u81EA\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u4E3B\uFF0C\u96BE\u9053\u662F\u6211\u5417\uFF1F\u201D<sup>23</sup> \u8036\u7A23\u56DE\u7B54\u8BF4\uFF1A\u201C\u90A3\u540C\u6211\u4E00\u8D77\u628A\u624B\u8638\u5728\u76D8\u5B50\u91CC\u7684\u4EBA\uFF0C\u8981\u51FA\u5356\u6211\u3002<sup>24</sup> \u4EBA\u5B50\u56FA\u7136\u8981\u6309\u7167\u6307\u7740\u4ED6\u6240\u8BB0\u8F7D\u7684\u800C\u53BB\uFF0C\u4F46\u51FA\u5356\u4EBA\u5B50\u7684\u90A3\u4EBA\u5374\u662F\u6709\u7978\u7684\uFF01\u90A3\u4EBA\u82E5\u6CA1\u6709\u751F\uFF0C\u4E3A\u4ED6\u66F4\u597D\u3002\u201D<sup>25</sup> \u90A3\u8981\u51FA\u5356\u4ED6\u7684\u72B9\u8FBE\u65AF\u4E5F\u5F00\u53E3\u95EE\u8036\u7A23\u8BF4\uFF1A\u201C\u8FA3\u5F7C\uFF0C\u96BE\u9053\u662F\u6211\u5417\uFF1F\u201D\u8036\u7A23\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u4F60\u8BF4\u7684\u662F\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u90A3\u53E5 &quot;\u65E0\u9175\u8282\u7684\u7B2C\u4E00\u5929&quot;, \u5BF9\u4E8E\u786E\u5B9A\u6700\u540E\u665A\u9910\u7684\u65E5\u671F\u5F88\u91CD\u8981. \u6700\u8FD1\u6709\u4EBA\u8BA4\u4E3A\u8036\u7A23\u91C7\u7528\u7684\u8F83\u53E4\u8001\u7684\u53F8\u796D\u5386 ( \u9633\u5386 ), \u6839\u636E\u8FD9\u79CD\u5386\u6CD5, \u903E\u8D8A\u8282\u6BCF\u5E74\u90FD\u5728\u661F\u671F\u4E8C. \u4F46\u6839\u636E\u6BD4\u8F83\u65B0\u7684\u5B98\u65B9\u5386 ( \u9634\u5386 ) ( \u5F53\u65F6\u4E3A\u8036\u8DEF\u6492\u51B7\u6240\u91C7\u7528 ), \u8036\u7A23\u901D\u4E16\u90A3\u5E74\u7684\u903E\u8D8A\u8282\u662F\u5728\u661F\u671F\u4E94\u665A\u4E0A. \u8FD9\u4E2A\u5047\u8BBE\u89E3\u51B3\u4E86\u82E5\u671B\u798F\u97F3\u548C\u5BF9\u89C2\u798F\u97F3\u4E4B\u95F4\u4F3C\u4E4E\u6709\u77DB\u76FE\u7684\u95EE\u9898. \u5B83\u8FD8\u4E3A\u8036\u7A23\u82E6\u96BE\u4E2D\u7684\u8BB8\u591A\u4E8B\u4EF6\u63D0\u4F9B\u4E86\u8F83\u5145\u88D5\u7684\u65F6\u95F4 ( \u4ED6\u53EF\u80FD\u5728\u661F\u671F\u4E8C\u665A\u4E0A\u88AB\u62D8\u6355, \u5728\u661F\u671F\u4E94\u88AB\u9489\u5728\u5341\u5B57\u67B6 ). \u540C\u65F6\u8FD9\u4E2A\u7B54\u6848\u6697\u6307\u8036\u7A23\u548C\u4ED6\u7684\u95E8\u5F92\u4EEC\u53EF\u80FD\u5E76\u6CA1\u6709\u5728\u6700\u540E\u665A\u9910\u91CC\u7528\u5DF4\u65AF\u5366\u7F94\u7F8A, \u800C\u53EA\u7528\u4E86\u65E0\u9175\u997C, \u56E0\u4E3A\u90A3\u5E74\u6740\u7F8A\u7684\u65F6\u95F4\u5E94\u8BE5\u662F\u661F\u671F\u4E94\u7684\u4E2D\u5348, \u800C\u4E3B\u7BA1\u6B64\u4E8B\u7684\u53F8\u796D\u4E0D\u53EF\u80FD\u5728\u975E\u5B98\u65B9\u8BA4\u53EF\u7684\u661F\u671F\u4E8C\u5C31\u5B8C\u6210\u4E86\u8FD9\u4E2A\u4EEA\u5F0F. \u8036\u7A23\u7684\u547D\u4EE4 &quot;\u4F60\u4EEC\u8FDB\u57CE\u53BB&quot; ( \u5373\u8036\u8DEF\u6492\u51B7 ) \u662F\u4E3A\u4E86\u9075\u5FAA\u6885\u745F\u6CD5\u7B46\u6240\u8BF4\u7684: \u903E\u8D8A\u665A\u9910\u5FC5\u987B\u5728\u57CE\u5185\u8FDB\u884C. \u8DEF\u52A0\u8FD8\u544A\u8BC9\u6211\u4EEC\u4F2F\u591A\u7984\u548C\u82E5\u671B\u88AB\u6307\u5B9A\u505A\u51C6\u5907\u5DE5\u4F5C\u7684\u4E24\u4F4D\u5BA0\u7231\u7684\u95E8\u5F92.</p><h2 id="\u5EFA\u7ACB\u5723\u4F53\u5723\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u5EFA\u7ACB\u5723\u4F53\u5723\u4E8B" aria-hidden="true">#</a> \u5EFA\u7ACB\u5723\u4F53\u5723\u4E8B</h2><h3 id="\u7ECF\u6587-4" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-4" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>26</sup> \u4ED6\u4EEC\u6B63\u5403\u665A\u9910\u7684\u65F6\u5019\uFF0C\u8036\u7A23\u62FF\u8D77\u997C\u6765\uFF0C\u795D\u798F\u4E86\uFF0C\u64D8\u5F00\u9012\u7ED9\u95E8\u5F92\u8BF4\uFF1A\u201C\u4F60\u4EEC\u62FF\u53BB\u5403\u5427\uFF01\u8FD9\u662F\u6211\u7684\u8EAB\u4F53\u3002\u201D<sup>27</sup> \u7136\u540E\uFF0C\u53C8\u62FF\u8D77\u676F\u6765\uFF0C\u795D\u8C22\u4E86\uFF0C\u9012\u7ED9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4F60\u4EEC\u90FD\u7531\u5176\u4E2D\u559D\u5427\uFF01<sup>28</sup> \u56E0\u4E3A\u8FD9\u662F\u6211\u7684\u8840\uFF0C\u65B0\u7EA6\u7684\u8840\uFF0C\u4E3A\u5927\u4F17\u503E\u6D41\uFF0C\u4EE5\u8D66\u514D\u7F6A\u8FC7\u3002 <sup>29</sup> \u6211\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4ECE\u4ECA\u4EE5\u540E\uFF0C\u6211\u4E0D\u518D\u559D\u8FD9\u8461\u8404\u6C41\u4E86\u76F4\u5230\u5728\u6211\u7236\u7684\u56FD\u91CC\u90A3\u4E00\u5929\uFF0C\u4E0E\u4F60\u4EEC\u540C\u559D\u65B0\u9152\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u665A\u9910\u4E2D, \u8036\u7A23\u9884\u8A00\u72B9\u8FBE\u65AF\u7684\u80CC\u4E3B, \u4ED6\u66A7\u6629\u7684\u6307\u79F0\u4ED6\u662F &quot;\u90A3\u540C\u6211\u4E00\u8D77\u628A\u624B\u8638\u5728\u4E00\u76D8\u5B50\u91CC\u7684\u4EBA&quot;. &quot;\u4EBA\u5B50\u56FA\u7136\u8981\u6309\u7167\u6307\u8457\u4ED6\u6240\u8BB0\u8F7D\u7684\u800C\u53BB&quot;, \u53EF\u7528\u6765\u5E2E\u52A9\u6211\u4EEC\u8BA4\u8BC6\u8036\u7A23\u4E4B\u6B7B\u4E43\u662F\u53D7\u82E6\u7684\u4E0A\u4E3B\u4EC6\u4EBA\u4E4B\u6B7B. \u8036\u7A23\u6BEB\u4E0D\u9690\u6666\u7684\u8BA4\u72B9\u8FBE\u65AF\u77E5\u9053, \u4ED6\u5DF2\u7ECF\u6653\u5F97\u4ED6\u7684\u80CC\u53DB\u4E86. \u4E0D\u8FC7, \u543E\u4E3B\u6240\u5BA3\u79F0\u7684 &quot;\u7978&quot; \u5E76\u4E0D\u662F\u8BC5\u5492, \u800C\u662F\u4E00\u79CD\u54C0\u4F24\u7684\u8868\u793A. \u5F53\u4ED6\u4EEC\u8FDB\u98DF\u7684\u65F6\u5019, \u8036\u7A23\u795D\u5723\u4E86\u4E00\u4E9B\u997C\u8BF4: &quot;\u8FD9\u662F\u6211\u7684\u8EAB\u4F53.&quot; \u540E\u6765, \u53EF\u80FD\u662F\u5F53\u4ED6\u9075\u5FAA\u72B9\u592A\u4EBA\u7684\u793C\u4FD7. \u5728\u793C\u4EEA\u4E2D\u996E\u6700\u540E\u4E00\u676F\u9152\u7684\u65F6\u5019, \u611F\u6069\u7948\u7977\u8BF4: &quot;\u8FD9\u662F\u6211\u7684\u8840, \u65B0\u7EA6\u7684\u8840, \u4E3A\u5927\u4F17\u503E\u6D41\u4EE5\u8D66\u514D\u7F6A\u8FC7&quot; \u5982\u6B64, \u739B\u7AA6\u628A\u4E8B\u60C5\u4EA4\u5F85\u6E05\u695A, \u5723\u4F53\u5723\u4E8B\u7684\u5EFA\u7ACB\u548C\u65B0\u7EA6\u7684\u8BA2\u5B9A, \u5982\u540C\u65E7\u7EA6\u6240\u9884\u8A00\u7684, \u540C\u65F6, \u5B83\u4E0E\u8036\u7A23, \u4E0A\u4E3B\u4EC6\u4EBA\u7684\u8D4E\u7F6A\u4E4B\u6B7B\u4E5F\u662F\u606F\u606F\u76F8\u5173\u7684. \u8FD9\u65B0\u7EA6\u4E3B\u9898\u662F\u5F88\u91CD\u8981\u7684: (1) \u5B83\u8BC1\u660E\u4E86\u8036\u7A23\u5728\u4E16\u4E4B\u65F6\u5EFA\u7ACB\u6559\u4F1A\u7684\u610F\u56FE -- \u6709\u76DF\u7EA6\u5C31\u5FC5\u7136\u6709\u7EA6\u6C11; (2) \u5B83\u8BC1\u660E\u4E86\u5723\u4F53\u5723\u4E8B\u4E2D, \u8036\u7A23\u4EB2\u81EA\u7684\u4E34\u5728. \u5728\u65E7\u7EA6\u7684\u65F6\u5019\u8BA2\u7ACB\u76DF\u7EA6\u7684\u51ED\u8BB0, \u603B\u662F\u4E00\u4E2A\u771F\u5B9E\u7684\u4E8B\u7269, \u800C\u5E76\u975E\u4E00\u4E2A\u8C61\u5F81\u6027\u7684\u5DE5\u5177. \u7531\u67D0\u4E9B\u8FF9\u8C61, \u6211\u4EEC\u770B\u5F97\u51FA\u6765, 26 ~ 28 \u8FD9\u51E0\u53E5\u8BDD\u662F\u739B\u7AA6\u4ECE\u793C\u4EEA\u9882\u8BCD\u91CC\u53D6\u51FA\u6765\u7684 ( \u53EF\u80FD\u5728\u4ED6\u5F53\u65F6\u7684\u6559\u4F1A\u91CC\u7528\u6765\u7EAA\u5FF5\u6700\u540E\u665A\u9910\u7684 ). \u5728\u7B2C 26 \u8282\u4E2D, \u6211\u4EEC\u770B\u5230 &quot;\u8036\u7A23&quot; \u548C &quot;\u4ED6\u7684\u95E8\u5F92&quot;, \u800C\u4E0D\u662F\u50CF\u9A6C\u5C14\u8C37\u798F\u97F3\u91CC\u53EA\u7528\u4E00\u4E2A\u7B80\u5355\u7684\u4EE3\u540D\u8BCD -- \u8FD9\u70B9\u663E\u793A\u739B\u7AA6\u7684\u53D9\u8FF0\u5E76\u975E\u6765\u81EA\u9A6C\u5C14\u8C37. &quot;\u62FF\u53BB\u5403&quot; \u4E0E 27 \u8282\u7684 &quot;\u559D&quot; \u90FD\u7528\u4E86\u547D\u4EE4\u5F0F, \u8FD9\u4E2A\u5E73\u884C\u7684\u5173\u7CFB ( \u4E3A\u9A6C\u5C14\u8C37\u798F\u97F3\u6240\u65E0 ) \u5C31\u662F\u4E00\u4E2A\u7EBF\u7D22, \u544A\u8BC9\u6211\u4EEC\u8FD9\u6BB5\u662F\u53D6\u81EA\u793C\u4EEA\u7684\u7ECF\u6587. 28 \u8282\u91CC, \u739B\u7AA6\u53C8\u5C31\u9A6C\u5C14\u8C37\u798F\u97F3\u52A0\u4E0A &quot;\u4EE5\u8D66\u514D\u7F6A\u8FC7&quot; -- \u5F3A\u8C03\u4E86\u8036\u7A23\u6D41\u8840\u662F\u5177\u6709\u796D\u732E\u6027\u7684. \u63A5\u4E0B\u6765\u7684\u53D9\u8FF0\u4F3C\u4E4E\u662F\u8036\u7A23\u548C\u590D\u6D3B\u540E\u7684\u81EA\u5DF1\u6240\u505A\u7684\u7EA6\u5B9A. \u8DEF\u52A0\u5219\u5C06\u8FD9\u4E8B\u89C6\u4E3A\u6559\u4F1A\u5723\u796D\u793C\u4EEA\u7684\u9884\u8A00. \u4ED6\u4EEC\u5531\u7684 &quot;\u5723\u548F&quot; \u5C31\u662F\u903E\u8D8A\u665A\u9910\u7ED3\u675F\u65F6\u6240\u5531\u7684 &quot;\u8D5E\u7F8E&quot; \u7684\u5723\u548F. \u5B83\u4E5F\u662F\u8D26\u84EC\u8282\u6B22\u4E50\u793C\u4EEA\u4E2D\u7684\u4E00\u90E8\u4EFD.</p><h2 id="\u9884\u8A00\u95E8\u5F92\u9003\u6563\u548C\u4F2F\u591A\u7984\u80CC\u4E3B" tabindex="-1"><a class="header-anchor" href="#\u9884\u8A00\u95E8\u5F92\u9003\u6563\u548C\u4F2F\u591A\u7984\u80CC\u4E3B" aria-hidden="true">#</a> \u9884\u8A00\u95E8\u5F92\u9003\u6563\u548C\u4F2F\u591A\u7984\u80CC\u4E3B</h2><h3 id="\u7ECF\u6587-5" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-5" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>30</sup> \u4ED6\u4EEC\u5531\u4E86\u5723\u548F\uFF0C\u5C31\u51FA\u6765\u5F80\u6A44\u6984\u5C71\u53BB\u3002<sup>31</sup> \u90A3\u65F6\uFF0C\u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4ECA\u591C\u4F60\u4EEC\u90FD\u8981\u4E3A\u6211\u7684\u7F18\u6545\u8DCC\u5012\uFF0C\u56E0\u4E3A\u7ECF\u4E0A\u8BB0\u8F7D\uFF1A\u2018\u6211\u8981\u6253\u51FB\u7267\u4EBA\uFF0C\u7F8A\u7FA4\u5C31\u8981\u56DB\u6563\u3002\u2019<sup>32</sup> \u4F46\u662F\uFF0C\u6211\u590D\u6D3B\u540E\uFF0C\u8981\u5728\u4F60\u4EEC\u4EE5\u5148\u5230\u52A0\u91CC\u808B\u4E9A\u53BB\u3002\u201D<sup>33</sup> \u4F2F\u591A\u7984\u5374\u56DE\u7B54\u4ED6\u8BF4\uFF1A\u201C\u5373\u4FBF\u4F17\u4EBA\u90FD\u4E3A\u4F60\u7684\u7F18\u6545\u8DCC\u5012\uFF0C\u6211\u51B3\u4E0D\u4F1A\u8DCC\u5012\u3002\u201D<sup>34</sup> \u8036\u7A23\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\uFF1A\u4ECA\u591C\u9E21\u53EB\u4EE5\u524D\uFF0C\u4F60\u8981\u4E09\u6B21\u4E0D\u8BA4\u6211\u3002\u201D<sup>35</sup> \u4F2F\u591A\u7984\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u5373\u4FBF\u6211\u8BE5\u540C\u4F60\u4E00\u8D77\u6B7B\uFF0C\u6211\u4E5F\u51B3\u4E0D\u4F1A\u4E0D\u8BA4\u4F60\u3002\u201D\u4F17\u95E8\u5F92\u4E5F\u90FD\u8FD9\u6837\u8BF4\u4E86\u3002</p></blockquote><h2 id="\u5C71\u56ED\u7948\u7977" tabindex="-1"><a class="header-anchor" href="#\u5C71\u56ED\u7948\u7977" aria-hidden="true">#</a> \u5C71\u56ED\u7948\u7977</h2><h3 id="\u7ECF\u6587-6" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-6" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>36</sup> \u968F\u540E\uFF0C\u8036\u7A23\u540C\u4ED6\u4EEC\u6765\u5230\u4E00\u4E2A\u540D\u53EB\u9769\u8D23\u739B\u5C3C\u7684\u5E84\u56ED\u91CC\uFF0C\u4FBF\u5BF9\u95E8\u5F92\u8BF4\uFF1A\u201C\u4F60\u4EEC\u5750\u5728\u8FD9\u91CC\uFF0C\u7B49\u6211\u5230\u90A3\u8FB9\u53BB\u7948\u7977\u3002\u201D<sup>37</sup> \u9042\u5E26\u4E86\u4F2F\u591A\u7984\u548C\u8F7D\u4F2F\u5FB7\u7684\u4E24\u4E2A\u513F\u5B50\u540C\u53BB\uFF0C\u5F00\u59CB\u5FE7\u95F7\u6050\u6016\u8D77\u6765\uFF0C<sup>38</sup> \u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u6211\u7684\u5FC3\u7075\u5FE7\u95F7\u5F97\u8981\u6B7B\uFF0C\u4F60\u4EEC\u7559\u5728\u8FD9\u91CC\u540C\u6211\u4E00\u8D77\u9192\u5BE4\u5427\uFF01\u201D<sup>39</sup> \u4ED6\u7A0D\u5FAE\u524D\u884C\uFF0C\u5C31\u4FEF\u9996\u81F3\u5730\u7948\u7977\u8BF4\uFF1A\u201C\u6211\u7236\uFF01\u82E5\u662F\u53EF\u80FD\uFF0C\u5C31\u8BA9\u8FD9\u676F\u79BB\u5F00\u6211\u5427\uFF01\u4F46\u4E0D\u8981\u7167\u6211\uFF0C\u800C\u8981\u7167\u4F60\u6240\u613F\u610F\u7684\u3002\u201D<sup>40</sup> \u4ED6\u6765\u5230\u95E8\u5F92\u90A3\u91CC\uFF0C\u89C1\u4ED6\u4EEC\u7761\u7740\u4E86\uFF0C\u4FBF\u5BF9\u4F2F\u591A\u7984\u8BF4\uFF1A\u201C\u4F60\u4EEC\u7ADF\u4E0D\u80FD\u540C\u6211\u9192\u5BE4\u4E00\u4E2A\u65F6\u8FB0\u5417\uFF1F<sup>41</sup> \u9192\u5BE4\u7948\u7977\u7F62\uFF01\u514D\u9677\u4E8E\u8BF1\u60D1\uFF1B\u5FC3\u795E\u56FA\u7136\u5207\u613F\uFF0C\u4F46\u8089\u4F53\u5374\u8F6F\u5F31\u3002\u201D<sup>42</sup> \u4ED6\u7B2C\u4E8C\u6B21\u518D\u53BB\u7948\u7977\u8BF4\uFF1A\u201C\u6211\u7236\uFF01\u5982\u679C\u8FD9\u676F\u4E0D\u80FD\u79BB\u53BB\uFF0C\u975E\u8981\u6211\u559D\u4E0D\u53EF\uFF0C\u5C31\u6210\u5C31\u4F60\u7684\u610F\u613F\u5427\uFF01\u201D<sup>43</sup> \u4ED6\u53C8\u56DE\u6765\uFF0C\u89C1\u4ED6\u4EEC\u4ECD\u7136\u7761\u7740\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u7684\u773C\u775B\u5F88\u662F\u6C89\u91CD\u3002<sup>44</sup> \u4ED6\u518D\u79BB\u5F00\u4ED6\u4EEC\uFF0C\u7B2C\u4E09\u6B21\u53BB\u7948\u7977\uFF0C\u53C8\u8BF4\u4E86\u540C\u6837\u7684\u8BDD\u3002<sup>45</sup> \u7136\u540E\u56DE\u5230\u95E8\u5F92\u90A3\u91CC\uFF0C\u5BF9\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4F60\u4EEC\u7761\u4E0B\u53BB\u5427\uFF01\u4F11\u606F\u5427\uFF01\u770B\uFF0C\u65F6\u5019\u5230\u4E86\uFF0C\u4EBA\u5B50\u5C31\u8981\u88AB\u4EA4\u4E8E\u7F6A\u4EBA\u624B\u91CC\u3002<sup>46</sup> \u8D77\u6765\uFF0C\u6211\u4EEC\u53BB\u5427\uFF01\u770B\uFF0C\u90A3\u51FA\u5356\u6211\u7684\u5DF2\u6765\u8FD1\u4E86\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u5F80\u6A44\u6984\u5C71\u7684\u8DEF\u4E0A, \u8036\u7A23\u5BA3\u5E03\u95E8\u5F92\u4EEC\u5C06\u8981\u5982\u5148\u77E5\u6240\u9884\u8A00\u7684\u80CC\u5F03\u4ED6. \u4ED6\u4E5F\u9884\u8A00\u4E86\u4ED6\u4EEC\u5C06\u8981\u5728\u52A0\u91CC\u808B\u4E9A\u89C1\u5230\u4ED6 -- \u590D\u6D3B\u7684\u8036\u7A23; \u81F3\u4E8E\u540E\u6765\u5728\u8036\u8DEF\u6492\u51B7\u4E00\u4E2A\u6D41\u884C\u7684\u8BF4\u6CD5, \u53C2\u7167\u82E5\u4E8C\u5341 19 \u4EE5\u540E\u8BF8\u8282. \u95E8\u5F92\u4EEC\u5BF9\u9ED8\u897F\u4E9A\u8036\u7A23\u7684\u90A3\u79CD\u81EA\u7136\u800C\u8BDA\u631A\u7684\u6DF1\u60C5, \u5FC5\u987B\u7ECF\u8FC7\u4E00\u756A\u51C0\u5316\u7684\u8003\u9A8C, \u800C\u5929\u4E3B\u5723\u7236\u6240\u6307\u793A\u7684\u65B9\u6CD5\u5C31\u662F\u8036\u7A23\u6B7B\u4EA1\u6240\u5F62\u6210\u7684 &quot;\u7ECA\u811A\u77F3&quot;.</p><p>\u4ED6\u4EEC\u8D70\u4E86\u8FD1\u4E00\u4E2A\u6A44\u6984\u56ED, \u8036\u7A23\u79BB\u5F00\u4E86\u95E8\u5F92\u4EEC, \u5E26\u4E86\u4F2F\u591A\u7984, \u96C5\u5404\u4F2F\u548C\u82E5\u671B\u8FD9\u4E09\u4F4D\u76EE\u51FB\u4ED6\u663E\u5723\u5BB9\u7684\u7231\u5F92, \u524D\u53BB\u7948\u7977. \u739B\u7AA6\u544A\u8BC9\u6211\u4EEC\u8036\u7A23\u5728\u8FD9\u65F6\u662F\u5982\u4F55\u7684 &quot;\u5F00\u5982\u5FE7\u95F7\u6050\u6016&quot; ( \u9A6C\u5C14\u8C37\u7684\u5F62\u5BB9, \u63AA\u8BCD\u66F4\u52A0\u5F3A\u70C8: &quot;\u5F00\u59CB\u60CA\u60E7\u6050\u6016&quot; ). \u8036\u7A23\u7948\u7977\u7684\u5185\u5BB9\u4E3B\u8981\u662F\u4ED6\u6559\u7ED9\u95E8\u5F92\u7684 &quot;\u5929\u4E3B\u7ECF&quot;: &quot;\u6211\u7236! ...... \u7167\u4F60\u6240\u613F\u610F\u7684, \u800C\u4E0D\u8981\u7167\u6211 ...... \u613F\u4F60\u7684\u65E8\u610F\u4E3A\u4EBA\u627F\u884C.&quot; \u5BF9\u95E8\u5F92\u8BF4: &quot;\u7948\u7977\u5427! \u4F7F\u4F60\u4EEC\u514D\u9677\u4E8E\u8BF1\u60D1.&quot; \u8036\u7A23\u7684\u8BDD\u53EF\u80FD\u662F\u4E00\u4E2A\u95EE\u53E5: &quot;\u4F60\u4EEC\u4ECD\u7136\u778C\u7761? \u4ECD\u65E7\u5B89\u7136\u4F11\u6187\u5417?&quot; \u5982\u679C\u8FD9\u79CD\u8BF4\u6CD5\u662F\u6B63\u786E\u7684\u8BDD, \u8036\u7A23\u5728\u8FD9\u91CC\u7528\u4E86\u4E2A\u6E29\u548C\u7684\u53CD\u8BBD: \u73B0\u5728\u540C\u6211\u4E00\u8D77\u9192\u5BE4\u5DF2\u7ECF\u8FDF\u4E86. \u5982\u679C\u4F60\u4EEC\u60F3\u7761, \u5C31\u7761\u5427! \u6211\u5FC5\u987B\u72EC\u81EA\u5FCD\u53D7\u8FD9\u4E2A\u8003\u9A8C. ( \u8FD9\u4E2A\u8003\u9A8C\u53EF\u80FD\u5C31\u6307\u672B\u4E16\u7684\u5927\u756A\u5224; \u8036\u7A23\u8EAB\u4E3A\u5929\u4E3B\u5B50\u6C11\u7684\u9886\u8896, \u5FC5\u987B\u9996\u5148\u5355\u72EC\u7684\u7ECF\u5386\u5B83. )</p><h2 id="\u8036\u7A23\u88AB\u6355\u5B97\u5F92\u9003\u6563" tabindex="-1"><a class="header-anchor" href="#\u8036\u7A23\u88AB\u6355\u5B97\u5F92\u9003\u6563" aria-hidden="true">#</a> \u8036\u7A23\u88AB\u6355\u5B97\u5F92\u9003\u6563</h2><h3 id="\u7ECF\u6587-7" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-7" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>47</sup> \u4ED6\u8FD8\u5728\u8BF4\u8BDD\u7684\u65F6\u5019\uFF0C\u770B\uFF01\u90A3\u5341\u4E8C\u4EBA\u4E2D\u4E4B\u4E00\u7684\u72B9\u8FBE\u65AF\u6765\u4E86\uFF1B\u540C\u4ED6\u4E00\u8D77\u7684\uFF0C\u8FD8\u6709\u8BB8\u591A\u5E26\u7740\u5200\u5251\u68CD\u68D2\u7684\u7FA4\u4F17\uFF0C\u662F\u7531\u53F8\u796D\u957F\u548C\u6C11\u95F4\u7684\u957F\u8001\u6D3E\u6765\u7684\u3002<sup>48</sup> \u90A3\u51FA\u5356\u8036\u7A23\u7684\u7ED9\u4E86\u4ED6\u4EEC\u4E00\u4E2A\u6697\u53F7\u8BF4\uFF1A\u201C\u6211\u53E3\u4EB2\u8C01\uFF0C\u8C01\u5C31\u662F\uFF0C\u4F60\u4EEC\u62FF\u4F4F\u4ED6\u3002\u201D<sup>49</sup> \u72B9\u8FBE\u65AF\u4E00\u6765\u5230\u8036\u7A23\u8DDF\u524D\uFF0C\u5C31\u8BF4\uFF1A\u201C\u8FA3\u5F7C\uFF0C\u4F60\u597D\uFF01\u201D\u5C31\u53E3\u4EB2\u4E86\u4ED6\u3002<sup>50</sup> \u8036\u7A23\u5374\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u670B\u53CB\uFF0C\u4F60\u6765\u505A\u7684\u4E8B\u5C31\u505A\u5427\uFF01\u201D\u4E8E\u662F\u4ED6\u4EEC\u4E0A\u524D\uFF0C\u5411\u8036\u7A23\u4E0B\u624B\uFF0C\u62FF\u4F4F\u4E86\u4ED6\u3002 <sup>51</sup> \u6709\u540C\u8036\u7A23\u5728\u4E00\u8D77\u7684\u4E00\u4E2A\u4EBA\uFF0C \u4F38\u624B\u62D4\u51FA\u81EA\u5DF1\u7684\u5251\uFF0C\u780D\u4E86\u5927\u53F8\u796D\u7684\u4EC6\u4EBA\u4E00\u5251\u524A\u53BB\u4E86\u4ED6\u7684\u4E00\u4E2A\u8033\u6735\u3002<sup>52</sup> \u8036\u7A23\u9042\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u628A\u4F60\u7684\u5251\u653E\u56DE\u539F\u5904\uFF0C\u56E0\u4E3A\u51E1\u6301\u5251\u7684\uFF0C\u5FC5\u6B7B\u5728\u5251\u4E0B\u3002<sup>53</sup> \u4F60\u60F3\u6211\u4E0D\u80FD\u8981\u6C42\u6211\u7236\uFF0C\u5373\u523B\u7ED9\u6211\u8C03\u52A8\u5341\u4E8C\u519B\u4EE5\u4E0A\u7684\u5929\u4F7F\u5417\uFF1F<sup>54</sup> \u82E5\u8FD9\u6837\uFF0C\u600E\u80FD\u5E94\u9A8C\u7ECF\u4E0A\u6240\u8F7D\u5E94\u5982\u6B64\u6210\u5C31\u7684\u4E8B\u5462\uFF1F\u201D<sup>55</sup> \u5728\u90A3\u65F6\uFF0C\u8036\u7A23\u5BF9\u7FA4\u4F17\u8BF4\uFF1A\u201C\u4F60\u4EEC\u5E26\u7740\u5200\u5251\u68CD\u68D2\u51FA\u6765\u62FF\u6211\uFF0C\u5982\u540C\u5BF9\u4ED8\u5F3A\u76D7\u3002\u6211\u5929\u5929\u5750\u5728\u5723\u6BBF\u5185\u65BD\u6559\uFF0C\u4F60\u4EEC\u6CA1\u6709\u62FF\u6211\u3002\u201D<sup>56</sup> \u8FD9\u4E00\u5207\u90FD\u53D1\u751F\u4E86\uFF0C\u662F\u4E3A\u5E94\u9A8C\u5148\u77E5\u6240\u8BB0\u8F7D\u7684\u3002\u4E8E\u662F\u95E8\u5F92\u90FD\u6487\u4E0B\u4ED6\u9003\u8DD1\u4E86\u3002</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-5" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-5" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u72B9\u8FBE\u65AF\u548C\u4E00\u7FA4\u53D7\u96C7\u4E8E\u53F8\u796D\u957F\u8001, \u624B\u6267\u6B66\u5668\u7684\u4EBA\u8D70\u8FD1\u4E86. \u8036\u7A23\u5BF9\u72B9\u8FBE\u65AF\u8BF4\u7684\u8BDD\u53EF\u80FD\u662F\u53E5\u95EE\u8BDD: &quot;\u670B\u53CB\u4F60\u4E3A\u4F55\u800C\u6765?&quot; \u4E5F\u53EF\u80FD\u662F\u4E2A\u76F4\u53D9\u53E5: &quot;\u4F60\u6240\u8981\u505A\u7684, \u5FEB\u53BB\u505A\u5427!&quot; \u53EA\u6709\u739B\u7AA6\u8BB0\u8F7D\u8036\u7A23\u65A5\u9000\u4E86\u4F01\u56FE\u7528\u6B66\u529B\u4FDD\u62A4\u4ED6\u7684\u95E8\u5F92\u8FD9\u4EF6\u4E8B. \u8FD9\u65F6, \u8036\u7A23\u7ED9\u4E86\u6559\u4F1A\u4E00\u4E2A\u539F\u5219, \u5728\u6559\u96BE\u4E4B\u4E2D, \u6559\u4F1A\u90FD\u5C06\u5949\u884C\u4E0D\u6E1D: \u552F\u6709\u5BF9\u5929\u4E3B\u7684\u7EDD\u5BF9\u4FE1\u8D56, \u624D\u80FD\u591F\u5BF9\u66B4\u6297\u66B4\u529B\u7684\u4FB5\u88AD. \u8036\u7A23\u77E5\u9053\u4ED6\u62C5\u8D1F\u7740\u4E0A\u4E3B\u4EC6\u4EBA\u7684\u91CD\u4EFB, \u5FC5\u987B\u7ECF\u5386\u8FD9\u4E00\u5207\u7684\u82E6\u96BE. \u739B\u7AA6\u5728\u8FD9\u91CC\u63D2\u5165\u7684\u53E6\u4E00\u6BB5\u8036\u7A23\u7684\u8BDD, \u6309\u82E5\u671B\u7684\u8BB0\u8F7D, \u5B83\u662F\u5BF9\u4E9A\u7EB3\u65AF\u800C\u53D1\u7684.</p><h2 id="\u76D6\u6CD5\u521D\u5BA1\u8036\u7A23" tabindex="-1"><a class="header-anchor" href="#\u76D6\u6CD5\u521D\u5BA1\u8036\u7A23" aria-hidden="true">#</a> \u76D6\u6CD5\u521D\u5BA1\u8036\u7A23</h2><h3 id="\u7ECF\u6587-8" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-8" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>57</sup> \u90A3\u4E9B\u62FF\u4F4F\u8036\u7A23\u7684\u4EBA\uFF0C\u5C06\u8036\u7A23\u5E26\u5230\u5927\u53F8\u796D\u76D6\u6CD5\u524D\uFF1B\u7ECF\u5E08\u548C\u957F\u8001\u5DF2\u805A\u96C6\u5728\u90A3\u91CC\u3002<sup>58</sup> \u4F2F\u591A\u7984\u8FDC\u8FDC\u8DDF\u7740\u8036\u7A23\uFF0C\u76F4\u5230\u5927\u53F8\u796D\u7684\u5EAD\u9662\uFF0C\u4ED6\u4E5F\u8FDB\u5230\u91CC\u9762\uFF0C\u5750\u5728\u5DEE\u5F79\u4E2D\u89C2\u770B\u7ED3\u5C40\u3002<sup>59</sup> \u53F8\u796D\u957F\u548C\u5168\u516C\u8BAE\u4F1A\u5BFB\u627E\u76F8\u53CD\u8036\u7A23\u7684\u5047\u8BC1\u636E\uFF0C\u8981\u628A\u4ED6\u5904\u6B7B\u3002<sup>60</sup> \u867D\u7136\u6709\u8BB8\u591A\u5047\u89C1\u8BC1\u51FA\u5EAD\uFF0C\u4F46\u6CA1\u6709\u627E\u51FA\u4EC0\u4E48\u3002\u6700\u540E\u6709\u4E24\u4E2A\u4EBA\u4E0A\u524D\u6765\uFF0C<sup>61</sup> \u8BF4\uFF1A\u201C\u8FD9\u4EBA\u66FE\u7ECF\u8BF4\u8FC7\uFF1A\u6211\u80FD\u62C6\u6BC1\u5929\u4E3B\u7684\u5723\u6BBF\uFF0C\u5728\u4E09\u5929\u5185\u6211\u80FD\u628A\u5B83\u91CD\u5EFA\u8D77\u6765\u3002\u201D<sup>62</sup> \u5927\u53F8\u796D\u5C31\u7AD9\u8D77\u6765\uFF0C\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u8FD9\u4E9B\u4EBA\u4F5C\u8BC1\u53CD\u5BF9\u4F60\u7684\u4E8B\uFF0C\u4F60\u4EC0\u4E48\u4E5F\u4E0D\u56DE\u7B54\u5417\uFF1F\u201D<sup>63</sup> \u8036\u7A23\u5374\u4E0D\u51FA\u58F0\u3002\u4E8E\u662F\u5927\u53F8\u796D\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u6211\u56E0\u751F\u6D3B\u7684\u5929\u4E3B\uFF0C\u8D77\u8A93\u547D\u4F60\u544A\u8BC9\u6211\u4EEC\uFF1A\u4F60\u662F\u4E0D\u662F\u9ED8\u897F\u4E9A\uFF0C\u5929\u4E3B\u4E4B\u5B50\uFF1F\u201D<sup>64</sup> \u8036\u7A23\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u4F60\u8BF4\u7684\u662F\u3002\u5E76\u4E14\uFF0C\u6211\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4ECE\u6B64\u4F60\u4EEC\u5C06\u8981\u770B\u89C1\u4EBA\u5B50\u5750\u5728\u5927\u80FD\u8005\u7684\u53F3\u8FB9\uFF0C\u4E58\u7740\u5929\u4E0A\u7684\u4E91\u5F69\u964D\u6765\u3002\u201D<sup>65</sup> \u5927\u53F8\u796D\u9042\u6495\u88C2\u81EA\u5DF1\u7684\u8863\u670D\u8BF4\uFF1A\u201C\u4ED6\u8BF4\u4E86\u4EB5\u6E0E\u7684\u8BDD\u3002\u4F55\u5FC5\u8FD8\u9700\u8981\u89C1\u8BC1\u5462\uFF1F\u4F60\u4EEC\u521A\u624D\u542C\u5230\u4E86\u8FD9\u4EB5\u6E0E\u7684\u8BDD\uFF0C<sup>66</sup> \u4F60\u4EEC\u4EE5\u4E3A\u8BE5\u600E\u6837\uFF1F\u201D\u4ED6\u4EEC\u56DE\u7B54\u8BF4\uFF1A\u201C\u4ED6\u8BE5\u6B7B\u3002\u201D<sup>67</sup> \u4F17\u4EBA\u9042\u5373\u5411\u4ED6\u8138\u4E0A\u5410\u553E\u6CAB\uFF0C\u7528\u62F3\u5934\u6253\u4ED6\uFF1B\u53E6\u6709\u4E00\u4E9B\u4EBA\u4E5F\u7528\u5DF4\u638C\u6253\u4ED6\uFF0C<sup>68</sup> \u8BF4\uFF1A\u201C\u9ED8\u897F\u4E9A\uFF0C\u4F60\u731C\u731C\u662F\u8C01\u6253\u4F60\uFF1F\u201D</p></blockquote><h2 id="\u4F2F\u591A\u7984\u4E09\u6B21\u80CC\u4E3B" tabindex="-1"><a class="header-anchor" href="#\u4F2F\u591A\u7984\u4E09\u6B21\u80CC\u4E3B" aria-hidden="true">#</a> \u4F2F\u591A\u7984\u4E09\u6B21\u80CC\u4E3B</h2><h3 id="\u7ECF\u6587-9" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-9" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>69</sup> \u4F2F\u591A\u7984\u5728\u5916\u9762\u5EAD\u9662\u91CC\u5750\u7740\uFF0C\u6709\u4E00\u4E2A\u4F7F\u5973\u6765\u5230\u4ED6\u8DDF\u524D\u8BF4\uFF1A\u201C\u4F60\u4E5F\u662F\u540C\u90A3\u52A0\u91CC\u808B\u4E9A\u4EBA\u8036\u7A23\u4E00\u8D77\u7684\u3002\u201D<sup>70</sup> \u4ED6\u5F53\u7740\u4F17\u4EBA\u5426\u8BA4\u8BF4\uFF1A\u201C\u6211\u4E0D\u77E5\u9053\u4F60\u8BF4\u7684\u662F\u4EC0\u4E48\u3002\u201D<sup>71</sup> \u4ED6\u51FA\u53BB\u5230\u4E86\u95E8\u5ECA\uFF0C\u53E6\u6709\u4E00\u4E2A\u4F7F\u5973\u770B\u89C1\u4ED6\uFF0C\u5C31\u5BF9\u90A3\u91CC\u7684\u4EBA\u8BF4\uFF1A\u201C\u8FD9\u4EBA\u662F\u540C\u90A3\u7EB3\u531D\u808B\u4EBA\u8036\u7A23\u4E00\u8D77\u7684\u3002\u201D<sup>72</sup> \u4ED6\u53C8\u53D1\u8A93\u5426\u8BA4\u8BF4\uFF1A\u201C\u6211\u4E0D\u8BA4\u8BC6\u8FD9\u4E2A\u4EBA\u3002\u201D<sup>73</sup> \u8FC7\u4E86\u4E00\u4F1A\uFF0C\u7AD9\u5728\u90A3\u91CC\u7684\u4EBA\u524D\u6765\u5BF9\u4F2F\u591A\u7984\u8BF4\uFF1A\u201C\u7684\u786E\uFF0C\u4F60\u4E5F\u662F\u4ED6\u4EEC\u4E2D\u7684\u4E00\u4E2A\uFF0C\u56E0\u4E3A\u4F60\u7684\u53E3\u97F3\u628A\u4F60\u9732\u51FA\u6765\u4E86\u201D<sup>74</sup> \u4F2F\u591A\u7984\u5C31\u5F00\u59CB\u8BC5\u5492\u53D1\u8A93\u8BF4\uFF1A\u201C\u6211\u4E0D\u8BA4\u8BC6\u8FD9\u4E2A\u4EBA\u3002\u201D\u7ACB\u65F6\u9E21\u5C31\u53EB\u4E86\u3002<sup>75</sup> \u4F2F\u591A\u7984\u4FBF\u60F3\u8D77\u8036\u7A23\u6240\u8BF4\u7684\u8BDD\u6765\uFF1A\u201C\u9E21\u53EB\u4EE5\u524D\uFF0C\u4F60\u8981\u4E09\u6B21\u4E0D\u8BA4\u6211\u3002\u201D\u4ED6\u4E00\u5230\u4E86\u5916\u9762\uFF0C\u5C31\u4F24\u5FC3\u75DB\u54ED\u8D77\u6765\u3002</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-6" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-6" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5173\u4E8E\u8036\u7A23\u88AB\u72B9\u592A\u9886\u8896\u5BA1\u95EE\u7684\u786E\u5B9E\u65F6\u95F4, \u798F\u97F3\u4F20\u7EDF\u4E2D\u7F3A\u4E4F\u53EF\u9760\u7684\u8BB0\u8F7D. \u739B\u7AA6\u548C\u9A6C\u5C14\u8C37\u90FD\u63D0\u5230\u4E24\u6B21\u5F00\u5EAD\u4FA6\u8BAF: \u4E00\u4E2A\u662F\u5728\u591C\u665A ( \u663E\u7136\u4E0D\u662F\u5B98\u65B9\u7684\u542C\u8BC1 ), \u53E6\u4E00\u4E2A\u662F\u5728\u7834\u6653\u65F6\u5206. \u72B9\u592A\u4EBA\u4E3A\u4F55\u5982\u6B64\u5B89\u6392, \u9A6C\u5C14\u8C37\u798F\u97F3\u672A\u4EA4\u4EE3\u6E05\u695A, \u739B\u7AA6\u5374\u89E3\u7FFB\u5F97\u4E0D\u751A\u9AD8\u660E: &quot;\u6765\u8BA1\u5212\u5982\u4F55\u53EF\u4EE5\u5C06\u4ED6\u5904\u6B7B&quot;. \u8DEF\u52A0\u798F\u97F3\u5219\u8BB0\u8F7D\u8036\u7A23\u88AB\u62D8\u6355\u4EE5\u540E, \u7ACB\u523B\u88AB\u5E26\u5230 &quot;\u5927\u53F8\u796D\u7684\u4F4F\u5B85&quot;, \u6574\u4E2A\u665A\u4E0A, \u4ED6\u5728\u90A3\u91CC\u5907\u53D7\u51CC\u8FB1. \u6309\u7167\u4EBA\u4E4B\u5E38\u60C5\u800C\u8BBA, \u6211\u4EEC\u4E0D\u96BE\u4E86\u89E3\u5230\u8036\u7A23\u4E00\u5B9A\u4E0D\u662F\u5728\u5B98\u65B9\u7684\u76D1\u72F1\u91CC, \u5426\u5219\u4ED6\u4E0D\u4F1A\u53D7\u5230\u8FD9\u79CD\u8650\u5F85. \u8DEF\u52A0\u8FD8\u660E\u767D\u7684\u5F3A\u8C03, \u8036\u7A23\u5BA1\u5224\u4E00\u76F4\u8981\u5230\u5929\u4EAE\u624D\u8FDB\u884C; \u82E5\u671B\u798F\u97F3\u7ED9\u4E86\u6211\u4EEC\u7684\u7591\u60D1\u4E00\u4E2A\u7ED3\u8BBA: \u8036\u7A23\u9996\u5148\u975E\u6B63\u5F0F\u7684\u88AB\u4E9A\u7EB3\u65AF\u5BA1\u5224 ( \u4E9A\u7EB3\u65AF\u66FE\u505A\u5927\u53F8\u796D, \u4EE5\u540E\u4ECD\u7EF4\u6301\u8FD9\u4E2A\u79F0\u53F7 ), \u540E\u6765\u53C8\u88AB\u62BC\u9001\u5230\u76D6\u6CD5\u90A3\u8FB9, \u76D6\u6CD5\u4EE5\u5927\u53F8\u796D\u7684\u8EAB\u4EFD\u5C06\u8981\u5728\u6B21\u65E5\u4E3B\u6301\u516C\u8BAE\u4F1A\u5BF9\u8036\u7A23\u7684\u6B63\u5F0F\u5BA1\u5224. \u65E9\u671F\u53E3\u4F20\u798F\u97F3\u91CC\u5BF9\u8FD9\u4E00\u95EE\u9898\u6240\u5B58\u7684\u8FF7\u60D1 ( \u7531\u739B\u7AA6\u548C\u9A6C\u5C14\u8C37\u798F\u97F3\u7684\u8BB0\u8F7D\u53EF\u8BC1\u660E ) \u53EF\u80FD\u662F\u56E0\u4E3A\u5F04\u4E0D\u6E05\u695A\u4E9A\u7EB3\u65AF\u7684 &quot;\u5927\u53F8\u796D&quot; \u7684\u5934\u8854\u7684\u786E\u5B9E\u610F\u4E49, \u800C\u5BFC\u81F4\u4E00\u4E2A\u4E0D\u751A\u6070\u5F53\u7684\u7ED3\u8BBA: \u8036\u7A23\u5728\u4ED6\u88AB\u6355\u7684\u5F53\u665A\u5C31\u88AB\u5927\u53F8\u796D\u76D6\u6CD5\u53CA\u516C\u8BAE\u4F1A\u6B63\u5F0F\u7684\u5BA1\u5224 ( \u8FD9\u5728\u5F53\u65F6\u6CD5\u5F8B\u6240\u4E0D\u5BB9\u8BB8 ).</p><p>\u4F1A\u8BAE\u4F1A\u627E\u4E0D\u51FA\u4E24\u4E2A\u4EE5\u4E0A\u8BC1\u8BCD\u4E00\u81F4\u7684\u8BC1\u4EBA ( \u8FD9\u662F\u6CD5\u5F8B\u6240\u8981\u6C42\u7684 ). \u739B\u7AA6\u628A\u4E24\u4E2A\u8BC1\u4EBA\u7684\u6258\u8BCD ( \u4ED6\u4EEC\u53EF\u80FD\u662F\u5206\u522B\u4F5C\u8BC1\u7684 ) \u8054\u6210\u4E00\u4E2A\u5355\u53E5. \u53EF\u80FD\u6709\u4E00\u4E2A\u4EBA\u6307\u63A7\u8036\u7A23\u7684\u8C6A\u8BED, &quot;\u6211\u80FD\u62C6\u6BC1\u5929\u4E3B\u7684\u5723\u6BBF&quot;, \u53E6\u4E00\u4E2A\u6307\u63A7\u4ED6\u7684 &quot;\u6211\u80FD\u5728\u4E09\u5929\u5185\u628A\u5723\u6BBF\u91CD\u5EFA\u8D77\u6765.&quot; \u76D6\u6CD5\u547D\u4EE4\u8036\u7A23\u56DE\u7B54\u8FD9\u4E9B\u63A7\u8BC9, \u4F46\u4ED6\u62D2\u7EDD\u4E86. \u7136\u540E, \u76D6\u6CD5\u4EE5\u8D77\u8A93\u4EE4\u8036\u7A23\u56DE\u7B54\u90A3\u6700\u91CD\u8981\u7684\u95EE\u9898: &quot;\u4F60\u662F\u4E0D\u662F\u57FA\u7763, \u5929\u4E3B\u5B50?&quot; \u8FD9\u65F6, \u76D6\u6CD5\u7684\u7F3A\u4E4F\u4FE1\u5FB7\u5F70\u663E\u65E0\u9057, \u4ED6\u7684\u8BE2\u95EE\u53EF\u80FD\u53EA\u6307\u8036\u7A23\u7684\u81EA\u8BA4\u4E3A &quot;\u9ED8\u897F\u4E9A&quot;; &quot;\u5929\u4E3B\u5B50&quot; \u662F\u9ED8\u897F\u4E9A\u7684\u540C\u4E49\u5B57. \u4F46\u662F, \u8036\u7A23\u7684\u56DE\u7B54\u5374\u8FDC\u6BD4\u8FD9\u4E2A\u95EE\u9898\u66F4\u4E30\u5BCC, \u66F4\u6DF1\u8FDC: \u4ED6\u627F\u8BA4\u4E86\u4ED6\u7684\u9ED8\u897F\u4E9A\u8EAB\u4EFD, \u5750\u5728 &quot;\u5927\u80FD\u8005\u7684\u53F3\u8FB9&quot; \u65F6, \u4ED6\u5C06\u8981\u8BC1\u660E\u8FD9\u5929\u4E3B\u6027, \u76D6\u6CD5\u4F3C\u61C2\u975E\u61C2\u7684\u770B\u5230\u8036\u7A23\u5BA3\u8A00\u66F4\u6DF1\u4E00\u5C42\u7684\u542B\u4E49, \u5C31\u6307\u8D23\u8036\u7A23\u4EB5\u6E0E\u5929\u4E3B. \u6CE8\u610F, \u5728\u6B64, \u516C\u8BAE\u4F1A\u5E76\u6CA1\u6709\u5224\u4ED6\u6B7B\u5211, \u56E0\u4E3A\u5728\u90A3\u4E2A\u65F6\u4EE3, \u516C\u8BAE\u4F1A\u5E76\u6CA1\u6709\u90A3\u4E2A\u6743\u67C4, \u4ED6\u4EEC\u53EA\u80FD\u8868\u793A\u4ED6\u4EEC\u5224\u51B3\u7684\u610F\u89C1: &quot;\u4ED6\u8BE5\u6B7B&quot;. \u739B\u7AA6\u798F\u97F3\u4E2D\u73B0\u5728\u6240\u8BF4\u7684\u8036\u7A23\u53D7\u8FB1, \u66F4\u53EF\u80FD\u5E94\u8BE5\u7167\u8DEF\u52A0\u798F\u97F3\u6240\u8BF4\u7684, \u653E\u5728\u6B63\u5F0F\u5BA1\u5224\u4E4B\u524D.</p><p>\u56DB\u90E8\u798F\u97F3\u90FD\u8BC1\u660E\u4E86\u4F2F\u591A\u7984\u7684\u4E09\u6B21\u80CC\u4E3B, \u867D\u7136\u5728\u7EC6\u8282\u65B9\u9762\u6709\u6240\u51FA\u5165. \u739B\u7AA6\u798F\u97F3\u91CC\u53D1\u95EE\u8005\u662F (1) \u4E00\u4E2A\u4F7F\u5973, (2) \u53E6\u4E00\u4E2A\u4F7F\u5973 (3) \u4E00\u4E2A\u65C1\u89C2\u8005. \u9A6C\u5C14\u8C37\u798F\u97F3\u91CC\u5219\u662F (1) \u4E00\u4E2A\u4F7F\u5973, (2) \u540C\u4E2A\u4F7F\u5973, (3) \u90A3\u4E2A\u65C1\u89C2\u8005. \u8DEF\u52A0\u798F\u97F3\u8BB0\u8F7D\u7684\u662F (1) \u4E00\u4E2A\u4F7F\u5973\uFF0C (2) \u4E00\u4E2A\u7537\u4EBA, (3) \u53E6\u4E00\u4E2A\u7537\u4EBA. \u82E5\u671B\u798F\u97F3\u91CC\u53D1\u95EE\u7684\u662F (1) \u770B\u95E8\u7684\u5F85\u5973\uFF0C (2) &quot;\u4ED6\u4EEC&quot;, (3) \u4E00\u4E2A\u7537\u4EC6, \u739B\u5C14\u66F7\u7684\u4EB2\u621A. \u5728\u8FD9\u79CD\u7C7B\u578B\u7684\u53D9\u8FF0\u91CC, \u8FD9\u6837\u7684\u53D8\u5316\u662F\u5F88\u81EA\u7136\u7684. \u4E9A\u62C9\u7F8E\u8BED\u7CFB\u4E2D, \u52A0\u91CC\u808B\u4E9A\u548C\u72B9\u5927\u4E24\u5730\u7684\u65B9\u8A00\u4E4B\u95F4, \u65B9\u6CD5, \u53D1\u97F3\u90FD\u663E\u8457\u7684\u533A\u522B.</p>',51),h=[t];function o(r,d){return a(),s("div",null,h)}var q=u(p,[["render",o],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u516D\u7AE0.html.vue"]]);export{q as default};
