import{_ as u,o as a,c as e,e as p}from"./app.6bf529a6.js";const t={},s=p('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u56DB\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u56DB\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u56DB\u7AE0</h1><h2 id="\u9884\u8A00\u8036\u8DEF\u6492\u51B7\u548C\u5723\u6BBF\u7684\u6BC1\u706D" tabindex="-1"><a class="header-anchor" href="#\u9884\u8A00\u8036\u8DEF\u6492\u51B7\u548C\u5723\u6BBF\u7684\u6BC1\u706D" aria-hidden="true">#</a> \u9884\u8A00\u8036\u8DEF\u6492\u51B7\u548C\u5723\u6BBF\u7684\u6BC1\u706D</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u8036\u7A23\u7531\u5723\u6BBF\u91CC\u51FA\u6765\u524D\u884C\uFF0C\u95E8\u5F92\u524D\u6765\u628A\u5723\u6BBF\u7684\u5EFA\u7B51\u6307\u7ED9\u4ED6\u770B\u3002<sup>2</sup> \u8036\u7A23\u56DE\u7B54\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u4F60\u4EEC\u4E0D\u662F\u770B\u89C1\u8FD9\u4E00\u5207\u5417\uFF1F\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u5C06\u6765\u8FD9\u91CC\u51B3\u6CA1\u6709\u4E00\u5757\u77F3\u5934\uFF0C\u7559\u5728\u53E6\u4E00\u5757\u77F3\u5934\u4E0A\uFF0C\u800C\u4E0D\u88AB\u62C6\u6BC1\u7684\u3002\u201D<sup>3</sup> \u4ED6\u5728\u6A44\u6984\u5C71\u4E0A\u5750\u7740\u7684\u65F6\u5019\uFF0C\u95E8\u5F92\u524D\u6765\u79C1\u4E0B\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u8BF7\u544A\u8BC9\u6211\u4EEC\uFF1A\u4EC0\u4E48\u65F6\u5019\u8981\u53D1\u751F\u8FD9\u4E9B\u4E8B\uFF1F\u53C8\u4EC0\u4E48\u662F\u4F60\u6765\u4E34\u548C\u65F6\u4EE3\u7A77\u5C3D\u7684\u5148\u5146\uFF1F\u201D<sup>4</sup> \u8036\u7A23\u56DE\u7B54\u8BF4\uFF1A\u201C\u4F60\u4EEC\u8981\u8C28\u614E\uFF0C\u514D\u5F97\u6709\u4EBA\u6B3A\u9A97\u4F60\u4EEC\uFF0C<sup>5</sup> \u56E0\u4E3A\u5C06\u6709\u8BB8\u591A\u4EBA\u5047\u5192\u6211\u7684\u540D\u5B57\u6765\u8BF4\uFF1A\u6211\u5C31\u662F\u9ED8\u897F\u4E9A\uFF1B\u4ED6\u4EEC\u8981\u6B3A\u9A97\u8BB8\u591A\u4EBA\u3002<sup>6</sup> \u4F60\u4EEC\u8981\u542C\u5230\u6218\u4E89\u548C\u6218\u4E89\u7684\u98CE\u58F0\u3002\u5C0F\u5FC3\uFF0C\u4E0D\u8981\u60CA\u614C\uFF01\u56E0\u4E3A\u8FD9\u662F\u5FC5\u987B\u53D1\u751F\u7684\uFF0C\u4F46\u8FD8\u4E0D\u662F\u7ED3\u5C40\uFF0C<sup>7</sup> \u56E0\u4E3A\u6C11\u65CF\u8981\u8D77\u6765\u653B\u51FB\u6C11\u65CF\uFF0C\u56FD\u5BB6\u8981\u8D77\u6765\u653B\u51FB\u56FD\u5BB6\uFF1B\u5E76\u4E14\u5230\u5904\u8981\u6709\u9965\u8352\u3001\u761F\u75AB\u548C\u5730\u9707\u3002<sup>8</sup> \u4F46\u662F\uFF0C\u8FD9\u4E00\u5207\u53EA\u662F\u75DB\u82E6\u7684\u5F00\u59CB\u3002</p></blockquote><blockquote><p><sup>9</sup> \u90A3\u65F6\uFF0C\u4EBA\u8981\u4EA4\u4ED8\u4F60\u4EEC\u53BB\u53D7\u5211\uFF0C\u8981\u6740\u5BB3\u4F60\u4EEC\uFF1B\u4F60\u4EEC\u4E3A\u4E86\u6211\u7684\u540D\u5B57\uFF0C\u8981\u4E3A\u5404\u6C11\u65CF\u6240\u618E\u6068\u3002<sup>10</sup> \u90A3\u65F6\uFF0C\u5FC5\u6709\u8BB8\u591A\u4EBA\u8981\u8DCC\u5012\uFF0C\u4E92\u76F8\u51FA\u5356\uFF0C\u4E92\u76F8\u4EC7\u6068\uFF1B<sup>11</sup> \u8BB8\u591A\u5047\u5148\u77E5\u8981\u8D77\u6765\uFF0C\u6B3A\u9A97\u8BB8\u591A\u4EBA\uFF1B<sup>12</sup> \u7531\u4E8E\u7F6A\u6076\u7684\u589E\u52A0\uFF0C\u8BB8\u591A\u4EBA\u7684\u7231\u60C5\u5FC5\u8981\u51B7\u6DE1\u3002<sup>13</sup> \u552F\u72EC\u575A\u6301\u5230\u5E95\u7684\uFF0C\u624D\u53EF\u5F97\u6551\u3002<sup>14</sup> \u5929\u56FD\u7684\u798F\u97F3\u5FC5\u5148\u5728\u5168\u4E16\u754C\u5BA3\u8BB2\uFF0C\u7ED9\u4E07\u6C11\u4F5C\u8BC1\uFF1B\u7136\u540E\u7ED3\u5C40\u624D\u4F1A\u6765\u5230\u3002</p></blockquote><blockquote><p><sup>15</sup> \u6240\u4EE5\uFF0C\u51E0\u65F6\u4F60\u4EEC\u89C1\u5230\u8FBE\u5C3C\u5C14\u6240\u8BF4\u7684\u2018\u62DB\u81F4\u8352\u51C9\u7684\u53EF\u618E\u4E4B\u7269\u2019\u5DF2\u7ACB\u4E8E\u5723\u5730\uFF0C\u2500\u2500\u8BFB\u8005\u5E94\u660E\u767D\uFF1A <sup>16</sup> \u90A3\u65F6\u5728\u72B9\u592A\u7684\uFF0C\u8BE5\u9003\u5F80\u5C71\u4E2D\uFF1B<sup>17</sup> \u5728\u5C4B\u9876\u4E0A\u7684\uFF0C\u4E0D\u8981\u4E0B\u6765\uFF0C\u4ECE\u81EA\u5DF1\u7684\u5C4B\u91CC\uFF0C\u53D6\u4EC0\u4E48\u4E1C\u897F\uFF1B<sup>18</sup> \u5728\u7530\u91CE\u91CC\u7684\uFF0C\u4E5F\u4E0D\u8981\u56DE\u6765\u53D6\u4ED6\u7684\u5916\u8863\u3002<sup>19</sup> \u5728\u90A3\u4E9B\u65E5\u5B50\u5185\uFF0C\u6000\u5B55\u7684\u548C\u54FA\u4E73\u7684\uFF0C\u662F\u6709\u7978\u7684\u3002<sup>20</sup> \u4F60\u4EEC\u5F53\u7948\u7977\uFF0C\u4E0D\u8981\u53EB\u4F60\u4EEC\u7684\u9003\u9041\u9047\u5230\u51AC\u5929\u6216\u5B89\u606F\u65E5\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u756A\u9053\u7406\u53EF\u5206\u4E3A\u4E09\u6BB5, \u4E3B\u8981\u662F\u6709\u5173\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D. \u7136\u540E\u4EE5\u4E09\u4E2A\u5BD3\u8A00\u6765\u8BF4\u660E\u6559\u4F1A\u91CC\u67D0\u7FA4\u4EBA\u7269\u7684\u547D\u8FD0, \u4EE5\u53CA\u4E00\u4E2A\u5BF9\u6700\u540E\u5BA1\u5224\u6709\u63CF\u7ED8.</p><p>\u5F53\u8036\u7A23\u5BA3\u4F48\u5723\u6BBF\u6BC1\u6765\u65F6, \u95E8\u5F92\u95EE\u4ED6\u8FD9\u4E8B\u53D1\u751F\u7684 &quot;\u65F6\u95F4&quot; \u548C &quot;\u5148\u5146&quot;. \u53EA\u6709\u739B\u7AA6\u4FDD\u7559\u4E86\u4ED6\u4EEC\u5185\u5FC3\u7684\u7591\u60D1, \u5E76\u4E14\u53CD\u6620\u5728\u7B2C\u4E8C\u4E2A\u95EE\u9898\u4E0A: &quot;\u4EC0\u4E48\u662F\u4F60\u6765\u4E34\u548C\u65F6\u4EE3\u7A77\u5C3D\u7684\u5148\u5146?&quot; \u95E8\u5F92\u4EEC ( \u9519\u8BEF\u5730 ) \u5047\u8BBE\u5723\u6BBF\u7684\u672B\u65E5\u5C31\u662F\u6307\u4E16\u754C\u7684\u672B\u65E5, \u56E0\u4E3A\u4F9D\u6492\u610F\u4E9A\u63CF\u8FF0\u5199\u7684\u9ED8\u897F\u4E9A\u65F6\u4EE3\u662F\u4EE5\u5723\u6BBF\u4E3A\u4E2D\u5FC3. \u5176\u5B9E\u8FD9\u4E2A\u707E\u53D8\u6307\u7684\u662F\u65E7\u7EA6\u4E16\u754C\u7684\u6BC1\u706D, \u4EE5\u53CA\u57FA\u7763\u4FE1\u4EF0\u8131\u79BB\u72B9\u592A\u6559\u800C\u83B7\u5F97\u81EA\u7531. \u8036\u7A23\u62D2\u7EDD\u6CC4\u9732\u8FD9\u4E8B &quot;\u4F55\u65F6&quot; \u53D1\u751F, \u4F46\u4ED6\u7684\u786E\u9884\u8A00\u4E86\u90A3\u4E2A &quot;\u5148\u5146&quot;, \u5E76\u5728\u516C\u5143\u4E03\u5341\u5E74\u4EE5\u524D\u90FD\u5B9E\u73B0\u4E86. \u8BB8\u591A\u5047\u5148\u77E5\u4F1A\u76F8\u7EE7\u51FA\u73B0, \u4EBA\u4EEC\u4F1A\u542C\u5230\u6218\u4E89\u7684\u98CE\u58F0. \u57FA\u7763\u5F92\u8981\u63D0\u9632\u4E0D\u8981\u4E3A\u5047\u5148\u77E5\u6240\u9A97, \u4E5F\u4E0D\u8981\u88AB\u6218\u4E89\u98CE\u58F0\u60CA\u5413; \u8FD9 &quot;\u8FD8\u4E0D\u662F\u7ED3\u5C40&quot;. \u8FD9\u79CD\u653F\u6CBB\u4E0A\u548C\u81EA\u7136\u754C\u7684\u5267\u53D8\u662F &quot;\u82E6\u75DB\u7684\u5F00\u7AEF&quot;, \u7ED3\u679C\u9020\u6210\u6559\u4F1A\u65F6\u4EE3\u7684\u8BDE\u751F. \u5728\u516C\u5143\u4E03\u5341\u5E74\u4EE5\u524D\u7684\u5E74\u4EE3\u91CC, \u5E74\u9752\u7684\u6559\u4F1A\u5C06\u9047\u5230\u4E00\u8FDE\u4E32\u7684\u5371\u673A; \u6B89\u6559, \u80CC\u6559, \u6700\u521D\u6240\u6709\u53F2\u5F1F\u822C\u70ED\u7231\u7684\u6D88\u901D. \u4E0D\u8FC7\u8010\u5FC3\u7684\u575A\u6301\u5C06\u4F1A\u83B7\u5F97\u6700\u540E\u7684\u80DC\u5229. \u798F\u97F3\u5C06\u904D\u4F20\u6574\u4E2A\u5730\u4E2D\u6D77\u5CB8\u7684\u56FD\u5BB6, \u4FDD\u7984\u5728\u516C\u5143\u4E94\u5341\u516B\u5E74\u5C31\u8BC1\u5B9E\u4E86\u8FD9\u4E00\u70B9, \u7136\u540E &quot;\u7ED3\u5C40\u624D\u4F1A\u5230\u6765&quot;.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u548C\u5723\u6BBF\u5B97\u6559\u9886\u8896\u7684\u5173\u7CFB\u65E5\u8D8B\u7D27\u5F20. \u5728\u5723\u6BBF\u91CC, \u8036\u7A23\u9996\u5148\u4EE5\u5148\u77E5\u6027\u7684\u884C\u52A8\u6E05\u9664\u4E86\u5723\u6BBF ( \u739B\u4E8C\u5341\u4E00 12 ~ 17 ), \u5E76\u4E0E\u5723\u6BBF\u5B97\u6559\u9886\u8896\u4EEC\u8FDB\u884C\u8FA9\u8BBA ( \u739B\u4E8C\u5341\u4E00 23 ~ \u4E8C\u5341\u4E8C 46 ), \u8FD8\u9488\u5BF9\u5B97\u6559\u9886\u8896\u7684\u4F2A\u5584\u5411\u95E8\u5F92\u8B66\u544A ( \u739B\u4E8C\u5341\u4E09 1 ~ 36 ). \u63A5\u7740, \u8036\u7A23\u9884\u8A00\u5723\u6BBF\u5C06\u88AB\u6BC1 ( \u739B\u4E8C\u5341\u56DB 1 ~ 2 ), \u628A\u8036\u808B\u7C73\u4E9A\u5148\u77E5\u6307\u51FA\u9ED8\u897F\u4E9A\u6765\u4E34\u4E4B\u65F6\u8FB0\u6240\u8981\u53D1\u751F\u4E4B\u4E8B ( \u8036\u4E03 1 ~ 15 ) \u8054\u7CFB\u5728\u4E00\u8D77\u4E86. \u5728\u739B\u7AA6\u5723\u53F2\u7F16\u5199\u798F\u97F3\u7684\u65F6\u4EE3, \u5723\u6BBF\u5DF2\u7ECF\u88AB\u6BC1\u6389\u4E86. \u4F46, \u5728\u8036\u7A23\u53D7\u72B9\u592A\u5B97\u6559\u9886\u8896\u5BA1\u8BE2\u65F6, \u505A\u5047\u89C1\u8BC1\u7684\u63A7\u544A\u8036\u7A23\u66FE\u5A01\u80C1\u8981\u62C6\u6BC1\u5723\u6BBF ( \u739B\u4E8C\u5341\u516D 61 ), \u5E76\u5728\u8036\u7A23\u88AB\u9489\u5341\u5B57\u67B6\u4E4B\u5904, \u8DEF\u8FC7\u7684\u4EBA\u4E5F\u4E3A\u6B64\u8FB1\u9A82\u4E86\u8036\u7A23 ( \u739B\u4E8C\u5341\u4E03 40 ).</p><p>\u63A5\u7740, \u8036\u7A23\u8C08\u53CA\u4E86\u672B\u4E16\u7684\u5F81\u5146. \u8036\u7A23\u50CF\u6709\u6743\u5A01\u7684\u8001\u5E08\u90A3\u6837 ( \u53C2\u9605: \u739B\u4E94 10: \u5341\u4E94 29 ), \u4ECE\u5728\u6A44\u6984\u5C71\u4E0A; \u6A44\u6984\u5C71\u662F\u672B\u4E16\u6700\u540E\u5BA1\u5224\u4E4B\u5730 ( \u531D\u5341\u56DB 4 ). \u95E8\u5F92\u5728\u6B64\u548C\u8036\u7A23\u79C1\u4E0B\u6559\u5BFC\u4ED6\u4EEC\u6BD4\u55BB\u7684\u610F\u4E49\u4E00\u6837 ( \u739B\u5341\u4E09 10 ~ 17 ), \u4E5F\u5F97\u5230\u4E86\u8036\u7A23\u79C1\u4E0B\u7684\u6559\u8BF2. \u8036\u7A23\u4E3A\u95E8\u5F92\u63CF\u7ED8\u51FA\u4E00\u5E45\u4E16\u754C\u6781\u5EA6\u6DF7\u4E71, \u5927\u89C4\u6A21\u906D\u5230\u6BC1\u574F\u7684\u56FE\u50CF, \u5230\u5904\u90FD\u662F &quot;\u5047\u9ED8\u897F\u4E9A&quot;, \u6218\u4E89, \u9965\u8352, \u5730\u9707, \u8FEB\u5BB3, \u56E0\u8036\u7A23\u7684\u540D\u53D7\u4EBA\u618E\u6068, \u7F6A\u5B7D, \u80CC\u53DB, \u6B3A\u9A97, \u4E0D\u9075\u5B88\u6CD5\u5F8B, \u4EBA\u5931\u53BB\u70ED\u60C5, \u4EE5\u53CA\u6765\u81EA\u5185\u5FC3\u53CA\u5904\u5728\u4E24\u65B9\u9762\u7684\u4E89\u6597\u51B2\u7A81.</p><p>\u51E0\u4E4E\u6BCF\u4E00\u5E74\u9F84\u5C42\u7684\u4EBA, \u5728\u770B\u5230\u8FD9\u4E9B\u5F81\u5146\u65F6\u90FD\u4F1A\u6000\u7591: \u8FD9\u662F\u5426\u9884\u793A\u7740\u672B\u4E16\u7684\u5230\u6765. \u8036\u7A23\u4E0D\u4E45\u524D\u6D3E\u9063\u95E8\u5F92\u51FA\u5916\u5BA3\u8BB2\u65F6, \u4E5F\u66FE\u63D0\u53CA\u4E86\u7C7B\u4F3C\u7684\u4E3B\u9898 ( \u739B\u5341 16 ~ 25, 34 ~ 39 ) \u800C\u5411\u95E8\u5F92\u4FDD\u8BC1: \u9762\u5BF9\u4EFB\u4F55\u4E8B\u60C5\u7684\u53D1\u751F, \u90FD\u4E0D\u8981\u5BB3\u6015, \u56E0\u4E3A\u5929\u4E3B\u4F1A\u7ED9\u4ED6\u4EEC\u6BCF\u65F6\u6BCF\u523B\u7684\u7167\u987E ( \u739B\u5341 26 ~ 33 ). \u5728\u8FD9\u91CC, \u540C\u6837\u5730, \u8036\u7A23\u544A\u8BC9\u95E8\u5F92, &quot;\u552F\u72EC\u575A\u6301\u5230\u5E95\u7684, \u624D\u53EF\u5F97\u6551&quot; ( 13 \u8282 ). \u8FD9\u4E9B\u73B0\u8C61 &quot;\u53EA\u662F\u75DB\u82E6\u7684\u5F00\u59CB&quot; ( 8 \u8282 ), \u72B9\u5982\u4EA7\u75DB\u662F\u65B0\u751F\u547D\u7684\u524D\u594F\u4E00\u6837. \u739B\u7AA6\u5723\u53F2\u6307\u51FA\u7ED3\u5C40 ( \u672B\u4E16 ) \u6765\u5230\u65F6, \u4F1A\u6709\u4E00\u4E2A\u6807\u8BB0, \u5C31\u662F\u5929\u56FD\u7684\u798F\u97F3\u8981\u5148\u4F20\u904D\u5929\u4E0B ( \u4E5F\u53C2\u9605: \u739B\u4E8C\u5341\u516B 16 ~ 20 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u300A\u739B\u7AA6\u798F\u97F3 \u300B\u7684\u7B2C\u4E8C\u5341\u56DB\u548C\u4E8C\u5341\u4E94\u7AE0\u4E3A\u4E00\u4E2A\u5355\u5143, \u662F\u4E94\u7BC7\u91CD\u8981\u8BB2\u9053\u4E2D\u7684\u6700\u540E\u4E00\u7BC7, \u6709\u5173\u672B\u4E16\u7684\u8BB2\u9053. \u5B83\u53EF\u4EE5\u5206\u4E3A\u4E09\u90E8\u5206: \u4E00\u3001\u63CF\u8FF0\u5C06\u8981\u53D1\u751F\u7684\u4E8B\u4EF6 (24:1 -44); \u4E8C\u3001\u529D\u5BFC\u4EBA\u4EEC\u4FDD\u6301\u8B66\u9192\u5E76\u7948\u7977 (24:45-25:30); \u4E09\u3001\u5BA1\u5224\u7684\u6BD4\u55BB (25: 31 - 46).</p><p>\u739B\u7AA6\u8BBE\u60F3, \u8036\u7A23\u4ECE\u5723\u6BBF\u51FA\u6765, \u5728\u8FDC\u5904\u6C89\u601D\u5723\u6BBF\u5C06\u6765\u7684\u547D\u8FD0. \u8FD9\u79CD\u56FE\u50CF\u5177\u6709\u8C61\u5F81\u610F\u4E49: \u8036\u7A23\u8D70\u51FA\u4E86\u5723\u6BBF, \u8FD9\u4E5F\u662F\u7942\u6700\u540E\u4E00\u6B21\u79BB\u5F00\u5723\u6BBF, \u6C38\u8FDC\u5730\u79BB\u5F00. \u968F\u540E, \u7942\u56DE\u5230\u81EA\u5DF1\u7684\u95E8\u5F92\u5F53\u4E2D, \u5373\u90A3\u4E2A\u65B0\u7684\u56E2\u4F53. \u8FD9\u4E2A\u7531\u5927\u9ED1\u843D\u5FB7\u6240\u5EFA\u9020, \u6709\u96C4\u4F1F\u77F3\u67F1\u548C\u58EE\u4E3D\u6BBF\u5ECA\u7684\u5723\u6BBF, \u5728\u8036\u7A23\u53E3\u4E2D\u6210\u4E3A\u6E21\u5411\u672B\u4E16\u4E3B\u9898\u7684\u8DF3\u677F.</p><p>\u8FD9\u53EF\u80FD\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D\u6700\u4E3A\u8D39\u89E3\u7684\u4E00\u6BB5, \u56E0\u4E3A\u6240\u8BB2\u8FF0\u7684\u90FD\u662F\u672A\u6765\u7684\u4E8B\u4EF6. \u4E8B\u4EF6\u4E2D\u7684\u5404\u4E2A\u7EC6\u8282, \u6211\u4EEC\u65E0\u4ECE\u77E5\u6653, \u800C\u4E14\u5176\u5199\u4F5C\u89D2\u5EA6\u4E5F\u5177\u6709\u65F6\u7A7A\u91CD\u53E0\u6027, \u53CD\u6620\u5B97\u5F92\u4EEC\u4F3C\u4E4E\u6709\u610F\u5408\u5E76\u4E14\u6DF7\u6DC6\u4E24\u4EF6\u4E8B\u60C5, \u5373\u5723\u6BBF\u88AB\u6BC1 ( \u5386\u53F2\u65F6\u7A7A\u4E2D\u53D1\u751F\u7684\u4E8B ) \u548C\u9ED8\u897F\u4E9A\u6765\u4E34\u65F6\u7684\u4E16\u754C\u672B\u65E5 ( \u5386\u53F2\u65F6\u7A7A\u7684\u7EC8\u7ED3 ). \u4ED6\u4EEC\u5728\u5BFB\u6C42\u4E00\u4E9B\u5177\u4F53\u7684\u6807\u8BB0, \u597D\u80FD\u5236\u9020\u4E00\u4E2A\u65E5\u5386, \u8F83\u4E3A\u51C6\u786E\u5730\u8BA1\u7B97\u672A\u65E5\u6765\u4E34\u7684\u65F6\u95F4. \u4ED6\u4EEC\u8FD9\u6837\u95EE, \u65E2\u662F\u51FA\u4E8E\u597D\u5947, \u53C8\u51FA\u4E8E\u6050\u60E7.</p><p>\u8036\u7A23\u7684\u56DE\u7B54\u662F\u62D2\u7EDD\u80AF\u5B9A\u4EFB\u4F55\u73B0\u4E16\u8981\u53D1\u751F\u7684\u4E8B. \u7942\u7684\u8BDD\u90FD\u5728\u52C9\u52B1\u95E8\u5F92\u4EEC\u4FDD\u6301\u8B66\u9192, \u597D\u80FD\u9762\u5BF9\u67D0\u4E9B\u5FC5\u7136\u7684\u82E6\u96BE. \u95E8\u5F92\u4EEC\u8BE2\u95EE\u4E24\u4EF6\u4E8B\u60C5, \u53EF\u662F\u4ED6\u4EEC\u5E76\u4E0D\u786E\u5B9A\u8FD9\u4E24\u4EF6\u4E8B\u60C5\u4E4B\u95F4\u7684\u5173\u7CFB. &quot;\u8FD9\u4E9B\u4E8B&quot; \u662F\u6307\u5723\u6BBF\u88AB\u6BC1 &quot;\u7962\u7684\u518D\u6765&quot; \u6307\u4E3B\u8036\u7A23\u5E26\u7740\u5706\u6EE1\u7684\u5149\u8363\u6765\u4E34\u7684\u65E5\u5B50, \u4E5F\u5C31\u662F\u4E16\u754C\u7EC8\u7A77\u7684\u65F6\u523B.</p><p>\u5728\u7EC8\u7ED3\u7684\u65F6\u5019, \u4F1A\u6709\u4E00\u8FDE\u4E32\u7684\u91CD\u5927\u4E8B\u60C5\u53D1\u751F ( 4 - 14), \u4F46\u662F, \u8FD9\u4E9B\u4E8B\u80AF\u5B9A\u4E0D\u4F1A\u6309\u7167\u4EBA\u4EEC\u6240\u9884\u671F\u7684\u65F6\u95F4\u987A\u5E8F\u53D1\u751F. \u4EBA\u4EEC\u7684\u5185\u5FC3\u5C06\u8981\u6DF7\u4E71, \u4EBA\u4E0E\u4EBA\u4E4B\u95F4\u5145\u6EE1\u6028\u6068\u4E0E\u4E89\u6597\u3001\u5929\u707E\u4EBA\u7978\u3001\u8FEB\u5BB3\u3001\u5C60\u622E\u7B49\u7B49, \u8FD9\u4E00\u5207\u5C31\u5982\u540C\u65B0\u65F6\u671F\u8BDE\u751F\u4E4B\u524D\u5FC5\u7136\u8981\u7ECF\u5386\u7684\u4EA7\u75DB. \u6700\u7EC8, \u4EBA\u5FC5\u987B\u5FCD\u53D7\u8FD9\u4E9B\u75DB\u82E6, \u8010\u5FC3\u5730\u7B49\u5F85, \u56E0\u4E3A\u5929\u4E3B\u5C06\u4F7F\u4E00\u5207\u75DB\u82E6\u5347\u534E (9), \u798F\u97F3\u5C06\u4F1A\u4F20\u904D\u4E16\u754C ( 14 ), \u5FE0\u4FE1\u7684\u4EBA\u90FD\u5FC5\u5F97\u6551 ( 13 ).</p><p>\u4E16\u754C\u672B\u65E5\u4E0D\u4F1A\u7ACB\u523B\u5230\u6765, \u4ECA\u65E5\u6D41\u884C\u7684\u770B\u6CD5\u4E5F\u662F\u5982\u6B64. \u672B\u65E5\u6765\u4E34\u4E4B\u524D\u8981\u51FA\u73B0\u4E00\u4E9B\u9884\u5146\u4F5C\u4E3A\u6807\u8BB0, \u4F46\u6240\u5217\u4E3E\u7684\u6807\u8BB0\u7EDD\u5BF9\u4E0D\u53EF\u4EE5\u88AB\u7406\u89E3\u4E3A\u672B\u65E5\u6765\u4E34\u7684\u65F6\u95F4\u8868. \u5386\u53F2\u4E0A, \u4EBA\u4EEC\u66FE\u591A\u6B21\u9884\u8A00\u672B\u65E5\u6765\u4E34\u7684\u65F6\u523B, \u4F46\u8FD9\u4E9B\u9884\u8A00\u90FD\u88AB\u8BC1\u660E\u662F\u9519\u8BEF\u7684! \u8FD9\u79CD\u5BF9\u672B\u65E5\u7684\u8BA1\u7B97\u548C\u9884\u6D4B, \u90FD\u4E0E\u798F\u97F3\u7684\u7CBE\u795E\u80CC\u9053\u800C\u9A70, \u56E0\u4E3A, \u6240\u6709\u8FD9\u4E9B\u8BAF\u606F\u90FD\u5728\u529D\u52C9\u4EBA\u4EEC, \u8981\u8B66\u9192 ( 25:13 ), \u5584\u7528 &quot;\u5854\u51B7\u901A&quot; \u5E76\u7ED3\u51FA\u76F8\u5E94\u7684\u679C\u5B9E ( 25:14 - 30 ), \u540C\u65F6\u8FD8\u8981\u5E2E\u52A9\u6709\u9700\u8981\u7684\u4EBA ( 25:31 - 46 ). \u5982\u679C\u798F\u97F3\u7ED9\u6211\u4EEC\u6307\u51FA\u672A\u6765, \u90A3\u4E5F\u662F\u4E3A\u4E86\u5E2E\u52A9\u6211\u4EEC\u66F4\u597D\u5730\u6295\u8EAB\u4E8E\u73B0\u4ECA\u7684\u5386\u53F2.</p><h2 id="\u4EBA\u5B50\u7684\u6765\u4E34" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u5B50\u7684\u6765\u4E34" aria-hidden="true">#</a> \u4EBA\u5B50\u7684\u6765\u4E34</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>21</sup> \u56E0\u4E3A\u90A3\u65F6\u5FC5\u6709\u5927\u707E\u96BE\uFF0C\u662F\u4ECE\u5B87\u5B99\u5F00\u59CB\uFF0C\u76F4\u5230\u5982\u4ECA\u4ECE\u672A\u6709\u8FC7\u7684\uFF0C\u5C06\u6765\u4E5F\u4E0D\u4F1A\u518D\u6709\uFF1B<sup>22</sup> \u5E76\u4E14\u90A3\u4E9B\u65F6\u65E5\u5982\u4E0D\u7F29\u77ED\uFF0C\u51E1\u6709\u8840\u8089\u7684\uFF0C\u90FD\u4E0D\u4F1A\u5F97\u6551\uFF1B\u4F46\u4E3A\u4E86\u90A3\u4E9B\u88AB\u9009\u7684\uFF0C\u90A3\u4E9B\u65F6\u65E5\uFF0C\u5FC5\u5C06\u7F29\u77ED\u3002<sup>23</sup> \u90A3\u65F6\uFF0C\u82E5\u6709\u4EBA\u5BF9\u4F60\u4EEC\u8BF4\uFF1A\u770B\uFF0C\u9ED8\u897F\u4E9A\u5728\u8FD9\u91CC\uFF01\u6216\u8BF4\uFF1A\u5728\u90A3\u91CC\uFF01\u4F60\u4EEC\u4E0D\u8981\u76F8\u4FE1\uFF0C<sup>24</sup> \u56E0\u4E3A\u5C06\u6709\u5047\u9ED8\u897F\u4E9A\u548C\u5047\u5148\u77E5\u5174\u8D77\uFF0C\u884C\u5927\u5947\u8FF9\u548C\u5F02\u8FF9\uFF0C\u4EE5\u81F4\u5982\u679C\u53EF\u80FD\uFF0C\u8FDE\u88AB\u9009\u7684\u4EBA\u4E5F\u8981\u88AB\u6B3A\u9A97\u3002<sup>25</sup> \u770B\uFF0C\u6211\u9884\u5148\u544A\u8BC9\u4E86\u4F60\u4EEC\u3002</p></blockquote><blockquote><p><sup>26</sup> \u4E3A\u6B64\uFF0C\u5982\u679C\u6709\u4EBA\u5BF9\u4F60\u4EEC\u8BF4\uFF1A\u770B\uFF0C\u4ED6\u5728\u65F7\u91CE\u3002\u4F60\u4EEC\u4E0D\u8981\u51FA\u6765\u3002\u6216\u8BF4\uFF1A\u770B\uFF0C\u5728\u5185\u5BA4\u3002\u4F60\u4EEC\u4E5F\u4E0D\u8981\u76F8\u4FE1\u3002<sup>27</sup> \u56E0\u4E3A\u5982\u540C\u95EA\u7535\u4ECE\u4E1C\u65B9\u53D1\u51FA\uFF0C\u76F4\u7167\u5230\u897F\u65B9\uFF0C\u4EBA\u5B50\u7684\u6765\u4E34\u4E5F\u8981\u8FD9\u6837\u3002<sup>28</sup> \u65E0\u8BBA\u5728\u90A3\u91CC\u6709\u6B7B\u5C38\uFF0C\u8001\u9E70\u5C31\u805A\u96C6\u5728\u90A3\u91CC\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u72B9\u592A\u7684\u57FA\u7763\u5F92\u4E00\u65E6\u770B\u5230 &quot;\u62DB\u81F4\u8352\u51C9\u7684\u53EF\u618E\u4E4B\u7269&quot; ( \u8FBE\u5C3C\u5C14\u7528\u8FD9\u8BDD\u6765\u63CF\u8FF0\u516C\u5143\u524D\u4E00\u516D\u4E03\u5E74, \u5B89\u63D0\u7EA6\u53E4\u7B2C\u56DB\u5384\u4E15\u6CD5\u4E43\u5728\u5168\u71D4\u796D\u575B\u4E0A\u5EFA\u7ACB\u7684\u5E0C\u814A\u795E\u5B99\u65AF ), \u5C31\u5E94\u5F53\u7ACB\u523B\u9003\u79BB\u5723\u57CE, \u739B\u7AA6\u5728\u8FD9\u91CC\u6240\u63CF\u8FF0\u7684\u5386\u53F2\u4E8B\u4EF6\u662F\u516C\u5143\u516D\u516B\u5E74\u5F00\u59CB\u63D0\u6258\u8FDB\u72AF\u5723\u57CE. \u7F57\u9A6C\u4EBA\u5EFA\u7ACB\u5076\u50CF\u7684\u4E60\u60EF\u5728\u72B9\u4EBA\u65E0\u5076\u50CF\u7684\u4F20\u7EDF\u4E2D\u663E\u5F97\u53EF\u618E. \u5386\u53F2\u544A\u8BC9\u6211\u4EEC, \u8036\u8DEF\u6492\u51B7\u7684\u7FA4\u4F17\u63A5\u53D7\u4E86\u8FD9\u4E2A\u6697\u793A, \u540E\u6765\u907F\u96BE\u5230\u5916\u7EA6\u65E6\u7684\u8D1D\u62C9.</p><p>\u739B\u7AA6\u8FD8\u52A0\u4E86 &quot;\u6216\u5B89\u606F\u65E5&quot;, \u5426\u5219\u90A3\u4E9B\u72B9\u57FA\u7763\u5F92\u8003\u8651\u5230\u5B89\u606F\u65E5\u7684\u4F11\u606F, \u53EF\u80FD\u4F1A\u59A8\u788D\u4ED6\u4EEC\u7684\u9003\u8131. \u4ED6\u5F15\u7528\u4E86 &quot;\u4EBA\u5B50\u7684\u518D\u4E34&quot; \u8FD9\u4E2A\u540D\u8BCD\u6765\u8868\u793A, \u4ED6\u8BA4\u4E3A\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D, \u662F\u5149\u8363\u7684\u57FA\u7763\u6765\u8BBF. parousia \u8FD9\u4E2A\u5B57, \u56E0\u4E3A\u4FDD\u7984\u7684\u5F71\u54CD, \u53EA\u7528\u6765\u6307\u57FA\u7763\u5728\u4E16\u754C\u672B\u65E5\u7684\u7B2C\u4E8C\u6B21\u6765\u4E34. \u53EF\u662F\u739B\u7AA6\u5728\u6B64\u5904\u53CA\u95E8\u5F92\u6700\u521D\u4E4B\u95EE\u9898 ( \u4E8C\u5341\u56DB 3 ) \u4E2D\u8FD0\u7528\u6B64\u5B57\u662F\u6307\u5E7F\u610F\u7684\u57FA\u7763\u518D\u4E34. \u9A6C\u5C14\u8C37\u548C\u8DEF\u52A0\u90FD\u907F\u514D\u8FD9\u79CD\u7528\u6CD5, \u56E0\u4E3A\u4ED6\u4EEC\u662F\u4FDD\u7984\u7684\u5F1F\u5B50. \u90A3\u95EA\u7535\u7684\u55BB\u8C61\u53EF\u80FD\u6307\u5728\u6BBF\u706D\u65F6, \u57FA\u7763\u6765\u4E34\u662F\u5B8C\u5168\u53EF\u89C1\u7684.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4E00\u6BB5\u91C7\u7528\u4E86\u9ED8\u793A\u6587\u5B66\u7684\u5199\u4F5C\u98CE\u683C, \u5F15\u7528\u8FBE\u5C3C\u5C14\u5148\u77E5\u6697\u793A\u7684\u516C\u5143\u524D\u4E00\u516D\u4E03\u5E74, \u5B89\u63D0\u7EA6\u53E4\u7B2C\u56DB\u7EA6\u5723\u53D1\u4E43\u5728\u5168\u7194\u796D\u575B\u4E0A\u5EFA\u7ACB\u5E0C\u814A\u795E\u5B99\u65AF\u50CF ( 15; \u53C2\u9605\u8FBE 9:27 ), \u5F3A\u8FEB\u72B9\u592A\u4EBA\u5D07\u62DC\u5F02\u6559\u795E\u660E\u7684\u5386\u53F2\u4E8B\u4EF6(\u53D6\u81EA\u5149\u542F\u300A\u65B0\u7EA6\u5BFC\u8BFB\u4E1B\u4E66\u4E00\u4E00\u739B\u7AA6\u798F\u97F3\u300B), \u5177\u4F53\u5730\u63CF\u8FF0\u4E86\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D, \u4F46\u5176\u4E3B\u8981\u76EE\u7684\u5728\u4E8E\u9F13\u52B1\u95E8\u5F92\u4EEC\u4ECE\u8FD9\u4E9B\u707E\u7978\u4E2D\u5B66\u5F97\u6559\u8BAD, \u4E0D\u81F3\u4E8E\u8BEF\u4FE1\u4EBA\u5B50\u6765\u4E34\u7684\u8C0E\u8A00. \u8FD9 &quot;\u4E00\u573A\u6D69\u52AB&quot; ( 21 ) \u5C06\u6311\u6218\u4EBA\u7684\u4FE1\u5FB7, \u751A\u81F3\u88AB\u62E3\u9009\u7684\u4EBA\u5728\u5F97\u6551\u7684\u4E8B\u4E0A\u4E5F\u8981\u9762\u4E34\u5371\u673A. \u4F46\u662F, \u5929\u4E3B\u51FA\u81EA\u7231\u7684\u5E72\u9884\u5C06\u4F1A\u7F29\u77ED\u8FD9\u6BB5\u65F6\u671F (22). \u4ED6\u4EEC\u5E94\u8BE5\u63D0\u9632\u5047\u5148\u77E5\u548C\u5047\u9ED8\u897F\u4E9A ( 23 - 28 ), \u8FD9\u4E9B\u4EBA\u751A\u81F3\u80FD\u5C06\u88AB\u62E3\u9009\u7684\u4EBA\u5E26\u5165\u6B67\u9014. \u91CD\u590D\u5730\u5F3A\u8C03 &quot;\u4E0D\u8981\u76F8\u4FE1&quot; ( 23, 26 ), \u662F\u8981\u6559\u8BAD\u4ED6\u4EEC, \u5728\u8FD9\u4E9B\u707E\u96BE\u548C\u9A97\u5B50\u9762\u524D\u65F6\u5E38\u4FDD\u6301\u6E05\u9192.</p><p>\u81F3\u4E8E\u5B87\u5B99\u95F4\u7684\u6DF7\u4E71\u548C\u6700\u540E\u7684\u8352\u51C9\u666F\u8C61, \u662F\u4E3A\u6307\u51FA\u672B\u65E5\u7684\u5BA1\u5224\u5C06\u7A81\u7136\u964D\u4E34\u5230\u6BCF\u4E00\u4E2A\u4EBA\u5934\u4E0A.</p><h2 id="\u4EBA\u5B50\u6765\u4E34\u7684\u5148\u5146" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u5B50\u6765\u4E34\u7684\u5148\u5146" aria-hidden="true">#</a> \u4EBA\u5B50\u6765\u4E34\u7684\u5148\u5146</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>29</sup> \u201C\u90A3\u4E9B\u65F6\u65E5\u7684\u707E\u96BE\u4E00\u8FC7\uFF0C\u7ACB\u65F6\u592A\u9633\u5C31\u8981\u660F\u6697\uFF0C\u6708\u4EAE\u4E5F\u4E0D\u53D1\u5149\uFF0C\u661F\u8FB0\u8981\u4ECE\u5929\u4E0A\u5760\u4E0B\uFF0C\u5929\u4E0A\u7684\u4E07\u8C61\u4E5F\u8981\u52A8\u6447\u3002<sup>30</sup> \u90A3\u65F6\uFF0C\u4EBA\u5B50\u7684\u8BB0\u53F7\u8981\u51FA\u73B0\u5929\u4E0A\uFF1B\u5730\u4E0A\u6240\u6709\u7684\u79CD\u65CF\uFF0C\u90FD\u8981\u54C0\u53F7\uFF0C\u8981\u770B\u89C1\u4EBA\u5B50\u5E26\u7740\u5A01\u80FD\u548C\u5927\u5149\u8363\uFF0C\u4E58\u7740\u5929\u4E0A\u7684\u4E91\u5F69\u964D\u6765\u3002<sup>31</sup> \u4ED6\u8981\u6D3E\u9063\u4ED6\u7684\u5929\u4F7F\uFF0C\u7528\u53D1\u51FA\u6D2A\u58F0\u7684\u53F7\u89D2\uFF0C\u7531\u56DB\u65B9\uFF0C\u4ECE\u5929\u8FD9\u8FB9\u5230\u5929\u90A3\u8FB9\uFF0C\u805A\u96C6\u4ED6\u6240\u62E3\u9009\u7684\u4EBA\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6B64\u5904\u7528\u9ED8\u793A\u7984\u4F53\u7684\u8BCD\u53E5\u662F\u4E3A\u4E86\u63CF\u8FF0\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D, \u800C\u4E0D\u662F\u4E16\u754C\u7684\u672B\u65E5. \u65E7\u7EA6\u65F6\u7684\u5148\u77E5\u66FE\u7ECF\u7528\u8FD9\u79CD\u5B87\u5B99\u6027\u7684\u8BCD\u53E5\u6765\u5F62\u5BB9\u5384\u4E1C\u7684\u6BC1\u706D, \u6CD5\u8001\u7684\u6B7B\u4EA1, \u72B9\u5927\u548C\u8036\u8DEF\u6492\u51B7\u7684\u88AB\u653B. \u8FD9\u79CD\u6587\u5B66\u4F53\u88C1\u4E0D\u4EC5\u662F\u8BD7\u6B4C\u7684\u55BB\u8C61, \u66F4\u662F\u65E7\u7EA6\u53F8\u796D\u795E\u8FD9\u7684\u8868\u8FBE\u6CD5. \u56E0\u4E3A\u96C5\u5A01\u662F\u5386\u53F2\u4E8B\u4EF6\u7684\u4F5C\u8005, \u4E5F\u662F\u5B87\u5B99\u7684\u521B\u4F5C\u8005, \u7CBE\u795E\u7684\u548C\u7269\u8D28\u7684\u4E16\u754C\u4E4B\u95F4, \u672C\u6765\u5B58\u5728\u7740\u4E00\u79CD\u5148\u5B58\u7684\u548C\u8C10. \u6240\u4EE5, \u4EFB\u4F55\u4EBA\u4E8B\u7684\u5371\u673A ( \u5982\u4E9A\u8FF0\u4EBA\u6216\u5DF4\u6BD4\u4F26\u4EBA\u8FDB\u653B\u5DF4\u52D2\u65AF\u5766 ), \u90FD\u4F1A\u5F15\u8D77\u81EA\u7136\u754C\u4E00\u79CD\u540C\u60C5\u7684\u56DE\u54CD. \u65B0\u7EA6\u4EE5\u4E3A\u8036\u7A23\u7684\u6B7B\u4EA1\u548C\u590D\u6D3B\u9020\u6210\u4E86\u4E00\u4E2A &quot;\u65B0\u5929\u65B0\u5730&quot;, \u8FD9\u4E2A\u89C2\u5FF5\u5C31\u662F\u76F8\u4FE1\u5929\u4E3B\u662F\u5386\u53F2\u7684\u5168\u80FD\u5F15\u9886\u8005\u7684\u53CD\u6620. &quot;\u4EBA\u5B50\u7684\u8BB0\u53F7\u5728\u5929\u4E0A\u51FA\u73B0&quot; \u4F7F\u666E\u4E16\u5404\u56FD\u7688\u4F9D; \u6309\u7167\u531D\u52A0\u5229\u4E9A\u6240\u9884\u8A00\u7684\u8FD9 &quot;\u8BB0\u53F7&quot;, \u5C31\u662F\u6307\u4EE5 &quot;\u521D\u4F20&quot; \u6765\u5BA3\u626C\u8036\u7A23\u6551\u8D4E\u6027\u7684\u6B7B\u4EA1\u4E0E\u590D\u6D3B\u7684\u521D\u671F\u6559\u4F1A, \u6210\u4E3A &quot;\u5411\u5217\u90A6\u9AD8\u4E3E\u7684\u8BB0\u53F7&quot;, \u5176 &quot;\u53EF\u89C1\u6027&quot; \u539F\u5148\u88AB\u5B83\u4E0E\u72B9\u592A\u6559\u7684\u5173\u8054\u5F04\u5F97\u6726\u80E7\u4E0D\u6E05, \u76F4\u5230\u5723\u6BBF\u503E\u6BC1\u4EE5\u540E, \u624D\u5F97\u5F70\u73B0. \u739B\u7AA6\u63CF\u7ED8 &quot;\u5929\u4F7F&quot; \u4EE5\u53F7\u89D2\u6765\u805A\u96C6\u65B0\u7684 &quot;\u9009\u6C11&quot;, \u53EF\u80FD\u662F\u8C61\u5F81\u7740\u5728\u5B97\u5F92\u9886\u5BFC\u4E0B\u7684\u6559\u4F1A\u6240\u8868\u73B0\u7684\u7EDF\u4E00\u4E0E\u666E\u4E16\u6027.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5F53\u672B\u4E16\u5230\u6765\u65F6, \u5C06\u4F1A\u51FA\u73B0\u786E\u51FF\u65E0\u8BEF\u7684\u5F81\u5146. \u5B83\u4F1A\u50CF\u8D8A\u8FC7\u5929\u9645\u7684\u95EA\u7535 ( 27 \u8282 ), \u6216\u8001\u9E70\u5728\u6B7B\u5C38\u4E0A\u76D8\u65CB ( 28 \u8282 ) \u90A3\u6837\u7684\u660E\u663E. \u6709\u4E00\u4E2A\u5F81\u5146\u7684\u51FA\u73B0\uFF0C \u4F1A\u50CF\u8FBE\u5C3C\u5C14\u5148\u77E5\u6240\u8BF4\u7684 &quot;\u62DB\u81F4\u8352\u51C9\u7684\u53EF\u618E\u4E4B\u7269&quot; \u51FA\u73B0\u5728\u5723\u6BBF\u5185 ( 15 \u8282; \u8FBE\u4E5D 27; \u5341\u4E00 31; \u5341\u4E8C 11 ). \u300A\u8FBE\u5C3C\u5C14\u5148\u77E5\u4E66\u300B\u6240\u8BF4 &quot;\u62DB\u81F4\u8352\u51C9\u7684\u53EF\u618E\u4E4B\u7269&quot;, \u6307\u7684\u662F\u4E3B\u524D 167 \u5E74\u5B89\u63D0\u7EA6\u53E4\u56DB\u4E16\u5384\u4E15\u6CD5\u4E43 ( Epiphanes ) \u4EB5\u6E0E\u5723\u6BBF, \u5728\u5723\u6BBF\u5185\u8BBE\u7ACB\u4E86\u5F02\u6559\u7684\u796D\u575B\u53CA\u5076\u50CF ( \u53EF\u618E\u4E4B\u7269 ), \u5BFC\u81F4\u4E86\u739B\u52A0\u4F2F\u7684\u8D77\u4E49; \u800C\u739B\u7AA6\u56E2\u4F53\u8BB0\u5FC6\u72B9\u65B0\u7684\u662F\u5728\u4E3B\u5386 40 \u5E74\u65F6, \u5F53\u65F6\u7684\u7F57\u9A6C\u7687\u5E1D\u5361\u5229\u53E4\u62C9 ( Caligula, 37 ~ 41 ) \u66FE\u5A01\u80C1\u8981\u91C7\u53D6\u76F8\u540C\u7684\u884C\u52A8\u6765\u4EB5\u6E0E\u5723\u6BBF.</p><p>\u5C06\u6765\u5982\u679C\u5728\u5723\u6BBF\u53D1\u751F\u5984\u505A\u5404\u79CD\u4EB5\u6E0E\u4E4B\u4E8B\u65F6, \u4FE1\u5F92\u5C31\u5E94\u660E\u767D, \u5927\u707E\u96BE\u8FD1\u4E86. \u9762\u5BF9\u8FD9\u7A7A\u524D\u7684\u5927\u707E\u96BE, \u53EA\u6709\u5C3D\u5FEB\u9003\u5954, \u8FDF\u5EF6\u4E0D\u5F97 ( \u5982\u540C: \u739B\u4E8C 12 ~ 13; \u5341 23 ). \u6000\u5B55\u7684\u5987\u4EBA\u53CA\u54FA\u4E73\u5B69\u5B50\u6BCD\u4EB2\u884C\u52A8\u4E0D\u4FBF, \u901A\u5E38\u662F\u6700\u5927\u7684\u53D7\u5BB3\u8005. \u95E8\u5F92\u5E94\u7948\u6C42\u5927\u707E\u96BE\u4E0D\u8981\u53D1\u751F\u5728\u4F7F\u9003\u4EA1\u66F4\u52A0\u56F0\u96BE\u7684\u65E5\u5B50, \u5982 &quot;\u51AC\u5929\u6216\u5B89\u606F\u65E5&quot; ( 20 \u8282 ) . \u5728\u5B89\u606F\u65E5\u9003\u4EA1, \u53EF\u80FD\u4F1A\u5F15\u8D77\u56E2\u4F53\u7684\u6CE8\u610F, \u4ECE\u800C\u4F7F\u9003\u4EA1\u8005\u5904\u4E8E\u5371\u9669\u7684\u5883\u5730. \u5982\u679C\u6709\u4EBA\u8BA4\u4E3A\u9003\u4EA1\u4F1A\u8FDD\u53CD\u9075\u5B88\u5B89\u606F\u65E5, \u8FD9\u6837\u5C31\u53EF\u80FD\u6210\u4E3A\u56E2\u4F53\u5206\u88C2\u7684\u539F\u56E0.</p><p>\u4EBA\u5B50\u6765\u4E34\u524D, \u661F\u8C61\u7684\u5FB5\u5146 ( \u53C2\u9605: \u4F9D\u5341\u4E09 10; \u4E09\u5341\u56DB 4; \u5219\u4E09\u5341\u4E8C 7; \u5CB3\u4E8C 10, 31; \u4E09 4; \u56DB 15; \u4E9A\u516B 9; \u76D6\u4E8C 6, 21 ) \u662F\u672B\u4E16\u5148\u5146\u7684\u5F00\u7AEF ( \u53C2\u9605\u739B\u516B 20 \u4E4B\u8BE0\u91CA, \u89C1\u672C\u4E66 87 ~ 88 \u9875 ). \u4E0D\u4E86\u89E3\u4E3A\u4EC0\u4E48 &quot;\u54C0\u53F7&quot; ( 30 \u8282 ) \u4F1A\u662F\u4F34\u968F\u4EBA\u5B50\u6765\u4E34\u7684\u5148\u5146? \u662F\u56E0\u4E3A\u5FE7\u60A3, \u82E6\u96BE? \u8FD8\u662F\u56E0\u4E3A\u4EBA\u4EEC\u5728\u5FCF\u6094? \u672B\u4E16\u65F6, \u5929\u4E3B\u8981\u805A\u96C6\u7942\u6240\u62E3\u9009\u7684\u5B50\u6C11, \u8FD9\u662F\u4E00\u4E2A\u5E38\u89C1\u7684\u4E3B\u9898 ( \u7533\u4E09\u5341 3 ~ 4; \u4F9D\u5341\u4E00 11 ~ 12; \u5219\u4E09\u5341\u4E03 21 ; \u4E09\u5341\u4E5D 27 ~ 29; \u531D\u4E8C 6 ~ 12 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6709\u5173\u9ED8\u897F\u4E9A\u6700\u540E\u6765\u4E34\u7684\u666F\u8C61\u63CF\u8FF0, \u5145\u6EE1\u5148\u77E5\u6027\u548C\u9ED8\u793A\u6027\u7684\u6587\u5B66\u7279\u5F81, \u6240\u7528\u7684\u8BED\u8A00\u6781\u5BCC\u8C61\u5F81\u610F\u4E49, \u662F\u4E3A\u8BF4\u660E\u5B50\u5728\u4E16\u754C\u672B\u65E5, \u4EE5\u5929\u4E3B\u7684\u540D\u5B57\u6240\u6267\u884C\u7684\u6700\u540E\u5BA1\u5224. \u5F53\u4F5C\u8005\u5C06\u524D\u9762\u6240\u63CF\u8FF0\u7684\u4E8B\u4EF6, \u7A7F\u63D2\u5728\u73B0\u4ECA\u7684\u5386\u53F2\u80CC\u666F\u4E2D\u65F6, \u57FA\u7763\u7684\u518D\u6765\u5C31\u6807\u5FD7\u7740\u5386\u53F2\u7684\u7EC8\u7ED3. \u57FA\u7763\u518D\u6765\u5C31\u662F\u5386\u53F2\u7684\u76EE\u6807\u3001\u7EC8\u7ED3\u548C\u51B3\u5B9A\u6027\u7684\u4E8B\u4EF6, \u4E5F\u662F\u6574\u4E2A\u4EBA\u7C7B\u5386\u53F2\u7684\u610F\u4E49\u6240\u5728. \u6BCF\u4E00\u4EE3\u4EBA\u90FD\u7ECF\u9A8C\u5230\u4EBA\u5B50\u7684\u5BA1\u5224, \u800C\u90A3\u4E9B\u80FD\u591F\u575A\u6301\u5230\u5E95\u7684, \u5C06\u4F1A\u83B7\u5F97\u6C38\u8FDC\u7684\u6551\u6069.</p><p>\u57FA\u7763\u6559\u4F1A\u4F20\u7EDF\u4E2D\u7684\u8C61\u5F81\u5B66\u90FD\u8BA4\u4E3A &quot;\u4EBA\u5B50\u7684\u8BB0\u53F7&quot; \u5C31\u662F\u5341\u5B57\u67B6 (30).</p><h2 id="\u65E0\u82B1\u679C\u6811\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u65E0\u82B1\u679C\u6811\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u65E0\u82B1\u679C\u6811\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>32</sup> \u201C\u4F60\u4EEC\u5E94\u8BE5\u7531\u65E0\u82B1\u679C\u53D6\u4E2A\u6BD4\u55BB\uFF1A\u51E0\u65F6\u5B83\u7684\u679D\u6761\u5DF2\u7ECF\u53D1\u5AE9\uFF0C\u53F6\u5B50\u51FA\u751F\u4E86\uFF0C\u4F60\u4EEC\u5C31\u77E5\u9053\u590F\u5929\u8FD1\u4E86\u3002<sup>33</sup> \u540C\u6837\uFF0C\u4F60\u4EEC\u51E0\u65F6\u770B\u89C1\u8FD9\u4E00\u5207\uFF0C\u4F60\u4EEC\u5C31\u8BE5\u77E5\u9053\uFF1A\u4ED6\u5DF2\u8FD1\u4E86\uFF0C\u5C31\u5728\u95E8\u53E3\u3002<sup>34</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u975E\u5230\u8FD9\u4E00\u5207\u53D1\u751F\u4E86\uFF0C\u8FD9\u4E00\u4E16\u4EE3\u51B3\u4E0D\u4F1A\u8FC7\u53BB\u3002<sup>35</sup> \u5929\u5730\u8981\u8FC7\u53BB\uFF0C\u4F46\u662F\u6211\u7684\u8BDD\uFF0C\u51B3\u4E0D\u4F1A\u8FC7\u53BB\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E2A\u7B80\u5355\u7684\u5BD3\u8A00\u89E3\u91CA\u4E86\u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D\u548C\u6559\u4F1A\u5185\u5929\u56FD\u5EFA\u7ACB\u7684\u5173\u7CFB. \u65E0\u82B1\u679C\u6811\u548C\u590F\u5929\u540C\u662F\u9ED8\u897F\u4E9A\u771F\u798F\u7684\u8BB0\u53F7. \u5BF9\u6709\u4FE1\u5FB7\u7684\u4EBA\u800C\u8A00, \u5723\u57CE\u503E\u6BC1\u7684\u5B9E\u9645\u610F\u4E49\u662F &quot;\u4ED6\u5DF2\u7ECF\u6765\u4E34\u4E86&quot;, \u5C31\u662F\u8BF4\u5347\u5929\u7684\u57FA\u7763\u85C9\u7740\u6559\u4F1A\u8131\u79BB\u72B9\u592A\u6559\u7684\u4E8B\u5B9E, \u6765\u8BC1\u660E\u4ED6\u7684\u529B\u91CF. \u8036\u7A23\u91CD\u590D\u8BF4\u4ED6\u6240\u9884\u8A00\u7684\u4E8B\u5C06\u8981\u5728\u8FD9\u4E00\u4E16\u4EE3\u7684\u6709\u751F\u4E4B\u5E74\u53D1\u751F. \u867D\u7136\u65E7\u4E16\u754C\u8981\u6D88\u901D, \u65E7\u7EA6\u7684\u795E\u5723\u5236\u5EA6\u544A\u4E00\u7ED3\u675F, \u4F46\u662F\u8036\u7A23\u7684\u542F\u793A -- \u53D6\u4EE3\u65E7\u7EA6\u7684 &quot;\u65B0\u521B\u9020&quot;, \u5374\u662F\u6C38\u4E45\u800C\u786E\u5B9A\u7684.</p><h2 id="\u9192\u5BE4\u7684\u7D27\u8981" tabindex="-1"><a class="header-anchor" href="#\u9192\u5BE4\u7684\u7D27\u8981" aria-hidden="true">#</a> \u9192\u5BE4\u7684\u7D27\u8981</h2><h3 id="\u7ECF\u6587-4" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-4" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>36</sup> \u201C\u81F3\u4E8E\u90A3\u65E5\u5B50\u548C\u90A3\u65F6\u523B\uFF0C\u9664\u7236\u4E00\u4E2A\u5916\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\uFF0C\u8FDE\u5929\u4E0A\u7684\u5929\u4F7F\u90FD\u4E0D\u77E5\u9053\u3002 <sup>37</sup> \u5C31\u5982\u5728\u8BFA\u5384\u7684\u65F6\u65E5\u600E\u6837\uFF0C\u4EBA\u5B50\u7684\u6765\u4E34\u4E5F\u8981\u600E\u6837\u3002<sup>38</sup> \u56E0\u4E3A\u5C31\u5982\u5728\u6D2A\u6C34\u524D\u7684\u65F6\u65E5\uFF0C\u4EBA\u7167\u5E38\u5403\u559D\u5A5A\u5AC1\uFF0C\u76F4\u5230\u8BFA\u5384\u8FDB\u5165\u65B9\u821F\u7684\u90A3\u4E00\u5929\uFF0C<sup>39</sup> \u4ECD\u7136\u6CA1\u6709\u89C9\u5BDF\uFF1B\u76F4\u5230\u6D2A\u6C34\u6765\u4E86\uFF0C\u628A\u4ED6\u4EEC\u90FD\u5377\u4E86\u53BB\uFF1B\u4EBA\u5B50\u7684\u6765\u4E34\uFF0C\u4E5F\u5FC5\u8981\u8FD9\u6837\u3002<sup>40</sup> \u90A3\u65F6\uFF0C\u4E24\u4E2A\u4EBA\u540C\u5728\u7530\u95F4\uFF0C\u4E00\u4E2A\u88AB\u63D0\u53BB\uFF0C\u4E00\u4E2A\u5374\u88AB\u9057\u5F03\uFF1B<sup>41</sup> \u4E24\u4E2A\u5973\u4EBA\u540C\u5728\u78E8\u65C1\u63A8\u78E8\uFF0C\u4E00\u4E2A\u88AB\u63D0\u53BB\uFF0C\u4E00\u4E2A\u5374\u88AB\u9057\u5F03\u3002</p></blockquote><blockquote><p><sup>42</sup> \u6240\u4EE5\uFF0C\u4F60\u4EEC\u8981\u9192\u5BE4\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u4E0D\u77E5\u9053\uFF1A\u5728\u90A3\u4E00\u5929\u4F60\u4EEC\u7684\u4E3B\u4EBA\u8981\u6765\u3002<sup>43</sup> \u8FD9\u4E00\u70B9\u4F60\u4EEC\u8981\u660E\u767D\uFF1A\u5982\u679C\u5BB6\u4E3B\u77E5\u9053\uFF0C\u76D7\u8D3C\u51E0\u66F4\u5929\u8981\u6765\uFF0C\u4ED6\u5FC5\u8981\u9192\u5BE4\uFF0C\u4E0D\u8BA9\u81EA\u5DF1\u7684\u623F\u5C4B\u88AB\u6316\u7A7F\u3002<sup>44</sup> \u4E3A\u6B64\uFF0C\u4F60\u4EEC\u5E94\u8BE5\u51C6\u5907\uFF0C\u56E0\u4E3A\u4F60\u4EEC\u4E0D\u6599\u60F3\u7684\u65F6\u8FB0\uFF0C\u4EBA\u5B50\u5C31\u6765\u4E86\u3002\u201D</p></blockquote><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6CA1\u6709\u4EBA\u77E5\u9053\u672B\u65E5\u6765\u4E34\u7684\u65F6\u523B. \u6545\u610F\u4E0D\u8BA9\u4EBA\u77E5\u9053\u90A3\u65E5\u671F, \u662F\u4E3A\u6FC0\u53D1\u4EBA\u4EEC\u65F6\u65F6\u8B66\u9192, \u4E0D\u80FD\u5FFD\u89C6\u73B0\u5728\u7684\u65F6\u65E5. \u65E0\u82B1\u679C\u6811\u7684\u6CE8\u89E3, \u529D\u5BFC\u4EBA\u8981\u61C2\u5F97\u5206\u8FA8. \u8FD9\u662F\u95E8\u5F92\u5728\u672B\u65E5\u65F6\u523B\u6765\u4E34\u4E4B\u524D\u5FC5\u987B\u5177\u5907\u7684\u6001\u5EA6.</p><p>\u5BF9\u4E8E\u8FD9\u5373\u523B\u7684\u6765\u4E34, \u4E0D\u53EF\u4EE5\u4ECE\u65F6\u95F4\u7684\u610F\u4E49\u4E0A\u7406\u89E3, \u800C\u5E94\u8BE5\u4ECE\u795E\u5B66\u7684\u610F\u4E49\u4E0A\u7406\u89E3. \u8FD9\u91CC\u6240\u5F3A\u8C03\u7684\u548C\u540E\u9762\u6BD4\u55BB\u6240\u8BF4\u7684, \u90FD\u80AF\u5B9A\u4E0A\u4E3B\u4F1A\u968F\u65F6\u5230\u6765. \u8FD9\u79CD\u80AF\u5B9A\u662F\u4E3A\u6FC0\u52B1\u56E2\u4F53, \u4E0D\u8981\u5728\u73B0\u4E16\u5984\u6C42\u4E00\u79CD\u6C38\u6052\u7684\u56FD\u5EA6.</p><p>\u5F53\u95E8\u5F92\u4EEC\u4ECE\u8BE2\u95EE\u4E8B\u4EF6\u8F6C\u4E3A\u8BE2\u95EE\u65F6\u95F4\u65F6, \u8036\u7A23\u7684\u56DE\u7B54\u975E\u5E38\u660E\u786E: \u6CA1\u6709\u4EBA\u77E5\u9053 ...... \u53EA\u6709\u5929\u7236\u77E5\u9053. \u867D\u7136\u6211\u4EEC\u4E0D\u80FD\u77E5\u9053\u672B\u65E5\u6765\u4E34\u7684\u65E5\u671F\u548C\u65F6\u95F4, \u4F46\u5374\u5E94\u8BE5\u65F6\u5E38\u8BB0\u4F4F, \u4EBA\u5B50\u5FC5\u5C06\u6765\u4E34. \u6211\u4EEC\u5FC5\u987B\u8981\u8B66\u9192, \u5E76\u4F5C\u597D\u5145\u5206\u51C6\u5907.</p><p>\u9762\u5BF9\u4F17\u591A\u6CA1\u6709\u628A\u63E1\u7684\u4E8B\u4EF6\u548C\u4E00\u5207\u4F3C\u4E4E\u663E\u5F97\u5931\u8D25\u4E14\u4EE4\u4EBA\u6CAE\u4E27\u65F6, \u8036\u7A23\u4EE5\u4E00\u4E2A\u7EDD\u4E0D\u52A8\u6447\u7684\u4FE1\u5FF5\u652F\u6301\u4FE1\u5F92\u4EEC &quot;\u5929\u5730\u90FD\u4F1A\u6D88\u901D,\u4F46\u6211\u7684\u8BDD\u6C38\u4E0D\u4F1A\u6D88\u901D&quot; (35).</p><h2 id="\u4EC6\u4EBA\u7B49\u5F85\u4E3B\u4EBA\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u4EC6\u4EBA\u7B49\u5F85\u4E3B\u4EBA\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u4EC6\u4EBA\u7B49\u5F85\u4E3B\u4EBA\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587-5" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-5" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>45</sup> \u201C\u7A76\u7ADF\u8C01\u662F\u90A3\u5FE0\u4FE1\u806A\u660E\u7684\u4EC6\u4EBA\uFF0C\u4E3B\u4EBA\u6D3E\u4ED6\u7BA1\u7406\u81EA\u5DF1\u7684\u5BB6\u4EC6\uFF0C\u6309\u65F6\u914D\u7ED9\u4ED6\u4EEC\u98DF\u7CAE\u5462\uFF1F<sup>46</sup> \u4E3B\u4EBA\u6765\u65F6\uFF0C\u770B\u89C1\u4ED6\u5982\u6B64\u884C\u4E8B\uFF0C\u90A3\u4EC6\u4EBA\u624D\u662F\u6709\u798F\u7684\u3002<sup>47</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4E3B\u4EBA\u5FC5\u8981\u59D4\u6D3E\u4ED6\u7BA1\u7406\u81EA\u5DF1\u7684\u4E00\u5207\u8D22\u4EA7\u3002<sup>48</sup> \u5982\u679C\u90A3\u6076\u4EC6\u5FC3\u91CC\u8BF4\uFF1A\u6211\u4E3B\u4EBA\u5FC5\u8981\u8FDF\u5EF6\uFF0C<sup>49</sup> \u4E8E\u662F\u5F00\u59CB\u62F7\u6253\u81EA\u5DF1\u7684\u540C\u4F34\uFF0C\u751A\u6216\u540C\u9189\u6C49\u4E00\u8D77\u5403\u559D\u3002<sup>50</sup> \u6B63\u5728\u4ED6\u4E0D\u671F\u5F85\u7684\u65E5\u5B50\uFF0C\u548C\u60F3\u4E0D\u5230\u7684\u65F6\u523B\uFF0C\u90A3\u4EC6\u4EBA\u7684\u4E3B\u4EBA\u8981\u6765\u5230\uFF0C <sup>51</sup> \u94F2\u9664\u4ED6\uFF0C\u4F7F\u4ED6\u4E0E\u5047\u5584\u4EBA\u906D\u53D7\u540C\u6837\u7684\u547D\u8FD0\uFF1B\u5728\u90A3\u91CC\u8981\u6709\u54C0\u53F7\u548C\u5207\u9F7F\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u65E0\u6CD5\u544A\u8BC9\u4ED6\u7684\u95E8\u5F92\u4EEC &quot;\u4F55\u65F6&quot; \u8FD9\u4E8B\u4F1A\u53D1\u751F, \u8FD9\u662F\u5929\u7236\u6240\u6301\u6709\u7684\u673A\u5BC6 \u8036\u7A23\u88AB\u9063\u6765\u4E16\u4E0A\u7684\u4EFB\u52A1\u5E76\u4E0D\u5305\u62EC\u542F\u793A\u8FD9\u4E2A\u673A\u5BC6. \u4F46\u662F, \u5982\u8BFA\u5384\u540C\u65F6\u4EE3\u7684\u4EBA\u4E00\u6837, \u4E16\u4EBA\u5DF2\u53D7\u5230\u8B66\u544A: \u8036\u8DEF\u6492\u51B7\u7684\u6BC1\u706D\u65E0\u53EF\u907F\u514D. \u7136\u800C\u50CF\u8BFA\u5384\u65F6\u4EE3, \u5927\u4E2D\u90E8\u4EFD\u7684\u4EBA\u6C11\u6CA1\u6709\u9632\u8303\u6D2A\u6C34\u7684\u7A81\u88AD, \u53EA\u6709\u9057\u6C11 &quot;\u5982\u8BFA\u5384\u5BB6&quot; \u88AB\u4FDD\u5168. \u8036\u7A23\u5F3A\u8C03\u90A3\u91CD\u8981\u7684\u6559\u8BAD: \u8981\u9192\u5BE4, \u8981\u968F\u65F6\u51C6\u5907\u597D. \u4ED6\u628A\u81EA\u5DF1\u6BD4\u64EC\u6210\u4E00\u4E2A\u76D7\u8D3C ( \u8FD9\u4E2A\u6BD4\u64EC, \u82E5\u4E0D\u662F\u8036\u7A23\u81EA\u5DF1\u9996\u521B, \u6CA1\u6709\u4EFB\u4F55\u57FA\u7763\u5F92\u6562\u5F15\u7528 ), \u5728\u65B0\u7EA6\u5176\u4ED6\u90E8\u5206, \u8FD9\u79CD\u9690\u55BB\u7528\u6765\u8868\u8FBE\u4ED6\u4EEC\u5BF9\u57FA\u7763\u5728\u672B\u65E5\u6765\u4E34\u7684\u4FE1\u5FC3: \u5386\u53F2\u7EC8\u7ED3\u4EE5\u524D\u5C06\u4E0D\u4F1A\u6709\u4EFB\u4F55 &quot;\u5F81\u5146&quot;, \u4F7F\u4EBA\u4EEC\u5F97\u4EE5\u9884\u77E5\u65F6\u65E5. \u76D1\u7763\u5BB6\u4EC6\u8005\u7684\u6BD4\u55BB, \u662F\u4E00\u4E2A\u6559\u5BFC\u5B97\u5F92\u4EEC\u5B97\u5F92\u4EEC\u5BF9\u4ED6\u4EEC\u7684\u7F8A\u7FA4\u5206\u5C42\u8D1F\u8D23\u7684\u6559\u8BAD ( \u4FDD\u7984\u663E\u7136\u662F\u5982\u6B64\u6765\u4E86\u89E3\u8FD9\u4E2A\u6BD4\u55BB ). \u5929\u56FD\u5E76\u975E\u4E00\u4E2A\u5B89\u5B81\u7684 &quot;\u9000\u4F11&quot;, \u800C\u662F\u66F4\u79EF\u6781\u7684\u6548\u5FE0\u57FA\u7763, \u53C2\u4E0E\u63A8\u5E7F\u5929\u56FD\u7684\u5DE5\u4F5C.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u6B64, \u4E00\u7CFB\u5217\u7684\u6BD4\u55BB\u548C\u8C61\u5F81\u6027\u7684\u8A00\u8BBA, \u90FD\u662F\u5728\u8B66\u544A\u95E8\u5F92\u8981\u9192\u5BE4. \u65E0\u82B1\u679C\u6811 ( 32 ~ 35 \u8282 ), \u4E0E\u5728\u5DF4\u52D2\u65AF\u5766\u5176\u5B83\u5927\u591A\u662F\u5E38\u9752\u7684\u6811\u79CD\u4E0D\u540C, \u65E0\u82B1\u679C\u6811\u4F1A\u5728\u51AC\u5929\u6389\u843D\u5168\u90E8\u7684\u6811\u53F6. \u6B63\u5982\u5B83\u7684\u53D1\u82BD\u662F\u590F\u65E5\u4E34\u8FD1\u7684\u8BB0\u53F7, \u524D\u9762\u51E0\u8282\u7684\u8BB0\u8FF0, \u4E5F\u662F\u5728\u63D0\u9192\u95E8\u5F92\u4EBA\u5B50\u7684\u6765\u4E34. \u8FD9\u91CC\u672B\u4E16\u5373\u5C06\u6765\u4E34\u7684\u90D1\u91CD\u5BA3\u544A ( 34 \u8282: &quot;\u975E\u5230\u8FD9\u4E00\u5207\u53D1\u751F\u4E86, \u8FD9\u4E00\u4EE3\u7EDD\u4E0D\u4F1A\u8FC7\u53BB&quot; ), \u4E0E\u524D\u8FF0 &quot;\u5929\u56FD\u7684\u798F\u97F3\u5FC5\u5148\u5728\u5168\u4E16\u754C\u5BA3\u8BB2, \u7ED9\u4E07\u6C11\u4F5C\u8BC1, \u7136\u540E\u7ED3\u5C40\u624D\u4F1A\u6765\u5230&quot; ( 14 \u8282 ) \u7684\u65AD\u8A00, \u8FD9\u4E24\u8005\u8BBA\u70B9\u4E4B\u95F4, \u5B58\u5728\u7740\u4E0D\u534F\u8C03. \u95E8\u5F92\u8981\u505A\u597D\u51C6\u5907, \u5E76\u575A\u5B9A\u4E0D\u79FB\u5730\u76F8\u4FE1\u8036\u7A23\u7684\u8BDD, \u7942\u7684\u8BDD\u7EDD\u4E0D\u4F1A\u8FC7\u53BB ( \u4E0E\u65E7\u7EA6\u6885\u745F\u6CD5\u5F8B\u76F8\u540C, \u53C2\u9605: \u739B\u4E8C\u5341\u516D 38, 40, 41 ).</p><p>\u524D\u9762\u6570\u8282\u5F3A\u8C03\u7684, \u662F\u5728\u4EBA\u5B50\u5230\u6765\u65F6, \u4EBA\u8981\u4FDD\u6301\u9192\u5BE4; \u800C\u4FE1\u7684\u4EC6\u4EBA\u8FD9\u4E2A\u6BD4\u55BB ( 45 ~ 51 \u8282 ), \u5219\u662F\u529D\u8BEB\u95E8\u5F92\u5728\u4E3B\u56DE\u6765\u524D\u5FC5\u987B\u5BF9\u6BCF\u65E5\u6240\u884C\u7684\u5F0F\u4F5C\u4FDD\u6301\u8B66\u9192. \u4EC6\u4EBA\u7684\u4EFB\u52A1\u4E4B\u4E00, \u662F\u5206\u914D\u65E5\u7528\u7684\u98DF\u7CAE ( 45 \u8282 ). \u8FD9\u4E2A\u7EC6\u8282\u4E5F\u8BB8\u662F\u6697\u6307\u521D\u671F\u6559\u4F1A\u5728\u98DF\u7269\u5206\u914D\u548C\u81B3\u98DF\u65B9\u9762\u6240\u9047\u5230\u7684\u56F0\u96BE, \u4F8B\u5982: \u5916\u90A6\u57FA\u7763\u5F92\u548C\u72B9\u592A\u57FA\u7763\u5F92\u4E00\u8D77\u8FDB\u9910\u65F6 ( \u8FE6\u4E8C 11 ~ 14 ), \u6216\u4E0D\u540C\u793E\u4F1A\u5730\u4F4D\u7684\u4EBA\u5728\u540C\u4E00\u5F20\u9910\u684C\u4E0A\u8FDB\u9910\u65F6 ( \u739B\u4E8C\u5341\u4E8C 1 ~ 14 ), \u6240\u4EA7\u751F\u7684\u51B2\u7A81.</p><p>\u53E6\u5916, \u5206\u914D\u7684\u98DF\u7269\u53EF\u7406\u89E3\u4E3A &quot;\u5929\u4E3B\u7684\u6559\u5BFC&quot;, \u8FD9\u662F\u739B\u7AA6\u5723\u53F2\u7684\u9690\u55BB\u7B14\u6CD5 ( \u5982: \u683C\u524D\u4E09 2; \u82E5\u516D 25 ~ 33 ), \u800C\u5FE0\u4FE1\u4EC6\u4EBA\u7684\u6BD4\u55BB, \u4E5F\u662F\u5BF9\u56E2\u4F53\u9886\u5BFC\u8005\u884C\u4F7F\u6559\u5BFC\u4F7F\u547D\u7684\u529D\u52C9. \u5BF9\u4FB5\u5360\u56E2\u4F53\u8D44\u6E90\u7684\u4EBA, \u8036\u7A23\u5BF9\u4ED6\u4EEC\u7684\u8B66\u544A\u76F8\u5F53\u53EF\u6015; \u540C\u65F6, \u53D7\u9063\u8D23\u7684\u4EBA\u4E5F\u8981\u88AB\u94F2\u9664 ( 51 \u8282, \u76F4\u8BD1\u4E3A &quot;\u5206\u5272\u4E3A\u4E8C&quot; ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-4" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-4" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4EC6\u4EBA\u7684\u6BD4\u55BB\u5F3A\u8C03\u95E8\u5F92\u4EEC\u5E94\u6709\u7684\u8B66\u9192\u548C\u6052\u5FC3\u5230\u5E95\u7684\u6001\u5EA6. \u4EC6\u4EBA\u5728\u4E3B\u4EBA\u5F52\u6765\u65F6\u8981\u5F97\u5230\u62A5\u5E94, \u6216\u5F97\u5230\u5956\u8D4F, \u6216\u53D7\u5230\u60E9\u7F5A.</p><p>\u8FD9\u4E2A\u6BD4\u55BB\u4E5F\u662F\u5728\u5631\u5490\u57FA\u7763\u5F92\u56E2\u4F53\u7684\u9886\u5BFC\u4EEC, \u8981\u6709\u4FDD\u6301\u8B66\u9192\u7684\u5FC3\u6001. \u4E3B\u4EBA\u5BF9\u8D1F\u8D23\u4EFB\u7684\u4EC6\u4EBA\u7684\u8981\u6C42\u4E5F\u5305\u62EC\u660E\u667A\u548C\u8B66\u9192. \u6BD4\u55BB\u4E5F\u5F3A\u8C03\u5FE0\u4FE1\u7684\u4EC6\u4EBA\u5C06\u4F1A\u83B7\u5F97\u5929\u56FD\u7684\u8D4F\u62A5, \u800C\u8FD9\u4E2A\u8D4F\u62A5\u5C31\u662F\u5929\u4E3B\u81EA\u5DF1\u548C\u7942\u7684\u4E8B\u4E1A, \u4E0D\u662F\u5176\u4ED6\u4EFB\u4F55\u4E8B\u7269. \u8FD9\u4E2A\u8D4F\u62A5, \u5728\u4EE5\u5F3A\u6743\u548C\u6CD5\u5F8B\u4E3B\u4E49\u6240\u5EFA\u7ACB\u7684\u793E\u4F1A\u91CC\u9762\u662F\u96BE\u4EE5\u7406\u89E3\u7684.</p>',68),o=[s];function h(r,d){return a(),e("div",null,o)}var q=u(t,[["render",h],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u56DB\u7AE0.html.vue"]]);export{q as default};
