import{_ as a,o as u,c as e,e as t}from"./app.6bf529a6.js";const h={},p=t('<h1 id="\u739B\u7AA6\u798F\u97F3\u4E8C\u5341\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u4E8C\u5341\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u4E8C\u5341\u7AE0</h1><h2 id="\u96C7\u5DE5\u7684\u6BD4\u55BB" tabindex="-1"><a class="header-anchor" href="#\u96C7\u5DE5\u7684\u6BD4\u55BB" aria-hidden="true">#</a> \u96C7\u5DE5\u7684\u6BD4\u55BB</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u5929\u56FD\u597D\u50CF\u4E00\u4E2A\u5BB6\u4E3B, \u6E05\u6668\u51FA\u53BB\u4E3A\u81EA\u5DF1\u7684\u8461\u8404\u56ED\u96C7\u5DE5\u4EBA. <sup>2</sup> \u4ED6\u4E0E\u5DE5\u4EBA\u8BAE\u5B9A\u4E00\u5929\u4E00\u4E2A &quot;\u5FB7\u7EB3&quot;. \u5C31\u6D3E\u4ED6\u4EEC\u5230\u8404\u8461\u56ED\u91CC\u53BB\u4E86. <sup>3</sup> \u7EA6\u5728\u7B2C\u4E09\u4E2A\u65F6\u8FB0, \u53C8\u51FA\u53BB, \u770B\u89C1\u53E6\u6709\u4E9B\u4EBA\u5728\u8857\u5E02\u4E0A\u95F2\u7ACB\u7740, <sup>4</sup> \u5C31\u5BF9\u4ED6\u4EEC\u8BF4: \u4F60\u4EEC\u4E5F\u5230\u6211\u7684\u8461\u8404\u56ED\u91CC\u53BB\u5427! \u51E1\u7167\u516C\u4E49\u8BE5\u7ED9\u7684, \u6211\u5FC5\u7ED9\u4F60\u4EEC. <sup>5</sup> \u4ED6\u4EEC\u5C31\u53BB\u4E86. \u7EA6\u5728\u7B2C\u516D\u548C\u7B2C\u4E5D\u4E2A\u65F6\u8FB0, \u4ED6\u53C8\u51FA\u53BB, \u4E5F\u7167\u6837\u4F5C\u4E86. <sup>6</sup> \u7EA6\u5728\u7B2C\u5341\u4E00\u65F6\u8FB0, \u4ED6\u53C8\u51FA\u53BB, \u770B\u89C1\u8FD8\u6709\u4E9B\u4EBA\u7AD9\u5728\u90A3\u91CC, \u5C31\u5BF9\u4ED6\u4EEC\u8BF4: \u4E3A\u4EC0\u4E48\u4F60\u4EEC\u7AD9\u5728\u8FD9\u91CC\u6574\u5929\u95F2\u7740? <sup>7</sup> \u4ED6\u4EEC\u5BF9\u4ED6\u8BF4: \u56E0\u4E3A\u6CA1\u6709\u4EBA\u96C7\u6211\u4EEC. \u4ED6\u7ED9\u4ED6\u4EEC\u8BF4: \u4F60\u4EEC\u4E5F\u5230\u6211\u7684\u8461\u8404\u56ED\u91CC\u53BB\u5427! <sup>8</sup> \u5230\u4E86\u665A\u4E0A, \u8461\u8404\u56ED\u7684\u4E3B\u4EBA\u5BF9\u4ED6\u7684\u7BA1\u4E8B\u4EBA\u8BF4: \u4F60\u53EB\u5DE5\u4EBA\u6765, \u5206\u7ED9\u4ED6\u4EEC\u5DE5\u8D44, \u7531\u6700\u540E\u7684\u5F00\u59CB, \u76F4\u5230\u6700\u5148\u7684. <sup>9</sup> \u90A3\u4E9B\u7EA6\u5728\u7B2C\u5341\u4E00\u65F6\u8FB0\u6765\u7684\u4EBA, \u6BCF\u4EBA\u9886\u4E86\u4E00\u4E2A &quot;\u5FB7\u7EB3&quot; . <sup>10</sup> \u90A3\u4E9B\u6700\u5148\u96C7\u7684\u524D\u6765, \u5FC3\u60F3\u81EA\u5DF1\u5FC5\u4F1A\u591A\u9886\uFF1B\u4F46\u4ED6\u4EEC\u53EA\u9886\u4E86\u4E00\u4E2A &quot;\u5FB7\u7EB3&quot;. <sup>11</sup> \u4ED6\u4EEC\u4E00\u9886\u4E86, \u5C31\u62B1\u6028\u5BB6\u4E3B. <sup>12</sup> \u8BF4: \u8FD9\u4E9B\u6700\u540E\u96C7\u7684\u4EBA, \u4E0D\u8FC7\u5DE5\u4F5C\u4E86\u4E00\u4E2A\u65F6\u8FB0, \u800C\u4F60\u7ADF\u628A\u4ED6\u4EEC\u4E0E\u6211\u4EEC\u8FD9\u6574\u5929\u53D7\u82E6\u7684\u53D7\u70ED\u7684\u540C\u7B49\u770B\u5F85. <sup>13</sup> \u4ED6\u7B54\u590D\u5176\u4E2D\u4E00\u4E2A\u8BF4: \u670B\u53CB! \u6211\u5E76\u6CA1\u6709\u4E8F\u5F85\u4F60, \u4F60\u4E0D\u662F\u548C\u6211\u8BAE\u5B9A\u4E86\u4E00\u4E2A &quot;\u5FB7\u7EB3&quot; \u5417? <sup>14</sup> \u62FF\u4F60\u7684\u8D70\u5427! \u6211\u613F\u610F\u7ED9\u6700\u540E\u7684\u548C\u4F60\u7684\u4E00\u6837. <sup>15</sup> \u96BE\u9053\u4E0D\u8BB8\u6211\u62FF\u6211\u6240\u6709\u7684\u8D22\u7269, \u884C\u6211\u6240\u613F\u610F\u7684\u5417? \u6216\u662F\u56E0\u4E3A\u6211\u597D, \u4F60\u5C31\u773C\u7EA2\u5417? <sup>16</sup> \u8FD9\u6837, \u6700\u540E\u7684, \u5C06\u6210\u4E3A\u6700\u5148\u7684, \u6700\u5148\u7684\u5C06\u6210\u4E3A\u6700\u540E\u7684.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u8BB0\u5F55\u7684\u8FD9\u4E2A\u6BD4\u55BB\u7684\u91CD\u70B9\u53EF\u4EE5\u4ECE\u4E3B\u4EBA\u6309\u4E0D\u5BFB\u5E38\u7684\u65B9\u5F0F\u5206\u914D\u5DE5\u8D44\u5F97\u77E5: &quot;\u7531\u6700\u540E\u7684\u5F00\u59CB, \u76F4\u5230\u6700\u5148\u7684&quot;. \u5728\u57FA\u7763\u5185, \u5F02\u6559\u7684\u7688\u4F9D\u8005\u4EAB\u53D7\u548C\u72B9\u592A\u4EBA\u7684\u57FA\u7763\u5F92\u540C\u7B49\u7684\u5F85\u9047. &quot;\u96BE\u9053\u4E0D\u8BB8\u6211\u62FF\u6211\u6240\u6709\u7684\u8D22\u7269, \u884C\u6211\u6240\u613F\u610F\u7684\u5417? \u6216\u662F\u56E0\u4E3A\u6211\u597D, \u4F60\u5C31\u773C\u7EA2\u5417?&quot; \u7528\u6765\u8BF4\u660E\u57FA\u7763\u7684\u5723\u53EC\u662F\u8D85\u81EA\u7136\u7684. \u4E5F\u662F\u4E2A\u7EDD\u5BF9\u7684\u6069\u8D50.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u6BD4\u55BB\u4EE5\u53CA\u524D\u9762\u7684\u60C5\u8282, \u90FD\u7528\u4E86\u540C\u4E00\u8C1A\u8BED &quot;\u5728\u5148\u7684\u8981\u6210\u4E3A\u540E\u7684&quot; ( \u739B\u5341\u4E5D 30; \u4E8C\u5341 16 ) \u6765\u505A\u7ED3\u8BBA. \u8FD9\u662F\u4E00\u4E2A\u5728\u65B0\u7EA6\u5723\u7ECF\u4E2D\u591A\u5916\u88AB\u5F15\u7528\u7684\u8C1A\u8BED, \u5B83\u4EE5\u4E0D\u540C\u7684\u6587\u5B57\u5F62\u5F0F, \u4E3A\u589E\u5F3A\u6548\u679C\u800C\u9644\u52A0\u5728\u5404\u5377\u65B0\u7EA6\u4F5C\u54C1\u7684\u4E0D\u540C\u6BB5\u843D\u4E2D ( \u4E5F\u53C2\u9605: \u8C37\u5341 31; \u8DEF\u5341\u4E09 30 ). \u5723\u53F2\u5E76\u6CA1\u6709\u63D0\u4F9B\u8FD9\u4E2A\u6BD4\u55BB\u7684\u5BD3\u610F. \u6545\u4E8B\u91CC\u5148\u50F1\u7528\u7684\u5DE5\u4EBA\u6700\u540E\u9886\u5230\u5DE5\u8D44, \u8FD9\u4E2A\u6BD4\u55BB\u6545\u4E8B\u7684\u8981\u70B9, \u4E43\u662F\u57FA\u4E8E\u5148\u50F1\u7528\u8005\u89C1\u5230\u540E\u6765\u88AB\u50F1\u7684\u9886\u5230\u7684\u5DE5\u8D44\u4E00\u6837\u591A. \u5DE5\u4EBA\u7684\u62B1\u6028 ( 12 \u8282 ) \u8BF4\u51FA\u4E86\u8FD9\u4E2A\u6BD4\u55BB\u7684\u4EE4\u4EBA\u56F0\u60D1\u4E4B\u5916, \u96BE\u9053\u516C\u4E49\u4E0D\u662F\u8981\u6C42\u5DE5\u4F5C\u66F4\u52AA\u529B\u7684\u4EBA\u9886\u8F83\u591A\u7684\u5DE5\u8D44\u5417? \u8461\u8404\u56ED\u7684\u4E3B\u4EBA\u5DF2\u627F\u8BFA\u5C06\u6309\u516C\u4E49\u652F\u4ED8\u5DE5\u8D44, \u5E76\u4E14\u5BB6\u4E3B\u8FD8\u575A\u6301\u4ED6\u6240\u505A\u7684\u5E76\u6CA1\u6709\u4E0D\u516C\u9053 ( 13 \u8282 ). \u6700\u540E, \u5BB6\u4E3B\u95EE\u9053: &quot;\u96BE\u9053\u4E0D\u8BB8\u6211\u62FF\u6211\u6240\u6709\u7684\u8D22\u7269, \u884C\u6211\u6240\u613F\u610F\u7684\u5417? \u6216\u662F\u56E0\u4E3A\u6211\u597D, \u4F60\u5C31\u773C\u7EA2\u5417?&quot; ( 15 \u8282 )</p><p>\u8461\u8404\u56ED\u4E3B\u7684\u56DE\u7B54, \u63D0\u51FA\u4E86\u4E24\u4E2A\u8981\u70B9, \u5982\u679C\u8461\u8404\u56ED\u4E3B\u662F\u5929\u4E3B\u7684\u8096\u50CF, \u90A3\u4E48, \u56ED\u4E3B\u7684\u884C\u4E3A\u663E\u793A\u51FA\u5929\u4E3B\u7684\u6177\u6168, \u800C\u5929\u4E3B\u7684\u6177\u6168\u4E0D\u662F\u51FA\u4E8E\u529F\u7EE9, \u66F4\u662F\u4E30\u5BCC\u5730\u8D50\u7ED9\u90A3\u4E9B\u771F\u6B63\u6709\u9700\u8981\u7684\u4EBA, \u5929\u4E3B\u7684\u6177\u6168\u5E76\u6CA1\u6709\u4E0D\u516C\u4E49, \u5B83\u4E0D\u662F\u53EF\u4EE5\u8BA1\u7B97\u51FA\u6765\u7684, \u4E5F\u4E0D\u662F\u9760\u8D5A\u53D6\u5C31\u5F97\u5230\u7684, \u8FD9\u4E2A\u6545\u4E8B\u662F\u8BF4\u4EBA\u4F1A\u770B\u5230\u4ED6\u4EEC\u5E94\u5F97\u5230\u7684, \u6240\u6709\u4EBA\u90FD\u6709\u5F97\u5230\u65E5\u7528\u7CAE\u7684\u6743\u5229. \u4ECE\u5904\u5728\u6BCF\u65E5\u9886\u53D6\u5DE5\u8D44\u7684\u52B3\u5DE5\u7ACB\u573A\u6765\u770B, \u4ED6\u4EEC\u662F\u7ECF\u6D4E\u4E0A\u6700\u7A77\u56F0\u7684\u82E6\u529B, \u6574\u5929\u7B49\u5728\u90A3\u91CC ( 6 \u8282 ), \u5E0C\u671B\u5F97\u5230\u5DE5\u4F5C, \u4F46\u5374\u6CA1\u6709\u88AB\u50F1\u7528, \u50F1\u4E3B\u7ED9\u4ED6\u4EEC\u7684\u5DE5\u8D44\u4EC5\u591F\u4ED6\u4EEC\u4F9B\u517B\u5BB6\u5EAD\u7684\u4E00\u65E5\u6240\u9700. \u5C11\u4E8E\u4E00\u4E2A\u5FB7\u7EB3\u7684\u5DE5\u8D44, \u5E76\u4E0D\u8DB3\u4EE5\u517B\u5BB6\u7CCA\u53E3. \u4ECE\u5148\u88AB\u50F1\u7528\u8005\u7684\u89C2\u772F\u6765\u770B, \u6E05\u6668\u5C31\u88AB\u50F1\u7528\u7684\u5DE5\u4EBA, \u867D\u7136\u4ED6\u4EEC\u5DE5\u4F5C\u7684\u65F6\u95F4\u548C, \u5DE5\u4F5C\u4E5F\u5F88\u52AA\u529B, \u4F46\u81F3\u5C11\u4ED6\u4EEC\u4E00\u6574\u5929\u90FD\u53EF\u4EE5\u6B23\u6170\u77E5\u9053\u65E5\u843D\u65F6, \u4ED6\u4EEC\u5C31\u4F1A\u6709\u80FD\u529B\u5582\u517B\u4ED6\u4EEC\u7684\u5BB6\u5EAD. \u5728\u5929\u4E3B\u7684\u56FD\u5EA6\u91CC, \u516C\u4E49\u7684\u610F\u601D\u662F: \u8BA9\u6240\u6709\u7684\u4EBA\u9971\u996B, \u662F\u5929\u4E3B\u516C\u5E73\u548C\u7231\u7684\u8C61\u5F81; \u516C\u4E49\u4E0D\u662F\u6307\u6211\u4EEC\u8981\u5F97\u5230\u6211\u4EEC\u5E94\u5F97\u7684, \u516C\u4E49\u7684\u6267\u884C\u65E2\u4E0D\u662F\u6309\u7167\u884C\u574F\u4E8B\u7684\u4EBA\u5F97\u5230\u7684\u62A5\u5E94\u60E9\u7F5A, \u4E5F\u4E0D\u6839\u636E\u884C\u5584\u8005\u6240\u5F97\u7684\u8D4F\u62A5.</p><p>\u8461\u8404\u56ED\u4E3B\u56DE\u7B54\u7684\u7B2C\u4E8C\u4E2A\u8981\u70B9, \u662F\u5728\u4E8E &quot;\u90AA\u6076\u7684\u773C\u795E&quot;. \u5992\u5AC9\u5728\u56E2\u4F53\u4E2D\u662F\u6700\u5177\u7834\u574F\u6027\u7684: &quot;\u96BE\u9053\u4E0D\u8BB8\u6211\u62FF\u6211\u6240\u6709\u7684\u8D22\u7269, \u884C\u6211\u6240\u613F\u610F\u7684\u5417? \u6216\u662F\u56E0\u4E3A\u6211\u597D, \u4F60\u5C31\u773C\u7EA2\u5417?&quot; ( 15 \u8282 ) \u6309\u5B57\u9762\u610F\u4E49, \u8FD9\u53E5\u8BDD\u53EF\u4EE5\u61C2\u6210: &quot;\u96BE\u9053\u56E0\u4E3A\u6211\u597D, \u4F60\u5C31\u76EE\u5149\u6076\u6BD2\u5417?&quot; \u5728\u4E3B\u5386\u7B2C\u4E00\u4E16\u7EAA\u7684\u4E16\u754C\u89C2\u4E2D, \u5229\u76CA\u662F\u6709\u9650\u7684, \u6240\u4EE5\u5F53\u67D0\u4E00\u4E2A\u4EBA\u5F97\u5230\u5229\u76CA\u65F6, \u5C31\u610F\u5473\u7740\u53E6\u4E00\u4E2A\u4EBA\u5C06\u8981\u53D7\u635F\u5931. \u6EE1\u8179\u7262\u9A9A\u7684\u4EBA\u5728\u53EA\u5173\u6CE8\u81EA\u5DF1\u7684\u5F97\u5931\u65F6, \u5374\u9519\u8FC7\u4E86\u56ED\u4E3B\u65E0\u9650\u826F\u5584\u548C\u6177\u6168. \u7EFC\u5408\u4EE5\u524D\u8BA8\u8BBA\u8FC7\u7684\u4E00\u4E2A\u6709\u5173\u56E0\u8D22\u5BCC\u5BFC\u81F4\u5371\u9669\u7684\u8BAE\u9898, \u8FD9\u4E2A\u6BD4\u55BB\u662F\u5728\u6311\u6218\u90A3\u4E9B\u6709\u8DB3\u591F\u65E5\u7528\u6240\u9700\u7684\u95E8\u5F92, \u8981\u6C42\u4ED6\u4EEC\u62D2\u7EDD\u8D2A\u5A6A, \u5E76\u5173\u5FC3\u90A3\u4E9B\u5728\u7EDD\u671B\u56F0\u5883\u4E2D\u7684\u4EBA\u6240\u9700.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6709\u5173\u653E\u5F03\u4E00\u5207\u5E76\u8DDF\u968F\u8036\u7A23\u7684\u4EBA\u8981\u5F97\u5230\u7684\u8D4F\u62A5, \u8036\u7A23\u4EE5\u8FD9\u4E2A\u6BD4\u55BB\u4F5C\u7ED3.</p><p>\u5BCC\u5C11\u5E74\u5BF9\u4E8E\u81EA\u5DF1\u8D22\u5BCC\u7684\u8FF7\u604B, \u4E0E\u5929\u4E3B\u5BF9\u4EBA\u7684\u8D4F\u8D50\u8D85\u8FC7\u4EBA\u6240\u5E94\u5F97\u7684\u6177\u6168, \u5F62\u6210\u4E86\u9C9C\u660E\u7684\u5BF9\u6BD4. \u5929\u4E3B\u7684\u4EC1\u6148\u5E76\u4E0D\u76F8\u53CD\u4EBA\u7C7B\u7684\u516C\u4E49, \u53CD\u800C\u4EE5\u7231\u63D0\u5347\u516C\u4E49. \u5929\u4E3B\u4E0D\u4F1A\u56E0\u4E3A\u6177\u6168\u800C\u53D8\u5F97\u4E0D\u516C\u4E49. \u8FD9\u91CC\u7684\u95EE\u9898\u4E0D\u5728\u4E8E\u5206\u914D\u662F\u5426\u5747\u5300\u5E73\u7B49 ( \u516C\u4E49 ), \u800C\u5728\u4E8E\u5BF9\u5982\u6B64\u5206\u914D\u7684\u611F\u6FC0.</p><p>\u6069\u5BA0\u5C31\u5728\u4E8E\u6240\u65BD\u4E88\u7684\u7231\u8D85\u8D8A\u4EBA\u7C7B\u516C\u4E49\u7684\u6807\u51C6. \u8036\u7A23\u65F6\u4EE3\u7684\u6CD5\u5F8B\u4E3B\u4E49\u6240\u7406\u89E3\u7684\u5929\u4EBA\u5173\u7CFB, \u4E5F\u57FA\u4E8E\u4EBA\u7684\u529F\u5FB7\u548C\u5929\u4E3B\u5E94\u5F53\u7ED9\u4E88\u7684\u62A5\u916C ( \u7279\u522B\u501F\u9075\u5B88\u6CD5\u5F8B\u89C4\u6761\u6240\u5F97\u5230\u7684\u62A5\u916C ). \u4F46\u662F, \u8036\u7A23\u6240\u575A\u6301\u548C\u6559\u5BFC\u7684\u5374\u6070\u6070\u76F8\u53CD. \u4EBA\u7C7B\u548C\u5929\u7236\u4E4B\u95F4\u7684\u5173\u7CFB, \u5E76\u975E\u5EFA\u7ACB\u5728\u9075\u5B88\u6CD5\u5F8B\u7684\u529F\u5FB7\u4E0A, \u800C\u662F\u5EFA\u7ACB\u5728\u7231\u4E0A. \u5929\u4E3B\u901A\u8FC7\u8036\u7A23\u6E05\u695A\u5730\u8868\u660E: \u5929\u4E3B\u4E0D\u4F1A\u770B\u91CD\u4EBA\u7684\u529F\u5FB7, \u800C\u662F\u770B\u91CD\u4EBA\u7C7B\u7684\u9700\u8981. \u90A3\u4E9B\u9700\u8981\u7942\u7684\u4EBA\u5C31\u4F1A\u5F97\u5230, \u800C\u4E0D\u662F\u582A\u5F53\u7942\u7231\u7684\u4EBA.</p><p>\u5C31\u5982\u540C\u90A3\u4E2A\u8461\u8404\u56ED\u7684\u4E3B\u4EBA\u4E00\u6837, \u5929\u4E3B\u4E5F\u4E3B\u52A8\u5730\u767D\u767D\u8D50\u7ED9\u81EA\u5DF1\u7684\u513F\u5973\u4EEC\u6069\u5BA0, \u8FDC\u8D85\u8FC7\u6211\u4EEC\u52B3\u52A8\u5E94\u5F97\u7684\u8D4F\u62A5. \u8036\u7A23\u5C31\u4EE5\u8FD9\u79CD\u6001\u5EA6, \u56DE\u5E94\u90A3\u4E9B\u53CD\u5BF9\u7942\u548C\u7A0E\u540F, \u7F6A\u4EBA\u4EA4\u5F80\u7684\u6CD5\u5F8B\u4E3B\u4E49\u8005 ( \u53C2\u9605 9: 11 ). \u540E\u6765, \u5728\u739B\u7AA6\u5F53\u65F6\u7684\u57FA\u7763\u56E2\u4F53\u4E2D, \u4E00\u4E9B\u72B9\u592A\u88D4\u57FA\u7763\u5F92\u4E5F\u4E0D\u660E\u767D, \u4E3A\u4EC0\u4E48\u90A3\u4E9B\u540E\u6765\u52A0\u5165\u7684\u5916\u90A6\u4EBA\u6709\u8D44\u683C\u5728\u6559\u4F1A\u4E2D\u4E0E\u4ED6\u4EEC\u5206\u4EAB\u540C\u6837\u7684\u5730\u4F4D.</p><p>\u8FD9\u4E2A\u6BD4\u55BB\u544A\u8BC9\u6211\u4EEC, \u8FD9\u91CC\u6240\u6D89\u53CA\u7684\u662F\u4E00\u4EFD\u6069\u8D50, \u662F\u6211\u4EEC\u5F53\u4E0D\u8D77\u63A5\u53D7, \u4E14\u4E3A\u6BCF\u4E2A\u4EBA\u90FD\u4E00\u6837\u7684\u793C\u7269. \u4E0E\u6211\u4EEC\u540C\u5728\u7684\u5929\u4E3B\u5BF9\u6211\u4EEC\u5C31\u662F\u8FD9\u4E48\u597D!</p><h2 id="\u4E09\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u4E09\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B" aria-hidden="true">#</a> \u4E09\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>17</sup> \u8036\u7A23\u4E0A\u8036\u8DEF\u6492\u51B7\u53BB, \u6697\u6697\u628A\u5341\u4E8C\u4E2A\u95E8\u5F92\u5E26\u5230\u4E00\u8FB9, \u5728\u8DEF\u4E0A\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u770B, \u6211\u4EEC\u4E0A\u8036\u8DEF\u6492\u51B7\u53BB, \u4EBA\u5B50\u8981\u88AB\u4EA4\u4E8E\u53F8\u796D\u548C\u7ECF\u5E08, \u4ED6\u4EEC\u8981\u5B9A\u4ED6\u7684\u6B7B\u7F6A; <sup>19</sup> \u5E76\u4E14\u628A\u4ED6\u4EA4\u7ED9\u5916\u90A6\u4EBA\u620F\u5F04, \u97AD\u6253, \u9489\u6B7B; \u4F46\u7B2C\u4E09\u5929, \u4ED6\u8981\u590D\u6D3B.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u8D44\u8036\u7A23\u6240\u9884\u8A00\u7684\u82E6\u96BE\u8F83\u524D\u66F4\u4E3A\u4ED4\u7EC6, ( \u5982\u540C\u9A6C\u5C14\u8C37\u548C\u8DEF\u52A0\u798F\u97F3 ). \u798F\u97F3\u4F5C\u8005\u4EEC\u5982\u6B64\u8BE6\u76DB\u7684\u8BB0\u8F7D, \u5E76\u672A\u51CF\u4F4E\u8FD9\u4EF6\u4E8B\u7684\u9884\u8A00\u6027.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u7B2C\u4E09\u6B21\u6240\u9884\u8A00\u7684\u82E6\u96BE, \u8F83\u524D\u4E24\u6B21\u66F4\u52A0\u8BE6\u7EC6. \u7B2C\u4E09\u6B21\u9884\u8A00\u53D1\u751F\u5728\u8036\u7A23\u548C\u95E8\u5F92\u4EEC\u5FEB\u8981\u5230\u8FBE\u8036\u8DEF\u6492\u51B7\u7684\u65F6\u5019. \u5728\u7B2C\u4E00\u4E2A\u9884\u8A00\u4E2D ( \u739B\u5341\u516D 21 - 22 ), \u8036\u7A23\u544A\u8BC9\u95E8\u5F92\u7942\u4F1A\u7531\u957F\u8001, \u53F8\u796D\u957F\u4EE5\u53CA\u7ECF\u5E08\u4EEC\u90A3\u91CC\u53D7\u5230\u8BB8\u591A\u75DB\u82E6, \u5E76\u5C06\u88AB\u6740. \u4E8B\u5B9E\u4E0A, \u72B9\u592A\u9886\u8896\u5E76\u6CA1\u6709\u6267\u884C\u6B7B\u5211\u7684\u6743\u529B ( \u53C2\u9605: \u82E5\u5341\u516B 31 ). \u8036\u7A23\u5B9E\u9645\u4E0A\u662F\u8981\u88AB\u4EA4\u4ED8\u5728\u5916\u90A6\u4EBA\u624B\u4E2D ( 19 \u8282 ), \u88AB\u5916\u90A6\u4EBA\u6740\u6B7B ( \u53C2\u89C1: \u7B2C\u4E8C\u6B21\u9884\u8A00, \u8036\u7A23\u5927\u81F4\u8BF4\u5230\u7942\u5C06\u88AB\u51FA\u5356\u548C\u4EA4\u4E8E\u4EBA\u4EEC\u624B\u4E2D, \u739B\u5341\u4E03 22 ~ 23 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u7B2C\u4E09\u6B21\u9884\u8A00\u81EA\u5DF1\u7684\u82E6\u96BE\u4E0E\u590D\u6D3B, \u66F4\u52A0\u5177\u4F53\u800C\u8BE6\u7EC6. \u56E0\u6B64, \u798F\u97F3\u4ECE\u6839\u672C\u4E0A\u6307\u5411\u8036\u7A23\u6218\u80DC\u6B7B\u4EA1\u548C\u590D\u6D3B\u7684\u5965\u8FF9.</p><h2 id="\u8F7D\u4F2F\u5FB7\u4E8C\u5B50\u6BCD\u4EB2\u7684\u8981\u6C42" tabindex="-1"><a class="header-anchor" href="#\u8F7D\u4F2F\u5FB7\u4E8C\u5B50\u6BCD\u4EB2\u7684\u8981\u6C42" aria-hidden="true">#</a> \u8F7D\u4F2F\u5FB7\u4E8C\u5B50\u6BCD\u4EB2\u7684\u8981\u6C42</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>20</sup> \u90A3\u65F6, \u8F7D\u4F2F\u5FB7\u513F\u5B50\u7684\u6BCD\u4EB2, \u540C\u81EA\u5DF1\u7684\u513F\u5B50\u524D\u6765, \u53E9\u62DC\u8036\u7A23, \u4ED6\u8BF7\u6C42\u4E00\u4EF6\u4E8B. <sup>21</sup> \u8036\u7A23\u5BF9\u5979\u8BF4: &quot;\u4F60\u8981\u4EC0\u4E48?&quot; \u5979\u56DE\u7B54\u8BF4: &quot;\u4F60\u53EB\u6211\u7684\u4E24\u4E2A\u513F\u5B50, \u5728\u4F60\u7684\u738B\u56FD\u5185, \u4E00\u4E2A\u5750\u5728\u4F60\u7684\u5728\u53F3\u8FB9, \u4E00\u4E2A\u5750\u5728\u4F60\u7684\u5DE6\u8FB9.&quot; <sup>22</sup> \u8036\u7A23\u56DE\u7B54\u8BF4: &quot;\u4F60\u4EEC\u4E0D\u77E5\u9053\u4F60\u4EEC\u6240\u6C42\u7684\u662F\u4EC0\u4E48. \u4F60\u4EEC\u80FD\u996E\u6211\u5C06\u8981\u996E\u7684\u7235\u5417?&quot; \u4ED6\u4EEC\u8BF4: &quot;\u6211\u4EEC\u80FD.&quot; <sup>23</sup> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u6211\u7684\u7235\u4F60\u4EEC\u56FA\u7136\u8981\u996E, \u4F46\u5750\u5728\u53F3\u8FB9\u6216\u5DE6\u8FB9, \u4E0D\u662F\u6211\u53EF\u4EE5\u7ED9\u7684, \u800C\u662F\u6211\u7236\u7ED9\u8C01\u9884\u5907\u4E86, \u5C31\u7ED9\u8C01.&quot; <sup>24</sup> \u90A3\u5341\u4E2A\u542C\u4E86, \u5C31\u607C\u6012\u4ED6\u4EEC\u4E24\u5144\u5F1F.</p><p><sup>25</sup> \u8036\u7A23\u53EB\u8FC7\u4ED6\u4EEC\u6765\u8BF4: &quot;\u4F60\u4EEC\u77E5\u9053: \u5916\u90A6\u4EBA\u6709\u9996\u957F\u4E3B\u5BB0\u4ED6\u4EEC, \u6709\u5927\u81E3\u7BA1\u8F96\u4ED6\u4EEC. <sup>26</sup> \u5728\u4F60\u4EEC\u4E2D\u5374\u4E0D\u53EF\u8FD9\u6837, \u8C01\u82E5\u613F\u610F\u5728\u4F60\u4EEC\u4E2D\u6210\u4E3A\u5927\u7684, \u5C31\u5F53\u4F5C\u4F60\u4EEC\u7684\u4EC6\u5F79; <sup>27</sup> \u8C01\u82E5\u613F\u610F\u5728\u4F60\u4EEC\u4E2D\u4E3A\u9996, \u5C31\u5F53\u4F5C\u4F60\u4EEC\u7684\u5974\u4EC6. <sup>28</sup> \u5C31\u5982\u4EBA\u5B50\u6765\u4E0D\u662F\u53D7\u670D\u4E8B, \u800C\u662F\u670D\u4E8B\u4EBA, \u5E76\u4EA4\u51FA\u81EA\u5DF1\u7684\u751F\u547D, \u4E3A\u5927\u4F17\u4F5C\u8D4E\u4EF7.&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8F7D\u4F2F\u5FB7\u7684\u6BCD\u4EB2\u8BF4\u51FA\u4E86\u4ED6\u4E24\u4E2A\u513F\u5B50\u7684\u613F\u671B. \u9002\u8DB3\u4EE5\u663E\u9732\u51FA\u4ED6\u4EEC\u5BF9\u5929\u56FD\u7684\u8BA4\u8BC6\u4F55\u5176\u80A4\u6D45. \u8036\u7A23\u7B54\u5E94\u4ED6\u4EEC\u4E0E\u4ED6\u5171\u5386\u82E6\u96BE; \u5728\u5929\u4E3B\u7236\u5185\u540C\u4EAB\u4ED6\u7684\u80DC\u5229. \u522B\u7684\u5B97\u5F92\u8868\u73B0\u51FA\u607C\u6012\u7684\u60C5\u7DD2, \u5929\u56FD\u91CC\u7684\u6807\u51C6\u548C\u8FD9\u91CC\u7684\u5927\u76F8\u5F84\u5EAD. \u8036\u7A23\u8BA9\u5927\u5BB6\u77E5\u9053, \u4ED6\u81EA\u5DF1\u77E5\u6653\u4ED6\u6765\u4E16\u754C\u505A\u53D7\u82E6\u7684\u4EC6\u4EBA\u7684\u4EFB\u52A1. \u4E5F\u77E5\u9053\u4ED6\u7684\u6B7B\u4EA1\u6240\u5177\u4F53\u7684\u53D7\u82E6\u7684\u672C\u8D28. &quot;\u5927\u4F17&quot; \u53EF\u80FD\u610F\u6307 &quot;\u56E2\u4F53&quot;, \u8C37\u6728\u5170\u4E2D\u7684\u7528\u6CD5\u76F8\u540C. \u56E0\u6B64\u6559\u4F1A\u7528\u8FD9\u79CD\u5F62\u5F0F\u5979\u5BF9\u5979\u7684\u6C42\u8D4E\u8005\u6240\u5177\u6DF1\u523B\u611F\u6069\u4E4B\u60C5.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u4E25\u8083\u5730\u9884\u8A00\u53D7\u96BE\u540E, \u95E8\u5F92\u4EEC\u5C31\u7ACB\u523B\u95EE\u5929\u56FD\u4E2D\u8C01\u6700\u5927\u800C\u4E89\u8BBA, \u770B\u8D77\u6765\u662F\u4E0D\u592A\u53EF\u80FD\u7684. \u6240\u4EE5, \u739B\u7AA6\u5723\u53F2\u5C31\u91CD\u65B0\u7F16\u5199\u4E86\u300A\u9A6C\u5C14\u8C37\u798F\u97F3\u300B\u7684\u8FD9\u6BB5\u8BB0\u8F7D ( \u53C2\u9605: \u8C37\u5341 35 ~ 45 ), \u8FD9\u5929\u56FD\u4E2D\u8C01\u6700\u5927\u7684\u8BF7\u6C42, \u5C31\u4E0D\u662F\u7531\u96C5\u5404\u4F2F\u53CA\u82E5\u671B\u4E24\u4F4D\u95E8\u5F92\u7684\u53E3\u4E2D\u8BF4\u51FA\u4E86, \u800C\u662F\u7531\u4E24\u4F4D\u6BCD\u4EB2\u4EE3\u4E3A\u63D0\u51FA, \u5B83\u7F13\u548C\u4E86\u5BF9\u95E8\u5F92\u7684\u6279\u8BC4, \u5E76\u5C06\u96C5\u5404\u4F2F\u53CA\u82E5\u671B\u7684\u6BCD\u4EB2\u63CF\u5199\u6210\u4E3A\u4E00\u4F4D\u5177\u6709\u5C5E\u4E16\u91CE\u5FC3\u7684\u4EBA. \u5176\u4ED6\u5341\u4F4D\u95E8\u5F92\u5230\u5E95\u662F\u56E0\u4E3A\u4E24\u4F4D\u80C6\u5927\u5984\u4E3A\u5730\u8981\u6C42\u800C\u607C\u6012, \u8FD8\u662F\u56E0\u8FD9\u4F4D\u6392\u65A5\u522B\u4EBA, \u53EA\u4E3A\u81EA\u5DF1\u4E89\u53D6\u5929\u56FD\u4E2D\u7684\u5D07\u9AD8\u5730\u4F4D\u800C\u611F\u5230\u6C14\u6124 ( 24 \u8282 ), \u8FD9\u91CC\u5E76\u6CA1\u6709\u660E\u793A.</p><p>\u5728\u65E7\u7EA6\u4E2D, &quot;\u676F\u7235&quot; \u5E38\u88AB\u7528\u6765\u6BD4\u55BB\u4EE5\u8272\u5217\u7684\u82E6\u96BE ( \u53C2\u9605: \u4F9D\u4E94\u4E00 17; \u8036\u4E8C\u5341\u4E94 15; \u56DB\u4E5D 12; \u4E94\u4E00 7; \u54C0\u56DB 21 ). \u8036\u7A23\u5728\u5C71\u56ED\u7948\u7977\u65F6, \u6073\u6C42\u5929\u4E3B\u8BA9\u8FD9 &quot;\u676F&quot; \u79BB\u5F00\u7942 ( \u739B\u4E8C\u5341\u516D 39 ). \u7136\u540E\u8036\u7A23\u8BAD\u793A\u95E8\u5F92\u6709\u5173\u4ED6\u4EEC\u884C\u4F7F\u9886\u5BFC\u7684\u65B9\u5F0F. \u4ED6\u4EEC ( \u4F5C\u4E3A\u9886\u5BFC\u4EBA ) \u4E0D\u8981 &quot;\u4E3B\u5BB0&quot; \u4ED6\u4EBA, \u800C\u5E94\u50CF\u8036\u7A23\u90A3\u6837\uFF0C \u670D\u52A1\u56E2\u4F53\u91CC\u7684\u6210\u5458\u3002 \u8036\u7A23\u7684\u670D\u4E8B\u662F\u670D\u52A1\u81F3\u6B7B, &quot;\u4EA4\u51FA\u81EA\u5DF1\u7684\u751F\u547D, \u4E3A\u5927\u4F17\u4F5C\u8D4E\u4EF7&quot;. &quot;\u5927\u4F17&quot; \u8BD1\u81EA\u5E0C\u814A\u5B9A polys, \u610F\u679C\u662F &quot;\u8BB8\u591A&quot; (many), \u5728\u8FD9\u91CC, \u6709\u4E0D\u8981\u628A\u4EFB\u4F55\u4EBA\u6392\u9664\u5728\u5916\u7684\u610F\u601D. \u5B83\u53CD\u6620\u95EA\u6587\u5B57\u7684\u8868\u8FBE\u65B9\u5F0F: &quot;\u8BB8\u591A&quot; \u662F &quot;\u4E00\u4E2A&quot; \u7684\u53CD\u4E49\u8BCD, \u56E0\u800C &quot;\u8BB8\u591A&quot; \u5C31\u4E0E &quot;\u5168\u90E8&quot; \u540C\u4E49.</p><p>\u5728 &quot;\u8036\u7A23\u6368\u53BB\u81EA\u5DF1\u7684\u751F\u547D, \u6210\u4E3A\u8D4E\u4EF7&quot; \u7684\u8FD9\u89C2\u5FF5\u4E2D, \u662F\u540C\u4EE5\u4E0B\u4E00\u5E45\u56FE\u50CF\u6765\u7684: \u5373\u4E00\u4E2A\u5974\u96B6\u7528\u91D1\u94B1 ( \u8D4E\u4EF7 ) \u4E70\u56DE\u4ED6\u7684\u81EA\u7531. \u8FD9\u4E00\u6BB5\u7684\u6700\u540E\u51E0\u8282\u7ECF\u6587, \u662F\u9488\u5BF9\u62E5\u6709\u4E00\u5B9A\u7A0B\u5EA6\u6743\u529B, \u7279\u6743, \u5730\u4F4D\u53CA\u51B3\u7B56\u7684\u9886\u5BFC\u4EBA\u6240\u8BF4\u7684: \u4ED6\u4EEC\u82E5\u9009\u62E9\u63A5\u53D7\u5351\u5FAE\u804C\u4F4D\u6765\u670D\u4E8B, \u662F\u4E0D\u592A\u9002\u5408\u7684.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u7EE7\u7EED\u8C08\u53CA\u5929\u56FD\u4E2D\u8C01\u5927\u8C01\u5C0F\u7684\u95EE\u9898. \u4E0D\u8FC7, \u8FD9\u4E00\u6B21\u6D89\u53CA\u6743\u529B\u7684\u8303\u56F4. \u8FD9\u6BB5\u60C5\u8282\u53D1\u751F\u5728\u90A3\u5341\u4E8C\u4EBA\u7684\u5708\u5B50\u91CC, \u8036\u7A23\u501F\u6B64\u6307\u51FA\u4E86\u5B97\u5F92\u4EEC\u5BF9\u5E08\u5085\u6559\u5BFC\u7684\u8BEF\u89E3. \u7942\u4ECE\u4E00\u4EF6\u5177\u4F53\u7684\u4E8B\u5B9E, \u7EE7\u800C\u8C08\u5230\u4E86\u4E00\u4E2A\u56E2\u4F53\u4E2D\u5FC5\u987B\u6709\u7684\u539F\u5219: \u5728\u5929\u56FD\u91CC\u9762, \u6700\u91CD\u8981\u7684\u4E0D\u662F\u62E5\u6709\u4E00\u4E2A\u5149\u8363\u7684\u5730\u4F4D, \u800C\u662F\u9075\u5FAA\u8036\u7A23\u7684\u9053\u8DEF, \u4EA4\u4ED8\u81EA\u5DF1, \u670D\u5F85\u6700\u9700\u8981\u5E2E\u52A9\u7684\u4EBA.</p><p>\u8FD9\u4E2A\u6559\u8BAD\u4E0D\u4EC5\u9488\u5BF9\u5F53\u65F6\u8DDF\u968F\u8036\u7A23\u7684\u95E8\u5F92\u4EEC, \u4E5F\u6307\u5411\u739B\u7AA6\u65F6\u4EE3, \u4EE5\u53CA\u4ECA\u5929\u7684\u57FA\u7763\u5F92\u56E2\u4F53. \u8FD9\u4E2A\u56E2\u4F53\u8981\u6297\u8861\u64CD\u7EB5\u4E16\u754C\u7684\u9738\u6743\u7EDF\u6CBB\u6A21\u5F0F, \u597D\u4F7F\u5168\u7403\u5316\u7684\u56E2\u7ED3\u5408\u4E00\u6210\u4E3A\u53EF\u80FD. \u6700\u5927\u7684\u8981\u6210\u4E3A\u670D\u52A1\u4EBA\u7684; \u6700\u5148\u7684\u8981\u6210\u4E3A\u6240\u6709\u4EBA\u7684\u4EC6\u4EBA.</p><h2 id="\u8036\u91CC\u54E5\u7684\u778E\u5B50" tabindex="-1"><a class="header-anchor" href="#\u8036\u91CC\u54E5\u7684\u778E\u5B50" aria-hidden="true">#</a> \u8036\u91CC\u54E5\u7684\u778E\u5B50</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>29</sup> \u4ED6\u4EEC\u7531\u8036\u91CC\u54E5\u51FA\u6765\u65F6, \u6709\u8BB8\u591A\u7FA4\u4F17\u8DDF\u968F\u8036\u7A23, <sup>30</sup> \u6709\u4E24\u4E2A\u778E\u4E86\u5728\u8DEF\u65C1, \u542C\u8BF4\u8036\u7A23\u8DEF\u8FC7, \u5C31\u558A\u53EB\u8BF4: &quot;\u4E3B, \u8FBE\u5473\u4E4B\u5B50, \u53EF\u601C\u6211\u4EEC\u5427!&quot; \u6C11\u4F17\u65A5\u8D23\u4ED6\u4EEC, <sup>31</sup> \u53EB\u4ED6\u4EEC\u4E0D\u8981\u4F5C\u58F0; \u4ED6\u4EEC\u53CD\u800C\u66F4\u558A\u53EB\u8BF4: &quot;\u4E3B, \u8FBE\u5473\u4E4B\u5B50, \u53EF\u601C\u6211\u4EEC\u5427!&quot; <sup>32</sup> \u8036\u7A23\u5C31\u7AD9\u4F4F, \u53EB\u8FC7\u4ED6\u4EEC\u6765, \u8BF4: &quot;\u4F60\u4EEC\u613F\u610F\u6211\u7ED9\u4EEC\u4F5C\u4EC0\u4E48?&quot; <sup>33</sup> \u4ED6\u4EEC\u56DE\u7B54\u8BF4: &quot;\u4E3B, \u53EB\u6211\u4EEC\u773C\u775B\u5F00\u5F00\u5427!&quot; <sup>34</sup> \u8036\u7A23\u52A8\u4E86\u6148\u5FC3, \u6478\u4E86\u6478\u4ED6\u4EEC\u7684\u773C\u775B, \u4ED6\u4EEC\u5C31\u7ACB\u523B\u770B\u89C1\u4E86, \u4E5F\u8DDF\u4ED6\u53BB\u4E86.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u91CC\u6211\u4EEC\u770B\u5230\u4E86\u739B\u7AA6\u5904\u5FC3\u673A\u8651\u4FDD\u5B58\u6709\u5173\u8036\u7A23\u7684\u4F20\u95FB\u7684\u53E6\u4E00\u4E2A\u4F8B\u5B50. \u50CF\u65E7\u7EA6\u7684\u4F5C\u8005, \u4ED6\u628A\u540C\u4E00\u4E8B\u4EF6\u7684\u4E0D\u540C\u8BF4\u6CD5\u90FD\u6536\u96C6\u5230\u4ED6\u7684\u798F\u97F3\u91CC, \u56E0\u4E3A\u4ED6\u4EEC\u90FD\u6709\u5176\u72EC\u7279\u7684\u4EF7\u503C. \u73B0\u4EE3\u7684\u5386\u53F2\u4F20\u8BB0\u4F5C\u8005\u662F\u7EFC\u5408\u8BB8\u591A\u6765\u6E90\u548C\u8BF4\u6CD5\u6765\u5199\u51FA\u4E00\u7BC7\u62A5\u9053, \u800C\u8FD9\u4F4D\u798F\u97F3\u4F5C\u5BB6\u5374\u8BA4\u4E3A, \u8FD9\u4E9B\u8D44\u6599\u5B9E\u5728\u73CD\u8D35\u795E\u5723, \u4E0D\u53EF\u5254\u9664.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D, \u6700\u540E\u4E00\u4E2A\u6CBB\u7652\u7684\u795E\u8FF9, \u5B83\u662F\u5728\u53CD\u6620\u739B\u4E5D 27 ~ 31 \u7684\u63CF\u8FF0, \u8FD9\u91CC\u7684\u4E24\u778E\u5B50, \u4E5F\u5BF9\u8036\u7A23\u5927\u58F0\u547C\u558A\u8BF4 &quot;\u8FBE\u5473\u4E4B\u5B50, \u53EF\u601C\u6211\u4EEC\u7F62&quot; ( \u53C2\u9605\u739B\u5341\u4E8C, \u5341\u4E9422 ). \u8036\u7A23\u6559\u5BFC\u95E8\u5F92\u4E00\u4F4D\u670D\u4E8B\u8005\u5982\u4F55\u53D1\u6325\u624D\u80FD ( \u739B\u4E8C\u5341 25 ~ 28 ) \u4E4B\u540E, \u8FD8\u4E3A\u95E8\u5F92\u5C55\u793A\u4E86\u7942\u662F\u600E\u6837\u7684\u4E00\u4F4D\u8FBE\u5473\u541B\u738B\u7684\u540E\u88D4. \u4E3A\u6B64, \u4E0E\u739B\u4E5D 27 ~ 31 \u7684\u60C5\u8282\u5B8C\u5168\u4E00\u6837, \u8036\u7A23\u5E76\u4E0D\u662F\u89E6\u6478\u4ED6\u4EEC\u4E00\u4E0B, \u5C31\u5F84\u81EA\u79BB\u53BB, \u800C\u662F\u82B1\u65F6\u95F4\u8DDF\u8FD9\u4E24\u4E2A\u778E\u5B50\u4EA4\u8C08, \u8036\u7A23\u5E76\u975E\u53EA\u5C06\u8FD9\u4E24\u4E2A\u778E\u5B50\u89C6\u4E3A\u540C\u60C5\u7684\u5BF9\u8C61. \u800C\u662F\u4EE5\u5C0A\u4E25\u5BF9\u5F85\u4ED6\u4EEC ( 32 \u8282 ), \u8036\u7A23\u5C31\u50CF\u4E00\u4E2A\u80FD\u4E86\u89E3\u4ED6\u4EEC\u9700\u8981\u7684\u4EBA. \u8FD9\u4E24\u4E2A\u590D\u660E\u540E\u8DDF\u968F\u8036\u7A23\u7684\u4EBA ( 34 \u8282 ), \u4E3A\u6C42\u5F97\u6069\u8D50\u7684\u95E8\u5F92\u8BE5\u5982\u4F55\u505A\u56DE\u5E94\u7ACB\u4E86\u699C\u6837. \u6B64\u65F6, \u8036\u7A23\u6B63\u51C6\u5907\u4EE5\u8FBE\u5473\u4E4B\u5B50\u7684\u8EAB\u4EFD\u8FDB\u5165\u8036\u8DEF\u6492\u51B7 ( \u739B\u4E8C\u5341\u4E00 9, 15 ), \u5F00\u59CB\u7942\u7684\u82E6\u96BE\u5386\u7A0B, \u8FD9\u82E6\u96BE\u5386\u7A0B\u8981\u8FBE\u5230\u7684\u9AD8\u6F6E\u662F: \u8036\u7A23\u4E0E\u5929\u7236\u638C\u6743\u7EDF\u6CBB\u4E07\u7269.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u6BB5\u6709\u5173\u76F2\u4EBA\u7684\u60C5\u8282\u7A7F\u63D2\u5728\u6545\u4E8B\u4E2D\u95F4, \u5EF6\u7EED\u4E86\u6709\u5173\u95E8\u5F92\u804C\u8D23\u7684\u6559\u5BFC, \u5E76\u501F\u8FD9\u4E9B\u76F2\u4EBA\u7684\u5BA3\u8BA4, \u9884\u544A\u8036\u7A23\u5728\u8036\u8DEF\u6492\u51B7\u7684\u80DC\u5229.</p><p>\u8FD9\u6BB5\u53D9\u8FF0\u6709\u4E9B\u8BBD\u524C\u610F\u5473, \u53CC\u76EE\u5931\u660E\u7684\u4EBA\u7ADF\u53EF\u4EE5\u6E05\u695A\u5730\u8BA4\u51FA\u8036\u7A23\u7684\u8EAB\u4EFD. \u90A3\u4E24\u4E2A\u778E\u5B50\u8C61\u5F81\u8FD9\u6279\u95E8\u5F92, \u867D\u7136\u660E\u8BA4\u8036\u7A23\u662F\u4E3B\u9ED8\u897F\u4E9A, \u4F46\u4ECD\u6CA1\u6709\u5B8C\u5168\u61C2\u900F\u8036\u7A23\u7684\u6559\u8BAD. \u8FD9\u4E24\u4E2A\u4EBA\u4E5F\u50CF\u8F7D\u4F2F\u5FB7\u7684\u4E24\u4E2A\u513F\u5B50\u4E00\u6837 ( 20:20 - 23 ), \u53CC\u53CC\u524D\u6765\u6C42\u8036\u7A23, \u4F46\u4ED6\u4EEC\u6CA1\u6709\u6C42\u663E\u8981\u7684\u5730\u4F4D, \u800C\u662F\u6000\u7740\u4FE1\u5FB7, \u6C42\u8036\u7A23\u5F00\u542F\u4ED6\u4EEC\u7684\u773C\u775B, \u4ECE\u800C\u80FD\u591F\u8BA4\u6E05\u7942\u6240\u8981\u6C42\u7684\u9053\u8DEF.</p><p>\u53D1\u751F\u5728\u8FD9\u4E24\u4E2A\u76F2\u4EBA\u8EAB\u4E0A\u7684\u6539\u53D8, \u4E5F\u5E94\u8BE5\u53D1\u751F\u5728\u95E8\u5F92\u4EEC\u7684\u8EAB\u4E0A. \u4E0E\u8036\u7A23\u76F8\u9047, \u4F7F\u4ED6\u4EEC\u773C\u775B\u987F\u5F00, \u5F97\u5230\u65B0\u5149, \u80FD\u591F\u4EFF\u6548\u8036\u7A23, \u4EA4\u4ED8\u81EA\u5DF1, \u670D\u52A1\u5927\u4F17.</p>',48),o=[p];function s(r,d){return u(),e("div",null,o)}var q=a(h,[["render",s],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u4E8C\u5341\u7AE0.html.vue"]]);export{q as default};
