import{_ as u,o as a,c as t,e}from"./app.6bf529a6.js";const o={},h=e('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E03\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E03\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E03\u7AE0</h1><h2 id="\u8036\u7A23\u663E\u5723\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u8036\u7A23\u663E\u5723\u5BB9" aria-hidden="true">#</a> \u8036\u7A23\u663E\u5723\u5BB9</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u516D\u5929\u4EE5\u540E, \u8036\u7A23\u5E26\u7740\u4F2F\u591A\u7984, \u96C5\u5404\u4F2F\u548C\u4ED6\u7684\u5144\u5F1F\u82E5\u671B, \u5355\u72EC\u5E26\u9886\u4ED6\u4EEC\u4E0A\u4E86\u4E00\u5EA7\u9AD8\u5C71, <sup>2</sup> \u5728\u4ED6\u4EEC\u9762\u524D\u53D8\u4E86\u5BB9\u8C8C: \u4ED6\u7684\u9762\u8C8C\u53D1\u5149\u6709\u5982\u592A\u9633, \u4ED6\u7684\u8863\u670D\u6D01\u767D\u5982\u5149. <sup>3</sup> \u5FFD\u7136, \u6885\u745F\u548C\u5384\u91CC\u4E9A\u4E5F\u663E\u73B0\u7ED9\u4ED6\u4EEC, \u6B63\u5728\u540C\u8036\u7A23\u8C08\u8BBA. <sup>4</sup> \u4F2F\u591A\u7984\u5C31\u5F00\u53E3\u5BF9\u8036\u7A23\u8BF4: &quot;\u4E3B\u554A, \u6211\u4EEC\u5728\u8FD9\u91CC\u771F\u597D! \u4F60\u82E5\u613F\u610F, \u6211\u5C31\u5728\u8FD9\u91CC\u5F20\u642D\u4E09\u4E2A\u5E10\u7BF7: \u4E00\u4E2A\u4E3A\u4F60, \u4E00\u4E2A\u4E3A\u6885\u745F, \u4E00\u4E2A\u4E3A\u5384\u91CC\u4E9A.&quot; <sup>5</sup> \u4ED6\u8FD8\u5728\u8BF4\u8BDD\u7684\u65F6\u5019, \u5FFD\u6709\u4E00\u7247\u5149\u8000\u4E91\u5F69\u906E\u853D\u4E86\u4ED6\u4EEC, \u5E76\u4E14\u5728\u4E91\u4E2D\u6709\u58F0\u97F3\u8BF4: &quot;\u8FD9\u662F\u6211\u7684\u7231\u5B50, \u6211\u6240\u559C\u60A6\u7684, \u4F60\u4EEC\u8981\u542C\u4ECE\u4ED6!&quot; <sup>6</sup> \u95E8\u5F92\u542C\u4E86, \u5C31\u4FEF\u4F0F\u5728\u5730, \u975E\u5E38\u5BB3\u6015. <sup>7</sup> \u8036\u7A23\u9042\u524D\u6765, \u629A\u6469\u4ED6\u4EEC\u8BF4: &quot;\u8D77\u6765, \u4E0D\u8981\u5BB3\u6015!&quot; <sup>8</sup> \u4ED6\u4EEC\u4E3E\u76EE\u4E00\u770B, \u4EFB\u8C01\u90FD\u4E0D\u89C1\u4E86, \u53EA\u6709\u8036\u7A23\u72EC\u81EA\u4E00\u4EBA.</p><p><sup>9</sup> \u4ED6\u4EEC\u4ECE\u5C71\u4E0A\u4E0B\u6765\u7684\u65F6\u5019, \u8036\u7A23\u5631\u5490\u4ED6\u4EEC\u8BF4: &quot;\u975E\u7B49\u4EBA\u5B50\u7531\u6B7B\u8005\u4E2D\u590D\u6D3B, \u4F60\u4EEC\u4E0D\u8981\u5C06\u6240\u89C1\u7684\u544A\u8BC9\u7ED9\u4EFB\u4F55\u4EBA.&quot; <sup>10</sup> \u95E8\u5F92\u4FBF\u95EE\u8036\u7A23\u8BF4: &quot;\u90A3\u4E48, \u4E3A\u4EC0\u4E48\u7ECF\u5E08\u8BF4: \u5384\u91CC\u4E9A\u5E94\u8BE5\u5148\u6765\u5462?&quot; <sup>11</sup> \u8036\u7A23\u56DE\u7B54\u8BF4: &quot;\u5384\u91CC\u4E9A\u7684\u786E\u8981\u6765, \u4E14\u8981\u91CD\u6574\u4E00\u5207; <sup>12</sup> \u4F46\u6211\u8981\u544A\u8BC9\u4F60\u4EEC: \u5384\u91CC\u4E9A\u5DF2\u7ECF\u6765\u4E86, \u4EBA\u4EEC\u5374\u4E0D\u8BA4\u8BC6\u4ED6, \u53CD\u800C\u4EFB\u610F\u5F85\u4E86\u4ED6; \u7167\u6837, \u4EBA\u5B50\u4E5F\u8981\u53D7\u4ED6\u4EEC\u7684\u78E8\u96BE. &quot; <sup>13</sup> \u95E8\u5F92\u8FD9\u624D\u660E\u767D\u8036\u7A23\u7ED9\u4ED6\u8BF4\u7684, \u662F\u6307\u6D17\u8005\u82E5\u7FF0.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8BF7\u7279\u522B\u6CE8\u610F\u8FD9\u4E00\u4E8B\u4EF6\u548C\u4F2F\u591A\u7984\u7684\u5BA3\u4FE1\u5728\u65F6\u95F4\u5148\u540E\u4E0A\u7684\u5173\u8054 ( \u8FD9\u4E00\u4E8B\u5E8F\u5728\u4E09\u90E8\u5BF9\u89C2\u798F\u4E2D\u8BB0\u8F7D\u7684\u76F8\u540C, \u53EF\u8BC1\u660E\u5341\u516D 13 ~ \u5341\u4E03 23 \u6570\u8282\u5728\u8FD9\u51E0\u90E8\u798F\u97F3\u5199\u5C31\u4E4B\u524D\u5C31\u5DF2\u7ECF\u6709\u6587\u5B57\u8BB0\u5F55\u4E86 ). \u6211\u4EEC\u770B\u5230\u6885\u745F\u548C\u5384\u91CC\u4E9A, \u65E7\u7EA6\u7684\u4EE3\u8868\u4EBA\u7269 -- &quot;\u6CD5\u5F8B\u548C\u5148\u77E5&quot;, \u90FD\u662F\u798F\u97F3\u6545\u4E8B\u91CC\u6700\u91CD\u8981\u7684\u4EBA\u7269. ( \u4F8B\u5982\u4ED6\u4EEC\u88AB\u63CF\u7ED8\u4E3A &quot;\u4E24\u4F4D\u89C1\u8BC1\u4EBA&quot; ). \u8FD9\u6574\u4E2A\u666F\u50CF\u7684\u4E3B\u9898\u53D6\u7B97\u5E10\u7BF7\u8282 -- \u4EE5\u8272\u5217\u6240\u6709\u8282\u5E86\u4E2D\u6700\u6B22\u6109\u7684\u4E00\u4E2A ( \u65B0\u7EA6\u91CC\u5176\u4ED6\u5730\u65B9\u5F15\u7528\u6765\u63CF\u8FF0\u5929\u5802\u7684\u559C\u4E50 ). \u90A3\u6765\u81EA\u5929\u4E0A\u7684\u58F0\u97F3\u5BF9\u4F2F\u591A\u7984, \u96C5\u5404\u4F2F\u548C\u82E5\u671B\u91CD\u590D\u4E86\u8036\u7A23\u5728\u9886\u5148\u5F53\u65F6\u6240\u542C\u5230\u7684\u8BDD\u8BED: \u5728\u8FD9\u91CC\u4ED6\u662F\u53D7\u4E86\u5149\u8363\u7684\u4E0A\u4E3B\u4EC6\u4EBA.</p><p>\u82E5\u7FF0\u662F\u5384\u91CC\u4E9A\u8FD9\u4E00\u4E8B\u5B9E, \u4E00\u76F4\u8981\u5230\u95E8\u5F92\u85C9\u5DF4\u65AF\u5366\u5965\u8FF9\u4E4B\u542F\u8FEA\u800C\u8BA4\u8BC6\u8036\u7A23\u7684\u5929\u4E3B\u6027\u4E4B\u540E, \u624D\u5B8C\u5168\u88AB\u62AB\u9732\u51FA\u6765. \u8036\u7A23\u8BA4\u6D17\u8005\u4E3A\u5384\u91CC\u4E9A\u4EE5\u540E, \u4ED6\u73B0\u5728\u5BA3\u4F48\u82E5\u7FF0\u7684\u88AB\u6740\u5C31\u662F\u4ED6\u81EA\u5DF1\u751F\u547D\u7684\u5199\u7167.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B</h3><p>\u5728\u8FD9\u6BB5\u60C5\u8282\u4E2D, \u7EE7\u7EED\u9690\u542B\u5F0F\u5730\u56DE\u7B54\u8036\u7A23\u8EAB\u5206\u7684\u95EE\u9898, \u5E76\u8BF4\u660E\u5176\u610F\u4E49. \u867D\u7136\u8036\u7A23\u9884\u8A00\u81EA\u5DF1\u5FC5\u987B\u53D7\u96BE, \u6B7B\u4EA1\u5E76\u590D\u6D3B (\u739B\u5341\u516D 21), \u4F46\u8BFB\u8005\u5728\u6B64\u5F97\u5230\u5B8C\u5168\u7EDD\u5BF9\u7684\u4FDD\u8BC1: \u8036\u7A23\u7684\u6B7B\u4EA1\u5E76\u975E\u610F\u5473\u7740\u7942\u53D7\u5230\u5929\u4E3B\u5492\u9A82 ( \u7533\u4E8C\u5341\u4E00 23 ), \u6216\u53D7\u5230\u5929\u4E3B\u7684\u5B8C\u5168\u62D2\u7EDD. \u7942\u7684\u9762\u8C8C\u548C\u8863\u670D\u53D1\u5149 ( 2 \u8282 ), \u8868\u793A\u7942\u662F &quot;\u5728\u7236\u7684\u56FD\u91CC\u53D1\u5149\u6709\u5982\u592A\u9633\u7684\u4E49\u4EBA&quot; ( \u53C2\u9605: \u739B\u5341\u4E09 43 ). \u6765\u81EA\u5929\u4E0A\u7684\u58F0\u97F3 ( 5 \u8282 ) \u518D\u6B21\u91CD\u7533\u8036\u7A23\u53D7\u6D17\u65F6\u6240\u542C\u89C1\u7684\u8BAF\u606F: \u7942\u662F\u5929\u4E3B\u7684\u7231\u5B50 ( \u739B\u4E09 17 ). &quot;\u542C\u4ECE\u7942&quot; ( 5 \u8282 ) \u7684\u547D\u4EE4, \u662F\u5728\u547C\u5E94 \u300A\u7533\u547D\u7EAA\u300B\u4E2D &quot;\u4E0A\u4E3B\u4F60\u7684\u5929\u4E3B, \u8981\u7531\u4F60\u4E2D\u95F4, \u7531\u4F60\u5144\u5F1F\u4E2D, \u4E3A\u4F60\u5174\u8D77\u4E00\u4F4D\u50CF\u6211\u4E00\u6837\u7684\u5148\u77E5, \u4F60\u4EEC\u5E94\u542C\u4FE1\u4ED6&quot; \u8FD9\u53E5\u8BDD, \u5E76\u5F3A\u8C03\u8036\u7A23\u662F\u6CD5\u5F8B\u7684\u8BE0\u91CA\u8005, \u50CF\u662F\u5148\u77E5, \u662F\u50CF\u6885\u745F\u548C\u5384\u91CC\u4E9A ( 3 \u8282 ) \u90A3\u6837\u7684\u5148\u77E5.</p><p>\u739B\u7AA6\u5723\u53F2\u8FDB\u4E00\u6B65\u4EE5\u4E0B\u8FF0\u5404\u6837\u7EC6\u8282, \u523B\u753B\u51FA\u8036\u7A23\u662F\u65B0\u6885\u745F\u7684\u56FE\u50CF: &quot;\u5728\u9AD8\u5C71\u4E0A&quot; ( 1 \u8282, \u53C2\u9605\u739B\u4E94 1: \u5341\u4E94 29: \u4E8C\u5341\u516B 16 ), &quot;\u9762\u8C8C\u53D1\u5149&quot; ( 2 \u8282, \u50CF\u6885\u745F\u5728\u897F\u4E43\u5C71\u4E0A\u4E0E\u5929\u4E3B\u76F8\u9047\u540E\u7684\u6837\u5B50, \u89C1: \u51FA\u4E09\u5341\u56DB 29 ), &quot;\u4E91\u5F69\u906E\u853D\u4E86\u4ED6\u4EEC&quot; ( 5 \u8282, \u8C61\u5F81\u5929\u4E3B\u7684\u8363\u8000\u663E\u73B0\u5728\u4E91\u5F69\u4E2D, \u4E0E\u88AB\u653E\u9010\u5728\u65F7\u91CE\u4E2D, \u5954\u5411\u81EA\u7531\u7684\u4EE5\u8272\u5217\u5B50\u6C11\u540C\u5728, \u89C1: \u51FA\u5341\u516D 10: \u5341\u4E5D 9 ). \u739B\u7AA6\u5723\u53F2\u7279\u522B\u63CF\u8FF0\u8FD9\u4E2A\u795E\u89C6\u7684\u7ECF\u9A8C ( 6 \u8282 ), \u95E8\u5F92\u7684\u53CD\u5E94\u4E0E\u8FBE\u5C3C\u5C14\u5148\u77E5\u89C1\u5230\u7684\u672B\u4E16\u7570\u8C61\u65F6\u7684\u53CD\u5E94\u76F8\u4F3C: &quot;\u4FEF\u4F0F\u5728\u5730&quot;, \u5E76\u53D7\u5230 &quot;\u629A\u6478&quot; \u7684\u5B89\u6170 ( \u8FBE\u516B 17~18: \u5341 7~9 ).</p><p>\u95E8\u5F92\u4E0E\u8036\u7A23\u8C08\u8BBA\u6709\u5173\u5384\u91CC\u4E9A\u5148\u77E5\u7684\u8BDD ( 9~13 \u8282 ), \u662F\u56E0\u4E3A\u300A\u739B\u62C9\u57FA\u4E9A\u5148\u77E5\u4E66\u300B\u4E0A\u6709\u8457 &quot;\u5728\u4E0A\u4E3B\u4F1F\u5927\u53CA\u53EF\u6015\u7684\u65E5\u5B50\u6765\u4E34\u4EE5\u524D, \u6211\u5FC5\u6D3E\u9063\u5148\u77E5\u5384\u91CC\u4E9A\u5230\u4F60\u4EEC\u8FD9\u91CC\u6765&quot; ( \u62C9\u56DB 5: \u5E0C\u4F2F\u6765\u7248\u672C\u4E09 23 ) \u8FD9\u53E5\u8BDD, \u5982\u4F55\u8BE0\u91CA\u8FD9\u53E5\u8BDD, \u662F\u53EF\u4E89\u8FA9\u7684. \u5BF9\u57FA\u7763\u5F92\u6765\u8BF4, \u5384\u91CC\u4E9A\u7684\u6765\u4E34, \u5728\u82E5\u7FF0\u6D17\u8005\u8EAB\u4E0A\u5DF2\u7ECF\u5B9E\u73B0\u4E86 ( \u4E5F\u8BF7\u53C2\u9605: \u739B\u4E09 1~17: \u4E5D 18~26; \u5341\u4E00 1~19; \u5341\u56DB 1~12 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5F53\u95E8\u5F92\u4EEC\u542C\u5230\u8036\u7A23\u5BA3\u5E03\u81EA\u5DF1\u8981\u53D7\u82E6, \u4EE5\u53CA\u8DDF\u8DDF\u968F\u7942\u6240\u4EA7\u751F\u7684\u540E\u91CC\u65F6, \u4ED6\u4EEC\u611F\u5230\u4E07\u5206\u6CAE\u4E27, \u840E\u9761\u4E0D\u632F. \u8036\u7A23\u663E\u5723\u5BB9\u6781\u5BCC\u9F13\u52B1\u6027, \u56E0\u4E3A\u5176\u4E2D\u663E\u793A\u4E86\u8036\u7A23\u7684\u5149\u8363, \u4E5F\u540C\u65F6\u9884\u8A00\u4E86\u7942\u5341\u5B57\u67B6\u4E0A\u7684\u80DC\u5229.</p><p>\u5728\u8FD9\u4E2A\u53D9\u8FF0\u91CC\u9762, \u6211\u4EEC\u770B\u5230\u4E00\u4E2A\u5B8C\u6574\u7684\u8036\u7A23\u5F62\u8C61. \u5728\u7942\u8EAB\u4E0A, \u5929\u4E3B\u7684\u5149\u8363\u5F97\u4EE5\u5C55\u73B0; \u7942\u5C31\u662F\u4EE5\u8272\u5217\u671F\u5F85\u5DF2\u4E45\u7684\u771F\u6B63\u9ED8\u897F\u4E9A. \u4E0D\u4EC5\u5982\u6B64, \u7942\u8FD8\u662F\u5929\u4E3B\u4E4B\u5B50. \u739B\u7AA6\u5728\u4ED6\u7684\u6574\u90E8\u798F\u97F3\u4E2D, \u90FD\u575A\u6301\u8FD9\u6837\u79F0\u547C\u7942. \u8036\u7A23\u7684\u8FD9\u6B21\u663E\u73B0, \u9488\u5BF9\u7684\u662F\u4F34\u968F\u7942\u7684\u51E0\u4F4D\u95E8\u5F92, \u53EF\u662F\u739B\u7AA6\u7684\u601D\u60F3\u4E2D, \u5374\u662F\u9488\u5BF9\u606D\u8BFB\u798F\u97F3\u7684\u6BCF\u4E00\u4E2A\u4EBA, \u9080\u8BF7\u4F17\u4EBA\u501F\u9ED8\u60F3\u8036\u7A23\u6218\u80DC\u6B7B\u4EA1\u7684\u4E8B\u5B9E, \u6765\u589E\u52A0\u5BF9\u7942\u7684\u4FE1\u5FB7; \u5982\u6B64, \u4ED6\u4EEC\u624D\u80FD\u591F\u62C5\u8D1F\u7684\u8D77\u95E8\u5F92\u7684\u981F\u79CD\u8D23\u4EFB, \u4EFF\u6548\u8036\u7A23\u7684\u4E00\u5207\u884C\u4E3A.</p><p>\u8036\u7A23\u662F\u4E00\u4F4D\u8001\u5E08, \u7942\u6559\u8BAD\u5E76\u5F15\u5BFC\u81EA\u5DF1\u7684\u95E8\u5F92; \u7942\u540C\u65F6\u53C8\u662F\u4E3B, \u5145\u6EE1\u4E86\u5929\u4E3B\u7684\u5149\u8363, \u88AB\u4E91\u5F69\u73AF\u7ED5 ( \u4E00\u79CD\u795E\u6027\u4E34\u5728\u7684\u8868\u8FBE ). \u5929\u4E3B\u60F3\u8981\u63ED\u53BB\u90A3\u5757\u906E\u76D6\u8036\u7A23\u7684\u795E\u79D8\u9762\u7EB1. \u95E8\u5F92\u4EEC\u7ACB\u523B\u8DEA\u5012\u5728\u7942\u9762\u524D, \u8FD9\u662F\u5728\u4E3B\u524D\u671D\u62DC\u7684\u6001\u5EA6. \u5929\u4E3B\u7684\u4E34\u5728\u4E5F\u4F7F\u4ED6\u4EEC\u4EA7\u751F\u6050\u60E7\u611F; \u4F46\u8FD9\u79CD\u6050\u60E7\u5BB3\u6015\u7684\u5FC3\u60C5, \u56E0\u8036\u7A23\u7684\u4E34\u5728\u548C\u90A3\u53E5 &quot;\u4E0D\u8981\u6015&quot; \u800C\u70DF\u6D88\u4E91\u6563.</p><p>\u739B\u7AA6\u501F\u8FD9\u6BB5\u53D9\u8FF0\u7740\u91CD\u4F20\u8FBE\u7684\u8BAF\u606F, \u662F\u8BC1\u660E\u5C06\u8981\u53D1\u751F\u4E00\u5207 ( \u5929\u56FD\u7684\u5FB7\u80FD, \u590D\u6D3B, \u5149\u8363\u7B49\u7B49 ), \u5982\u4ECA\u5DF1\u5728\u8036\u7A23\u8FD9\u4E2A\u4EBA\u8EAB\u4E0A\u6210\u4E3A\u4E8B\u5B9E. \u8FD9\u4E2A\u53D9\u8FF0\u4E5F\u9080\u8BF7\u95E8\u5F92\u4EEC\u8D85\u8D8A\u90A3\u4E2A\u5149\u8363\u800C\u8F7B\u677E\u7684\u9ED8\u897F\u4E9A\u4E3B\u4E49\u7684\u8BF1\u60D1, \u9F13\u52B1\u4ED6\u4EEC\u8D70\u8036\u7A23\u8D70\u8FC7\u7684\u8DEF, \u575A\u51B3\u5730\u670D\u4ECE\u5929\u7236\u7684\u65E8\u610F. \u4E3A\u739B\u7AA6\u800C\u8A00, \u8036\u7A23\u7684\u82E6\u96BE\u548C\u5149\u8363, \u662F\u7942\u62EF\u6551\u5DE5\u7A0B\u4E2D\u4E0D\u53EF\u5206\u5272\u7684.</p><p>\u5173\u4E8E\u5384\u91CC\u4E9A\u7684\u95EE\u9898, \u5E94\u8BE5\u5728\u663E\u793A\u5723\u5BB9\u7684\u60C5\u8282\u4E2D\u7406\u89E3. \u95E8\u5F92\u4EEC\u4E5F\u60F3\u4E86\u7ECF\u5E08\u6240\u8BB2\u7684\u4E00\u79CD\u6D41\u884C\u8BF4\u6CD5: \u5982\u679C\u5384\u91CC\u4E9A\u8FD8\u6CA1\u6709\u56DE\u6765, \u90A3\u8036\u7A23\u5C31\u4E0D\u662F\u9ED8\u897F\u4E9A. \u8036\u7A23\u5728\u81EA\u5DF1\u7684\u56DE\u7B54\u4E2D, \u5DF1\u7ECF\u5C06\u6D17\u8005\u82E5\u7FF0\u7B49\u540C\u4E8E\u5148\u77E5\u5384\u91CC\u4E9A, \u56E0\u4E3A\u82E5\u7FF0\u5DF2\u7ECF\u5B8C\u6210\u4E86\u9884\u5907\u4EBA\u4EEC\u53BB\u8FCE\u63A5\u9ED8\u897F\u4E9A\u7684\u4F7F\u547D. \u65E2\u7136\u4EBA\u4EEC\u6CA1\u6709\u63A5\u53D7\u82E5\u7FF0, \u53CD\u800C\u6740\u5BB3\u4E86\u4ED6, \u90A3\u4E48, \u4ED6\u4EEC\u4E5F\u4E0D\u4F1A\u63A5\u53D7\u8036\u7A23 -- \u8FD9\u4F4D\u4EBA\u5B50, \u5E76\u4E14\u8981\u8BEC\u544A\u7942, \u5C06\u7942\u7F6E\u4E8E\u6B7B\u5730.</p><p>\u8036\u7A23\u4E4B\u6240\u4EE5\u8FD9\u6837\u5F3A\u8C03\u81EA\u5DF1\u7684\u82E6\u96BE, \u662F\u4E3A\u4E86\u6253\u7834\u4EFB\u4F55\u653F\u6CBB\u5316\u6216\u6C11\u65CF\u4E3B\u4E49\u7684\u9ED8\u897F\u4E9A\u89C2\u5FF5. \u4EBA\u5B50\u7684\u786E\u662F\u9ED8\u897F\u4E9A, \u4F46\u7942\u5982\u540C\u96C5\u5A01\u7684\u4EC6\u4EBA\u4E00\u6837, \u662F\u53D7\u82E6\u7684\u9ED8\u897F\u4E9A.</p><h2 id="\u6CBB\u597D\u9644\u9B54\u7684\u513F\u7AE5" tabindex="-1"><a class="header-anchor" href="#\u6CBB\u597D\u9644\u9B54\u7684\u513F\u7AE5" aria-hidden="true">#</a> \u6CBB\u597D\u9644\u9B54\u7684\u513F\u7AE5</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>14</sup> \u5F53\u4ED6\u4EEC\u6765\u5230\u7FA4\u4F17\u90A3\u91CC\u65F6, \u6709\u4E00\u4E2A\u4EBA\u6765\u5230\u8036\u7A23\u8DDF\u524D, <sup>15</sup> \u8BF4: \u4E3B\u554A, \u53EF\u601C\u6211\u7684\u513F\u5B50\u5427! \u4ED6\u60A3\u766B\u75EB\u75C5\u5F88\u82E6, \u5C61\u6B21\u8DCC\u5728\u706B\u4E2D, \u53C8\u5C61\u6B21\u8DCC\u5728\u6C34\u91CC. <sup>16</sup> \u6211\u628A\u4ED6\u5E26\u5230\u4F60\u7684\u95E8\u5F92\u8DDF\u524D, \u4ED6\u4EEC\u5374\u4E0D\u80FD\u6CBB\u597D\u4ED6. <sup>17</sup> \u8036\u7A23\u56DE\u7B54\u8BF4: &quot;\u54CE, \u65E0\u4FE1\u8D25\u574F\u7684\u4E16\u4EE3, \u6211\u540C\u4F60\u4EEC\u5728\u4E00\u8D77\u8981\u5230\u51E0\u65F6\u5462? \u6211\u5BB9\u5FCD\u4F60\u4EEC\u8981\u5230\u51E0\u65F6\u5462? \u628A\u4ED6\u7ED9\u6211\u5E26\u5230\u8FD9\u91CC\u6765!&quot; <sup>18</sup> \u8036\u7A23\u9042\u53F1\u8D23\u9B54\u9B3C, \u9B54\u9B3C\u5C31\u4ECE\u5B69\u5B50\u8EAB\u4E0A\u51FA\u53BB\u4E86; \u4ECE\u90A3\u65F6\u523B, \u5B69\u5B50\u5C31\u597D\u4E86. <sup>19</sup> \u4EE5\u540E, \u95E8\u5F92\u79C1\u4E0B\u5BF9\u8036\u7A23\u8BF4: &quot;\u4E3A\u4EC0\u4E48\u6211\u4EEC\u4E0D\u80FD\u9010\u51FA\u8FD9\u9B54\u9B3C\u5462?&quot; <sup>20</sup> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u7531\u4E8E\u4F60\u4EEC\u7F3A\u5C11\u4FE1\u5FB7; \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u5047\u5982\u4F60\u4EEC\u6709\u50CF\u82A5\u5B50\u90A3\u4E48\u5927\u7684\u4FE1\u5FB7, \u4F60\u4EEC\u5411\u8FD9\u5EA7\u5C71\u8BF4: \u4ECE\u8FD9\u8FB9\u79FB\u5230\u90A3\u8FB9\u53BB! \u5B83\u5FC5\u4F1A\u79FB\u8FC7\u53BB\u7684; \u4E3A\u4F60\u4EEC\u6CA1\u6709\u4E0D\u53EF\u80FD\u7684\u4E8B.&quot; <sup>21</sup> [ \u4F46\u8FD9\u7C7B\u9B54\u9B3C\u975E\u7528\u7948\u7977\u548C\u7981\u98DF, \u662F\u4E0D\u80FD\u8D76\u51FA\u53BB\u7684. ]</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u85C9\u8FD9\u4E00\u4E8B\u4EF6\u5F3A\u8C03\u5BF9\u884C\u5947\u8FF9\u5145\u6EE1\u4FE1\u5FB7\u7684\u5FC5\u8981; \u9A6C\u5C14\u8C37\u7684\u8457\u91CD\u70B9\u5219\u7565\u6709\u4E0D\u540C.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B</h3><p>\u60A3\u6709 &quot;\u766B\u75EB\u75C5&quot; ( \u8FD9\u4E2A\u5E0C\u814A\u5B57\u7684\u610F\u601D\u662F &quot;\u53D1\u72C2\u7684&quot; ) \u5B69\u5B50\u7684\u60B2\u60E8\u906D\u9047, \u6210\u4E3A\u8FDB\u4E00\u6B65\u57F9\u8BAD\u95E8\u5F92\u7684\u673A\u4F1A. \u5B69\u5B50\u7236\u4EB2\u5411\u8036\u7A23\u8BF4 &quot;\u4E3B\u554A, \u53EF\u601C\u6211\u7684\u513F\u5B50\u7F62&quot; \u7684\u54C0\u6C42, \u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u4E2D\u6240\u6709\u82E6\u96BE\u8005\u8BF7\u6C42\u601C\u60AF\u65F6\u7684\u5171\u540C\u4F5C\u4E3A ( \u739B\u516B 2, 5~6, 25; \u5341\u56DB 30: \u5341\u4E94 22, 25\uFF1B\u4E8C\u5341 30~31 ). \u95E8\u5F92\u88AB\u6388\u4E88\u6CBB\u7652\u5404\u79CD\u75BE\u75C5\u7684\u6743\u67C4 ( \u5341 1 ), \u7136\u800C, \u739B\u7AA6\u5723\u53F2\u5E76\u6CA1\u6709\u63D0\u5230\u95E8\u5F92\u66FE\u7ECF\u884C\u4F7F\u8FC7\u8FD9\u4E9B\u6743\u67C4 ( \u53C2\u9605, \u8C37 13, 30 ). \u8036\u7A23\u5BF9\u95E8\u5F92\u4E25\u5389\u7684\u8BAD\u8BEB, \u662F\u5728\u53CD\u5E94\u6885\u745F\u5BF9\u4EE5\u8272\u5217\u6C11\u7684\u6124\u6012 ( \u7533\u4E09\u5341 5 ). \u8036\u7A23\u518D\u6B21\u8BAD\u8BEB\u95E8\u5F92, \u4E0D\u8981\u5173\u6CE8\u81EA\u5DF1\u6240\u7F3A\u4E4F\u7684, \u800C\u662F\u8981\u8FD0\u7528\u81EA\u5DF1\u7684 &quot;\u5C0F\u4FE1\u5FB7&quot; \u6765\u5BA3\u626C\u798F\u97F3, \u5E76\u884C\u4F7F\u81EA\u5DF1\u5DF1\u7ECF\u5F97\u5230\u7684\u6743\u67C4 ( \u8BF7\u53C2\u9605: \u739B\u516D 30; \u516B 26; \u5341\u56DB 31; \u5341\u516D 8; \u4E8C\u5341\u4E00 21~22 ). \u4E5F\u8BF7\u53C2\u8003 &quot;\u82A5\u5B50\u6BD4\u55BB&quot; \u6559\u5BFC ( \u739B\u5341\u4E09 31~32 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u672C\u6BB5\u4F5C\u8FF0\u7684\u76EE\u7684, \u662F\u4E3A\u4E86\u901A\u8FC7\u5177\u4F53\u7684\u4E8B\u5B9E\u6765\u8BE0\u91CA\u4FE1\u4EF0\u7684\u771F\u8AE6. \u9A6C\u5C14\u8C37\u4E5F\u8BB0\u8F7D\u4E86\u540C\u4E00\u4E2A\u6545\u4E8B, \u4F46\u662F\u739B\u7AA6\u7684\u53D9\u8FF0\u66F4\u52A0\u5E7F\u9614, \u7EC6\u8282\u4E5F\u8F83\u591A, \u6240\u63CF\u8FF0\u7684\u573A\u666F\u66F4\u5BB9\u6613\u8BA9\u4EBA\u7406\u89E3. \u5E76\u7279\u522B\u5F3A\u8C03\u7948\u7977.</p><p>\u739B\u7AA6\u63D0\u5230\u4EFB\u4F55\u6CBB\u6108\u7684\u5947\u8FF9, \u90FD\u662F\u4E3A\u4E86\u91CD\u590D\u5F3A\u8C03\u4FE1\u5FB7\u7684\u529B\u91CF, \u7279\u522B\u662F\u5F53\u90A3\u4E9B\u5C0F\u4FE1\u5FB7\u7684\u95E8\u5F92\u95E8\u7F3A\u4E4F\u4FE1\u5FB7\u7684\u65F6\u5019.</p><p>\u771F\u6B63\u7684\u4FE1\u5FB7, \u65E0\u8BBA\u591A\u5C0F, \u751A\u81F3\u5982\u540C\u82A5\u5B50, \u4E5F\u662F\u5206\u4EAB\u5929\u4E3B\u7684\u80FD\u529B ( \u7F57 4:17-21 ). \u56E0\u6B64, \u624D\u53EF\u4EE5\u8BF4\u4FE1\u5FB7\u80FD\u591F\u79FB\u5C71 ( \u683C\u524D 13:2 ). \u8FD9\u79CD\u65B9\u5F0F\u662F\u5BF9\u521B\u9020\u8005\u529B\u91CF\u7684\u4E00\u79CD\u8BD7\u6B4C\u5F0F\u7684\u8868\u8FBE, \u5C31\u662F\u8BF4, \u65E0\u8BBA\u963B\u788D\u591A\u4E48\u5F3A\u5927, \u751A\u81F3\u5982\u540C\u5927\u5C71\u4E00\u6837 ( &quot;\u5C71\u5CB3\u8DF3\u8DC3\u5982\u540C\u516C\u7F8A, \u5C0F\u4E18\u821E\u8E48\u6070\u4F3C\u7EF5\u7F8A&quot;, \u548F114\uFF1B4 ), \u5728\u7942\u9762\u524D\u4E5F\u90FD\u4F1A\u4E91\u6D88\u96FE\u6563.</p><p>\u5728\u8FD9\u91CC, \u739B\u7AA6\u65A5\u8D23\u95E8\u5F92\u5BF9\u4E8E\u81EA\u5DF1\u4ECE\u8036\u7A23\u63A5\u53D7\u6765\u7684\u529B\u91CF\u6CA1\u6709\u8DB3\u591F\u7684\u4FE1\u5FC3. \u6240\u4EE5, \u8FD9\u79CD\u610F\u4E49\u4E0A, \u8FD9\u6BB5\u8BAF\u606F\u4ECD\u65E7\u6CA1\u6709\u5931\u53BB\u771F\u6B63\u7684\u4EF7\u503C: \u529D\u4EBA\u4E0D\u8981\u6000\u7591\u798F\u97F3\u7684\u62EF\u6551\u529B\u91CF.</p><h2 id="\u4E8C\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B" aria-hidden="true">#</a> \u4E8C\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>22</sup> \u5F53\u8036\u7A23\u540C\u95E8\u5F92\u5728\u52A0\u91CC\u808B\u4E9A\u5468\u6E38\u65F6, \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u4EBA\u5B50\u5C06\u88AB\u4EA4\u4E8E\u4EBA\u4EEC\u624B\u4E2D. <sup>23</sup> \u4ED6\u4EEC\u8981\u6740\u5BB3\u4ED6, \u7B2C\u4E09\u5929\u4ED6\u5FC5\u8981\u590D\u6D3B. &quot; \u95E8\u5F92\u5C31\u975E\u5E38\u5FE7\u90C1.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u6CE8\u610F, \u739B\u7AA6\u6240\u5199\u7684 &quot;\u7B2C\u4E09\u5929&quot; \u548C\u4ED6\u5728\u5341\u516D 21 \u8BB0\u8F7D\u7684\u76F8\u540C. ( \u53C2\u7167\u9A6C\u5C14\u8C37\u798F\u97F3\u4E2D\u7684 &quot;\u4E09\u5929\u4E4B\u540E&quot;, \u662F\u76F8\u5F53\u96BE\u89E3\u7684 ). \u739B\u7AA6\u6240\u8BB0\u7684\u5728\u65E9\u671F\u6559\u4F1A\u7684\u4EEA\u5F0F\u4E2D\u5DF2\u7ECF\u4E3A\u4EBA\u91C7\u7528.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B</h3><p>\u95E8\u5F92\u5BF9\u8036\u7A23\u7B2C\u4E8C\u6B21\u9884\u8A00\u53D7\u96BE\u548C\u590D\u6D3B\u7684\u53CD\u5E94, \u4E0D\u518D\u50CF\u8036\u7A23\u9996\u6B21\u9884\u8A00\u65F6 ( \u739B\u5341\u516D 21~23 ) \u4E00\u6837, \u62B1\u7740\u5426\u5B9A\u7684\u6001\u5EA6, \u4F46\u95E8\u5F92\u8FD8\u662F\u8868\u73B0\u5F97 &quot;\u975E\u5E38\u4F18\u90C1&quot;. \u5728\u4E0E\u8036\u7A23\u4E00\u540C\u524D\u5F80\u8036\u8DEF\u6492\u51B7\u7684\u9014\u4E2D, \u95E8\u5F92\u9010\u6E10\u660E\u767D\u5E76\u63A5\u53D7\u8036\u7A23\u5FC5\u987B\u53D7\u96BE, \u6B7B\u4EA1, \u590D\u6D3B\u8FD9\u4E00\u4E8B\u5B9E( \u8BF7\u4E0E\u8C37 30~32 \u505A\u5BF9\u6BD4 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u4E0D\u613F\u610F\u8BA9\u4EBA\u77E5\u9053\u7942\u7684\u5230\u6765, \u56E0\u4E3A\u7942\u6E34\u671B\u4E0E\u81EA\u5DF1\u7684\u95E8\u5F92\u5728\u4E00\u8D77, \u7B2C\u4E8C\u6B21\u5411\u4ED6\u4EEC\u5BA3\u544A\u81EA\u5DF1\u7684\u82E6\u96BE, \u6B7B\u4EA1\u548C\u590D\u6D3B. \u88AB\u52A8\u52A8\u8BCD &quot;\u88AB\u4EA4\u51FA&quot; \u4E5F\u53EF\u4EE5\u5047\u5B9A\u4E3A\u5929\u4E3B\u628A\u8036\u7A23\u4EA4\u51FA\u6765, \u4F46\u8FD9\u5E76\u4E0D\u8868\u793A\u5929\u4E3B\u613F\u610F\u8650\u5F85\u4EBA; \u4E3A\u62EF\u6551\u4E16\u4EBA, \u5929\u4E3B\u628A\u81EA\u5DF1\u7684\u72EC\u751F\u5B50\u4EA4\u51FA\u6765, \u4F46\u90A3\u4E9B\u611F\u5230\u88AB\u8036\u7A23\u5A01\u80C1\u7684 &quot;\u4EBA&quot; \u7684\u4ED3\u4FC3\u51B3\u5B9A, \u6700\u540E\u6B8B\u66B4\u5730\u628A\u7942\u6740\u6B7B\u4E86 ( \u53C2\u9605\u4F9D 53:12 ). \u4ECD\u7136\u6C89\u9189\u4E8E\u80DC\u5229\u5149\u8363\u4E4B\u613F\u671B\u4E2D\u7684\u95E8\u5F92\u4EEC, \u5B9E\u5728\u4E0D\u80FD\u660E\u767D, \u4E3A\u4EC0\u4E48\u9ED8\u897F\u4E9A\u5FC5\u987B\u5FCD\u53D7\u5341\u5B57\u67B6.</p><h2 id="\u7F34\u7EB3\u6BBF\u7A0E" tabindex="-1"><a class="header-anchor" href="#\u7F34\u7EB3\u6BBF\u7A0E" aria-hidden="true">#</a> \u7F34\u7EB3\u6BBF\u7A0E</h2><p><sup>24</sup> \u4ED6\u4EEC\u6765\u5230\u845B\u6CD5\u7FC1\u65F6, \u6536\u6BBF\u7A0E\u7684\u4EBA\u6765\u5230\u4F2F\u591A\u7984\u7684\u8DDF\u524D\u8BF4: &quot;\u4F60\u4EEC\u7684\u5E08\u5085\u4E0D\u7EB3\u6BBF\u7A0E\u5417?&quot; <sup>25</sup> \u4F2F\u591A\u7984\u8BF4: &quot;\u81EA\u7136\u7EB3\u7684.&quot; \u4ED6\u4E00\u8FDB\u5230\u5C4B\u91CC, \u8036\u7A23\u5C31\u5148\u5BF9\u4ED6\u8BF4: &quot;\u897F\u6EE1! \u4F60\u4EE5\u4E3A\u600E\u6837? \u5730\u4E0A\u7684\u541B\u738B\u5411\u8C01\u5F81\u6536\u5173\u7A0E\u6216\u4E01\u7A0E\u5462? \u5411\u81EA\u5DF1\u7684\u513F\u5B50, \u6216\u662F\u5411\u5916\u4EBA?&quot; <sup>26</sup> \u4F2F\u591A\u7984\u8BF4: &quot;\u5411\u5916\u4EBA.&quot; \u8036\u7A23\u5BF9\u4ED6\u8BF4: &quot;\u6240\u4EE5\u513F\u5B50\u662F\u514D\u7A0E\u7684\u4E86. <sup>27</sup> \u4F46\u662F, \u4E3A\u4E86\u907F\u514D\u4F7F\u4ED6\u4EEC\u7591\u602A, \u4F60\u5F80\u6D77\u8FB9\u53BB\u5782\u9493, \u62FF\u9493\u4E0A\u6765\u7684\u7B2C\u4E00\u6761\u9C7C, \u5F00\u4E86\u5B83\u7684\u53E3, \u5C31\u4F1A\u627E\u5230\u4E00\u5757 &#39;\u65AF\u5854\u7279&#39;. \u62FF\u53BB\u4EA4\u7ED9\u4ED6\u4EEC, \u5F53\u4F5C\u6211\u548C\u4F60\u7684\u6BBF\u7A0E. &quot;</p><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u53EA\u6709\u739B\u7AA6\u798F\u97F3\u544A\u8BC9\u6211\u4EEC\u8036\u7A23\u987A\u4ECE\u4E86\u72B9\u592A\u7684\u6CD5\u5F8B, \u5E76\u5F3A\u8C03\u4F2F\u591A\u7984\u663E\u8981\u7684\u5730\u4F4D, \u7136\u540E\u5F15\u5165\u90A3\u4E2A\u95EE\u9898 &quot;\u5728\u5929\u56FD\u91CC\u7A76\u7ADF\u8C01\u662F\u6700\u5927\u7684?&quot; \u66F4\u7531\u8FD9\u95EE\u9898\u5F15\u51FA\u8036\u7A23\u7684\u4E00\u756A\u9053\u7406.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u8BE0\u91CA\u300B</h3><p>\u8FD9\u8BB0\u8FF0\u4E3A\u300A\u739B\u7AA6\u798F\u97F3\u300B\u72EC\u6709. \u5176\u4E2D\u7684\u4E89\u8BAE\u70B9\u5728: \u662F\u5426\u6240\u6709\u4E8C\u5341\u5C81\u4EE5\u4E0A\u7684\u72B9\u592A\u7537\u4E01\u6BCF\u5E74\u90FD\u8981\u4EA4\u4ED8\u534A\u4E2A &quot;\u534F\u523B\u8033&quot; \u7684\u6BBF\u7A0E, \u4F5C\u4E3A\u7ED9\u4E0A\u4E3B\u7684\u732E\u4EEA ( \u51FA\u4E09\u5341 11~16 ). \u6BBF\u7A0E\u7528\u4E8E\u5723\u6BBF\u7EF4\u4FEE, \u540C\u65F6\u5B83\u8FD8\u662F\u4EE5\u8272\u5217\u72B9\u592A\u4EBA\u6563\u5C45\u4E8E\u5F02\u6C11\u4E2D\u7684\u72B9\u592A\u56E2\u7ED3\u7684\u8C61\u5F81. \u6BBF\u7A0E\u7684\u4E89\u8BAE\u53EF\u80FD\u662F\u6E90\u4E8E\u4E0D\u8D5E\u540C\u6492\u675C\u8D5B\u4EBA\u6EE5\u7528\u6BBF\u7A0E\u7684\u884C\u4E3A, \u6216\u6E90\u4E8E\u4E0D\u8D5E\u540C\u8FD9\u6837\u4F1A\u8BA9\u65E0\u529B\u4EA4\u4ED8\u6BBF\u7A0E\u7684\u4EBA\u611F\u5230\u7F9E\u6127\u96BE\u582A. \u8036\u7A23\u4E0E\u4F2F\u591A\u7984\u7684\u5BF9\u8BDD\u6E05\u695A\u7684\u8868\u793A\u5723\u6BBF\u4E3A\u5929\u4E3B\u4E4B\u5C45\u6240, \u53EF\u662F, \u8036\u7A23\u56E0\u4E3A\u662F\u5929\u4E3B\u5B50, \u6240\u4EE5\u5E94\u88AB\u8C41\u514D\u6BBF\u7A0E. \u4E0D\u8FC7, \u4E3A\u4E86\u4E0D\u5F15\u53D1\u4E8B\u7AEF, \u8036\u7A23\u7F34\u4ED8\u4E86\u6BBF\u7A0E. \u9C7C\u53E3\u91CC\u627E\u5230\u4E00 &quot;\u534F\u523B\u8033&quot;, \u8FD9\u4E00\u79BB\u5947\u7EC6\u8282\u7684\u63CF\u8FF0, \u7ED9\u8FD9\u4E8B\u4EF6\u589E\u6DFB\u4E86\u4E00\u80A1\u6C11\u95F4\u4F20\u5947\u6545\u4E8B\u7684\u6C1B\u56F4.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u6B21\u7684\u7EB3\u7A0E\u95EE\u9898\u662F\u7531\u72B9\u592A\u4EBA\u7684\u98CE\u4FD7\u4E60\u60EF\u5F15\u53D1\u7684. \u6309\u7167\u6CD5\u5F8B ( \u51FA 30:11 - 13 ), \u51E1\u5E74\u6EE1\u4E8C\u5341\u5C81\u7684\u72B9\u592A\u7537\u5B50, \u65E0\u8BBA\u5C45\u4E8E\u8036\u8DEF\u6492\u51B7, \u8FD8\u662F\u5C45\u4E8E\u72B9\u5927\u5730\u533A, \u751A\u6216\u6563\u5C45\u4E8E\u7F57\u9A6C\u5E1D\u56FD, \u6BCF\u5E74\u90FD\u8981\u7F34\u7EB3\u5BF9\u6BBF\u7A0E. \u4ED6\u4EEC\u53EF\u4EE5\u5728\u903E\u8D8A\u8282\u4EB2\u81EA\u5230\u5723\u6BBF\u7F34\u7EB3, \u4E5F\u53EF\u63D0\u524D\u7F34\u7EB3. \u6240\u7F34\u4ED8\u7684\u91D1\u989D\u53EA\u9700\u8981\u4E24\u4E2A &quot;\u8FBE\u739B&quot; ( \u65B0\u7EA6\u5E74\u4EE3\u7684\u5E0C\u814A\u8D27\u5E01\u5355\u4F4D ), \u76F8\u5F53\u4E8E\u4E00\u4E2A\u5DE5\u4EBA\u4E24\u5929\u7684\u5DE5\u8D44. \u4F2F\u591A\u7984\u5728\u9C7C\u5634\u4E2D\u627E\u5230\u7684 &quot;\u94F6\u5E01&quot;, \u76F4\u8BD1\u4E3A &quot;\u65AF\u5854\u7279&quot;, \u5E01\u503C\u56DB\u4E2A &quot;\u8FBE\u739B&quot;, \u6B63\u597D\u591F\u4ED8\u4E24\u4EBA\u7684\u5723\u6BBF\u7A0E. \u4E0E\u7F57\u9A6C\u5E1D\u56FD\u5411\u4EBA\u4EEC\u5F81\u6536\u7684\u7A0E\u91D1\u76F8\u6BD4\uFF0C \u5723\u6BBF\u7A0E\u751A\u5C11.</p><p>\u8036\u7A23\u6240\u8BF4\u7684\u7C7B\u6BD4\u53E6\u6709\u6DF1\u610F, \u4E00\u65B9\u9762\u660E\u663E\u5730\u6307\u51FA\u7942\u6CA1\u6709\u7F34\u7EB3\u8D4B\u7A0E\u7684\u8D23\u4EFB, \u56E0\u4E3A\u8FD9\u79CD\u8D23\u4EFB\u4EC5\u9650\u4E8E\u53D7\u7BA1\u7406\u7684\u6C11\u4F17, \u56FD\u738B\u7684\u513F\u5B50\u4E0D\u5FC5\u9075\u5B88. \u6709\u5173\u5F1F 25 - 26 \u8282\u7684\u89E3\u91CA, \u662F\u57FA\u4E8E\u8036\u7A23\u4E3A\u5929\u4E3B\u5B50\u7684\u7F18\u6545. \u5723\u6BBF\u4E4B\u4E3B\u5C31\u662F\u5929\u4E3B, \u800C\u8036\u7A23\u5C31\u662F\u5929\u4E3B\u7684\u513F\u5B50. \u6240\u4EE5\u76F8\u4FE1\u8036\u7A23\u7684\u4EBA, \u4E5F\u5C31\u5206\u4EAB\u4E86\u513F\u5B50\u7684\u8FD9\u79CD\u8EAB\u4EFD, \u5C31\u83B7\u5F97\u4E86\u8036\u7A23\u548C\u7942\u95E8\u5F92\u4EEC\u6240\u4EAB\u6709\u7684\u90A3\u79CD\u81EA\u7531. \u4F46\u662F, \u901A\u8FC7\u90A3\u79CD\u81EA\u7531, \u8036\u7A23\u4E5F\u60F3\u8981\u8868\u8FBE\u81EA\u5DF1\u5BF9\u4E8E\u8FD9\u4E9B\u9644\u5C5E\u89C4\u6761\u7684\u5C0A\u91CD, \u4EE5\u53CA\u5BF9\u4E8E\u5929\u4E3B\u7684\u5C45\u6240 -- \u5723\u6BBF\u7684\u5C0A\u91CD.</p><p>\u5C3D\u7BA1\u8036\u7A23\u6CA1\u6709\u7F34\u7EB3\u8D4B\u7A0E\u7684\u8D23\u4EFB, \u4F46\u662F\u4E3A\u4E86\u4E0D\u5F15\u8D77\u8BEF\u89E3\uFF0C\u6216\u8005\u4E3A\u4E86\u907F\u514D\u4E0E\u5408\u6CD5\u6743\u5A01\u7684\u51B2\u7A81, \u4ECD\u65E7\u7F34\u7EB3\u4E86\u5723\u6BBF\u7A0E. \u5723\u6BBF\u5728\u516C\u5143 70 \u5E74\u88AB\u6BC1\u706D\u4E4B\u540E, \u6240\u6709\u4ECE\u72B9\u592A\u4EBA\u6536\u7F34\u8FC7\u6765\u7684\u7A0E\u91D1, \u90FD\u88AB\u4E13\u7528\u4E3A\u7EF4\u4FEE\u7F57\u9A6C\u5F02\u6559\u795E\u660E\u5728\u5361\u6BD4\u6258\u91CC\u5C71\u7684\u6731\u5E87\u7279\u795E\u6BBF. \u8FD9\u4E2A\u6545\u4E8B\u53EF\u4EE5\u5E2E\u52A9\u739B\u7AA6\u65F6\u4EE3\u7684\u72B9\u592A\u4EBA\u56E2\u4F53\u5BF9\u6B64\u6709\u6240\u7406\u89E3. \u5C3D\u7BA1\u6CA1\u6709\u4E49\u52A1, \u4F46\u4ED6\u4EEC\u4E3A\u4E86\u907F\u514D\u95F2\u8BDD\u548C\u8BFD\u8C24\u800C\u8FD9\u6837\u505A, \u4E5F\u662F\u5408\u5B9C\u7684 ( \u53C2\u9605\u7F57 13:1 - 7\uFF1B \u4F2F\u524D 2: 13-17 ).</p>',49),p=[h];function r(d,s){return a(),t("div",null,p)}var i=u(o,[["render",r],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E03\u7AE0.html.vue"]]);export{i as default};
