import{_ as u,o as t,c as o,e as a}from"./app.6bf529a6.js";const e={},q=a('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E5D\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E5D\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E5D\u7AE0</h1><h2 id="\u5A5A\u59FB\u4E0E\u8D1E\u6D01" tabindex="-1"><a class="header-anchor" href="#\u5A5A\u59FB\u4E0E\u8D1E\u6D01" aria-hidden="true">#</a> \u5A5A\u59FB\u4E0E\u8D1E\u6D01</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sub>1</sub> \u8036\u7A23\u8BB2\u5B8C\u8FD9\u4E9B\u8BDD\u4EE5\u540E, \u5C31\u79BB\u5F00\u52A0\u91CC\u808B\u4E9A, \u6765\u5230\u7EA6\u4F46\u5BF9\u5CB8\u7684\u72B9\u592A\u5883\u5185. <sub>2</sub> \u6709\u8BB8\u591A\u7FA4\u4F17\u8DDF\u968F\u4ED6, \u4ED6\u5C31\u5728\u90A3\u91CC\u533B\u597D\u4E86\u4ED6\u4EEC.</p><p><sub>3</sub> \u6709\u4E9B\u6CD5\u5229\u585E\u4EBA\u6765\u5230\u4ED6\u8DDF\u524D, \u8BD5\u63A2\u4ED6\u8BF4: &quot;\u8BB8\u4E0D\u8BB8\u4EBA\u4E3A\u4E86\u4EFB\u4F55\u4E8B\u7F18\u6545\u4F11\u81EA\u5DF1\u7684\u59BB\u5B50?&quot; <sub>4</sub> \u4ED6\u56DE\u7B54\u8BF4: &quot;\u4F60\u4EEC\u6CA1\u6709\u5FF5\u8FC7: \u90A3\u521B\u9020\u8005\u81EA\u8D77\u521D\u5C31\u9020\u4E86\u4ED6\u4EEC\u4E00\u7537\u4E00\u5973; <sub>5</sub> \u4E14\u8BF4: &#39;\u4E3A\u6B64, \u4EBA\u8981\u79BB\u5929\u7236\u4EB2\u548C\u6BCD\u4EB2, \u4F9D\u9644\u81EA\u5DF1\u7684\u59BB\u5B50, \u4E24\u4EBA\u6210\u4E3A\u4E00\u4F53&#39; \u7684\u8BDD. <sub>6</sub> \u8FD9\u6837, \u4ED6\u4EEC\u4E0D\u662F\u4E24\u4E2A, \u800C\u662F\u4E00\u4F53\u4E86. \u4E3A\u6B64, \u51E1\u5929\u4E3B\u6240\u7ED3\u5408\u7684, \u4EBA\u4E0D\u53EF\u62C6\u6563. &quot; <sub>7</sub> \u4ED6\u4EEC\u5BF9\u4ED6\u8BF4: &quot;\u90A3\u4E48, \u4E3A\u4EC0\u4E48\u6885\u745F\u8FD8\u5429\u5490\u4EBA\u4E0B\u4F11\u4E66\u4F11\u59BB\u5462?&quot; <sub>8</sub> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u6885\u745F\u4E3A\u4E86\u4F60\u4EEC\u7684\u5FC3\u786C, \u624D\u51C6\u8BB8\u4F60\u4EEC\u4F11\u59BB; \u4F46\u8D77\u521D\u5E76\u4E0D\u662F\u8FD9\u6837. <sub>9</sub> \u5982\u4ECA\u6211\u5BF9\u4F60\u4EEC\u8BF4: \u65E0\u8BBA\u8C01\u4F11\u59BB, \u9664\u975E\u56E0\u4E3A\u59D8\u5C45, \u800C\u53E6\u5A36\u4E00\u4E2A, \u4ED6\u5C31\u662F\u72AF\u5978\u6DEB; \u51E1\u5A36\u88AB\u4F11\u7684, \u4E5F\u662F\u72AF\u5978\u6DEB.&quot; <sub>10</sub> \u95E8\u5F92\u5BF9\u4ED6\u8BF4: &quot;\u4EBA\u540C\u59BB\u5B50\u7684\u5173\u7CFB, \u5982\u679C\u662F\u8FD9\u6837, \u5012\u4E0D\u5982\u4E0D\u5A36\u7684\u597D.&quot; <sub>11</sub> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u8FD9\u8BDD\u4E0D\u662F\u4EBA\u4EBA\u6240\u80FD\u9886\u609F\u7684, \u53EA\u6709\u90A3\u4E9B\u5F97\u4E86\u6069\u8D50\u7684\u4EBA, \u624D\u80FD\u9886\u609F. <sub>12</sub> \u56E0\u4E3A\u6709\u4E9B\u9604\u4EBA, \u4ECE\u6BCD\u80CE\u751F\u6765\u5C31\u662F\u8FD9\u6837; \u6709\u4E9B\u9604\u4EBA, \u662F\u88AB\u4EBA\u9604\u7684; \u6709\u4E9B\u9604\u4EBA, \u5374\u662F\u4E3A\u4E86\u5929\u56FD, \u800C\u81EA\u9604\u7684. \u80FD\u9886\u609F\u7684, \u5C31\u9886\u609F\u5427!&quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u6B21\u524D\u5F80\u5723\u5730\u7ED3\u675F\u4E86\u5BF9\u7FA4\u4F17\u7684\u6559\u8BAD, \u66F4\u4E3A\u4E0B\u9762\u5168\u90E8\u7684\u53D9\u8FF0\u9884\u7F6E\u91CD\u8981\u660E\u663E\u7684\u4E3B\u9898.</p><p>\u739B\u7AA6\u7B80\u5355\u7684\u63CF\u8FF0\u4E86\u8036\u7A23\u5728\u7EA6\u65E6\u6CB3\u5BF9\u5CB8\u7684\u72B9\u592A\u4EBA\u5883\u5185\u6CBB\u597D\u75C5\u4EBA, \u4EE5\u53CA &quot;\u8BB8\u591A\u7FA4\u4F17&quot; \u90FD\u8DDF\u968F\u7740\u4ED6.</p><p>\u8036\u7A23\u65A5\u79BB\u5A5A\u662F\u4E0D\u5408\u6CD5\u7684. \u5728\u57FA\u7763\u4FE1\u4EF0\u91CC, \u5A5A\u59FB\u8981\u8FFD\u6EAF\u5230 &quot;\u6700\u8D77\u521D&quot;, \u56E0\u4E3A\u9020\u4EBA\u7684\u5929\u4E3B\u547D\u4EE4\u4EBA\u548C\u59BB\u5B50\u5FC5\u8BE5 &quot;\u5408\u4E3A\u4E00\u4F53&quot; ( &quot;\u8089&quot; \u5728\u5E0C\u4F2F\u6765\u6587\u539F\u610F\u662F &quot;\u4EBA&quot; ). \u6885\u745F\u6CD5\u5F8B\u5BF9\u5929\u4E3B\u539F\u5148\u7684\u8BA1\u5212\u6709\u4E9B\u4FEE\u6539, \u662F\u4E3A\u4E86\u5728\u4E24\u4EF6\u7F6A\u6076\u4E4B\u4E2D\u9009\u62E9\u8F83\u8F7B\u5FAE\u7684 ( \u56E0\u4E3A\u505A\u4E08\u592B\u7684\u53EF\u80FD\u56E0\u4E0D\u51C6\u79BB\u5A5A\u800C\u8C0B\u6740\u59BB\u5B50. )</p><p>\u5B97\u5F92\u4EEC\u7684\u4E00\u53E5\u8BC4\u8BBA, \u5C31\u9887\u6D88\u6781\u5730\u628A\u8BDD\u9898\u5F15\u5230\u8D1E\u6D01\u95EE\u9898\u4E0A\u6765\u4E86. \u8036\u7A23\u6025\u6B32\u7EA0\u6B63\u4EE5\u65B9\u4FBF\u4E3A\u5E94\u5426\u7ED3\u5A5A\u7684\u51C6\u5219, \u800C\u80AF\u5B9A\u8D1E\u6D01\u662F\u5929\u4E3B\u8D35\u91CD\u7684\u56E0\u8D50. \u8FD9\u4E2A\u57FA\u7763\u4FE1\u4EF0\u6240\u7279\u6709\u7684\u7406\u60F3\u4E0E\u65E7\u7EA6\u65F6\u7684\u5B97\u6559\u601D\u60F3\u80CC\u9053\u800C\u9A70, \u4F8B\u5982\u4F9D\u5F17\u5927\u7684\u5973\u513F\u7684\u6545\u4E8B\u6240\u663E\u793A\u7684. \u543E\u4E3B\u5728\u6B64\u5904\u7684\u53E3\u6C14\u662F\u5178\u578B\u7684\u95EA\u65CF\u8BED\u6CD5, \u4F46\u6211\u4EEC\u5343\u4E07\u4E0D\u8BE5\u5FFD\u7565\u4ED6\u7ED9\u6211\u4EEC\u7EDD\u5BF9\u7684\u81EA\u7531\u6765\u63A5\u53D7\u8FD9\u4E2A\u6311\u6218\u6027\u7684\u56E0\u5BA0, \u4EE5\u53CA\u4ED6\u6240\u8981\u6C42\u7684\u5728\u610F\u5FF5\u4E0A\u7684\u7EAF\u6D01. \u5728\u65E7\u7EA6\u65F6\u4EE3, \u53F8\u796D\u53EA\u4ECE\u4EE5\u8272\u5217\u7684\u4E00\u652F\u4E2D\u9009\u51FA\u6765 ( \u808B\u672A ); \u5728\u57FA\u7763\u5F92\u4E2D, \u8FD9\u4E2A\u601D\u5BA0\u662F\u8D50\u7ED9\u90A3\u4E9B\u6177\u6168\u5927\u65B9\u7684\u7537\u7537\u5973\u5973. \u540C\u65F6\u8FD8\u8981\u6CE8\u610F\u4E00\u70B9, \u65B0\u7EA6\u5723\u7ECF, \u5B88\u8D1E\u8005\u6210\u4E3A\u7406\u60F3\u57FA\u7763\u5F92\u7684\u8C61\u5F81, \u4FDD\u7984\u66FE\u8868\u793A\u4ED6\u610F\u8BC6\u5230\u5728\u5B88\u8D1E\u4E0A\u6240\u62E5\u6709\u7684\u81EA\u7531, \u4EE5\u53CA\u5B83\u7684\u65E0\u6BD4\u73CD\u8D35. \u4FEE\u4F1A\u91CC\u7684\u8D1E\u6CBB\u8A93\u613F, \u4F8B\u5982\u795E\u7236\u4FEE\u5973\u7684\u72EC\u8EAB\u751F\u6D3B\u613F, \u662F\u4E00\u4E2A\u79EF\u6781\u7684\u884C\u52A8, \u662F\u7231\u7684\u8868\u793A, \u662F\u81EA\u6211\u5949\u732E\u7ED9\u57FA\u7763. \u739B\u7AA6\u798F\u97F3\u53E6\u4E00\u4E2A\u6BD4\u55BB\u7528\u8D1E\u5973\u6765\u4EE3\u8868\u6559\u4F1A\u9ED8\u89C2\u4E2D\u7684\u7231\u60C5, \u66F4\u52A0\u5F3A\u8BF4\u660E\u4E86\u8FD9\u4E00\u70B9.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5728\u8036\u7A23\u65F6\u4EE3, \u6765\u5F80\u52A0\u91CC\u808B\u4E9A\u548C\u72B9\u592A\u5730\u533A\u6700\u7B80\u6377\u7684\u8DEF\u5F84, \u662F\u7ECF\u8FC7\u6492\u739B\u9ECE\u96C5. \u4F46\u739B\u7AA6\u5723\u53F2\u8BB0\u8FF0\u8036\u7A23\u524D\u5F80\u8036\u8DEF\u6492\u51B7\u7684\u65C5\u9014, \u4E0E\u5927\u591A\u6570\u7684\u72B9\u592A\u4EBA\u4E00\u6837, \u662F\u7ED5\u8FC7\u7EA6\u4F46\u6CB3\u4E1C\u8FB9\u800C\u76F4\u8FBE\u72B9\u592A\u5883\u754C, \u4E3A\u7684\u662F\u8981\u907F\u514D\u7ECF\u8FC7\u6492\u739B\u9ECE\u96C5 ( 1 \u8282 ). \u8FD9\u65F6, \u53CD\u5BF9\u8036\u7A23\u7684\u72B9\u592A\u5B97\u6559\u9886\u8896\u53C8\u4EE5\u79BB\u5A5A\u95EE\u9898\u5411\u8036\u7A23\u6311\u8845, \u4E3A\u7684\u662F\u8981\u8BD5\u63A2\u7942 ( \u53C2\u9605: \u739B\u5341\u516D 1 ). \u8036\u7A23\u4E0D\u51C6\u79BB\u5A5A\u7684\u8A00\u8BBA\u5728 &quot;\u5C71\u4E2D\u5723\u8BAD&quot; \u4E2D\u5DF2\u6709\u6559\u5BFC ( \u739B\u4E94 31 - 32 ). \u73B0\u5728\u63D0\u51FA\u8FD9\u4E2A\u95EE\u9898, \u662F\u8981\u8BD5\u95EE\u8036\u7A23\u5BF9\u79BB\u5A5A\u8BAE\u9898\u6709\u6CA1\u6709\u4F8B\u5916\u6CD5\u89C4 ( 3 \u8282 ). \u5F53\u65F6 ( \u4E3B\u5386\u7B2C\u4E00\u4E16\u7EAA ) \u7684\u72B9\u592A\u7ECF\u5E08\u4F20\u7EDF\u4E2D\u6709\u4E0D\u540C\u7684\u4E3B\u5F20, \u4F8B\u5982: \u971E\u739B\u4F9D\u5B66\u6D3E\u548C\u5E0C\u808B\u8033\u5B66\u6D3E\u4E4B\u95F4\u5C31\u5E38\u8FA9\u8BBA. \u5E0C\u808B\u8033\u5B66\u6D3E\u575A\u4FE1, \u7537\u4EBA\u751A\u81F3\u53EF\u4EE5\u56E0\u4E3A\u59BB\u5B50\u716E\u7684\u83DC\u4E0D\u597D\u548C\u5979\u79BB\u5A5A ( \u8FD9\u4E00\u6D3E\u8F83\u5BBD\u677E, \u8BA4\u4E3A\u4EFB\u4F55\u4EE4\u4E08\u592B\u70E6\u6270\u7684\u4E8B, \u90FD\u53EF\u662F\u6784\u6210\u4F11\u59BB\u7684\u7406\u7531 ); \u800C\u971E\u739B\u4F9D\u5B66\u6D3E\u5219\u4E89\u8FA9\u8BF4, \u53EA\u53D1\u751F\u6DEB\u4E71\u7684\u4E8B ( \u59BB\u5B50\u6709\u5916\u9047 ), \u624D\u53EF\u4F11\u59BB, \u624D\u662F\u79BB\u5A5A\u7684\u7406\u7531.</p><p>\u8036\u7A23\u56DE\u7B54\u65F6, \u9996\u5148\u5F15\u7528\u300A\u521B\u4E16\u7EAA\u300B\u4E2D &quot;\u90A3\u521B\u9020\u8005\u81EA\u8D77\u521D\u5C31\u9020\u4E86\u4ED6\u4EEC\u4E00\u7537\u4E00\u5973&quot; ( \u521B\u4E00 27 ) \u8FD9\u53E5\u8BDD, \u7136\u540E\u53C8\u5F15\u7528\u4E86\u300A\u521B\u4E16\u7EAA\u300B\u7684\u53E6\u4E00\u53E5\u8BDD: &quot;\u4E3A\u6B64, \u4EBA\u8981\u79BB\u5F00\u7236\u4EB2\u548C\u6BCD\u4EB2, \u4F9D\u9644\u81EA\u5DF1\u7684\u59BB\u5B50, \u4E24\u4EBA\u6210\u4E3A\u4E00.&quot; ( \u521B\u4E8C 24 ), \u4ECE\u6B64\u8BF4\u660E\u5929\u4E3B\u521B\u9020\u7684\u65E8\u610F, \u662F\u8981\u7537\u4EBA\u548C\u5973\u4EBA\u6210\u4E3A\u4E00\u4F53. \u8036\u7A23\u7684\u654C\u5BF9\u8005, \u4E5F\u5F15\u7528\u300A\u7533\u547D\u7EAA\u300B\u4E2D\u7684\u51C6\u8BB8\u4EBA\u4F11\u59BB\u7684\u89C4\u5B9A ( \u7533\u4E8C\u5341\u56DB 1 ~ 4 ) \u6765\u53CD\u9A73\u8036\u7A23: \u6885\u745F\u5145\u8BB8\u7537\u4EBA\u53EA\u9700\u8981\u4E00\u5F20\u4F11\u4E66, \u5C31\u53EF\u4E0E\u4ED6\u7684\u59BB\u5B50\u79BB\u5A5A. \u4E8E\u662F, \u8036\u7A23\u628A\u300A\u521B\u4E16\u7EAA\u300B\u4E2D\u5929\u4E3B\u6240\u542F\u793A\u7684\u65E8\u610F\u53CA\u5929\u4E3B\u4E0D\u5BB9\u7F6E\u7591\u7684\u547D\u4EE4, \u4E0E\u6885\u745F\u56E0\u4EE5\u8272\u5217\u4EBA\u65E0\u6CD5\u8FBE\u5230\u7406\u60F3\u6807\u51C6\u800C\u505A\u51FA\u7684\u8BA9\u6B65, \u4E24\u8005\u4E4B\u95F4\u505A\u51FA\u4E86\u533A\u9694.</p><p>\u8036\u7A23\u628A\u4F11\u59BB\u5F62\u5BB9\u4E3A\u5978\u6DEB, \u9664\u975E\u4F11\u59BB\u662F\u57FA\u4E8E &quot;\u59D8\u5C45&quot; \u7684\u539F\u56E0 ( 9 \u8282, \u4E5F\u8BF7\u53C2\u9605: \u739B\u4E94 32 ). &quot;\u59D8\u5C45&quot; \u8FD9\u4E2A\u5B9A\u7684\u610F\u4E49\u4E0D\u662F\u5F88\u660E\u786E, \u4E5F\u8BB8\u6307\u7684\u662F\u6027\u884C\u4E3A\u4E0D\u68C0\u70B9, \u53EF\u80FD\u662F\u6307 &quot;\u88AB\u72B9\u592A\u6CD5\u5F8B\u7981\u6B62\u7684\u901A\u5978\u884C\u4E3A&quot;, \u4E5F\u53EF\u80FD\u662F\u6307 &quot;\u8FD1\u4EB2\u7684\u5A5A\u59FB&quot;, \u90A3\u4E48, \u8FD9\u4E2A\u95EE\u9898\u6D89\u53CA\u5230\u4E86\u4E00\u4E9B\u5E0C\u671B\u6210\u4E3A\u57FA\u7763\u5F92, \u800C\u53C8\u5904\u4E8E\u8FD9\u79CD\u88AB\u7981\u6B62\u7684\u5A5A\u59FB\u72B6\u51B5\u4E2D\u7684\u5916\u90A6\u4EBA. \u4ED6\u4EEC\u662F\u5426\u5E94\u5148\u79BB\u5A5A, \u7136\u540E\u624D\u53EF\u4EE5\u52A0\u5165\u6559\u4F1A\u56E2\u4F53\u5462?</p><p>\u8036\u7A23\u95E8\u5F92\u7684\u53CD\u5E94, \u663E\u793A\u8036\u7A23\u7684\u6559\u5BFC\u6709\u70B9\u6781\u7AEF. \u95E8\u5F92\u8BF4 &quot;\u4EBA\u540C\u59BB\u5B50\u7684\u5173\u7CFB, \u5982\u679C\u662F\u8FD9\u6837, \u5012\u4E0D\u5982\u4E0D\u5A36\u7684\u597D&quot; ( 10 \u8282 ), \u8FD9\u53E5\u8BDD\u57FA\u5B9E\u4E0E\u8036\u7A23\u524D\u9762\u8BF4\u8FC7\u7684 &quot;\u5018\u82E5\u4F60\u7684\u624B, \u6216\u4F60\u7684\u811A\u4F7F\u4F60\u8DCC\u5012, \u780D\u4E0B\u5B83\u6765 ......, \u5018\u82E5\u4F60\u7684\u773C\u4F7F\u4F60\u8DCC\u5012, \u525C\u51FA\u5B83\u6765 .......&quot; ( \u739B\u5341\u516B 8 ~ 9 ) \u4E00\u6837, \u662F\u4E00\u79CD\u5938\u5F20\u7684\u5199\u4F5C\u624B\u6CD5; \u8BF4 &quot;\u5B81\u613F\u628A\u624B, \u6216\u811A, \u6216\u773C\u775B\u5207\u9664, \u800C\u4E0D\u4F7F\u5176\u4EE4\u4EBA\u8DCC\u5012 ( \u9677\u4E8E\u7F6A\u6076 )&quot;, \u8FD9\u5E76\u4E0D\u662F\u8BF4\u8036\u7A23\u8981\u4F7F\u6240\u6709\u4EBA\u7684\u90FD\u63A5\u53D7\u8FD9\u6837\u7684\u6559\u5BFC. \u957F\u4E45\u4EE5\u6765, \u5BF9\u4E8E &quot;\u6709\u6B64\u9604\u4EBA, \u5374\u662F\u4E3A\u4E86\u5929\u56FD, \u800C\u81EA\u9604\u7684&quot; ( 12 \u8282 ) \u8FD9\u53E5\u8BDD, \u5230\u5E95\u662F\u6307\u9009\u62E9\u4FDD\u6301\u72EC\u8EAB\u7684\u4EBA, \u8FD8\u662F\u6307\u4E27\u5076\u6216\u79BB\u5A5A\u540E\u4E0D\u518D\u5A5A\u5AC1\u7684\u4EBA, \u4E00\u76F4\u4E89\u8FA9\u4E0D\u4F11. \u72B9\u592A\u7684\u4F20\u7EDF\u5A5A\u59FB\u662F\u7B26\u5408\u6CD5\u5F8B\u6807\u51C6\u548C\u89C4\u8303\u7684, \u4EC5\u7BA1\u6709\u4E9B\u6D3E\u522B, \u5982\u8C37\u6728\u5170\u9690\u58EB\u7B49, \u4ED6\u4EEC\u663E\u7136\u662F\u5949\u884C\u72EC\u8EAB\u751F\u6D3B\u7684.</p><p>\u57FA\u7763\u5F92\u505A\u51FA\u8FD9\u6837\u7684\u72EC\u8EAB\u9009\u62E9, \u5176\u539F\u56E0\u662F\u4E3A\u4E86\u5BA3\u8BB2\u4E8B\u5DE5, \u80DC\u4E8E\u518D\u5EA6\u7ED3\u5A5A ( \u53C2\u9605: \u5B97\u4E5D 39, \u4E5F\u8BB8\u5C31\u662F\u8FD9\u6837\u7684\u60C5\u5F62: \u5F1F\u524D\u4E94 3 ~ 16 \u5219\u8BBA\u53CA\u5BE1\u5987\u884C\u4E8B\u4E3A\u4EBA\u7684\u89C4\u8303 ). \u6709\u5173\u8036\u7A23\u5BF9\u79BB\u5A5A\u7684\u4E25\u683C\u6559\u5BFC\uFF0C \u4E5F\u8BB8\u662F\u4ED6\u5BF9\u90A3\u65F6\u4EE3\u7684\u5987\u5973\u6240\u4EA7\u751F\u7684\u6190\u60AF\u548C\u540C\u60C5\u7684\u7ED3\u679C, \u4E5F\u662F\u8981\u4FDD\u969C\u5987\u5973\u4E0D\u4F1A\u56E0\u4E3A\u6CA1\u6709\u7406\u7531\u5730\u88AB\u629B\u5F03, \u6216\u88AB\u7F6E\u4E8E\u793E\u4F1A\u548C\u7ECF\u6D4E\u7684\u5F31\u52BF\u5730\u4F4D. \u540C\u6837, \u73B0\u4EE3\u4EBA\u5728\u505A\u79BB\u5A5A\u8FD9\u4E2A\u75DB\u82E6\u7684\u51B3\u5B9A\u524D, \u4E5F\u5FC5\u987B\u60F3\u5230, \u8036\u7A23\u9996\u5148\u5173\u5FC3\u7684\u662F\u4FE1\u4EF0\u56E2\u4F53\u4E2D, \u6BCF\u4E00\u4E2A\u4EBA, \u4F5C\u4E3A\u5929\u4E3B\u7684\u5B50\u5973\u7684\u5E78\u798F.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u79BB\u5A5A\u7684\u4E89\u8BBA, \u5728\u6709\u798F\u97F3\u4E4B\u524D\u5C31\u5DF2\u7ECF\u5B58\u5728. \u8036\u7A23\u65F6\u4EE3, \u5173\u4E8E\u8FD9\u4E00\u4E89\u8BBA\u51FA\u4E86\u4E24\u4E2A\u6781\u7AEF\u7684\u5B66\u6D3E: \u4E00\u4E2A\u662F\u7279\u522B\u968F\u4FBF\u7684\u770B\u6CD5, \u5141\u8BB8\u4EBA\u53EF\u501F\u4EFB\u4F55\u7406\u7531\u79BB\u5A5A, \u4F8B\u5982: \u59BB\u5B50\u70E7\u7126\u6216\u53EA\u662F\u718F\u9ED1\u4E86\u996D, \u4E08\u592B\u5C31\u53EF\u4EE5\u5199\u4F11\u4E66\u5C06\u5979\u4F11\u4E86. \u6240\u4EE5, \u4ED6\u4EEC\u6309\u7167\u8FD9\u4E2A\u6D3E\u522B\u7684\u4F20\u7EDF\u6765\u89E3\u91CA\u6CD5\u5F8B, \u4E00\u822C\u5141\u8BB8\u79BB\u5A5A\u7684\u6761\u4EF6, \u662F\u4E08\u592B &quot;\u53D1\u73B0\u59BB\u5B50\u6709\u4EFB\u4F55\u53EF\u7684\u884C\u4E3A&quot; ( \u7533 24: 1 ). \u53E6\u5916\u4E00\u4E2A\u6D3E\u522B\u6BD4\u8F83\u4E25\u683C\uFF0C \u4ED6\u4EEC\u5C06\u300A\u7533\u547D\u7EAA\u300B\u4E2D\u6240\u8BF4\u7684\u4F8B\u5916\u60C5\u51B5\u4EC5\u4EC5\u89E3\u91CA\u4E3A\u901A\u5978\u884C\u4E3A.</p><p>\u6CD5\u5229\u8D5B\u4EBA\u8BD5\u56FE\u4EE5\u79BB\u5A5A\u7684\u95EE\u9898\u6765\u6293\u4F4F\u8036\u7A23\u7684\u628A\u67C4. \u8036\u7A23\u5374\u544A\u8BC9\u4ED6\u4EEC\u4E00\u9879\u79EF\u6781\u7684\u6CD5\u5F8B, \u4ECE\u5929\u4E3B\u8D77\u521D\u6240\u5EFA\u7ACB\u7684\u539F\u59CB\u79E9\u5E8F, \u6765\u8BF4\u660E\u8FD9\u4E2A\u95EE\u9898, \u4E0D\u662F\u5F3A\u8FEB, \u800C\u662F\u81EA\u7531\u7684\u9009\u62E9 ( \u521B 1: 27\uFF0C 2: 24, 5: 2 ).</p><p>\u5728\u5F53\u65F6\u7537\u4EBA\u4E3B\u5BFC\u4E00\u5207\u7684\u793E\u4F1A\u91CC, \u4E00\u4E2A\u88AB\u629B\u5F03\u7684\u5973\u4EBA\u5FC5\u987B\u56DE\u5230\u81EA\u5DF1\u7684\u7236\u4EB2\u5BB6, \u8FD9\u6837\u4F7F\u5979\u81EA\u5DF1, \u4EE5\u53CA\u6574\u4E2A\u5BB6\u65CF\u8499\u7F9E. \u79BB\u5A5A\u7684\u5A01\u80C1\u5982\u540C\u6B8B\u9177\u7684\u6B66\u5668, \u8FEB\u4F7F\u59BB\u5B50\u5C48\u670D\u5730\u4E08\u592B. \u5728\u8FD9\u79CD\u80CC\u666F\u4E0B, \u8036\u7A23\u7684\u8BDD\u5E26\u6765\u5B8C\u5168\u7684\u89E3\u8131\u548C\u91CA\u653E. \u7981\u6B62\u79BB\u5A5A\u7684\u76EE\u7684\u4E3B\u8981\u662F\u4E3A\u4E86\u4FDD\u62A4\u5987\u5973, \u5E76\u4E3A\u6062\u590D\u5929\u4E3B\u4ECE\u8D77\u521D\u5EFA\u7ACB\u7684\u79E9\u5E8F.</p><p>\u5E0C\u814A\u6587 &quot;\u6DEB\u4E71&quot; ( 9 ) \u4E00\u8BCD\u7684\u53E6\u4E00\u79CD\u610F\u601D\u662F &quot;\u59D8\u5C45&quot;, \u5373\u7537\u5973\u4E0D\u5408\u6CD5\u7684\u7ED3\u5408. &quot;\u4E0D\u80FD\u7ED3\u5A5A\u751F\u80B2&quot; \u548C &quot;\u653E\u5F03\u4E86\u5A5A\u59FB&quot; ( 12 ), \u539F\u6587\u76F4\u8BD1\u4E3A &quot;\u9604\u4EBA&quot;.</p><p>\u95E8\u5F92\u4EEC\u542C\u5230\u8FD9\u79CD\u4E0D\u53EF\u62C6\u6563\u7684\u7EA6\u675F\u65F6, \u90FD\u611F\u5230\u975E\u5E38\u60CA\u8BB6 ( \u6CD5\u5229\u585E\u4EBA\u5DF2\u79BB\u573A, \u6CA1\u6709\u53C2\u4E0E\u8BA8\u8BBA ). \u8036\u7A23\u5E76\u6CA1\u6709\u53D6\u6D88\u5DF2\u7ECF\u8BF4\u8FC7\u7684\u8BDD, \u800C\u662F\u66F4\u8FDB\u4E00\u6B65, \u63D0\u51FA\u4E86\u4E00\u4E2A\u5C06\u8981\u51FA\u73B0\u5728\u81EA\u5DF1\u56E2\u4F53\u4E2D\u7684\u60C5\u51B5, \u5373\u81EA\u613F\u72EC\u8EAB\u751F\u6D3B ( 12 ). \u57FA\u7763\u5F92\u7684\u72EC\u8EAB, \u53EA\u6709\u5728\u5929\u56FD\u5965\u79D8\u7684\u610F\u4E49\u4E0B\u624D\u80FD\u7406\u89E3. \u4E3A\u6B64, \u8036\u7A23\u4E5F\u8BF4: &quot;\u8C01\u80FD\u63A5\u53D7\u5C31\u63A5\u53D7\u5427&quot;.</p><h2 id="\u8036\u7A23\u795D\u798F\u513F\u7AE5" tabindex="-1"><a class="header-anchor" href="#\u8036\u7A23\u795D\u798F\u513F\u7AE5" aria-hidden="true">#</a> \u8036\u7A23\u795D\u798F\u513F\u7AE5</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sub>13</sub> \u90A3\u65F6, \u6709\u4EBA\u7ED9\u8036\u7A23\u9886\u6765\u4E00\u4E9B\u5C0F\u5B69\u5B50, \u8981\u4ED6\u7ED9\u4ED6\u4EEC\u8986\u624B\u7948\u7977, \u95E8\u5F92\u5374\u65A5\u8D23\u4ED6\u4EEC. <sub>14</sub> \u8036\u7A23\u8BF4: &quot;\u4F60\u4EEC\u8BA9\u5C0F\u5B69\u5B50\u6765\u5427! \u4E0D\u8981\u963B\u6B62\u4ED6\u4EEC\u5230\u6211\u8DDF\u524D\u6765, \u56E0\u4E3A\u5929\u56FD\u6B63\u5C5E\u4E8E\u8FD9\u6837\u7684\u4EBA.&quot; <sub>15</sub> \u8036\u7A23\u7ED9\u4ED6\u4EEC\u8986\u4E86\u624B, \u5C31\u4ECE\u90A3\u91CC\u8D70\u4E86.</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4E3A\u4E86\u89E3\u91CA\u8FD9\u4E2A\u6545\u4E8B\u5728\u5B97\u5F92\u4F20\u6559\u4E4B\u4E2D\u51FA\u73B0, \u6709\u4EBA\u8BA4\u4E3A\u521D\u671F\u6559\u4F1A\u85C9\u6B64\u6545\u4E8B\u4F20\u8FBE\u5173\u4E8E\u8BE5\u7AE5\u9886\u5148\u7684\u6559\u4E49. \u90A3\u53E5 &quot;\u4E0D\u8981\u963B\u6B62\u4ED6\u4EEC&quot; \u662F\u6700\u521D\u6D17\u793C\u91CC\u6700\u88F9\u5F15.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u73B0\u5728\u739B\u7AA6\u5723\u53F2\u4EE5\u5E7F\u89D2\u955C\u5934\u5C06\u88AB\u63CF\u8FF0\u7684\u5BF9\u8C61\u96C6\u4E2D\u5728\u5BB6\u5EAD\u5355\u4F4D\u4E2D\u6700\u5F31\u52BF\u7684\u6210\u5458\u8EAB\u4E0A ( 13 ~ 15 \u8282 ). \u5728\u8FD9\u4E00\u6BB5\u4E0E\u5148\u524D\u7684\u60C5\u8282\u8FDE\u7ED3\u8D77\u6765, \u5C31\u53EF\u4EE5\u770B\u5230\u8036\u7A23\u5BF9\u5C0F\u5B69\u5B50\u7684\u795D\u798F\u548C\u7948\u7977, \u662F\u610F\u8BC6\u5230\u7236\u6BCD\u6253\u7B97\u79BB\u5A5A\u65F6, \u6700\u53D7\u4F24\u5BB3\u7684\u4E2D\u80FD\u662F\u5C0F\u5B69. \u95E8\u5F92\u4E3A\u4EC0\u4E48\u8981\u963B\u6B62\u5B69\u5B50\u5230\u8036\u7A23\u8DDF\u524D, \u5723\u53F2\u5E76\u6CA1\u6709\u8BF4\u660E\u539F\u56E0, \u8036\u7A23\u66FE\u5728\u4E4B\u524D\u7684\u8A00\u8BBA\u8C08\u5230\u8FC7\u5173\u4E8E\u5C0F\u5B69\u5728\u5929\u56FD\u7684\u91CD\u8981\u6027 ( \u739B\u5341\u516B ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u65E9\u5DF2\u5C06\u513F\u7AE5\u4F5C\u4E3A\u95E8\u5F92\u4EEC\u5E94\u6548\u4EFF\u7684\u6A21\u8303. \u8036\u7A23\u7684\u6001\u5EA6\u4E0E\u95E8\u5F92\u4EEC\u7684\u538C\u70E6\u5F62\u6210\u4E86\u7A23\u660E\u7684\u5BF9\u6BD4. \u8036\u7A23\u501F\u7740\u8FD9\u4E2A\u8C61\u5F81\u6027\u7684\u884C\u4E3A\u5F3A\u8C03: \u5929\u56FD\u6C38\u8FDC\u4F18\u5148\u5C5E\u4E8E\u90A3\u4E9B\u5C06\u81EA\u5DF1\u53D8\u4E3A\u5C0F\u5B69\u7684\u4EBA, \u6216\u8005\u90A3\u4E9B\u4EE5\u8C26\u865A\u548C\u7EAF\u6734\u7684\u6001\u5EA6\u63A5\u53D7\u5929\u4E3B\u56E0\u8D50\u7684\u4EBA.</p><h2 id="\u8D22\u5E1B\u7684\u4E3A\u5BB3" tabindex="-1"><a class="header-anchor" href="#\u8D22\u5E1B\u7684\u4E3A\u5BB3" aria-hidden="true">#</a> \u8D22\u5E1B\u7684\u4E3A\u5BB3</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sub>13</sub> \u6709\u4E00\u4E2A\u4EBA\u6765\u5230\u8036\u7A23\u8DDF\u524D\u8BF4: &quot;\u5E08\u5085! \u6211\u8BE5\u884C\u4EC0\u4E48 &#39;\u5584&#39;, \u4E3A\u5F97\u6C38\u751F?&quot; <sub>17</sub> \u8036\u7A23\u5BF9\u4ED6\u8BF4: &quot;\u4F60\u4E3A\u4EC0\u4E48\u95EE\u6211\u5173\u4E8E &#39;\u5584&#39;? \u5584\u7684\u53EA\u6709\u4E00\u4E2A. \u5982\u679C\u4F60\u613F\u610F\u8FDB\u5165\u751F\u547D, \u5C31\u8BE5\u9075\u8BEB\u547D.&quot; <sub>18</sub> \u4ED6\u5BF9\u8036\u7A23\u8BF4: &quot;\u4EC0\u4E48\u8BEB\u547D?&quot; \u8036\u7A23\u8BF4: &quot;\u5C31\u662F: \u4E0D\u53EF\u6740\u4EBA, \u4E0D\u53EF\u5978\u6DEB, \u4E0D\u53EF\u5077\u76D7, \u4E0D\u53EF\u4F5C\u5047\u89C1\u8BC1; <sub>19</sub> \u5E94\u8BE5\u5B5D\u656C\u7236\u6BCD, \u5E94\u8BE5\u7231\u4F60\u7684\u8FD1\u4EBA, \u5982\u7231\u4F60\u81EA\u5DF1.&quot; <sub>20</sub> \u90A3\u5C11\u5E74\u4EBA\u5BF9\u8036\u7A23\u8BF4: &quot;\u8FD9\u4E00\u5207\u6211\u90FD\u9075\u5B88\u4E86, \u8FD8\u7F3A\u5C11\u4EC0\u4E48?&quot; <sub>21</sub> \u8036\u7A23\u5BF9\u4ED6\u8BF4: &quot;\u4F60\u82E5\u613F\u610F\u6210\u5168\u7684, \u53BB! \u53D8\u5356\u4F60\u6240\u6709\u7684, \u65BD\u820D\u7ED9\u7A77\u4EBA, \u4F60\u5FC5\u6709\u5B9D\u85CF\u5728\u5929\u4E0A; \u7136\u540E\u8DDF\u968F\u6211.&quot; <sub>22</sub> \u5C11\u5E74\u4EBA\u542C\u8FD9\u8BDD, \u5C31\u5FE7\u95F7\u7684\u8D70\u4E86, \u56E0\u4E3A\u4ED6\u62E5\u6709\u8BB8\u591A\u4EA7\u4E1A.</p><p><sub>23</sub> \u4E8E\u662F, \u8036\u7A23\u5BF9\u95E8\u5F92\u8BF4: &quot;\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u5BCC\u4EBA\u96BE\u8FDB\u5929\u56FD. <sub>24</sub> \u6211\u518D\u544A\u8BC9\u4F60\u4EEC: \u9A86\u9A7C\u7A7F\u8FC7\u9488\u5B54, \u6BD4\u5BCC\u4EBA\u8FDB\u5929\u56FD\u8FD8\u5BB9\u6613. &quot; <sub>25</sub> \u95E8\u5F92\u4EEC\u542C\u4E86, \u5C31\u975E\u5E38\u60CA\u5F02\u8BF4: &quot;\u8FD9\u6837, \u8C01\u8FD8\u80FD\u5F97\u6551\u5462?&quot; <sub>26</sub> \u8036\u7A23\u6CE8\u89C6\u4ED6\u4EEC\u8BF4: &quot;\u4E3A\u4EBA\u8FD9\u662F\u4E0D\u53EF\u80FD\u7684; \u4F46\u4E3A\u5929\u4E3B, \u4E00\u5207\u90FD\u662F\u53EF\u80FD\u7684.&quot;</p></blockquote><blockquote><p><sub>27</sub> \u90A3\u65F6, \u4F2F\u591A\u7984\u5F00\u53E3\u5BF9\u4ED6\u8BF4: &quot;\u770B, \u6211\u4EEC\u820D\u5F03\u4E00\u5207, \u8DDF\u968F\u4E86\u4F60; \u90A3\u4E48, \u5C06\u6765\u6211\u4EEC\u53EF\u5F97\u5230\u4EC0\u4E48\u5462?&quot; <sub>28</sub> \u8036\u7A23\u5BF9\u4ED6\u4EEC\u8BF4: &quot;\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC: \u4F60\u4EEC\u8FD9\u4E9B\u8DDF\u968F\u6211\u7684\u4EBA, \u5728\u91CD\u751F\u7684\u4E16\u4EE3, \u4EBA\u5B50\u5750\u5728\u81EA\u5DF1\u5149\u8363\u7684\u5B9D\u5EA7\u4E0A\u65F6, \u4F60\u4EEC\u4E5F\u8981\u5750\u5728\u5341\u4E8C\u5B9D\u5EA7\u4E0A, \u5BA1\u5224\u4EE5\u8272\u5217\u5341\u4E8C\u652F\u6D3E; <sub>29</sub> \u5E76\u4E14, \u51E1\u4E3A\u6211\u7684\u540D, \u820D\u5F03\u4E86\u623F\u5C4B, \u6216\u5144\u5F1F, \u6216\u59CA\u59BA, \u6216\u7236\u4EB2, \u6216\u6BCD\u4EB2, \u6216\u513F\u5973, \u6216\u7530\u5730\u7684, \u5FC5\u8981\u9886\u53D6\u767E\u500D\u7684\u8D4F\u62A5, \u5E76\u627F\u53D7\u6C38\u751F. <sub>30</sub> \u6709\u8BB8\u591A\u5728\u5148\u7684\u8981\u6210\u4E3A\u5728\u540E\u7684, \u5728\u540E\u7684\u8981\u6210\u4E3A\u5728\u5148\u7684. &quot;</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u9047\u5230\u4E00\u4E2A\u5BCC\u4EBA, \u4ED6\u8C28\u6CD5\u5F8B, \u65E0\u61C8\u53EF\u51FB, \u5374\u4E0D\u613F\u6309\u53D7\u798F\u97F3\u7684\u795E\u8D2B. \u8FD9\u7ED9\u4E86\u4ED6\u4E00\u4E2A\u968F\u673A\u65BD\u6559\u7684\u673A\u4F1A\uFF0C\u544A\u8BEB\u5B97\u5F92\u4EEC\u5BCC\u6709\u7684\u4EBA\u88AB\u5141\u8BB8\u5165\u5929\u56FD\u662F\u4F55\u5176\u56F0\u96BE. \u90A3\u53E5 &quot;\u662F\u7684, \u6211\u518D\u8BF4\u4E00\u6B21&quot; ( \u8036\u7A23\u4EE5\u524D\u4ECE\u6765\u6CA1\u6709\u8BF4\u8FC7\u8FD9\u79CD\u8BDD ) \u662F\u739B\u7AA6\u610F\u56FE\u7B80\u7565\u7684\u8868\u793A. &quot;\u9488\u5B54&quot; \u53EF\u80FD\u6307\u4E00\u79CD\u5F88\u7A84\u7684\u57CE\u95E8, \u4E5F\u53EF\u80FD\u5C31\u662F\u5B57\u9762\u7684\u610F\u4E49, ( \u662F\u4E1C\u65B9\u5F0F\u7684\u5938\u5F20\u7684\u4E00\u4F8B ). \u5B97\u5F92\u7684\u53CD\u5E94 ( \u5BF9\u6211\u4EEC\u800C\u8A00\u771F\u662F\u5947\u602A\u975E\u5E38 ) \u662F\u4EE5\u65E7\u7EA6\u65F6\u4EE3\u7684\u89C2\u70B9\u4E3A\u6839\u636E, \u7EDD\u4E0D\u662F\u4EBA\u529B\u6240\u80FD\u5F71\u54CD\u7684. \u4F2F\u591A\u7984\u8BE2\u95EE\u5173\u4E8E\u4ED6\u4EEC\u5341\u4E8C\u4EBA\u629B\u5F03\u4E00\u5207\u8FFD\u968F\u8036\u7A23\u6240\u5E94\u5F97\u7684\u8D4F\u62A5, \u8036\u7A23\u7B54\u5E94\u7ED9\u4ED6\u4EEC\u5728\u5C06\u6765\u90FD\u4F1A\u7684\u5BA1\u5224\u6743 ( \u65E7\u7EA6\u679C\u6240\u6709\u4E0E\u8FD9\u4EF6\u4E8B\u60C5\u76F8\u5173\u7684\u8BED\u53E5\u5207\u4E0D\u53EF\u8BEF\u4F1A\u662F\u6307\u672B\u4E16\u7684\u5BA1\u5224 ). \u90A3\u4E9B\u653E\u5F03\u5BB6\u5EAD\u548C\u4E00\u5207\u6240\u6709\u7684\u57FA\u7763\u5F92\u90FD\u5728\u8FD9\u91CC\u5F97\u5230\u6B64\u540E\u548C\u6C38\u751F\u7684 &quot;\u8BB8\u591A\u500D&quot; \u7684\u8D4F\u62A5. \u8FD9\u6BB5\u6587\u5B57\u91CC, \u8FD9\u4E00\u5D07\u9AD8\u7684\u7406\u60F3\u5C31\u6210\u4E3A\u4F17\u4EBA\u77E5\u6653\u7684\u798F\u97F3\u529D\u8C15 ( \u5373\u4E09\u613F ).</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u548C\u5BCC\u5C11\u5E74\u4E4B\u95F4\u7684\u5BF9\u8BDD, \u4EE5\u53CA\u968F\u540E\u548C\u95E8\u5F92\u7684\u8BA8\u8BBA, \u90FD\u4E25\u8083\u5730\u9610\u660E\u8D22\u5BCC\u53EF\u80FD\u6210\u4E3A\u95E8\u5F92\u7684\u969C\u7919 ( \u7ECA\u811A\u77F3 ). \u739B\u7AA6\u5723\u53F2\u89E3\u91CA\u4E86 ( \u53C2\u9605: \u8C37\u5341 17 ~ 31; \u8DEF\u5341\u516B 18 ~ 30 ) \u5BCC\u5C11\u5E74\u95EE\u8036\u7A23: \u8981\u5982\u4F55\u884C\u5584, \u624D\u80FD\u627F\u53D7\u6C38\u751F? \u8FD9\u662F\u739B\u7AA6\u5723\u53F2\u6700\u559C\u6B22\u63CF\u5199\u7684\u4E3B\u9898\u4E4B\u4E00 ( \u739B\u4E94 16: \u4E03 17 ~ 19; \u5341\u4E8C 12, 33 ~ 35 ; \u5341\u4E09 23, 24; \u4E8C\u5341\u516D 10 ). \u5C0A\u5B88\u8BEB\u547D\u662F\u884C\u5584\u7684\u7B2C\u4E00\u6B65. \u5BCC\u5C11\u5E74\u95EE &quot;\u4EC0\u4E48\u8BEB\u547D?&quot; \u8FD9\u4E2A\u95EE\u9898, \u542C\u8D77\u6765\u4E0D\u5408\u9002, \u56E0\u4E3A\u6211\u4EEC\u5FC5\u987B\u8981\u9075\u5B88, \u5E76\u540C\u7B49\u5BF9\u5F85\u6240\u6709\u7684\u8BEB\u547D.</p><p>\u8036\u7A23\u4E0D\u53EA\u9080\u8BF7\u5BCC\u5C11\u5E74\u9075\u5B88\u8BEB\u547D\u800C\u5DF2, \u8FD8\u9080\u8BF7\u4ED6\u6210\u4E3A &quot;\u6210\u5168\u7684&quot; ( \u53C2\u9605: \u739B\u4E94 48 ), \u8FD9\u9879\u9080\u60C5\u6240\u5173\u6CE8\u7684, \u662F\u8981\u4EBA\u6210\u4E3A &quot;\u6EE1\u5168\u7684&quot; \u6216 &quot;\u5B8C\u5907\u7684&quot;. \u6B63\u5982\u739B\u4E94 48 \u6240\u793A, \u8FD9\u9879\u9080\u8BF7\u5E76\u4E0D\u662F\u9488\u5BF9\u5C11\u6570\u88AB\u62E3\u9009\u7684\u4EBA, \u4E5F\u4E0D\u662F\u8981\u663E\u73B0\u72B9\u592A\u6559\u548C\u57FA\u7763\u4FE1\u4EF0\u4E4B\u95F4\u7684\u5DEE\u5F02\u6027. \u5728\u65E7\u7EA6\u7ECF\u4E66\u4E2D, \u867D\u7136\u8D22\u5BCC\u88AB\u8BA4\u4E3A\u662F\u8499\u53D7\u5929\u4E3B\u795D\u798F\u7684\u8C61\u5F81 ( \u7533\u4E8C\u5341\u516B 1 ~ 14 ), \u4F46\u4EA6\u662F\u6709\u5173\u4E8E\u8D22\u5B9D\u80FD\u8150\u8680\u4EBA\u5FC3\u7684\u53EF\u6015\u8B66\u544A ( \u5219\u4E03 19; \u4E9A\u516D 4 ~ 8; \u8474\u5341\u4E94 16 ).</p><p>\u7531\u739B\u7AA6\u7684\u89C2\u5360\u770B\u6765, \u4F5C\u4E3A\u8036\u7A23\u7684\u95E8\u5F92, \u5C31\u5F97\u5BF9\u8036\u7A23\u6240\u8BE0\u91CA\u7684\u72B9\u592A\u6CD5\u5F8B\u5177\u6709\u4FE1\u5FB7, \u5176\u4E2D\u5C31\u5305\u542B\u4E86\u5FC5\u987B\u6781\u7AEF\u5730\u4FE1\u8D56\u8036\u7A23. \u5BCC\u4EBA\u8FDB\u5165\u5929\u56FD\u5982\u540C\u9A86\u9A7C\u6324\u8FC7\u9488\u5B54\u4E00\u6837\u56F0\u96BE. ( 24 \u8282 ). \u5F88\u591A\u4EBA\u8BF4 &quot;\u9488\u5B54&quot; \u662F\u8036\u8DEF\u6492\u51B7\u5927\u57CE\u95E8\u4E0A\u7684\u4E00\u6247\u5C0F\u95E8\u7684\u540D\u5B57 ( \u9A86\u9A7C\u5E76\u975E\u7A7F\u4E0D\u8FC7, \u4F46\u662F\u5F88\u56F0\u96BE ), \u8FD9\u662F\u6CA1\u6709\u4F9D\u636E\u7684. \u5BF9\u4E8E\u95E8\u5F92\u7684\u60CA\u8BB6 ( \u540C\u6837\u60C5\u5F62\u53D1\u751F\u5728\u739B\u5341\u4E5D 10 ), \u8036\u7A23\u7684\u56DE\u5E94\u662F\u91CD\u65B0\u628A\u95E8\u5F92\u7684\u6CE8\u610F\u529B, \u96C6\u4E2D\u5728\u5929\u4E3B\u5BF9\u4ED6\u4EEC\u7684\u4E3B\u52A8\u9080\u60C5, \u4EE5\u53CA\u8D50\u4E88\u4ED6\u4EEC\u7684\u80FD\u529B\u4E0A, \u8FD9\u4F7F\u95E8\u5F92\u6709\u80FD\u529B\u884C\u5584, \u8FD9\u4E5F\u5C31\u662F\u5BCC\u5C11\u5E74\u5728\u4E00\u5F00\u59CB\u5C31\u63D0\u51FA\u7684\u95EE\u9898 ( 16 \u8282 )</p><p>\u771F\u798F\u516B\u7AEF\u4E2D\u6709 &quot;\u795E\u8D2B\u7684\u4EBA\u662F\u6709\u798F\u7684&quot; ( \u739B\u4E94 3 ); \u8036\u7A23\u4E5F\u8BF4\u8FC7: &quot;\u4F60\u7684\u8D22\u5B9D\u5728\u90A3\u91CC, \u4F60\u7684\u5FC3\u4E5F\u5FC5\u5728\u90A3\u91CC&quot; ( \u739B\u516D 21 ); \u5E76\u8B66\u544A\u8BF4: \u6CA1\u6709\u4EBA &quot;\u80FD\u4E8B\u5949\u5929\u4E3B\u800C\u53C8\u4E8B\u5949\u94B1\u8D22&quot; ( \u739B\u516D 21 ); \u5929\u4E3B\u7684\u56FD\u6BD4\u8981\u5BFB\u627E\u7684\u73CD\u5B9D\u8D35 ( \u739B\u5341\u4E09 44 ); \u95E8\u5F92\u5F97\u5230\u5929\u4E3B\u8D4F\u62A5\u7684\u4E3B\u9898, \u8D2F\u7A7F\u6574\u90E8\u300A\u798F\u97F3\u300B( \u739B\u4E94 12, 46 ~ 47; \u516D 1 ~ 6; 18; \u5341 39 ~ 42; \u4E8C\u5341\u4E94 21, 23, 34 ). \u8FD9\u91CC\u7684\u91CD\u70B9\u662F\u672B\u4E16, \u56E0\u4E3A\u95E8\u5F92\u5C06\u5206\u4EAB\u5929\u4E3B\u7684\u8363\u8000, \u5E76\u53C2\u4E0E\u4EBA\u5B50\u5728\u672B\u4E16\u65F6\u7684\u5BA1\u5224, \u56E0\u4E3A\u95E8\u5F92\u4E3A\u5929\u4E3B\u56FD\u5EA6\u7684\u81EA\u6211\u5949\u732E, \u5DF2\u4F7F\u4ED6\u4EEC\u5BF9\u9886\u53D7\u5929\u4E3B\u4E3A\u4E16\u4EBA\u6C38\u8FDC\u7EE7\u627F\u7684\u65E8\u610F\u505A\u4E86\u51C6\u5907.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u6062\u590D\u4E86\u5148\u77E5\u7684\u4E00\u4E2A\u539F\u5219: \u8D22\u5BCC\u662F\u901A\u5411\u5929\u56FD\u9014\u4E2D\u7684\u5DE8\u5927\u969C\u788D. \u53EF\u4EE5\u8BF4, \u8FD9\u4E00\u6BB5\u662F\u798F\u97F3\u4E2D\u6700\u80FD\u5F71\u54CD\u57FA\u7763\u5B97\u6559\u5386\u53F2\u7684\u7CBE\u5F69\u7247\u6BB5\u4E4B\u4E00. \u4E3A\u90A3\u4E2A\u5BCC\u5C11\u5E74\u6765\u8BF4, \u505A\u8036\u7A23\u7684\u95E8\u5F92\u6240\u8981\u4ED8\u51FA\u7684\u4EE3\u4EF7\u4F3C\u4E4E\u592A\u5927, \u56E0\u4E3A\u4ED6\u5F88\u5BCC\u6709 (22). \u5176\u5B9E, \u4ED6\u671F\u671B\u8036\u7A23\u544A\u8BC9\u4ED6\u53E6\u5916\u4E00\u4E9B\u4E8B, \u6BD4\u5982\u8981\u6C42\u4ED6\u505A\u4E00\u4E9B\u5584\u4E8B, \u5927\u91CF\u5730\u65BD\u820D\u7B49\u7B49; \u6362\u53E5\u8BDD\u8BF4, \u4ED6\u5E0C\u671B\u5728\u4E0D\u5F71\u54CD\u81EA\u5DF1\u73B0\u6709\u751F\u6D3B\u65B9\u5F0F\u7684\u60C5\u51B5\u4E0B\uFF0C\u8FD0\u7528\u8D22\u5BCC\u505A\u4E9B\u4E8B\u60C5. \u7136\u800C, \u5728\u5929\u4E3B\u9762\u524D, \u5949\u732E\u4E0A\u6240\u6709\u7684\u4E00\u5207, \u5426\u5219\u5C31\u7B49\u4E8E\u6CA1\u6709\u5949\u732E.</p><p>\u8036\u7A23\u8FD9\u4E9B\u8BDD, \u5DF2\u7ECF\u6FC0\u52B1\u4E86\u4E00\u4E9B\u4EBA\u4EE5\u6781\u7AEF\u7684\u65B9\u5F0F\u8DDF\u968F\u7942 ( \u6BD4\u5982\u4E9A\u897F\u897F\u7684\u5723\u65B9\u6D4E\u5404 \u5C06\u81EA\u5DF1\u6240\u6709\u7684\u8D22\u7269\u8005\u9001\u7ED9\u7A77\u4EBA), \u6559\u4EC1\u4E5F\u7531\u8FD9\u4E9B\u8BDD\u5F15\u7533\u51FA &quot;\u798F\u97F3\u529D\u8C15&quot;. \u8FD9\u79CD\u89C2\u8C15\u6709\u522B\u4E8E &quot;\u8BEB\u547D&quot;, \u5E76\u4E0D\u662F\u6BCF\u4E2A\u4EBA\u7684\u4E49\u52A1. \u4F46\u662F, \u8036\u7A23\u5BF9\u4E8E\u8FD9\u4E2A\u5E74\u8F7B\u4EBA\u6240\u8BF4\u7684\u8BDD, \u5374\u4E0D\u662F\u529D\u8C15, \u800C\u662F\u4E2A\u4EBA\u7684, \u4E0D\u53EF\u56DE\u907F\u7684\u547D\u4EE4 (21). \u5F53\u8D22\u7269\u53D8\u6210\u6211\u4EEC\u8DDF\u968F\u8036\u7A23\u548C\u5BFB\u6C42\u5929\u56FD\u7684\u969C\u788D\u65F6, \u5F03\u7EDD\u8D22\u5BCC\u5DF2\u7ECF\u4E0D\u518D\u662F\u529D\u8C15, \u800C\u662F\u7EDD\u5BF9\u5C65\u884C\u7684\u8981\u6C42. \u4E00\u4E2A\u4EBA\u4E0D\u53EF\u4EE5\u540C\u65F6\u5F85\u5949\u5929\u4E3B, \u800C\u53C8\u5F85\u5949\u8D22\u5BCC.</p><p>\u8036\u7A23\u7EE7\u7EED\u8DDF\u95E8\u5F92\u4EEC\u8BB2\u8FF0\u8D22\u5BCC\u963B\u788D\u8FDB\u5165\u5929\u56FD ( 23 - 26 ). \u9A86\u9A7C\u548C\u9488\u5B54\u7684\u56FE\u50CF\u63CF\u8FF0\u662F\u4E2D\u4E1C\u6587\u5316\u7684\u4E00\u79CD\u5938\u5F20\u624B\u6CD5, \u8BF4\u660E\u4E00\u4E2A\u4EBA, \u8981\u629B\u5F03\u8D22\u5BCC\u6240\u5E26\u6765\u7684\u6743\u529B\u548C\u8363\u8A89\u662F\u4F55\u7B49\u7684\u8270\u96BE\u548C\u4E0D\u53EF\u80FD.</p><p>\u8036\u7A23\u5411\u90A3\u4E2A\u5BCC\u5C11\u5E74\u6240\u63D0\u51FA\u7684\u4E24\u4E2A\u8981\u6C42, \u95E8\u5F92\u5DF2\u7ECF\u505A\u5230\u4E86: \u4ED6\u4EEC\u820D\u5F03\u4E00\u5207\u5E76\u8DDF\u968F\u4E86\u7942. \u4E3A\u6B64, \u4ED6\u4EEC\u7684\u56DE\u62A5\u5C06\u662F\u4E00\u4E2A\u5B8C\u6EE1\u7684\u751F\u547D, \u800C\u4E14\u5DF2\u7ECF\u9884\u5148\u5C1D\u5230\u4E86\u8FD9\u751F\u547D\u7684\u5947\u5999. \u8036\u7A23\u7684\u8BB8\u8BFA\u4E5F\u5EF6\u4F38\u81F3\u6240\u6709\u4E3A\u4E86\u7942\u7684\u7F18\u6545\u800C\u820D\u5F03\u4E00\u5207\u7684\u4EBA, \u5177\u4F53\u5730\u8BF4\u5C31\u662F\u6240\u6709\u4FE1\u5F92. \u5C3D\u7BA1\u65E0\u6CD5\u786E\u5B9A\u6BCF\u4E2A\u4EBA\u5728\u5929\u56FD\u7684\u5730\u4F4D, \u4F46\u662F, \u6BCF\u4E2A\u4EBA\u6240\u5F97\u7684\u56DE\u62A5\u80AF\u5B9A\u8FDC\u8FDC\u5927\u4E8E\u81EA\u5DF1\u6240\u60F3\u8C61\u7684 ( 30 ). \u5341\u4E8C\u4E2A\u5149\u8363\u5B9D\u5EA7\u7684\u610F\u601D, \u5C31\u662F\u8981\u6C42\u8BA9\u5F92\u4EEC\u670D\u5F85\u5E76\u57F9\u80B2\u90A3\u4E9B\u5929\u56FD\u7684\u5929\u4E3B\u5B50\u6C11.</p>',46),s=[q];function h(r,d){return t(),o("div",null,s)}var i=u(e,[["render",h],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u4E5D\u7AE0.html.vue"]]);export{i as default};
