import{_ as u,o as a,c as t,e}from"./app.6bf529a6.js";const o={},p=e('<h1 id="\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u516B\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u516B\u7AE0" aria-hidden="true">#</a> \u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u516B\u7AE0</h1><h2 id="\u5929\u56FD\u4E2D\u8C01\u6700\u5927" tabindex="-1"><a class="header-anchor" href="#\u5929\u56FD\u4E2D\u8C01\u6700\u5927" aria-hidden="true">#</a> \u5929\u56FD\u4E2D\u8C01\u6700\u5927</h2><h3 id="\u7ECF\u6587" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>1</sup> \u5C31\u5728\u90A3\u65F6\u523B\uFF0C\u95E8\u5F92\u6765\u5230\u8036\u7A23\u8DDF\u524D\u8BF4\uFF1A\u201C\u5728\u5929\u56FD\u91CC\u7A76\u7ADF\u8C01\u662F\u6700\u5927\u7684\uFF1F\u201D <sup>2</sup> \u8036\u7A23\u5C31\u53EB\u4E00\u4E2A\u5C0F\u5B69\u6765\uFF0C\u4F7F\u4ED6\u7AD9\u5728\u4ED6\u4EEC\u4E2D\u95F4\uFF0C <sup>3</sup> \u8BF4\uFF1A\u201C\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4F60\u4EEC\u82E5\u4E0D\u53D8\u6210\u5982\u540C\u5C0F\u5B69\u4E00\u6837\uFF0C\u4F60\u4EEC\u51B3\u4E0D\u80FD\u8FDB\u5165\u5929\u56FD\u3002 <sup>4</sup> \u6240\u4EE5\uFF0C\u8C01\u82E5\u81EA\u8C26\u81EA\u5351\u5982\u540C\u8FD9\u4E00\u4E2A\u5C0F\u5B69\uFF0C\u8FD9\u4EBA\u5C31\u662F\u5929\u56FD\u4E2D\u6700\u5927\u7684\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4E00\u4E2A\u5C0F\u5B69, \u5929\u56FD\u6D3B\u751F\u751F\u7684\u6BD4\u55BB. \u8BF4\u660E\u4E86\u8FDB\u5165\u6559\u4F1A\u5FC5\u987B\u5177\u6709\u7684\u5FC3\u5883. \u90A3\u5C0F\u5B69\u662F\u57FA\u7763\u7684\u8C61\u5F81. \u662F\u95E8\u5F92\u4EEC\u6240\u63D0\u95EE\u9898\u7684\u7B54\u6848. \u8C01\u82E5 &quot;\u81EA\u8C26\u81EA\u5351\u5982\u540C\u8FD9\u4E00\u4E2A\u5C0F\u5B69&quot;, \u5728\u5929\u56FD\u91CC\u5C31\u662F\u6700\u91CD\u8981\u7684.</p><h2 id="\u6212\u7ACB\u6076\u8868" tabindex="-1"><a class="header-anchor" href="#\u6212\u7ACB\u6076\u8868" aria-hidden="true">#</a> \u6212\u7ACB\u6076\u8868</h2><h3 id="\u7ECF\u6587-1" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-1" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>5</sup> \u201C\u65E0\u8BBA\u8C01\u56E0\u6211\u7684\u540D\u5B57\uFF0C\u6536\u7559\u4E00\u4E2A\u8FD9\u6837\u7684\u5C0F\u5B69\uFF0C\u5C31\u662F\u6536\u7559\u6211\uFF1B <sup>6</sup> \u4F46\u65E0\u8BBA\u8C01\uFF0C\u4F7F\u8FD9\u4E9B\u4FE1\u6211\u7684\u5C0F\u5B50\u4E2D\u7684\u4E00\u4E2A\u8DCC\u5012\uFF0C\u5012\u4E0D\u5982\u62FF\u4E00\u5757\u9A74\u62C9\u7684\u78E8\u77F3\uFF0C\u7CFB\u5728\u4ED6\u7684\u9888\u4E0A\uFF0C\u6C89\u5728\u6D77\u7684\u6DF1\u5904\u66F4\u597D\u3002 <sup>7</sup> \u4E16\u754C\u56E0\u4E86\u6076\u8868\u662F\u6709\u7978\u7684\uFF0C\u6076\u8868\u56FA\u7136\u514D\u4E0D\u4E86\u8981\u6765\uFF0C\u4F46\u7ACB\u6076\u8868\u7684\u90A3\u4EBA\u662F\u6709\u7978\u7684\u3002 <sup>8</sup> \u4E3A\u6B64\uFF0C\u5018\u82E5\u4F60\u7684\u624B\uFF0C\u6216\u4F60\u7684\u811A\u4F7F\u4F60\u8DCC\u5012\uFF0C\u780D\u4E0B\u5B83\u6765\uFF0C\u4ECE\u4F60\u8EAB\u4E0A\u6254\u6389\uFF0C\u4E3A\u4F60\u6216\u6B8B\u6216\u7638\u8FDB\u5165\u751F\u547D\uFF0C\u6BD4\u6709\u53CC\u624B\u53CC\u811A\uFF0C\u800C\u88AB\u6295\u5165\u6C38\u706B\u4E2D\u66F4\u597D\u3002 <sup>9</sup> \u5018\u82E5\u4F60\u7684\u773C\u4F7F\u4F60\u8DCC\u5012\uFF0C\u525C\u51FA\u5B83\u6765\uFF0C\u4ECE\u4F60\u8EAB\u4E0A\u6254\u6389\uFF0C\u4E3A\u4F60\u6709\u4E00\u53EA\u773C\u8FDB\u5165\u751F\u547D\uFF0C\u6BD4\u6709\u53CC\u773C\u800C\u88AB\u6295\u5165\u6C38\u706B\u4E2D\u66F4\u597D\u3002 <sup>10</sup> \u4F60\u4EEC\u5C0F\u5FC3\uFF0C\u4E0D\u8981\u8F7B\u89C6\u8FD9\u4E9B\u5C0F\u5B50\u4E2D\u7684\u4E00\u4E2A\uFF0C\u56E0\u4E3A\u6211\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4ED6\u4EEC\u7684\u5929\u4F7F\u5728\u5929\u4E0A\uFF0C\u5E38\u89C1\u6211\u5728\u5929\u4E4B\u7236\u7684\u9762\u3002<sup>11</sup> \u3010\u56E0\u4E3A\u4EBA\u5B50\u6765\u662F\u4E3A\u6551\u90A3\u4E27\u4EA1\u4E86\u7684\u3002\u3011</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8036\u7A23\u5F53\u4F17\u6307\u8D23\u7ED9 &quot;\u8FD9\u4E9B\u5C0F\u5B50&quot; ( \u5B69\u7AE5\u6210\u4E3A\u5355\u7EAF\u4FE1\u53CB\u7684\u8C61\u5F81 ) \u7ACB\u6076\u8868\u8FD9\u4EF6\u4E8B. \u5E76\u4E25\u91CD\u8B66\u544A\u8FD9\u4E9B\u4EBA, \u7ACB\u6076\u8868\u7684\u4EBA\u5C06\u53D7\u5230\u4E25\u60E9.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u739B\u7AA6\u5728\u8FD9\u4E00\u7AE0\u8BB0\u8F7D\u4E86\u8036\u7A23\u4E94\u7BC7\u91CD\u8981\u8BB2\u9053\u4E2D\u7684\u7B2C\u56DB\u7BC7\uFF0C\u662F\u5BF9\u56E2\u4F53\u7684\u8BAD\u5BFC\u3002\u6211\u4EEC\u53EF\u4EE5\u731C\u60F3\u5230\uFF0C\u4E0D\u540C\u7684\u5C0F\u56E2\u4F53\u540C\u4F4F\u5728\u4E00\u8D77\u7684\u95EE\u9898\u4E0E\u56F0\u96BE\uFF0C\u4EE5\u53CA\u56E0\u6B64\u800C\u4EA7\u751F\u7684\u7D27\u5F20\u6C14\u6C1B\u3002\u739B\u7AA6\u4E3A\u8BF4\u660E\u8FD9\u79CD\u60C5\u51B5\uFF0C\u529D\u544A\u6211\u4EEC\uFF0C\u7167\u987E\u5F31\u5C0F\u8005\u5E76\u5F7C\u6B64\u5BBD\u6055\u5E94\u8BE5\u662F\u57FA\u7763\u5F92\u56E2\u4F53\u7684\u57FA\u672C\u51C6\u5219:\u8FD9\u79CD\u56E2\u4F53\u662F\u4EFB\u4F55\u65F6\u4EE3\u56E2\u4F53\u7684\u6A21\u8303\u3002 \u5929\u56FD\u7684\u6765\u4E34\u5C31\u4F1A\u5E26\u6765\u8FD9\u79CD\u4EF7\u503C\u89C2\u7684\u6539\u53D8\u3002 \u95E8\u5F92\u4EEC\u7684\u95EE\u9898\u5F15\u51FA\u4E86\u8FD9\u7BC7\u8BB2\u9053\u3002 \u6559\u4F1A\u65E2\u7136\u5FC5\u987B\u7EC4\u7EC7\u8D77\u6765\uFF0C\u4FBF\u9700\u8981\u4E00\u4E9B\u4EBA\u627F\u62C5\u67D0\u4E9B\u8D23\u4EFB\u548C\u4E49\u52A1\uFF0C\u8FD9\u4E9B\u4EBA\u662F\u4E0D\u662F\u6700\u91CD\u8981\u7684\u4EBA\u7269\u5462?\u95E8\u5F92\u4EEC\u7279\u522B\u60F3\u77E5\u9053\u7B54\u6848\u3002 \u7136\u800C\uFF0C\u739B\u7AA6\u5374\u63D0\u9192\u4ED6\u4EEC\uFF0C\u4E0D\u8981\u5FD8\u8BB0\u8036\u7A23\u501F\u6B64\u4E8B\u6240\u6559\u8BAD\u7684\uFF0C\u5929\u56EF\u7684\u6765\u4E34\u5FC5\u7136\u8981\u5E26\u6765\u4E00\u79CD\u4EF7\u503C\u89C2\u4E0A\u7684\u6539\u53D8\u3002\u8036\u7A23\u62B1\u8D77\u90A3\u4E2A\u5B69\u7AE5\u5DF2\u7ECF\u662F\u4E00\u4E2A\u7B54\u6848 .\u540C\u65F6\u8981\u6C42\u888D\u7684\u95E8\u5F92\u4EEC\u4E5F\u5E94\u8BE5\u50CF\u5B69\u7AE5\u4E00\u6837 \u3002 \u5F53\u65F6\u7684\u793E\u4F1A\u4E0E\u4ECA\u65E5\u7684\u793E\u4F1A\u5927\u4E0D\u76F8\u540C\uFF0C\u8036\u7A23\u65F6\u4EE3\u7684\u5B69\u7AE5\u5728\u793E\u4F1A\u4E2D\u6CA1\u6709\u6CD5\u5B9A\u7684\u6743\u5229\uFF0C\u800C\u4E14\uFF0C\u4ED6\u4EEC\u53EA\u80FD\u4F9D\u8D56\u81EA\u5DF1\u7684\u7236\u6BCD\uFF0C \u4ECE\u7236\u6BCD\u63A5\u53D7\u4E00\u5207\u9988\u8D60\u3002 \u5929\u4E3B\u7684\u56FD\u4E0D\u80FD\u9760\u4E00\u4E2A\u4EBA\u7684\u80FD\u529B\u83B7\u5F97;\u5929\u56FD\u662F\u4E00\u4EF6\u793C\u7269\uFF0C\u53EA\u6709\u51FA\u81EA\u5B69\u7AE5\u822C\u7684\u5355\u7EAF\u548C\u611F\u6FC0\uFF0C\u624D\u80FD\u63A5\u53D7\u8FD9\u4EFD\u793C\u7269\u3002 \u798F\u97F3\u4E2D\u7684&quot;\u5C0F&quot;\u5B57\u4E0D\u4EC5\u6307\u5B69\u7AE5\u4E5F\u6307\u6240\u6709\u8BDA\u6734\u548C\u8C26\u5351\u7684\u4EBA\u3002 \u4ED6\u4EEC\u5728\u7B80\u6734\u7684\u751F\u6D3B\u4E2D\uFF0C\u9009\u62E9\u5168\u5FC3\u5168\u610F\u5730\u8DDF\u968F\u8036\u7A23\u3002\u57FA\u7763\u5F92\u56E2\u4F53\uFF0C\u7279\u8D23\u4EBA\uFF0C\u5207\u83AB\u4F4E\u4F30\u8FD9\u4E9B\u4EBA\u5728\u56E2\u4F53\u4E2D\u7684\u4F5C\u7528\u548C\u8D21\u732E\u3002\u8FD9\u4E9B&quot;\u5C0F&quot;\u5F1F\u5144\u4EEC\uFF0C\u5177\u4F53\u5730\u5B9E\u73B0\u4E86\u798F\u97F3\u7684\u7CBE\u795E\uFF0C\u8BA9\u8036\u7A23\u5728\u8D2B\u7A77\u8005\u548C\u7B80\u5355\u7684\u4EBA\u4E2D\u7684\u4E34\u5728\u5F70\u663E\u65E0\u9057\u3002</p><h2 id="\u4EA1\u7F8A\u55BB" tabindex="-1"><a class="header-anchor" href="#\u4EA1\u7F8A\u55BB" aria-hidden="true">#</a> \u4EA1\u7F8A\u55BB</h2><h3 id="\u7ECF\u6587-2" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-2" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>12</sup> \u201C\u4F60\u4EEC\u4EE5\u4E3A\u5982\u4F55\uFF1F\u5982\u679C\u4E00\u4E2A\u4EBA\u6709\u4E00\u767E\u53EA\u7F8A\uFF0C\u5176\u4E2D\u4E00\u53EA\u8FF7\u5931\u4E86\u8DEF\uFF0C\u4ED6\u5C82\u4E0D\u628A\u90A3\u4E5D\u5341\u4E5D\u53EA\u7559\u5728\u5C71\u4E0A\uFF0C\u800C\u53BB\u5BFB\u627E\u90A3\u53EA\u8FF7\u5931\u4E86\u8DEF\u7684\u5417\uFF1F <sup>13</sup> \u5982\u679C\u4ED6\u5E78\u8FD0\u627E\u7740\u4E86\uFF0C\u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u4ED6\u4E3A\u8FD9\u4E00\u53EA\uFF0C\u6BD4\u4E3A\u90A3\u4E5D\u5341\u4E5D\u53EA\u6CA1\u6709\u8FF7\u8DEF\u7684\uFF0C\u66F4\u89C9\u6B22\u559C\uFF1B <sup>14</sup> \u540C\u6837\uFF0C\u4F7F\u8FD9\u4E9B\u5C0F\u5B50\u4E2D\u7684\u4E00\u4E2A\u4E27\u4EA1\uFF0C\u51B3\u4E0D\u662F\u4F60\u4EEC\u5728\u5929\u4E4B\u7236\u7684\u610F\u613F\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E9B &quot;\u5C0F\u5B50&quot; \u5BF9\u8036\u7A23\u800C\u8A00\u662F\u76F8\u5F53\u91CD\u8981\u7684. \u8FD9\u4E2A\u6BD4\u55BB\u5C55\u793A\u5929\u4E3B\u62EF\u6551\u666E\u4E16\u7684\u610F\u613F. \u5E76\u4E14\u6697\u793A\u8036\u7A23\u4E3A &quot;\u5584\u7267&quot; \u7684\u4EFB\u52A1. \u7B2C\u5341\u4E00\u8282\u53EF\u80FD\u662F\u4ECE\u8DEF\u5341\u4E5D 10 \u501F\u7528\u6765\u7684\u6CE8\u89E3.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u7684\u56DB\u5927\u8A00\u8BBA\u96C6, \u5173\u6CE8\u7684\u6559\u4F1A\u56E2\u4F53\u751F\u6D3B\u548C\u79E9\u5E8F. \u7B2C\u4E00\u5927\u6BB5\u5F3A\u8C03\u5E94\u8BE5\u8C26\u5351, \u5E76\u8981\u5173\u5FC3\u5F31\u52BF\u7FA4\u4F53\u7684\u9700\u8981 ( 1 ~ 14 \u8282 ); \u7B2C\u4E8C\u5927\u6BB5\u662F\u5411\u56E2\u4F53\u4E2D\u6124\u6124\u4E0D\u5E73\u7684\u6210\u5458\u8BF4\u660E\u548C\u89E3\u7684\u6B65\u805A\u548C\u8FC7\u7A0B ( 15 ~ 20 \u8282 ); \u7D27\u63A5\u7740\u7684\u6700\u540E\u4E00\u5927\u6BB5, \u662F\u4E00\u5219 &quot;\u65E0\u9650\u7684\u5BBD\u6055&quot; \u7684\u6BD4\u55BB ( \u7B2C 21 ~ 35 \u8282 ). \u8FD9\u4E9B\u8A00\u8BBA\u662F\u5411\u95E8\u5F92\u6240\u8BF4 ( 1 \u8282 ), \u4F46\u6559\u5BFC\u7684\u6027\u8D28\u5374\u662F\u9488\u5BF9\u90A3\u4E9B\u5728\u6559\u4F1A\u4E2D\u8D1F\u6709\u9886\u5BFC\u8D23\u4EFB\u7684\u4EBA, \u800C\u4E0D\u662F\u9488\u5BF9 &quot;\u5C0F\u5B69\u5B50&quot;.</p><p>\u7B2C\u4E00\u5927\u6BB5\u7684\u7B2C\u4E00\u90E8\u5206 ( 1 ~ 5 \u8282 ), \u662F\u8036\u7A23\u6559\u5BFC\u56E2\u4F53\u9886\u5BFC\u4EBA\u5E94\u8BE5\u6709\u610F\u8BC6\u7684, \u628A\u81EA\u5DF1\u89C6\u4E3A\u56E2\u4F53\u662F\u6700\u5351\u5FAE\u7684\u4EBA, \u5982\u6B64\u57F9\u517B\u8C26\u5351\u4E4B\u5FB7. &quot;\u5C0F\u5B69\u5B50&quot; \u5728\u5BB6\u5EAD\u4E2D\u53D7\u5230\u7406\u6240\u5F53\u7136\u7684\u91CD\u89C6, \u4F46\u4ED6\u4EEC\u5374\u662F\u6700\u5F31\u52BF\u7684\u7FA4\u4F53, \u800C\u4E14\u6CA1\u6709\u80FD\u529B\u77E3\u5E2E\u52A9\u5BB6\u5EAD\u7684\u751F\u8BA1, \u81F3\u5C11\u8981\u7B49\u5230\u4ED6\u4EEC\u957F\u5927\u540E. \u7B2C\u4E8C\u4E2A\u64CD\u7EC3\u8C26\u5351\u7684\u65B9\u6CD5, \u5C31\u662F\u76DB\u60C5\u6B3E\u5F85\u90A3\u4E9B &quot;\u65E0\u8DB3\u8F7B\u91CD\u4E4B\u4EBA&quot; \u6216 &quot;\u5C0F\u5B69\u5B50&quot;. \u8981\u65E0\u5FAE\u4E0D\u81F3\u7684\u5173\u5FC3\u7167\u987E\u4ED6\u4EEC, \u5982\u540C\u6B3E\u5F85\u91CD\u8981\u5BA2\u4EBA\u65F6\u7684\u6BB7\u52E4\u5468\u9053\u53CA\u65E0\u79C1, \u8FD9\u4E9B\u90FD\u662F\u4F5C\u4E3A\u4E00\u4E2A\u771F\u6B63\u9886\u8896\u5E94\u6709\u7684\u98CE\u8303.</p><p>\u6700\u540E, \u56E2\u961F\u9886\u5BFC\u4EBA\u8981\u5C0F\u5FC3\u7ECA\u811A\u77F3\u6321\u4F4F &quot;\u5C0F\u5B69\u5B50&quot; \u7684\u8DEF ( 6 ~ 9 \u8282 ). \u8FD9\u6837\u7684\u540E\u679C\u662F\u53EF\u6015\u7684, \u739B\u7AA6\u5723\u53F2\u6CA1\u6709\u8BB2\u6E05\u695A\u8C01\u662F\u5C0F\u5B69\u5B50. &quot;\u5C0F\u5B69\u5B50&quot; \u53EF\u4EE5\u662F\u65B0\u7684\u7688\u4F9D\u8005, \u4E5F\u53EF\u80FD\u662F\u6307\u90A3\u4E9B\u4FE1\u5FB7\u5C1A\u672A\u575A\u5B9A\u7684\u4EBA, \u65E0\u8BBA\u5982\u4F55, \u4ED6\u4EEC\u90FD\u662F\u8036\u7A23\u7684 &quot;\u95E8\u5F92&quot;, \u57FA\u7763\u798F\u97F3\u7684\u5BA3\u8BB2\u8005. \u4E00\u4E2A\u4EBA\u5982\u4F55\u5BF9\u5F85 &quot;\u6700\u5C0F\u7684&quot;, \u5C06\u6210\u4E3A\u672B\u65E5\u5BA1\u5224\u65F6\u8D4F\u62A5\u548C\u60E9\u7F5A\u7684\u4F9D\u636E. ( \u739B\u4E8C\u5341\u4E94 40, 45 )</p><p>\u5723\u53F2\u6700\u540E\u4EE5 &quot;\u4EA1\u7F8A\u7684\u6BD4\u55BB&quot; \u6765\u8BF4\u660E, \u6BCF\u4E00\u4E2A\u5C0F\u5B50\u90FD\u5E94\u8BE5\u88AB\u91CD\u89C6. \u7267\u7F8A\u4EBA\u8981\u5C3D\u4E00\u5207\u6240\u80FD\u5BFB\u56DE\u8FF7\u5931\u7684\u7F8A ( 10 ~ 14 \u8282 ). \u57FA\u7763\u5F92\u7684\u9886\u8896\u5E94\u8BE5\u6548\u6CD5\u5929\u4E3B\u5BF9\u4EE5\u8272\u5217\u7684\u7737\u987E (\u548F\u4E8C\u5341\u4E09, \u4F9D\u56DB\u5341 11). \u8FD8\u8981\u6548\u6CD5\u8036\u7A23\u601C\u60AF\u90A3\u4E9B\u5982\u540C &quot;\u6CA1\u6709\u7267\u4EBA\u7684\u7F8A&quot; ( \u739B\u4E5D 36 ) \u7684\u4EBA. \u7267\u7F8A\u4EBA\u4E0D\u5E94\u662F \u300A\u523B\u5219\u5384\u8033\u5148\u77E5\u4E66\u300B\u6240\u8B74\u8D23\u7684, \u628A\u81EA\u5DF1\u7684\u5229\u76CA\u653E\u5728\u7F8A\u7FA4\u4E4B\u4E0A\u7684\u7267\u7F8A\u4EBA ( \u5219\u4E09\u5341\u56DB 1 ~ 4 ). \u8FD9\u91CC\u6240\u6307\u7684\u7267\u7F8A\u4EBA, \u662F\u8981\u5BFB\u627E &quot;\u4EE5\u8272\u5217\u5BB6\u8FF7\u5931\u7684\u7F8A&quot; ( \u739B\u5341 6 ). \u739B\u7AA6\u5723\u53F2\u7684\u8FD9\u4E2A\u6BD4\u55BB, \u4E0D\u662F\u5F3A\u8C03\u7F8A\u7684\u6094\u6539 ( \u53C2\u89C1: \u8DEF\u5341\u4E94 7 ), \u800C\u662F\u5F3A\u8C03\u7267\u7F8A\u4EBA\u7684\u7D27\u8FEB\u4EFB\u52A1\u662F\u9075\u5FAA\u5929\u4E3B\u7684\u65E8\u610F, \u5E76\u4E14\u7267\u7F8A\u4EBA\u5728\u627E\u5230\u4EA1\u7F8A\u4E4B\u540E, \u80FD\u4F53\u9A8C\u5230\u6781\u5927\u7684\u6B22\u559C ( 13 ~ 14 \u8282 ).</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u6BB5\u53D9\u8FF0\u7684\u4E3B\u89D2\u5176\u5B9E\u662F\u90A3\u4E2A\u51FA\u53BB\u5BFB\u7F8A\u7684\u7267\u4EBA\u3002\u8036\u7A23\u6240\u7ECF\u9A8C\u5230\u7684\u5929\u7236\u4E0D\u662F\u4E00\u4F4D\u6709\u504F\u89C1\u548C\u79C1\u5FC3\u7684\u5929\u4E3B\u3002\u8036\u7A23\u77E5\u9053\uFF0C\u5929\u4E3B\u4E4B\u6240\u4EE5\u4E3A\u7236\u4EB2\uFF0C\u662F\u56E0\u4E3A\u7942\u51FA\u53BB\u5BFB\u627E\u8FF7\u5931\u8005\uFF0C\u7231\u62A4\u5904\u4E8E\u6076\u52A3\u73AF\u5883\u4E2D\u7684\u4EBA\u3002\u4E3A\u4E86\u627E\u5230\u90A3\u53EA\u8FF7\u5931\u7684\u7F8A\uFF0C\u7942\u7ADF\u7136\u5C06\u53E6\u5916\u7684\u4E5D\u5341\u4E5D\u53EA\u7559\u5728\u4E00\u8FB9: \u627E\u5230\u4EE5\u540E\uFF0C\u4FBF\u5C06\u5B83\u80CC\u5728\u81EA\u5DF1\u7684\u80A9\u4E0A\uFF0C\u548C\u5176\u4ED6\u4EBA\u5206\u4EAB\u627E\u5230\u7F8A\u7684\u559C\u4E50\u3002\u8FD9\u96BE\u9053\u4E0D\u8DB3\u4EE5\u8BF4\u660E\u5929\u4E3B\u5C31\u662F\u4E00\u4F4D\u771F\u6B63\u7684\u7236\u4EB2\u5417?\u5F53\u7942\u7231\u4E00\u4E2A\u8FF7\u5931\u8005\u65F6\uFF0C\u5E76\u6CA1\u6709\u505C\u6B62\u7231\u5176\u4ED6\u4EBA;\u7942\u53EA\u4E0D\u8FC7\u662F\u5728\u4FDD\u8BC1\uFF0C\u5373\u4F7F\u4ED6\u4EEC\u53EF\u80FD\u4F1A\u8FF7\u5931\uFF0C\u4E5F\u4E0D\u4F1A\u5931\u53BB\u7942\u7684\u7231\u3002</p><p>\u739B\u7AA6\u5C06\u8FD9\u4E2A\u6BD4\u55BB\u5E94\u7528\u5230\u90A3\u4E9B\u53D7\u6B3A\u9A97\u6216\u8BF1\u60D1\u800C\u8BEF\u5165\u6B67\u9014\uFF0C\u4E14\u79BB\u5F03\u8036\u7A23\u6559\u8BAD\u7684\u95E8\u5F92\u4EEC\u8EAB\u4E0A\u3002\u4EBA\u4EEC\u5E94\u8BE5\u91C7\u53D6\u7267\u4EBA\u5BFB\u7F8A\u7684\u884C\u52A8\uFF0C\u5BF9\u5F85\u90A3\u4E9B\u5DF2\u7ECF\u8DCC\u5012\u548C\u5C06\u8981\u8DCC\u5012\u7684\u4EBA\u3002\u8FD9\u4E9B\u5728\u751F\u6D3B\u4E2D\u8FF7\u5931\u7684\u4EBA\uFF0C\u9700\u8981\u522B\u4EBA\u7684\u73CD\u60DC\u548C\u5F15\u5BFC\uFF0C\u624D\u4E0D\u81F4\u5954\u5411\u6B7B\u4EA1\u3002\u5929\u4E3B\u7EDD\u5BF9\u4E0D\u4F1A\u653E\u5F03\u4EFB\u4F55\u8FF7\u5931\u7684\u4EBA\uFF0C\u7942\u6C38\u8FDC\u8010\u5FC3\u5730\u7B49\u5F85\u5E76\u5BFB\u627E\u6211\u4EEC\u3002</p><p>\u8054\u7CFB\u4E0A\u4E0B\u6587\uFF0C\u6211\u4EEC\u770B\u5230\uFF0C\u8FD9\u4E2A\u6BD4\u55BB\u7279\u522B\u5F3A\u8C03\u6BCF\u4E00\u4E2A\u4EBA\u7684\u7EDD\u5BF9\u4EF7\u503C\uFF0C \u540C\u65F6\uFF0C\u4E5F\u663E\u793A\u51FA\uFF0C\u7531\u4E8E\u5929\u4E3B\u662F\u4F17\u4EBA\u7684\u7236\u4EB2\uFF0C\u624D\u5EFA\u7ACB\u8D77\u57FA\u7763\u5F92\u4E2D\u95F4\u7684\u5144\u5F1F\u59D0\u59B9\u5173\u7CFB\u3002\u5F53\u4ED6\u4EEC\u80FD\u591F\u51FA\u53BB\u5BFB\u627E\u8FF7\u5931\u7684\u5144\u5F1F\u59D0\u59B9\u65F6\uFF0C\u4ED6\u4EEC\u5C31\u5B8C\u6210\u4E86\u5929\u7236\u7684\u65E8\u610F&quot;\u4F60\u4EEC\u5929\u4E0A\u7684\u7236\u4E5F\u4E0D\u613F\u610F\u8FD9\u4E9B\u5C0F\u5B69\u4E27\u5931\u4EFB\u4F55\u4E00\u4E2A&quot; \u3002</p><h2 id="\u5144\u5F1F\u89C4\u529D\u4E4B\u9053" tabindex="-1"><a class="header-anchor" href="#\u5144\u5F1F\u89C4\u529D\u4E4B\u9053" aria-hidden="true">#</a> \u5144\u5F1F\u89C4\u529D\u4E4B\u9053</h2><blockquote><p><sup>15</sup> \u201C\u5982\u679C\u4F60\u7684\u5F1F\u5144\u5F97\u7F6A\u4E86\u4F60\uFF0C\u53BB\uFF0C\u8981\u5728\u4F60\u548C\u4ED6\u72EC\u5904\u7684\u65F6\u5019\uFF0C\u89C4\u529D\u4ED6\uFF1B\u5982\u679C\u4ED6\u542C\u4ECE\u4E86\u4F60\uFF0C\u4F60\u4FBF\u8D5A\u5F97\u4E86\u4F60\u7684\u5144\u5F1F\uFF1B <sup>16</sup> \u4F46\u4ED6\u5982\u679C\u4E0D\u542C\uFF0C\u4F60\u5C31\u53E6\u5E26\u4E0A\u4E00\u4E2A\u6216\u4E24\u4E2A\u4EBA\uFF0C\u4E3A\u53EB\u4EFB\u4F55\u4E8B\u60C5\uFF0C\u51ED\u4E24\u4E2A\u6216\u4E09\u4E2A\u89C1\u8BC1\u4EBA\u7684\u53E3\u4F9B\uFF0C\u5F97\u4EE5\u6210\u7ACB\u3002 <sup>17</sup> \u82E5\u662F\u4ED6\u4ECD\u4E0D\u542C\u4ECE\u4ED6\u4EEC\uFF0C\u4F60\u8981\u544A\u8BC9\u6559\u4F1A\uFF1B\u5982\u679C\u4ED6\u8FDE\u6559\u4F1A\u4E5F\u4E0D\u542C\u4ECE\uFF0C\u4F60\u5C31\u5C06\u4ED6\u770B\u4F5C\u5916\u6559\u4EBA\u6216\u7A0E\u540F\u3002<sup>18</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u51E1\u4F60\u4EEC\u5728\u5730\u4E0A\u6240\u675F\u7F1A\u7684\uFF0C\u5728\u5929\u4E0A\u4E5F\u8981\u88AB\u675F\u7F1A\uFF1B\u51E1\u4F60\u4EEC\u5728\u5730\u4E0A\u6240\u91CA\u653E\u7684\uFF0C\u5728\u5929\u4E0A\u4E5F\u8981\u88AB\u91CA\u653E\u3002<sup>19</sup> \u6211\u5B9E\u5728\u544A\u8BC9\u4F60\u4EEC\uFF1A\u82E5\u4F60\u4EEC\u4E2D\u4E8C\u4EBA\uFF0C\u5728\u5730\u4E0A\u540C\u5FC3\u5408\u610F\uFF0C\u65E0\u8BBA\u4E3A\u4EC0\u4E48\u4E8B\u7948\u7977\uFF0C\u6211\u5728\u5929\u4E4B\u7236\uFF0C\u5FC5\u8981\u7ED9\u4ED6\u4EEC\u6210\u5C31\uFF0C<sup>20</sup> \u56E0\u4E3A\u90A3\u91CC\u6709\u4E24\u4E2A\u6216\u4E09\u4E2A\u4EBA\uFF0C\u56E0\u6211\u7684\u540D\u5B57\u805A\u5728\u4E00\u8D77\uFF0C\u6211\u5C31\u5728\u4ED6\u4EEC\u4E2D\u95F4\u3002\u201D</p></blockquote><h3 id="\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u65B0\u7EA6\u5BFC\u8BFB\u7CFB\u5217 -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E00\u6BB5\u6587\u5B57, \u7C7B\u4F3C\u53E4\u6728\u5170\u7684\u5B89\u8272\u5C3C\u56E2\u4F53\u7684\u6212\u5F8B. \u63D0\u4F9B\u4E86\u5144\u5F1F\u89C4\u529D\u4E4B\u9053. \u5B83\u4F3C\u4E4E\u4E8E\u7F6A\u7684\u8D66\u514D\u6709\u5173. \u6CE8\u610F\u8FD9\u8282\u91CC\u7684 &quot;\u5F97\u7F6A\u4F60&quot;, \u539F\u6587\u53EF\u80FD\u5E76\u975E\u5982\u6B64. \u65E9\u5148\u8036\u7A23\u66FE\u58F0\u660E, &quot;\u5728\u5730\u4E0A\u6709\u8D66\u7F6A\u7684\u6743\u67C4.&quot; \u798F\u97F3\u4F5C\u8005\u5728\u90A3\u6BB5\u6587\u5B57\u4E4B\u540E\u52A0\u5165\u7684\u8BC4\u8BBA\u4E5F\u9887\u5BCC\u542B\u610F -- \u5728\u65C1\u89C2\u770B\u7684\u7FA4\u4F17\u9042\u90FD\u8D5E\u9882\u5929\u4E3B, &quot;\u56E0\u4E3A\u4ED6\u7ED9\u4E86\u4EBA\u7C7B\u8FD9\u6837\u5927\u7684\u6743\u67C4&quot;. \u90A3\u6BB5\u6587\u5B57\u663E\u793A\u739B\u7AA6\u627F\u8BA4\u8036\u7A23\u628A\u8FD9\u79CD\u6743\u67C4\u8D50\u7ED9\u4E86\u6559\u4F1A. ekklesia \u4E00\u5B57 ( \u6B64\u5730\u53EF\u80FD\u6307\u5730\u65B9\u6027\u7684\u6559\u4F1A\u56E2\u4F53 ); \u4E0D\u662F\u666E\u904D\u7684\u7ED9\u4E88\u6574\u4E2A\u6559\u4F1A, \u800C\u4E5F\u4E3A\u5404\u5730\u533A\u7684\u6559\u4F1A\u6240\u62E5\u6709. \u4F20\u7EDF\u4E0A, \u5929\u4E3B\u516C\u6559\u7ECF\u7531\u544A\u89E3\u5723\u4E8B\u5DF2\u7ECF\u884C\u4F7F\u4E86\u8FD9\u4E2A\u6765\u81EA\u5929\u4E3B\u7684\u6743\u529B. &quot;\u675F\u7F1A&quot; \u548C &quot;\u91CA\u653E&quot; \u662F\u4E00\u79CD\u7ECF\u5E08\u6587\u5B66\u7684\u8868\u8FBE\u6CD5. \u8C61\u5F81\u7740\u8FD9\u79CD\u5224\u51B3\u6743\u7684\u5B9E\u884C: \u8FD9\u4E24\u4E2A\u540D\u8BCD\u5E94\u8BE5\u5F53\u505A\u4E00\u4E2A\u6574\u4F53\u770B\u5F85. \u6709\u4EBA\u95EE, \u4E0E &quot;\u91CA\u653E&quot; \u76F8\u5BF9\u7684 &quot;\u675F\u7F1A&quot; \u6307\u7684\u662F\u4EC0\u4E48? \u4ED6\u5C31\u8BEF\u89E3\u4E86\u8FD9\u4E2A\u6BD4\u55BB -- \u6559\u4F1A\u5BF9\u7F6A\u7684\u6743\u529B\u548C\u8C61\u5F81, \u4E5F\u5C31\u662F\u6559\u4F1A\u80FD\u591F\u5B8C\u5168\u8D66\u514D\u7F6A\u7F5A.</p><p>\u56E2\u4F53\u6700\u9002\u5408\u7684\u7948\u7977, \u5C31\u662F\u793C\u4EEA\u7684\u7948\u7977. \u751A\u4E3A\u5929\u4E3B\u6240\u559C\u7231, \u800C\u4E14\u4F3C\u4E4E\u6BD4\u4E2A\u4EBA\u7948\u7977\u66F4\u5BB9\u6613\u4E3A\u5929\u4E3B\u6240\u60A6\u7EB3; \u8036\u7A23, \u6211\u4EEC\u7684\u5384\u9A6C\u5974\u8033, \u5141\u8BFA\u4ED6\u5C06\u4EE5\u4E00\u79CD\u7279\u6B8A\u7684\u4E34\u5728\u53C2\u4E0E\u6211\u4EEC\u7684\u793C\u4EEA, \u7948\u7977. \u4E0D\u8FC7\u503C\u5F97\u91CD\u89C6\u7684\u662F\u8FD9\u51E0\u8282\u53EA\u6709\u739B\u7AA6\u798F\u97F3\u6709\u6240\u8BB0\u8F7D.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-1" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u8FD9\u4E00\u6BB5\u4ECB\u53EC\u4E86\u5728\u6559\u4F1A\u56E2\u4F53\u4E2D\uFF0C \u5F53\u4E00\u4E2A\u4EBA\u5F97\u7F6A\u4E86\u53E6\u4E00\u4E2A\u4EBA\u65F6, \u5E94\u91C7\u53D6\u7684\u6001\u5EA6, \u9996\u5148\u662F\u9762\u5BF9\u9762\u7684\u89C4\u529D, \u5148\u7531\u88AB\u5F97\u7F6A\u7684\u4E00\u65B9 ( 15 \u8282 ) \u7528\u4E00\u9897\u613F\u610F\u5BBD\u6055\u7684\u5FC3\u53BB\u63A5\u7EB3\u53E6\u4E00\u65B9. \u6700\u4F73\u4E4B\u60C5\u51B5\u662F\u4E00\u5F00\u59CB\u9762\u5BF9\u9762\u89C4\u529D, \u5C31\u4F5C\u5230\u4E86\u4F7F\u4ED6\u6094\u609F\u5E76\u548C\u89E3. \u5982\u679C\u6CA1\u6709\u6210\u529F, \u4E0B\u4E00\u6B65\u5C31\u662F\u8BA9\u6559\u4F1A\u56E2\u4F53\u4E2D\u7684\u4E00\u81F3\u4E24\u4E2A\u6210\u5458\u4ECB\u5165 ( 16 \u8282 ), \u76EE\u7684\u662F\u4F9D\u9760\u516C\u5E73\u7684\u8BC1\u4EBA\u6216\u8C03\u89E3\u5458\u8BC1\u5B9E\u4E8B\u60C5\u7684\u771F\u76F8. \u5982\u679C\u8FD8\u4E0D\u884C, \u53EA\u597D\u628A\u4E8B\u60C5\u5E26\u5230\u6574\u4E2A\u6559\u4F1A\u56E2\u4F53\u7684\u9762\u524D, ( &quot;\u6559\u4F1A&quot; \u8FD9\u4E2A\u5B57\u4EC5\u5728\u6B64\u5904\u548C\u739B\u5341\u516D 18 \u4E2D\u5E94\u7528 ). \u5982\u679C\u8FD9\u6837\u8FD8\u4E0D\u884C, \u90A3\u4E48\u53EA\u597D\u5C06\u6B64\u4EBA\u770B\u4F5C\u662F &quot;\u5916\u90A6\u4EBA\u6216\u7A0E\u540F&quot; ( 17 \u8282 ). \u8FD9\u91CC\u4E0D\u662F\u5F88\u6E05\u695A, \u662F\u5426\u610F\u5473\u7740\u628A\u6B64\u7B49\u4EBA\u9A71\u9010\u51FA\u6559\u4F1A, \u8FD8\u662F\u8981\u6548\u6CD5\u8036\u7A23, \u4ECD\u4EE5\u826F\u5584\u884C\u4E3A\u5BF9\u5F85\u8FD9\u6837\u7684\u4EBA. ( \u53C2\u9605\u739B\u516B 5 ~ 13; \u4E5D 9 ~ 13; \u5341\u4E00 19; \u5341\u4E94 21 ~ 28 ).</p><p>\u5728\u6B64, \u8036\u7A23\u4E5F\u8BB8\u662F\u5728\u4E3B\u5F20\u57FA\u7763\u5F92\u8981\u4E50\u610F\u4E00\u8D77\u540C\u5E2D\u548C\u64D8\u997C, \u751A\u81F3\u662F\u4ED6\u4EEC\u6B63\u5728\u4E3A\u5F7C\u6B64\u4E4B\u95F4\u7684\u5206\u6B67, \u4ECE\u4E8B\u548C\u89E3\u7684\u65F6\u5019. \u6CE8\u610F\u8FD9\u6BB5\u7ECF\u6587\u4E2D, \u5E76\u6CA1\u6709\u8BF4\u660E &quot;\u5F97\u7F6A&quot; \u7684\u6027\u8D28. \u4F46\u8FD9\u7B56\u7565, \u4F3C\u4E4E\u5E76\u975E\u9488\u5BF9\u6BCF\u7C7B\u7F6A\u884C\u90FD\u884C\u7684\u901A. \u6211\u4EEC\u4E5F\u8981\u77E5\u9053, \u6574\u4E2A\u6559\u4F1A\u56E2\u4F53\u6709\u7740 &quot;\u675F\u7F1A&quot; \u548C &quot;\u91CA\u653E&quot; \u7684\u6743\u67C4. \u5E76\u4E14\u6574\u4E2A\u6559\u4F1A\u4E5F\u8981\u4E3A\u548C\u597D\u800C\u7948\u7977.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u57FA\u7763\u5F92\u56E2\u4F53\u5E94\u8BE5\u4EE5\u548C\u5E73\u4E3A\u6838\u5FC3\u6CD5\u5219\uFF0C \u6216\u8005\u6CA1\u6709\u51B2\u7A81\uFF0C\u6216\u8005\u51B2\u7A81\u540E\u6709\u8FC7\u4FEE\u548C \u3002\u5982\u679C\u56E2\u4F53\u4E2D\u7684\u4E00\u4E2A\u6210\u5458\u62D2\u7EDD\u4FEE\u548C\uFF0C\u90A3\u4E48\uFF0C\u4ED6\u5373\u4F7F\u7559\u5728\u56E2\u4F53\u5185\uFF0C\u4E5F\u53EA\u662F\u4E2A\u964C\u751F\u4EBA:\u5982\u679C\u4ED6\u4ECD\u65E7\u4E0D\u6539\u53D8\u4EC7\u6068\u7684\u6001\u5EA6\uFF0C\u56E2\u4F53\u7684\u8D1F\u8D23\u4EBA \u6709\u6743\u5229\u5C06\u5176\u5F00\u9664(\u53C2\u9605\u683C\u524D5 :5-6) \u3002</p><p>\u8FD9\u79CD\u5173\u4E8E\u5BBD\u6055\u548C\u4FEE\u548C\u7684\u6559\u8BAD\uFF0C \u5728\u56E2\u4F53\u7948\u7977\u7684\u8BAD\u793A\u4E2D\u5F97\u4EE5\u6EE1\u5168\u3002 \u4E00\u4E2A\u7948\u7977\u7684\u56E2\u4F53\u53EF\u4EE5\u83B7\u4EAB\u8036\u7A23\u7684\u4E34\u5728(\u53C2\u960528:20) \uFF0C\u4F46\u662F\uFF0C\u4ED6\u4EEC\u9700\u8981\u62E5\u6709 \u8036\u7A23\u5728&quot;\u5929\u4E3B\u7ECF&quot;\u91CC\u6240\u8981\u6C42\u7684\u6001\u5EA6\uFF0C\u4E5F\u9700\u8981\u5B9E\u8DF5\u5176\u4E2D\u6240\u8981\u6C42\u7684\u6761\u4EF6\u3002</p><h2 id="\u5BBD\u6055\u4E4B\u9053" tabindex="-1"><a class="header-anchor" href="#\u5BBD\u6055\u4E4B\u9053" aria-hidden="true">#</a> \u5BBD\u6055\u4E4B\u9053</h2><h3 id="\u7ECF\u6587-3" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u6587-3" aria-hidden="true">#</a> \u7ECF\u6587</h3><blockquote><p><sup>21</sup> \u90A3\u65F6\uFF0C\u4F2F\u591A\u7984\u524D\u6765\u5BF9\u8036\u7A23\u8BF4\uFF1A\u201C\u4E3B\u554A\uFF01\u82E5\u6211\u7684\u5F1F\u5144\u5F97\u7F6A\u4E86\u6211\uFF0C\u6211\u8BE5\u5BBD\u6055\u4ED6\u591A\u6B21\uFF1F\u76F4\u5230\u4E03\u6B21\u5417\uFF1F\u201D <sup>22</sup> \u8036\u7A23\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u6211\u4E0D\u5BF9\u4F60\u8BF4\uFF1A\u76F4\u5230\u4E03\u6B21\uFF0C\u800C\u662F\u5230\u4E03\u5341\u4E2A\u4E03\u6B21\u3002 <sup>23</sup> \u4E3A\u6B64\u5929\u56FD\u597D\u6BD4\u4E00\u4E2A\u541B\u738B\uFF0C\u8981\u540C\u4ED6\u7684\u4EC6\u4EBA\u7B97\u8D26\u3002 <sup>24</sup> \u4ED6\u5F00\u59CB\u7B97\u8D26\u7684\u65F6\u5019\uFF0C\u7ED9\u4ED6\u9001\u6765\u4E86\u4E00\u4E2A\u6B20\u4ED6\u4E00\u4E07\u2018\u5854\u51B7\u901A\u2019\u7684\uFF0C<sup>25</sup> \u56E0\u4ED6\u6CA1\u6709\u53EF\u8FD8\u7684\uFF0C\u4E3B\u4EBA\u5C31\u4E0B\u4EE4\uFF0C\u8981\u4ED6\u628A\u81EA\u5DF1\u548C\u59BB\u5B50\u513F\u5973\uFF0C\u4EE5\u53CA\u4ED6\u6240\u6709\u7684\u4E00\u5207\uFF0C\u90FD\u53D8\u5356\u6765\u8FD8\u503A\u3002<sup>26</sup> \u90A3\u4EC6\u4EBA\u5C31\u4FEF\u4F0F\u5728\u5730\u53E9\u62DC\u4ED6\u8BF4\uFF1A\u4E3B\u554A\uFF01\u5BB9\u5FCD\u6211\u5427\uFF01\u4E00\u5207\u6211\u90FD\u8981\u8FD8\u7ED9\u4F60\u3002<sup>27</sup> \u90A3\u4EC6\u4EBA\u7684\u4E3B\u4EBA\u5C31\u52A8\u5FC3\u628A\u4ED6\u91CA\u653E\u4E86\uFF0C\u5E76\u4E14\u4E5F\u8D66\u514D\u4E86\u4ED6\u7684\u503A\u3002 <sup>28</sup> \u4F46\u90A3\u4EC6\u4EBA\u6B63\u51FA\u53BB\u65F6\uFF0C\u9047\u89C1\u4E86\u4E00\u4E2A\u6B20\u4ED6\u4E00\u767E\u2018\u5FB7\u7EB3\u2019\u7684\u540C\u4F34\uFF0C\u4ED6\u5C31\u6293\u4F4F\u4ED6\uFF0C\u627C\u4F4F\u4ED6\u7684\u5589\u5499\u8BF4\uFF1A\u8FD8\u4F60\u6B20\u7684\u503A\uFF01&quot; <sup>29</sup> \u4ED6\u7684\u540C\u4F34\u5C31\u4FEF\u4F0F\u5728\u5730\u54C0\u6C42\u4ED6\u8BF4\uFF1A\u5BB9\u5FCD\u6211\u5427\uFF01\u6211\u5FC5\u8FD8\u7ED9\u4F60\u3002<sup>30</sup> \u53EF\u662F\u4ED6\u4E0D\u613F\u610F\uFF0C\u4E14\u628A\u4ED6\u4E0B\u5728\u76D1\u91CC\uFF0C\u76F4\u5230\u4ED6\u8FD8\u6E05\u4E86\u6B20\u503A\u3002 <sup>31</sup> \u4ED6\u7684\u540C\u4F34\u89C1\u5230\u6240\u53D1\u751F\u7684\u4E8B\uFF0C\u975E\u5E38\u60B2\u6124\uFF0C\u9042\u53BB\u628A\u6240\u53D1\u751F\u7684\u4E00\u5207\u544A\u8BC9\u4E86\u4E3B\u4EBA\u3002 <sup>32</sup> \u4E8E\u662F\u4E3B\u4EBA\u628A\u90A3\u4EC6\u4EBA\u53EB\u6765\uFF0C\u5BF9\u4ED6\u8BF4\uFF1A\u6076\u4EC6\uFF01\u56E0\u4E3A\u4F60\u54C0\u6C42\u4E86\u6211\uFF0C\u6211\u8D66\u514D\u4E86\u4F60\u90A3\u4E00\u5207\u7684\u503A\uFF1B<sup>33</sup> \u96BE\u9053\u4F60\u4E0D\u8BE5\u601C\u60AF\u4F60\u7684\u540C\u4F34\uFF0C\u5982\u540C\u6211\u601C\u60AF\u4E86\u4F60\u4E00\u6837\u5417\uFF1F<sup>34</sup> \u4ED6\u7684\u4E3B\u4EBA\u5927\u6012\uFF0C\u9042\u628A\u4ED6\u4EA4\u7ED9\u5211\u5F79\uFF0C\u76F4\u5230\u4ED6\u8FD8\u6E05\u6240\u6B20\u7684\u4E00\u5207\u3002<sup>35</sup> \u5982\u679C\u4F60\u4EEC\u4E0D\u5404\u81EA\u4ECE\u5FC3\u91CC\u5BBD\u6055\u81EA\u5DF1\u7684\u5F1F\u5144\uFF0C\u6211\u7684\u5929\u7236\u4E5F\u8981\u8FD9\u6837\u5BF9\u5F85\u4F60\u4EEC\u3002\u201D</p></blockquote><p>\u8036\u7A23\u5728\u56DE\u7B54\u4F2F\u591A\u7984\u6240\u63D0, &quot;\u4E00\u4E2A\u5144\u5F1F\u5E94\u88AB\u5BBD\u6055\u51E0\u6B21&quot; \u65F6, \u66F4\u6539\u539F\u59CB\u7684\u62C9\u9ED8\u5BA2\u7684\u62A5\u590D\u5F8B, \u800C\u5BA3\u544A\u4E86\u57FA\u7763\u5F92\u7684 &quot;\u62A5\u590D\u5F8B&quot;: \u65E0\u9650\u7684\u5BBD\u6055. \u8FD9\u4E00\u4E2A\u5F15\u4EBA\u6CE8\u76EE\u7684\u6BD4\u55BB ( \u53EA\u6709\u739B\u7AA6\u798F\u97F3\u6709 ) \u662F\u6211\u4EEC\u5728\u5929\u4E3B\u7ECF\u91CC\u5411\u5929\u4E3B\u6C42\u5BBD\u6055\u7684\u89E3\u8BF4: &quot;\u5C14\u514D\u6211\u503A, \u5982\u6211\u4EA6\u514D\u8D1F\u6211\u503A\u8005&quot;.</p><h3 id="\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" tabindex="-1"><a class="header-anchor" href="#\u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA-\u739B\u7AA6\u798F\u97F3\u300B-2" aria-hidden="true">#</a> \u300A\u65B0\u5B66\u9662\u6751\u5723\u7ECF\u8BE0\u91CA -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u4E0A\u9762\u7B80\u8FF0\u548C\u89E3\u8FC7\u7A0B\u6F2B\u957F\u800C\u8270\u8DDD. \u4F2F\u591A\u7984\u95EE\u4E00\u4E2A\u5144\u5F1F\u5E94\u8BE5\u88AB\u5BBD\u6055\u51E0\u6B21, \u76F4\u5230 &quot;\u4E03\u6B21&quot; \u5417? &quot;\u4E03&quot; \u5728\u5723\u7ECF\u7684\u672F\u8BED\u4E2D\u662F\u4E00\u4E2A\u6574\u7684\u6570\u5B9A, \u5728\u8FD9\u91CC\u8868\u793A\u65E0\u6570\u6B21\u7684\u610F\u601D. \u8036\u7A23\u529D\u8BEB\u8981\u5BBD\u6055\u4ED6\u4EBA &quot;\u4E03\u5341\u4E2A\u4E03\u6B21&quot; ( 22 \u8282 ), \u8FD9\u4E0E\u62C9\u9ED8\u5BA2\u53D1\u8A93\u8981\u7ED9\u4E88 &quot;\u4E03\u5341\u4E03\u500D&quot; \u7684\u62A5\u590D ( \u521B\u56DB 24 ) \u5F62\u6210\u5BF9\u6BD4.</p><p>\u5728\u6B64\u8036\u7A23\u8BB2\u4E86\u4E00\u4E2A\u6709\u5173 &quot;\u5BBD\u6055\u4E4B\u9053&quot; \u7684\u6BD4\u55BB, \u8FD9\u6BD4\u55BB\u6709\u4E09\u5E55\u573A\u666F. \u7B2C\u4E00\u5E55\u662F\u5173\u4E8E\u4E00\u4E2A\u541B\u738B\u51B3\u5B9A\u8981\u6536\u4ED6\u5BB6\u4EC6 &quot;\u6B20\u503A&quot; \u7684\u60C5\u8282 ( 23 ~ 27 \u8282 ), \u8FD9\u4F4D\u5BB6\u4EC6\u662F\u4F4D\u9AD8\u7EA7\u5B98\u50DA ( 24 \u8282, \u7531\u6240\u6B20\u7684\u91D1\u989D\u663E\u793A, \u6B64\u4EC6\u662F\u9AD8\u7EA7\u5B98\u50DA ). \u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u663E\u7136\u662F\u8D1F\u8D23\u5411\u5C5E\u4E0B ( \u5176\u4ED6\u5BB6\u4EC6 ) \u7D22\u53D6\u6B20\u503A\u7684. \u4ED6\u5E94\u5EFA\u7ACB\u4EBA\u9645\u7F51\u7EDC, \u53D1\u5C55\u5BF9\u4ED6\u548C\u541B\u738B\u6709\u5229\u7684\u8FD0\u4F5C\u4F53\u5236. \u8FD9\u4F4D\u60F3\u8981\u663E\u793A\u6743\u529B\u7684\u541B\u738B, \u60F3\u8981\u6536\u56DE &quot;\u4E00\u4E07\u5854\u51B7\u901A&quot;, \u5927\u7EA6\u6298\u5408\u516D\u5343\u81F3\u4E00\u4E07\u4E2A\u5DE5\u4F5C\u65E5\u7684\u5DE5\u8D44. \u541B\u738B\u7684\u76EE\u7684\u662F\u8981\u63D0\u9192\u5BB6\u4EC6\u5BF9\u4ED6\u987A\u670D. \u5BB6\u4EC6\u7684\u53CD\u5E94\u6B63\u5408\u541B\u738B\u7684\u610F ( 26 \u8282 ). \u4ED6\u5411\u541B\u738B\u8868\u73B0\u4E86\u987A\u670D\u548C\u656C\u610F, \u540C\u65F6\u8FD8\u8868\u793A\u4E86\u5BF9\u541B\u738B\u4F9D\u8D56\u548C\u5FE0\u8BDA. \u541B\u738B\u5BF9\u6B64\u611F\u5230\u6EE1\u610F, \u5E76\u6062\u590D\u4E86\u5BB6\u4EC6\u7684\u804C\u4F4D. \u541B\u738B\u7684\u6743\u529B\u548C\u5BBD\u5B8F\u5927\u91CF\u56E0\u6B64\u4F20\u626C\u5F00\u53BB.</p><p>\u7B2C\u4E8C\u5E55\u662F\u5173\u4E8E\u8FD9\u4F4D\u88AB\u8C41\u514D\u8FD8\u503A\u7684\u5B98\u50DA\u7BA1\u5BB6, \u8DDF\u541B\u738B\u5411\u4ED6\u8BA8\u503A\u4E00\u6837\u5BF9\u81EA\u5DF1\u7684\u5C5E\u4E0B\u7D20\u503A\u7684\u60C5\u8282 ( 28 ~ 30 \u8282 ). \u5C5E\u4E0B\u6B20\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u7684\u91D1\u989D, \u6BD4\u8D77\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u6B20\u541B\u738B\u7684\u91D1\u989D\u8981\u5C11\u4E00\u767E\u500D. \u95EE\u9898\u4E0D\u662F\u6570\u989D\u4E0A\u7684\u591A\u5C11, \u800C\u662F\u4E24\u4EBA\u90FD\u65E0\u6CD5\u511F\u8FD8\u503A\u52A1. \u867D\u7136\u5C5E\u4E0B\u4E5F\u8DDF\u4ED6\u4E0A\u7EA7 ( \u5B98\u50DA\u7BA1\u5BB6 ) \u5BF9\u541B\u738B\u6240\u505A\u7684\u4E00\u6837, \u4FEF\u4F0F\u5728\u5730\u54C0\u6C42\u5B98\u50DA\u7BA1\u5BB6, \u4F46\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u5374\u7528\u6781\u5EA6\u6050\u5687, \u5A01\u80C1\u7684\u65B9\u5F0F, \u800C\u4E0D\u662F\u4EE5\u8C41\u514D\u4ED6\u503A\u52A1\u6765\u5BF9\u5F85\u4ED6.</p><p>\u6700\u540E\u4E00\u5E55\u662F\u8FD9\u5B98\u50DA\u7BA1\u5BB6\u7684\u540C\u4F34\u5C06\u6240\u53D1\u7684\u4E00\u5207, \u62A5\u544A\u7ED9\u541B\u738B, \u541B\u738B\u5BF9\u6B64\u6781\u4E3A\u9707\u6012 ( 31 ~ 34 \u8282 ). \u5982\u679C\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u77AD\u89E3\u541B\u738B\u8C41\u514D\u4ED6\u503A\u52A1\u7684\u7528\u610F, \u90A3\u4E48\u4ED6\u5C31\u5E94\u8BE5\u6548\u6CD5\u541B\u738B\u6240\u505A\u7684. \u5982\u679C\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u9700\u8981\u522B\u4EBA\u5BF9\u4ED6\u8868\u793A\u5FE0\u8BDA, \u963F\u8C00\u53CA\u627F\u8BA4\u4ED6\u7684\u6743\u529B, \u541B\u738B\u65E9\u5148\u7684\u4F5C\u4E3A\u5DF2\u7ECF\u5411\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u660E\u793A\u4E86\u4ED6\u8BE5\u600E\u4E48\u505A. \u7136\u800C\u8FD9\u4F4D\u5B98\u50DA\u7BA1\u5BB6\u5374\u4E0D\u6548\u8BBF\u541B\u738B\u7684\u6240\u4E3A, \u800C\u4EE4\u541B\u738B\u8499\u7F9E. \u5B98\u50DA\u7BA1\u5BB6\u8BA4\u7528\u4ED6\u7684\u884C\u52A8\u8868\u793A\u541B\u738B\u65BD\u884C\u7684\u6743\u529B\u65B9\u6CD5\u662F\u6CA1\u6709\u6548\u529B\u7684. \u5982\u679C\u5B98\u50DA\u7BA1\u5BB6\u8BA4\u4E3A\u4EBA\u8EAB\u653B\u51FB, \u8D2C\u4F4E\u4ED6\u4EBA\u548C\u6B8B\u5FCD\u76D1\u7981, \u662F\u83B7\u5F97\u6743\u529B\u7684\u65B9\u5F0F, \u90A3\u4E48\u541B\u738B\u5C06\u4F1A\u5C55\u793A\u7ED9\u4ED6\u7684, \u6B63\u662F\u81EA\u5DF1\u7684\u4E3A\u4EBA\u4E4B\u9053. &quot;\u5982\u679C\u4F60\u4EEC\u4E0D\u981F\u81EA\u4ECE\u5FC3\u91CC\u5BBD\u6055\u81EA\u5DF1\u7684\u5F1F\u5144, \u6211\u7684\u5929\u7236\u4E5F\u8981\u8FD9\u6837\u5BF9\u5F85\u4F60\u4EEC&quot; ( 35 \u8282 ) \u8FD9\u53E5\u7ED3\u8BBA, \u53EF\u80FD\u662F\u739B\u7AA6\u5723\u53F2\u52A0\u4E0A\u7684.</p><p>\u50CF\u6240\u6709\u7684\u6BD4\u55BB\u624B\u6CD5\u90A3\u6837, \u8FD9\u91CC\u7684\u541B\u738B\u65E2\u50CF\u5929\u4E3B, \u53C8\u4E0D\u50CF\u5929\u4E3B. \u5929\u4E3B\u4E0D\u4F1A\u50CF\u8FD9\u6BD4\u55BB\u4E2D\u4E13\u5236\u7684\u541B\u738B, \u5929\u4E3B\u7684\u4F5C\u4E3A\u4E0D\u662F\u4E3A\u4E86\u6269\u5F20\u6743\u529B\u6216\u8D22\u5BCC, \u800C\u662F\u4E3A\u4E86\u589E\u8FDB\u6240\u6709\u53D7\u9020\u7269\u7684\u5E78\u798F. \u4F46\u662F, \u5929\u4E3B\u4E5F\u4F1A\u50CF\u8FD9\u4E2A\u541B\u738B\u4E00\u6837, \u900F\u8FC7\u8036\u7A23, \u4EC1\u6148\u5730\u5BBD\u6055\u4E86\u6211\u4EEC\u6240\u6709\u7684\u503A, \u72B9\u5982\u8036\u7A23\u5728 \u300A\u5929\u4E3B\u7ECF\u300B \u4E2D\u6559\u5BFC\u95E8\u5F92\u7948\u7977\u90A3\u6837 ( \u53C2\u9605\u739B\u516D 12 ). \u5BF9\u8FD9\u6837\u7684\u601C\u60AF, \u552F\u4E00\u7684\u56DE\u5E94, \u5C31\u662F\u56E0\u5929\u4E3B\u7684\u601C\u60AF\u80FD\u53D8\u5316\u4EBA\u5FC3, \u4F7F\u4EBA\u80FD\u591F\u7528\u540C\u6837\u601C\u60AF\u4E4B\u5FC3\u5BF9\u5F85\u4ED6\u4EBA. \u8FD9\u79CD\u8BA9\u4EBA\u80FD\u4EA7\u751F\u601C\u7684\u529B\u91CF, \u662F\u900F\u8FC7\u8C26\u5351\u5FC3\u6001, \u5E76\u52AA\u529B\u653E\u5F03\u62A5\u590D\u7684\u610F\u613F, \u800C\u8FBE\u81F4\u548C\u89E3\u548C\u7ED3\u679C. \u90A3\u4E9B\u5728\u4E0E\u4ED6\u4EBA\u76F8\u5904\u65F6\u4E0D\u5B66\u4E60\u4EFF\u6548\u5929\u4E3B\u7684\u884C\u4E3A\u7684\u4EBA, \u5929\u4E3B\u5C06\u4F1A\u7528\u4ED6\u4EEC\u7684\u5F85\u4EBA\u4E4B\u5F0F\u5BF9\u5F85\u4ED6\u4EEC\u81EA\u5DF1.</p><h3 id="\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" tabindex="-1"><a class="header-anchor" href="#\u300A\u5055\u4E3B\u8BFB\u7ECF-\u739B\u7AA6\u798F\u97F3\u300B-3" aria-hidden="true">#</a> \u300A\u5055\u4E3B\u8BFB\u7ECF -- \u739B\u7AA6\u798F\u97F3\u300B</h3><p>\u5BF9\u4E8E\u4F2F\u591A\u7984\u8FD9\u4E2A&quot;\u6570\u5B66&quot;\u9898\uFF0C\u8036\u7A23\u540C\u6837\u4EE5\u4E00\u4E2A\u4EE3\u8868&quot;\u65E0\u9650&quot; \u7684\u6570\u5B57\u4F5C\u7B54\uFF0C\u5E76\u4E14\u501F\u7740\u8FD9\u4E2A\u5BF9\u6BD4\u9C9C\u660E\u7684\u6BD4\u55BB\uFF0C\u6765\u8868\u8FBE\u7942\u7684\u610F\u601D\u3002\u5728\u53E4\u4EE3\u7684\u4E1C\u65B9\u6587\u5316\u4E2D\uFF0C\u62A5\u590D\u5F8B\u662F\u4E00\u4E2A\u795E\u5723\u7684\u4F20\u7EDF\uFF0C\u800C\u5BBD\u6055\u610F\u5473\u7740\u4F7F\u81EA\u5DF1\u8499\u7F9E\u5E76\u4E27\u5931\u5C0A\u4E25;\u7136\u800C\uFF0C\u4E3A\u57FA\u7763\u5F92\u800C\u8A00\uFF0C\u6CA1\u6709\u6B62\u5883\u7684\u5BBD\u6055\u4EE3\u66FF\u4E86\u62A5\u4EC7\u3002</p><p>\u8FD9\u4E2A\u6BD4\u55BB\u63CF\u8FF0\u4E86\u4EBA\u548C\u5929\u4E3B\u4E4B\u95F4\u7684\u5173\u7CFB\uFF0C\u4EE5\u53CA\u4EBA\u4E0E\u4EBA\u5F7C\u6B64\u95F4\u7684\u5173\u7CFB\u3002 \u4E00\u4E07\u4E2A\u5854\u51B7\u901A\u7684\u503A\u52A1\uFF0C\u65E0\u8BBA\u600E\u6837\u90FD\u662F\u507F\u8FD8\u4E0D\u8D77\u7684\uFF0C\u8FD9\u8C61\u5F81\u6240\u6709\u7684\u4EBA\u90FD\u8499\u53D7\u5929\u4E3B\u7684\u4EC1\u6148\u800C\u83B7\u5F97\u5BBD\u6055 (24-25) \u3002\u90A3\u4E2A\u6BEB\u65E0\u601C\u60AF\u7684\u4EC6\u4EBA\u7684\u6001\u5EA6\uFF0C\u4EE3\u8868\u4EBA\u5FC3\u7684\u541D\u556C\u3002\u6211\u4EEC\u5F7C\u6B64\u76F8\u6B20&quot;\u4E00\u767E\u5757\u94F6\u5143&quot; (28) \uFF0C\u8FD9\u4E2A\u6570\u76EE\u4E0E\u6211\u4EEC \u83B7\u5F97\u7684\u5BBD\u6055\u76F8\u6BD4\uFF0C\u7B80\u76F4\u4E0D\u503C\u4E00\u63D0\u3002\u5929\u4E3B\u7ED9\u4E88\u6211\u4EEC\u7684\u5BBD\u6055\u4E4B\u6069\u662F\u8D85\u4E4E\u60F3\u8C61\u7684\uFF0C\u4F46\u662F\uFF0C\u5BF9\u4E8E\u90A3\u4E9B\u62D2\u7EDD\u5BBD\u6055\u81EA\u5DF1\u90BB\u4EBA\u7684\u786C\u5FC3\u80A0\uFF0C\u888D\u5374\u8981\u5C06\u8FD9\u79CD\u6069\u5BA0\u5B8C\u5168\u6536\u56DE\u3002\u6211\u4EEC\u5E94\u8BE5\u5982\u4F55\u5BF9\u5F85\u90BB\u4EBA\u5462?</p><p>\u65E0\u8BBA\u8C01\uFF0C\u53EA\u8981\u7ECF\u9A8C\u5230\u5929\u7236\u7684\u8FD9\u79CD\u4EC1\u6148\uFF0C\u90FD\u4E0D\u4F1A\u518D\u53BB\u8BA1\u8F83\u54EA\u91CC\u624D\u662F\u5BB9\u5FCD\u548C\u5BBD\u6055\u5144\u5F1F\u59CA\u59B9\u7684\u5E95\u7EBF\u3002</p>',52),h=[p];function s(r,d){return a(),t("div",null,h)}var i=u(o,[["render",s],["__file","\u739B\u7AA6\u798F\u97F3\u7B2C\u5341\u516B\u7AE0.html.vue"]]);export{i as default};
