import{_ as o,o as e,c as t,e as c}from"./app.6bf529a6.js";const d={},r=c('<h1 id="\u767D\u663C\u5C06\u8FD1-\u8BA9\u6211\u4EEC\u901A\u8FC7\u8BF4\u7406\u8FBE\u4E4E\u9053" tabindex="-1"><a class="header-anchor" href="#\u767D\u663C\u5C06\u8FD1-\u8BA9\u6211\u4EEC\u901A\u8FC7\u8BF4\u7406\u8FBE\u4E4E\u9053" aria-hidden="true">#</a> \u767D\u663C\u5C06\u8FD1, \u8BA9\u6211\u4EEC\u901A\u8FC7\u8BF4\u7406\u8FBE\u4E4E\u9053</h1><h2 id="\u6211\u4EEC\u7684\u7F18\u8D77" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u7684\u7F18\u8D77" aria-hidden="true">#</a> \u6211\u4EEC\u7684\u7F18\u8D77</h2><p>2020 \u5E74 6 \u6708 22 \u65E5, \u7B2C\u4E00\u671F <code>\u767D\u663C\u7535\u53F0</code> \u4E0A\u7EBF. \u8FD9\u671F\u8282\u76EE\u662F <code>\u300A\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u4E8B\u4EF6 | \u54C0\u54ED\u7684\u4EBA\u8981\u540C\u54ED\u300B</code>.</p><p>\u5F53\u65F6\u6B63\u503C\u7F2A\u53EF\u99A8\u4E8B\u4EF6\u5168\u7F51\u53D1\u9175. \u6211\u4F5C\u4E3A\u4E24\u4E2A\u5973\u5B69\u7684\u5BB6\u957F, \u5728\u770B\u5230\u76F8\u5173\u65B0\u95FB\u540E, \u5FC3\u4E2D\u6EE1\u4E86\u7126\u8651\u548C\u7EDD\u671B\u7684\u60C5\u7EEA. \u6211\u53C2\u4E0E\u4E86\u5C0F\u674E\u8001\u5E08\u7EC4\u7EC7\u7684 FSTD \u8BFB\u7ECF\u5C0F\u7EC4, \u5C0F\u7EC4\u7684\u7FA4\u91CC\u90FD\u662F\u53C2\u52A0\u8BFB\u7ECF\u5B66\u4E60\u7684\u57FA\u7763\u5F92\u670B\u53CB. \u6211\u4E0E\u7FA4\u91CC\u7684\u670B\u53CB\u4EEC\u804A\u4E86\u6211\u5173\u4E8E\u8FD9\u4E8B\u7684\u60F3\u6CD5, \u7ED3\u679C\u53D1\u73B0\u5927\u5BB6\u5BF9\u8FD9\u4EF6\u4E8B\u90FD\u6709\u81EA\u5DF1\u975E\u5E38\u72EC\u7279\u7684\u89C2\u70B9. \u4E8E\u662F\u6211\u4EA7\u751F\u505A\u64AD\u5BA2\u6765\u8BF4\u8BF4\u8FD9\u4E8B\u7684\u60F3\u6CD5.</p><p>\u6211\u628A\u4E2A\u60F3\u6CD5\u8BB2\u7ED9\u540C\u5728 FSTD \u8BFB\u7ECF\u5C0F\u7EC4\u7684 stella, \u5979\u4E5F\u613F\u610F\u6765\u804A\u804A\u8FD9\u4E9B\u4E8B, \u4E0E\u662F\u5C31\u6709\u4E86 <code>\u767D\u663C\u7535\u53F0</code>.</p><blockquote><p>\u9ED1\u591C\u6DF1\u4E86\uFF0C\u767D\u663C\u5DF2\u8FD1\uFF0C\u6240\u4EE5\u6211\u4EEC\u8BE5\u8131\u53BB\u9ED1\u6697\u7684\u884C\u4E3A\uFF0C\u4F69\u6234\u5149\u660E\u7684\u6B66\u5668. \u884C\u52A8\u8981\u7AEF\u5E84, \u597D\u50CF\u884C\u5728\u767D\u663C\u4E00\u6837. -- \u7F57\u9A6C\u4E66. \u7B2C\u5341\u4E09\u7AE0. \u7B2C 12 \u8282</p></blockquote><p><code>\u767D\u663C\u7535\u53F0</code> \u7684\u540D\u5B57\u6765\u6E90\u4E8E\u5723\u7ECF\u7684\u300A\u7F57\u9A6C\u4E66\u300B\u7B2C\u5341\u4E09\u7AE0\u7B2C 12 \u8282. \u4F7F\u5F92\u4FDD\u7F57\u5199\u4FE1\u7ED9\u7F57\u9A6C\u6559\u4F1A, \u529D\u5BFC\u4ED6\u4EEC\u5E94\u8BE5\u65F6\u5E38\u9192\u5BE4, \u56E0\u4E3A <strong>&quot;\u9ED1\u591C\u6DF1\u4E86, \u767D\u663C\u5DF2\u8FD1&quot;</strong>. <strong>\u767D\u663C</strong> \u6307\u7684\u662F\u5929\u56FD, \u8036\u7A23\u7684\u6551\u6069\u8BA1\u5212\u5DF2\u7136\u5B8C\u6210, \u5929\u56FD\u968F\u65F6\u53EF\u80FD\u964D\u4E34, \u56E0\u6B64 <strong>&quot;\u767D\u663C\u5C06\u8FD1&quot;</strong>. \u4F46\u6BD5\u7ADF\u767D\u663C\u53EA\u662F\u5C06\u8FD1, \u73B0\u5728\u8FD8\u5904\u4E8E\u5929\u4EAE\u524D\u6700\u9ED1\u6697\u7684\u65F6\u523B. \u5728\u8FD9\u6837\u4E00\u4E2A <strong>&quot;\u9ED1\u591C\u6DF1\u4E86, \u767D\u663C\u5DF2\u8FD1&quot;</strong> \u7684\u65F6\u523B, \u6211\u4EEC\u8FD9\u4E9B\u4FE1\u5F92\u5E94\u5F53\u5982\u4F55\u81EA\u5904\u5462? \u4FDD\u7F57\u8BF4: <strong>&quot;\u6240\u4EE5\u6211\u4EEC\u5E94\u8BE5\u8131\u53BB\u9ED1\u6697\u7684\u884C\u4E3A, \u4F69\u6234\u5149\u660E\u7684\u6B66\u5668, \u884C\u52A8\u8981\u7AEF\u5E84, \u597D\u50CF\u884C\u5728\u767D\u663C\u4E00\u6837.&quot;</strong> \u867D\u7136\u5929\u8FD8\u672A\u4EAE, \u4F46\u6211\u4EEC\u56E0\u4E3A\u77E5\u9053\u5929\u8981\u4EAE\u4E86\u7684\u6D88\u606F, \u6211\u4EEC\u5C31\u5F53\u505A\u597D\u968F\u65F6\u5929\u4EAE\u7684\u51C6\u5907, \u65F6\u5E38\u9192\u5BE4, <strong>\u5728\u9ED1\u591C\u4E2D\u884C\u4E8B\u5C31\u8981\u5982\u884C\u5728\u767D\u663C\u4E2D\u4E00\u6837</strong>. <code>\u767D\u663C\u7535\u53F0</code> \u4EE5\u6B64\u4E3A\u540D, \u6B63\u662F\u8981\u8868\u8FBE\u8FD9\u6837\u7684\u610F\u601D: \u4E24\u4E2A\u5173\u5FC3\u4E16\u754C\u7684\u57FA\u7763\u5F92, \u4EE5 <strong>\u5982\u884C\u767D\u663C</strong> \u7684\u89C6\u89D2\u6765\u5173\u5207\u6211\u4EEC\u8FD9\u4E2A <strong>&quot;\u9ED1\u591C\u6DF1\u4E86&quot;</strong> \u7684\u73B0\u4EE3\u4E16\u754C.</p><p>\u5728\u8FD9\u6837\u7684\u81EA\u6211\u671F\u8BB8\u4E0B, \u6211\u4EEC\u9488\u5BF9\u7F2A\u53EF\u99A8\u4E8B\u4EF6\u505A\u4E86\u767D\u663C\u7535\u53F0\u7684\u524D\u4E09\u671F\u8282\u76EE:</p><ul><li><code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u4E8B\u4EF6 | \u4E0E\u54C0\u54ED\u7684\u4EBA\u8981\u540C\u54ED</code></li><li><code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u4E8B\u4EF6 | \u5979\u6709\u4E00\u79CD\u5B64\u72EC\u7684\u771F\u8BDA</code></li><li><code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u7F2A\u53EF\u99A8\u4E8B\u4EF6 | \u8981\u771F\u5B9E\u7684\u611F\u53D7\u81EA\u5DF1\u7684\u5B58\u5728</code></li></ul><p>\u6211\u4EEC\u5E76\u4E0D\u60F3\u628A\u53BB\u6DF1\u7A76\u5230\u5E95\u8C01\u8BE5\u4E3A\u6B64\u4E8B\u8D1F\u8D23, \u4E5F\u5E76\u4E0D\u60F3\u628A\u76EE\u5149\u653E\u5230 \u201C\u6B63\u80FD\u91CF\u201D \u7EC8\u4E8E\u6740\u4EBA\u4E86\u4E4B\u4E0A, \u800C\u662F\u66F4\u591A\u60F3\u8BA8\u8BBA, \u8FD9\u4EF6\u4E8B\u80CC\u540E\u7684\u7ED3\u6784\u6027\u95EE\u9898, \u4EE5\u53CA\u5728\u8FD9\u6837\u7684\u7ED3\u6784\u6027\u95EE\u9898\u4E0B, \u6211\u4EEC\u8FD8\u53EF\u4EE5\u505A\u4E9B\u4EC0\u4E48.</p><p>\u5728\u7F2A\u53EF\u99A8\u4E8B\u4EF6\u4E4B\u540E, \u6211\u4EEC\u4F9D\u7136\u4FDD\u6301\u7740\u5BF9\u8FD9\u4E2A\u4E16\u754C\u7684\u5173\u5FC3, \u5E76\u4EE5\u6211\u4EEC\u7684\u89C6\u89D2\u63D0\u51FA\u81EA\u5DF1\u7684\u770B\u6CD5:</p><ul><li>\u5173\u4E8E\u5DE5\u4F5C -- <code>\u60F3\u548C\u4F60\u804A\u804A\u627E\u5DE5\u4F5C\u8FD9\u4EF6\u5C0F\u4E8B | \u6295\u8D44\u4EBA\u90FD\u662F SB</code></li><li>\u5173\u4E8E\u9AD8\u8003 -- <code>\u60F3\u548C\u4F60\u804A\u804A\u9AD8\u8003 | \u7559\u4E0B\u4EC0\u4E48, \u6211\u4EEC\u5C31\u53D8\u6210\u4EC0\u4E48\u6837\u7684\u5927\u4EBA</code></li><li>\u5173\u4E8E\u53CB\u8C0A -- <code>\u60F3\u548C\u4F60\u804A\u804A\u53CB\u8C0A | \u4E3A\u4EC0\u4E48\u73B0\u5728\u7684\u5E74\u9752\u4EBA\u666E\u904D\u611F\u5230\u5B64\u72EC</code></li><li>\u5173\u4E8E\u6280\u672F -- <code>\u60F3\u548C\u4F60\u804A\u804A\u6280\u672F | \u5982\u4F55\u7528\u64CD\u4F5C\u7CFB\u7EDF\u5BF9\u6297\u7CFB\u7EDF\u7684\u64CD\u7EB5</code></li></ul><h2 id="\u4EE5\u8BF4\u7406\u7684\u65B9\u5F0F\u8FBE\u4E4E\u9053" tabindex="-1"><a class="header-anchor" href="#\u4EE5\u8BF4\u7406\u7684\u65B9\u5F0F\u8FBE\u4E4E\u9053" aria-hidden="true">#</a> \u4EE5\u8BF4\u7406\u7684\u65B9\u5F0F\u8FBE\u4E4E\u9053</h2><p><code>&quot;\u6211\u4EEC\u5E94\u8BE5\u8131\u53BB\u9ED1\u6697\u7684\u884C\u4E3A, \u4F69\u6234\u5149\u660E\u7684\u6B66\u5668.&quot;</code> \u8981\u60F3\u5728\u5DF2\u6DF1\u7684\u9ED1\u591C\u5982\u884C\u767D\u663C\u4E00\u822C, \u4FBF\u8981 <strong>\u4F69\u6234\u5149\u660E\u7684\u6B66\u5668</strong>. \u800C\u6211\u4EEC <strong>\u4F69\u6234\u5149\u660E\u7684\u6B66\u5668</strong> \u5C31\u8981\u901A\u8FC7\u8BF4\u7406\u548C\u505A\u4E8B\u6784\u5EFA\u826F\u597D\u751F\u6D3B.</p><p>\u9ED1\u591C\u4E0D\u4EC5\u662F\u4E00\u79CD\u72B6\u6001, \u8FD8\u662F\u4E00\u79CD <strong>\u6027\u60C5</strong>. \u6240\u4EE5\u624D\u6709 <code>&quot;\u8131\u53BB\u9ED1\u6697\u7684\u884C\u4E3A&quot;</code> \u7684\u8BF4\u6CD5. \u6211\u4EEC\u6D3B\u5728\u9ED1\u591C, \u9ED1\u591C\u4FBF\u603B\u60F3\u6D78\u67D3\u6211\u4EEC.</p><p>\u7136\u800C <code>&quot;\u9ED1\u591C\u7ED9\u4E86\u6211\u9ED1\u8272\u7684\u773C\u775B, \u6211\u5374\u8981\u7528\u5B83\u5BFB\u627E\u5149\u660E&quot;</code>. <code>&quot;\u9ED1\u8272\u7684\u773C\u775B&quot;</code> \u4FBF\u662F\u6211\u4EEC\u5728\u6CE8\u89C6\u8FD9\u4E2A\u4E16\u754C\u65F6\u4EA7\u751F\u7684\u95EE\u9898\u610F\u8BC6.</p><p>\u5F53\u6211\u4EEC\u4E0D\u613F\u653E\u8FC7\u751F\u6D3B\u4E2D\u79CD\u79CD\u56F0\u987F\u4E0E\u96BE\u89E3\u4E4B\u5904, \u628A\u5B83\u4EEC\u4F5C\u4E3A\u6211\u4EEC\u7684\u95EE\u9898\u610F\u601D\u6765\u53CD\u601D, \u7528\u6211\u4EEC\u4F69\u6234\u7684 <strong>\u5149\u660E\u7684\u6B66\u5668</strong> -- \u4E5F\u5C31\u662F\u8BF4\u7406 -- \u6765\u9762\u5BF9\u5B83\u4EEC.</p><ul><li>\u5F53\u6211\u4EEC\u53D1\u73B0\u5728\u793E\u4EA4\u4E2D\u6709\u4F7F\u4EBA\u538C\u70E6\u7684\u98CE\u9669\u65F6, \u6211\u4EEC\u804A\u4E86\u8FC7\u5EA6\u53CD\u601D\u7684\u95EE\u9898 -- <code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u8FC7\u5EA6\u53CD\u601D | \u6211\u5E94\u8BE5\u5BB3\u6015\u628A\u4EBA\u804A\u70E6\u4E48</code></li><li>\u5F53\u6211\u4EEC\u53D1\u73B0\u6211\u4EEC\u88AB\u8BF4\u6559\u65F6, \u6211\u4EEC\u804A\u4E86\u8BF4\u6559\u7684\u95EE\u9898 -- <code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u8BF4\u6559 | \u5982\u4F55\u771F\u6B63\u5EFA\u7ACB\u6743\u5A01</code></li><li>\u5F53\u6211\u4EEC\u89C9\u5F97\u81EA\u5DF1\u5728\u5DE5\u4F5C\u548C\u751F\u6D3B\u4E2D\u4E0D\u591F\u6210\u719F\u65F6, \u6211\u4EEC\u804A\u4E86\u5982\u4F55\u6210\u719F\u7684\u95EE\u9898 -- <code>\u6211\u60F3\u548C\u4F60\u804A\u804A\u6210\u719F | \u82CF\u683C\u62C9\u5E95\u6CA1\u6709\u670B\u53CB</code></li></ul><p>\u5F53\u6211\u9762\u4E34\u8FD9\u4E9B\u95EE\u9898\u65F6, \u6211\u4EEC\u5E76\u4E0D\u60F3\u4EC5\u4EC5\u5C55\u793A\u9ED1\u591C\u662F\u4EC0\u4E48\u6837\u7684, \u6211\u4EEC\u66F4\u60F3\u7528\u8BF4\u7406\u7684\u65B9\u5F0F\u53BB\u601D\u8003\u5B83\u4EEC, \u627E\u5230\u771F\u6B63\u7684\u95EE\u9898\u6240\u5728, \u8BA8\u8BBA\u6211\u4EEC\u8FD8\u53EF\u4EE5\u505A\u4E9B\u4EC0\u4E48.</p><p>\u6211\u4EEC\u66F4\u5173\u5FC3\u5982\u4F55\u5728\u9ED1\u591C\u4E2D\u5982\u884C\u767D\u663C. \u6216\u8005\u7528\u66F4\u76F4\u767D\u8BF4\u6CD5, \u9762\u5BF9\u95EE\u9898\u610F\u8BC6, \u6211\u4EEC\u7EC8\u7A76\u8FD8\u662F\u60F3\u5EFA\u6784\u8D77\u5BF9\u826F\u597D\u751F\u6D3B\u7684\u4FE1\u5FF5.</p><hr><p>\u8FD9\u5C31\u662F\u6211\u4EEC\u7684\u767D\u663C\u7535\u53F0. \u7531\u4E24\u4E2A\u5173\u5FC3\u4E16\u754C\u7684\u57FA\u7763\u5F92\u5171\u540C\u53D1\u8D77\u7684\u64AD\u5BA2\u8282\u76EE\u3002\u6211\u4EEC\u5E0C\u671B\u5173\u6CE8\u73B0\u4EE3\u793E\u4F1A\u7684\u5171\u6027\u95EE\u9898\uFF0C\u5206\u4EAB\u4E2A\u4EBA\u4E0E\u5609\u5BBE\u65B0\u7684\u89C6\u89D2\u4E0E\u53CD\u601D\uFF0C\u5E76\u8BD5\u56FE\u901A\u8FC7\u8BF4\u7406\u7684\u65B9\u5F0F\u8FBE\u4E4E\u9053\uFF0C\u5EFA\u7ACB\u826F\u597D\u751F\u6D3B\u7684\u4FE1\u5FF5\u3002</p><p>\u73B0\u5728\u767D\u663C\u7535\u53F0\u5DF2\u5728\u5404\u5404\u5E73\u53F0\u53D1\u5E03, \u63A8\u8350\u4F60\u4F7F\u7528Spotify\u3001Apple Podcast\uFF08\u975E\u4E2D\u56FD\u533A\uFF09\u3001Google Podcast\u3001Pocket Casts\u4EE5\u53CA\u5C0F\u5B87\u5B99app\u8BA2\u9605\u6536\u542C\u767D\u663C\u7535\u53F0 The Day\u3002</p><p>\u540C\u65F6\u4F60\u4E5F\u53EF\u4EE5\u5728\u7F51\u6613\u4E91\u97F3\u4E50, \u559C\u9A6C\u62C9\u96C5, \u873B\u8713FM \u627E\u5230\u6211\u4EEC</p><p>\u60F3\u53C2\u4E0E\u8BDD\u9898\u8BA8\u8BBA\uFF0C\u6B22\u8FCE\u60A8\u52A0\u5165\u767D\u663C\u7535\u53F0\u7535\u62A5\u7FA4\uFF1Ahttps://t.me/baizhoutheday</p><p>\u6216\u8BB8\u4F60\u6709\u8BDD\u60F3\u5BF9\u6211\u4EEC\u8BF4\uFF0C\u6216\u8BB8\u4F60\u4E5F\u6B63\u5728\u88AB\u4E00\u4E9B\u95EE\u9898\u56F0\u60D1\uFF0C\u6B22\u8FCE\u6765\u4FE1\u5230\u6211\u4EEC\u7684\u90AE\u7BB1\uFF1Atheday@pcast.me</p>',26),s=[r];function p(a,n){return e(),t("div",null,s)}var i=o(d,[["render",p],["__file","\u767D\u663C\u7535\u53F0\u63A8\u5E7F\u6587.html.vue"]]);export{i as default};
