import{_ as o,o as e,c,e as d}from"./app.efeddd24.js";const t={},p=d('<h1 id="\u76F4\u4E0E\u771F\u6211" tabindex="-1"><a class="header-anchor" href="#\u76F4\u4E0E\u771F\u6211" aria-hidden="true">#</a> \u76F4\u4E0E\u771F\u6211</h1><p>\u5728\u6628\u5929\u7684\u5171\u8BFB\u5199\u4F5C\u4E2D, \u6211\u628A\u66FE\u5B50\u7684 <code>\u4E09\u7701\u543E\u8EAB</code> \u5173\u8054\u5230 <code>\u771F\u6211</code> \u7684\u611F\u5FF5. \u4F46\u662F\u975E\u5E38\u4E0D\u597D\u610F\u601D, \u8FD9\u4E2A\u6982\u5FF5\u53EF\u80FD\u662F\u6211\u81EA\u5DF1\u778E\u7F16\u4E86. \u4F46\u6211\u8FD9\u4E2A\u6982\u5FF5\u5012\u4E5F\u4E0D\u662F\u5B8C\u5168\u6CA1\u6709\u7531\u6765, \u786E\u5B9E\u5305\u542B\u7740\u6211\u5BF9\u4E8E\u5112\u5BB6\u7684\u4E00\u4E9B\u7406\u89E3.</p><p>\u6628\u5929 stella \u5728\u7FA4\u91CC\u95EE\u4E86\u6211\u5173\u4E8E <code>\u771F\u6211</code> \u662F\u4EC0\u4E48\u610F\u601D\u7684\u95EE\u9898. \u6211\u901A\u8FC7\u8BED\u97F3\u7684\u65B9\u5F0F\u5728\u7FA4\u91CC\u8FDB\u884C\u56DE\u7B54. \u4F46\u662F\u4E00\u65B9\u9762, \u901A\u8FC7\u8BED\u97F3\u56DE\u7B54, \u6211\u81EA\u4E2A\u662F\u86EE\u723D\u7684, \u4F46\u5BF9\u4E8E\u7FA4\u91CC\u7684\u5176\u4ED6\u7684\u53EF\u80FD\u5C31\u90A3\u4E48\u53CB\u597D; \u53E6\u4E00\u65B9\u9762, \u6211\u4E5F\u89C9\u5F97\u5982\u679C\u4E0D\u901A\u8FC7\u5199\u4F5C\u7684\u65B9\u5F0F\u628A\u5B83\u56FA\u5316\u4E0B\u6765, \u53EF\u80FD\u8FD8\u771F\u6709\u70B9\u53EF\u60DC, \u8FD9\u5173\u8FD9\u70B9, \u53EF\u4EE5\u770B\u4ECA\u5929\u7684\u53E6\u4E00\u7BC7\u63A8\u6587 \u300A\u5199\u4F5C\u662F\u4E00\u79CD\u903C\u8FEB\u300B.</p><p>\u6240\u4EE5\u6211\u5C31\u60F3\u628A\u6211\u5173\u4E8E <code>\u76F4</code> \u548C <code>\u771F\u6211</code> \u7684\u60F3\u6CD5\u5199\u6210\u4E00\u7BC7\u6587\u5B57\u8F93\u51FA\u51FA\u6765.</p><h2 id="\u5112\u5BB6\u7684\u76F4" tabindex="-1"><a class="header-anchor" href="#\u5112\u5BB6\u7684\u76F4" aria-hidden="true">#</a> \u5112\u5BB6\u7684\u76F4</h2><p>\u6211\u5173\u4E8E\u5112\u5BB6, \u5C24\u5176\u662F\u5B54\u5B50\u5B66\u95EE\u4E2D\u5173\u4E8E <code>\u76F4</code> \u7684\u60F3\u6CD5, \u4E3B\u8981\u6765\u81EA\u4E8E\u51AF\u53CB\u5170. \u4ED6\u5728\u300A\u4E2D\u56FD\u54F2\u5B66\u53F2\u300B\u4E2D\u8BFB\u5230\u5B54\u5B50\u7684\u5B66\u8BF4, \u662F\u901A\u8FC7 <code>\u76F4, \u4EC1, \u5FE0, \u6055</code> \u4E32\u8D77\u6765\u7684.</p><p>\u5B54\u5B50\u975E\u5E38\u5F3A\u8C03\u4EBA <code>\u76F4</code> \u7684\u54C1\u6027, \u6240\u8C13\u7684\u76F4\u5927\u6982\u662F\u8BF4 <code>\u771F\u8BDA</code>, \u4E0D\u865A\u4F2A</p><blockquote><p>\u5B50\u66F0: &quot;\u719F\u8C13\u5FAE\u751F\u9AD8\u76F4? \u6216\u4E5E\u91AF\u7109, \u4E5E\u8BF8\u5176\u90BB\u800C\u4E88\u4E4B.&quot;</p></blockquote><p>\u5F53\u65F6\u6709\u4E2A\u53EB\u5FAE\u751F\u9AD8\u7684\u4EBA, \u522B\u4EBA\u8DDF\u4ED6\u6C42\u91AF ( xi \u97F3, \u814C\u83DC\u7528\u7684\u918B, \u4E5F\u53EF\u80FD\u662F\u6307\u9152 ), \u4ED6\u5BB6\u91CC\u6CA1\u6709, \u4ED6\u5C31\u53BB\u8DDF\u90BB\u4EBA\u501F\u6765\u7ED9\u522B\u4EBA. \u522B\u4EBA\u90FD\u8BF4\u4ED6\u76F4, \u5B54\u5B50\u4E0D\u540C\u610F. \u5982\u679C\u81EA\u5DF1\u6CA1\u6709\u91AF, \u76F4\u63A5\u8BF4\u6CA1\u6709, \u8C22\u7EDD\u5C31\u597D\u4E86. \u4F46\u662F\u975E\u8981\u53BB\u8DDF\u90BB\u4EBA\u501F\u4E86\u7ED9\u4ED6, \u90A3\u53EA\u662F\u60DF\u6050\u522B\u4EBA\u4E0D\u9AD8\u5174, \u6240\u4EE5\u4E0D\u7BA1\u81EA\u5DF1\u7684\u771F\u5B9E\u60C5\u51B5, \u53BB\u6EE1\u8DB3\u4ED6\u4EBA. \u8FD9\u79CD\u60C5\u51B5, \u5728\u5B54\u5B50\u770B\u6765, \u5C31\u662F <code>\u5DE7\u8A00\u4EE4\u8272</code> \u4E86. \u5B54\u5B50\u662F\u975E\u5E38\u53CD\u5BF9\u8FD9\u6837, \u66F2\u610F\u8FCE\u5408\u4E4B\u4E8B:</p><blockquote><p>\u5B50\u8D21\u95EE\u66F0: &quot;\u4E61\u4EBA\u7686\u597D\u4E4B, \u4F55\u5982?&quot; \u5B50\u66F0: &quot;\u672A\u53EF\u4E5F.&quot; \u66F0: &quot;\u4E61\u4EBA\u7686\u6076\u4E4B, \u4F55\u5982?&quot; \u5B50\u66F0: &quot;\u672A\u6765\u4E5F, \u4E0D\u5982\u4E61\u4EBA\u5584\u8005\u597D\u4E4B, \u5176\u4E0D\u5584\u8005\u6076\u4E4B.&quot;</p></blockquote><p>\u8FD9\u4E2A\u9053\u7406\u5176\u5B9E\u6211\u4EEC <code>\u767D\u663C\u7535\u53F0</code> \u8C08\u5F97\u5F88\u591A, \u5C31\u662F\u771F\u8BDA\u5C31\u662F\u6709\u8FD9\u6837\u7684\u98CE\u9669. \u6211\u4EEC\u8981\u662F\u771F\u8BDA, \u5C31\u662F\u4F1A\u6709\u660E\u786E\u7684\u89C2\u70B9/\u4E3B\u5F20/\u770B\u6CD5/\u5173\u5207. \u5982\u679C\u6211\u4EEC\u771F\u8BDA\u7684\u53BB\u8868\u8FBE\u81EA\u5DF1\u7684\u89C2\u70B9/\u4E3B\u5F20/\u770B\u6CD5/\u5173\u5207. \u90A3\u4E48\u5C31\u4F1A\u6709\u522B\u4EBA\u4E0D\u8BA4\u53EF\u7684\u98CE\u9669. \u800C\u5982\u679C\u6211\u4EEC\u8981\u771F\u8BDA\u8868\u8FBE, \u5C31\u8981\u627F\u62C5\u8FD9\u6837\u7684\u98CE\u9669.</p><p>\u4F46\u662F\u5C31\u6709\u8FD9\u6837\u4E00\u4E2A\u95EE\u9898, \u8FD9\u4E2A\u76F4\u662F\u54EA\u91CC\u6765\u7684\u554A?</p><h2 id="\u76F4\u4E0E\u6027" tabindex="-1"><a class="header-anchor" href="#\u76F4\u4E0E\u6027" aria-hidden="true">#</a> \u76F4\u4E0E\u6027</h2><blockquote><p>\u53F6\u516C\u8BED\u5B54\u5B50: &quot;\u543E\u515A\u6709\u76F4\u8EAC\u8005, \u5176\u7236\u6518\u7F8A, \u800C\u5B50\u8BC1\u5B50.&quot; \u5B54\u5B50\u66F0: &quot;\u543E\u515A\u4E4B\u76F4\u8005\u5F02\u4E8E\u662F, \u7236\u4E3A\u5B50\u9690, \u5B50\u4E3A\u7236\u9690, \u76F4\u5728\u5176\u4E2D.&quot;</p></blockquote><p>\u4E5F\u662F\u8BF4, <code>\u76F4</code> \u8FD9\u73A9\u610F, \u4E0D\u662F\u51ED\u7A7A\u6765\u7684, \u4E5F\u4E0D\u662F\u4EFB\u6027\u4E3A\u4E4B. \u800C\u662F\u6765\u81EA\u4E8E\u4EBA\u4F26\u672C\u6027. \u53E6\u4E00\u4E2A\u4F8B\u5B50\u6765\u81EA\u4E8E\u5BB0\u6211\u5173\u4E8E\u4E27\u793C\u7684\u53D1\u95EE:</p><blockquote><p>\u5BB0\u6211\u554F\uFF1A\u300C\u4E09\u5E74\u4E4B\u55AA\uFF0C\u671F\u5DF2\u4E45\u77E3\u3002\u541B\u5B50\u4E09\u5E74\u4E0D\u70BA\u79AE\uFF0C\u79AE\u5FC5\u58DE\uFF1B\u4E09\u5E74\u4E0D\u70BA\u6A02\uFF0C\u6A02\u5FC5\u5D29\u3002\u820A\u7A40\u65E2\u6C92\uFF0C\u65B0\u7A40\u65E2\u5347\uFF0C\u947D\u71E7\u6539\u706B\uFF0C\u671F\u53EF\u5DF2\u77E3\u3002\u300D\u5B50\u66F0\uFF1A\u300C\u98DF\u592B\u7A3B\uFF0C\u8863\u592B\u9326\uFF0C\u65BC\u5973\u5B89\u4E4E\uFF1F\u300D\u66F0\uFF1A\u300C\u5B89\u3002\u300D\u300C\u5973\u5B89\u5247\u70BA\u4E4B\uFF01\u592B\u541B\u5B50\u4E4B\u5C45\u55AA\uFF0C\u98DF\u65E8\u4E0D\u7518\uFF0C\u805E\u6A02\u4E0D\u6A02\uFF0C\u5C45\u8655\u4E0D\u5B89\uFF0C\u6545\u4E0D\u70BA\u4E5F\u3002\u4ECA\u5973\u5B89\uFF0C\u5247\u70BA\u4E4B\uFF01\u300D\u5BB0\u6211\u51FA\u3002\u5B50\u66F0\uFF1A\u300C\u4E88\u4E4B\u4E0D\u4EC1\u4E5F\uFF01\u5B50\u751F\u4E09\u5E74\uFF0C\u7136\u5F8C\u514D\u65BC\u7236\u6BCD\u4E4B\u61F7\u3002\u592B\u4E09\u5E74\u4E4B\u55AA\uFF0C\u5929\u4E0B\u4E4B\u901A\u55AA\u4E5F\u3002\u4E88\u4E5F\uFF0C\u6709\u4E09\u5E74\u4E4B\u611B\u65BC\u5176\u7236\u6BCD\u4E4E\uFF1F\u300D</p></blockquote><p>\u5BB0\u6211\u95EE, \u5148\u751F\u60A8\u8BF4, \u4EBA\u8981\u5B88\u4E09\u5E74\u4E27. \u4F46\u662F\u4EBA\u8981\u662F\u4E09\u5E74\u5565\u4E5F\u4E0D\u5E72, \u5C82\u4E0D\u662F\u793C\u4E50\u8352\u5E9F, \u751F\u4EA7\u505C\u987F. \u5B54\u5B50\u8BF4, \u4E09\u5E74\u4E27\u672A\u5230, \u4F60\u5403\u597D\u7684, \u7A7F\u597D\u7684, \u80FD\u5B89\u5FC3\u4E48? \u5BB0\u6211\u8BF4, \u53EF\u4EE5\u5B89\u5FC3\u554A. \u5B54\u5B50\u8BF4, \u4F60\u8981\u662F\u5B89\u5FC3\u5C31\u4E0D\u5B88\u5427, \u6211\u8BF4\u8981\u5B88\u4E09\u5E74\u4E27, \u4E0D\u662F\u786C\u6027\u89C4\u5B9A, \u800C\u662F\u901A\u5E38\u6765\u8BF4, \u4EBA\u5728\u4E27\u671F\u6CA1\u6709\u5FC3\u60C5\u5403\u597D\u7684\u7A7F\u597D\u7684. \u4F60\u8981\u662F\u80FD\u5B89\u5FC3, \u4F60\u4E0D\u5B88\u4E5F\u884C. \u5BB0\u6211\u542C\u4E86\u5C31\u9AD8\u9AD8\u5174\u51FA\u53BB\u4E86. \u5B54\u5B50\u8BF4: \u5BB0\u6211\u8FD9\u4E2A\u4EBA, \u771F\u662F\u4E0D\u4EC1\u554A. \u6211\u4EEC\u51FA\u751F\u524D\u4E09\u5E74, \u81EA\u5DF1\u4EC0\u4E48\u90FD\u5E72\u4E0D\u4E86, \u6211\u4EEC\u90FD\u662F\u5728\u7236\u6BCD\u6000\u62B1\u91CC\u5EA6\u8FC7\u7684, \u6240\u4EE5\u5929\u4E0B\u6240\u6709\u4EBA\u7684\u90FD\u5B88\u8FD9\u4E09\u5E74\u4E27. \u5BB0\u6211\u8FD9\u4E2A\u4EBA\u54EA, \u5BF9\u7236\u6BCD\u7684\u7231\u90FD\u7EF4\u6301\u4E0D\u4E86\u4E48?</p><p>\u6211\u8BFB\u8FD9\u4E00\u6BB5\u65F6, \u4E00\u5EA6\u8BA4\u4E3A\u5BB0\u6211\u662F\u4E0D\u662F\u6709\u58A8\u5BB6\u503E\u5411. \u540E\u6765\u95EE\u4E86\u8BB8\u8001\u5E08, \u624D\u77E5\u9053\u5BB0\u6211\u548C\u5B50\u8D21\u4E24\u4EBA\u7684\u53D1\u5C55\u65B9\u5411\u90FD\u662F\u4E3A\u653F, \u6240\u4EE5\u89C2\u70B9\u6BD4\u8F83\u73B0\u5B9E. \u5BB0\u6211\u8981\u6539\u4E09\u5E74\u4E27\u7684\u53E4\u793C, \u4E0D\u662F\u8FD9\u4E2A\u4EBA\u771F\u7684\u4E0D\u884C, \u800C\u662F\u4ECE\u653F\u6CBB\u7684\u89D2\u5EA6\u770B, \u5982\u679C\u5B88\u8FD9\u4E2A\u793C, \u53EF\u4EE5\u56FD\u5BB6\u5728\u653F\u6CBB\u4E0A, \u5C31\u8981\u9762\u5BF9 <code>\u793C\u5FC5\u574F</code> <code>\u4E50\u5FC5\u5D29</code> \u7684\u96BE\u9898.</p><p>\u6211\u4EEC\u77E5\u9053\u5B54\u5B50\u662F\u4EE5\u590D\u5174\u5468\u793C\u4E3A\u4F7F\u547D\u7684, \u4F46\u662F\u5F53\u65F6\u90A3\u4E2A\u793C\u5D29\u4E50\u574F\u7684\u65F6\u4EE3, \u5468\u793C\u5DF2\u7ECF\u65E0\u6CD5\u9002\u5E94\u5F53\u65F6\u73AF\u5883\u4E86. \u6240\u4EE5\u5B54\u5B50\u8981\u590D\u5174\u5468\u793C, \u5C31\u8981\u56DE\u7B54\u8FD9\u6837\u4E00\u4E2A\u95EE\u9898: \u4E3A\u4EC0\u4E48\u5728\u8FD9\u4E2A\u4E00\u4E2A\u65F6\u4EE3, \u5468\u793C\u4F9D\u7136\u662F\u6709\u6548\u7684\u4E1C\u897F. \u800C\u4ED6\u7684\u56DE\u7B54\u662F, \u56E0\u4E3A\u5468\u793C\u662F\u5408\u4E8E\u4EBA\u6027\u7684. \u4E09\u5E74\u4E27\u4E0D\u662F\u4E00\u79CD\u5916\u5728\u7684\u89C4\u8303, \u800C\u662F\u5185\u5728\u4EBA\u7684\u60C5\u611F\u9700\u6C42, \u8FD9\u79CD\u60C5\u611F\u9700\u6C42\u6765\u81EA\u4E8E\u4EBA\u4F26\u7684\u5929\u6027.</p><p>\u800C\u8FD9\u79CD\u4EBA\u4F26\u5929\u6027\u53C8\u662F\u6709\u5F62\u800C\u4E0A\u7684\u610F\u4E49\u7684, \u300A\u4E2D\u5EB8\u300B\u91CC\u8BF4:</p><blockquote><p>\u5929\u547D\u4E4B\u8C13\u6027, \u7387\u6027\u4E4B\u4E3A\u9053, \u4FEE\u9053\u4E4B\u8C13\u6559.</p></blockquote><p>\u6240\u8C13\u5929\u547D\u8005, \u4EBA\u6027\u8FD9\u4E2A\u4E1C\u897F, \u597D\u50CF\u662F\u4E0A\u5929\u7ED9\u4EBA\u4E0B\u7684\u547D\u4EE4\u4E00\u6837. \u6211\u4EEC\u6700\u6B63\u5F53\u7684\u751F\u6D3B\u65B9\u5F0F ( <code>\u9053</code> ), \u5C31\u662F\u628A\u8FD9\u79CD\u5929\u547D\u4E4B\u6027\u6D3B\u51FA\u6765 ( <code>\u7387\u6027</code> ). \u800C\u4F7F\u6211\u4EEC\u4E0D\u65AD\u4FEE\u6B63\u6211\u4EEC\u7684\u751F\u6D3B\u65B9\u5F0F\u4F7F\u5176\u6B63\u5F53 ( <code>\u4FEE\u9053</code> ) \u5C31\u662F\u6559\u80B2.</p><p>\u6574\u4E2A\u8FD9\u4E2A\u6D3B\u51FA\u5929\u547D\u7684\u8FC7\u7A0B, \u5C31\u662F <code>\u76F4</code>. \u6240\u4EE5\u8FD9\u4E2A <code>\u76F4</code> \u6700\u7EC8\u5C31\u662F\u8981\u901A\u8FBE\u4E00\u4E2A\u5929\u547D\u6240\u7ED9\u4E88\u6211\u4EEC\u7684 <code>\u771F\u6211</code>.</p>',23),a=[p];function u(r,l){return e(),c("div",null,a)}var h=o(t,[["render",u],["__file","\u76F4\u4E0E\u771F\u6211.html.vue"]]);export{h as default};
