import{_ as o,o as t,c as e,e as c}from"./app.6bf529a6.js";const u={},q=c('<h1 id="\u771F\u8BDA\u7684\u52AA\u529B\u4E0E\u771F\u8BDA\u7684\u76EE\u7684" tabindex="-1"><a class="header-anchor" href="#\u771F\u8BDA\u7684\u52AA\u529B\u4E0E\u771F\u8BDA\u7684\u76EE\u7684" aria-hidden="true">#</a> \u771F\u8BDA\u7684\u52AA\u529B\u4E0E\u771F\u8BDA\u7684\u76EE\u7684</h1><h2 id="\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u95EE\u9898" aria-hidden="true">#</a> \u95EE\u9898</h2><p>\u5F20\u542F\u9707:</p><blockquote><p>\u5C0F\u674E\u8001\u5E08, \u4E0A\u6B21\u9762\u57FA\u63D0\u5230 &quot;\u81EA\u4FE1\u5FC3&quot; \u548C &quot;\u771F\u8BDA\u7684\u4ED8\u51FA\u52AA\u529B&quot; \u7684\u95EE\u9898, \u6211\u6700\u8FD1\u4E00\u76F4\u5728\u60F3\u8FD9\u4E2A\u95EE\u9898, \u76EE\u524D\u6709\u4E00\u4E9B\u96BE\u89E3\u7684\u95EE\u9898, \u60F3\u5411\u60A8\u8BF7\u6559.</p></blockquote><blockquote><p>\u6211\u611F\u89C9\u5230, \u6211\u5F88\u96BE\u5BF9\u751F\u6D3B\u4E2D\u7684\u6240\u6709\u7684\u4E8B\u60C5\u90FD\u4ED8\u51FA\u771F\u8BDA\u7684\u52AA\u529B: \u5927\u591A\u6570\u65F6\u5019, \u6211\u9700\u8981\u4ED8\u51FA\u7684\u52AA\u529B\u592A\u591A\u4E86. \u4EE5\u81F3\u4E8E\u6211\u6CA1\u6709\u529E\u6CD5\u5BF9\u6BCF\u4E00\u4EF6\u4E8B\u60C5, \u6216\u5927\u591A\u6570\u4E8B\u60C5\u4ED8\u51FA\u771F\u8BDA\u7684\u52AA\u529B.</p></blockquote><blockquote><p>\u6BD4\u5982\u6211\u542C\u7535\u53F0, \u542C\u5230\u5F88\u591A\u91CD\u8981\u7684\u89C2\u70B9, \u77E5\u9053\u6211\u8981\u4E3A\u4E4B\u4ED8\u51FA\u771F\u8BDA\u7684\u52AA\u529B, \u9700\u8981\u53BB\u627E\u539F\u8457\u6765\u8BFB, \u4F46\u5982\u679C\u6211\u771F\u7684\u5982\u6B64\u4E3A\u4E4B\u8BA8\u51FA\u771F\u8BDA\u7684\u52AA\u529B, \u53EF\u80FD\u5DE5\u4F5C\u91CF\u5C31\u4F1A\u5927\u5230\u65E0\u6CD5\u7A0B\u53D7\u4E86.</p></blockquote><p>\u674E\u539A\u8FB0:</p><blockquote><p>\u8FD9\u91CC\u6709\u51E0\u70B9: 1. \u6709\u6CA1\u6709\u5728\u5F88\u591A\u4E8B\u60C5\u4E0A\u4ED8\u51FA\u771F\u8BDA\u8FD9\u56DE\u4E8B; 2. \u662F\u4E0D\u662F\u8BFB\u539F\u8457\u662F\u771F\u8BDA; 3. \u771F\u8BDA\u662F\u4E00\u79CD\u6001\u5EA6\u8FD8\u662F\u4EC0\u4E48?</p></blockquote><blockquote><p>\u6211\u89C9\u5F97\u771F\u8BDA\u662F\u4E2A\u63CF\u8FF0\u4E00\u79CD\u7B26\u5408, \u5362\u68AD\u6307 &quot;\u8BBA\u8FF0\u548C\u4E8B\u5B9E\u7B26\u5408&quot;.</p></blockquote><blockquote><p>\u4F46\u662F\u5C3C\u91C7\u4E4B\u540E\u7684\u771F\u8BDA, \u8FD8\u662F\u6307, &quot;\u6211\u505A\u7684\u4E8B\u60C5\u662F\u6211\u7684\u610F\u5FD7, \u662F\u6211\u771F\u60F3\u505A\u7684.&quot;</p></blockquote><blockquote><p>\u6240\u4EE5\u5728\u8FD9\u4E2A\u95EE\u9898\u4E0A, \u53EF\u80FD &quot;\u771F\u8BDA\u7684\u52AA\u529B&quot; \u4E0D\u5982 &quot;\u771F\u8BDA\u7684\u76EE\u7684&quot;.</p></blockquote><p>\u5F20\u542F\u9707:</p><blockquote><p>\u4F60\u7684\u610F\u601D\u662F\u8FD9\u6837\u7684\u4E48?</p></blockquote><blockquote><p>\u6211\u6253\u5FC3\u773C\u91CC\u60F3\u505A\u8FD9\u4EF6\u4E8B\u60C5, \u800C\u4E14\u56E0\u4E3A\u6211\u662F\u60F3\u7684\u60F3\u505A, \u6240\u4EE5\u6211\u5C31\u4F1A\u771F\u7684\u5728\u8FD9\u4EF6\u4E8B\u91CC\u4E0B\u529F\u592B. \u56E0\u6B64:</p></blockquote><blockquote><p>1 \u4E5F\u5C31\u6CA1\u6709\u5728\u6240\u6709\u4E8B\u60C5\u4E0A\u771F\u8BDA\u8FD9\u4E2A\u95EE\u9898\u4E86, \u56E0\u4E3A\u60F3\u5BF9\u6240\u6709\u4E8B\u60C5\u771F\u8BDA, \u5176\u5B9E\u662F\u6CA1\u60F3\u597D\u81EA\u5DF1\u60F3\u505A\u4EC0\u4E48;</p></blockquote><blockquote><p>2 \u8BFB\u539F\u8457\u662F\u4E0D\u662F\u771F\u8BDA\u4E0D\u53D6\u51B3\u4E8E\u539F\u8457, \u53D6\u51B3\u4E8E\u8FD9\u672C\u4E66\u4E0E\u6211\u60F3\u505A\u7684\u4E8B\u662F\u4E0D\u662F\u6709\u5173.</p></blockquote><blockquote><p>3 \u6240\u4EE5\u771F\u8BDA\u4E0D\u662F\u4E00\u79CD\u6001\u5EA6, \u53EF\u4EE5\u50CF\u5C5E\u6027\u4E00\u6837\u9644\u52A0\u5728\u884C\u4E3A\u4E0A. \u800C\u662F\u4E00\u79CD\u610F\u5FD7, \u5177\u6709\u6392\u5B83\u6027, \u53EA\u80FD\u5BF9\u4E00\u4E2A\u771F\u8BDA, \u5426\u5219\u5F88\u6709\u53EF\u80FD\u4E0D\u662F\u771F\u8BDA.</p></blockquote>',17),l=[q];function p(a,_){return t(),e("div",null,l)}var k=o(u,[["render",p],["__file","\u771F\u8BDA\u7684\u52AA\u529B\u4E0E\u771F\u8BDA\u7684\u76EE\u7684.html.vue"]]);export{k as default};
