import{_ as r,r as p,o as u,c as n,a as e,b as i,w as l,e as a,d as t}from"./app.6bf529a6.js";const s={},d=a('<h1 id="\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA" aria-hidden="true">#</a> \u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA</h1><h2 id="\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6982\u5FF5" aria-hidden="true">#</a> \u6982\u5FF5</h2><p>\u7CFB\u7EDF\u8BBA\u548C\u8FD8\u539F\u8BBA\u662F\u4E24\u79CD\u8BA4\u77E5\u4E16\u754C\u7684\u6A21\u578B.</p><p><strong>\u8FD8\u539F\u8BBA</strong> \u8BA4\u4E3A\u6240\u6709\u590D\u6742\u7684\u4E8B <em>\u7269\u662F\u7531\u7B80\u5355\u7684\u4E8B</em> \u7269\u7EC4\u6210, \u56E0\u6B64, \u6240\u6709\u590D\u6742\u4E8B\u7269\u90FD\u53EF\u4EE5\u4ECE\u7B80\u5355\u4E8B\u7269\u4E2D\u627E\u5230\u5176\u539F\u56E0.</p><ul><li>\u793E\u4F1A\u662F\u7531\u4EBA\u6784\u6210\u7684, \u6240\u4EE5\u793E\u4F1A\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u751F\u7269\u5B66;</li><li>\u751F\u7269\u662F\u7531\u5176\u751F\u7269\u5316\u5B66\u5F71\u54CD\u7684, \u6240\u4EE5\u751F\u7269\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u5316\u5B66;</li><li>\u5316\u5B66\u662F\u7531\u5176\u5206\u5B50, \u539F\u5B50\u7684\u7269\u7406\u7279\u6027\u51B3\u5B9A\u7684, \u56E0\u6B64\u5316\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u7269\u7406\u5B66;</li><li>\u800C\u6240\u6709\u4E00\u5207\u4E8B\u7269\u90FD\u662F\u7531\u57FA\u672C\u7C92\u5B50\u6784\u6210\u7684, \u6210\u4EE5\u4E00\u5207\u73B0\u8C61\u6700\u540E\u90FD\u53EF\u4EE5\u8FD8\u4E3A\u7269\u7406\u5B66.</li></ul><p><strong>\u7CFB\u7EDF\u8BBA</strong> \u5219\u8BA4\u4E3A\u590D\u6742\u4E8B\u7269\u6240\u5448\u73B0\u7684\u73B0\u8C61\u4E0D\u662F\u7B80\u5355\u4E8B\u7269\u73B0\u8C61\u4E4B\u548C, \u800C\u662F\u4EE5\u5176\u6574\u4F53\u4F5C\u4E3A\u4E00\u4E2A\u7CFB\u7EDF\u4EA7\u751F\u5F71\u54CD\u7684.</p><p>\u6BD4\u5982:</p><ul><li>\u56FA\u8F6F\u4EF6\u8FD0\u884C\u5728\u64CD\u4F5C\u7CFB\u7EDF\u4E0A, \u800C\u64CD\u4F5C\u7CFB\u7EDF\u8FD0\u884C\u5728\u786C\u4EF6\u4E0A;</li><li>\u4F46\u786C\u4EF6\u672C\u8EAB\u5E76\u4E0D\u76F4\u63A5\u51B3\u5B9A\u8F6F\u4EF6;</li><li>\u56E0\u4E3A\u64CD\u4F5C\u7CFB\u7EDF\u5C06\u786C\u4EF6\u63D0\u4F9B\u7684\u80FD\u529B\u91CD\u65B0\u6574\u5408\u540E, \u91CD\u65B0\u63D0\u4F9B\u7ED9\u8F6F\u4EF6.</li><li>\u64CD\u4F5C\u7CFB\u7EDF\u62BA\u5E73\u4E86\u786C\u4EF6\u7684\u5DEE\u5F02.</li></ul><h2 id="\u4E24\u79CD\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u4E24\u79CD\u65B9\u6CD5" aria-hidden="true">#</a> \u4E24\u79CD\u65B9\u6CD5</h2><p>\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA\u4E0E<code>\u5176\u8BF4\u662F\u4E24\u79CD\u5BF9\u4E8E\u4E16</code>\u754C\u7684\u8BA4\u89C2\u6A21\u578B, \u4E0D\u5982\u8BF4\u662F\u4E24\u79CD\u65B9\u6CD5. \u56E0\u4E3A\u662F\u4E24\u79CD\u65B9\u6CD5, \u56E0\u6B64\u5E76\u6CA1\u6709\u4EC0\u4E48\u4E92\u65A5\u6027.</p><p>\u5BF9\u4E8E\u4E00\u4E2A\u73B0\u8C61\u5373\u53EF\u4EE5\u505A\u8FD8\u539F\u8BBA\u7684\u5206\u6790, \u4E5F\u53EF\u4EE5\u4F5C\u7CFB\u7EDF\u7684\u8BBA\u7684\u5206\u6790.</p><p>\u7269\u7406\u5B9A\u5F8B\u81EA\u7136\u89E3\u91CA\u4E0D\u4E86\u4E3A\u4EC0\u4E48\u4E2D\u56FD\u8D70\u4E0A\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF, \u4F46\u662F\u53EF\u4EE5\u89E3\u91CA\u4E3A\u4EC0\u4E48\u4E00\u4E2A\u5251\u4E8E\u9B54\u6CD5\u7684\u4E16\u754C\u4E0D\u53EF\u80FD.</p><p>\u6211\u4EEC\u4E0D <s>\u80FD\u50CF\u72EC\u65AD\u8BBA\u8005</s> \u4E00\u6837, \u8BA4\u4E3A\u53EF\u4EE5\u901A\u8FC7\u5BF9\u4E8E\u73B0\u8C61\u7684\u8FD8\u539F\u627E\u5230\u4E00\u4E2A\u4E16\u754C\u552F\u4E00\u7684\u771F\u7406, \u4F46\u662F, \u6211\u4EEC\u5374\u53EF\u4EE5\u7528\u8FD8\u539F\u8BBA\u627E\u5230, \u8FD9\u4E2A\u4E16\u754C\u7684\u9650\u5236: \u4E16\u754C\u4E3A\u4EC0\u4E48\u4E0D\u662F\u90A3\u6837\u7684.</p><h2 id="\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#\u5E94\u7528" aria-hidden="true">#</a> \u5E94\u7528</h2>',14),h=t("\u738B\u9633\u660E\u683C\u7AF9\u5B50\u7684\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA\u5206\u6790"),c=a('<p><em>2019-01-18\u589E\u8865</em></p><h2 id="\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4-\u7CFB\u7EDF\u8FD8\u539F\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4-\u7CFB\u7EDF\u8FD8\u539F\u8BBA" aria-hidden="true">#</a> \u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4 &quot;\u7CFB\u7EDF\u8FD8\u539F\u8BBA&quot;</h2><p>\u5047\u5982\u6211\u4EEC\u8BA4\u4E3A &quot;\u7269\u7406\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u4E3A\u7269\u7406\u5B66&quot;; &quot;\u5316\u5B66\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u4E3A\u5316\u5B66&quot;; \u90A3\u4E48\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u8BF4 &quot;\u7CFB\u7EDF\u8FD8\u539F\u8BBA&quot;, \u7528\u4EE5\u8868\u8FF0 &quot;\u8FD8\u539F\u4E3A\u7CFB\u7EDF&quot;.</p><p>\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8FD9\u6837\u8868\u8FF0, \u90A3\u4E48, \u662F\u4E0D\u662F\u610F\u5473\u7740, \u7CFB\u7EDF\u8BBA\u5E76\u4E0D\u662F\u548C\u8FD8\u539F\u8BBA <strong>\u76F8\u5BF9\u7684</strong> \u4E00\u79CD\u8BA4\u77E5\u65B9\u5F0F, \u800C\u662F <strong>\u4E00\u79CD\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u5BF9\u8C61</strong>?</p><p>\u5982\u679C\u7CFB\u7EDF\u8BBA\u4E0D\u662F\u4E0E\u539F\u8BBA\u76F8\u5BF9\u7684\u4E00\u79CD\u8BA4\u77E5\u65B9\u5F0F, \u90A3\u4EC0\u4E48\u662F <strong>\u4E0E\u8FD8\u539F\u8BBA\u7684\u8BA4\u77E5\u65B9\u5F0F</strong>?</p><p>\u662F\u7C7B\u6BD4\u5F0F\u7684\u8BA4\u77E5\u65B9\u5F0F\u4E48? \u76F4\u89C2\u7684\u8BA4\u77E5\u65B9\u5F0F? \u6216\u8005\u672C\u8D28\u4E0A\u8BB2 <strong>\u5E76\u6CA1\u6709\u4E00\u79CD\u4E0E\u8FD8\u539F\u8BBA\u76F8\u5BF9\u7684</strong> \u8BA4\u77E5\u65B9\u5F0F?</p><h2 id="\u8FD8\u539F\u548C\u53EF\u7B97" tabindex="-1"><a class="header-anchor" href="#\u8FD8\u539F\u548C\u53EF\u7B97" aria-hidden="true">#</a> \u8FD8\u539F\u548C\u53EF\u7B97</h2><p>\u8FD8\u539F\u662F\u6307, \u5C06\u590D\u6742\u7684\u73B0\u8C61\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406. \u90A3\u4E48\u4EC0\u4E48\u53EB\u505A, \u628A\u590D\u6742\u7684\u73B0\u8C61\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406?</p><p>\u8BA9\u6211\u4EA7\u751F\u8FD9\u4E2A\u60F3\u6CD5\u7684\u662F\u7FFB\u7535\u884C\u4E3A\u7ECF\u6D4E\u5B66\u4E2D\u7684\u4E00\u4E2A\u4F8B\u5B50:</p><ul><li>\u5982\u679C\u7ED9\u4E00\u4E2A\u4EBA\u4E24\u4E2A\u9009\u62E9: <ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u4E2A\u5C0F\u65F6\u540E\u83B7\u5F97 25 \u5757\u94B1;</li></ul></li><li>\u901A\u5E38\u4ED6\u90FD\u4F1A\u9009\u62E9 2 \u5C0F\u65F6\u540E\u83B7\u5F97 25 \u5757\u94B1;</li><li>\u8BA9\u6211\u4EEC\u6765\u4FEE\u6539\u6570\u503C; <ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u5C0F\u65F6\u540E\u83B7\u5F97 20.5 \u5757\u94B1;</li></ul></li><li>\u901A\u5E38\u4ED6\u90FD\u4F1A\u9009\u62E9\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li></ul><p>\u8FD9\u4E2A\u4F8B\u5B50\u662F\u8868\u660E, \u4EBA\u7684\u51B3\u7B56\u662F\u5728\u6BD4\u8F83\u4E2D\u4F5C\u51FA\u7684.</p><p>\u4E5F\u5373\u662F\u8BF4, \u53D7\u8BD5\u5728\u8FDB\u884C\u51B3\u7B56\u7684\u65F6\u5019, \u5728\u8111\u5B50\u91CC\u628A\u4E24\u4E2A\u9009\u9879\u8FD8\u539F\u4E3A\u4E00\u4E2A\u6BD4\u8F83, \u901A\u8FC7\u6BD4\u8F83\u6765\u8FDB\u884C\u51B3\u7B56.</p><p>\u5728\u542C\u5230\u8FD9\u4E2A\u4F8B\u5B50\u7684\u65F6\u5019, \u6211\u5728\u60F3, \u5982\u679C\u6211\u662F\u8FD9\u4E2A\u53D7\u8BD5, \u6211\u4F1A\u4E3A\u4E86\u7B49\u5F85 2 \u5C0F\u65F6\u83B7\u5F97\u63A5\u53D7\u4E00\u4E2A\u600E\u6837\u7684\u503C?</p><p>\u6211\u60F3, \u5927\u6982\u4F1A\u662F 27 \u5757\u94B1.</p><p>\u4E0E\u662F\u6211\u8FDB\u800C\u60F3, \u4E3A\u4EC0\u4E48\u6211\u4F1A\u4E3A\u4E86 27 \u5757\u94B1\u8FD9\u4E2A\u503C\u63A5\u53D7\u7B49\u5F85 2 \u5C0F\u65F6?</p><p>\u6211\u60F3, \u5927\u6982\u662F\u56E0\u4E3A\u725B\u8089\u9762\u662F 7 \u5757\u94B1. \u5012\u5E76\u4E0D\u662F\u725B\u8089\u9762\u5BF9\u6211\u6709\u4EC0\u4E48\u91CD\u8981\u7684\u610F\u4E49, \u662F\u56E0\u4E3A, \u957F\u671F\u5728\u5170\u5DDE\u7684\u751F\u6D3B, \u4F7F\u4E00\u7897\u725B\u8089\u9762\u7684\u94B1\u53D8\u6210\u4E00\u4E2A\u53EF\u8BA1\u7B97\u7684\u5355\u4F4D.</p><p>\u5982\u679C\u6211\u4EEC\u4E0D\u6539\u53D8\u5B9E\u9A8C\u7684\u503C, \u800C\u4EC5\u4EC5\u6539\u53D8\u5B9E\u9A8C\u7684\u8868\u8FF0:</p><ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u4E2A\u5C0F\u65F6\u83B7\u5F97\u4E00\u4E2A 20 \u5757\u94B1\u52A0\u4E00\u4E2A\u5C0F\u793C\u54C1 ( \u793C\u54C1\u503C 5 \u6BDB\u94B1. )</li></ul><p>\u53D7\u8BD5\u63A5\u53D7\u540E\u7684\u673A\u7387\u4F1A\u5927\u5927\u63D0\u5347\u5427.</p><p>\u6240\u4EE5\u5F53\u6211\u4EEC\u8BF4, \u53D7\u8BD5\u5728\u51B3\u7B56\u7684\u65F6\u5019, \u5728\u8111\u5B50\u91CC\u5BF9\u4E24\u4E2A\u9009\u9879\u8FDB\u884C\u4E86\u8FD8\u539F, \u8FD9\u91CC\u7684\u8FD8\u539F, \u662F\u4E0D\u662F\u6307\u5F97\u662F \u53D7\u8BD5\u7684\u8111\u5B50\u91CC, \u4F7F\u5F97\u8FD9\u4E24\u4E2A\u9009\u9879 &quot;\u53EF\u7B97&quot; \u4E86.</p><p>\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8FD9\u6837\u5728\u8868\u8FF0\u4E2D\u4F7F\u7528 &quot;\u8FD8\u539F&quot;, \u90A3\u662F\u4E0D\u662F &quot;\u8FD8\u539F&quot; \u53EF\u4EE5\u770B\u4F5C\u662F\u4E00\u79CD\u6211\u4EEC\u5BF9\u4E8E\u73B0\u8C61\u7684\u4E00\u79CD &quot;\u53EF\u7B97\u5316\u5904\u7406&quot;?</p><p>\u5982\u679C &quot;\u8FD8\u539F&quot; \u662F\u4E00\u79CD &quot;\u53EF\u7B97\u5316\u5904\u7406&quot;, \u662F\u4E0D\u662F\u610F\u5473\u7740 &quot;\u7269\u7406\u8FD8\u539F\u8BBA&quot; \u610F\u5473\u7740\u4F7F\u73B0\u8C61\u53EF\u4EE5\u4F7F\u7528\u7269\u7406\u5B66\u7684\u8BA1\u7B97\u65B9\u5F0F\u6765\u8BA4\u77E5?</p>',22),_=t("\u5173\u4E8E\u53EF\u7B97\u5355\u4F4D\u7684\u60F3\u6CD5 "),q=t("\u4E8C\u8FDB\u5236\u5982\u4F55\u8868\u793A\u8D1F\u6570"),f=t("; [\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570](../\u6BD5\u8FBE\u54E5\u62C9\u65AF\u7684\u65E0\u7406\u6570");function m(B,g){const o=p("RouterLink");return u(),n("div",null,[d,e("ul",null,[e("li",null,[i(o,{to:"/write/%E6%A0%BC%E7%AB%B9.html"},{default:l(()=>[h]),_:1})])]),c,e("p",null,[_,i(o,{to:"/%E4%BA%8C%E8%BF%9B%E5%88%B6%E5%A6%82%E4%BD%95%E8%A1%A8%E7%A4%BA%E8%B4%9F%E6%95%B0.html"},{default:l(()=>[q]),_:1}),f])])}var x=r(s,[["render",m],["__file","\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA.html.vue"]])