import{_ as e,o as d,c as o,e as c}from"./app.efeddd24.js";const a={},r=c('<h1 id="\u7F8E\u6D85\u514B\u585E\u52AA\u7BC7\u8BB2\u4E86\u5565" tabindex="-1"><a class="header-anchor" href="#\u7F8E\u6D85\u514B\u585E\u52AA\u7BC7\u8BB2\u4E86\u5565" aria-hidden="true">#</a> \u7F8E\u6D85\u514B\u585E\u52AA\u7BC7\u8BB2\u4E86\u5565</h1><h2 id="\u6545\u4E8B\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u6545\u4E8B\u80CC\u666F" aria-hidden="true">#</a> \u6545\u4E8B\u80CC\u666F</h2><p><code>\u82CF\u683C\u62C9\u5E95</code> \u78B0\u5230\u4ECE\u5E02\u573A\u548C\u8BAE\u4E8B\u4F1A\u51FA\u6765 <code>\u7F8E\u6D85\u514B\u585E\u52AA</code>. <code>\u7F8E\u6D85\u514B\u585E\u52AA</code> \u4E4B\u6240\u4EE5\u8981\u53BB\u8BAE\u4E8B\u4F1A, \u662F\u56E0\u4E3A\u8BAE\u4E8B\u4F1A\u60F3\u8981\u9009\u4E00\u4F4D\u7ED9\u6B7B\u96BE\u90FD\u81F4\u60BC\u8BCD. \u4F46\u8BAE\u4E8B\u4F1A\u8FD8\u6CA1\u6709\u9009\u51FA\u81F4\u60BC\u8BCD\u7684\u4EBA.</p><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8868\u793A, \u4E3A\u6B7B\u96BE\u8005\u81F4\u60BC\u8BCD\u5E76\u975E\u96BE\u4E8B. \u56E0\u4E3A &quot;\u4E00\u4F4D\u60F3\u8981\u5728\u53D7\u5230\u8D5E\u626C\u4EBA\u6C11\u4F17\u4E2D\u83B7\u5F97\u540D\u58F0\u7684\u4EBA\u6765\u8BF4, \u8D62\u5F97\u4ED6\u4EEC\u7684\u638C\u58F0\u5E76\u975E\u96BE\u4E8B&quot;. \u4ED6\u8BF4\u4ED6\u521A\u542C\u5230\u4ED6\u7684\u5973\u8001\u5E08 <code>\u963F\u4E1D\u5E15\u5E0C\u5A05</code> \u51C6\u5907\u7684\u4E00\u7BC7\u846C\u793C\u6F14\u8BF4\u8BCD.</p><p><code>\u7F8E\u6D85\u514B\u585E\u52AA</code> \u8BF7\u6C42 <code>\u82CF\u683C\u62C9\u5E95</code> \u628A\u60BC\u8BCD\u8BB2\u7ED9\u4ED6\u542C, \u4E8E\u662F <code>\u82CF\u683C\u62C9\u5E95</code> \u5F00\u59CB\u8BB2\u8FF0\u8FD9\u7BC7\u6F14\u8BB2.</p><h2 id="\u6B63\u6587" tabindex="-1"><a class="header-anchor" href="#\u6B63\u6587" aria-hidden="true">#</a> \u6B63\u6587</h2><h3 id="\u5BF9\u4E8E\u8D5E\u7F8E\u7684\u8BBA\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u4E8E\u8D5E\u7F8E\u7684\u8BBA\u8FF0" aria-hidden="true">#</a> \u5BF9\u4E8E\u8D5E\u7F8E\u7684\u8BBA\u8FF0</h3><p><code>\u82CF\u683C\u62C9\u5E95</code> \u8BF4, \u8D5E\u7F8E\u6709\u4E24\u79CD, \u4E00\u79CD\u662F\u901A\u8FC7\u884C\u4E3A\u6765\u8D5E\u7F8E, \u53E6\u4E00\u79CD\u901A\u8FC7\u8BED\u8A00\u6765\u8D5E\u7F8E. \u70C8\u58EB\u4EEC\u5DF2\u7528\u81EA\u5DF1\u7684\u884C\u4E3A\u8D5E\u7F8E\u4E86\u81EA\u5DF1, \u73B0\u5728\u6211\u4EEC\u8981\u7528\u8BED\u8A00\u6765\u8D5E\u7F8E\u81EA\u5DF1.</p><p>\u8D5E\u7F8E\u7684\u987A\u5E8F\u5E94\u5F53\u5148\u8D5E\u7F8E\u51FA\u751F, \u518D\u8D5E\u7F8E\u5F97\u5230\u7684\u629A\u517B\u548C\u6559\u80B2, \u518D\u8D5E\u7F8E\u4ED6\u4EEC\u7684\u884C\u4E3A\u7684\u9AD8\u5C1A\u6765\u8BC1\u660E\u6559\u80B2\u6709\u591A\u6709\u4EF7\u503C.</p><h3 id="\u5BF9\u70C8\u58EB\u7684\u8D5E\u7F8E" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u70C8\u58EB\u7684\u8D5E\u7F8E" aria-hidden="true">#</a> \u5BF9\u70C8\u58EB\u7684\u8D5E\u7F8E</h3><h4 id="\u5BF9\u51FA\u751F\u548C\u629A\u517B\u7684\u8D5E\u7F8E" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u51FA\u751F\u548C\u629A\u517B\u7684\u8D5E\u7F8E" aria-hidden="true">#</a> \u5BF9\u51FA\u751F\u548C\u629A\u517B\u7684\u8D5E\u7F8E</h4><p>\u9996\u5148, \u8FD9\u4E9B\u70C8\u58EB <strong>\u51FA\u751F</strong> \u548C <strong>\u751F\u957F</strong> \u5728\u81EA\u5DF1\u7684\u56FD\u5BB6, \u5C31\u5982\u88AB\u4EB2\u751F\u6BCD\u4EB2\u517B\u80B2\u957F\u5927\u7684\u5B69\u5B50\u4E00\u6837.</p><p>\u5176\u6B21, <strong>\u8FD9\u4E2A\u56FD\u5BB6, \u672C\u8EAB\u5C31\u662F\u503C\u5F97\u8D5E\u7F8E\u7684</strong>, \u56E0\u4E3A</p><ul><li>\u5B83\u662F\u53D7\u8BF8\u795E\u6240\u7231\u7684</li><li>\u5B83\u5148\u62E9\u4E86\u4EBA\u7C7B\u5728\u8FD9\u91CC\u51FA\u751F</li><li>\u5B83\u7269\u4EA7\u4E30\u9976</li><li>\u8BF8\u795E\u8FD8\u5411\u6211\u4EEC\u4F20\u6388\u4E86\u6280\u827A</li></ul><p>\u518D\u6B21, \u70C8\u58EB\u7684\u5148\u7956\u4EEC\u8FD8\u5728\u8FD9\u4E2A\u56FD\u5BB6\u5EFA\u7ACB\u4E86\u826F\u597D\u7684\u653F\u6CBB: \u4E00\u65B9\u9762\u4EBA\u4EEC\u4E0D\u4F1A\u56E0\u4F53\u5F31, \u8D2B\u7A77\u6216\u51FA\u751F\u4F4E\u8D31\u800C\u88AB\u6392\u9664\u5728\u653F\u6CBB\u4E4B\u5916; \u53E6\u4E00\u65B9\u9762, \u653F\u5E9C\u662F\u7531\u591A\u6570\u4EBA\u8D5E\u540C\u800C\u5EFA\u7ACB\u7684\u8D24\u4EBA\u653F\u6CBB.</p><h4 id="\u5BF9\u70C8\u58EB\u884C\u4E3A\u7684\u8D5E\u7F8E" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u70C8\u58EB\u884C\u4E3A\u7684\u8D5E\u7F8E" aria-hidden="true">#</a> \u5BF9\u70C8\u58EB\u884C\u4E3A\u7684\u8D5E\u7F8E</h4><p>\u5728\u4E0A\u53E4\u65F6\u671F, \u70C8\u58EB\u4EEC\u5C31\u8FDB\u884C\u4E86\u6218\u4E89. \u4ED6\u4EEC <strong>\u4E3A\u4E86\u634D\u536B\u81EA\u7531\u800C\u6218</strong>, \u56E0\u6B64\u5373\u6709\u4E3A\u4E86\u5E0C\u814A\u4EBA\u53CD\u6297\u5F02\u90A6\u4EBA\u7684\u6218\u4E89, \u4E5F\u6709\u4E3A\u4E86\u5E0C\u814A\u4EBA\u53CD\u5BF9\u5E0C\u814A\u4EBA\u7684\u6218\u4E89. \u4F46\u5728\u8FD9\u91CC, \u5C06\u4E0D\u8BF4\u7EC6\u8BBA\u8FF0\u8FD9\u4E9B\u6218\u4E89.</p><p>\u8FD9\u91CC\u9996\u5148\u503C\u5F97\u8BB2\u7684\u662F\u6CE2\u5E0C\u6218\u4E89. \u5F53\u65F6\u6CE2\u65AF\u662F\u6B27\u6D32\u7684\u9738\u4E3B, \u4ECE <code>\u5C45\u9C81\u58EB</code> \u5230 <code>\u5927\u6D41\u58EB</code> \u4E09\u4EE3\u6CE2\u65AF\u4EBA\u5F81\u670D\u4E86\u5F53\u65F6\u53EF\u89C1\u7684\u4E00\u5207\u6587\u660E. \u5927\u6D41\u58EB\u6307\u8D23 <code>\u96C5\u5178</code> \u548C <code>\u57C3\u96F7\u7279\u91CC\u4E9A</code> \u4EBA\u4E00\u540C\u53CD\u5BF9 <code>\u8428\u5C14\u8FEA\u65AF</code>. <code>\u5927\u6D41\u58EB</code> \u6D3E <code>\u8FBE\u63D0\u65AF</code> \u8FDB\u653B\u5E0C\u814A, \u4ED6\u5F88\u5FEB\u5C31\u5F81\u670D\u4E86 <code>\u57C3\u96F7\u7279\u91CC\u4E9A</code> \u4EBA. \u800C <code>\u96C5\u5178</code> \u4EBA\u5219\u5728\u9A6C\u62C9\u677E\u51FB\u8D25\u4E86 <code>\u8FBE\u63D0\u65AF</code>. \u8FD9\u6B21\u80DC\u5229\u8BC1\u660E\u4E86\u6CE2\u65AF\u4EBA\u4E0D\u662F\u4E0D\u53EF\u6218\u80DC\u7684, \u7ED9\u4E86\u5E0C\u814A\u4EBA\u5E0C\u671B. \u800C\u540E\u503C\u5F97\u8BF4\u7684\u662F\u8428\u62C9\u7C73\u548C\u963F\u7279\u7C73\u897F\u4E4C\u6D77\u6218, \u4ECE\u6D77\u4E0A\u518D\u6B21\u6218\u80DC\u4E86\u5728\u6D77\u4E0A\u4ECD\u65E7\u62E5\u6709\u5175\u5458\u4F17\u591A, \u4F9B\u5E94\u5145\u5206, \u6280\u672F\u5A34\u719F, \u519B\u529B\u5F3A\u5927\u7684\u6CE2\u65AF\u4EBA. \u6700\u540E\u503C\u5F97\u8BF4\u7684\u662F, \u666E\u62C9\u8482\u4E9A\u6218\u5F79. \u5728\u8FD9\u573A\u6218\u5F79\u4E2D, <code>\u62C9\u727A\u4EE3\u5B5F\u4EBA</code> \u548C <code>\u96C5\u5178</code> \u7AD9\u5728\u4E86\u4E00\u8D77, \u6253\u8D62\u4E86\u8FD9\u6B21\u6218\u5F79.</p><p>\u5E0C\u814A\u4E16\u754C\u5728\u6297\u51FB\u91CE\u86EE\u4EBA\u7684\u6218\u4E89\u4E2D\u83B7\u5F97\u4E86\u80DC\u5229, \u4F46\u968F\u540E\u5C31\u9677\u5165\u4E86\u56E0\u5992\u5FCC\u800C\u8D77\u7684\u5185\u6218. \u9996\u5148, \u4E3A\u4E86 <code>\u73BB\u4FC4\u63D0\u4E9A\u4EBA</code> \u7684\u81EA\u7531, <code>\u96C5\u5178\u4EBA</code> \u548C <code>\u62C9\u727A\u4EE3\u8499\u4EBA</code> \u53D1\u751F\u4E86\u6218\u4E89. \u800C\u540E ( <code>\u4F2F\u7F57\u5954\u5C3C\u6492\u6218\u4E89</code> \u7684\u7B2C\u4E03\u4E2A\u5E74\u5934 ) \u6240\u6709\u5E0C\u814A\u4EBA\u8054\u5408\u8D77\u6765\u653B\u51FB\u96C5\u5178, \u800C\u96C5\u5178\u5728\u6D77\u4E0A\u6218\u80DC\u4E86\u4ED6\u4EEC. \u6700\u540E, \u96C5\u5178\u5374\u5728\u8FDC\u5F81\u897F\u897F\u91CC\u65F6\u906D\u5230\u4E86\u4E0D\u5E78: \u90A3\u4E9B\u56E0\u4E3A\u6781\u7AEF\u5992\u5FCC\u96C5\u5178\u7684\u5E0C\u814A\u57CE\u90A6\u7ADF\u7136\u4E0E\u6CE2\u65AF\u56FD\u738B\u8C08\u5224, \u8054\u5408\u8D77\u6765\u5BF9\u4ED8\u5E0C\u814A.</p><p>\u5728\u897F\u897F\u91CC\u8FDC\u5F81\u4E4B\u540E, \u96C5\u5178\u57CE\u90A6\u5185\u90E8\u9677\u5165\u4E86\u5185\u6218. \u4E09\u5341\u50ED\u4E3B\u5EFA\u7ACB\u4E86\u50ED\u4E3B\u653F\u6CBB, \u4F46\u6C11\u4E3B\u6D3E\u5F88\u5FEB\u8BA8\u4F10\u4E86\u50ED\u4E3B\u4EEC. \u4F46\u56E0\u96C5\u5178\u7684\u8282\u5236\u548C\u4EC1\u6148, \u4ED6\u4EEC\u5728\u90A3\u6837\u7684\u60C5\u51B5\u4E0B\u4ECD\u65E7\u7528\u732E\u796D\u548C\u7948\u7977\u6765\u8C0B\u6C42\u548C\u89E3.</p><p>\u73B0\u5728\u86EE\u5937\u5DF2\u7ECF\u4E0D\u518D\u662F\u5A01\u80C1\u4E86, \u4F46\u662F\u4FDD\u62A4\u4E86\u6574\u4E2A\u5E0C\u814A\u7684\u96C5\u5178\u57CE\u5899\u5374\u56E0\u4E3A\u5E0C\u814A\u57CE\u90A6\u7684\u5185\u6218\u800C\u88AB\u62C6\u9664. \u73B0\u5728\u6700\u5927\u7684\u95EE\u9898\u662F\u60F3\u8981\u5F81\u670D\u4E00\u5207\u5E0C\u814A\u4EBA\u7684 <code>\u62C9\u727A\u4EE3\u8499\u4EBA</code>.</p><p>\u96C5\u5178\u7684\u552F\u4E00\u7684\u95EE\u9898\u662F, \u5979\u592A\u8FC7\u5F31\u8005\u592A\u540C\u60C5, \u603B\u8981\u5E2E\u52A9\u5F31\u8005. \u751A\u81F3\u5728\u6CE2\u65AF\u738B\u56FD\u78B0\u5230\u95EE\u9898\u65F6, \u5979\u5141\u8BB8\u90A3\u4E9B\u88AB\u653E\u9010\u7684\u4EBA\u548C\u81EA\u613F\u524D\u53BB\u63D0\u4F9B\u5E2E\u52A9. \u800C\u5F53\u6CE2\u65AF\u4EBA\u4EE5\u653E\u5F03\u90A3\u4E9B\u4E9A\u7EC6\u4E9A\u7684\u5E0C\u814A\u4EBA\u4E3A\u6761\u4EF6, \u8981\u4E0E\u96C5\u5178\u5EFA\u7ACB\u540C\u76DF\u65F6, \u96C5\u5178\u4E5F\u65AD\u7136\u62D2\u7EDD. \u800C\u4E4B\u6240\u4EE5\u96C5\u5178\u80FD\u6709\u8FD9\u6837\u7684\u60C5\u64CD, \u4E3B\u8981\u662F\u56E0\u4E3A\u96C5\u5178\u4EBA\u662F\u7EAF\u7CB9\u7684\u5E0C\u814A\u4EBA. \u800C\u5176\u4ED6\u4EBA\u5219\u6DF7\u6742\u4E86\u86EE\u5937\u7684\u8840\u6DB2. \u800C\u6B63\u662F\u56E0\u4E3A\u8FD9\u6837\u7684\u9AD8\u5C1A\u60C5\u64CD, \u96C5\u5178\u73B0\u5728\u518D\u6B21\u53D7\u5230\u5B64\u7ACB, \u53C8\u843D\u5230\u88AB\u5F81\u670D\u7684\u5883\u5728. \u4F46\u597D\u5728\u8FD9\u4E00\u6B21, \u96C5\u5178\u8FD8\u4FDD\u7559\u4E86\u6218\u8239, \u57CE\u5899\u548C\u6B96\u6C11\u5730.</p><h3 id="\u70C8\u58EB\u4EEC\u7684\u529D\u6212" tabindex="-1"><a class="header-anchor" href="#\u70C8\u58EB\u4EEC\u7684\u529D\u6212" aria-hidden="true">#</a> \u70C8\u58EB\u4EEC\u7684\u529D\u6212</h3><p>\u5728\u8FD9\u91CC\u57CB\u846C\u52C7\u58EB\u5E76\u8D5E\u7F8E\u4ED6\u4EEC, \u4E0D\u4F46\u662F\u7EAA\u5FF5\u4ED6\u4EEC, \u8FD8\u662F\u8981\u63D0\u9192\u548C\u9F13\u52B1\u4ED6\u4EEC\u7684\u540E\u4EE3, \u4E00\u5B9A\u8981\u6210\u4E3A\u52C7\u58EB, \u56E0\u6B64\u5728\u8FD9\u91CC, \u8FD8\u8981\u8BF4\u51FA\u90A3\u4E9B\u52C7\u58EB\u4EEC\u5BF9\u8FD8\u6D3B\u7740\u7684\u4EBA\u7684\u8BDD:</p><blockquote><p>\u9996\u5148\u662F\u8BF4\u7ED9\u5B69\u5B50\u4EEC\u7684\u8BDD. \u65E0\u8BBA\u6709\u4EC0\u4E48\u76EE\u6807, \u90FD\u8981\u51ED\u501F\u7F8E\u5FB7\u53BB\u5B9E\u73B0, \u5982\u679C\u6CA1\u6709\u7F8E\u5FB7, \u62E5\u6709\u7684\u4E00\u5207\u4E1C\u897F\u90FD\u662F\u53EF\u803B\u7684, \u7F6A\u6076\u7684. \u56E0\u6B64\u6211\u4EEC\u5E0C\u671B\u5B69\u5B50\u4EEC\u5728\u7F8E\u5FB7\u4E0A\u4E0D\u4EC5\u8981\u8D85\u8FC7\u6211\u4EEC, \u8FD8\u8981\u8D85\u8FC7\u4F60\u4EEC\u7684\u7956\u5148. \u5982\u679C\u4F60\u4EEC\u9075\u5B88\u5BF9\u5FB7\u6027\u7684\u8FFD\u6C42, \u90A3\u4E48\u5F53\u4F60\u4EEC\u6B7B\u540E\u6211\u4EEC\u76F8\u89C1\u65F6, \u6211\u4EEC\u4F1A\u628A\u4F60\u4EEC\u5F53\u670B\u53CB; \u5426\u5219\u6CA1\u6709\u4EBA\u4F1A\u6B22\u8FCE\u4F60, \u63A5\u53D7\u4F60.</p></blockquote><blockquote><p>\u5176\u6B21\u662F\u8BF4\u7ED9\u7236\u6BCD\u4EEC\u7684\u8BDD. \u4E0D\u8981\u592A\u8FC7\u6FC0\u53D1\u4ED6\u4EEC\u7684\u4F24\u611F. \u4E0D\u8981\u53BB\u7948\u6C42\u5B69\u5B50\u6C38\u751F, \u800C\u662F\u7948\u6C42\u8BA9\u5B69\u5B50\u4EEC\u83B7\u5F97\u52C7\u6562\u548C\u626C\u540D. \u8981\u8BB0\u5F97\u53E4\u8C1A: &quot;\u4E07\u52FF\u8FC7\u5EA6&quot;\uFF1A \u8981\u628A\u5E78\u798F\u5168\u90E8\u6216\u5C3D\u53EF\u80FD\u5EFA\u7ACB\u5728\u81EA\u8EAB\u7684\u57FA\u7840\u4E0A, \u800C\u4E0D\u662F\u4F9D\u8D56\u4ED6\u4EBA\u6216\u542C\u51ED\u547D\u8FD0\u6446\u5E03. \u4E0D\u8981\u8FC7\u5EA6\u60B2\u54C0, \u800C\u8981\u628A\u5FC3\u601D\u8F6C\u79FB\u5230\u7167\u6599\u548C\u5173\u6000\u6211\u4EEC\u7684\u59BB\u5B50\u548C\u5B50\u5973\u4E0A.</p></blockquote><blockquote><p>\u6700\u540E\u662F\u8BF4\u7ED9\u57CE\u90A6\u7684\u8BDD: \u8BF7\u5C0A\u91CD\u6211\u4EEC\u7684\u7236\u6BCD, \u629A\u517B\u6211\u4EEC\u7684\u5B50\u5973.</p></blockquote><p>\u800C\u57CE\u90A6\u516C\u6C11\u4E5F\u4F1A\u5BF9\u6B7B\u8005\u7684\u5BB6\u5C5E\u5C3D\u53EF\u80FD\u7684\u7167\u987E, \u57CE\u90A6\u56FD\u5BB6\u4E5F\u4F1A\u5BF9\u70C8\u58EB\u7684\u5BB6\u5C5E\u8FDB\u884C\u7167\u987E, \u5E76\u628A\u4ED6\u4EEC\u4ED6\u4EEC\u7684\u5B69\u5B50\u90FD\u80B2\u6210\u52C7\u58EB. \u800C\u4E14\u57CE\u90A6\u4E5F\u4F1A\u901A\u8FC7\u5404\u6BD4\u8D5B\u6765\u7EAA\u5FF5\u70C8\u58EB\u4EEC.</p><h2 id="\u5C3E\u58F0" tabindex="-1"><a class="header-anchor" href="#\u5C3E\u58F0" aria-hidden="true">#</a> \u5C3E\u58F0</h2><p>\u542C\u5B8C\u4E86\u82CF\u683C\u62C9\u5E95\u7684\u6F14\u8BB2, \u7F8E\u6D85\u514B\u585E\u52AA\u8868\u672A\u60CA\u8BB6, \u4F46\u82CF\u683C\u62C9\u5E95\u5374\u8981\u7F8E\u6D85\u514B\u585E\u52AA\u5BF9\u4ED6\u6240\u8BB2\u8FDB\u884C\u4FDD\u5B88\u79D8\u5BC6. \u7F8E\u6D85\u514B\u585E\u52AA\u6700\u540E\u7B54\u5E94\u4E86\u4ED6.</p><p>\u5168\u4E66\u5B8C.</p>',31),h=[r];function t(i,n){return d(),o("div",null,h)}var s=e(a,[["render",t],["__file","\u7F8E\u6D85\u514B\u585E\u52AA\u7BC7\u8BB2\u4E86\u5565.html.vue"]]);export{s as default};
