import{_ as t,o,c as u,e as q}from"./app.efeddd24.js";const p={},e=q('<h2 id="\u80CC\u56DE\u6765\u7684\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u80CC\u56DE\u6765\u7684\u4EBA" aria-hidden="true">#</a> \u80CC\u56DE\u6765\u7684\u4EBA</h2><p>\u4ECA\u5929\u6765\u8BB2\u4E86\u4E2A\u9B3C\u6545\u4E8B.</p><p>\u6211\u4E00\u76F4\u6709\u60F3\u8BB2\u9B3C\u6545\u4E8B\u7684\u51B2\u52A8. \u4EE5\u81F3\u4E8E\u521D\u9AD8\u4E2D\u7684\u65F6\u5019\u60F3\u5199\u6050\u6016\u5C0F\u8BF4, \u7F20\u7740\u957F\u8F88\u8BA9\u4ED6\u4EEC\u8BB2\u4ED6\u4EEC\u77E5\u9053\u7684\u6C11\u95F4\u4F20\u8BF4. \u6211\u5916\u516C\u88AB\u6211\u7F20\u4E0D\u8FC7, \u8BB2\u4E86\u4ED6\u8001\u5BB6\u7684\u4E00\u4E2A\u6C11\u4FD7, \u53EB\u8BF7\u4E94\u7237. \u8FD9\u4E94\u7237\u59D3\u4EC0\u540D\u8C01\u4F55\u65B9\u795E\u5723\u65E0\u4EBA\u77E5\u6653. \u5230\u4E86\u8BF7\u4E94\u7237\u90A3\u4E00\u5929, \u5927\u5BB6\u711A\u9999\u70E7\u7EB8, \u4F9B\u5949\u74DC\u679C\u9762\u70B9. \u63A5\u7740\u4E94\u7237\u4FBF\u4E0A\u4E86\u4E00\u4E2A\u4EBA\u7684\u8EAB. \u88AB\u4E94\u7237\u4E0A\u8EAB\u4E4B\u4EBA\u7ACB\u65F6\u75AF\u98A0\u8D77\u6765, \u6EE1\u53E3\u5C3D\u662F\u6C61\u8A00\u79FD\u8BED, \u5C3D\u8A00\u5404\u5BB6\u9690\u5BC6\u4E4B\u4E8B. \u5C06\u6240\u4F9B\u4E4B\u7269 -- \u9999\u8721\u88F1\u7EB8, \u74DC\u679C\u9762\u70B9, \u751A\u81F3\u76DB\u74DC\u679C\u7684\u676F\u76D8 -- \u5C3D\u6570\u5403\u4E0B, \u676F\u76D8\u7684\u788E\u7247\u5212\u4F24\u5634\u8138, \u9C9C\u8840\u6D41\u7684\u6EE1\u8138\u6EE1\u8EAB. \u800C\u540E\u7FA4\u4F17\u5C06 &quot;\u4E94\u7237&quot; \u9AD8\u9AD8\u4E3E\u8D77, \u7C07\u62E5\u7740\u5C06\u5B83\u4ECE\u5C71\u9876\u8DD1\u4E0B, \u5411\u4ED6\u8DEA\u62DC\u4E5E\u6C42.</p><p>\u6211\u88AB\u8FD9\u4E8B\u4E2D\u75AF\u72C2\u8840\u8165\u7684\u6C14\u8D28\u9707\u60CA\u4E86, \u751A\u81F3\u60F3\u7ED3\u5408\u897F\u5317\u5E7F\u4E3A\u6D41\u4F20\u7684\u5218\u4F2F\u6E29\u65A9\u9F99\u8109\u7684\u6545\u4E8B\u5199\u4E00\u4E2A\u88AB\u90AA\u6076\u796D\u4EEA\u63A7\u5236\u7684\u75AF\u72C2\u5C71\u6751\u7684\u6050\u6016\u6545\u4E8B. \u5982\u679C\u5F53\u65F6\u6211\u719F\u6089\u514B\u82CF\u9C81\u5F0F\u7684\u5199\u4F5C\u5957\u8DEF, \u8BF4\u4E0D\u5B9A\u4E5F\u5C31\u5199\u51FA\u6765\u4E86. \u4F46\u53EF\u60DC, \u5F53\u65F6\u6211\u8FDE\u514B &quot;\u514B\u82CF\u9C81&quot; \u8FD9\u4E2A\u8BCD\u90FD\u6CA1\u542C\u8FC7.</p><p>\u4ECA\u5929\u8BB2\u7684\u6545\u4E8B, \u4E3B\u89D2\u662F\u4E2A\u53F0\u6E7E\u4EBA. \u56E0\u4E3A\u662F\u90FD\u5E02\u4F20\u8BF4, \u5177\u4F53\u59D3\u540D\u4E0D\u53EF\u8003, \u6211\u4EEC\u4F30\u4E14\u79F0\u4ED6\u53EB &quot;\u5C0F\u9B4F&quot; \u5427.</p><p>\u6211\u4EEC\u77E5\u9053, \u53F0\u6E7E\u662F\u4E2A\u591A\u4FE1\u4EF0\u7684\u5730\u533A, \u4E0D\u540C\u4FE1\u4EF0\u7684\u4EBA\u5BB6\u90FD\u4F1A\u5728\u5BB6\u91CC\u4F9B\u5949\u4E9B\u795E\u575B. \u56E0\u4E3A\u4FE1\u4EF0\u6DF7\u6742, \u4F5B\u9053\u8428\u6EE1\u6559\u7684\u795E\u4ED9\u4F9B\u5728\u4E00\u4E2A\u795E\u575B\u91CC\u4E5F\u4E0D\u662F\u7A00\u5947\u7684\u4E8B. \u5C0F\u9B4F\u5BB6\u4E5F\u662F\u5982\u6B64, \u81EA\u4ED6\u751F\u4E0B\u6765, \u5BB6\u91CC\u5C31\u4F9B\u7740\u4E00\u4E2A\u795E\u575B. \u795E\u575B\u91CC\u4F9B\u7740 &quot;\u8D75\u7237&quot;.</p><p>&quot;\u8D75\u7237&quot; \u4E0D\u662F\u795E\u4ED9, \u662F\u5C0F\u9B4F\u7237\u7237\u7684\u6362\u5E16\u5144\u5F1F. \u6240\u8C13\u6362\u5E16\u5144\u5F1F, \u662F\u8BF4\u4E24\u4EBA\u5173\u7CFB\u5F88\u597D, \u4EE5\u81F3\u4E8E\u5C06\u81EA\u5DF1\u7684\u516B\u5B57\u5199\u5728\u5E16\u4E0A\u4E0E\u5BF9\u65B9\u4EA4\u6362. \u65F6\u503C\u89E3\u653E\u6218\u4E89\u5C31\u8981\u80DC\u5229\u4E86. \u9B4F\u5BB6\u53A6\u95E8\u7684\u4EB2\u4EBA\u5C31\u634E\u4FE1\u7ED9\u9B4F\u5927\u7237, \u8BA9\u4ED6\u8D76\u5FEB\u53BB\u53A6\u95E8\u4F1A\u5408, \u597D\u4E00\u8D77\u53BB\u53F0\u6E7E. \u9B4F\u5927\u7237\u5C31\u6025\u6025\u7684\u627E\u4E86\u8D75\u7237, \u4E00\u8D77\u4E0A\u8DEF. \u4E00\u8DEF\u5175\u614C\u9A6C\u4E71, \u9B4F\u5927\u5BB6\u8D70\u5230\u534A\u8DEF\u7ADF\u4E0D\u5C0F\u5FC3\u6454\u65AD\u4E86\u817F. \u8D75\u7237\u4E0D\u6127\u662F\u7ED3\u4E49\u91D1\u5170, \u4E00\u8DEF\u628A\u9B4F\u5927\u7237\u80CC\u5230\u4E86\u53A6\u95E8.</p><p>\u4E24\u4EBA\u8D76\u5230\u53A6\u95E8\u65F6, \u5DF2\u53EA\u5269\u4E86\u534A\u6761\u547D\u5728. \u56E0\u4E3A\u64A4\u9000\u65F6\u592A\u8FC7\u614C\u4E71, \u52A0\u4E0A\u9B4F\u5927\u7237\u7684\u4EB2\u4EBA\u4E0D\u8BA4\u8BC6\u8D75\u7237, \u7ADF\u53EA\u63A5\u4E86\u9B4F\u5927\u7237\u4E0A\u8239. \u7B49\u9B4F\u5927\u7237\u77E5\u9053\u8D75\u7237\u88AB\u7559\u5728\u53A6\u95E8\u65F6, \u8239\u5DF2\u5F00\u4E86\u5F88\u4E45.</p><p>\u4ECE\u6B64\u5927\u6C5F\u5927\u6D77, \u5144\u5F1F\u4E8C\u4EBA\u518D\u65E0\u97F3\u4FE1\u5F80\u6765.</p><p>\u6539\u9769\u5F00\u653E\u4E24\u5CB8\u5173\u7CFB\u7F13\u548C\u540E, \u9B4F\u5927\u7237\u5E26\u7740\u513F\u5B50\u56DE\u5BB6\u63A2\u4EB2. \u4E00\u4E0B\u98DE\u673A\u5C31\u89C9\u5F97\u5934\u660F\u773C\u82B1\u4E0A\u5410\u4E0B\u6CFB. \u6B64\u65F6\u9B4F\u5927\u7237\u5DF2\u5E74\u8FC7\u4E03\u5341, \u522B\u4EBA\u770B\u6765, \u4ED6\u8FD9\u662F\u6C34\u571F\u4E0D\u670D. \u4F46\u4ED6\u81EA\u5DF1\u5374\u76F8\u4FE1, \u8FD9\u662F\u8D75\u7237\u77E5\u9053\u4ED6\u56DE\u6765, \u6765\u627E\u4ED6\u7B97\u5E10\u4E86.</p><p>\u9B4F\u5927\u7237\u5C31\u56DB\u5904\u6253\u542C\u8D75\u7237\u7684\u6D88\u606F, \u53EA\u5927\u6982\u77E5\u9053\u4ED6\u5DF1\u6B7B\u4E86, \u800C\u4E14\u4F3C\u4E4E\u4E5F\u6CA1\u7559\u4E0B\u5B50\u55E3. \u6700\u540E\u9760\u7740\u8BB2\u8FF7\u4FE1\u7684\u529E\u6CD5\u624D\u7B97\u662F\u627E\u5230\u8D75\u7237\u7684\u575F -- \u51E0\u5757\u5806\u5728\u8352\u5C71\u7684\u77F3\u7891. \u9B4F\u5927\u7237\u5728\u575F\u524D\u5927\u54ED, \u7ACB\u8A93\u8BF4: \u5F53\u5E74\u4F60\u4E00\u8DEF\u80CC\u6211\u5230\u53A6\u95E8, \u662F\u6211\u5BF9\u4E0D\u8D77\u4F60\u628A\u4F60\u7559\u5728\u5F53\u5730. \u73B0\u5728\u6362\u6211\u628A\u4F60\u80CC\u56DE\u53F0\u6E7E, \u8BA9\u6211\u5B50\u5B50\u5B59\u5B59\u4F9B\u517B\u4F60. \u4E8E\u662F\u5C31\u627E\u4EBA\u505A\u4E86\u4EEA\u5F0F, \u628A\u8D75\u7237\u8BF7\u56DE\u4E86\u53F0\u6E7E.</p><p>\u672C\u6765\u5C0F\u9B4F\u7684\u7236\u4EB2\u8001\u9B4F\u4E0D\u592A\u540C\u610F\u6B64\u4E8B, \u4F46\u8003\u8651\u5230\u81EA\u5DF1\u7236\u4EB2\u4E00\u8F88\u5B50\u6700\u5927\u7684\u5FC3\u75C5\u5C31\u662F\u6127\u5BF9\u8D75\u7237, \u4E5F\u5C31\u6CA1\u6709\u963B\u6B62. \u4F46\u6CA1\u60F3\u5230, \u8D75\u7237\u8BF7\u56DE\u4E4B\u540E, \u9B4F\u5BB6\u4E00\u4E0B\u5B50\u5C31\u98DE\u9EC4\u817E\u8FBE\u8D77\u6765. \u9B4F\u5BB6\u672C\u6765\u505A\u70B9\u5C0F\u751F\u610F, \u539F\u672C\u4E5F\u4E0D\u4E0A\u4E0D\u4E0B\u7684\u53EA\u6323\u70B9\u5C0F\u94B1, \u4F46\u8BF7\u56DE\u8D75\u7237\u540E, \u751F\u610F\u8D8A\u505A\u8D8A\u5927.</p><p>\u9B4F\u5927\u7237\u8FC7\u4E16\u65F6, \u4EA4\u5F85\u5B50\u5B69\u628A\u8D75\u7237\u5F53\u672C\u5BB6\u957F\u8F88\u4E00\u6837\u4F9B\u5949, \u56E0\u4E3A &quot;\u8FD9\u662F\u6211\u4EEC\u5BB6\u6B20\u4ED6\u7684&quot;. \u5F53\u65F6\u9B4F\u5BB6\u751F\u610F\u6B63\u505A\u7684\u5982\u65E5\u4E2D\u5929, \u4E5F\u90FD\u89C9\u5F97\u662F\u53D7\u4E86\u8D75\u7237\u7684\u5E87\u836B, \u5F53\u7136\u4E5F\u5C31\u62CD\u80F8\u812F\u5411\u9B4F\u5927\u7237\u4FDD\u8BC1. \u5C0F\u9B4F\u7684\u7236\u6BCD\u4E5F\u8BA4\u4E86\u8D75\u7237\u505A\u5E72\u7239. \u9B4F\u5927\u7237\u8FD9\u624D\u542B\u7B11\u4E5D\u6CC9.</p><p>\u81EA\u6B64\u4E4B\u540E\u9B4F\u5BB6\u987A\u98CE\u987A\u6C34, \u771F\u5230\u5C0F\u9B4F\u7ED3\u5A5A\u624D\u663E\u51FA\u602A\u4E8B\u6765.</p><p>\u5C0F\u9B4F\u662F\u5949\u5B50\u6210\u5A5A, \u5AB3\u5987\u6000\u5B55\u65F6\u4E24\u4EBA\u8FD8\u90FD\u5728\u4E0A\u5927\u5B66. \u4E24\u4EBA\u529E\u5B8C\u5A5A\u793C, \u6025\u6025\u5FD9\u5FD9\u5EA6\u4E86\u871C\u6708, \u4E00\u56DE\u6765\u5AB3\u5987\u5C31\u7EE7\u7EED\u5F00\u5DE5\u5199\u8BBA\u6587. \u4F46\u81EA\u6B64\u4E4B\u540E\u5979\u5C31\u5F00\u59CB\u4F5C\u6076\u68A6. \u5F00\u59CB\u662F\u68A6\u5230\u5979\u8D70\u5728\u8857\u4E0A, \u6709\u4EBA\u7A81\u7136\u51FA\u6765\u62A2\u5979\u7684\u4E1C\u897F. \u800C\u540E\u53D8\u6210\u4E86\u4EFB\u4F55\u5730\u65B9 -- \u5BB6\u91CC, \u5B66\u6821, \u8D85\u5E02, \u5546\u573A, \u516C\u56ED -- \u968F\u65F6\u6709\u4EBA\u4F1A\u51B2\u51FA\u6765\u62A2\u5979\u624B\u4E0A\u7684\u4E1C\u897F. \u867D\u7136\u6BCF\u6B21\u68A6\u4E2D, \u5979\u4E5F\u80FD\u628A\u4E1C\u897F\u62A2\u56DE\u6765, \u4F46\u662F\u9192\u6765\u4E4B\u540E\u5374\u771F\u8DDF\u548C\u522B\u4EBA\u62A2\u8FC7\u4E1C\u897F\u4E00\u6837\u7CBE\u75B2\u529B\u5C3D. \u5C0F\u9B4F\u4E5F\u53EA\u89C9\u5F97\u81EA\u5DF1\u5AB3\u5987\u662F\u56E0\u4E3A\u6000\u5B55\u5176\u95F4\u8FD8\u8981\u5199\u8BBA\u6587, \u538B\u529B\u592A\u5927\u5BFC\u81F3\u7684\u7CBE\u795E\u8870\u5F31, \u5E76\u6CA1\u6709\u592A\u5F53\u56DE\u4E8B.</p><p>\u76F4\u5230\u6709\u4E00\u6B21, \u5C0F\u9B4F\u5AB3\u5987\u5728\u68A6\u4E2D\u4E0E\u4EBA\u62A2\u4E1C\u897F, \u5C06\u5BF9\u65B9\u7529\u51FA\u53BB. \u5BF9\u65B9\u8DCC\u5750\u5728\u5730\u4E0A\u53D8\u6210\u4E00\u53EA\u5DE8\u5927\u7684\u9ED1\u72D7, \u5472\u4E5F\u54A7\u5634\u7684\u5411\u5979\u6251\u6765. \u5979\u4FBF\u5C16\u53EB\u7740\u4ECE\u68A6\u4E2D\u9192\u6765. \u4ECE\u6B64\u4E4B\u540E\u5728\u68A6\u91CC\u548C\u5979\u62A2\u4E1C\u897F\u7684\u5F00\u59CB\u53D8\u6210\u4E00\u56E2\u50CF\u4EBA\u50CF\u72D7\u53C8\u50CF\u9ED1\u96FE\u7684\u4E1C\u897F. \u800C\u5979\u6BCF\u6B21\u90FD\u62A2\u8F93. \u5C0F\u9B4F\u5AB3\u5987\u5F00\u59CB\u6BCF\u5929\u665A\u4E0A\u5D29\u6E83\u7684\u9192\u6765, \u54ED\u7740\u558A\u7740\u54C0\u6C42: \u6C42\u6C42\u4F60, \u62DC\u6258\u8FD8\u7ED9\u6211, \u4E0D\u8981\u5E26\u8D70\u4ED6...... \u7136\u540E\u5979\u5F00\u59CB\u5728\u5BB6\u91CC\u9501\u95E8. \u5BB6\u91CC\u6240\u6709\u7684\u95E8\u90FD\u8981\u9501, \u54EA\u6015\u6CA1\u6709\u4EBA\u7684\u623F\u95EE\u4E5F\u9501. \u8FD8\u628A\u81EA\u5DF1\u9501\u5728\u5BB6\u91CC, \u4E0D\u518D\u51FA\u95E8\u4E86.</p><p>\u8FD9\u65F6, \u5C0F\u9B4F\u7684\u7236\u4EB2\u8001\u9B4F\u624D\u60F3\u8D77\u4E00\u4EF6\u4E8B\u6765. \u9B4F\u5927\u7237\u53BB\u4E16\u7684\u534A\u5E74\u524D, \u6709\u4E00\u5929\u7A81\u7136\u6765\u4E86\u7CBE\u795E\u8981\u53BB\u722C\u5C71. \u8001\u9B4F\u770B\u9B4F\u5927\u7237\u96BE\u5F97\u7CBE\u795E\u597D, \u5C31\u627E\u4E86\u4E2A\u5E73\u7F13\u7684\u767B\u5C71\u6B65\u9053\u5E26\u9B4F\u5927\u7237\u53BB\u6563\u6B65. \u6563\u6B65\u65F6\u9B4F\u5927\u7237\u7A81\u7136\u7ED9\u8001\u9B4F\u63D0\u4E86\u4E00\u4E2A\u8981\u6C42: \u5982\u679C\u4ED6\u518D\u751F\u5C0F\u5B69, \u5C31\u628A\u8FD9\u4E2A\u6700\u5C0F\u7684\u8FC7\u7EE7\u7ED9\u8D75\u7237, \u7B97\u662F\u5E2E\u4ED6\u7559\u540E; \u5982\u679C\u4ED6\u518D\u6253\u7B97\u751F\u7684\u8BDD, \u628A\u957F\u5B59 -- \u4E5F\u5C31\u662F\u5C0F\u9B4F -- \u7684\u5934\u4E00\u80CE\u8FC7\u7ED9\u8D75\u7237\u4E5F\u884C. \u53CD\u6B63 &quot;\u8D75\u7237\u8BF4\u4ED6\u53EF\u4EE5\u7B49.&quot; \u5F53\u65F6\u5C0F\u9B4F\u624D\u4E0A\u4E2D\u5B66. \u8001\u9B4F\u770B\u8001\u4EBA\u4E00\u8138\u6073\u5207, \u60F3\u65E0\u975E\u662F\u6539\u59D3\u8D75\u800C\u5DF2, \u5B9E\u5728\u4E0D\u884C, \u4E5F\u53EF\u4EE5\u51A0\u6210\u590D\u59D3, \u8D77\u540D\u53EB &quot;\u9B4F\u8D75\u67D0&quot;. \u4E8E\u662F\u5F53\u65F6\u5C31\u7B54\u5E94\u4E86\u4E0B\u6765. \u9B4F\u5927\u7237\u559C\u51FA\u671B\u5916, \u5F53\u665A\u56DE\u5BB6\u4FBF\u70E7\u4E86\u9999\u5728\u8D75\u7237\u7684\u724C\u4F4D\u524D\u56DE\u590D\u4E86\u8FD9\u4E8B.</p><p>\u7136\u800C\u65E2\u4FBF\u662F\u8981\u8FC7\u7EE7\u5B69\u5B50\u7ED9\u8D75\u7237, \u600E\u4E48\u4F1A\u95F9\u5F97\u8FD9\u4E48\u5F97\u51F6?</p><p>\u5C31\u5728\u8FD9\u65F6, \u53C8\u53D1\u751F\u4E86\u4E00\u4EF6\u4E8B. \u5C0F\u9B4F\u6709\u4E00\u4E2A\u59D0\u59D0, \u6709\u4E00\u5929\u665A\u4E0A\u5E26\u540C\u4E8B\u56DE\u5BB6\u53D6\u6696. \u540C\u4E8B\u521A\u8D70\u8FDB\u5C0F\u533A\u4E2D\u5EAD\u5C31\u8FDE\u6EDA\u5E26\u722C\u5730\u9003\u4E86\u51FA\u53BB. \u4E8B\u540E\u540C\u4E8B\u624D\u544A\u8BC9\u5C0F\u9B4F\u59D0, \u5979\u5728\u5C0F\u533A\u770B\u5230\u4E00\u6237\u4EBA\u5BB6\u9633\u53F0\u4E0A\u6EE1\u662F\u4EBA, \u4E00\u4E2A\u9ECF\u6293\u7740\u4E00\u4E2A\u5782\u5728\u4E2D\u5EAD\u4E0A\u7A7A\u6446\u52A8. \u8BF4\u8D77\u8FD9\u6237\u4EBA\u5BB6\u7684\u4F4D\u7F6E, \u6B63\u662F\u9B4F\u5BB6. \u5C0F\u9B4F\u59D0\u4E5F\u662F\u5413\u4E86\u4E00\u8DF3, \u5148\u662F\u7792\u7740\u8001\u9B4F\u627E\u4E86\u98CE\u6C34\u5148\u751F\u6765\u770B. \u98CE\u6C34\u5148\u751F\u770B\u540E, \u4E00\u8FB9\u5B89\u6170\u5C0F\u9B4F\u59D0, \u4E00\u8FB9\u8BF4\u8FD9\u4E8B\u4ED6\u5904\u7406\u4E0D\u4E86. \u7136\u540E\u4ED6\u53C8\u7ED9\u5C0F\u9B4F\u59D0\u63A8\u8350\u4E86\u4E00\u4E2A\u4E2D\u5B66\u7684\u7406\u5316\u8001\u5E08.\u7406\u5316\u8001\u5E08\u6765\u9B4F\u5BB6\u6643\u4E86\u4E00\u5708, \u4FBF\u6293\u7740\u5C0F\u9B4F\u59D0\u5230\u5DF7\u53E3\u62BD\u70DF. \u62BD\u70DF\u65F6, \u8001\u5E08\u95EE\u4E86\u5173\u4E8E\u8D75\u7237\u7684\u4E8B.</p><p>\u5C0F\u9B4F\u59D0\u7ED9\u5C0F\u9B4F\u8BF4: &quot;\u7406\u5316\u8001\u5E08\u8BF4\u4E86, \u8FD9\u4E0D\u53EF\u80FD\u662F\u4EC0\u4E48\u8D75\u7237, \u6211\u4EEC\u8BF7\u9519\u4E1C\u897F\u56DE\u6765\u4E86.&quot; \u9B4F\u5BB6\u8BF7\u56DE\u7684, \u6839\u672C\u4E0D\u662F\u4E00\u4E2A\u4EBA\u7C7B\u7684\u9B42\u9B44, \u800C\u662F &quot;\u4E00\u7FA4&quot;. \u6211\u4EEC\u5E38\u53EB\u5B83\u4EEC &quot;\u4E94\u901A&quot;.</p><h2 id="\u4E0A\u65B9\u5C71\u4E0A\u7684\u9634\u503A" tabindex="-1"><a class="header-anchor" href="#\u4E0A\u65B9\u5C71\u4E0A\u7684\u9634\u503A" aria-hidden="true">#</a> \u4E0A\u65B9\u5C71\u4E0A\u7684\u9634\u503A</h2><p>\u5728\u53F0\u6E7E\u7684\u90FD\u5E02\u4F20\u8BF4\u4E2D\u770B\u5230 &quot;\u4E94\u901A&quot; \u8FD8\u662F\u86EE\u8BA9\u4EBA\u60CA\u8BB6\u7684. &quot;\u4E94\u901A&quot; \u662F\u6C5F\u5357\u5730\u533A\u975E\u5E38\u590D\u6742\u7684\u6C11\u95F4\u4FE1\u4EF0. \u6240\u8C13 &quot;\u5357\u6709\u4E94\u901A, \u72B9\u5317\u4E4B\u6709\u72D0\u4E5F&quot;. \u7136\u800C\u72D0\u4ED9\u4FE1\u4EF0\u591A\u591A\u5C11\u5C11\u8FD8\u80FD\u8BF4\u6E05\u695A\u662F\u6E90\u4E8E\u81EA\u7136\u5D07\u62DC, \u4F46\u4E94\u901A\u662F\u4E2A\u5565, \u5374\u65E0\u4EBA\u80FD\u8BF4\u6E05\u695A\u9053\u660E\u767D.</p><p>\u636E\u8003\u8BC1 &quot;\u4E94\u901A&quot; \u4E00\u8BCD\u6700\u65E9\u51FA\u4E8E\u4F5B\u5178, \u662F\u6307\u4F5B\u6CD5\u795E\u901A. \u5357\u5317\u671D\u5218\u52F0\u300A\u706D\u60D1\u8BBA\u300B\u8BF4: &quot;\u82E5\u4E43\u795E\u4ED9\u5C0F\u9053, \u540D\u4E3A\u4E94\u901A, \u798F\u6781\u751F\u5929, \u4F53\u5C3D\u98DE\u817E.&quot; \u4F46\u4F5B\u6559\u8BED\u5883\u4E2D, \u4E94\u901A\u4EC5\u4E3A\u795E\u901A\u7684\u6982\u5FF5, \u800C\u65E0\u5177\u4F53\u4EBA\u683C\u5F62\u8C61. \u5230\u4E86\u5510\u4EE3, \u8BD7\u4EBA\u65BD\u80A9\u543E\u6709\u8BD7 &quot;\u4E94\u901A\u672C\u662F\u4F5B\u5BB6\u5974, \u8EAB\u8457\u9752\u8863\u4E00\u8DB3\u65E0&quot;. \u867D\u7136\u6709\u4E86\u5F62\u8C61, \u4F46\u8FD9\u4E2A\u5F62\u8C61\u5374\u4E0E\u4F5B\u5178\u65E0\u5173, \u800C\u662F\u51FA\u81EA\u6C11\u95F4\u4FE1\u4EF0.</p><p>\u6258\u540D\u4E8E\u6801\u5B97\u5143\u7684\u300A\u9F99\u57CE\u5F55\u300B\u4E2D, \u6709\u4E00\u5219\u4E94\u901A\u7684\u6545\u4E8B: &quot;\u67F3\u5DDE\u65E7\u6709\u9B3C, \u540D\u4E94\u901A. \u4F59\u59CB\u5230, \u4E0D\u4E4B\u4FE1. \u4E00\u65E5, \u56E0\u53D1\u7BA7\u6613\u8863, \u5C3D\u4E3A\u7070\u70AB. \u4F59\u4E43\u4E3A\u6587\u91AE\u8BC9\u4E8E\u5E1D. \u5E1D\u6073\u6211\u5FC3, \u9042\u5C14\u9F99\u57CE\u7EDD\u5996\u90AA\u4E4B\u602A, \u800C\u5EB6\u58EB\u4EA6\u5F97\u4EE5\u5B81\u4E5F.&quot; \u4F3C\u4E4E\u4E94\u901A\u662F\u67F3\u5DDE\u7684\u5730\u65B9\u9B3C\u7C7B, \u8DDF\u5230\u4EFB\u7684\u5730\u65B9\u5B98\u4E3A\u96BE, \u4F46\u7EC8\u8FD8\u662F\u88AB\u5236\u670D.</p><p>\u5230\u4E86\u5B8B\u4EE3, \u4E94\u901A\u7684\u5F62\u8C61\u590D\u6742\u8D77\u6765. \u5317\u5B8B\u65F6\u671F\u7406\u5B66\u5BB6\u674E\u89CF\u4E3A\u5EFA\u660C\u7684\u4E94\u901A\u7960\u64B0\u5199\u796D\u6587, \u8BA4\u4E3A\u5728\u4E94\u901A\u7684\u4FDD\u4F51\u4E0B, \u5EFA\u660C\u4E00\u5730\u7684\u514D\u4E8E\u761F\u75AB: &quot;\u4E94\u901A\u4E4B\u4E3A\u795E, \u4E0D\u540D\u4E8E\u65E7\u6587, \u4E0D\u6B21\u4E8E\u5178\u7940, \u6B63\u4EBA\u541B\u5B50\u672A\u4E4B\u5C1D\u8A00, \u800C\u6709\u529F\u4E8E\u4E88, \u5176\u53EF\u4EE5\u5E9F?&quot; \u4F3C\u4E4E\u4E94\u901A\u662F\u4FDD\u4F51\u4E00\u65B9\u7684\u5730\u65B9\u795E. \u5357\u5B8B\u521D\u5E74\u7684\u6D2A\u8FC8\u5728\u300A\u5937\u575A\u5FD7\u300B\u4E2D\u8BB0\u8F7D\u4E86\u6570\u5341\u6761\u5173\u4E8E\u4E94\u901A\u7684\u4F20\u8BF4. \u5176\u4E2D\u4E94\u901A\u7684\u5F62\u8C61\u4EA6\u6B63\u4EA6\u90AA: \u65F6\u800C\u4F20\u8FBE\u4E0A\u5929\u7684\u65E8\u610F\u529D\u4EBA\u5411\u5584, \u65F6\u800C\u8D2A\u8D22\u597D\u8272\u56FE\u8C0B\u4E0D\u8F68. \u6731\u71B9\u56DE\u5FB5\u5DDE\u5A7A\u6E90\u796D\u7956\u65F6, \u5F53\u5730\u4E94\u901A\u5E99\u9999\u706B\u9F0E\u76DB. \u4EB2\u53CB\u90FD\u6731\u71B9\u524D\u53BB\u62DC\u8C12. \u6731\u71B9\u975E\u4F46\u62D2\u7EDD, \u8FD8\u76F4\u8A00 &quot;\u4EBA\u505A\u5DDE\u90E1, \u987B\u53BB\u6DEB\u7960.&quot;</p><p>\u6C11\u95F4\u56E0\u754F\u800C\u656C\u4E3A\u9B3C\u7269\u5EFA\u6DEB\u7960, \u671D\u5EF7\u5219\u51FA\u4E8E &quot;\u6C11\u6C14\u53EF\u7528&quot; \u7684\u7ACB\u573A, \u60F3\u5C06\u5B83\u7EB3\u5165\u5230\u56FD\u5BB6\u5B97\u6559\u4F53\u7CFB\u4E2D. \u5317\u5B8B\u5BA3\u548C\u4E94\u5E74, \u4E94\u901A\u53D7\u5C01\u5019\u7235, \u5C01\u53F7\u4E3A: &quot;\u901A\u8D36&quot; &quot;\u901A\u4F51&quot; &quot;\u901A\u6CFD&quot; &quot;\u901A\u60E0&quot; &quot;\u901A\u6D4E&quot;; \u5357\u5B8B\u4E7E\u9053\u4E09\u5E74, \u7ECF\u8FC7\u4E09\u6B21\u52A0\u5C01, \u4E94\u901A\u5C01\u81F3\u516B\u5B57\u4FAF; \u5357\u5B8B\u6DF3\u7199\u5143\u5E74, \u4E94\u901A\u53D7\u5C01\u516C\u7235, \u5C01\u53F7\u4E3A: &quot;\u663E\u5E94\u516C&quot; &quot;\u663E\u6D4E\u516C&quot; &quot;\u663E\u7950\u516C&quot; &quot;\u663E\u7075\u516C&quot; &quot;\u663E\u5B81\u516C&quot;; \u5357\u5B8B\u5E86\u5143\u516D\u5E74, \u7ECF\u8FC7\u4E09\u6B21\u52A0\u5C01, \u5C01\u81F3\u516B\u5B57\u516C; \u5357\u5B8B\u5609\u6CF0\u4E8C\u5E74, \u53D7\u5C01\u738B\u7235, \u66F0 &quot;\u663E\u806A\u738B&quot; &quot;\u663E\u660E\u738B&quot; &quot;\u663E\u6B63\u738B&quot; &quot;\u663E\u76F4\u738B&quot; &quot;\u663E\u5FB7\u738B&quot;; \u5357\u5B8B\u7ECD\u5B9A\u516D\u5E74, \u7ECF\u8FC7\u4E09\u6B21\u52A0\u5C01, \u5C01\u81F3\u516B\u5B57\u738B; \u5357\u5B8B\u6DF3\u7950\u56DB\u5E74, \u5C01\u4E3A &quot;\u4E94\u5723\u738B&quot;.</p><p>\u7136\u800C\u671D\u5EF7\u7684\u518C\u5C01\u5E76\u6CA1\u6709\u5B8C\u5168\u6D82\u62B9\u6389\u4E94\u901A\u7684\u90AA\u795E\u6C14\u8D28. \u5230\u4E86\u660E\u6E05\u4E94\u901A\u53C8\u4E8E\u5200\u5175\u5173\u8054\u8D77\u6765. \u300A\u7571\u9752\u65E5\u672D\u300B\u4E91: \u5373\u4E94\u901A\u795E\u4E5F. \u6216\u8C13\u660E\u592A\u7956\u5B9A\u5929\u4E0B, \u5C01\u529F\u81E3, \u68A6\u9635\u4EA1\u5175\u5352\u5343\u4E07\u8BF7\u6064. \u592A\u7956\u8BB8\u4EE5\u4E94\u4EBA\u4E3A\u4F0D. \u5904\u5904\u8840\u98DF. \u53C8\u6709\u4F20\u8BF4, \u4E94\u901A\u662F\u9A9A\u6270\u6C5F\u5357, \u70E7\u6740\u62A2\u63A0\u7684\u502D\u5BC7. \u6B64\u65F6\u4E94\u901A\u7684\u53C8\u6709\u4E86\u6A2A\u884C\u4E61\u91CE\u7684\u6076\u9B3C\u5F62\u8C61.</p><p>\u84B2\u677E\u9F84\u7684\u300A\u804A\u658B\u5FD7\u5F02\u300B\u6709\u4E00\u7BC7\u300A\u4E94\u901A\u300B\u7684\u6545\u4E8B. \u8BF4\u5434\u5730\u6709\u4E00\u4E2A\u5178\u5546\u53EB\u8D75\u5F18. \u59BB\u5B50\u960E\u6C0F\u957F\u5F97\u6F02\u4EAE. \u7A81\u7136\u6709\u4E00\u591C &quot;\u6709\u4E08\u592B\u5CB8\u7136\u81EA\u5916\u5165&quot;. \u53F7\u79F0 &quot;\u4E94\u901A\u795E\u56DB\uF92C&quot;, \u5C06\u960E\u6C0F &quot;\u62B1\u8170\u5982\u5A74\u513F, \u7F6E\u5E8A\u4E0A, \u88D9\u5E26\u81EA\u8131, \u9042\u72CE\u4E4B&quot;. \u4E8B\u540E\u6765\u76F8\u7EA6\u4E94\u65E5\u540E\u590D\u6765. \u8D75\u5F18\u77E5\u9053\u4E94\u901A, \u4E0D\u6562\u95EE. \u7ED3\u679C\u4E94\u65E5\u540E\u8FD9\u56DB\uF92C\u53C8\u5E26\u4E86\u4E24\u4EBA\u6765, &quot;\u6216\u547C\u5927\u5144, \u6216\u547C\u4E09\u5F1F&quot;. \u4EE5\u81F3\u4E8E\u960E\u6C0F &quot;\u8840\u6DB2\u6D41\u79BB, \u660F\u4E0D\u77E5\u4EBA, \u56DB\uF92C\u59CB\u53BB&quot;. \u960E\u6C0F\u7F9E\u6124, \u60F3\u4E0A\u540A. \u4F46\u6BCF\u6B21\u4E0A\u540A, &quot;\u6295\u7F33\u5219\u81EA\u7EDD&quot;, &quot;\u82E6\u4E0D\u5F97\u6B7B&quot;. \u8D75\u5F18\u6709\u4E00\u4E2A\u8868\u5F1F\u53EB\u4E07\u751F, &quot;\u521A\u731B\u5584\u5C04&quot;. \u5230\u8D75\u5F18\u5BB6\u505A\u5BA2, \u6B63\u8D76\u4E0A &quot;\u56DB\uF92C&quot; \u6765\u627E\u960E\u6C0F. \u4E07\u751F &quot;\u6349\u5200\u6F5C\u89C6\u4E4B&quot;, &quot;\u5954\u800C\u5165&quot;, \u628A\u56DB\uF92C\u780D\u7FFB\u5728\u5730. \u56DB\uF92C\u73B0\u51FA\u539F\u5F62, \u662F\u53EA\u5C0F\u9A6C. \u960E\u6C0F\u8FD9\u624D\u7ED9\u4E07\u751F\u8BF4\u4E86\u6765\u9F99\u53BB\u8109, \u4E14\u8BF4: &quot;\u8BF8\u795E\u5C06\u81F3, \u4E3A\u4E4B\u5948\u4F55?&quot; \u4E07\u751F &quot;\u706D\u70DB\u53D6\u5F13\u77E2, \u4F0F\u6697\u4E2D&quot;. &quot;\u672A\u51E0, \u6709\u56DB\u4E94\u4EBA\u81EA\u7A7A\u98DE\u5815&quot;. \u88AB\u4E07\u751F\u5C04\u6B7B\u4E00\u4E2A, \u780D\u6B7B\u4E00\u4E2A. \u5269\u4E0B\u7684\u9003\u8D70\u4E86. \u70B9\u4E0A\u706F\u770B, \u539F\u6765\u662F\u4E24\u53EA\u732A. \u4ECE\u6B64\u4E07\u751F\u51FA\u4E86\u540D. \u53C8\u6709\u4EBA\u5BB6\u6709\u672A\u5AC1\u7684\u5973\u513F\u88AB\u4E94\u901A\u9A9A\u6270, \u542C\u8BF4\u4E86\u4E07\u751F\u4FBF\u8BF7\u4ED6\u53BB\u5E2E\u5FD9. \u7B49\u4E94\u901A\u6765\u65F6, &quot;\u89C1\u4E07, \u53CD\u8EAB\u800C\u5954, \u4E07\u8FFD\u51FA, \u4F46\u89C1\u9ED1\u6C14\u6B32\u98DE, \u4EE5\u5200\u8DC3\u6325\u4E4B, \u65AD\u4E00\u8DB3, \u5927\u55E5\u800C\u53BB.&quot; \u780D\u4E0B\u7684\u4E1C\u897F &quot;\u5DE8\u722A\u5982\u624B\u4E0D\u77E5\u4F55\u7269&quot;. \u4ECE\u6B64\u4E4B\u540E, \u53EA\u8981\u6709\u4EBA\u5BB6\u95F9\u4E94\u901A, \u90FD\u62DC\u8BF7\u4E07\u751F &quot;\u4E00\u5BBF\u5176\u5BB6&quot;. \u8FD9\u7BC7\u6545\u4E8B\u4E4B\u540E, \u53C8\u8DDF\u4E86\u8865\u4E86\u4E00\u4E2A\u7BC7, \u9898\u540D\u4E3A\u300A\u53C8\u300B, \u8BB2\u5199\u91D1\u9F99\u5927\u738B\u5973\u513F\u7684\u4FFE\u5973\u9609\u5272\u4E00\u901A\u7684\u6545\u4E8B, \u5E76\u5728\u7BC7\u672B\u8BF4: &quot;\u82E5\u5728\u4E07\u751F\u7528\u6B66\u4E4B\u540E, \u5219\u5434\u4E0B\u53EA\u4F59\u534A\u901A, \u5B9C\u5176\u4E0D\u8DB3\u5371\u5BB3.&quot;</p><p>\u81EA\u7136, \u4E94\u901A\u5E76\u4E0D\u50CF\u804A\u658B\u6240\u8BF4 &quot;\u4E0D\u8DB3\u5371\u5BB3&quot;, \u4E8B\u5B9E\u6B63\u597D\u76F8\u53CD, \u4E94\u901A\u88AB\u7EB3\u5165\u5230\u66F4\u590D\u6742\u7684\u6C11\u95F4\u4FE1\u4EF0\u4F53\u7CFB\u4E2D. \u660E\u4EE3\u5B66\u8005\u9646\u7CB2\u7684\u300A\u5E9A\u5DF3\u7F16\u300B\u4E2D\u8BF4 &quot;\u5434\u4FD7\u6240\u5949\u5996\u795E, \u53F7\u4E94\u5723, \u53C8\u66F0\u4E94\u663E\u7075\u516C, \u4E61\u6751\u547C\u4E3A\u4E94\uF92C\u795E, \u76D6\u6DF1\u5C71\u8001\u9B45, \u5C71\u9B48, \u6728\u5BA2\u4E4B\u7C7B\u4E5F&quot;. \u8FD8\u8BB0\u5F55\u4E86\u5F53\u65F6\u4E94\u901A\u4FE1\u4EF0\u7684\u5B97\u6559\u4EEA\u5F0F -- \u8336\u7B73: &quot;\u4E94\u9B45\u7686\u79F0\u4FAF\u738B, \u5176\u725D\u79F0\u592B\u4EBA, \u6BCD\u79F0\u592A\u592B\u4EBA, \u53C8\u66F0\u592A\u5988, \u6C11\u754F\u4E4B\u751A ...... \u796D\u5219\u6742\u4EE5\u89C2\u97F3, \u57CE\u968D, \u571F\u5730\u4E4B\u795E\u522B\u796D\u9A6C\u4E0B\u662F\u4E3A\u4ECE\u5B98 ...... \u5DEB\u7686\u53F9\u6B4C, \u8F9E\u9053\u795E\u4E4B\u51FA\u5904, \u4E91\u795E\u542C\u4E4B\u5219\u4E50, \u8C13\u4E4B &#39;\u8336\u7B73&#39;, \u5C24\u76DB\u8005\u66F0\u70E7\u7EB8, \u867D\u58EB\u5927\u592B\u5BB6\u7686\u7136, \u5C0F\u6C11\u7AED\u4EA7\u4EE5\u4ECE\u4E8B, \u81F3\u79F0\u8D37\u4E3A\u4E4B .......&quot; \u53EF\u89C1\u81F3\u5C11\u5230\u660E\u4EE3\u65F6, \u5728\u6C11\u95F4\u7684\u4FE1\u4EF0\u4F53\u7CFB\u91CC, \u4E0D\u4F46\u4E94\u901A\u6709\u4E86\u914D\u5076\u6BCD\u4EB2, \u751A\u81F3\u796D\u4EEA\u4E2D\u89C2\u97F3, \u57CE\u968D, \u571F\u5730\u7B49\u7ADF\u6210\u4E86\u4ECE\u7940\u4E4B\u795E. \u800C\u4E14\u9646\u7CB2\u6240\u8BF4\u7684\u4EEA\u5F0F, \u76F4\u5230\u4ECA\u5929\u5927\u4F53\u5982\u6B64: \u5177\u4F53\u6765\u8BF4, \u5C31\u662F\u82CF\u5DDE\u4E0A\u65B9\u5C71\u7684\u592A\u59E5\u4FE1\u4EF0. \u6240\u8C13\u592A\u59E5\u5373\u662F\u9646\u7CB2\u6240\u8BF4\u4E94\u901A\u7684\u6BCD\u4EB2, \u6709\u592A\u59E5\u4FE1\u4EF0\u7684\u5BB6\u5EAD, \u5982\u6709\u707E\u6709\u75C5\u5C31\u4F1A\u8BF7\u5E08\u5A18 ( \u5373\u6C11\u95F4\u7684\u795E\u5A46 ) \u6765\u5BB6\u4E2D &quot;\u5BA3\u5377&quot;, \u6240\u8C13\u5BA3\u5377\u7684\u4EEA\u5F0F\u5373\u662F &quot;\u5DEB\u90FD\u53F9\u6B4C, \u8F9E\u9053\u795E\u4E4B\u51FA\u5904&quot;. \u800C\u6240\u5BA3\u4E4B\u5377\u540D\u4E3A \u300A\u592A\u59E5\u5B9D\u5377\u300B. \u519C\u5386\u7684\u516B\u6708\u5341\u4E94\u88AB\u8BA4\u4E3A\u662F\u4E94\u901A\u7684\u751F\u65E5. \u5230\u8FD9\u4E00\u65E5, \u53C8\u6709\u9646\u7CB2\u6240\u8C13 &quot;\u70E7\u7EB8&quot; \u7684\u4EEA\u5F0F, \u5411\u4E94\u901A &quot;\u79F0\u8D37&quot;. \u8FD9\u4E2A\u4EEA\u5F0F, \u73B0\u5728\u53EB\u505A &quot;\u501F\u9634\u503A&quot;.</p><p>&quot;\u8F9E\u9053\u795E\u4E4B\u51FA\u5904&quot; \u7684\u300A\u592A\u59E5\u5B9D\u5377\u300B\u8BB2\u8FF0\u4E86\u4E94\u901A\u7684\u6765\u5386\u548C\u505A\u4E3A, \u8BF4\u592A\u59E5\u672C\u662F\u5982\u6765\u5EA7\u4E0B\u8718\u86DB\u7CBE, \u52A8\u4E86\u51E1\u5FC3\u4E0B\u51E1\u4E0E\u4E00\u540D\u8427\u5458\u5916\u7ED3\u5408\u751F\u4E0B\u4E00\u4E2A\u8089\u7403. \u8089\u7403\u88AB\u795E\u4ED9\u6551\u53BB\u5256\u51FA\u4E00\u80CE\u4E94\u80DE\u5144\u5F1F,. \u592A\u59E5\u56E0\u6000\u4E94\u901A, &quot;\u517B\u7A7A\u4E86\u809A\u76AE&quot;, \u8981\u6BCF\u5929\u5728\u5BB6\u5403\u7AE5\u7537\u7AE5\u5973, \u7EC8\u4E8E\u88AB\u9F99\u6811\u6CD5\u738B\u6444\u53BB\u4E30\u90FD\u5341\u516B\u5C42\u5730\u72F1\u53D7\u82E6. \u4E94\u901A\u5B66\u827A\u5F52\u6765, \u4ECE\u5730\u72F1\u4E2D\u6551\u6BCD\u51FA\u6765, \u53C8\u53BB\u5929\u4E0A\u5077\u4ED9\u6843\u56DE\u6765\u6CBB\u4E86\u592A\u59E5\u8981\u5403\u4EBA\u7684\u6BDB\u75C5. \u738B\u6BCD\u53D1\u73B0\u5077\u6843, \u8981\u9886\u5175\u8BA8\u4F10\u4E94\u901A, \u89C2\u97F3\u89E3\u529D, \u7389\u5E1D\u7F5A\u592A\u59E5\u6BCD\u5B50\u516D\u4EBA\u51FA\u6276\u6851\u56FD\u6C89\u9999\u6811\u4E0A\u5C45\u4F4F. \u540E\u6765\u6709\u4E00\u9F9F\u5C71\u6C34\u6BCD\u5728\u6CD7\u6D32\u4F5C\u602A, \u88AB\u89C2\u97F3\u6536\u670D\u5728\u82CF\u5DDE\u4E0A\u65B9\u5C71. \u89C2\u97F3\u53BB\u6276\u6851\u5411\u592A\u59E5\u501F\u6C89\u9999\u5EFA\u5854\u501F\u6C89\u9999, \u9020\u5854\u9547\u538B\u6C34\u6BCD, \u592A\u59E5\u4E8E\u4E94\u901A\u968F\u89C2\u97F3\u6765\u82CF\u5DDE\u4E00\u540C\u9547\u538B\u6C34\u6BCD.</p><p>\u8981\u662F\u719F\u6089\u4E2D\u56FD\u795E\u9B54\u5C0F\u8BF4\u7684, \u770B\u300A\u592A\u59E5\u5B9D\u5377\u300B, \u603B\u89C9\u5F97\u4F3C\u66FE\u76F8\u8BC6. \u6545\u4E8B\u7684\u524D\u534A\u6BB5\u57FA\u672C\u4E0E\u4E00\u672C\u53EB\u300A\u4E94\u663E\u7075\u5B98\u5927\u5E1D\u534E\u5149\u5929\u738B\u4F20\u300B\u5927\u4F53\u76F8\u540C. \u8FD9\u672C\u4E66\u53C8\u79F0\u300A\u5357\u6E38\u8BB0\u300B, \u4E66\u4E2D\u5C06\u706B\u795E\u738B\u7075\u5B98, \u534E\u5149\u83E9\u8428, \u4E94\u901A\u7B49\u5F62\u8C61\u7ED3\u5408\u5728\u4E00\u8D77, \u62FC\u63A5\u4E86\u897F\u6E38\u6545\u4E8B, \u76EE\u8FDE\u6551\u6BCD\u6545\u4E8B\u7B49\u6C11\u95F4\u4F20\u8BF4, \u610F\u56FE\u5B9E\u73B0\u4E94\u901A\u7531\u90AA\u795E\u5230\u6B63\u795E\u7684\u51C0\u5316.</p><p>\u4E94\u901A\u8FD8\u4E0E\u897F\u6E38\u6545\u4E8B\u6709\u4E9B\u5173\u8054. \u660E\u521D\u5BAB\u5EF7\u6742\u5267\u4F5C\u5BB6\u6768\u666F\u8D24\u7684\u6742\u5267\u300A\u897F\u6E38\u8BB0\u300B, \u5267\u4E2D\u7B2C\u516B\u51FA\u9898\u4E3A &quot;\u534E\u5149\u7F72\u4FDD&quot;, \u8BB2\u89C2\u97F3\u5411\u7389\u5E1D\u4E3E\u8350\u5510\u50E7\u53D6\u7ECF\u8DEF\u4E0A\u7684\u5341\u4F4D\u4FDD\u5B98, \u5176\u4E2D\u534E\u5149\u4E00\u4EBA\u6F14\u5531\u516B\u53EA\u66F2\u5B50, \u4E0D\u4EC5\u5BA3\u544A\u4E86\u81EA\u5DF1\u7684\u706B\u795E\u8EAB\u4EFD, \u4E0D\u5FD8\u81EA\u8BE9 &quot;\u8C01\u4EBA\u4E0D\u77E5\u4E94\u663E\u9AD8\u5F3A&quot;. \u6709\u5B66\u8005\u6307\u51FA: &quot;\u534E\u5149\u672C\u6765\u4E0E\u897F\u6E38\u6545\u4E8B\u6BEB\u65E0\u74DC\u845B, \u6768\u666F\u8D24\u504F\u8981\u8282\u5916\u751F\u679D, \u5728\u5510\u50E7\u884C\u5C06\u767B\u7A0B\u7684\u7D27\u8981\u5173\u5934\u6309\u4E0B\u53D6\u7ECF\u6545\u4E8B\u4E0D\u8868, \u5374\u5C06\u6BEB\u4E0D\u76F8\u5E72\u7684\u534E\u5149\u62C9\u5165\u5267\u4E2D, \u8BA9\u4ED6\u4EE5\u706B\u795E\u4FE1\u4EF0\u7684\u8EAB\u4EFD\u5927\u5439\u5927\u64C2, \u51FA\u8DB3\u4E86\u98CE\u5934, \u7136\u540E\u626C\u957F\u800C\u53BB; \u6B64\u540E\u76F4\u81F3\u5267\u7EC8, \u518D\u4E0D\u89C1\u4ED6\u7684\u8EAB\u5F71 ...... \u82E5\u4E86\u89E3\u8FD9\u662F\u5FA1\u7528\u6587\u4EBA\u5229\u7528\u4E00\u4E2A\u58F0\u540D\u663E\u8D6B\u7684\u4F20\u7EDF\u795E\u4F5B\u6545\u4E8B ( &#39;\u5510\u50E7\u53D6\u7ECF&#39; ) \u6765\u62AC\u4E3E\u4E00\u4F4D\u65B0\u795E, \u4E3A\u5F53\u4ECA\u7687\u4E0A\u6240\u75F4\u8FF7\u7684\u706B\u795E\u5D07\u62DC\u63A8\u6CE2\u9020\u52BF, \u5BFB\u627E\u6839\u636E, \u5219\u6768\u666F\u8D24\u6B64\u4E3E, \u53C8\u662F\u5341\u5206\u6B63\u5E38\u7684.&quot;</p><p>\u4E0D\u8BBA\u6587\u4EBA\u5982\u4F55\u6D82\u62B9\u4E94\u901A\u7684\u90AA\u6027, \u4E94\u901A\u5728\u6C11\u95F4\u7684\u5F62\u8C61\u4E00\u76F4\u6B63\u90AA\u96BE\u5206. \u6216\u8005\u66F4\u76F4\u767D\u7684\u8BF4, \u4E94\u901A\u4F9D\u7136\u5927\u90E8\u5206\u662F\u90AA\u795E\u7684\u5F62\u8C61. \u81F3\u4ECA\u82CF\u5DDE\u8FD8\u6709 &quot;\u4E0A\u65B9\u5C71\u7684\u9634\u503A\u8FD8\u4E0D\u6E05&quot; \u7684\u8BF4\u6CD5. \u636E\u8BF4\u5982\u679C\u53BB\u4E0A\u65B9\u5C71\u5411\u4E94\u901A\u501F\u4E86\u9634\u501F, \u90A3\u5C31\u8981\u6BCF\u5E74\u7684\u519C\u5386\u516B\u6708\u5341\u4E94\u5230\u4E0A\u65B9\u5C71\u4F9B\u5949\u9999\u70DB\u94B1\u7CAE &quot;\u8FD8\u9634\u503A&quot;, \u800C\u8FD9\u9634\u503A\u5F80\u5F80\u4E00\u8F88\u5B50\u90FD\u8FD8\u4E0D\u6E05, \u8FD8\u8981\u7D2F\u53CA\u5B50\u5B59\u540E\u4EE3, \u8BA9\u5B50\u5B59\u540E\u4EE3\u63A5\u7740\u8FD8.</p><p>\u6709\u8DA3\u7684\u662F, \u6211\u4E3A\u5199\u8FD9\u7BC7\u6545\u4E8B\u67E5\u627E\u8D44\u6599\u65F6, \u7FFB\u5230\u4E86\u82CF\u5DDE\u672C\u5730\u7684\u8BBA\u575B. \u6709\u4E00\u7BC7\u5E16\u5B50\u662F\u5728\u95EE\u8981\u4E0D\u8981\u53BB\u4E0A\u65B9\u5C71\u501F\u9634\u503A. \u4E0B\u8FB9\u7ADF\u6709\u4EBA\u56DE\u5E16, \u8BF4\u53CD\u6B63\u81EA\u5DF1\u4E5F\u4E0D\u6253\u7B97\u7ED3\u5A5A\u751F\u80B2\u4E86, \u7D22\u6027\u4E5F\u4E0D\u5FC5\u7BA1\u4EC0\u4E48\u7978\u53CA\u5B50\u5B59, \u53BB\u501F\u9634\u503A\u7F62. \u770B\u5230\u8FD9\u91CC, \u6211\u8111\u5B50\u91CC\u4E00\u4E0B\u5B50\u5C31\u8DF3\u51FA\u4E00\u4E2A\u8BCD\u6765: &quot;\u7EDD\u6237\u8BA1&quot;. \u8BF4\u662F\u592A\u76D1\u505A\u4E8B\u5F80\u5F80\u5FC3\u72E0\u624B\u8FA3\u4E0D\u7559\u540E\u8DEF, \u5C31\u662F\u56E0\u4E3A\u81EA\u5DF1\u662F &quot;\u7EDD\u6237&quot;, \u4E0D\u5FC5\u53BB\u60F3\u5B50\u5B59\u540E\u4EE3\u7684\u4E8B. \u5E16\u5B50\u4E2D\u8BF4\u8981\u53BB\u501F\u9634\u503A\u7684, \u601D\u8003\u8FD9\u4E8B\u65F6\u7684\u8111\u56DE\u8DEF, \u548C &quot;\u7EDD\u6237&quot; \u4E86\u7684\u592A\u76D1\u5982\u51FA\u4E00\u8F99. \u4F46\u4ED4\u7EC6\u60F3\u60F3, \u81EA\u53E4\u4EE5\u6765, \u53BB\u501F\u9634\u503A\u7684, \u4E5F\u90FD\u662F\u90A3\u4E9B\u8D70\u5934\u65E0\u8DEF\u7684\u4EBA, \u591A\u6570\u4E5F\u6CA1\u6709\u5A36\u59BB\u751F\u5B50\u7684\u6253\u7B97. \u800C\u90A3\u4E9B\u5178\u578B\u7684\u501F\u9634\u503A\u7684\u60B2\u5267\u6545\u4E8B, \u4E5F\u5F80\u5F80\u662F\u8BB2, \u4E00\u4E2A\u7A77\u5230\u8981\u7EDD\u6237\u7684\u4EBA, \u501F\u9634\u503A\u53D1\u4E86\u5BB6, \u5A36\u59BB\u751F\u5B50, \u800C\u540E\u53C8\u56E0\u8FD8\u9634\u503A\u800C\u88AB\u903C\u5F97\u5BB6\u7834\u4EBA\u4EA1.</p><p>\u636E\u8BF4\u501F\u9634\u503A\u7684\u4EEA\u5F0F\u5982\u4E0B: \u9EC4\u660F\u65F6, \u4E00\u540D\u5DEB\u5A46\u711A\u9999\u53E9\u795E\u6BD5, \u7531\u4E24\u540D\u5DEB\u5A46\u631F\u6301, \u75BE\u9A70\u4E0B\u5C71, \u540D\u53EB &quot;\u8DD1\u9A6C&quot;. \u81F3\u5C71\u4E0B\u70B9\u71C3\u706F\u70DB\u5904, \u5979\u6545\u4F5C\u98A0\u53A5, \u4E94\u901A\u8001\u7237\u4E0A\u8EAB\u4E86. \u65BC\u662F\u6492\u6CFC\u8C29\u9A82, \u541E\u5403\u71C3\u70BD\u7684\u8721\u70DB, \u663E\u793A\u795E\u5A01, \u4EE5\u5E7F\u4E3A\u62DB\u5F95. \u5404\u4F4D\u770B\u5B98, \u4E0D\u77E5\u9053\u662F\u4E0D\u662F\u8FD8\u8BB0\u6545\u4E8B\u5F00\u7BC7\u6211\u5199\u7684\u5916\u516C\u8001\u5BB6 &quot;\u8BF7\u4E94\u7237&quot; \u7684\u98CE\u4FD7, \u4E0E\u501F\u9634\u503A\u5927\u540C\u5C0F\u5F02. \u6211\u5916\u516C\u8001\u5BB6\u5728\u9655\u897F, \u53EF\u89C1\u4E94\u901A\u4FE1\u4EF0\u5730\u57DF\u4E4B\u5E7F.</p><p>\u6211\u4E0D\u77E5\u9053\u88AB\u9B3C\u795E\u4E0A\u8EAB\u662F\u4EC0\u4E48\u611F\u89C9, \u4F46 &quot;\u501F\u9634\u503A&quot; \u7684\u63CF\u5199\u5374\u8BA9\u6211\u60F3\u5230\u300A\u767D\u9E7F\u539F\u300B\u4E2D\u7684\u4E00\u6BB5\u6545\u4E8B. \u6C11\u56FD\u653F\u5E9C\u5BF9\u767D\u9E7F\u539F\u7684\u519C\u6C11\u8BFE\u91CD\u7A0E, \u767D\u9E7F\u4E61\u6C11\u4E0D\u6EE1, \u767D\u5609\u8F69\u540C\u5F90\u5148\u751F\u9F13\u52A8\u767E\u59D3\u5C06\u519C\u5177\u4EA4\u5230\u5177\u5E9C, \u7F62\u79CD\u7F62\u6536, \u662F\u4E3A &quot;\u4EA4\u519C&quot;. \u5230\u4E86\u4EA4\u519C\u5F53\u5929, \u6C11\u56FD\u653F\u5E9C\u7684\u5B98\u5458\u7ECA\u4F4F\u767D\u5609\u8F69, \u8131\u8EAB\u4E0D\u5F97. \u773C\u770B\u4EA4\u519C\u52A8\u8FD0\u5C31\u8981\u5931\u8D25, \u767D\u5BB6\u7684\u957F\u5DE5\u9E7F\u4E09\u7A81\u7136\u7AD9\u51FA\u6765\u505A\u4E86\u4EA4\u519C\u7684\u9886\u8896. \u519C\u4EBA\u4EEC\u89C1\u6709\u4E86\u9886\u8896, \u6512\u62E5\u7740\u62AC\u8D77\u9E7F\u4E09, \u6765\u5230\u53BF\u57CE\u57CE\u5899\u4E0B, \u62C6\u5899\u6252\u7816, \u903C\u7740\u53BF\u957F\u5E9F\u9664\u4E86\u65B0\u7A0E. \u4E66\u4E2D\u5199\u9E7F\u4E09, \u88AB\u4F17\u4EBA\u67B6\u8D77\u6765\u7684\u65F6\u5019, \u7A81\u7136\u6709\u4E00\u79CD\u5947\u7279\u7684\u4E0D\u771F\u5B9E\u7684\u611F\u89C9, \u597D\u50CF\u88AB\u4F17\u4EBA\u67B6\u8D77\u7684\u4E0D\u662F\u9E7F\u4E09, \u800C\u662F\u767D\u5609\u8F69. \u5BC2\u5BC2\u65E0\u540D\u7684\u82B8\u82B8\u5927\u4F17, \u5728\u5145\u6EE1\u4EEA\u5F0F\u611F\u7684\u7FA4\u4F53\u8FD0\u52A8\u4E2D, \u5927\u6982\u90FD\u6709\u8FD9\u79CD &quot;\u9B3C\u4E0A\u8EAB&quot; \u7684\u611F\u89C9. \u4E00\u65F6\u597D\u50CF\u4E00\u5207\u90FD\u4E0D\u771F\u5B9E\u7684\u5E7B\u8C61, \u81EA\u5DF1\u5DF2\u4E0D\u518D\u662F\u81EA\u5DF1; \u4E00\u65F6\u53C8\u597D\u50CF\u8FD9\u624D\u662F\u771F\u6B63\u771F\u5B9E\u7684\u751F\u6D3B, \u81EA\u5DF1\u6C89\u5BC2\u7684\u65E5\u5E38\u53EA\u662F\u4E9B\u65E0\u5173\u7D27\u8981\u7684\u9501\u4E8B.</p><p>\u6211\u4E0D\u7981\u60F3\u95EE: \u4E00\u4E2A\u5510\u65F6\u67F3\u5DDE\u7684\u9B3C\u7269, \u662F\u600E\u6837\u7ECF\u8FC7\u5343\u767E\u5E74\u7684\u53D1\u5C55\u5728\u6C11\u95F4\u53D1\u5C55\u6210\u5982\u6B64\u590D\u6742\u7684\u4FE1\u4EF0 -- \u5176\u5730\u57DF\u4E4B\u5E7F, \u81EA\u6C5F\u5357\u76F4\u81F3\u897F\u5317; \u5176\u5730\u4F4D\u4E4B\u9AD8, \u89C2\u97F3\u57CE\u968D\u4E3A\u5176\u4ECE\u7940. \u6211\u4EEC\u5927\u6982\u53EF\u4EE5\u770B\u5230\u4E24\u6761\u8109\u843D: \u4E00\u6761\u5728\u6C11\u95F4, \u4E00\u6761\u5728\u671D\u5EF7.</p><p>\u5728\u6C11\u95F4, \u5BF9\u4E8E\u65E0\u6240\u656C\u754F\u7684 &quot;\u7EDD\u6237&quot; \u4EEC, \u4E94\u901A\u662F\u6765\u504F\u8D22\u7684\u6377\u8FD1. \u53EA\u8981\u5728\u90A3\u5145\u6EE1\u4EEA\u5F0F\u611F\u7684\u7FA4\u4F53\u8FD0\u52A8\u4E2D, \u4E3E\u8D77\u4E94\u901A\u4E0A\u8EAB\u7684\u5DEB\u5A46\u795E\u6C49, \u5C31\u80FD\u501F\u6765\u9634\u503A. \u56FA\u7136 &quot;\u4E0A\u65B9\u5C71\u7684\u9634\u503A\u8FD8\u4E0D\u6E05&quot;, \u4F46\u7EDD\u6237\u6C49\u4EEC\u53C8\u600E\u4F1A\u60F3\u5230\u5B50\u5B59\u7684\u798F\u6CFD. \u5BF9\u4E8E\u8001\u5B9E\u7684\u4EBA\u5BB6, \u6CA1\u6709\u4E07\u751F\u90A3\u6837 &quot;\u521A\u731B\u5584\u5C04&quot; \u7684\u8868\u5F1F, \u5927\u6982\u53EA\u80FD\u5728\u4E94\u901A\u7684\u6DEB\u5A01\u4E0B &quot;\u4E0D\u6562\u95EE&quot; \u4E86. \u5F53\u7136\u4E5F\u6709\u901A\u8FC7\u548C\u4E94\u901A\u6500\u626F\u4E0A\u5173\u7CFB\u6765\u9632\u6B62\u707E\u7978\u7684. \u5728\u592A\u59E5\u4FE1\u4EF0\u4E2D\u6709\u4E00\u79CD\u4EEA\u5F0F, \u53EB &quot;\u5BC4\u540D&quot;. \u662F\u62DC\u592A\u59E5\u4E3A\u5E72\u5988, \u8FD9\u6837\u5728\u67D0\u79CD\u610F\u4E49\u4E0A\u5C31\u6210\u4E86\u4E94\u901A\u7684\u5144\u5F1F, \u770B\u8D77\u6765\u4F3C\u4E4E\u4E5F\u5C31\u80FD\u907F\u514D\u707E\u7978\u4E86.</p><p>\u5BF9\u671D\u5EF7, \u6700\u521D\u53EA\u662F\u51FA\u4E8E &quot;\u6709\u529F\u4E8E\u4E88, \u4F55\u53EF\u4EE5\u5E9F&quot; \u7684\u539F\u56E0, \u7ED9\u4E8E\u6C11\u95F4\u4FE1\u4EF0\u4E00\u5B9A\u7684\u5B98\u65B9\u80CC\u4E66, \u7ED3\u679C\u5374\u4F7F\u4E94\u901A\u5728\u6C11\u95F4\u8D8A\u4F20\u8D8A\u5E7F. \u52A0\u4E0A\u8FD8\u6709\u6768\u666F\u8D24\u8FD9\u6837\u8FCE\u5949\u4E0A\u610F\u7684\u5FA1\u7528\u77E5\u8BC6\u5206\u5B50\u6DFB\u6CB9\u52A0\u918B, \u4F7F\u5F97\u4E94\u901A\u4FE1\u4EF0\u7406\u8BBA\u5316\u4F53\u7CFB\u5316. \u7EC8\u4E8E, \u6700\u540E\u8FDE\u89C2\u97F3\u57CE\u968D\u4E4B\u7C7B\u6B63\u7EDF\u4FE1\u4EF0\u4E2D\u7684\u795E\u4ED9\u53CD\u6210\u4E86\u4E94\u901A\u7684\u4ECE\u7940.</p><p>\u4E00\u5C0F\u90E8\u5206\u7684\u4EBA\u533A\u533A\u5C0F\u5229, \u53CA\u81F3\u800C\u4ECA\u5982\u6B64, \u8FD8\u771F\u5E94\u4E86\u90A3\u53E5: \u4E0A\u65B9\u5C71\u7684\u9634\u503A\u8FD8\u4E0D\u6E05.</p><h2 id="\u4EBA\u7684\u5C0A\u4E25" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u7684\u5C0A\u4E25" aria-hidden="true">#</a> \u4EBA\u7684\u5C0A\u4E25</h2><p>\u5BF9\u88F9\u631F\u5728\u4E94\u901A\u4E2D\u7684\u7EDD\u6237\u548C\u671D\u5EF7\u9A82\u53E5 &quot;\u4E0A\u65B9\u5C71\u7684\u9634\u503A\u8FD8\u4E0D\u6E05&quot;, \u4F3C\u4E4E\u4E5F\u6709\u4E86\u9A82 &quot;\u59CB\u4F5C\u4FD1\u8005, \u5176\u65E0\u540E\u4E4E&quot; \u7684\u592B\u5B50\u7684\u6C14\u9B44, \u597D\u50CF\u592B\u5B50\u4E0A\u8EAB\u4E86\u4E00\u6837. \u4F46\u8FD9\u8FC7\u763E\u662F\u8FC7\u763E, \u5374\u6CA1\u4EC0\u4E48\u7528. \u505A\u51FA\u8FD9\u4E9B\u5BB6\u4F19, \u5927\u6982\u4E0D\u5728\u4E4E\u7978\u53CA\u5B50\u5B59. \u800C\u4E14, \u6709\u65F6\u4E0E\u4E94\u901A\u6253\u4EA4\u9053\u7684, \u4E5F\u4E0D\u662F\u51FA\u4E8E\u81EA\u613F. \u6BD4\u5982\u53F0\u6E7E\u7684\u5C0F\u9B4F\u4E00\u5BB6. \u4ED6\u8BE5\u5982\u4F55\u529E\u5462?</p><p>\u597D\u5427, \u8BA9\u6211\u4EEC\u4E66\u5F52\u6B63\u6587, \u8BF4\u56DE\u90FD\u5E02\u4F20\u8BF4.</p><p>\u600E\u4E48\u529E? \u6CA1\u529E\u6CD5. \u7406\u5316\u8001\u5E08\u8BD5\u7740\u7528\u5404\u79CD\u529E\u6CD5\u4E0E\u4E94\u901A\u8C08\u5224, \u7ED3\u679C\u90FD\u5931\u8D25\u4E86. \u5C0F\u9B4F\u81EA\u5DF1\u4E5F\u8BD5\u7740\u4ECE\u5178\u85C9\u4E2D\u5BFB\u627E\u529E\u6CD5, \u4E5F\u4E00\u65E0\u6240\u83B7. \u4ED6\u5F97\u51FA\u7ED3\u8BBA &quot;\u770B\u4E45\u4E86\u5C31\u660E\u767D, \u90FD\u53EA\u662F\u5728\u9A97\u81EA\u5DF1. \u90FD\u662F\u53EA\u5728\u6321\u90A3\u4E9B\u9ED1\u6697\u7684\u7F3A\u53E3. \u628A\u65E0\u6CD5\u7406\u89E3\u7684\u4E1C\u897F\u76D6\u4E2A\u5E99\u88C5\u8D77\u6765, \u518D\u5B89\u4E2A title \u6216\u4EBA\u95F4\u7684\u5B98\u804C, \u5C31\u53EF\u4EE5\u62CD\u62CD\u624B\u8DDF\u4E61\u6C11\u4EEC\u8BF4, \u6CA1\u4E8B\u4E86, \u6CA1\u4E8B\u4E86, \u56DE\u53BB\u5E72\u6D3B\u5427. \u4ED6\u4F1A\u4FDD\u4F51\u5730\u65B9\u7684.&quot;</p><p>\u65E5\u5B50\u4E00\u5929\u5929\u8FC7\u53BB, \u7EC8\u4E8E\u5230\u4E86\u4E8B\u60C5\u53D1\u751F\u7684\u90A3\u4E00\u5929. \u90A3\u4E00\u5929, \u4ED6\u8FC7\u9A6C\u8DEF\u7684\u65F6\u5019\u624B\u673A\u6ED1\u4E86\u4E00\u4E0B\u5C31\u88AB\u538B\u70C2\u4E86. \u7136\u540E\u4E00\u6574\u5929\u90FD\u89C9\u5F97\u5FC3\u6D6E\u6C14\u8E81. \u7B49\u5230\u665A\u4E0A\u4E0B\u73ED\u56DE\u5230\u5BB6\u91CC, \u4ED6\u5728\u697C\u4E0B\u6309\u4E86\u95E8\u94C3.</p><p>\u95E8\u5F00\u4E86, \u91CC\u8FB9\u6CA1\u4EBA.</p><p>\u4ED6\u9760\u76F4\u89C9\u5C31\u77E5\u9053, \u5AB3\u5987\u8981\u751F\u4E86. \u7136\u540E\u4ED6\u4E5F\u77E5\u9053, \u5BB6\u91CC\u8FD8\u6709\u5176\u5B83\u4E1C\u897F. \u90A3\u4E00\u77AC\u95F4, \u4ED6\u5206\u5916\u6E05\u9192, \u4F3C\u4E4E\u4ECE\u6765\u6CA1\u6709\u90A3\u4E48 &quot;\u660E\u767D&quot; \u8FC7. \u4ED6\u77E5\u9053, &quot;\u90A3\u4E2A\u4E1C\u897F&quot; \u5C31\u5728\u90A3\u91CC, \u5728\u7B49\u7740\u770B\u4ED6\u7684\u53CD\u5E94.</p><p>\u4ED6\u6CA1\u6709\u6C14\u6025\u8D25\u574F\u5730\u8D76\u53BB\u5AB3\u5987\u751F\u4EA7\u7684\u533B\u9662, \u800C\u662F\u5F00\u59CB\u6253\u626B\u5BB6\u91CC. \u6253\u626B\u7684\u8FC7\u7A0B\u4E2D\u4ED6\u4EC0\u4E48\u90FD\u6CA1\u60F3, \u4F46\u662F\u51E0\u79CD\u53EF\u80FD\u5374\u5F88\u6E05\u695A\u5730\u6D6E\u73B0\u5728\u8111\u4E2D.</p><p>\u6216\u8BB8\u513F\u5B50\u4F1A\u88AB\u5E26\u8D70. \u4ED6\u7A81\u7136\u53D1\u73B0\u81EA\u5DF1\u597D\u7231\u8FD9\u4E2A\u4ED6\u8FD8\u6CA1\u89C1\u8FC7\u9762\u7684\u513F\u5B50, \u8D85\u8FC7\u4E16\u754C\u4E0A\u7684\u4E00\u5207.</p><p>\u6216\u8BB8\u59BB\u5B50\u4F1A\u548C\u513F\u5B50\u4E00\u540C\u6B7B\u53BB.</p><p>\u6216\u8BB8\u4ED6\u81EA\u5DF1\u4F1A\u6B7B, \u800C\u513F\u5B50\u4F1A\u6D3B\u4E0B\u53BB.</p><p>\u7136\u800C\u8FD9\u4E00\u5207\u90FD\u4E0D\u7531\u4ED6\u6765\u9009\u62E9.</p><p>\u4ED6\u628A\u5BB6\u91CC\u6574\u7406\u5E72\u51C0\u540E, \u53C8\u53BB\u6D17\u4E86\u4E2A\u6FA1. \u6362\u4E0A\u5E72\u51C0\u7684\u8863\u670D\u5750\u5728\u5BA2\u5385\u91CC. \u7136\u540E, \u4ED6\u7A81\u7136\u4E86\u89E3\u4E86\u4EC0\u4E48\u53EB\u505A\u5C0A\u4E25, \u4F5C\u4E3A\u4E00\u4E2A\u4EBA\u7C7B\u7684\u5C0A\u4E25.</p><p>\u5BF9\u4E8E\u6211\u4EEC\u65E0\u80FD\u529B\u4E3A\u7684\u4E0D\u53EF\u6297\u529B, \u603B\u6709\u540E\u679C\u5C06\u8981\u53D1\u751F\u5728\u6211\u4EEC\u8EAB\u4E0A, \u4F46\u6211\u4EEC\u81F3\u5C11\u8FD8\u53EF\u4EE5\u5C0A\u4E25\u7684\u9762\u5BF9\u5B83.</p><p>\u7136\u540E, \u7535\u8BDD\u54CD\u4E86.</p><p>\u5BF9\u9762\u662F\u7834\u53E3\u5927\u9A82\u7684\u5973\u65B9\u5BB6\u5C5E. \u4ED6\u4EEC\u6253\u904D\u4E86\u624B\u673A\u548C\u5EA7\u673A, \u4F46\u6CA1\u6709\u4EBA\u63A5\u542C. \u56E0\u4E3A\u4ED6\u6839\u672C\u6CA1\u6709\u542C\u5230\u5EA7\u673A\u54CD.</p><p>\u5C0F\u5B69\u51FA\u751F\u65F6\u5DF2\u7ECF\u6CA1\u6709\u4E86\u547C\u5438. \u8110\u5E26\u7ED5\u9888, \u5168\u8EAB\u90FD\u662F\u7D2B\u8272\u7684.</p><p>\u4F46\u662F, \u5B69\u5B50\u6551\u56DE\u6765\u4E86. \u6BCD\u5B50\u5E73\u5B89.</p><p>\u540E\u6765\u5C0F\u9B4F\u7684\u5AB3\u5987\u5BF9\u4ED6\u8BF4, \u662F\u5979\u548C &quot;\u4E94\u901A&quot; \u6253\u4E86\u51E0\u767E\u573A\u67B6, \u786C\u662F\u628A\u513F\u5B50\u62A2\u4E86\u56DE\u6765.</p><p>\u662F\u7684, \u8FD9\u662F\u4EBA\u7684\u5C0A\u4E25\u6218\u80DC\u4E0A\u53E4\u90AA\u79FD\u7684\u6545\u4E8B.</p><h2 id="\u5982\u679C\u6211\u4EEC\u4E0D\u5BF9\u6297\u90AA\u6076-\u8BF7\u8BD5\u7740\u7ED9\u4E88\u5C0A\u4E25" tabindex="-1"><a class="header-anchor" href="#\u5982\u679C\u6211\u4EEC\u4E0D\u5BF9\u6297\u90AA\u6076-\u8BF7\u8BD5\u7740\u7ED9\u4E88\u5C0A\u4E25" aria-hidden="true">#</a> \u5982\u679C\u6211\u4EEC\u4E0D\u5BF9\u6297\u90AA\u6076, \u8BF7\u8BD5\u7740\u7ED9\u4E88\u5C0A\u4E25</h2><p>\u5F53\u7136, \u4EBA\u7684\u5C0A\u4E25\u6218\u80DC\u4E0A\u53E4\u90AA\u79FD, \u8FD9\u662F\u4E00\u4E2A\u592A\u8FC7\u6D6A\u6F2B\u4E3B\u4E49\u7684\u60F3\u6CD5: \u597D\u50CF\u4E00\u4E2A\u4EBA\u5982\u679C\u7EF4\u6301\u81EA\u5DF1\u7684\u5C0A\u4E25, \u5C31\u80FD\u89E3\u51B3\u4E00\u5207\u95EE\u9898. \u4F46\u4F60\u8981\u77E5\u9053\u8FD9\u662F\u4E00\u4E2A\u9B3C\u6545\u4E8B, \u603B\u4E0D\u80FD\u5BF9\u5B83\u6C42\u5168\u8D23\u5907.</p><p>\u6240\u4EE5, \u4E3A\u4E86\u51CF\u8F7B\u8FD9\u7BC7\u6587\u7AE0\u4E2D\u7684\u6D6A\u6F2B\u4E3B\u4E49, \u6211\u5F97\u518D\u8BB2\u4E00\u4E2A\u771F\u5B9E\u7684\u6545\u4E8B.</p><p>\u6587\u9769\u65F6\u671F, \u6211\u5916\u516C\u662F\u9769\u59D4\u4F1A\u4E3B\u5E2D, \u800C\u6211\u7237\u7237\u662F\u88AB\u6253\u6210\u725B\u9B3C\u86C7\u795E\u7684\u5DE5\u7A0B\u5E08.</p><p>\u90A3\u662F\u4E00\u4E2A\u6BCF\u4E2A\u4EBA\u90FD\u50CF\u9B3C\u795E\u4E0A\u8EAB\u7684\u75AF\u72C2\u5E74\u4EE3. \u65E0\u6570\u4EBA\u5728\u6279\u6597\u7684\u8FC7\u7A0B\u4E2D, \u88AB\u7EA2\u536B\u5175\u8650\u6253\u800C\u6B7B.</p><p>\u6211\u7237\u7237\u4F5C\u4E3A\u725B\u9B3C\u86C7\u795E\u81EA\u7136\u662F\u8981\u6328\u6279\u6597\u7684. \u7EA2\u536B\u5175\u62BC\u4ED6\u4E0A\u53F0, \u8981\u7ED9\u4ED6 &quot;\u5750\u98DE\u673A&quot;. \u5750\u4E0A\u98DE\u673A, \u4FBF\u8981\u52A8\u624B\u6253\u4EBA\u4E86.</p><p>\u5916\u516C\u4F5C\u4E3A\u9769\u59D4\u4F1A\u4E3B\u5E2D, \u4E3B\u6301\u6279\u6597\u6D41\u7A0B. \u89C1\u6211\u7237\u7237\u88AB\u62BC\u4E0A\u6765, \u4FBF\u5927\u559D: &quot;\u5750\u4E0B&quot;. \u7EA2\u536B\u5175\u53EA\u597D\u62FF\u6905\u5B50\u4E0A\u6765, \u8BA9\u6211\u7237\u7237\u5750\u4E0B. \u4EBA\u4E00\u65E6\u5750\u4E0B, \u6279\u6597\u5C31\u53EA\u80FD\u6587\u6597\u800C\u4E0D\u80FD\u6B66\u6597\u4E86.</p><p>\u5728\u90A3\u4E2A\u75AF\u72C2\u7684\u5E74\u4EE3, \u6BCF\u4E2A\u4EBA\u90FD\u8981\u4E89\u7740\u5265\u593A\u4ED6\u4EBA\u7684\u5C0A\u4E25. \u5C31\u5982\u4E94\u901A\u4E0A\u4E86\u5DEB\u5A46\u795E\u6C49\u7684\u8EAB, \u4FBF\u8981\u6492\u6CE2\u8C29\u9A82, \u901A\u8FC7\u6D88\u9664\u4EBA\u7684\u5C0A\u4E25\u6765\u5F70\u663E\u795E\u5A01. \u901A\u5E38\u6211\u4EEC\u6CA1\u6709\u80FD\u529B\u6B63\u9762\u7684\u5BF9\u6297\u90A3\u5F25\u6F2B\u7684\u90AA\u6076, \u4F46\u6709\u65F6\u6211\u4EEC\u5374\u53EF\u4EE5\u529B\u6240\u80FD\u53CA\u7ED9\u4E88\u4ED6\u4EBA\u4E00\u70B9\u5C0A\u4E25. \u54EA\u6015\u4EC5\u4EC5\u662F\u5750\u4E0B\u7684\u5C0A\u4E25, \u90FD\u6709\u53EF\u80FD\u8BA9\u4ED6\u4EBA\u71AC\u8FC7\u6700\u8270\u96BE\u7684\u65F6\u95F4.</p>',68),a=[e];function r(h,d){return o(),u("div",null,a)}var c=t(p,[["render",r],["__file","\u80CC\u56DE\u6765\u7684\u4EBA.html.vue"]]);export{c as default};
