import{_ as t,o,c as u,e as q}from"./app.efeddd24.js";const e={},p=q('<h1 id="\u8BF4\u8BF4\u8650\u732B\u88AB\u5F00\u9664\u4E00\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u8BF4\u8BF4\u8650\u732B\u88AB\u5F00\u9664\u4E00\u4E8B" aria-hidden="true">#</a> \u8BF4\u8BF4\u8650\u732B\u88AB\u5F00\u9664\u4E00\u4E8B</h1><p>\u7F51\u4E0A\u66DD\u51FA\u4E86\u4E00\u4EF6\u4FDD\u5B89\u56E0\u8650\u732B\u88AB\u5F00\u9664\u7684\u4E8B\u60C5, \u770B\u5230\u5927\u5BB6\u4E00\u76F4\u90FD\u5728\u8BA8\u8BBA. \u8FD9\u4E2A\u4E8B\u4EF6\u53CD\u6620\u4E86\u5F88\u591A\u4E0D\u540C\u5C42\u9762\u7684\u95EE\u9898, \u4F46\u770B\u5927\u5BB6\u8BA8\u8BBA\u7684\u65F6\u5019\u603B\u628A\u8FD9\u4E9B\u95EE\u9898\u6DF7\u5728\u4E00\u8D77\u8BF4, \u7ECF\u5E38\u5BFC\u81F4\u4E24\u4E2A\u4EBA\u4E89\u8BBA\u4E86\u51E0\u4E2A\u5C0F\u65F6, \u7ED3\u679C\u5927\u5BB6\u8BF4\u7684\u4E0D\u662F\u4E00\u4EF6\u4E8B. \u6240\u4EE5\u6211\u5C31\u60F3\u628A\u8FD9\u4E2A\u95EE\u9898\u62C6\u5F00\u6765\u7EC6\u7EC6\u8BF4\u8BF4.</p><p>\u5F53\u7136\u6211\u5373\u4E0D\u662F\u88AB\u5F00\u9664\u8005\u7684\u4EB2\u53CB, \u4E5F\u4E0D\u662F\u5F00\u9664\u8005\u7684\u4EB2\u53CB, \u4E5F\u4E0D\u662F\u8650\u732B\u7684\u4EB2\u53CB, \u6240\u4EE5\u5E76\u4E0D\u60F3\u4E3A\u4EFB\u4F55\u4E00\u65B9\u8FA9\u62A4. \u6211\u4E5F\u4E0D\u8BA4\u4E3A\u6211\u624B\u6301\u7740\u6B63\u4E49\u7684\u5929\u5E73, \u53EF\u4EE5\u5BF9\u6B64\u4E8B\u4E0B\u4E00\u4E2A\u516C\u6B63\u7684\u5224\u65AD. \u6240\u4EE5\u6211\u5E76\u4E0D\u7279\u522B\u8FD9\u4EF6\u4E8B\u7684\u7EC6\u8282, \u8FD9\u91CC\u66F4\u591A\u7684\u662F\u628A\u8FD9\u4EF6\u4E8B\u4F5C\u4E3A\u4E00\u4F8B\u5B50, \u8BA8\u8BBA\u5176\u4E2D\u53CD\u6620\u51FA\u7684\u95EE\u9898, \u751A\u81F3\u5728\u8BA8\u8BBA\u4E2D\u53EF\u80FD\u8FD8\u4F1A\u4E0D\u65AD\u53D8\u52A8\u4F8B\u5B50\u7684\u4E2D\u7684\u4E00\u4E9B\u53D8\u91CF, \u6269\u5927\u6216\u7F29\u5C0F\u8303\u56F4, \u8BD5\u7740\u8868\u8FBE\u6211\u5BF9\u4E00\u4E9B\u95EE\u9898\u7684\u770B\u6CD5.</p><h2 id="\u4E3A\u4EC0\u4E48\u8650\u732B\u4E0D\u597D" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8650\u732B\u4E0D\u597D" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u8650\u732B\u4E0D\u597D?</h2><p>\u4E0D\u4ECE\u8650\u732B\u597D\u4E0D\u597D\u8BF4\u8D77, \u800C\u76F4\u63A5\u95EE\u4E3A\u4EC0\u4E48\u8650\u732B\u4E0D\u597D, \u662F\u56E0\u4E3A\u8650\u732B\u4E00\u5B9A\u4E0D\u597D. &quot;\u8650\u5F85&quot; \u8FD9\u4E2A\u8BCD\u672C\u8EAB\u5E26\u7740 &quot;\u4E0D\u597D&quot; \u7684\u610F\u601D. \u6240\u4EE5 &quot;\u8650\u732B\u4E3A\u4EC0\u4E48\u4E0D\u597D&quot; \u9996\u5148\u662F\u5728\u95EE &quot;\u8650\u5F85\u4E3A\u4EC0\u4E48\u4E0D\u597D&quot;.</p><p>\u8650\u5F85\u662F\u4E0D\u597D\u7684, \u4E00\u5B9A\u662F\u4E0E\u5176\u5236\u9020\u75DB\u82E6\u6709\u5173. \u4F46\u5E76\u975E\u4E00\u5207\u5236\u9020\u75DB\u82E6\u7684\u4E8B\u60C5, \u90FD\u88AB\u8BA4\u4E3A\u8FD9\u4E48\u4E0D\u597D. \u6BD4\u5982\u60E9\u6212\u884C\u4E3A\u5C31\u4E0D\u90A3\u4E48\u88AB\u8BA4\u4E3A\u4E0D\u597D, \u8FD8\u6709\u590D\u4EC7\u884C\u4E3A, \u5F53\u7136\u4E5F\u4E0D\u597D, \u4F46\u4E5F\u8FD8\u6CA1\u8650\u5F85\u90A3\u4E48\u574F. \u6240\u4EE5\u5728\u8FD9\u4EF6\u4E8B\u91CC, \u7528\u5F00\u6C34\u6D47\u732B\u7684\u4EBA\u9996\u5148\u8FA9\u79F0\u56E0\u4E3A\u732B\u5077\u9999\u80A0\u6240\u4EE5\u624D\u8FD9\u4E48\u505A ( \u60E9\u6212 ), \u800C\u540E\u8BF4\u81EA\u5DF1\u662F\u5728\u9017\u732B\u65F6\u88AB\u6293\u4F24\u4E86\u6240\u4EE5\u624D\u8FD9\u4E48\u505A ( \u590D\u4EC7 ), \u4F46\u6211\u4EEC\u8BA4\u4E3A\u4ED6\u5C31\u662F\u8650\u5F85\u732B.</p><p>\u60E9\u6212\u884C\u4E3A\u662F\u660E\u663E\u4E0E\u5176\u5B83\u4E24\u8005\u4E0D\u540C\u7684: \u5B83\u4E0D\u8FFD\u6C42\u5236\u9020\u75DB\u82E6, \u5B83\u5236\u9020\u75DB\u82E6\u662F\u4E3A\u4E86\u53E6\u4E00\u4E2A\u76EE\u7684. \u590D\u4EC7\u884C\u4E3A\u867D\u7136\u8FFD\u6C42\u5236\u9020\u75DB\u82E6, \u4F46\u4F7F\u590D\u4EC7\u8005\u5F97\u5230\u6EE1\u8DB3\u7684\u4E0D\u662F\u5236\u9020\u75DB\u82E6, \u800C\u662F &quot;\u6062\u590D\u516C\u6B63&quot; \u7684\u611F\u89C9. \u8650\u5F85\u884C\u4E3A\u4E0D\u4F46\u8FFD\u6C42\u5236\u9020\u75DB\u82E6, \u800C\u4E14\u4E5F\u901A\u8FC7\u5236\u9020\u75DB\u82E6\u4E0A\u83B7\u5F97\u6EE1\u8DB3. \u4E5F\u5C31\u662F\u8BF4, \u8FFD\u6C42\u5236\u9020\u75DB\u82E6\u5DF2\u7136\u662F\u4E0D\u597D\u4E86, \u4ECE\u5236\u9020\u75DB\u82E6\u4E2D\u83B7\u5F97\u6EE1\u8DB3\u5219\u5B8C\u5168\u662F\u574F\u7684.</p><p>\u90A3\u4E3A\u4EC0\u4E48\u4ECE\u5236\u9020\u75DB\u82E6\u4E2D\u83B7\u5F97\u6EE1\u8DB3\u5B8C\u5168\u662F\u574F\u7684? \u4ECE\u529F\u5229\u7684\u89D2\u5EA6\u4E0A\u8BB2, \u6211\u5F53\u7136\u4E0D\u5E0C\u671B\u6211\u8EAB\u8FB9\u6709\u4E00\u4E2A\u80FD\u4ECE\u5236\u9020\u75DB\u82E6\u4E2D\u83B7\u5F97\u6EE1\u8DB3\u7684\u4EBA. \u4ED6\u4E07\u4E00\u54EA\u4E00\u5929\u6709\u4E00\u4E2A\u673A\u4F1A, \u8981\u4ECE\u6211\u6216\u6211\u7684\u4EB2\u53CB\u8EAB\u4E0A\u83B7\u5F97\u6EE1\u8DB3\u600E\u4E48\u529E? \u4F46\u6211\u5E76\u4E0D\u60F3\u5B8C\u5168\u4ECE\u529F\u5229\u7684\u89D2\u5EA6\u6765\u8BF4, \u4ECE\u6211\u5728\u7528\u8BCD\u4E0A\u4F7F\u7528 &quot;\u5236\u9020\u75DB\u82E6&quot; \u800C\u4E0D\u4F7F\u7528 &quot;\u5236\u9020\u4F24\u5BB3&quot; \u5C31\u80FD\u770B\u51FA\u6765. \u6211\u8FD8\u662F\u60F3\u8BB2\u8650\u5F85\u6709\u8FDD\u540C\u7406\u5FC3\u7684\u90A3\u5957\u9648\u8F9E\u6EE5\u8C03.</p><p>\u8650\u5F85\u732B\u4E0D\u4EBA\u9053, \u4E0D\u662F\u8BF4\u6CA1\u628A\u732B\u5F53\u4EBA\u770B, \u800C\u662F\u8BF4\u8650\u5F85\u8FD9\u4E8B\u4E0D\u662F\u4EBA\u5E72\u7684. \u56E0\u4E3A\u4EBA\u6709\u540C\u7406\u5FC3, \u80FD\u4ECE\u4ED6\u8005\u8868\u73B0\u51FA\u7684\u75DB\u82E6\u611F\u53D7\u5230\u75DB\u82E6. \u5112\u5BB6\u8BA4\u4E3A\u4EBA\u7684\u540C\u7406\u5FC3\u975E\u5E38\u91CD\u8981, \u662F &quot;\u4EC1&quot; \u7684\u53D1\u7AEF:</p><blockquote><p>\u5B5F\u5B50\u66F0\uFF1A\u300C\u4EBA\u7686\u6709 \u4E0D\u5FCD\u4EBA\u4E4B\u5FC3\u3002\u5148\u738B \u6709\u4E0D\u5FCD\u4EBA\u4E4B\u5FC3\uFF0C\u65AF\u6709\u4E0D\u5FCD\u4EBA\u4E4B\u653F\u77E3\u3002\u4EE5\u4E0D\u5FCD\u4EBA\u4E4B\u5FC3\uFF0C\u884C\u4E0D\u5FCD\u4EBA\u4E4B\u653F\uFF0C\u6CBB\u5929\u4E0B\u53EF\u904B\u4E4B\u638C\u4E0A\u3002\u6240\u4EE5\u8B02\u4EBA\u7686\u6709\u4E0D\u5FCD\u4EBA\u4E4B\u5FC3\u8005\uFF0C\u4ECA\u4EBA\u4E4D\u898B\u5B7A\u5B50\u5C07\u5165\u65BC\u4E95\uFF0C\u7686\u6709\u6035\u60D5\u60FB\u96B1\u4E4B\u5FC3 \u2014\u975E\u6240\u4EE5\u5167\u4EA4\u65BC\u5B7A\u5B50\u4E4B\u7236\u6BCD\u4E5F\uFF0C\u975E\u6240\u4EE5\u8981\u8B7D\u65BC\u9109\u9EE8\u670B\u53CB\u4E5F\uFF0C\u975E\u60E1\u5176\u8072\u800C\u7136\u4E5F\u3002\u7531\u662F\u89C0\u4E4B\uFF0C\u7121\u60FB\u96B1\u4E4B\u5FC3\uFF0C\u975E\u4EBA\u4E5F\uFF1B\u7121\u7F9E\u60E1\u4E4B\u5FC3\uFF0C\u975E\u4EBA\u4E5F\uFF1B\u7121\u8FAD\u8B93\u4E4B\u5FC3\uFF0C\u975E\u4EBA\u4E5F\uFF1B\u7121\u662F\u975E\u4E4B\u5FC3\uFF0C\u975E\u4EBA\u4E5F\u3002\u60FB\u96B1\u4E4B\u5FC3\uFF0C\u4EC1\u4E4B\u7AEF\u4E5F\uFF1B\u7F9E\u60E1\u4E4B\u5FC3\uFF0C\u7FA9\u4E4B\u7AEF\u4E5F\uFF1B\u8FAD\u8B93\u4E4B\u5FC3\uFF0C\u79AE\u4E4B\u7AEF\u4E5F\uFF1B\u662F\u975E\u4E4B\u5FC3\uFF0C\u667A\u4E4B\u7AEF\u4E5F\u3002\u4EBA\u4E4B\u6709\u662F\u56DB\u7AEF\u4E5F\uFF0C\u7336\u5176\u6709\u56DB\u9AD4\u4E5F\u3002\u6709\u662F\u56DB\u7AEF\u800C\u81EA\u8B02\u4E0D\u80FD\u8005\uFF0C\u81EA\u8CCA\u8005\u4E5F\uFF1B\u8B02\u5176\u541B\u4E0D\u80FD\u8005\uFF0C \u8CCA\u5176\u541B\u8005\u4E5F\u3002\u51E1\u6709\u56DB\u7AEF\u65BC\u6211\u8005\uFF0C\u77E5\u7686\u64F4\u800C\u5145\u4E4B\u77E3\uFF0C\u82E5\u706B\u4E4B\u59CB\u7136\uFF0C\u6CC9\u4E4B\u59CB\u9054\u3002\u82DF\u80FD\u5145\u4E4B\uFF0C\u8DB3\u4EE5\u4FDD\u56DB\u6D77\uFF1B\u82DF\u4E0D\u5145\u4E4B\uFF0C\u4E0D\u8DB3\u4EE5\u4E8B\u7236\u6BCD\u3002\u300D</p></blockquote><p>\u7167\u7740\u5B5F\u5B50\u8BDD\u8BF4, \u8981\u6CA1\u4E86\u540C\u7406\u5FC3, \u5C31\u7B97\u4E0D\u5F97\u4EBA\u4E86. \u9000\u4E00\u6B65, \u5C31\u662F\u4E0D\u4ECE\u5112\u5BB6\u7684\u89D2\u5EA6\u6765\u628A\u540C\u7406\u5FC3\u653E\u5728\u8FD9\u4E48\u6838\u5FC3\u7684\u4F4D\u7F6E, &quot;\u611F\u540C\u8EAB\u53D7&quot; \u4E5F\u662F\u8FBE\u6210\u7406\u89E3\u5408\u4F5C\u7684\u91CD\u8981\u524D\u63D0.</p><p>\u4E00\u4E2A\u6709\u540C\u7406\u5FC3\u7684\u4EBA, \u662F\u4E0D\u80FD\u4ECE\u5236\u9020\u75DB\u82E6\u4E2D\u83B7\u5F97\u6EE1\u8DB3\u7684, \u56E0\u6B64\u4ED6\u5728\u5236\u9020\u4ED6\u8005\u7684\u75DB\u82E6\u65F6, \u4E5F\u80FD\u4ECE\u4ED6\u8005\u8EAB\u4E0A\u611F\u53D7\u5230\u75DB\u82E6. \u751A\u81F3\u5728\u4E00\u4E9B\u6587\u827A\u4F5C\u54C1\u91CC, \u8FD8\u6709\u590D\u4EC7\u8005\u56E0\u4E3A\u540C\u7406\u5FC3\u800C\u653E\u5F03\u590D\u4EC7\u7684\u6545\u4E8B.</p><p>\u56E0\u6B64\u8650\u732B\u4E3A\u4EC0\u4E48\u4E0D\u597D, \u6216\u8005\u66F4\u660E\u786E\u7684\u8BF4 -- \u4E0D\u9053\u5FB7 -- \u539F\u56E0\u5927\u6982\u5982\u6B64. \u7136\u800C\u65E2\u4F7F\u5982\u6B64, \u6211\u4EEC\u8FD8\u662F\u53EF\u4EE5\u95EE:</p><h2 id="\u6211\u4EEC\u53EF\u4EE5\u4F9D\u636E\u4E00\u4E2A\u4EBA\u7684\u884C\u4E3A\u8BBA\u65AD\u4ED6\u4E0D\u5FB7\u9053\u4E48" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u53EF\u4EE5\u4F9D\u636E\u4E00\u4E2A\u4EBA\u7684\u884C\u4E3A\u8BBA\u65AD\u4ED6\u4E0D\u5FB7\u9053\u4E48" aria-hidden="true">#</a> \u6211\u4EEC\u53EF\u4EE5\u4F9D\u636E\u4E00\u4E2A\u4EBA\u7684\u884C\u4E3A\u8BBA\u65AD\u4ED6\u4E0D\u5FB7\u9053\u4E48?</h2><p>\u8650\u732B\u662F\u4E0D\u5FB7\u9053\u7684\u884C\u4E3A, \u90A3\u4E48\u6211\u4EEC\u53EF\u56E0\u4E3A\u4E00\u4E2A\u4EBA\u6709\u8650\u732B\u884C\u4E3A\u5C31\u8BA4\u5B9A\u4ED6\u662F\u4E00\u4E2A\u4E0D\u9053\u5FB7\u7684\u4EBA, \u5E76\u4E14\u636E\u6B64\u8BA4\u4E3A\u4ED6\u4EE5\u540E\u5FC5\u7136\u4F1A\u505A\u51FA\u4E0D\u9053\u5FB7\u7684\u884C\u4E3A\u4E48?</p><p>\u6211\u4EEC\u662F\u5426\u53EF\u4EE5\u4F5C\u51FA\u8FD9\u6837\u4E00\u4E2A\u5047\u8BBE:</p><ol><li>\u4EBA\u6709\u4E00\u4E2A\u672C\u8D28\u6216\u6E90\u521D\u81EA\u6211, \u8FD9\u4E2A\u672C\u8D28\u662F\u4EC0\u4E48\u6837\u7684, \u4ED6\u7684\u884C\u4E3A\u5C31\u662F\u4EC0\u4E48\u6837\u7684;</li><li>\u56E0\u6B64\u4E00\u4E2A\u4EBA\u6709\u4EC0\u4E48\u6837\u7684\u884C\u4E3A, \u5176\u5B9E\u53CD\u5E94\u51FA\u4ED6\u7684\u672C\u8D28\u6216\u6E90\u521D\u81EA\u6211\u662F\u4EC0\u4E48\u6837\u7684;</li><li>\u56E0\u6B64\u4E00\u4E2A\u4EBA\u8650\u732B\u53CD\u5E94\u51FA\u4ED6\u7684\u672C\u8D28\u662F\u4E0D\u9053\u5FB7\u7684 -- \u4E5F\u5C31\u662F\u8BF4\u4ED6\u662F\u4E2A\u4E0D\u9053\u5FB7\u7684\u4EBA;</li><li>\u56E0\u6B64\u4ED6\u4EE5\u540E\u7684\u884C\u4E3A\u5FC5\u7136\u4E5F\u662F\u4E0D\u9053\u5FB7\u7684.</li></ol><p>\u51E0\u4E4E\u6240\u6709\u4EC0\u4E48 &quot;\u5BF9\u4EBA\u4E0D\u5BF9\u4E8B&quot;, &quot;\u5148\u5B66\u505A\u4EBA\u518D\u5B66\u505A\u4E8B&quot;, &quot;\u4EC0\u4E48\u6837\u7684\u4EBA\u505A\u4EC0\u4E48\u6837\u7684\u4E8B&quot; \u7684\u9053\u7406\u80CC\u540E\u90FD\u4EE5\u4E0A\u8FF0\u5047\u8BBE\u4E3A\u524D\u63D0. \u800C\u4E14\u5B5F\u5B50\u7684 &quot;\u607B\u9690\u4E4B\u5FC3\u4EC1\u4E4B\u7AEF&quot; \u7684\u8BF4\u6CD5\u4F3C\u4E4E\u4E5F\u652F\u6301\u4E0A\u8FF0\u7684\u5047\u8BBE: \u4EBA\u6709\u56DB\u7AEF\u7684\u672C\u6027, \u6240\u4EE5\u624D\u80FD\u6709\u4EC1\u4E49\u793C\u667A\u7684\u884C\u4E3A.</p><p>\u5177\u4F53\u6765\u8BF4, \u4E00\u4E2A\u4FDD\u5B89\u56E0\u4E3A\u732B\u5077\u4E86\u9999\u80A0\u5C31\u628A\u5B83\u5173\u5728\u7B3C\u5B50\u91CC\u7528\u5F00\u6C34\u6D47, \u662F\u4E0D\u662F\u5C31\u53EF\u4EE5\u63A8\u65AD\u51FA\u4ED6\u662F\u4E2A\u6CA1\u6709\u540C\u7406\u5FC3\u7684\u4EBA, \u8FDB\u800C\u60F3\u4ED6\u4F1A\u56E0\u6D41\u6D6A\u513F\u5077\u4E86\u9762\u5305\u5C31\u628A\u4ED6\u7ED1\u5728\u66A7\u5668\u7BA1\u4E0A\u6BD2\u6253.</p><p>\u5B5F\u5B50\u6070\u6070\u4E0D\u662F\u8FD9\u4E2A\u610F\u601D. \u6309\u4ED6\u7684\u8BF4\u6CD5, \u54EA\u6015\u4E00\u4E2A\u4EBA\u6709\u8650\u732B\u7684\u884C\u4E3A, \u4ED6\u4E5F\u662F\u6709\u540C\u7406\u5FC3\u7684. \u56E0\u4E3A &quot;\u65E0\u607B\u9690\u4E4B\u5FC3\u975E\u4EBA\u4E5F&quot;. \u53EA\u662F\u8650\u732B\u4EBA\u5728\u8650\u732B\u7684\u65F6\u5019, \u5E76\u6CA1\u6709\u5C06\u5176\u540C\u7406\u5FC3 &quot;\u6269\u800C\u5145\u4E4B&quot; \u53D1\u6325\u51FA\u6765. \u751A\u81F3\u90A3\u4E2A\u8650\u732B\u7684\u4EBA, \u4E0D\u4F46\u8650\u732B\u8FD8\u81EA\u5DF1\u8BF4: &quot;\u6211\u5C31\u662F\u7231\u8650\u732B, \u6211\u5C31\u662F\u6CA1\u6709\u540C\u7406\u5FC3.&quot; \u4E5F\u4E0D\u4EE3\u8868\u4ED6\u5C31\u662F\u6CA1\u6709\u540C\u7406\u5FC3, \u800C\u53EA\u80FD\u8BF4\u4ED6 &quot;\u6709\u662F\u56DB\u7AEF\u800C\u81EA\u8C13\u4E0D\u80FD\u8005, \u81EA\u8D3C\u8005\u4E5F&quot; -- \u4ED6\u90A3\u662F\u5728\u81EA\u6B3A\u7F62\u4E86.</p><p>\u6211\u662F\u5B66\u6CD5\u5F8B\u7684, \u6240\u4EE5\u4E0A\u8FC7\u72AF\u7F6A\u5FC3\u7406\u5B66\u7684\u8BFE. \u4F46\u5176\u5B9E\u4E5F\u6CA1\u5B66\u5230\u4EC0\u4E48, \u5F53\u65F6\u53EA\u5F53\u730E\u5947\u6545\u4E8B\u542C\u4E86. \u4F46\u6211\u8BB0\u5F97\u72AF\u7F6A\u5FC3\u7406\u5B66\u8001\u5E08\u8BB2\u8FC7, \u8FD9\u95E8\u5B66\u79D1\u7684\u5C34\u5C2C\u4E4B\u5904: \u72AF\u7F6A\u5FC3\u7406\u5B66\u67D0\u79CD\u610F\u4E49\u4E0A\u662F\u53CD\u7F6A\u5211\u6CD5\u5B9A\u7684.</p><p>&quot;\u6CD5\u65E0\u660E\u6587\u89C4\u5B9A, \u4E0D\u4E3A\u7F6A; \u6CD5\u65E0\u660E\u6587\u89C4\u5B9A, \u4E0D\u5904\u7F5A.&quot; \u4EFB\u4F55\u4EBA\u672A\u7ECF\u5BA1\u5224\u90FD\u4E0D\u5F97\u5B9A\u7F6A. \u4F46\u72AF\u7F6A\u5FC3\u7406\u5B66\u7684\u4E00\u90E8\u5206\u4EFB\u52A1\u5C31\u662F\u7814\u7A76\u72AF\u7F6A\u4EBA\u7684\u5FC3\u7406, \u7136\u540E\u53BB\u8BC6\u522B\u672A\u6765\u53EF\u80FD\u72AF\u7F6A\u7684\u4EBA, \u5E76\u63D0\u4F9B\u76F8\u5E94\u7684\u7B56\u7565. \u751A\u81F3\u5728\u72AF\u7F6A\u5FC3\u7406\u5B66\u4E2D\u6709\u4E00\u79CD\u5B66\u8BF4, \u8BA4\u4E3A\u6709 &quot;\u5929\u751F\u72AF\u7F6A\u4EBA&quot;. \u7136\u800C\u4EC5\u4EC5\u6839\u636E\u4E00\u4E9B\u5FC3\u7406\u5B66\u4E0A\u7684\u7279\u5F81, \u5C31\u628A\u4E00\u90E8\u5206\u4EBA\u8BC6\u522B\u4E3A\u672A\u6765\u7684\u72AF\u7F6A\u4EBA, \u8FD9\u5408\u9002\u4E48?</p><p>\u7F6A\u5211\u6CD5\u5B9A\u539F\u5219\u4EE5\u4EBA\u5177\u6709\u81EA\u7531\u610F\u5FD7\u7684\u5047\u8BBE\u4E3A\u524D\u63D0, \u56E0\u4E3A\u4EBA\u6709\u81EA\u7531\u610F\u5FD7, \u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u81EA\u7531\u610F\u5FD7\u51B3\u5B9A\u505A\u51FA\u6216\u4E0D\u505A\u51FA\u4EC0\u4E48\u884C\u4E3A. \u56E0\u6B64\u5211\u6CD5\u624D\u5C06\u7981\u6B62\u7684\u884C\u4E3A\u4E8B\u5148\u5B9A\u7ACB, \u4EBA\u53EA\u6709\u4F9D\u636E\u5176\u81EA\u7531\u610F\u5FD7\u6545\u610F\u6216\u8FC7\u5931\u7684\u8FDD\u53CD\u5211\u6CD5\u7981\u6B62\u7684\u89C4\u5B9A, \u624D\u4F1A\u88AB\u8BA4\u4E3A\u662F\u72AF\u7F6A. \u5047\u5982\u4EBA\u7684\u884C\u4E3A\u662F\u88AB &quot;\u5FC3\u7406&quot; \u51B3\u5B9A\u7684, \u751A\u81F3\u4E8E\u5B58\u5728 &quot;\u5929\u751F\u72AF\u7F6A\u4EBA&quot;, \u90A3\u4ED6\u4EEC</p>',23),a=[p];function i(r,c){return o(),u("div",null,a)}var h=t(e,[["render",i],["__file","\u8650\u732B\u88AB\u5F00\u9664.html.vue"]]);export{h as default};
