import{_ as p,o as t,c as o,e as u}from"./app.6bf529a6.js";const e={},q=u('<h1 id="\u8BDD\u8BF4-\u6211\u4E5F\u88AB\u9738\u51CC\u8FC7" tabindex="-1"><a class="header-anchor" href="#\u8BDD\u8BF4-\u6211\u4E5F\u88AB\u9738\u51CC\u8FC7" aria-hidden="true">#</a> \u8BDD\u8BF4, \u6211\u4E5F\u88AB\u9738\u51CC\u8FC7</h1><p>17 \u5E74\u7684\u65F6\u5019, \u6211\u8FD8\u5728\u4E00\u4E2A\u533A\u5757\u94FE\u516C\u53F8\u4E0A\u73ED. \u516C\u53F8\u603B\u90E8\u5728\u5317\u4EAC, \u6211\u5728\u65E0\u9521\u529E\u4E8B\u5904\u4E0A\u73ED.</p><p>\u65E0\u9521\u529E\u4E8B\u5904, \u9664\u4E86\u6211, \u8FD8\u6709\u4E09\u4E2A\u5973\u540C\u4E8B. \u6BCF\u5929\u542C\u5979\u4EEC\u516B\u5366, \u8FDB\u800C\u53C2\u4E0E\u5176\u4E2D, \u7ADF\u7136\u9886\u7565\u5230\u4E86\u516B\u5366\u4E50\u8DA3.</p><p>\u56E0\u4E3A\u5927\u5BB6\u90FD\u662F\u6709\u5C0F\u5B69\u7684\u4EBA, \u6709\u4E00\u6B21\u5C31\u804A\u5230\u4E86\u6821\u56ED\u9738\u51CC\u7684\u4E8B\u60C5. \u5927\u5BB6\u4ECE\u793E\u4F1A\u65B0\u95FB, \u8BF4\u5230\u6559\u80B2\u73AF\u5883; \u4ECE\u4E16\u98CE\u73B0\u72B6, \u804A\u5230\u5F80\u6614\u8BB0\u5FC6.</p><p>\u4E00\u6BB5\u8BB0\u5FC6\u5728\u6211\u8111\u6D77\u4E2D\u8822\u8822\u6B32\u52A8, \u5FFD\u7136\u7075\u5149\u4E00\u73B0, \u6211\u62CD\u817F\u53EB\u5230: &quot;\u8FD9\u4E48\u8BF4\u6765, \u6211\u597D\u50CF\u4E5F\u88AB\u9738\u51CC\u8FC7.&quot;</p><hr><p>\u6545\u4E8B\u53D1\u751F\u5728\u6211\u521D\u4E2D\u7684\u65F6\u5019. \u5F53\u65F6\u6211\u5BB6\u5728\u8FD1\u90CA, \u800C\u5B66\u6821\u5219\u5728\u5E02\u533A.</p><p>\u90A3\u662F\u4E00\u4E2A\u5468\u672A\u7684\u4E0A\u5348, \u6211\u53BB\u627E\u5BB6\u5728\u5B66\u6821\u9644\u8FD1\u7684\u540C\u5B66\u73A9. \u4F46\u540C\u5B66\u5E76\u4E0D\u5728\u5BB6. \u4E8E\u662F\u6211\u5C31\u5728\u5B66\u6821\u9644\u8FD1\u6643\u60A0.</p><p>\u6B63\u5728\u95F2\u6781\u65E0\u804A, \u51C6\u5907\u56DE\u5BB6\u7684\u65F6\u5019, \u7A81\u7136\u770B\u89C1\u4E00\u4E2A\u5E7D\u6697\u5C0F\u5DF7\u91CC, \u4E00\u4E2A\u793E\u4F1A\u9752\u5E74\u5728\u62DB\u547C\u6211\u8FC7\u53BB.</p><p>\u8FD9\u4E2A\u4EBA\u6211\u5E94\u8BE5\u89C1\u8FC7. \u73ED\u4E0A\u6709\u51E0\u4E2A\u4E0D\u826F\u5206\u5B50\u548C\u8FD9\u4E2A\u6DF7\u5728\u4E00\u8D77. \u6211\u78B0\u89C1\u8FC7\u51E0\u56DE, \u4F46\u76F8\u4E92\u5E76\u672A\u6253\u8FC7\u62DB\u547C.</p><p>\u56E0\u4E3A\u773C\u719F, \u6211\u5C31\u8DDF\u7740\u4ED6\u8FDB\u4E86\u5C0F\u5DF7.</p><p>&quot;\u5E26\u94B1\u4E86\u4E48? \u501F\u6765\u7528\u7528.&quot; \u4ED6\u8BF4.</p><p>&quot;\u6709\u5341\u5757.&quot; \u6211\u9012\u7ED9\u4ED6. &quot;\u600E\u4E48\u4E86, \u8FD9\u4E48\u6025\u7740\u7528\u94B1?&quot;</p><p>\u4ED6\u63A5\u8FC7\u94B1, \u5FC3\u4E0D\u5728\u7109\u7684\u56DE\u7B54: &quot;\u6211\u8981\u53BB\u8D9F\u5317\u9053.&quot;</p><p>\u5317\u9053\u662F\u53E6\u4E00\u4E2A\u533A, \u4ECE\u90A3\u91CC\u53BB\u5317\u9053\u8981\u5750\u8FD1\u4E00\u4E2A\u5C0F\u65F6\u5927\u5DF4.</p><p>&quot;\u53BB\u5317\u9053\u6709\u4E8B?&quot;</p><p>&quot;\u6709\u4E8B.&quot;</p><p>&quot;\u6025\u4E48?&quot;</p><p>&quot;\u6025.&quot;</p><p>\u6211\u4E5F\u89C9\u5F97, \u4ED6\u53EF\u80FD\u6709\u70B9\u6025. \u56E0\u4E3A\u80FD\u4ECE\u4ED6\u7684\u56DE\u7B54\u4E2D\u542C\u51FA\u4E0D\u8010\u70E6\u6765.</p><p>&quot;\u90A3\u5341\u5757\u94B1\u4E0D\u591F\u554A.&quot; \u6211\u8BF4.</p><p>&quot;\u554A?&quot; \u4ED6\u6709\u70B9\u60CA\u8BB6.</p><p>&quot;\u4F60\u53BB\u5317\u9053\u7684\u5927\u5DF4, \u8F66\u7968\u5C31\u8981\u4E94\u5757. \u53BB\u4E86\u6765\u5F97\u56DE\u6765\u5427, \u90A3\u53EF\u4E0D\u5C31\u5341\u5757\u4E86. \u4F60\u8FD9\u4E2A\u70B9\u53BB, \u56DE\u6765\u600E\u4E48\u7740\u4E5F\u4E0B\u5348\u4E86\u5427. \u4F60\u4E2D\u5348\u4E0D\u5403\u996D\u4E48?&quot; \u6211\u5E2E\u4ED6\u76D8\u7B97\u7740.</p>',23),_=[q];function a(r,c){return t(),o("div",null,_)}var s=p(e,[["render",a],["__file","\u8BDD\u8BF4\u6211\u4E5F\u88AB\u9738\u51CC\u8FC7.html.vue"]]);export{s as default};
