import{_ as t,o,c as u,e as p}from"./app.efeddd24.js";const q={},e=p('<h1 id="\u6211\u4EEC\u5982\u4F55\u5171\u8BFB\u8BBA\u8BED" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u5982\u4F55\u5171\u8BFB\u8BBA\u8BED" aria-hidden="true">#</a> \u6211\u4EEC\u5982\u4F55\u5171\u8BFB\u8BBA\u8BED</h1><p>\u4ECA\u5929\u5728\u770B\u6731\u71B9\u7684\u300A\u56DB\u4E66\u7AE0\u53E5\u96C6\u6CE8\u300B, \u770B\u5230\u5728\u300A\u8BBA\u8BED\u300B\u7684\u96C6\u6CE8\u524D\u6709\u4E00\u7BC7\u300A\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5\u300B, \u770B\u540E\u611F\u89C9\u975E\u5E38\u53D7\u7528, \u6240\u4EE5\u60F3\u5728\u8FD9\u91CC\u5206\u4EAB\u7ED9\u5927\u5BB6.</p><p>\u8FD9\u662F\u7BC7\u662F\u6731\u71B9\u6536\u96C6\u7684 &quot;\u7A0B\u5B50&quot; \u5173\u4E8E\u5982\u4F55\u8BFB\u300A\u8BBA\u8BED\u300B\u300A\u5B5F\u5B50\u300B\u7684\u8BED\u5F55\u7684\u6587\u7AE0. \u8FD9\u91CC\u7684 &quot;\u7A0B\u5B50&quot; \u662F\u7A0B\u98A2\u8FD8\u662F\u7A0B\u9890, \u4E66\u91CC\u5E76\u6CA1\u6709\u7EC6\u8BF4, \u4F46\u6211\u89C9\u5F97\u4E5F\u6CA1\u6709\u5FC5\u8981\u7EA0\u7ED3\u4E8E\u6B64. \u5982\u679C\u60F3\u4E86\u89E3\u8BE6\u7EC6\u7684\u8003\u636E, \u6211\u6709\u4E00\u7BC7\u6587\u7AE0, \u5927\u5BB6\u53EF\u4EE5\u901A\u8FC7\u4E0B\u9762\u7684 [ \u9605\u8BFB\u539F\u6587 ] \u53BB\u770B.</p><h2 id="\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5" aria-hidden="true">#</a> \u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5</h2><p>\u7A0B\u5B50\u66F0: &quot;\u5B66\u8005\u5F53\u4EE5\u8BBA\u8BC1\u5B5F\u5B50\u4E3A\u672C. \u8BBA\u8BC1\u5B5F\u5B50\u65E2\u6CBB, \u5219\u516D\u7ECF\u53EF\u4E0D\u6CBB\u800C\u660E\u77E3. \u8BFB\u4E66\u8005\u5F53\u89C2\u5723\u4EBA\u6240\u4EE5\u4F5C\u7ECF\u4E4B\u610F, \u4E0E\u5723\u4EBA\u6240\u4EE5\u7528\u5FC3, \u5723\u4EBA\u4E4B\u6240\u4EE5\u81F3\u4E8E\u5723\u4EBA, \u800C\u543E\u4E4B\u6240\u4EE5\u672A\u5F97\u8005. \u53E5\u53E5\u6C42\u4E4B, \u4E66\u8BF5\u800C\u5473\u4E4B, \u4E2D\u591C\u800C\u601D\u4E4B, \u5E73\u5176\u5FC3, \u6613\u5176\u6C14, \u9619\u5176\u7591, \u5219\u5723\u4EBA\u4E4B\u610F\u53EF\u89C1\u77E3.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u51E1\u770B\u6587\u5B57, \u987B\u5148\u6653\u5176\u6587\u4E49, \u7136\u540E\u53EF\u4EE5\u6C42\u5176\u610F. \u672A\u6709\u4E0D\u6653\u6587\u4E49\u800C\u89C1\u610F\u8005\u4E5F.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u5B66\u8005\u987B\u5C06\u8BBA\u8BED\u4E2D\u8BF8\u5F1F\u5B50\u95EE\u5904\u4FBF\u4F5C\u81EA\u5DF1\u95EE, \u5723\u4EBA\u7B54\u5904\u4FBF\u4F5C\u4ECA\u65E5\u8033\u95FB, \u81EA\u7136\u6709\u5F97. \u867D\u5B54\u5B5F\u590D\u751F\u4E0D\u8FC7\u4EE5\u6B64\u6559\u4EBA. \u82E5\u80FD\u4E8E\u8BED\u5B5F\u4E2D\u6DF1\u6C42\u73A9\u5473, \u5C06\u6765\u6DB5\u517B\u6210\u4EC0\u4E48\u6C14\u8D28!&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u51E1\u770B\u8BED\u5B5F, \u4E14\u987B\u719F\u8BFB\u73A9\u5473, \u987B\u5C06\u5723\u4EBA\u8A00\u5207\u5DF1, \u4E0D\u53EF\u53EA\u4F5C\u573A\u8BDD\u8BF4. \u4EBA\u53EA\u770B\u5F97\u4E8C\u4E66\u5207\u5DF1, \u7EC8\u8EAB\u5C3D\u591A\u4E5F.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u8BBA\u5B5F\u53EA\u5269\u8BFB\u7740, \u4FBF\u81EA\u610F\u8DB3. \u5B66\u8005\u987B\u662F\u73A9\u5473. \u82E5\u4EE5\u8BED\u8A00\u89E3\u7740, \u610F\u4FBF\u4E0D\u8DB3.&quot;</p><p>\u6216\u95EE: &quot;\u4E14\u5C06\u8BBA\u5B5F\u7D27\u8981\u5904\u770B, \u5982\u4F55?&quot; \u7A0B\u5B50\u66F0: &quot;\u56FA\u662F\u597D, \u4F46\u7EC8\u662F\u4E0D\u6D43\u6D3D\u8033.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u5B54\u5B50\u8A00\u8BED\u53E5\u53E5\u662F\u81EA\u7136, \u5B5F\u5B50\u8A00\u8BED\u53E5\u53E5\u662F\u771F\u5B9E.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u5B66\u8005\u5148\u8BFB\u8BBA\u8BED\u5B5F\u5B50, \u5982\u5C3A\u5EA6\u6743\u8861\u76F8\u4F3C, \u4EE5\u6B64\u53BB\u91CF\u5EA6\u4E8B\u7269, \u81EA\u7136\u5F97\u89C1\u957F\u77ED\u8F7B\u91CD.&quot;</p><p>\u7A0B\u5B50\u66F0: &quot;\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u800C\u4E0D\u77E5\u9053, \u6240\u8C13 &#39;\u867D\u591A, \u4EA6\u595A\u4EE5\u4E3A&#39;.&quot;</p><h2 id="\u5199\u4F5C\u662F\u79CD\u903C\u8FEB" tabindex="-1"><a class="header-anchor" href="#\u5199\u4F5C\u662F\u79CD\u903C\u8FEB" aria-hidden="true">#</a> \u5199\u4F5C\u662F\u79CD\u903C\u8FEB</h2><p>\u5728\u8FD9\u7BC7\u300A\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5\u300B\u4E2D, \u6211\u6700\u53D7\u7528\u7684\u662F\u8FD9\u53E5: &quot;\u51E1\u770B\u8BED\u5B5F, \u4E14\u987B\u719F\u8BFB\u73A9\u5473, \u987B\u5C06\u5723\u4EBA\u8A00\u5207\u5DF1, \u4E0D\u53EF\u4EE5\u4F5C\u573A\u8BDD\u8BF4. \u4EBA\u53EA\u770B\u5F97\u4E8C\u4E66\u5207\u5DF1, \u7EC8\u8EAB\u5C3D\u591A\u4E5F.&quot; \u8FD9\u53E5\u662F\u5728\u8BF4, \u6211\u4EEC\u8BFB\u300A\u8BBA\u8BED\u300B\u300A\u5B5F\u5B50\u300B, \u719F\u8BFB\u5F53\u7136\u662F\u57FA\u672C\u7684\u529F\u592B, \u4F46\u719F\u8BFB\u8FDC\u8FDC\u4E0D\u591F, \u8FD8\u8981\u73A9\u5473. \u6240\u8C13\u73A9\u5473, \u5927\u6982\u662F\u8981 &quot;\u628A\u5473\u5176\u4E2D\u610F\u5473&quot;, \u50CF\u662F\u628A\u73A9\u5668\u7269\u4E00\u6837\u6765\u628A\u73A9\u8BDD\u8BED\u91CC\u7684\u610F\u601D. \u597D\u6BD4\u4E00\u4E2A\u80F6\u4F6C, \u62FF\u5230\u624B\u529E\u540E, \u62FF\u5728\u624B\u4E2D\u53CD\u590D\u89C2\u8D4F\u7EC6\u8282, \u8FD8\u4E0D\u591F, \u8FD8\u8981\u6446\u4E86\u540D\u79CD\u52A8\u4F5C, \u653E\u5728\u4E0D\u540C\u73AF\u5883\u573A\u666F\u4E0B\u62CD\u7167. \u6211\u4EEC\u8BFB\u300A\u8BBA\u8BED\u300B\u4E5F\u662F\u8981\u5982\u6B64, \u8BFB\u5230\u67D0\u4E00\u53E5\u8BDD, \u4E0D\u65AD\u53BB\u63A8\u6572\u5176\u95EE\u7EC6\u8282, \u8FD8\u4E0D\u591F, \u8FD8\u8981\u628A\u5B83\u653E\u5728\u5177\u4F53\u7684\u573A\u666F\u4E0B, \u6765\u770B\u5B83\u5728\u5177\u4F53\u7684\u573A\u666F\u4E0B\u7684\u610F\u5473\u662F\u4EC0\u4E48. &quot;\u653E\u5230\u5177\u4F53\u573A\u666F\u4E0B&quot; \u5C31\u662F\u8FD9\u91CC\u8BF4\u7684\u5207\u5DF1.</p><p>\u5982\u679C\u6211\u4EEC\u8BFB\u300A\u8BBA\u8BED\u300B\u53EA\u662F\u628A\u8BED\u8A00\u5F53\u4F5C\u8BED\u8A00\u6765\u89E3, \u6700\u597D\u7684\u60C5\u51B5\u662F\u4E86\u89E3\u4E86\u4E00\u4E9B\u6B7B\u4E86\u597D\u4E45\u7684\u4EBA\u8BF4\u4E86\u4E9B\u4EC0\u4E48, \u589E\u52A0\u4E86\u4E9B\u4E0D\u75DB\u4E0D\u75D2\u7684\u77E5\u8BC6\u7F62\u4E86. \u8FD9\u5BF9\u6211\u4EEC\u5E76\u6CA1\u6709\u4EC0\u4E48\u771F\u7684\u5E2E\u52A9. \u800C\u4E14\u5982\u679C\u6211\u4EEC\u771F\u7684\u53EA\u662F\u8BFB\u5230\u4E00\u4E2A\u4E0D\u75DB\u4E0D\u75D2\u7684\u77E5\u8BC6, \u53EA\u6015\u8BFB\u8FC7\u4E5F\u5C31\u5FD8\u4E86, \u8FDE\u83B7\u5F97\u77E5\u8BC6\u7684\u6548\u679C\u90FD\u6CA1\u6709.</p><p>\u5176\u5B9E\u5173\u4E8E &quot;\u5207\u5DF1&quot; \u8FD9\u4E00\u70B9, \u6211\u4EEC\u5C0F\u7EC4\u7684\u5199\u4F5C\u4E2D, \u8FD8\u662F\u86EE\u5207\u5DF1\u7684. \u5C24\u5176\u662F\u5728\u300A\u5B66\u800C\u7BC7\u300B\u7684\u7B2C\u4E8C\u7AE0, \u5728\u804A\u5230\u5B5D\u608C\u7684\u95EE\u9898\u65F6, \u5927\u5BB6\u90FD\u5199\u51FA\u5F88\u591A &quot;\u5207\u5DF1&quot; \u7684\u771F\u60C5\u5B9E\u611F\u7684\u6587\u5B57\u6765. \u8FD9\u5C31\u662F\u5728 &quot;\u8BFB\u8BBA\u8BED\u5B5F\u5B50, \u5982\u5C3A\u5EA6\u6743\u8861, \u4EE5\u6B64\u53BB\u91CF\u5EA6\u4E8B\u7269, \u81EA\u7136\u5F97\u89C1\u957F\u77ED\u8F7B\u91CD.&quot;</p><p>\u7136\u800C\u6211\u4EEC\u8981\u771F\u53BB &quot;\u5207\u5DF1&quot; \u7684\u8BFB\u4E66, \u5C31\u8981\u9762\u5BF9 &quot;\u53EA\u4EE5\u8BED\u8A00\u89E3\u7740&quot; \u6240\u4E0D\u5B58\u5728\u7684\u95EE\u9898: \u5C31\u662F\u62FF\u7740\u5723\u4EBA\u7684\u8A00\u8BED\u53BB\u91CF\u5EA6\u6211\u4EEC\u751F\u6D3B\u7684\u957F\u77ED\u8F7B\u91CD, \u5C31\u4F1A\u53D1\u73B0, \u5723\u4EBA\u7684\u8BDD\u6709\u7684\u65F6\u5019\u62FF\u5230\u751F\u6D3B\u91CC, \u4E0E\u6211\u4EEC\u7684\u65E5\u5E38\u7ECF\u9A8C\u5408\u4E0D\u4E0A. \u6BD4\u5982\u6211\u4EEC\u6628\u5929\u5199\u4F5C\u7684\u65F6\u5019, \u9AD8\u7545\u5C31\u5199\u5230, \u5982\u679C\u62FF\u7740\u5B54\u592B\u5B50\u7684\u5FE0\u4FE1\u89C2\u5230\u804C\u573A\u4E0A, \u53EF\u80FD\u5B8C\u5168\u662F\u884C\u4E0D\u901A\u7684.</p><p>\u5982\u679C\u6211\u4EEC\u53EA\u662F\u9605\u8BFB, \u53EF\u80FD\u53D1\u73B0\u8FD9\u4E2A\u95EE\u9898\u4E5F\u5C31\u653E\u8FC7\u53BB\u4E86. \u4F46\u662F\u5982\u679C\u6211\u4EEC\u662F\u5199\u4F5C, \u8FD9\u4E2A\u95EE\u9898\u5C31\u653E\u4E0D\u8FC7\u53BB, \u6211\u4EEC\u5C31\u5F97\u8981\u5199\u6E05\u695A, \u8FD9\u91CC\u4E0E\u5723\u4EBA\u4E4B\u8A00\u4E0E\u6211\u4EEC\u7684\u65E5\u5E38\u7ECF\u9A8C\u4E0D\u5408, \u662F\u5728\u4EC0\u4E48\u573A\u666F\u4E0B\u4E0D\u5408? \u4EE5\u4EC0\u4E48\u5F62\u592A\u4E0D\u5408? \u751A\u81F3\u6211\u4EEC\u4F1A\u5206\u6790, \u662F\u600E\u4E48\u4E2A\u4E0D\u5408\u6CD5, \u6709\u6CA1\u6709\u4E00\u79CD\u7406\u89E3\u627E\u5230\u4E00\u79CD\u4F7F\u5B83\u4EEC\u76F8\u5408\u7684\u7406\u89E3.</p><p>\u8FD9\u5C31\u662F\u6211\u8BF4\u7684, \u5199\u4F5C\u662F\u4E00\u79CD\u903C\u8FEB.</p><p>\u6211\u4EEC\u53EF\u80FD\u5728\u5E73\u65F6\u9605\u8BFB\u7684\u65F6\u5019, \u6709\u4E9B\u4E1C\u897F\u662F\u53EF\u4EE5\u653E\u8FC7\u53BB\u7684, \u4F46\u5728\u5199\u4F5C\u4E2D, \u4F60\u5C31\u653E\u4E0D\u8FC7\u53BB. \u5199\u4F5C\u8FD9\u79CD\u8F93\u51FA\u65B9\u5F0F, \u4F1A\u903C\u8FEB\u4F60\u628A\u4E8B\u60C5\u8BF4\u6E05\u695A, \u800C\u628A\u4E8B\u60C5\u8BF4\u6E05\u695A\u65F6\u5C31\u4F1A\u8FEB\u4F7F\u6211\u4EEC\u9762\u5BF9\u65E0\u6570\u7684\u7EC6\u8282. \u800C\u771F\u7684\u7406\u89E3, \u5C31\u5728\u8FD9\u65E0\u6570\u7684\u7EC6\u8282\u91CC. \u6240\u4EE5, \u5199\u4F5C\u8FD9\u5C31\u662F\u4E00\u79CD &quot;\u5207\u5DF1&quot; \u7684\u65B9\u5F0F, \u5B83\u903C\u8FEB\u6211\u4EEC\u505A\u51FA\u771F\u7684\u7406\u89E3.</p><p>\u8FD9\u4E5F\u662F\u6211\u4EEC\u505A\u8FD9\u4E2A\u5171\u8BFB\u5199\u4F5C\u610F\u4E49.</p>',22),a=[e];function r(h,c){return o(),u("div",null,a)}var _=t(q,[["render",r],["__file","\u8BFB\u8BBA\u8BED\u5B5F\u5B50\u6CD5.html.vue"]]);export{_ as default};
