import{_ as a,o as e,c as h,e as r}from"./app.6bf529a6.js";const d={},i=r('<h1 id="\u8D6B\u897F\u4FC4\u5FB7\u7684\u5DE5\u4F5C\u4E0E\u65F6\u65E5" tabindex="-1"><a class="header-anchor" href="#\u8D6B\u897F\u4FC4\u5FB7\u7684\u5DE5\u4F5C\u4E0E\u65F6\u65E5" aria-hidden="true">#</a> \u8D6B\u897F\u4FC4\u5FB7\u7684\u5DE5\u4F5C\u4E0E\u65F6\u65E5</h1><h2 id="\u6982\u8981" tabindex="-1"><a class="header-anchor" href="#\u6982\u8981" aria-hidden="true">#</a> \u6982\u8981</h2><p>\u8D6B\u897F\u4FC4\u5FB7\u7684\u300A\u5DE5\u4F5C\u4E0E\u65F6\u65E5\u300B\u662F\u4E00\u7BC7\u7C7B\u4F3C\u4E8E &quot;\u5BB6\u8BAD&quot; \u7684\u6587\u7AE0. \u4F46\u662F\u8FD9\u7BC7\u6587\u7AE0\u4E0D\u662F\u8001\u5B50\u5199\u7ED9\u513F\u5B50\u7684, \u800C\u662F\u5F1F\u5F1F\u5199\u7ED9\u54E5\u54E5\u7684.</p><p>\u636E\u8BF4, \u8D6B\u897F\u4FC4\u5FB7\u5BB6\u4E2D\u8D2B\u7A77, \u4E0E\u662F\u4ED6\u7236\u4EB2\u51FA\u6D77\u7ECF\u5546, \u8D5A\u4E86\u94B1, \u56DE\u5BB6\u8D2D\u7F6E\u7530\u4EA7, \u5E76\u82E6\u5FC3\u7ECF\u8425, \u8FC7\u4E0A\u4E86\u5BCC\u519C\u7684\u751F\u6D3B.</p><p>\u8D6B\u897F\u4FC4\u5FB7\u5BB6\u4E2D\u6709\u5144\u5F1F\u4E8C\u4E2A. \u7B49\u4ED6\u4EEC\u7684\u7236\u4EB2\u53BB\u4E16, \u5176\u5144\u4F69\u8033\u585E\u65AF\u901A\u8FC7\u8D3F\u8D42\u5F53\u5730\u738B\u7237, \u83B7\u5F97\u4E86\u8F83\u5927\u7684\u4E00\u4EFD.</p><p>\u4F46\u4F69\u8033\u585E\u65AF\u662F\u4E2A\u7EA8\u7ED4\u5B50\u5F1F, \u5728\u62FF\u5230\u8F83\u5927\u7684\u540E, \u5F88\u5FEB\u5C06\u5176\u6325\u970D\u4E00\u7A7A. \u800C\u540E\u53C8\u6765\u5411\u8D6B\u897F\u4FC4\u5FB7\u4E5E\u6C42\u63A5\u6D4E\u5E76\u53D1\u8D77\u8BC9\u8BBC.</p><p>\u300A\u5DE5\u4F5C\u4E0E\u65F6\u65E5\u300B\u5C31\u662F\u8D6B\u897F\u4FC4\u5FB7\u4E3A\u4E86\u63A7\u8BC9\u53D7\u8D3F\u8D42\u7684\u738B\u7237\u4E0D\u516C\u548C\u8BAD\u8BEB\u5176\u5144\u957F\u7684\u957F\u8BD7.</p><h2 id="\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u5E8F" aria-hidden="true">#</a> \u5E8F</h2><p>\u5E8F\u7684\u90E8\u5206\u8BF7\u6C42\u7F2A\u65AF\u5973\u795E\u6765\u503E\u542C\u672C\u8BD7, \u5E76\u8D5E\u7F8E\u4E86\u5B99\u65AF.</p><h2 id="\u4E24\u4E2A\u4E89\u6597\u5973\u795E" tabindex="-1"><a class="header-anchor" href="#\u4E24\u4E2A\u4E89\u6597\u5973\u795E" aria-hidden="true">#</a> \u4E24\u4E2A\u4E89\u6597\u5973\u795E</h2><p>\u800C\u540E\u8BB2\u4E24\u4E2A\u4E0D\u548C\u5973\u795E\u7684\u6545\u4E8B: \u597D\u7684\u4E0D\u548C\u5973\u795E\u5F15\u4EBA\u7ADE\u4E89, \u574F\u7684\u4E0D\u548C\u5973\u795E\u5F15\u4EBA\u4E89\u6597.</p><p>\u7531\u6B64\u63D0\u5230\u4E86\u4F69\u8033\u585E\u65AF\u8D3F\u8D42\u738B\u7237\u548C\u5411\u4ED6\u8BC9\u8BBC\u7684\u4E8B\u60C5.</p><h2 id="\u76D7\u706B\u7684\u6545\u4E8B\u4E0E\u6F58\u591A\u62C9\u7684\u6545\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u76D7\u706B\u7684\u6545\u4E8B\u4E0E\u6F58\u591A\u62C9\u7684\u6545\u4E8B" aria-hidden="true">#</a> \u76D7\u706B\u7684\u6545\u4E8B\u4E0E\u6F58\u591A\u62C9\u7684\u6545\u4E8B</h2><p>\u8BB2\u4E86\u666E\u7F57\u7C73\u4FEE\u65AF\u76D7\u706B, \u5B99\u65AF\u8BA9\u8BF8\u795E\u521B\u9020\u6F58\u591A\u62C9, \u547D\u8FD0\u5973\u795E\u6253\u5F00\u9B54\u76D2\u7684\u6545\u4E8B.</p><p>\u4ED6\u8BB2\u8FD9\u4E2A\u6545\u4E8B\u7684\u76EE\u7684\u662F\u8BF4\u660E, \u4E3A\u4EC0\u4E48\u4EBA\u8981\u8F9B\u82E6\u8FC7\u6D3B\u7684\u539F\u56E0.</p><h2 id="\u4EBA\u7C7B\u7684\u4E16\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u7C7B\u7684\u4E16\u4EE3" aria-hidden="true">#</a> \u4EBA\u7C7B\u7684\u4E16\u4EE3</h2><p>\u8BB2\u4E86\u4EBA\u7C7B\u4E0E\u795E\u540C\u6E90, \u5E76\u5982\u4F55\u7ECF\u8FC7\u4E94\u4E2A\u4E16\u4EE3, \u53D8\u6210\u8FD9\u4E2A\u6837\u5B50\u7684.</p><p>\u8FD9\u4E94\u4E2A\u4E16\u4EE3\u662F: \u9EC4\u91D1, \u767D\u94F6, \u9752\u94DC, \u82F1\u96C4, \u9ED1\u94C1; \u5176\u4E2D\u8377\u9A6C\u53F2\u8BD7\u5199\u7684\u662F\u82F1\u96C4\u65F6\u4EE3\u7684\u4E8B, \u800C\u4ED6\u5904\u4E8E\u9ED1\u94C1\u65F6\u4EE3.</p><h2 id="\u8001\u9E70\u4E0E\u591C\u83BA" tabindex="-1"><a class="header-anchor" href="#\u8001\u9E70\u4E0E\u591C\u83BA" aria-hidden="true">#</a> \u8001\u9E70\u4E0E\u591C\u83BA</h2><p>\u8BB2\u4E86\u4E00\u4E2A\u8001\u9E70\u6293\u4E86\u53EA\u591C\u83BA\u5E76\u903C\u4ED6\u5531\u6B4C\u7684\u6545\u4E8B.</p><p>\u4EE5\u6B64\u6765\u533A\u5206\u6B63\u4E49\u548C\u66B4\u529B. \u7531\u6B64\u5F3A\u8C03, \u5BB3\u4EBA\u5BB3\u5DF1, \u88AB\u8BBE\u8BA1\u51FA\u7684\u4E0D\u5E78, \u6700\u53D7\u4F24\u5BB3\u7684\u662F\u8BBE\u8BA1\u8005\u672C\u4EBA.</p><p>\u5F53\u7136\u4ED6\u8BF4\u8FD9\u4E9B\u662F\u4E3A\u4E86\u63A7\u8BC9\u6536\u53D7\u8D3F\u8D42\u800C\u4F5C\u51FA\u4E0D\u516C\u6B63\u88C1\u5B9A\u7684\u738B\u7237.</p><h2 id="\u8BAD\u8BEB-\u52E4\u52B3-\u53CB\u90BB" tabindex="-1"><a class="header-anchor" href="#\u8BAD\u8BEB-\u52E4\u52B3-\u53CB\u90BB" aria-hidden="true">#</a> \u8BAD\u8BEB: \u52E4\u52B3, \u53CB\u90BB</h2><p>\u4ECE\u8FD9\u91CC\u5F00\u59CB\u8FDB\u884C\u8BAD\u8BEB\u4E86: \u9996\u5148\u8BB2\u4E86\u52E4\u52B3, \u5BCC\u88D5\u4E0E\u81EA\u4FE1\u7684\u5173\u7CFB, \u800C\u540E\u8BB2\u4E86\u5982\u4F55\u4E0E\u53CB\u90BB\u76F8\u5904\u7684\u4E8B.</p><h2 id="\u8BAD\u8BEB-\u5BB6\u5EAD" tabindex="-1"><a class="header-anchor" href="#\u8BAD\u8BEB-\u5BB6\u5EAD" aria-hidden="true">#</a> \u8BAD\u8BEB: \u5BB6\u5EAD</h2><p>\u5982\u4F55\u5EFA\u7ACB\u4E00\u4E2A\u826F\u597D\u7684\u5BB6\u5EAD.</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u5BB6\u957F\u5982\u4F55\u914D\u7F6E\u81EA\u5DF1\u4F5C\u4E3A\u751F\u4EA7\u5355\u4F4D\u7684\u5BB6\u5EAD: \u5973\u5974, \u5974\u96B6\u548C\u8015\u725B; \u5982\u4F55\u5B89\u6392\u8FD9\u4E9B\u52B3\u529B\u5DE5\u4F5C; \u5728\u6BCF\u4E2A\u65F6\u8282\u5E94\u8BE5\u5E72\u4EC0\u4E48</p><h2 id="\u8BAD\u8BEB-\u51FA\u6D77\u505A\u751F\u610F" tabindex="-1"><a class="header-anchor" href="#\u8BAD\u8BEB-\u51FA\u6D77\u505A\u751F\u610F" aria-hidden="true">#</a> \u8BAD\u8BEB: \u51FA\u6D77\u505A\u751F\u610F</h2><p>\u5982\u4F55\u5B89\u6392\u51FA\u6D77\u505A\u751F\u610F, \u6CA1\u6709\u4EC0\u4E48\u751F\u610F\u7ECF, \u53EA\u6709\u5982\u4F55\u770B\u62A4\u597D\u81EA\u5DF1\u7684\u8239\u548C\u5C11\u5E26\u8D27\u7269\u51FA\u6D77\u7684\u8BAD\u8BEB.</p><h2 id="\u8BAD\u8BEB-\u7ED3\u5A5A\u4EA4\u53CB" tabindex="-1"><a class="header-anchor" href="#\u8BAD\u8BEB-\u7ED3\u5A5A\u4EA4\u53CB" aria-hidden="true">#</a> \u8BAD\u8BEB: \u7ED3\u5A5A\u4EA4\u53CB</h2><p>\u4EC0\u4E48\u65F6\u5019\u7ED3\u5A5A, \u548C\u4EC0\u4E48\u6837\u7684\u4EBA\u7ED3\u5A5A.</p><p>\u5982\u4F55\u4E0E\u4EBA\u76F8\u5F85.</p><h2 id="\u8BAD\u8BEB-\u5FCC\u5B9C" tabindex="-1"><a class="header-anchor" href="#\u8BAD\u8BEB-\u5FCC\u5B9C" aria-hidden="true">#</a> \u8BAD\u8BEB: \u5FCC\u5B9C</h2><p>\u4E0D\u8981\u505A\u7684\u4E8B\u60C5 ( \u7981\u5FCC ), \u548C\u4EC0\u4E48\u65F6\u95F4\u5B9C\u505A\u4EC0\u4E48. ( \u4E0E\u6211\u4EEC\u7684\u9EC4\u9053\u5409\u65E5\u5DEE\u4E0D\u591A. )</p>',34),n=[i];function p(t,c){return e(),h("div",null,n)}var o=a(d,[["render",p],["__file","\u8D6B\u897F\u4FC4\u5FB7\u7684\u5DE5\u4F5C\u4E0E\u65F6\u65E5.html.vue"]]);export{o as default};
