import{_ as t,o,c as p,e as u}from"./app.6bf529a6.js";const l={},i=u('<h1 id="\u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77-\u804A\u804A\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u6307\u7684\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77-\u804A\u804A\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u6307\u7684\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77, \u804A\u804A\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u6307\u7684\u662F\u4EC0\u4E48</h1><h2 id="\u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77" aria-hidden="true">#</a> \u4ECE\u6728\u6876\u539F\u7406\u8BF4\u8D77</h2><p>\u524D\u4E9B\u65E5\u5B50\u770B\u4E86\u4E00\u7BC7\u5173\u4E8E\u513F\u7AE5\u6559\u80B2\u7684\u7406\u8BBA, \u8BB2\u7684\u662F\u6240\u8C13\u7684 &quot;\u65B0\u6728\u6876\u7406\u8BBA&quot;.</p><p>\u6587\u7AE0\u5927\u610F\u662F\u8BF4, \u65E9\u5148\u7684\u6728\u6876\u7406\u8BBA\u8BA4\u4E3A, \u4EBA\u7684\u80FD\u529B\u4E0A\u9650\u53D6\u51B3\u4E8E\u4EBA\u5176\u8BF8\u591A\u80FD\u529B\u4E2D\u6700\u4F4E\u7684\u4E00\u4E2A. \u5373\u662F\u8BF4, \u4F60\u7684\u80FD\u529B\u7684\u4E0A\u9650, \u53D6\u51B3\u4E8E\u5176\u80FD\u529B\u7684\u77ED\u677F.</p><p>\u56E0\u4E3A\u4E00\u4E2A\u4EBA\u80FD\u505A\u6210\u4EC0\u4E48\u4E8B, \u53D6\u51B3\u4E8E\u4ED6\u5728\u4EC0\u4E48\u7A0B\u5EA6\u4E0A\u80FD\u591F <strong>\u5B8C\u5168</strong> \u53D6\u5F97\u4E8B\u7269\u7684\u638C\u63A7. \u4EBA\u7684\u77ED\u677F\u6B63\u662F\u5BF9\u4E8E\u4EBA\u5728\u4EC0\u4E48\u7A0B\u5EA6\u4E0A\u4E0D\u80FD\u638C\u63A7\u4E8B\u60C5\u7684\u5B9A\u4E49.</p><p><strong>\u8FD9\u4E2A\u7406\u8BBA\u6211\u4EEC\u79F0\u4E3A\u77ED\u677F\u539F\u7406</strong>.</p><p>\u800C\u73B0\u5728\u6709\u4E00\u4E2A\u65B0\u7684\u6728\u6876\u7406\u8BBA, \u8BA4\u4E3A\u4EBA\u7684\u80FD\u529B\u4E0A\u9650\u53D6\u51B3\u4E8E\u4EBA\u8BF8\u591A\u80FD\u529B\u662F\u6700\u9AD8\u7684\u4E00\u4E2A, \u4E5F\u5373\u957F\u677F.</p><p>\u56E0\u4E3A\u5728\u534F\u4F5C\u4E2D, \u4EBA\u5B8C\u5168\u53EF\u4EE5\u5C06\u81EA\u5DF1\u7684\u957F\u5904, \u653E\u5728\u534F\u4F5C\u4E2D\u6700\u6709\u5229\u7684\u5730\u65B9.</p><p><strong>\u8FD9\u4E2A\u7406\u8BBA\u6211\u4EEC\u79F0\u4E3A\u957F\u677F\u539F\u7406</strong>.</p><p>\u770B\u4E86\u8FD9\u4E00\u7BC7\u6587\u7AE0\u540E, \u6211\u7684\u611F\u60F3\u662F, \u4E14\u4E0D\u8BBA\u5230\u5E95\u662F\u957F\u677F\u539F\u7406\u8BF4\u7684\u5BF9\u8FD8\u662F\u77ED\u677F\u539F\u7406\u8BF4\u7684\u5BF9, \u8FD9\u4E24\u79CD\u60F3\u6CD5\u90FD\u662F\u4E00\u79CD\u8FC7\u5EA6\u62BD\u8C61\u7684\u89C2\u70B9.</p><p>\u8FD9\u4E24\u4E2A\u8BF4\u6CD5\u90FD\u5EFA\u7ACB\u5728\u8FD9\u6837\u4E00\u4E2A\u903B\u8F91\u7684\u57FA\u7840\u4E0A: \u4EBA\u7684\u80FD\u529B\u662F\u53EF\u4EE5\u62C6\u89E3\u6210\u76F8\u5BF9\u72EC\u7ACB\u7684\u6A21\u5757\u7684.</p><p>\u8FD9\u79CD\u7684\u80FD\u529B\u89C2\u7684\u5C31\u597D\u50CF RPG \u6E38\u620F\u7684\u4EBA\u7269\u80FD\u529B\u6A21\u578B: \u667A\u529B\u591A\u5C11\u591A\u5C11, \u6B66\u529B\u591A\u5C11\u591A\u5C11.</p><p>&quot;\u8FD9\u662F\u4E00\u79CD\u5178\u578B\u7684 <strong>\u673A\u68B0\u8FD8\u539F\u8BBA</strong> \u7684\u60F3\u6CD5&quot;, \u6211\u81EA\u7136\u800C\u7136\u7684\u60F3.</p><p>\u4F46\u6211\u7D27\u63A5\u7740\u60F3\u9053: &quot;\u4EC0\u4E48\u662F <strong>\u673A\u68B0\u8FD8\u539F\u8BBA</strong>.&quot;</p><h2 id="\u673A\u68B0\u8FD8\u539F\u8BBA-\u662F-\u8FD8\u539F\u4E3A\u673A\u68B0\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u673A\u68B0\u8FD8\u539F\u8BBA-\u662F-\u8FD8\u539F\u4E3A\u673A\u68B0\u8BBA" aria-hidden="true">#</a> &quot;\u673A\u68B0\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u4E3A\u673A\u68B0\u8BBA&quot;</h2><p>\u5982\u679C\u6211\u4EEC\u4E0D\u8981\u592A\u8FC7\u4E25\u683C, &quot;\u673A\u68B0\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u8BBA&quot; \u4E00\u8BCD\u7684\u4E00\u79CD\u7279\u6B8A\u7684\u56FA\u5B9A\u7528\u6CD5.</p><p>\u6211\u4EEC\u4E0D\u662F\u7ECF\u5E38\u8FD9\u6837\u8BF4\u4E48:</p><ul><li>&quot;\u8FD9\u662F\u5316\u5B66\u7684\u7269\u7406\u8FD8\u539F\u8BBA&quot;</li><li>&quot;\u8FD9\u662F\u793E\u4F1A\u5B66\u7684\u7ECF\u6D4E\u5B66\u539F\u8BBA&quot;</li></ul><p>\u6070\u5982\u6211\u4EEC\u8BF4, \u6728\u6876\u539F\u7406\u662F\u5173\u4E8E\u4EBA\u7684\u80FD\u529B\u7684\u673A\u68B0\u8FD8\u539F\u8BBA.</p><p>\u5728\u8FD9\u4E2A\u56FA\u5B9A\u7684\u7528\u6CD5\u4E2D, \u5F53\u6211\u4EEC\u5728\u8BF4, A \u7684 B \u8FD8\u539F\u8BBA\u65F6, \u6211\u4EEC\u5176\u5B9E\u662F\u5728\u8BF4, \u5C06 A \u73B0\u8C61\u8FD8\u539F\u4E3A B.</p><p>\u6BD4\u5982:</p><ul><li>\u5F53\u6211\u4EEC\u8BF4 &quot;\u5316\u5B66\u7684\u7269\u7406\u8FD8\u539F\u8BBA&quot; \u65F6, \u5176\u5B9E\u6211\u4EEC\u662F\u5728\u8BF4 <strong>&quot;\u6211\u4EEC\u5C06\u5316\u5B66\u73B0\u8C61\u8FD8\u539F\u4E3A\u7269\u7406\u539F\u7406&quot;</strong>.</li><li>\u5F53\u6211\u4EEC\u8BF4 &quot;\u793E\u4F1A\u5B66\u7684\u7ECF\u6D4E\u5B66\u539F\u8BBA&quot; \u65F6, \u5176\u5B9E\u6211\u4EEC\u662F\u5728\u8BF4 <strong>&quot;\u6211\u4EEC\u5C06\u793E\u4F1A\u5B66\u73B0\u8C61\u8FD8\u539F\u4E3A\u7ECF\u6D4E\u5B66\u7406\u539F\u7406&quot;</strong></li></ul><p>\u6240\u4EE5\u5F53\u6211\u4EEC\u60F3, &quot;\u6728\u6876\u539F\u7406\u662F\u5173\u4E8E\u4EBA\u7684\u80FD\u529B\u7684\u673A\u68B0\u8FD8\u539F\u8BBA&quot; \u65F6, \u6211\u7684\u60F3\u6CD5\u5176\u5B9E\u662F <strong>&quot;\u6728\u6876\u539F\u7406\u662F\u4E00\u79CD\u5C06\u4EBA\u7684\u80FD\u529B\u8FD8\u539F\u4E3A\u673A\u68B0\u7684\u539F\u7406\u7684\u770B\u6CD5&quot;</strong>.</p><p>\u5177\u4F53\u8BF4\u6765, \u6211\u7684\u60F3\u6CD5\u662F, \u6728\u6876\u539F\u7406\u8BA4\u4E3A:</p><ul><li>\u4EBA\u5177\u6709\u4E0D\u540C\u80FD\u529B, \u5982\u540C\u673A\u68B0\u5177\u6709\u4E0D\u540C\u6A21\u5757;</li><li>\u4EBA\u7684\u80FD\u529B\u8D77\u4F5C\u7528\u7684\u65B9\u5F0F\u548C\u673A\u68B0\u4E0D\u540C\u6A21\u5757\u4E4B\u95F4\u8D77\u4F5C\u7684\u65B9\u5F0F\u7C7B\u4F3C;</li><li>\u5206\u6790\u4EBA\u7684\u80FD\u529B\u5BF9\u4EBA\u7684\u4F5C\u7528, \u53EF\u4EE5\u7528\u5206\u6790\u673A\u68B0\u7684\u65B9\u5F0F\u6765\u5206\u6790;</li></ul><p>\u800C\u8FD9\u79CD\u60F3\u6CD5\u4E00\u5B9A\u662F\u8FC7\u5EA6\u62BD\u8C61\u7684, \u56E0\u4E3A\u4EBA\u7684\u80FD\u529B\u4E0D\u662F\u5982\u540C\u6728\u6876\u7684\u6728\u677F\u4E00\u6837\u5B8C\u5168\u72EC\u7ACB\u7684\u6A21\u5757, \u800C\u5176\u8D77\u4F5C\u7528\u4E5F\u4E0D\u662F\u5982\u540C\u673A\u68B0\u4E00\u6837\u9075\u5FAA\u529B\u5B66\u539F\u7406.</p><p>\u4F46\u662F, \u5982\u679C <strong>&quot;\u673A\u68B0\u8FD8\u539F\u8BBA\u662F\u6307\u8FD8\u539F\u4E3A\u673A\u68B0\u8BBA&quot; \u90A3\u4E48, \u4EC0\u4E48\u53C8\u662F &quot;\u8FD8\u539F&quot; \u5462</strong>.</p><h2 id="\u8FD8\u539F\u8BBA-\u7684\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u8FD8\u539F\u8BBA-\u7684\u6982\u5FF5" aria-hidden="true">#</a> &quot;\u8FD8\u539F\u8BBA&quot; \u7684\u6982\u5FF5</h2><p>\u4F5C\u4E3A\u4E00\u4E2A\u73B0\u4EE3\u4EBA, \u6211\u9996\u5148\u67E5\u4E86\u7EF4\u57FA.</p><p>\u8FD8\u539F\u8BBA\u662F\u4E00\u79CD\u8BA4\u77E5\u4E16\u754C\u7684\u6A21\u578B.</p><p><strong>\u8FD8\u539F\u8BBA</strong> \u8BA4\u4E3A\u6240\u6709\u590D\u6742\u7684\u4E8B\u7269\u662F\u7531\u7B80\u5355\u7684\u4E8B\u7269\u7EC4\u6210, \u56E0\u6B64, \u6240\u6709\u590D\u6742\u4E8B\u7269\u90FD\u53EF\u4EE5\u4ECE\u7B80\u5355\u4E8B\u7269\u4E2D\u627E\u5230\u5176\u539F\u56E0.</p><p>\u6BD4\u5982:</p><ul><li>\u793E\u4F1A\u662F\u7531\u4EBA\u6784\u6210\u7684, \u6240\u4EE5\u793E\u4F1A\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u751F\u7269\u5B66;</li><li>\u751F\u7269\u662F\u7531\u5176\u751F\u7269\u5316\u5B66\u5F71\u54CD\u7684, \u6240\u4EE5\u751F\u7269\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u5316\u5B66;</li><li>\u5316\u5B66\u662F\u7531\u5176\u5206\u5B50, \u539F\u5B50\u7684\u7269\u7406\u7279\u6027\u51B3\u5B9A\u7684, \u56E0\u6B64\u5316\u5B66\u53EF\u4EE5\u8FD8\u539F\u4E3A\u7269\u7406\u5B66;</li><li>\u800C\u6240\u6709\u4E00\u5207\u4E8B\u7269\u90FD\u662F\u7531\u57FA\u672C\u7C92\u5B50\u6784\u6210\u7684, \u6210\u4EE5\u4E00\u5207\u73B0\u8C61\u6700\u540E\u90FD\u53EF\u4EE5\u8FD8\u4E3A\u7269\u7406\u5B66.</li></ul><p>\u4E0E\u8FD8\u539F\u8BBA\u76F8\u5BF9\u7684\u8BA4\u77E5\u65B9\u5F0F\u662F <strong>\u7CFB\u7EDF\u8BBA</strong>.</p><p><strong>\u7CFB\u7EDF\u8BBA</strong> \u8BA4\u4E3A\u590D\u6742\u4E8B\u7269\u6240\u5448\u73B0\u7684\u73B0\u8C61\u4E0D\u662F\u7B80\u5355\u4E8B\u7269\u73B0\u8C61\u4E4B\u548C, \u800C\u662F\u4EE5\u5176\u6574\u4F53\u4F5C\u4E3A\u4E00\u4E2A\u7CFB\u7EDF\u4EA7\u751F\u5F71\u54CD\u7684.</p><p>\u6BD4\u5982:</p><ul><li>\u56FA\u8F6F\u4EF6\u8FD0\u884C\u5728\u64CD\u4F5C\u7CFB\u7EDF\u4E0A, \u800C\u64CD\u4F5C\u7CFB\u7EDF\u8FD0\u884C\u5728\u786C\u4EF6\u4E0A;</li><li>\u4F46\u786C\u4EF6\u672C\u8EAB\u5E76\u4E0D\u76F4\u63A5\u51B3\u5B9A\u8F6F\u4EF6;</li><li>\u56E0\u4E3A\u64CD\u4F5C\u7CFB\u7EDF\u5C06\u786C\u4EF6\u63D0\u4F9B\u7684\u80FD\u529B\u91CD\u65B0\u6574\u5408\u540E, \u91CD\u65B0\u63D0\u4F9B\u7ED9\u8F6F\u4EF6.</li><li>\u64CD\u4F5C\u7CFB\u7EDF\u62BA\u5E73\u4E86\u786C\u4EF6\u7684\u5DEE\u5F02.</li></ul><p>\u7CFB\u7EDF\u8BBA\u4E0E\u8FD8\u539F\u8BBA\u4E0E\u5176\u8BF4\u662F\u4E24\u79CD\u5BF9\u4E8E\u4E16\u754C\u7684\u8BA4\u89C2\u6A21\u578B, \u4E0D\u5982\u8BF4\u662F\u4E24\u79CD\u65B9\u6CD5. \u56E0\u4E3A\u662F\u4E24\u79CD\u65B9\u6CD5, \u56E0\u6B64\u5E76\u6CA1\u6709\u4EC0\u4E48\u4E92\u65A5\u6027.</p><p>\u5BF9\u4E8E\u4E00\u4E2A\u73B0\u8C61\u5373\u53EF\u4EE5\u505A\u8FD8\u539F\u8BBA\u7684\u5206\u6790, \u4E5F\u53EF\u4EE5\u4F5C\u7CFB\u7EDF\u7684\u8BBA\u7684\u5206\u6790.</p><p>\u7269\u7406\u5B9A\u5F8B\u81EA\u7136\u89E3\u91CA\u4E0D\u4E86\u4E3A\u4EC0\u4E48\u4E2D\u56FD\u8D70\u4E0A\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF, \u4F46\u662F\u53EF\u4EE5\u89E3\u91CA\u4E3A\u4EC0\u4E48\u4E00\u4E2A\u5251\u4E8E\u9B54\u6CD5\u7684\u4E16\u754C\u4E0D\u53EF\u80FD.</p><p>\u6211\u4EEC\u4E0D\u80FD\u50CF\u72EC\u65AD\u8BBA\u8005\u4E00\u6837, \u8BA4\u4E3A\u53EF\u4EE5\u901A\u8FC7\u5BF9\u4E8E\u73B0\u8C61\u7684\u8FD8\u539F\u627E\u5230\u4E00\u4E2A\u4E16\u754C\u552F\u4E00\u7684\u771F\u7406, \u4F46\u662F, \u6211\u4EEC\u5374\u53EF\u4EE5\u7528\u8FD8\u539F\u8BBA\u627E\u5230, \u8FD9\u4E2A\u4E16\u754C\u7684\u9650\u5236: \u4E16\u754C\u4E3A\u4EC0\u4E48\u4E0D\u662F\u90A3\u6837\u7684.</p><h2 id="\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4-\u7CFB\u7EDF\u8FD8\u539F\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4-\u7CFB\u7EDF\u8FD8\u539F\u8BBA" aria-hidden="true">#</a> \u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8BF4 &quot;\u7CFB\u7EDF\u8FD8\u539F\u8BBA&quot;</h2><p>\u5047\u5982\u6211\u4EEC\u8BA4\u4E3A &quot;\u7269\u7406\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u4E3A\u7269\u7406\u5B66&quot;; &quot;\u5316\u5B66\u8FD8\u539F\u8BBA&quot; \u662F &quot;\u8FD8\u539F\u4E3A\u5316\u5B66&quot;; <strong>\u90A3\u4E48\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u4F5C\u8FD9\u6837\u7684\u8868\u8FF0 &quot;\u7CFB\u7EDF\u8FD8\u539F\u8BBA&quot;, \u7528\u4EE5\u8868\u8FF0 &quot;\u8FD8\u539F\u4E3A\u7CFB\u7EDF&quot;</strong>.</p><p>\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8FD9\u6837\u8868\u8FF0, \u90A3\u4E48, \u662F\u4E0D\u662F\u610F\u5473\u7740, \u7CFB\u7EDF\u8BBA\u5E76\u4E0D\u662F\u548C\u8FD8\u539F\u8BBA <strong>\u76F8\u5BF9\u7684</strong> \u4E00\u79CD\u8BA4\u77E5\u65B9\u5F0F, \u800C\u662F <strong>\u4E00\u79CD\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u5BF9\u8C61</strong>?</p><p>\u5982\u679C\u7CFB\u7EDF\u8BBA\u4E0D\u662F\u4E0E\u539F\u8BBA\u76F8\u5BF9\u7684\u4E00\u79CD\u8BA4\u77E5\u65B9\u5F0F, \u90A3\u4EC0\u4E48\u662F <strong>\u4E0E\u8FD8\u539F\u8BBA\u7684\u8BA4\u77E5\u65B9\u5F0F</strong>?</p><p>\u662F\u7C7B\u6BD4\u5F0F\u7684\u8BA4\u77E5\u65B9\u5F0F\u4E48? \u76F4\u89C2\u7684\u8BA4\u77E5\u65B9\u5F0F? \u6216\u8005\u672C\u8D28\u4E0A\u8BB2 <strong>\u5E76\u6CA1\u6709\u4E00\u79CD\u4E0E\u8FD8\u539F\u8BBA\u76F8\u5BF9\u7684</strong> \u8BA4\u77E5\u65B9\u5F0F?</p><p>\u5373\u7136\u6211\u4EEC\u5DF1\u7ECF\u5C06 &quot;\u8FD8\u539F\u8BBA&quot; \u7684 &quot;\u8FD8\u539F&quot; \u4E00\u8BCD\u8FD8\u539F\u4E3A &quot;\u5C06\u590D\u6742\u7684\u73B0\u8C61\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406&quot;, \u800C\u4E14\u95EE\u9898\u4E5F\u5DF2\u7ECF\u63A8\u8FDB\u81F3\u6B64, \u90A3\u4E48\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u8FDB\u4E00\u6B65\u95EE\u4E0B\u53BB: \u4EC0\u4E48\u53EB\u505A <strong>&quot;\u628A\u590D\u6742\u7684\u95EE\u9898\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406&quot;</strong>.</p><h2 id="\u53EF\u7B97" tabindex="-1"><a class="header-anchor" href="#\u53EF\u7B97" aria-hidden="true">#</a> \u53EF\u7B97</h2><p>\u8FD8\u539F\u662F\u6307, \u5C06\u590D\u6742\u7684\u73B0\u8C61\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406. \u90A3\u4E48\u4EC0\u4E48\u53EB\u505A, \u628A\u590D\u6742\u7684\u73B0\u8C61\u5F52\u56E0\u4E3A\u7B80\u5355\u7684\u539F\u7406?</p><p>\u8BA9\u6211\u4EA7\u751F\u8FD9\u4E2A\u60F3\u6CD5\u7684\u662F\u884C\u4E3A\u7ECF\u6D4E\u5B66\u4E2D\u7684\u4E00\u4E2A\u4F8B\u5B50, <strong>(\u8FD9\u4E2A\u4F8B\u5B50\u53EF\u4EE5\u5728\u7FFB\u8F6C\u7535\u53F0\xB7\u884C\u4E3A\u7ECF\u6D4E\u5B66\u8282\u76EE\u7684\u7B2C\u4E09\u671F\u91CC\u542C\u5230)</strong>:</p><ul><li>\u5982\u679C\u7ED9\u4E00\u4E2A\u4EBA\u4E24\u4E2A\u9009\u62E9: <ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u4E2A\u5C0F\u65F6\u540E\u83B7\u5F97 30 \u5757\u94B1;</li></ul></li><li>\u901A\u5E38\u4ED6\u90FD\u4F1A\u9009\u62E9 2 \u5C0F\u65F6\u540E\u83B7\u5F97 25 \u5757\u94B1;</li><li>\u8BA9\u6211\u4EEC\u6765\u4FEE\u6539\u6570\u503C; <ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u5C0F\u65F6\u540E\u83B7\u5F97 20.5 \u5757\u94B1;</li></ul></li><li>\u901A\u5E38\u4ED6\u90FD\u4F1A\u9009\u62E9\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li></ul><p>\u8FD9\u4E2A\u4F8B\u5B50\u662F\u8868\u660E, \u4EBA\u7684\u51B3\u7B56\u662F\u5728\u6BD4\u8F83\u4E2D\u4F5C\u51FA\u7684.</p><p>\u4E5F\u5373\u662F\u8BF4, \u53D7\u8BD5\u5728\u8FDB\u884C\u51B3\u7B56\u7684\u65F6\u5019, \u5728\u8111\u5B50\u91CC\u628A\u4E24\u4E2A\u9009\u9879\u8FD8\u539F\u4E3A\u4E00\u4E2A\u6BD4\u8F83, \u901A\u8FC7\u6BD4\u8F83\u6765\u8FDB\u884C\u51B3\u7B56.</p><p>\u5728\u542C\u5230\u8FD9\u4E2A\u4F8B\u5B50\u7684\u65F6\u5019, \u6211\u5728\u60F3, \u5982\u679C\u6211\u662F\u8FD9\u4E2A\u53D7\u8BD5, \u6211\u4F1A\u4E3A\u4E86\u7B49\u5F85 2 \u5C0F\u65F6\u83B7\u5F97\u63A5\u53D7\u4E00\u4E2A\u600E\u6837\u7684\u503C?</p><p>\u6211\u60F3, \u5927\u6982\u4F1A\u662F 27 \u5757\u94B1.</p><p>\u4E0E\u662F\u6211\u8FDB\u800C\u60F3, \u4E3A\u4EC0\u4E48\u6211\u4F1A\u4E3A\u4E86 27 \u5757\u94B1\u8FD9\u4E2A\u503C\u63A5\u53D7\u7B49\u5F85 2 \u5C0F\u65F6?</p><p>\u6211\u60F3, \u5927\u6982\u662F\u56E0\u4E3A\u725B\u8089\u9762\u662F 7 \u5757\u94B1. \u5012\u5E76\u4E0D\u662F\u725B\u8089\u9762\u5BF9\u6211\u6709\u4EC0\u4E48\u91CD\u8981\u7684\u610F\u4E49, \u662F\u56E0\u4E3A, \u957F\u671F\u5728\u5170\u5DDE\u7684\u751F\u6D3B, \u4F7F\u4E00\u7897\u725B\u8089\u9762\u7684\u94B1\u53D8\u6210\u4E00\u4E2A\u53EF\u8BA1\u7B97\u7684\u5355\u4F4D.</p><p>\u5982\u679C\u6211\u4EEC\u4E0D\u6539\u53D8\u5B9E\u9A8C\u7684\u503C, \u800C\u4EC5\u4EC5\u6539\u53D8\u5B9E\u9A8C\u7684\u8868\u8FF0:</p><ul><li>\u73B0\u5728\u83B7\u5F97 20 \u5757\u94B1;</li><li>\u6216\u8005 2 \u4E2A\u5C0F\u65F6\u83B7\u5F97\u4E00\u4E2A 20 \u5757\u94B1\u52A0\u4E00\u4E2A\u5C0F\u793C\u54C1 ( \u793C\u54C1\u503C 5 \u6BDB\u94B1. )</li></ul><p>\u53D7\u8BD5\u613F\u610F\u7B49\u5F85\u4E24\u4E2A\u5C0F\u65F6\u7684\u673A\u7387\u4F1A\u5927\u5927\u63D0\u5347\u5427.</p><p>\u6240\u4EE5\u5F53\u6211\u4EEC\u8BF4, \u53D7\u8BD5\u5728\u51B3\u7B56\u7684\u65F6\u5019, \u5728\u8111\u5B50\u91CC\u5BF9\u4E24\u4E2A\u9009\u9879\u8FDB\u884C\u4E86\u8FD8\u539F, \u8FD9\u91CC\u7684\u8FD8\u539F, \u662F\u4E0D\u662F\u6307\u5F97\u662F \u53D7\u8BD5\u7684\u8111\u5B50\u91CC, \u4F7F\u5F97\u8FD9\u4E24\u4E2A\u9009\u9879 &quot;\u53EF\u7B97&quot; \u4E86.</p><p>\u5982\u679C\u6211\u4EEC\u53EF\u4EE5\u8FD9\u6837\u5728\u8868\u8FF0\u4E2D\u4F7F\u7528 &quot;\u8FD8\u539F&quot;, \u90A3\u662F\u4E0D\u662F &quot;\u8FD8\u539F&quot; \u53EF\u4EE5\u770B\u4F5C\u662F\u4E00\u79CD\u6211\u4EEC\u5BF9\u4E8E\u73B0\u8C61\u7684\u4E00\u79CD &quot;\u53EF\u7B97\u5316\u5904\u7406&quot;?</p><p>\u5982\u679C &quot;\u8FD8\u539F&quot; \u662F\u4E00\u79CD &quot;\u53EF\u7B97\u5316\u5904\u7406&quot;, \u662F\u4E0D\u662F\u610F\u5473\u7740 &quot;\u7269\u7406\u8FD8\u539F\u8BBA&quot; \u610F\u5473\u7740\u4F7F\u73B0\u8C61\u53EF\u4EE5\u4F7F\u7528\u7269\u7406\u5B66\u7684\u8BA1\u7B97\u65B9\u5F0F\u6765\u8BA4\u77E5?</p><h2 id="\u56DE\u5230-\u300A\u5F62\u800C\u4E0A\u5B66\u300B" tabindex="-1"><a class="header-anchor" href="#\u56DE\u5230-\u300A\u5F62\u800C\u4E0A\u5B66\u300B" aria-hidden="true">#</a> \u56DE\u5230 \u300A\u5F62\u800C\u4E0A\u5B66\u300B</h2><blockquote><p>\u6240\u6709\u7684\u4EBA\u90FD\u4ECE\u5BF9\u4E07\u8C61\u7684\u60CA\u5F02\u4E3A\u5F00\u7AEF, \u5982\u5080\u5121\u81EA\u884C, \u5982\u51AC\u81F3\u4E0E\u590F\u81F3, \u5982 <strong>&quot;\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0D\u80FD\u7528\u8FB9\u6765\u8BA1\u91CF&quot;</strong> \u7B49; \u8BF4\u662F\u4E16\u4E0A\u6709\u4E00\u4E8B\u7269, \u5373\u4FBF\u5F15\u7528\u6700\u5C0F\u7684\u5355\u4F4D\u8FD8\u662F\u4E0D\u80FD\u52A0\u4EE5\u8BA1\u91CF ...... \u5BF9\u4E8E\u4E00\u4E2A\u51E0\u4F55\u5B66\u8005, \u5982\u679C\u5BF9\u89D2\u7EBF\u6210\u4E3A\u53EF\u8BA1\u91CF\u7684, \u90A3\u624D\u662F\u4E16\u95F4\u602A\u4E8B.</p></blockquote><blockquote><p>---- \u4E9A\u91CC\u58EB\u591A\u5FB7 \u300A\u5F62\u800C\u4E0A\u5B66 \xB7 \u7B2C\u4E00\u5377 \xB7 \u7B2C\u4E8C\u7AE0\u300B</p></blockquote><p>\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728 \u300A\u5F62\u800C\u4E0A\u5B66\u300B \u91CC\u63D0\u5230\u53E4\u5E0C\u814A\u4EBA\u4E3A\u4EC0\u4E48\u8BA4\u4E3A\u65E0\u7406\u6570\u4E0D\u53EF\u7406\u89E3: <strong>&quot;\u6B63\u65B9\u5F62\u7684\u5BF9\u89D2\u7EBF\u4E0D\u80FD\u7528\u8FB9\u6765\u8BA1\u91CF.&quot;</strong></p><p>\u4E0D\u53EF\u7406\u89E3\u662F\u548C\u65E0\u6CD5\u7528\u5DF2\u77E5\u4E8B\u7269\u8BA1\u91CF\u662F\u9AD8\u5EA6\u76F8\u5173\u7684.</p><p>\u6211\u4EEC\u5F53\u7136\u53EF\u4EE5\u95EE: \u4E3A\u4EC0\u4E48\u4E0D\u53EF\u8BA1\u91CF\u7684\u4E1C\u897F\u662F\u4E0D\u53EF\u7406\u89E3\u7684.</p><p>\u6839\u636E\u6211\u4EEC\u5BF9\u4E8E\u8FD8\u539F\u7684\u7406\u89E3, \u53EF\u4EE5\u5F97\u77E5:</p><ul><li>\u4E0D\u53EF\u8BA1\u91CF\u7684\u4E1C\u897F\u662F\u65E0\u6CD5\u8BA1\u7B97\u7684, (\u81F3\u5C11\u65E0\u7406\u6570\u5728\u53E4\u5E0C\u814A\u662F\u4E0D\u53EF\u8BA1\u7B97\u7684);</li><li>\u8FD8\u539F\u610F\u5473\u7740\u53EF\u4EE5\u4F7F\u4E4B\u4EE5\u67D0\u79CD\u65B9\u5F0F\u8BA1\u7B97;</li><li>\u4E0D\u53EF\u8BA1\u91CF\u610F\u5473\u7740 <strong>\u4E0D\u53EF\u8FD8\u539F</strong>;</li><li><strong>\u4E0D\u53EF\u8FD8\u539F\u610F\u5473\u7740\u4E0D\u53EF\u4F7F\u4E4B\u5F52\u56E0\u4E3A\u66F4\u7B80\u5355\u7684\u539F\u7406</strong>;</li><li>\u4E0D\u53EF\u5F52\u56E0\u4E3A\u66F4\u7B80\u5355\u7684\u539F\u7406\u610F\u5473\u7740\u8FD9\u91CC\u662F\u7406\u6027\u7684\u5C3D\u5934.</li></ul><p>\u56E0\u6B64\u65E0\u7406\u6570\u662F\u795E\u7684\u9886\u57DF. \u800C\u5728\u8FD9\u4E2A\u610F\u4E49\u4E0A, \u5F53\u4E00\u4E2A\u5B66\u8005\u9886\u609F\u5230: <code>\u5BF9\u4E8E\u4E00\u4E2A\u51E0\u4F55\u5B66\u8005, \u5982\u679C\u5BF9\u8C61\u7EBF\u6210\u4E3A\u53EF\u8BA1\u91CF\u7684, \u90A3\u624D\u662F\u4EBA\u95F4\u602A\u4E8B.</code>, \u90A3\u4ED6\u5C31\u8FDB\u5165\u795E\u5B66.</p><h2 id="\u56DE\u5230\u6728\u6876\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u56DE\u5230\u6728\u6876\u539F\u7406" aria-hidden="true">#</a> \u56DE\u5230\u6728\u6876\u539F\u7406</h2><p>\u6728\u6876\u539F\u7406\u56FA\u7136\u662F\u4E00\u79CD\u8FC7\u5EA6\u62BD\u8C61\u7684\u73B0\u6CD5.</p><p>\u4EBA\u7684\u80FD\u529B\u5F53\u7136\u4E0D\u80FD\u7528\u673A\u68B0\u7684\u539F\u7406\u6765\u8BA1\u7B97, \u4F46\u662F\u628A\u4EBA\u7684\u80FD\u529B\u5F53\u6210\u673A\u68B0\u6A21\u5757\u5374\u4F7F\u4EBA\u7684\u80FD\u529B\u53D8\u6210\u53EF\u7B97\u7684\u4E86.</p><p>\u6240\u4EE5\u5F53\u7F8E\u56FD\u7BA1\u7406\u5B66\u8005\u5F7C\u5FB7\u63D0\u51FA\u6728\u6876\u539F\u7406\u7684\u65F6\u5019, \u5F53\u7136\u5373\u4E0D\u662F\u56E0\u4E3A\u4ED6\u50BB, \u8BF4\u9519\u4E86\u4E00\u4EF6\u4E8B; \u4E5F\u4E0D\u662F\u56E0\u4E3A\u4ED6\u574F, \u6545\u610F\u8981\u9A97\u6211\u4EEC; \u800C\u662F\u8BA9\u4EBA\u7684\u80FD\u529B\u53D8\u6210\u4E00\u4E2A\u53EF\u4EE5\u901A\u8FC7\u4EBA\u7C7B\u7406\u6027\u8BA1\u7B97\u7684\u4E8B\u60C5.</p>',76),r=[i];function n(q,a){return o(),p("div",null,r)}var s=t(l,[["render",n],["__file","\u8FD8\u539F\u8BBA\u7684\u8FD8\u539F\u662F\u4EC0\u4E48.html.vue"]]);export{s as default};
