import{_ as o,o as t,c as p,e as u}from"./app.6bf529a6.js";const e={},q=u('<h1 id="\u6000\u7279\u6D77-\u601D\u7EF4\u65B9\u5F0F-\u91CD\u8981\u6027" tabindex="-1"><a class="header-anchor" href="#\u6000\u7279\u6D77-\u601D\u7EF4\u65B9\u5F0F-\u91CD\u8981\u6027" aria-hidden="true">#</a> \u6000\u7279\u6D77-\u601D\u7EF4\u65B9\u5F0F-\u91CD\u8981\u6027</h1><p>\u54F2\u5B66\u8003\u5BDF\u4E00\u822C\u7684\u7EC8\u6781\u6982\u5FF5. \u8FD9\u4E9B\u6982\u56E0\u5176\u4E00\u822C\u6027\u65E0\u6CD5\u88AB\u5B9A\u4E49.</p><p>\u8FD9\u91CC\u8BA8\u8BBA\u7684\u662F <strong>\u91CD\u8981\u6027</strong> \u6982\u5FF5.</p><p>\u4F53\u7CFB\u5316\u7684\u7814\u7A76\u95EE\u9898\u5728\u4E8E, \u4F53\u7CFB\u5316\u9700\u8981\u9884\u5148\u5C06\u67D0\u4E9B\u6982\u5FF5\u6392\u9664\u5728\u5916. \u56E0\u6B64, \u5728\u6B64\u4E4B\u524D\u9700\u8981\u6709\u4E00\u4E2A\u54F2\u5B66\u6982\u5FF5\u7684 &quot;\u6536\u96C6\u8FC7\u7A0B&quot;. \u8FD9\u90E8\u5206\u7684\u8BA8\u8BBA\u66F4\u591A\u7684\u662F\u5728\u505A\u6536\u96C6\u7684\u5DE5\u4F5C.</p><p>\u5F53\u6211\u4EEC\u8BF4 <strong>\u91CD\u8981\u6027</strong> \u65F6\u5FC5\u6709\u5176\u5BF9\u8C61, ( &quot;\u8FD9\u4E2A\u662F\u91CD\u8981\u7684&quot; \u6216 &quot;\u90A3\u4E2A\u662F\u91CD\u8981\u7684&quot;. &quot;\u8FD9&quot; \u4E0E &quot;\u90A3&quot; \u5C31\u662F\u5BF9\u4E8E\u5BF9\u8C61\u7684\u6307\u793A ). \u8FD9\u4E2A\u5BF9\u8C61\u5C31\u662F <strong>\u4E8B\u5B9E</strong>.</p><blockquote><p>\u4E3A\u4E86\u83B7\u5F97\u77E5\u8BC6, \u6211\u4EEC\u5FC5\u987B\u4F7F\u81EA\u5DF1\u4E0D\u53D7\u77E5\u8BC6\u7684\u675F\u7F1A.</p></blockquote><p>\u6211\u4EEC\u8003\u5BDF\u91CD\u8981\u6027\u4E0E\u4E8B\u5B9E\u7684\u5173\u7CFB, \u9700\u8981\u6446\u8131\u53E4\u4EE3\u7684\u601D\u60F3\u5F62\u6001 ( \u53E4\u5E0C\u814A -- \u5173\u5B66\u548C\u903B\u8F91-- \u4EAB\u53D7/ \u95EA\u65CF -- \u9053\u5FB7\u548C\u5B97\u6559 -- \u5D07\u62DC/ \u57C3\u53CA -- \u5B9E\u7528 -- \u5B9E\u9645\u89C2\u5BDF ).</p><p>\u91CD\u8981\u6027\u5B58\u5728\u7A0B\u5EA6, \u8FD9\u79CD\u7A0B\u5EA6\u662F\u4EBA\u7684\u81EA\u7531\u9009\u62E9. \u800C\u4E8B\u5B9E\u672C\u8EAB\u5177\u6709\u5FC5\u7136\u6027\u7684\u7279\u5F81. \u56E0\u6B64\u6211\u4EEC\u57CE\u6735\u5BF9\u81EA\u7531\u548C\u5BC6\u7136\u6027\u52A0\u4EE5\u89E3\u91CA.</p><p>\u91CD\u8981\u6027\u662F &quot;\u5BFC\u81F4\u5C06\u4E2A\u4EBA\u611F\u53D7\u516C\u5F00\u8868\u8FBE\u51FA\u6765\u7684\u90A3\u79CD\u5F3A\u5EA6\u7684\u5174\u8DA3&quot;. \u8FD9\u5F53\u7136\u4E0E\u81EA\u7136\u79D1\u5B66\u7684\u7406\u60F3\u4E0D\u540C. \u81EA\u7136\u79D1\u5B66\u8BA4\u4E3A\u552F\u6709\u5BA2\u89C2\u7684\u4E1C\u897F\u6709\u91CD\u8981\u6027: \u5355\u7EAF\u7684\u5B58\u5728\u548C\u5176\u8FC7\u7A0B.</p><p>\u7136\u800C &quot;\u4E3B\u5F20\u4E00\u79CD\u7406\u8BBA&quot; -- \u54EA\u6015\u662F\u5426\u8BA4\u5174\u8DA3\u7684\u7406\u8BBA -- \u672C\u8EAB\u5C31\u662F\u4E00\u79CD\u5174\u8DA3\u7684\u8868\u8FBE. \u5426\u5219\u4FBF\u53EA\u662F &quot;\u6307\u51FA\u4E00\u79CD\u7406\u8BBA&quot; \u4E86.</p><blockquote><p>\u4E00\u65E6\u5B83 ( \u91CD\u8981\u6027 ) \u5931\u53BB\u652F\u914D\u5730\u4F4D, \u7ECF\u9A8C\u5C31\u4F1A\u53D8\u5F97\u7410\u788E, \u5E76\u63A5\u8FD1\u4E8E\u865A\u65E0.</p></blockquote><p>\u5355\u7EAF\u4E8B\u5B9E\u7684\u6982\u5FF5\u662F\u62BD\u8C61\u7406\u77E5\u7684\u6210\u679C. \u800C\u795E\u8BDD\u601D\u7EF4\u662F\u5B64\u7ACB\u7684\u4E8B\u5B9E. \u800C\u8054\u7CFB\u6027\u662F\u4E8B\u7269\u7684\u672C\u8D28. \u4EFB\u52A1\u4E8B\u5B9E\u90FD\u9884\u5148\u5047\u5B9A\u4E00\u4E2A\u4E8B\u5B9E\u5B58\u5728\u5FC5\u4E0D\u53EF\u5C11\u7684\u540C\u683C\u73AF\u5883. \u8FD9\u662F\u5B83\u89C6\u57DF\u4E2D\u7684\u6574\u4E2A\u5B87\u5B99. \u56E0\u5174\u8DA3\u6709\u7B49\u7EA7, \u56E0\u6B64</p><blockquote><p>\u6211\u4EEC\u5BF9\u4E8E\u8FD9\u4E9B\u7ED3\u679C\u6709\u4E0D\u540C\u611F\u53D7, \u4E8E\u662F\u628A\u5B83\u4EEC\u5F52\u7ED3\u4E3A\u4E00\u79CD\u89C6\u57DF. &quot;\u6210\u4E3A\u53EF\u4EE5\u5FFD\u89C6\u7684\u4E1C\u897F&quot; \u6307\u7684\u662F &quot;\u5BF9\u611F\u53D7\u7684\u67D0\u79CD\u540C\u683C\u6210\u4E3A\u53EF\u4EE5\u5FFD\u89C6\u7684\u4E1C\u897F&quot;.</p></blockquote><p>\u6240\u4EE5\u4EFB\u52A1\u7CFB\u7EDF\u5FC5\u987B\u9884\u9009\u8BBE\u5B9A\u4E00\u79CD\u4ECE\u5176\u6574\u4F53\u8BF4\u6211\u4EEC\u4E0D\u80FD\u7ED9\u5B83\u4E0B\u5B9A\u4E49\u7684\u73AF\u5883. \u8FD9\u662F\u4E00\u5207\u4F53\u7CFB\u7684\u5C40\u9650\u6027. \u4ECE\u8FD9\u70B9\u4E0A\u8BF4, \u4F53\u7CFB\u53D1\u5C55\u5230\u4E00\u5B9A\u4F53\u91CF\u540E, \u4E00\u5B9A\u4F1A\u51FA\u73B0\u5185\u90E8\u77DB\u76FE.</p><blockquote><p>\u8F83\u4E3A\u6DF1\u523B\u7684\u771F\u7406\u5FC5\u4E3A\u795E\u8BDD\u7684\u9634\u5F71\u6240\u7B3C\u7F69.</p></blockquote><p>\u56E0\u6B64\u4F53\u7CFB\u7684\u77E5\u8BC6\u6CA1\u6709\u4E00\u4E2A\u662F\u9519\u7684, \u800C\u662F\u53D7\u9650\u4E0E\u5B83\u6240\u4F9D\u8D56\u7684\u73AF\u5883\u7684\u5C40\u9650\u6027.</p><p>\u91CD\u8981\u6027\u4E0E\u611F\u53D7\u76F8\u5173. \u89C6\u57DF\u88AB\u7F6E\u4E8E\u6240\u611F\u89C9\u5230\u7684\u4E8B\u7269\u7684\u6574\u4E2A\u9886\u57DF. \u5728\u8FD9\u4E2A\u9886\u57DF\u6211\u4EEC\u7528\u5174\u8DA3\u5212\u5206\u7B49\u7EA7. \u5F15\u5BFC\u6CE8\u610F\u529B.</p><blockquote><p>\u89C6\u57DF\u662F\u4ECE\u6240\u611F\u89C9\u5230\u7684\u4E8B\u7269\u7684\u6D3B\u751F\u751F\u7684\u91CD\u8981\u6027\u800C\u5F97\u5230\u7684\u5355\u7EAF\u4E8B\u5B9E\u7684\u50F5\u6B7B\u7684\u62BD\u8C61.</p></blockquote><p>\u9053\u5FB7, \u903B\u8F91, \u5B97\u6559, \u827A\u672F\u8FD9\u4E9B\u540D\u8BCD\u88AB\u5BA3\u5E03\u5305\u542B\u4E86\u91CD\u8981\u6027\u7684\u5168\u90E8\u542B\u4E49. \u4F46\u5B58\u5728\u4E0E\u5B83\u4EEC\u65E0\u5173\u7684\u89C6\u57DF. \u6240\u4EE5\u5BF9\u9053\u5FB7, \u601D\u7EF4\u8FC7\u7A0B, \u795E\u5BC6\u60C5\u611F\u548C\u7F8E\u7684\u7EF4\u62A4, \u4F7F\u8868\u8FBE\u878D\u5165\u81EA\u7136\u8FC7\u7A0B\u7684\u7EC8\u6781\u76EE\u7684\u7684\u6D3B\u52A8\u53D8\u5F97\u7410\u788E.</p><p>\u91CD\u8981\u6027\u8BA9\u4EBA\u60F3\u5230\u81EA\u50B2, \u8FD9\u79CD\u81EA\u50B2\u4F7F\u91CD\u8981\u6027\u7684\u610F\u4E49\u6781\u7AEF\u7410\u788E\u5316. \u8BCD\u8BED\u5FC5\u987B\u8D85\u51FA\u65E5\u5E38\u751F\u6D3B, \u4F46\u5FC5\u987B\u4EE5\u65E5\u5E38\u751F\u6D3B\u4E2D\u7684\u9884\u5148\u505A\u51FA\u7684\u5047\u5B9A\u4F5C\u4E3A\u5176\u57FA\u7840.</p><blockquote><p>\u5F53\u6211\u4EEC\u63A5\u89E6\u54F2\u5B66\u65F6, \u5C31\u5E94\u5F53\u6392\u9664\u77E5\u8BC6. \u6211\u4EEC\u5E94\u5F53\u4F9D\u9760\u4ECE\u6587\u660E\u7684\u4E00\u822C\u793E\u4F1A\u5173\u7CFB\u4E2D\u4EA7\u751F\u7684\u6DF3\u6734\u6982\u5FF5.</p></blockquote><p>\u5E26\u5C0F\u670B\u53CB\u770B\u5361\u95E8\u7684\u4F8B\u5B50: &quot;\u8FD9\u4E9B\u4EBA\u771F\u7684\u662F\u597D\u4EBA\u5417?&quot;</p><blockquote><p>\u5F53\u4ED6\u4EEC\u5728\u821E\u53F0\u4E0A\u53C8\u6B4C\u53C8\u821E\u65F6, \u9053\u5FB7\u6D88\u5931\u4E86, \u5269\u4E0B\u7684\u53EA\u662F\u7F8E</p><p>\u5728\u97F3\u4E50\u4E2D, \u821E\u6B65\u4E2D\u4EE5\u53CA\u5267\u573A\u7684\u4E00\u822C\u6B22\u4E50\u4E2D, \u9053\u5FB7\u9690\u800C\u4E0D\u73B0\u4E86.</p></blockquote><p>\u9053\u5FB7\u6709\u5176\u9884\u5148\u7684\u5047\u5B9A, \u5426\u5219\u4FBF\u662F\u7A7A\u6D1E\u7684\u547D\u9898. \u8BED\u8A00\u7684\u76F8\u5BF9\u7A33\u5B9A\u6027\u4EA7\u751F\u4E86\u9053\u5FB7\u89C4\u5219\u5177\u6709\u7EDD\u5BF9\u7A33\u5B9A\u6027\u7684\u5E7B\u89C9. \u9053\u5FB7\u89C4\u5219\u7684\u7ED3\u8BBA\u4E0D\u80FD\u6269\u5927, \u4EE5\u81F4\u5B83\u5426\u5B9A &quot;\u9053\u5FB7&quot; \u4E00\u8BCD\u7684\u4EFB\u4F55\u610F\u4E49.</p>',24),c=[q];function l(r,n){return t(),p("div",null,c)}var a=o(e,[["render",l],["__file","\u91CD\u8981\u6027.html.vue"]]);export{a as default};
