import{_ as e,o as p,c as l,e as r}from"./app.efeddd24.js";var i="/blog/img/project/shangyemoshi02/01.jpeg",a="/blog/img/project/shangyemoshi02/03.jpeg",h="/blog/img/project/shangyemoshi02/04.jpeg",t="/blog/img/project/shangyemoshi02/05.jpeg",_="/blog/img/project/shangyemoshi02/06.jpeg";const s={},o=r('<h1 id="\u957F\u5C3E\u5546\u4E1A\u6A21\u5F0F-\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u51C9\u4E86" tabindex="-1"><a class="header-anchor" href="#\u957F\u5C3E\u5546\u4E1A\u6A21\u5F0F-\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u51C9\u4E86" aria-hidden="true">#</a> \u957F\u5C3E\u5546\u4E1A\u6A21\u5F0F\uFF1A\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u51C9\u4E86</h1><p><img src="'+i+'" alt="\u5C01\u9762"></p><h2 id="\u4E00-\u4EC0\u4E48\u662F\u957F\u5C3E\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u4EC0\u4E48\u662F\u957F\u5C3E\u6A21\u5F0F" aria-hidden="true">#</a> \u4E00 \u4EC0\u4E48\u662F\u957F\u5C3E\u6A21\u5F0F</h2><h3 id="_1-\u51FA\u7248\u4E1A\u7684\u53D8\u9769" tabindex="-1"><a class="header-anchor" href="#_1-\u51FA\u7248\u4E1A\u7684\u53D8\u9769" aria-hidden="true">#</a> 1 \u51FA\u7248\u4E1A\u7684\u53D8\u9769</h3><p>\u4F20\u7EDF\u51FA\u7248\u793E\u7684\u8FD0\u8425\u6A21\u5F0F\u5982\u4E0B:</p><ul><li>\u4F5C\u5BB6\u63D0\u4EA4\u624B\u7A3F;</li><li>\u51FA\u7248\u793E\u5BA1\u9605\u5E76\u7504\u9009\u51FA\u53EF\u80FD\u8FBE\u5230\u6700\u5C0F\u9500\u91CF\u7684\u4F5C\u54C1;</li><li>\u800C\u540E\u6392\u7248\u5370\u5237;</li></ul><p><img src="'+i+'" alt="\u51FA\u7248\u4E1A\u7684\u53D8\u9769"></p><p>\u4F20\u7EDF\u51FA\u7248\u793E\u4E4B\u6240\u4EE5\u91C7\u7528\u8FD9\u79CD\u6A21\u5F0F, \u662F\u56E0\u4E3A\u4F20\u7EDF\u51FA\u7248\u4E1A\u4F9D\u9760\u89C4\u6A21\u7ECF\u6D4E. \u5982\u679C\u4E00\u672C\u4E66\u65E0\u6CD5\u5356\u5230\u4E00\u5B9A\u89C4\u6A21, \u90A3\u5B83\u7684\u6536\u76CA\u662F\u65E0\u6CD5\u8986\u76D6\u5176\u6392\u7248, \u8BBE\u8BA1, \u5370\u5237\u548C\u9500\u552E\u8FD9\u4E9B\u4E66\u7684\u4381\u672C\u7684.</p><p>\u56E0\u6B64\u51FA\u7248\u793E\u53EA\u80FD\u628A\u76EE\u5149\u805A\u7126\u5230\u5927\u8303\u56F4\u7684\u8BFB\u8005\u8EAB\u4E0A. \u800C\u8FD9\u5C31\u9700\u8981\u901A\u8FC7\u7504\u9009\u6765\u627E\u51FA\u7B26\u5408\u5927\u4F17\u53E3\u5473\u7684\u4F5C\u54C1</p><p>Lulu.com \u6539\u53D8\u4E86\u4F20\u7EDF\u7684\u51FA\u7248\u6A21\u5F0F.</p><p>\u8FD9\u5BB6\u7F51\u7AD9\u901A\u8FC7\u5174\u8DA3\u793E\u533A\u548C\u5728\u7EBF\u6863\u6848, \u4E3A\u5C0F\u4F17\u4F5C\u5BB6\u548C\u5C0F\u4F17\u8BFB\u8005\u5EFA\u7ACB\u8054\u7CFB. \u901A\u8FC7\u81EA\u52A9\u51FA\u7248\u670D\u52A1\u4F7F\u5C0F\u4F17\u4F5C\u5BB6\u7684\u4F5C\u54C1\u5F97\u4EE5\u51FA\u7248, \u5E76\u4E3A\u5176\u63D0\u4F9B\u4EA4\u6613\u7684\u573A\u6240\u4F7F\u8FD9\u4E9B\u4F5C\u54C1\u53EF\u4EE5\u8FBE\u5230\u5C0F\u4F17\u8BFB\u8005\u624B\u4E2D.</p><p>\u5728\u8FD9\u79CD\u6A21\u5F0F\u4E0B, \u53EA\u6709\u5728\u8BA2\u5355\u4EA7\u751F\u7684\u60C5\u51B5\u4E0B, \u4E66\u624D\u4F1A\u88AB\u5370\u51FA\u6765</p><p><img src="'+a+'" alt="lulu.com"></p><h3 id="_2-\u4E50\u9AD8\u7684\u65B0\u957F\u5C3E\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_2-\u4E50\u9AD8\u7684\u65B0\u957F\u5C3E\u6A21\u5F0F" aria-hidden="true">#</a> 2 \u4E50\u9AD8\u7684\u65B0\u957F\u5C3E\u6A21\u5F0F</h3><p>2005 \u5E74, \u4E50\u9AD8\u63A8\u51FA\u4E86 \u201C\u4E50\u9AD8\u5DE5\u5382\u201D , \u4F7F\u5F97\u5BA2\u6237\u5F97\u4EE5\u4F9D\u7167\u4E2A\u4EBA\u610F\u613F\u7EC4\u5408\u4E50\u9AD8\u5957\u88C5, \u5E76\u53EF\u4EE5\u5728\u7EBF\u5B8C\u6210\u8BA2\u8D2D. \u7531\u6B64\u4E50\u9AD8\u8FDB\u5165\u4E86\u957F\u5C3E\u6A21\u5F0F\u7684\u9886\u57DF.</p><p>\u201C\u4E50\u9AD8\u5DE5\u5382\u201D \u5728\u7EBF\u9500\u552E\u7528\u6237\u8BBE\u8BA1\u7684\u5957\u88C5, \u8FD9\u6709\u6548\u7684\u62D3\u5C55\u4E86\u5176\u805A\u7126\u4E8E\u751F\u4EA7\u6709\u9650\u7545\u552E\u5957\u88C5\u7684\u751F\u4EA7\u7EBF.</p><p><img src="'+a+'" alt="\u4E50\u9AD8"></p><p>\u201C\u4E50\u9AD8\u5DE5\u5382\u201D \u7684\u5546\u4E1A\u6A21\u5F0F\u8981\u6C42\u5176\u4F9B\u5E94\u94FE\u57FA\u7840\u8BBE\u65BD\u7684\u8F6C\u578B, \u5E76\u4E14\u7531\u4E8E\u5B9A\u5236\u5957\u7684\u4F4E\u9700\u6C42\u91CF, \u4E50\u9AD8\u7684\u652F\u6301\u7CFB\u7EDF\u8FD8\u65E0\u6CD5\u5B8C\u5168\u9002\u5E94 \u201C\u4E50\u9AD8\u5DE5\u5382\u201D \u7684\u65B0\u6A21\u5F0F.</p><p>\u4F46\u8FD9\u662F\u4E50\u9AD8\u5B9E\u73B0\u957F\u5C3E\u6A21\u5F0F\u7684\u7B2C\u4E00\u6B65. \u957F\u5C3E\u6A21\u5F0F\u53EF\u80FD\u662F\u4E50\u9AD8\u539F\u6709\u5546\u4E1A\u6A21\u5F0F\u7684\u8865\u5145.</p><h2 id="\u4E8C-\u957F\u5C3E\u6A21\u5F0F\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u957F\u5C3E\u6A21\u5F0F\u539F\u7406" aria-hidden="true">#</a> \u4E8C \u957F\u5C3E\u6A21\u5F0F\u539F\u7406</h2><p>\u957F\u5C3E\u6982\u5FF5\u6700\u521D\u7684\u63D0\u51FA\u662F\u4E3A\u4E86\u63CF\u8FF0\u4F20\u5A92\u884C\u4E1A\u53D1\u751F\u7684\u8F6C\u53D8: \u7531\u9500\u552E\u5C11\u91CF\u7684\u4EBA\u6C14\u4EA7\u54C1\u8F6C\u5411\u9500\u552E\u591A\u79CD\u7C7B\u578B\u7684\u5C0F\u4F17\u4EA7\u54C1, \u800C\u6BCF\u79CD\u4EA7\u54C1\u7684\u9500\u91CF\u90FD\u5F88\u5C0F.</p><p>\u4E3A\u4EC0\u4E48\u6709\u7684\u4F01\u4E1A\u53EF\u4EE5\u4ECE\u670D\u52A1\u4E8E\u5927\u4F17\u5E02\u573A\u7684\u89C4\u6A21\u7ECF\u6D4E, \u8F6C\u5411\u670D\u52A1\u4E8E\u5C0F\u4F17\u7528\u6237\u7684\u957F\u5C3E\u6A21\u5F0F\u5462? \u6709\u4E09\u4E2A\u539F\u56E0:</p><ul><li>\u751F\u4EA7\u5DE5\u5177\u7684\u666E\u53CA\u964D\u4F4E\u4E86\u751F\u4EA7\u7684\u6210\u672C;</li><li>\u9500\u552E\u6E20\u9053\u7684\u666E\u53CA\u964D\u4F4E\u4E86\u6D41\u901A\u7684\u6210\u672C;</li><li>\u4E92\u8054\u7F51\u8FDE\u63A5\u4F9B\u9700\u53CC\u65B9, \u4F7F\u4F9B\u9700\u4FE1\u606F\u9AD8\u5EA6\u5339\u914D.</li></ul><p><img src="'+h+'" alt="\u957F\u5C3E\u6A21\u5F0F"></p><p>\u6211\u4EEC\u770B\u5230, \u957F\u5C3E\u6A21\u5F0F\u5173\u6CE8\u7684\u662F\u5C0F\u4F17\u5BA2\u6237\u7684\u975E\u6807\u51C6\u9700\u6C42. \u800C\u8FD9\u4E9B\u9700\u6C42\u4E4B\u6240\u4EE5\u53EF\u4EE5\u88AB\u6EE1\u8DB3, \u662F\u56E0\u4E3A\u6709\u5339\u914D\u7684\u975E\u6807\u51C6\u670D\u52A1\u63D0\u4F9B\u8005. \u800C\u628A\u5B83\u4EEC\u8054\u63A5\u8D77\u6765\u7684\u662F\u4E92\u8054\u7F51.</p><h2 id="\u4E09-\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u8981\u51C9\u4E86" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u8981\u51C9\u4E86" aria-hidden="true">#</a> \u4E09 \u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u8981\u51C9\u4E86</h2><p>\u4ECA\u5929\u521A\u4ECE\u670B\u53CB\u5708\u91CC\u770B\u5230 ofo \u9000\u4E0D\u4E86\u62BC\u91D1\u7684\u4E8B. \u770B\u6765\u5171\u4EAB\u5355\u8F66\u786E\u5B9E\u662F\u8981\u51C9\u4E86.</p><p>\u5171\u4EAB\u7ECF\u6D4E\u7684\u903B\u8F91\u672C\u8D28\u4E0A\u662F\u957F\u5C3E\u6A21\u5F0F:</p><ul><li>\u4F20\u7EDF\u5546\u6765\u6A21\u5F0F\u4F9D\u8D56\u4E8E\u89C4\u6A21\u7ECF\u6D4E. \u89C4\u6A21\u7ECF\u6D4E\u5173\u6CE8\u7684\u7126\u70B9\u662F\u6807\u51C6\u5316\u7684\u5927\u4F17\u5E02\u573A;</li><li>\u8FD9\u4F7F\u5927\u91CF\u7684\u5C0F\u4F17\u5E02\u573A\u7684\u975E\u6807\u51C6\u5316\u957F\u5C3E\u9700\u6C42\u65E0\u6CD5\u5F97\u5230\u6EE1\u8DB3;</li><li>\u5171\u4EAB\u7ECF\u6D4E\u627F\u8BFA\u901A\u8FC7\u4E92\u8054\u7F51, \u5C06\u793E\u4F1A\u7684\u95F2\u7F6E\u8D44\u6E90\u5229\u7528\u8D77\u6765;</li><li>\u56E0\u4E3A\u8FD9\u4E9B\u8D44\u6E90\u672C\u8EAB\u662F\u95F2\u7F6E\u8D44\u6E90, \u6240\u4EE5\u4ED6\u4EEC\u7684\u4F7F\u7528\u6210\u672C\u975E\u5E38\u4F4E;</li><li>\u56E0\u4E3A\u8FD9\u4E9B\u8D44\u6E90\u672C\u8EAB\u7531\u4E2A\u4EBA\u63D0\u4F9B, \u6240\u4EE5\u4ED6\u4EEC\u672C\u8EAB\u5177\u6709\u975E\u6807\u51C6\u5316\u7684\u7279\u8D28;</li><li>\u5171\u4EAB\u7ECF\u6D4E\u627F\u8BFA\u5C06\u8FD9\u4E9B\u4E2A\u4EBA\u63D0\u4F9B\u7684\u95F2\u7F6E\u8D44\u6E90\u4E0E\u5C0F\u4F17\u5E02\u573A\u7684\u957F\u5C3E\u9700\u6C42\u8FDB\u884C\u5339\u914D, \u4ECE\u800C\u6EE1\u8DB3\u8FD9\u4E9B\u957F\u5C3E\u9700\u6C42.</li></ul><p><img src="'+t+'" alt="\u5171\u4EAB\u5355\u8F66"></p><p>\u5171\u4EAB\u5355\u8F66\u53F7\u79F0\u81EA\u5DF1\u662F\u5171\u4EAB\u7ECF\u6D4E, \u4F46\u4E8B\u5B9E\u4E0A\u5B83\u5E76\u4E0D\u662F:</p><ul><li>\u5171\u4EAB\u5355\u8F66\u786E\u5B9E\u9762\u5BF9\u7740\u975E\u6807\u51C6\u5316\u7684\u957F\u5C3E\u9700\u6C42: \u5C0F\u533A\u5230\u8F66\u7AD9, \u8F66\u7AD9\u5230\u516C\u53F8\u7684\u8FD9\u4E9B\u4F20\u7EDF\u516C\u5171\u4EA4\u901A\u65E0\u6CD5\u89E6\u53CA\u7684\u5730\u65B9;</li><li>\u5171\u4EAB\u7ECF\u6D4E\u662F\u901A\u8FC7\u7531\u591A\u6570\u975E\u6807\u51C6\u5316\u7684\u5C0F\u4F17\u670D\u52A1\u63D0\u4F9B\u8005\u89E3\u51B3\u5927\u91CF\u7684\u975E\u6807\u51C6\u5316\u957F\u5C3E\u9700\u6C42, \u4F46\u5171\u4EAB\u5355\u8F66\u4E0D\u662F, \u5B83\u901A\u8FC7\u81EA\u5DF1\u7684\u8FD0\u7EF4\u6765\u4E3A\u975E\u6807\u51C6\u5316\u7684\u957F\u5C3E\u9700\u6C42\u63D0\u4F9B\u670D\u52A1:</li><li>\u8FD9\u5BFC\u81F4\u4ED6\u5FC5\u987B\u82B1\u8D39\u5927\u91CF\u7684\u6210\u672C\u6765\u8FDB\u884C\u8FD0\u7EF4:</li><li>\u96C7\u4F63\u5927\u91CF\u7684\u8FD0\u7EF4\u4EBA\u5458\u5C06\u805A\u96C6\u8D77\u6765\u5355\u8F66\u6295\u653E\u5230\u9700\u8981\u5730\u6BB5;</li><li>\u800C\u96C6\u4E2D\u7684\u8FD0\u7EF4\u65E0\u6CD5\u987E\u53CA\u5982\u6B64\u5927\u91CF\u7684\u975E\u6807\u51C6\u5316\u9700\u6C42, \u56E0\u6B64\u9020\u6210\u5927\u91CF\u7684\u635F\u574F\u7684\u4E22\u5931, \u8FD9\u7531\u8FDB\u4E00\u6B65\u589E\u52A0\u4E86\u6210\u672C.</li></ul><p><img src="'+_+'" alt="\u5171\u4EAB\u5355\u8F66"></p><p>\u957F\u5C3E\u6A21\u5F0F\u5F97\u4EE5\u6210\u7ACB, \u9996\u5148\u662F\u56E0\u4E3A\u6210\u672C\u7684\u964D\u4F4E\u4F7F\u5F97\u66F4\u591A\u7684\u670D\u52A1\u63D0\u4F9B\u8005\u5F97\u4EE5\u8FDB\u5165, \u800C\u4E92\u8054\u7F51\u53C8\u5C06\u8FD9\u4E9B\u670D\u52A1\u63D0\u4F9B\u8005\u5339\u914D\u7ED9\u4E86\u957F\u5C3E\u7528\u6237.</p><p>\u800C\u5171\u4EAB\u5355\u8F66\u5219\u65E2\u6CA1\u6709\u5F15\u5165\u66F4\u591A\u7684\u670D\u52A1\u63D0\u4F9B\u8005, \u51FA\u6CA1\u6709\u771F\u6B63\u7684\u964D\u5E95\u6210\u672C, \u53EA\u662F\u4E00\u5473\u7684\u901A\u5730\u8865\u8D34\u7ED9\u4EBA\u4EEC\u4E00\u79CD \u201C\u5171\u4EAB\u7684\u5047\u8C61\u201D.</p><p>\u5047\u8C61\u7EC8\u7A76\u4E0D\u80FD\u957F\u4E45, \u800C\u73B0\u5728\u5171\u4EAB\u5355\u8F66\u770B\u8D77\u6765\u771F\u662F\u8981\u51C9\u4E86.</p><p>\u73B0\u5728\u60F3\u6765, \u5982\u679C\u4ED6\u4EEC\u8001\u8001\u5B9E\u5B9E\u628A\u81EA\u5DF1\u7684\u4E1A\u52A1\u5B9A\u4F4D\u4E3A\u77ED\u79DF\u4E1A\u52A1, \u4E0D\u77E5\u9053\u4F1A\u4E0D\u4F1A\u662F\u53E6\u4E00\u4E2A\u7ED3\u5C40.</p>',37),c=[o];function d(n,m){return p(),l("div",null,c)}var u=e(s,[["render",d],["__file","\u957F\u5C3E\u5546\u4E1A\u6A21\u5F0F\uFF1A\u5171\u4EAB\u5355\u8F66\u548B\u770B\u8D77\u6765\u4E86\u51C9\u4E86.html.vue"]]);export{u as default};
