import{_ as t,o,c as u,e}from"./app.efeddd24.js";const p={},q=e('<h2 id="\u8981\u7259\u7B7E\u7684\u4E5E\u4E10" tabindex="-1"><a class="header-anchor" href="#\u8981\u7259\u7B7E\u7684\u4E5E\u4E10" aria-hidden="true">#</a> \u8981\u7259\u7B7E\u7684\u4E5E\u4E10</h2><p>\u6709\u8FD9\u4E48\u4E2A\u7B11\u8BDD:</p><blockquote><p>\u996D\u5E97\u4E2D\u4E00\u684C\u98DF\u5BA2\u6B63\u5728\u5403\u996D, \u7A81\u7136\u6709\u4E2A\u4E5E\u4E10\u7A81\u7136\u8DD1\u8FDB\u6765. &quot;\u8001\u7237\u4EEC\u884C\u884C\u597D, \u65BD\u820D\u6839\u7259\u7B7E\u5427.&quot; \u98DF\u5BA2\u4EEC\u90FD\u662F\u4F53\u9762\u4EBA, \u600E\u4E48\u4F1A\u820D\u4E0D\u5F97\u6839\u7259\u7B7E, \u81EA\u7136\u662F\u5927\u65B9\u65BD\u820D\u4E86. \u4F46\u6CA1\u60F3\u5230\u63A5\u4E8C\u8FDE\u4E09\u7684\u6709\u4E5E\u4E10\u6765\u8981\u7259\u7B7E. \u98DF\u5BA2\u4EEC\u90FD\u4E00\u5934\u96FE\u6C34, \u4E0D\u77E5\u7F18\u6545. \u76F4\u5230\u53C8\u6709\u4E00\u4E2A\u4E5E\u4E10\u8DD1\u8FDB\u6765. &quot;\u8001\u7237\u4EEC\u884C\u884C\u597D, \u65BD\u820D\u6839\u5438\u7BA1\u5427.&quot; \u98DF\u5BA2\u4EEC\u597D\u5947 &quot;\u54A6, \u522B\u4EBA\u90FD\u8981\u7259\u7B7E, \u600E\u4E48\u53EA\u6709\u4F60\u8981\u5438\u7BA1?&quot; \u4E5E\u4E10\u7740\u6025\u7684\u8BF4: &quot;\u521A\u624D\u6709\u4EBA\u559D\u591A, \u5728\u95E8\u53E3\u5410\u4E86. \u90A3\u4E9B\u8981\u5230\u7259\u7B7E\u7684\u5DF2\u7ECF\u628A\u5E72\u7684\u6311\u5B8C\u4E86, \u8001\u7237\u4EEC\u8D4F\u6839\u5F15\u7BA1, \u6211\u8FD8\u80FD\u843D\u70B9\u7A00\u7684\u5403.&quot;</p></blockquote><p>\u4E00\u6BB5\u65F6\u95F4\u4EE5\u6765, \u603B\u89C9\u5F97\u8EAB\u8FB9\u7684\u4EBA -- \u5305\u62EC\u73B0\u5B9E\u4EA4\u5F80\u5230\u7684\u4EBA\u548C\u7F51\u4E0A\u7684\u7F51\u53CB -- \u6709\u79CD\u5BF9\u77E5\u8BC6\u6216\u9053\u7406\u6311\u5254\u7684\u6001\u5EA6: \u65E0\u8BBA\u662F\u4EC0\u4E48\u9053\u7406, \u603B\u60F3\u627E\u51FA\u5B83\u4F55\u4EE5\u4E0D\u8D77\u4F5C\u7528\u7684\u8BF4\u6CD5\u6765. \u5F62\u800C\u4E0A\u5230\u7EF4\u7279\u6839\u65AF\u5766, \u5F62\u800C\u4E0B\u5230 pmp \u9879\u76EE\u7BA1\u7406\u7406\u8BBA, \u51E1\u80FD\u8BF4\u51FA &quot;\u9053\u7406&quot; \u7684, \u90FD\u80FD\u88AB\u8BF4\u6210\u662F\u9648\u8BCD\u6EE5\u8C03. \u6BCF\u542C\u5230\u8FD9\u79CD\u9AD8\u8BBA, \u6211\u603B\u8981\u5F3A\u5FCD\u4F4F\u53BB\u8BB2 &quot;\u5982\u4EBA\u996E\u6C34, \u51B7\u66A7\u81EA\u77E5&quot; \u7684\u9053\u7406: \u4EBA\u8981\u4ECE\u9053\u7406\u4E2D\u53D7\u76CA, \u603B\u662F\u5148\u52AA\u529B\u8BD5\u7740\u628A\u9053\u7406\u9886\u53D7\u6210\u6709\u7528\u7684\u624D\u884C, \u5C31\u5982\u4EBA\u559D\u6C34, \u662F\u51C9\u662F\u70ED, \u80FD\u4E0D\u80FD\u54BD\u5230\u5410\u5B50\u91CC, \u603B\u662F\u81EA\u5DF1\u51B3\u5B9A\u7684. \u7136\u800C\u7EC8\u4E8E, \u6211\u8FD8\u662F\u5FCD\u5F80\u6CA1\u8BB2\u8FD9\u4E2A\u9053\u7406. \u56E0\u4E3A\u8FD9\u6BD5\u7ADF\u662F\u4E2A\u9053\u7406, \u662F\u9053\u7406, \u5C31\u603B\u80FD\u88AB\u8BF4\u6210\u662F\u9648\u8BCD\u6EE5\u8BCD: &quot;\u5982\u4EBA\u996E\u6C34, \u51B7\u66A7\u81EA\u77E5&quot; \u561B, \u9053\u7406\u8D77\u4E0D\u8D77\u4F5C\u7528, \u603B\u8981\u770B\u542C\u7684\u4EBA\u542C\u7684\u8FDB\u542C\u4E0D\u8D77\u4E86.</p><p>\u76F4\u5230\u6709\u4E00\u5929\u6211\u60F3\u8D77\u8FD9\u4E2A\u7B11\u8BDD, \u5C31\u91CA\u7136\u4E86. \u6211\u8FD9\u662F\u4EE5\u4E5E\u4E10\u4E4B\u5FC3\u5EA6\u98DF\u5BA2\u4E4B\u8179\u4E86. \u5BF9\u4E8E\u9988\u4E4F\u5982\u4E5E\u4E10\u7684\u4EBA, \u65E0\u8BBA\u4EC0\u4E48\u4E1C\u897F, \u603B\u8981\u8BD5\u7740\u4ECE\u4E2D\u627E\u51FA\u4EF7\u503C\u6765, \u54EA\u6015\u662F\u5410\u51FA\u7684\u79FD\u7269, \u4E5F\u8981\u6311\u5E72\u7684\u5438\u7A00\u7684\u5229\u7528\u8D77\u6765. \u6240\u4EE5\u5BF9\u4E8E\u9053\u7406, \u6211\u603B\u8BD5\u7740\u53BB\u8D77\u5B83\u4F55\u8D77\u4F1A\u8D77\u4F5C\u7528\uFF0C \u5047\u8BBE\u5728\u54EA\u79CD\u60C5\u51B5\u4E0B\u6211\u80FD\u7528\u4E0A. \u81F3\u4E8E\u90A3\u4E9B\u4E30\u6C9B\u5230\u8981\u5F80\u51FA\u5410\u7684\u98DF\u5BA2, \u4ED6\u4EEC\u591A\u89C1\u5230\u4E00\u70B9, \u90FD\u89C9\u5F97\u6076\u5FC3.</p><p>\u6709\u7684\u4EBA\u7684\u76DB\u5BB4, \u522B\u4EBA\u770B\u6765\u53EA\u662F\u6218\u573A.</p><h2 id="\u6D17\u5730\u7684\u798F\u97F3" tabindex="-1"><a class="header-anchor" href="#\u6D17\u5730\u7684\u798F\u97F3" aria-hidden="true">#</a> \u6D17\u5730\u7684\u798F\u97F3</h2><p>\u5728\u77E5\u4E4E\u4E0A\u770B\u5230\u4E00\u4E2A\u8BF4\u6CD5: \u300A\u5723\u7ECF\u300B\u7684\u798F\u97F3\u4E66\u662F\u5728\u7ED9\u7F57\u9A6C\u4EBA\u6D17\u5730.</p><p>\u4F9D\u636E\u662F\u300A\u739B\u7AA6\u798F\u97F3\u300B\u6700\u540E\u4E09\u7AE0\u91CC, \u5199\u8036\u7A23\u53D7\u96BE\u65F6, \u6BCF\u6BCF\u5199\u5230\u7F57\u9A6C\u603B\u7763\u5F7C\u62C9\u591A\u5BF9\u5904\u6B7B\u8036\u7A23\u7684\u6001\u5EA6, \u603B\u662F\u4E0D\u60C5\u4E0D\u613F: \u5148\u662F\u5199\u5F7C\u62C9\u591A\u7684\u8001\u5A46\u68A6\u5230\u8036\u7A23\u662F\u4E49\u4EBA, \u4E0D\u8BA9\u4ED6\u5224\u8036\u7A23\u6B7B\u5211; \u518D\u8BB2\u5F7C\u62C9\u591A &quot;\u539F\u6765\u4ED6\u77E5\u9053\uFF0C\u4ED6\u4EEC\u662F\u7531\u4E8E\u5AC9\u5FCC\u624D\u628A\u4ED6\u89E3\u9001\u6765\u7684&quot;; \u7B49\u5230\u5224\u4E86\u8036\u7A23\u6B7B\u5211, &quot;\u5C31\u62FF\u6C34\uFF0C\u5F53\u7740\u6C11\u4F17\u6D17\u624B\u8BF4\uFF1A&#39;\u5BF9\u8FD9\u4E49\u4EBA\u7684\u8840\uFF0C\u6211\u662F\u65E0\u7F6A\u7684\uFF0C\u4F60\u4EEC\u81EA\u5DF1\u8D1F\u8D23\u5427\uFF01&#39;&quot;. \u4F3C\u4E4E\u5F7C\u62C9\u591A\u662F\u88AB\u72B9\u592A\u4EBA\u903C\u7740\u6740\u4E86\u8036\u7A23.</p><p>\u4F46\u662F, \u72B9\u592A\u4EBA\u5904\u6B7B\u4EBA\u7684\u65B9\u5F0F\u662F\u77F3\u5211 -- \u5373\u7528\u4E71\u77F3\u7838\u6B7B -- \u800C\u9489\u4E0A\u5341\u5B57\u67B6, \u662F\u7F57\u9A6C\u4EBA\u5904\u5211\u7684\u65B9\u5F0F. \u8FD9\u4E8B\u600E\u4E48\u770B, &quot;\u4ECE\u6027\u8D28\u4E0A\u8BB2&quot;, \u90FD\u50CF\u662F\u5177\u6709\u7EDF\u6CBB\u5730\u4F4D\u7684\u7F57\u9A6C\u4EBA\u5904\u6B7B\u4E86\u5177\u6709\u9769\u547D\u6027\u7684\u65B0\u5174\u5B97\u6559\u9886\u8896. \u4F46\u300A\u5723\u7ECF\u65B0\u7EA6\u300B\u5374\u975E\u5E38\u8BE1\u5F02\u5C06\u6B64\u4E8B\u5851\u9020\u6210\u7F57\u9A6C\u4EBA\u5728\u72B9\u592A\u9886\u8896\u7684\u538B\u529B\u4E0B, \u5904\u6B7B\u4E86\u8036\u7A23. \u4F3C\u4E4E\u662F\u5728\u6709\u610F\u7684\u5C06\u72B9\u592A\u4EBA\u4E0E\u7EDF\u6CBB\u4ED6\u4EEC\u7684\u7F57\u9A6C\u4EBA\u7684\u77DB\u76FE\u8F6C\u79FB\u6210\u72B9\u592A\u4EBA\u81EA\u5DF1\u4E4B\u95F4\u7684\u77DB\u76FE. \u518D\u8003\u8651\u5230\u540E\u6765\u7F57\u9A6C\u5C06\u57FA\u7763\u6559\u7ACB\u4E3A\u56FD\u6559\u7684\u53F2\u5B9E, \u8FD9\u4E00\u6BB5\u5173\u4E8E\u7F57\u9A6C\u4EBA\u6740\u6B7B\u8036\u7A23\u7684\u63CF\u8FF0\u5C31\u5F88\u50CF\u662F\u5728\u4E3A\u7F57\u9A6C\u4EBA\u6D17\u5730\u4E86.</p><p>\u4E0D\u5F97\u4E0D\u8BF4, \u8FD9\u79CD\u8BF4\u6CD5\u5F88\u5177\u6709\u5A31\u4E50\u6027. \u8BA9\u6211\u60F3\u8D77\u4E00\u672C\u975E\u5E38\u559C\u6B22\u7684\u4E66 -- \u300A\u5251\u6865\u501A\u5929\u5C60\u9F99\u53F2\u300B -- \u8FD9\u4E66\u5047\u8BBE\u300A\u501A\u5929\u5C60\u9F99\u8BB0\u300B\u662F\u53F2\u5B9E, \u4EE5\u5386\u53F2\u7814\u7A76\u7684\u5F62\u5F0F, \u4EE5\u660E\u6559\u7684\u53D1\u5C55\u9690\u55BB\u4E86\u4E2D\u56FD\u8FD1\u4EE3\u53F2, \u91CC\u8FB9\u7684\u9053\u7406\u80FD\u81EA\u5706\u5176\u8BF4, \u5A31\u4E50\u6027\u4E5F\u5F88\u8DB3, \u53EA\u662F\u6211\u4EEC\u77E5\u9053\u300A\u501A\u5929\u5C60\u9F99\u8BB0\u300B\u4E0D\u662F\u53F2\u5B9E.</p><p>\u5176\u5B9E, \u53EA\u8981\u6211\u4EEC\u80FD\u591F\u4EE5 &quot;\u5386\u53F2\u4E3B\u4E49\u7684\u6001\u5EA6&quot; \u770B\u300A\u5723\u7ECF\u300B ( &quot;\u5386\u53F2\u4E3B\u4E49&quot; \u662F\u6211\u5B66\u5495\u54D2\u7684\u820C, \u5176\u5B9E\u6211\u5E76\u4E0D\u77E5\u9053 &quot;\u5386\u53F2\u4E3B\u4E49&quot; \u662F\u4EC0\u4E48\u610F\u601D, \u6211\u4F30\u4E14\u628A\u8FD9\u4E2A\u8BCD\u7406\u89E3\u6210 &quot;\u4EE5\u770B\u5386\u53F2\u7684\u65B9\u6CD5\u548C\u6001\u5EA6\u6765\u770B\u300A\u5723\u7ECF\u300B&quot; ), \u6211\u4EEC\u5B8C\u5168\u4E0D\u7528\u8FD9\u79CD\u8FC7\u4E8E\u620F\u5267\u5316\u548C\u9634\u8C0B\u8BBA\u7684\u773C\u5149\u6765\u770B\u5F85\u8FD9\u4E2A\u4E8B\u60C5. \u7F57\u9A6C\u4EBA\u5F53\u65F6, \u5BF9\u4E8E\u72B9\u592A\u4EBA\u7684\u5B97\u6559, \u5B8C\u5168\u662F\u4E00\u79CD\u8499\u903C\u7684\u72B6\u6001.</p><p>\u6211\u4EEC\u77E5\u9053, \u72B9\u592A\u4EBA\u8981\u6C42\u7F57\u9A6C\u4EBA\u5904\u6B7B\u8036\u7A23\u7684\u7406\u7531\u662F, \u8036\u7A23\u81EA\u79F0\u662F\u5929\u4E3B\u5B50. \u8FD9\u5BF9\u4E8E\u7F57\u9A6C\u4EBA\u6765\u8BF4, \u81EA\u79F0\u795E\u4E4B\u5B50\u600E\u4E48\u5C31\u8981\u88AB\u5904\u6B7B\u4E86? \u5728\u5E0C\u814A\u7F57\u9A6C\u7684\u4F20\u7EDF\u4E2D, \u662F\u4E2A\u8D35\u65CF\u5C31\u8BF4\u81EA\u5DF1\u662F\u795E\u7684\u540E\u88D4, \u8FD9\u4E2A\u5BB6\u65CF\u6765\u6E90\u4E8E\u5B99\u65AF, \u90A3\u4E2A\u5BB6\u65CF\u6765\u6E90\u4E8E\u6CE2\u585E\u51AC, \u5B9E\u5728\u6B63\u5E38\u4E0D\u8FC7\u4E86. \u751A\u81F3\u5728\u4ED6\u4EEC\u7684\u89C2\u5FF5\u91CC, \u4E00\u4E2A\u4EBA\u5982\u679C\u4E0D\u662F\u795E\u7684\u540E\u88D4, \u4ED6\u51ED\u4EC0\u4E48\u6210\u4E3A\u8D35\u65CF\u5462?</p><p>\u4ECE\u8FD9\u4E2A\u89D2\u5EA6, \u6211\u4EEC\u5B8C\u5168\u53EF\u4EE5\u7406\u89E3, \u4E3A\u4EC0\u4E48\u5728\u5F7C\u62C9\u591A\u770B\u6765\u8036\u7A23\u53EF\u4EE5\u662F\u4E00\u4E2A\u4E49\u4EBA. \u751A\u81F3\u53EF\u4EE5\u7406\u89E3, \u5F53\u4ED6\u8BA9\u72B9\u592A\u4EBA\u4ECE\u6740\u4EBA\u8D8A\u8D27\u7684\u5927\u76D7\u5DF4\u8FA3\u5DF4\u548C\u8036\u7A23\u4E2D\u9009\u4E00\u4E2A\u91CA\u653E\u53E6\u4E00\u4E2A\u5904\u6B7B, \u800C\u72B9\u592A\u4EBA\u9009\u62E9\u91CA\u653E\u5DF4\u8FA3\u5DF4\u5904\u6B7B\u8036\u7A23\u65F6, \u4ED6\u9707\u60CA\u5FC3\u60C5, \u4EE5\u81F3\u4E8E\u8981\u5F53\u4F17\u6D17\u624B, \u8868\u793A\u65E0\u7F6A.</p><p>\u5F53\u7136\u300A\u5723\u7ECF\u65B0\u7EA6\u300B\u662F\u5728\u7ED9\u7F57\u9A6C\u4EBA\u6D17\u5730\u8FD9\u4E2A\u8BF4\u6CD5, \u683C\u5916\u7B26\u5408 &quot;\u666E\u4E16\u5B97\u6559\u662F\u5728\u4E3A\u5E1D\u56FD\u4E3B\u4E49\u6D17\u5730&quot; \u7684\u53D9\u4E8B, \u56E0\u6B64\u5728\u67D0\u4E9B\u4EBA\u90A3\u91CC\u683C\u5916\u5177\u6709\u8BF4\u670D\u529B. \u5C31\u5982\u56E0\u4E3A\u53EF\u4EE5\u9690\u55BB\u8FD1\u4EE3\u53F2, \u300A\u501A\u5929\u5C60\u9F99\u8BB0\u300B\u8005\u53EF\u4EE5\u88AB\u5F53\u53F2\u5B9E\u4E00\u6837.</p>',15),a=[q];function r(c,_){return o(),u("div",null,a)}var d=t(p,[["render",r],["__file","\u968F\u60F3\u4E09\u5219.html.vue"]]);export{d as default};
