import{_ as o,o as t,c as r,e as n}from"./app.efeddd24.js";const s={},e=n('<h1 id="\u9879\u76EE\u7BA1\u7406\u4F55\u4EE5\u6709\u6548" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7BA1\u7406\u4F55\u4EE5\u6709\u6548" aria-hidden="true">#</a> \u9879\u76EE\u7BA1\u7406\u4F55\u4EE5\u6709\u6548</h1><h2 id="\u4F5C\u4E3A\u65B9\u6CD5\u8BBA\u7684\u9879\u76EE\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u4E3A\u65B9\u6CD5\u8BBA\u7684\u9879\u76EE\u7BA1\u7406" aria-hidden="true">#</a> \u4F5C\u4E3A\u65B9\u6CD5\u8BBA\u7684\u9879\u76EE\u7BA1\u7406</h2><p>2015 \u5E74\u7684\u67D0\u4E00\u5929\u665A\u4E0A, \u5927\u6982\u5341\u70B9\u949F\u5DE6\u53F3, \u6211\u521A\u51C6\u5907\u53BB\u7761\u89C9, \u7A81\u7136\u63A5\u5230\u516C\u53F8\u4EA7\u54C1\u603B\u76D1\u7684\u7684\u7535\u8BDD. \u4ED6\u95EE\u6211: \u4F60\u60F3\u505A\u4EA7\u54C1\u7ECF\u7406\u4E48? \u6211\u56DE\u7B54\u8BF4: \u60F3\u554A! \u4ED6\u8BF4: \u90A3\u5C31\u6765\u6D77\u5357\u51FA\u5DEE\u5427. \u7136\u540E\u7B2C\u4E8C\u5929, \u516C\u53F8\u884C\u653F\u5C31\u7ED9\u6211\u4E70\u4E86\u4E00\u5F20\u673A\u7968, \u628A\u6211\u9001\u53BB\u4E86\u6D77\u5357.</p><p>\u5176\u5B9E, \u80CC\u540E\u7684\u6545\u4E8B\u662F\u8FD9\u6837\u7684: \u53EB\u6211\u53BB\u6D77\u5357\u7684\u4EA7\u54C1\u603B\u76D1\u662F\u5F53\u65F6\u6211\u53F8\u7684\u552F\u4E00\u4EA7\u54C1\u7ECF\u7406, \u4ED6\u56E0\u4E3A\u751F\u75C5\u4E0D\u80FD\u518D\u9A7B\u573A\u53C2\u4E0E\u9879\u76EE, \u56E0\u6B64\u4ED6\u9700\u8981\u627E\u4E2A\u4EBA\u6765\u9876\u66FF\u4ED6; \u800C\u5F53\u65F6\u6211\u662F\u516C\u53F8\u4E1A\u52A1\u90E8\u95E8\u91CC\u552F\u4E00\u7A0D\u7A0D\u77E5\u9053\u4E9B\u6280\u672F\u77E5\u8BC6\u7684\u4EBA, \u6240\u4EE5\u5C31\u9009\u4E86\u6211. \u6211\u7684\u4EA7\u54C1\u7ECF\u7406\u751F\u6DAF\u5C31\u662F\u8FD9\u6837\u5F00\u59CB\u7684.</p><p>\u90A3\u662F\u4E2A\u91C7\u8D2D\u652F\u4ED8\u7CFB\u7EDF\u5EFA\u8BBE\u9879\u76EE, \u5B83\u9700\u8981\u5BF9\u63A5\u4E00\u5957\u5DF2\u5EFA\u6210\u7684\u91C7\u8D2D\u7BA1\u7406\u7CFB\u7EDF. \u8FD9\u5957\u91C7\u8D2D\u7BA1\u7406\u7CFB\u7EDF\u7684\u5EFA\u8BBE\u65B9\u8D5B\u610F\u662F\u4E00\u5BB6\u56FD\u5185\u77E5\u540D\u7684 ORALCE \u7684\u4EE3\u7406\u5546. \u5F53\u6211\u5728\u9605\u8BFB\u8D5B\u610F\u7684\u6587\u7AE0\u7684\u65F6\u5019, \u6211\u6CE8\u610F\u5230\u4E00\u4E2A\u7EC6\u8282: \u5B83\u4EEC\u6240\u6709\u7684\u6587\u6863\u90FD\u6709\u4E00\u4E2A\u526F\u6807\u9898 -- &quot;\u8D5B\u610F\u65B9\u6CD5\u8BBA&quot;.</p><p>\u540E\u6765\u6211\u518D\u542C\u5230\u7C7B\u4F3C\u7684\u8BF4\u6CD5\u662F\u5728 2018 \u5E74. \u5F53\u65F6\u6211\u5728\u4E00\u5BB6\u5927\u6570\u636E\u516C\u53F8. \u516C\u53F8\u7684\u4EA7\u54C1\u7ECF\u7406\u7ED9\u6211\u4EEC\u4ECB\u7ECD\u516C\u53F8\u7684\u9879\u76EE\u5236\u5EA6\u65F6, \u518D\u6B21\u8BF4\u4E86\u7C7B\u4F3C\u7684\u8BDD: \u9879\u76EE\u7ECF\u7406\u4E3B\u8981\u63D0\u4F9B\u4E00\u5957\u65B9\u6CD5\u8BBA, \u4ED6\u53EF\u4EE5\u5B8C\u5168\u4E0D\u61C2\u6280\u672F, \u4F46\u662F\u901A\u8FC7\u8FD9\u5957\u65B9\u6CD5\u8BBA, \u4F9D\u7136\u53EF\u4EE5\u4F7F\u9879\u76EE\u6B63\u5E38\u7684\u8FD0\u8F6C\u8D77\u6765.</p><p>\u9879\u76EE\u7BA1\u7406\u662F\u4E00\u5957\u65B9\u6CD5\u8BBA, \u8FD9\u4F3C\u4E4E\u662F\u4E00\u4E2A\u666E\u904D\u7684\u5171\u8BC6. \u7136\u800C\u4EC0\u4E48\u662F\u65B9\u6CD5\u8BBA\u5462?</p><p>\u671B\u6587\u751F\u610F\u7684\u8BF4, \u65B9\u6CD5\u8BBA\u5C31\u662F\u65B9\u6CD5\u7684\u7406\u8BBA, \u6216\u8005\u5E26\u7740\u7406\u8BBA\u7684\u65B9\u6CD5. \u65B9\u6CD5\u662F\u4E00\u5957\u5B9E\u73B0\u65E2\u5B9A\u76EE\u6807\u7684, \u89E3\u51B3\u65E2\u5B9A\u95EE\u9898\u7684\u6709\u6548\u7684, \u53EF\u91CD\u590D\u4F7F\u7528\u7684\u7ECF\u9A8C. \u7167\u7740\u4E9A\u91CC\u58EB\u591A\u5FB7\u5728\u300A\u5F62\u800C\u4E0A\u5B66\u300B\u4E2D\u8BF4\u7684, \u611F\u89C9\u4EA7\u751F\u8BB0\u5FC6, \u91CD\u590D\u7684\u8BB0\u5FC6\u4EA7\u751F\u7ECF\u9A8C, \u4ECE\u7ECF\u9A8C\u4E2D\u63D0\u7EC3\u51FA\u666E\u904D\u6027, \u5E76\u4F7F\u5176\u5F97\u4EE5\u5E7F\u6CDB\u7684\u5E94\u7528\u5C31\u662F\u6280\u672F. \u4ECE\u7ECF\u9A8C\u4E2D\u63D0\u7EC3\u51FA\u7684\u666E\u904D\u6027, \u5C31\u662F\u6211\u4EEC\u8BF4\u7684\u7406\u8BBA. \u8FD9\u4E48\u8BF4\u6765, \u65B9\u6CD5\u8BBA\u5C31\u662F\u6280\u672F.</p><p>\u65E2\u7136\u9879\u76EE\u662F <code>\u4E3A\u4E86\u63D0\u4F9B\u7279\u5B9A\u7684\u4EA7\u54C1, \u670D\u52A1\u6216\u6210\u679C\u6240\u8FDB\u884C\u7684\u4E00\u6B21\u6027\u6D3B\u52A8</code>, \u90A3\u6211\u4EEC\u5927\u53EF\u8BF4, \u4F5C\u4E3A\u65B9\u6CD5\u8BBA\u7684\u9879\u76EE\u7BA1\u7406\u662F\u4E00\u79CD <code>\u628A\u4E8B\u52A1\u4F5C\u4E3A\u4E00\u6B21\u6027\u6D3B\u52A8\u8FDB\u884C\u7BA1\u7406\u7684\u6280\u672F</code>.</p><h2 id="\u6280\u672F\u4E0E\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u4E0E\u7CFB\u7EDF" aria-hidden="true">#</a> \u6280\u672F\u4E0E\u7CFB\u7EDF</h2><p>\u636E\u8BF4\u5728\u8003\u53E4\u5B66\u4E2D, \u6709\u4E00\u79CD\u5224\u65AD\u4E00\u4E2A\u90E8\u843D\u662F\u5426\u8FDB\u5165\u65F6\u5668\u65F6\u4EE3\u7684\u6807\u5FD7: <code>\u5728\u90E8\u843D\u4E2D\u662F\u5426\u51FA\u73B0\u4E86\u5927\u91CF\u76F8\u4F3C\u7684\u77F3\u65A7</code>. \u8BDA\u5982\u4E9A\u91CC\u58EB\u591A\u5FB7\u6240\u8BF4, \u6280\u672F\u901A\u8FC7\u4ECE\u7ECF\u9A8C\u4E2D\u63D0\u7EC3\u666E\u904D\u6027\u6765\u5B9E\u73B0\u7ECF\u9A8C\u7684\u590D\u7528. \u5F53\u4E00\u4E2A\u539F\u59CB\u90E8\u843D\u4E2D\u5F00\u59CB\u51FA\u73B0 <strong>\u76F8\u4F3C</strong> \u7684\u77F3\u65A7\u65F6, \u610F\u5473\u7740\u8FD9\u6279\u5148\u6C11\u4EEC, \u5DF2\u7ECF\u5F00\u59CB <strong>\u638C\u63E1</strong> \u4E00\u79CD\u7279\u5B9A\u7684\u5C06\u77F3\u5934\u5236\u6210\u77F3\u5668\u7684 <strong>\u65B9\u6CD5</strong>, \u5E76\u5F00\u59CB\u8FDB\u884C <strong>\u590D\u7528</strong> \u4E86.</p><p>\u7136\u800C <strong>\u638C\u63E1</strong> \u662F\u4E00\u79CD\u592A\u8FC7\u73B0\u4EE3\u7684\u60F3\u6CD5. \u5BF9\u4E8E\u5148\u6C11\u6765\u8BF4, \u8FD9\u66F4\u591A\u662F\u4E00\u79CD\u81EA\u7136 <strong>\u7531\u9690\u853D\u800C\u5F00\u663E</strong> \u7684\u8FC7\u7A0B. \u8D6B\u897F\u4FC4\u5FB7\u5728\u300A\u5DE5\u4F5C\u4E0E\u65F6\u65E5\u300B\u4E2D\u8BF4:</p><blockquote><p>\u8BF8\u795E\u4E0D\u8BA9\u4EBA\u77E5\u9053\u751F\u6D3B\u7684\u65B9\u6CD5, \u5426\u5219, \u4F60\u5DE5\u4F5C\u4E00\u5929\u6216\u8BB8\u5C31\u80FD\u8F7B\u6613\u5730\u83B7\u5F97\u8DB3\u591F\u7684\u8D2E\u5907, \u4EE5\u81F3\u4E00\u6574\u5E74\u90FD\u4E0D\u9700\u8981\u518D\u4E3A\u751F\u6D3B\u800C\u52B3\u4F5C\u4E86; \u6216\u8BB8\u7ACB\u523B\u5C31\u53EF\u4EE5\u628A\u8239\u8235\u5378\u4E0B\u7F6E\u4E8E\u70DF\u4E0A, \u725B\u548C\u58EE\u9AA1\u7FFB\u8015\u8FC7\u7684\u7530\u4EA9\u53C8\u4F1A\u53D8\u6210\u8352\u5730. \u4F46\u662F, \u6124\u6012\u7684\u5B99\u65AF\u4E0D\u8BA9\u4EBA\u7C7B\u77E5\u9053\u8C0B\u751F\u4E4B\u6CD5, \u56E0\u4E3A\u72E1\u733E\u7684\u666E\u7F57\u7C73\u4FEE\u65AF\u6B3A\u9A97\u4E86\u4ED6. \u56E0\u4E9B, \u5B99\u65AF\u4E3A\u4EBA\u7C7B\u8BBE\u8BA1\u4E86\u60B2\u54C0. \u4ED6\u85CF\u8D77\u4E86\u706B\u79CD. \u4F46\u662F, \u4F0A\u963F\u4F69\u6258\u65AF\u7684\u4F18\u79C0\u513F\u5B50\u53C8\u66FF\u4EBA\u7C7B\u4ECE\u82F1\u660E\u7684\u5B99\u65AF\u90A3\u91CC\u7528\u4E00\u6839\u7A7A\u8334\u9999\u6746\u5077\u4E86\u706B\u79CD, \u800C\u8FD9\u4F4D\u96F7\u7535\u4E4B\u795E\u7ADF\u672A\u5BDF\u89C9.</p></blockquote><p>\u4EBA\u672C\u6765\u53EF\u4EE5\u5728\u81EA\u7136\u4E2D\u5B89\u4E50\u548C\u5BCC\u8DB3\u7684\u751F\u6D3B, \u4F46\u8BF8\u795E\u56E0\u6124\u6012\u800C\u9690\u85CF\u4E86 <strong>\u751F\u6D3B\u7684\u65B9\u6CD5</strong>, \u4F7F\u4EBA\u4EEC\u53EA\u80FD\u8F9B\u82E6\u6D41\u79BB. \u7C7B\u4F3C\u7684\u6545\u4E8B\u8FD8\u6709\u88AB\u4E9A\u5F53\u88AB\u9010\u51FA\u4F0A\u7538\u56ED. \u6216\u51FA\u4E8E\u795E\u7684\u6069\u8D50, \u6216\u51FA\u4E8E\u4EBA\u7684\u667A\u6167 ( \u4EBA\u7684\u667A\u6167\u4E0D\u4E5F\u662F\u795E\u7684\u6069\u8D50\u4E48? ), \u81EA\u7136\u4E2D\u9690\u85CF\u7684 <strong>\u751F\u6D3B\u7684\u65B9\u6CD5</strong> \u88AB\u518D\u6B21\u5F00\u663E\u51FA\u6765, \u88AB\u4EBA\u6D1E\u5BDF\u5230, \u8FD9\u4FBF\u662F <strong>\u6280\u672F</strong> \u4E86. \u65E0\u8BBA\u662F\u6BD5\u8FBE\u54E5\u62C9\u65AF\u4E3A\u4E86\u52FE\u80A1\u5B9A\u7406\u732E\u4E0A\u767E\u725B\u796D\u7684\u6545\u4E8B, \u8FD8\u662F\u7C73\u5F00\u6717\u57FA\u7F57\u4ECE\u77F3\u5934\u4E2D\u770B\u51FA\u5927\u536B\u7684\u6545\u4E8B, \u90FD\u5728\u8BB2\u8FF0\u8FD9\u4E2A\u7531\u9690\u853D\u800C\u5F00\u663E\u7684\u6545\u4E8B.</p><p>\u5728\u4E00\u9897\u53E4\u5178\u7684\u5FC3\u7075\u91CC, \u81EA\u7136\u662F\u4E07\u7269\u52FE\u8054\u7684\u6574\u4F53. \u8FD9\u4E2A &quot;\u4E07\u7269&quot; \u4E5F\u5305\u62EC\u7740\u4EBA\u4E0E\u4EBA\u9020\u7269. <code>\u5B50\u66F0: &quot;\u793C\u4E91\u793C\u4E91, \u7389\u5E1B\u4E91\u4E4E\u54C9; \u4E50\u4E91\u4E50\u4E91, \u949F\u9F13\u4E91\u4E4E\u54C9.&quot;</code> \u5F00\u663E\u751F\u6D3B\u65B9\u6CD5\u7684\u6280\u672F, \u5C31\u4E0D\u4EC5\u4EC5\u662F\u4E00\u5957\u5236\u4F5C\u7684\u7A0B\u5F0F, \u540C\u65F6\u4E5F\u662F\u5BF9\u81EA\u7136\u52FE\u8054\u4E07\u7269\u7684\u65B9\u5F0F\u7684\u6D1E\u67E5. \u5728\u53E4\u5E0C\u814A\u8FD9\u79CD\u52FE\u8054\u65B9\u5F0F\u662F <strong>\u52AA\u65AF</strong> ( <code>\u4F7F\u4E07\u7269\u5904\u4E8E\u5BF9\u81EA\u5DF1\u6700\u597D\u7684\u4F4D\u7F6E\u7684\u5FC3\u7075</code> ) \u548C\u63ED\u793A\u52AA\u65AF\u7684 <strong>\u903B\u683C\u65AF</strong>, \u5728\u6211\u4EEC\u8FD9\u91CC\u8FD9\u79CD\u52FE\u8054\u662F <strong>\u9053</strong> \u548C\u5B9E\u73B0\u9053\u7684 <strong>\u5FB7</strong>. \u6240\u4EE5\u7389\u6709\u4E94\u5FB7, \u9E21\u4E5F\u6709\u4E94\u5FB7, \u541B\u5B50\u4E5F\u6709\u4E94\u5FB7, <strong>\u5FB7</strong> \u52FE\u8054\u7740\u4E07\u7269\u7684\u5FC3\u7075.</p><p>\u6280\u672F\u662F\u90A3\u4E2A\u4F7F\u4EBA\u4E0E\u8FDC\u8D85\u51FA\u81EA\u5DF1\u7684\u81EA\u7136\u4EA7\u751F\u5173\u8054\u7684\u4E1C\u897F.</p><p>\u7136\u800C\u54E5\u767D\u5C3C\u7684\u9769\u547D\u5207\u65AD\u4E86\u8FD9\u4E2A\u4EBA\u4E0E\u81EA\u7136\u7684\u8054\u7CFB. \u4EBA\u5E76\u4E0D\u662F\u81EA\u7136\u7684\u4E2D\u5FC3, \u4E5F\u4E0E\u5B83\u5E76\u65E0 <strong>\u5185\u5728\u7684</strong> ( \u76EE\u7684\u8BBA\u4E0A\u7684 ) \u5173\u8054. \u81EA\u7136\u6216\u8005\u81EA\u6709\u5176\u4E0E\u4EBA\u65E0\u5173\u7684\u76EE\u7684, <strong>\u6216\u8005\u81EA\u7136\u538B\u6839\u5C31\u6CA1\u6709\u76EE\u7684</strong>. \u65E0\u8BBA\u5982\u4F55\u4EBA\u662F\u4E0D\u80FD\u60F3\u60F3, \u81EA\u7136\u4E0E\u4EBA\u540C\u5728\u4E00\u4E2A\u76EE\u7684\u4E4B\u4E0B\u4E86.</p><p>\u4E8E\u662F\u4EBA\u9020\u4E0E\u81EA\u7136\u5C31\u5272\u88C2\u5F00\u6765, \u7389\u4E5F\u6CA1\u6709\u4E94\u5FB7, \u9E21\u4E5F\u6CA1\u6709\u4E94\u5FB7, \u541B\u5B50 ...... \u5384 ...... \u968F\u4ED6\u7684\u4FBF\u5427. \u81EA\u7136\u6210\u4E3A\u4E86\u4EBA\u7684\u6539\u9020\u7684\u5BF9\u8C61, \u6280\u672F\u662F\u9A6F\u670D\u81EA\u7136\u4F7F\u5176\u6210\u4E3A\u4EBA\u9020\u7269\u7684\u624B\u6BB5. \u94A2\u7B4B\u6DF7\u51DD\u571F\u6784\u7B51\u7684\u57CE\u5E02\u4ECE\u5927\u5730\u4E0A\u62E8\u5730\u800C\u8D77, \u6C34\u575D\u622A\u65AD\u6CB3\u6D41\u4ECE\u5176\u52BF\u80FD\u4E2D\u69A8\u53D6\u7535\u529B.</p><p>\u7136\u800C\u6280\u672F\u4F9D\u7136\u662F\u54EA\u4E2A\u4F7F\u4EBA\u4E0E\u8FDC\u8D85\u81EA\u5DF1\u7684\u5B58\u5728\u4EA7\u751F\u5173\u7CFB\u7684\u4E1C\u897F. \u53EA\u662F\u8FD9\u4E2A <strong>\u8FDC\u8D85\u81EA\u5DF1\u7684\u5B58\u5728</strong> \u7531\u81EA\u7136\u6362\u6210\u4E86 <strong>\u7CFB\u7EDF</strong> \u800C\u5DF2.</p>',19),a=[e];function g(p,c){return t(),r("div",null,a)}var h=o(s,[["render",g],["__file","\u9879\u76EE\u7BA1\u74062.html.vue"]]);export{h as default};
