import{_ as o,o as e,c,e as d}from"./app.efeddd24.js";const t={},p=d('<h1 id="\u56DB\u5377\u672C\u300A\u53E4\u5E0C\u814A\u54F2\u5B66\u300B\u662F\u5173\u4E8E\u9AD8\u5C14\u5409\u4E9A\u7684\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u56DB\u5377\u672C\u300A\u53E4\u5E0C\u814A\u54F2\u5B66\u300B\u662F\u5173\u4E8E\u9AD8\u5C14\u5409\u4E9A\u7684\u4ECB\u7ECD" aria-hidden="true">#</a> \u56DB\u5377\u672C\u300A\u53E4\u5E0C\u814A\u54F2\u5B66\u300B\u662F\u5173\u4E8E\u9AD8\u5C14\u5409\u4E9A\u7684\u4ECB\u7ECD</h1><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u51FA\u5728\u5728 <code>\u897F\u897F\u91CC</code> \u4E1C\u90E8\u7684 <code>\u6797\u5730\u5C3C</code>, \u8FD9\u662F <code>\u4F0A\u5965\u5C3C\u4E9A</code> \u7684 <code>\u5361\u5C14\u897F\u65AF</code> \u4EBA\u4E8E\u516C\u5143\u524D 729 \u5E74\u5EFA\u7ACB\u7684\u6B96\u6C11\u57CE\u90A6. \u4ED6\u7684\u751F\u5352\u4E0D\u8BE6, \u4F46\u662F\u53E4\u4EE3\u8BB0\u8F7D\u90FD\u8BF4\u5230\u4ED6\u7684\u957F\u5BFF, <code>\u7B2C\u6B27\u6839\u5C3C.\u62C9\u5C14\u4FEE</code> \u5728 <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u7684\u4F20\u8BB0\u4E2D\u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u6D3B\u4E86 109 \u5C81, <code>\u666E\u6797\u5C3C</code> \u8BF4\u662F 108 \u5C81, <code>\u897F\u8D5B\u7F57</code> \u8BF4\u662F 107 \u5C81, <code>\u9C8D\u8428\u5C3C\u963F</code> \u8BF4\u662F 105 \u5C81, <code>\u963F\u7279\u7EB3\u5965</code> \u8BF4\u4ED6\u6D3B\u5230\u4E86 &quot;\u4E00\u767E\u8FC7\u4E86\u5934, \u6B7B\u5728 <code>\u82CF\u683C\u62C9\u5E95</code> \u4EE5\u540E&quot;. <code>\u666E\u9C81\u5854\u514B</code> \u5728\u300A\u6F14\u8BF4\u5BB6\u4F20\u300B\u4E2D\u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u6BD4 <code>\u5B89\u63D0\u4E30</code> \u5E74\u9752\u4E9B, <code>\u5B89\u63D0\u4E30</code> \u751F\u4E8E\u516C\u5143\u524D 408 \u5E74, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5927\u7EA6\u751F\u4E8E\u8FD9\u4E00\u5E74\u524D\u540E. \u4ED6\u5927\u4F53\u4E0A\u662F\u548C <code>\u666E\u7F57\u6CF0\u6208\u62C9</code> \u540C\u4E00\u65F6\u4EE3\u7684\u4EBA, \u4F46\u6D3B\u7684\u6BD4 <code>\u666E\u7F57\u6CF0\u6208\u62C9</code> \u957F: \u516C\u5143\u524D 399 \u5E74 <code>\u82CF\u683C\u62C9\u5E95</code> \u53D7\u5BA1\u65F6\u4ED6\u8FD8\u6D3B\u7740. <code>\u67CF\u62C9\u56FE</code> \u5728\u300A\u7533\u8FA9\u7BC7\u300B\u4E2D\u8BF4 <code>\u82CF\u683C\u62C9\u5E95</code> \u4E3A\u81EA\u5DF1\u8FA9\u62A4: &quot;\u8BF4\u6211\u6536\u8D39\u6388\u5F92\u4E5F\u4E0D\u662F\u4E8B\u5B9E, \u867D\u7136\u6211\u8BA4\u4E3A\u5982\u679C\u6211\u771F\u6709\u80FD\u529B\u6559\u4EBA\u90A3\u662F\u597D\u4E8B, \u50CF <code>\u6797\u5730\u5C3C</code> \u7684 <code>\u9AD8\u5C14\u5409\u4E9A</code>, <code>\u5F00\u5965\u65AF</code> \u7684 <code>\u666E\u7F57\u72C4\u79D1</code> \u548C <code>\u57C3\u5229\u65AF</code> \u7684 <code>\u5E0C\u5E87\u4E9A</code>. \u4ED6\u4EEC\u5468\u6E38\u5404\u4E2A\u57CE\u90A6, \u80FD\u591F\u529D\u8BF4\u5E74\u9752\u4EBA\u8DDF\u968F\u4ED6\u4EEC, \u4ED8\u7ED9\u4ED6\u4EEC\u94B1, \u8FD8\u611F\u8C22\u4ED6\u4EEC.&quot; \u897F\u65B9\u5B66\u8005\u6307\u51FA\u4ECE\u5E0C\u814A\u539F\u6587\u6765\u770B <code>\u82CF\u683C\u62C9\u5E95</code> \u63D0\u5230\u7684\u90FD\u662F\u6D3B\u7740\u7684\u4EBA, <code>\u666E\u7F57\u6CF0\u6208\u62C9</code> \u66F4\u4E3A\u6709\u540D, \u5374\u6CA1\u6709\u88AB\u63D0\u5230, \u8FD9\u662F\u56E0\u4E3A\u4ED6\u5DF2\u7ECF\u6B7B\u4E86. <code>\u9C8D\u8428\u5C3C\u963F</code> \u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u665A\u5E74\u8FD8\u540C\u5E74\u9752\u7684 <code>\u6CE2\u5415\u514B\u62C9\u5E95</code> \u6BD4\u8D5B\u4FEE\u8F9E\u548C\u6F14\u8BF4\u53D6\u5F97\u4E86\u80DC\u5229, \u4F7F <code>\u6CE2\u5415\u514B\u62C9\u5E95</code> \u5931\u53BB\u4E86 <code>\u8D34\u6492\u5C3C</code> \u5730\u533A <code>\u6590\u8D56</code> \u7684\u50ED\u4E3B <code>\u4F0A\u963F\u5B8B</code> \u7684\u5BA0\u7231. <code>\u4F0A\u963F\u5B8B</code> \u7684\u7EDF\u6CBB\u662F\u5728\u516C\u5143\u524D 380 - 370 \u5E74, \u53EF\u80FD <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5230\u90A3\u65F6\u8FD8\u6D3B\u7740.</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u5927\u7EA6\u662F <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u7684\u5B66\u751F, \u66FE\u5411\u4ED6\u5B66\u8FC7\u4FEE\u8F9E\u5B66, \u53EF\u80FD\u8FD8\u8DDF\u4ED6\u5B66\u8FC7\u533B\u5B66\u548C\u81EA\u7136\u54F2\u5B66. <code>\u7B2C\u6B27\u6839\u5C3C.\u62C9\u5C14\u4FEE</code> \u5728\u53D9\u8FF0 <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u7684\u751F\u5E73\u65F6\u8BF4: &quot;<code>\u8428\u63D0\u7F57\u65AF</code> \u5728\u4ED6\u7684\u300A\u751F\u5E73\u300B\u4E2D\u8BF4, <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u8FD8\u662F\u4F4D\u533B\u751F\u548C\u5353\u8D8A\u7684\u6F14\u8BF4\u5BB6, <code>\u6797\u5730\u5C3C</code> \u7684 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u4FEE\u8F9E\u5B66\u65B9\u9762\u662F\u5F88\u6709\u540D\u7684, \u8FD8\u662F\u6709\u5173\u8FD9\u4E00\u6280\u827A\u7684\u8BBA\u8457\u7684\u4F5C\u8005, \u80AF\u5B9A\u505A\u8FC7 <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u7684\u5B66\u751F. <code>\u963F\u6CE2\u7F57\u591A\u6D1B</code> \u5728\u7F16\u5E74\u53F2\u4E2D\u8BF4, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u6D3B\u4E86\u4E00\u767E\u96F6\u4E5D\u5C81, <code>\u8428\u63D0\u7F57\u65AF</code> \u5F15\u4E86 <code>\u963F\u6CE2\u7F57\u591A\u6D1B</code> \u7684\u8BDD\u65F6\u8BF4, <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u8868\u73B0\u4ED6\u7684\u6CD5\u672F\u65F6 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u4E5F\u5728\u573A.&quot; \u5728 <code>\u67CF\u62C9\u56FE</code> \u7684\u300A\u9AD8\u5C14\u5409\u4E9A\u300B\u7BC7\u4E2D, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u8BF4\u4ED6\u7684\u5144\u5F1F\u4E5F\u662F\u533B\u751F, \u5E76\u8BC1\u660E\u4FEE\u8F9E\u5B66\u9AD8\u4E8E\u533B\u5B66, \u56E0\u4E3A\u533B\u751F\u4E0D\u80FD\u8BF4\u670D\u4ED6\u7684\u75C5\u4EBA\u670D\u836F\u548C\u5F00\u5200, \u53EA\u6709\u4FEE\u8F9E\u5B66\u5BB6\u80FD\u591F\u505A\u5230. \u300A\u82CF\u8FBE\u300B\u8F9E\u4E66\u4E2D\u4E5F\u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u662F <code>\u6069\u57F9\u591A\u514B\u52D2</code> \u7684\u5B66\u751F, \u8FD8\u8DDF\u4ED6\u5B66\u8FC7\u533B\u5B66.</p><p>\u516C\u5143\u524D 431 \u5E74\u4F2F\u7F57\u5954\u5C3C\u6492\u6218\u4E89\u7206\u53D1\u540E, <code>\u897F\u897F\u91CC</code> \u548C <code>\u5357\u610F\u5927\u5229</code> \u5730\u533A\u7684\u8BF8\u5E0C\u814A\u57CE\u90A6\u7531\u4E8E\u540C\u6BCD\u90A6\u7684\u5173\u7CFB\u53CA\u5404\u81EA\u7684\u5229\u5BB3\u51B2\u7A81, \u5F7C\u6B64\u95F4\u7684\u5173\u7CFB\u4E5F\u6076\u5316\u4E86. \u516C\u5143\u524D 427 \u5E74 <code>\u6797\u5730\u5C3C</code> \u548C <code>\u53D9\u62C9\u53E4</code> \u53D1\u751F\u4E89\u6267. <code>\u897F\u897F\u91CC</code> \u7684 <code>\u591A\u5229\u4E9A</code> \u4EBA\u57CE\u90A6\u548C <code>\u5357\u610F\u5927\u5229</code> \u7684 <code>\u96F7\u5409\u59C6</code> \u548C <code>\u6797\u5730\u5C3C</code> \u5C5E\u4E8E\u540C\u65CF\u4EBA\u6240\u4EE5\u652F\u6301\u6797\u5730\u5C3C. <code>\u6797\u5730\u5C3C</code> \u7684\u540C\u76DF\u8005\u6D3E\u4E86\u4E00\u4E2A\u4EE3\u8868\u56E2\u5230 <code>\u96C5\u5178</code> \u53BB, \u6839\u636E\u4ED6\u4EEC\u6614\u65E5\u7684\u540C\u76DF\u6761\u7EA6\u548C\u4ED6\u4EEC\u540C\u4E3A <code>\u4F0A\u5965\u5C3C\u4E9A</code> \u4EBA\u7684\u7406\u7531, \u8BF7\u6C42 <code>\u96C5\u5178</code> \u6D3E\u8230\u961F\u63F4\u52A9\u4ED6\u4EEC. \u56E0\u4E3A\u4ED6\u4EEC\u5728\u9646\u5730\u4E0A\u548C\u6D77\u4E0A\u90FD\u88AB <code>\u53D9\u62C9\u53E4</code> \u4EBA\u5C01\u9501\u4E86. \u8FD9\u4E2A\u4F7F\u56E2\u7684\u9996\u5E2D\u4EE3\u8868\u5C31\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code>, \u4ED6\u8BF4\u670D\u4E86 <code>\u96C5\u5178</code> \u4E0E <code>\u6797\u5730\u5C3C</code> \u7ED3\u76DF\u53CD\u5BF9 <code>\u53D9\u62C9\u53E4</code>. <code>\u72C4\u5965\u591A\u7F57</code> \u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u4F5C\u4E3A\u4F7F\u8282\u5728 <code>\u96C5\u5178</code> \u53D1\u8868\u7684\u4F7F\u516C\u6C11\u5927\u4F1A\u4E3A\u4E4B\u9707\u52A8, \u4E0D\u4EC5\u6C11\u4F17\u800C\u4E14\u5B98\u5458\u4E5F\u6B23\u8D4F\u4ED6\u7684\u7406\u667A\u548C\u653F\u6CBB\u624D\u80FD. <code>\u9C8D\u8428\u5C3C\u963F</code> \u8BF4 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u906D\u5230\u7AD9\u5728 <code>\u53D9\u62C9\u53E4</code> \u4E00\u9762\u7684, \u9887\u6709\u58F0\u671B\u7684\u4FEE\u8F9E\u5B66\u5BB6 <code>\u5FD2\u897F\u4E9A\u65AF</code> \u7684\u53CD\u5BF9, \u4F46\u8FD9\u4F4D\u4FEE\u8F9E\u5B66\u6559\u672C\u7684\u4F5C\u8005, \u88AB <code>\u9AD8\u5C14\u5409\u4E9A</code> \u51FB\u8D25\u4E86, <code>\u96C5\u5178\u4EBA</code> \u51B3\u5B9A\u548C <code>\u6797\u5730\u5C3C\u4EBA</code> \u7ED3\u76DF\u5E76\u6D3E\u51FA\u4E00\u4E2A\u8230\u961F\u652F\u6301 <code>\u6797\u5730\u5C3C</code>. \u7531\u6B64\u53EF\u89C1 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u4FEE\u8F9E\u5B66, \u6F14\u8BF4\u548C\u8FA9\u8BBA\u65B9\u9762\u6709\u5F88\u9AD8\u7684\u624D\u80FD, \u5177\u6709\u5F88\u9AD8\u7684\u58F0\u671B; \u4E5F\u53EF\u4EE5\u770B\u51FA\u4FEE\u8F9E, \u6F14\u8BF4\u548C\u8FA9\u8BBA\u6280\u827A\u7684\u53D1\u5C55\u662F\u5E0C\u814A\u793E\u4F1A\u53D1\u5C55\u7684\u9700\u8981. \u5F53\u65F6 <code>\u96C5\u5178</code> \u5404\u57CE\u90A6\u4E5F\u6709\u5BFB\u6C42\u76DF\u53CB\u4EE5\u5B64\u7ACB\u654C\u4EBA\u7684\u95EE\u9898, \u6B63\u50CF\u4E2D\u56FD\u5728\u6625\u79CB\u6218\u56FD\u65F6\u4EE3\u5404\u56FD\u8981\u6D3E\u51FA\u4F7F\u8005 -- \u7EB5\u6A2A\u5BB6\u6E38\u8BF4\u5217\u56FD\u4E00\u6837, \u5728 <code>\u4FEE\u6614\u5E95\u5FB7</code> \u7684\u300A\u4F2F\u7F57\u5954\u5C3C\u6492\u6218\u4E89\u53F2\u300B\u4E2D\u53D9\u8FF0\u8FC7\u51E0\u5341\u4E2A\u8FD9\u6837\u7684\u4F7F\u547D\u6E38\u8BF4\u7684\u4E8B\u4F8B.</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u54EA\u4E9B\u5730\u65B9\u4ECE\u4E8B\u667A\u8005\u6D3B\u52A8? \u5386\u53F2\u4E0A\u6CA1\u6709\u4E13\u95E8\u8BB0\u8F7D. <code>\u4F0A\u7D22\u514B\u62C9\u5E95</code> \u8BF4\u4ED6\u4ECE\u4E0D\u56E0\u5B9A\u5C45\u4F4F\u5728\u67D0\u4E00\u4E2A\u57CE\u90A6, \u4EE5\u514D\u81EA\u5DF1\u7684\u516C\u5F00\u6D3B\u52A8\u548C\u79C1\u4EBA\u6D3B\u52A8\u53D7\u5230\u7EA6\u675F. \u4ED6\u4E5F\u4ECE\u4E0D\u7ED3\u5A5A, \u6CA1\u6709\u59BB\u5B50\u548C\u513F\u5973, \u53EF\u662F\u4E0B\u9762\u5F15\u8BC1\u7684\u53E6\u4E00\u4E2A\u6750\u6599\u53C8\u8BF4\u4ED6\u540C\u59BB\u5B50\u548C\u513F\u5973\u7684\u5173\u7CFB\u7834\u88C2. \u6839\u636E <code>\u4F0A\u7D22\u514B\u62C9\u5E95</code> \u548C <code>\u67CF\u62C9\u56FE</code> \u7684\u6750\u6599, \u4ED6\u7684\u540E\u534A\u751F\u5927\u591A\u662F\u5728 <code>\u8D34\u6492\u5229</code> \u5730\u533A\u7684 <code>\u62C9\u5229\u8428</code> \u57CE\u5EA6\u8FC7\u7684. \u516C\u5143\u524D 480 \u5E74\u5F00\u59CB\u7EDF\u6CBB\u8BE5\u5730\u533A\u7684 <code>\u963F\u7409\u4E9A\u8FBE</code> \u5BB6\u65CF\u5B9E\u884C\u4FDD\u62A4\u548C\u53D1\u5C55\u6587\u5316\u7684\u653F\u7B56, \u7528\u91CD\u91D1\u8058\u8BF7 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u4F20\u6388\u4FEE\u8F9E, \u6F14\u8BF4\u548C\u8FA9\u8BBA\u7684\u6280\u827A. <code>\u67CF\u62C9\u56FE</code> \u300A\u7F8E\u8BFA\u7BC7\u300B\u5F00\u59CB\u65F6 <code>\u7F8E\u8BFA</code> \u95EE <code>\u82CF\u683C\u62C9\u5E95</code>, \u4EBA\u7684\u7F8E\u5FB7\u662F\u600E\u6837\u83B7\u5F97\u7684? \u82CF\u683C\u62C9\u5E95\u56DE\u7B54\u8BF4: &quot;\u54E6, <code>\u7F8E\u8BFA</code>, \u66FE\u7ECF\u6709\u4E00\u4E2A\u65F6\u671F <code>\u5E16\u6492\u5229\u4EBA</code> \u4EE5\u4ED6\u4EEC\u7684\u5BCC\u6709\u548C\u9A91\u672F\u95FB\u540D\u4E8E\u5168\u5E0C\u814A, \u73B0\u5728\u5982\u679C\u6211\u6CA1\u5F04\u9519\u7684\u8BDD, \u4ED6\u4EEC\u8FD8\u4EE5\u667A\u6167\u95FB\u540D\u4E8E\u5E0C\u814A, \u7279\u522B\u662F <code>\u62C9\u5229\u8428</code> \u662F\u4F60\u7684\u670B\u53CB <code>\u963F\u91CC\u65AF\u63D0\u6CE2</code> \u7684\u6545\u4E61. \u8FD9\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u529F\u7EE9, \u5F53\u4ED6\u6765\u5230\u8FD9\u4E2A\u57CE\u90A6\u7684\u65F6\u5019\u5C31\u4EE5\u4ED6\u7684\u667A\u6167\u8D62\u5F97\u4E86\u5927\u591A\u6570 <code>\u963F\u7409\u4E9A\u8FBE</code> \u7684\u9886\u5BFC\u4EBA, \u5176\u4E2D\u5305\u62EC\u4F60\u7684\u670B\u53CB <code>\u963F\u91CC\u65AF\u63D0\u6CE2</code> \u548C\u4E00\u822C <code>\u5E16\u6492\u5229\u4EBA</code> \u7684\u5D07\u656C. \u4ED6\u6559\u7ED9\u4F60\u4EEC\u7528\u6C14\u52BF\u78C5\u7934\u6BEB\u4E0D\u754F\u60E7\u7684\u98CE\u683C\u56DE\u7B54\u95EE\u9898\u7684\u4E60\u60EF, \u8FD9\u662F\u61C2\u5F97\u5982\u4F55\u56DE\u7B54\u95EE\u9898\u7684\u4EBA\u7684\u4E60\u60EF, \u4ED6\u81EA\u5DF1\u5C31\u6562\u4E8E\u56DE\u7B54\u4EFB\u4F55\u4E00\u4E2A\u5E0C\u814A\u4EBA\u5411\u4ED6\u63D0\u51FA\u7684\u4EFB\u4F55\u95EE\u9898&quot;</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u53EF\u80FD\u8FD8\u5728\u4E2D\u5E0C\u814A\u7684 <code>\u73BB\u4FC4\u63D0\u4E9A</code> \u4ECE\u4E8B\u8FC7\u6559\u6388\u6D3B\u52A8, <code>\u73BB\u4FC4\u63D0\u4E9A</code> \u4EBA <code>\u666E\u6D1B\u514B\u8D5B\u52AA</code> \u662F <code>\u8272\u8BFA\u82AC</code> \u7684\u670B\u53CB\u53C8\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u5B66\u751F. <code>\u8272\u8BFA\u82AC</code> \u5728\u300A\u8FDC\u5F81\u8BB0\u300B\u4E2D\u8BF4: &quot;<code>\u73BB\u4FC4\u63D0\u4E9A</code> \u4EBA <code>\u666E\u6D1B\u514B\u8D5B\u52AA</code> \u4ECE\u5C0F\u5C31\u6709\u6210\u4E3A\u680B\u6881\u4E4B\u6750\u7684\u6025\u5207\u613F\u671B, \u56E0\u6B64\u4EE5\u675F\u4FEE\u5E08\u5949\u6797\u5730\u5C3C\u4EBA <code>\u9AD8\u5C14\u5409\u4E9A</code>. \u5728\u4ED6\u95E8\u4E0B\u5B66\u4E60\u4EE5\u540E\u81EA\u8BA4\u4E3A\u5DF2\u5177\u6709\u653F\u6CBB\u624D\u80FD, \u4E0E\u5F53\u5730\u540D\u6D41\u4EA4\u5F80\u5E76\u534F\u540C <code>\u5C45\u9C81\u58EB</code> \u8FDB\u884C\u6B64\u756A\u4E8B\u4E1A ( \u6307\u53C2\u52A0\u5C0F <code>\u5C45\u9C81\u58EB</code> \u7684\u8FDC\u7247\u519B ), \u5E0C\u671B\u501F\u6B64\u673A\u4F1A\u4E00\u4E3E\u6210\u540D, \u6267\u638C\u653F\u6743\u5E76\u79EF\u805A\u8D22\u5BCC.&quot;</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u5230\u8FC7\u96C5\u5178\u51E0\u6B21\u6216\u8005\u5728\u96C5\u5178\u4F4F\u4E86\u591A\u4E45? \u5747\u6CA1\u6709\u786E\u5B9E\u7684\u53F2\u6599. <code>\u67CF\u62C9\u56FE</code> \u5199\u300A\u666E\u7F57\u6CF0\u6208\u62C9\u7BC7\u300B\u63D0\u5230\u51E0\u4E2A\u8457\u540D\u7684\u667A\u8005\u5374\u6CA1\u6709 <code>\u9AD8\u5C14\u5409\u4E9A</code>, \u5927\u6982\u90A3\u65F6\u4ED6\u4E0D\u5728\u96C5\u5178. \u4F46 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u66FE\u5E94\u9080\u5728\u96C5\u5178\u53D1\u8868\u5173\u4E8E\u9635\u4EA1\u5C06\u58EB\u7684\u6F14\u8BF4. <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u8BF4, \u4ED6\u5728\u96C5\u5178\u7684\u5267\u573A\u53D1\u8868\u6F14\u8BF4\u65F6\u9080\u8BF7\u542C\u4F17\u5411\u4ED6\u968F\u610F\u63D0\u95EE, \u83B7\u5F97\u542C\u4F17\u7684\u8D5E\u8D4F. \u6839\u636E <code>\u897F\u585E\u7F57</code> \u53CA\u7F57\u9A6C\u4FEE\u8F9E\u5B66\u5BB6 <code>\u6606\u63D0\u5229\u5B89</code> \u8BB0\u8F7D, \u96C5\u5178\u7684\u5927\u6F14\u8BF4\u5BB6 <code>\u4F0A\u7D22\u514B\u62C9\u5E95</code> \u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u5B66\u751F, \u4ED6\u5C06\u81EA\u5DF1\u7684\u8BB8\u591A\u6F14\u8BF4\u5F52\u529F\u4E8E <code>\u9AD8\u5C14\u5409\u4E9A</code>, \u5E76\u4E3A <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u6536\u8D39\u95EE\u9898\u8FDB\u884C\u8FA9\u62A4. <code>\u666E\u7F57\u5854\u514B</code> \u751A\u81F3\u8BF4, \u5728 <code>\u4F0A\u7D22\u514B\u62C9\u5E95</code> \u7684\u5893\u7891\u4E2D\u523B\u6709 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u4EF0\u89C6\u5929\u4F53\u7684\u96D5\u50CF. <code>\u67CF\u62C9\u56FE</code> \u5728\u300A\u7F8E\u8BFA\u7BC7\u300B\u4E2D\u8BB0\u8F7D <code>\u7F8E\u8BFA</code> \u95EE <code>\u82CF\u683C\u62C9\u5E95</code>: &quot;<code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u96C5\u5178\u7684\u65F6\u5019, \u4F60\u9047\u5230\u8FC7\u4ED6\u5417?&quot; <code>\u82CF\u683C\u62C9\u5E95</code> \u56DE\u7B54: &quot;\u9047\u5230\u8FC7.&quot; \u53EF\u80FD <code>\u82CF\u683C\u62C9\u5E95</code> \u4E5F\u542C\u8FC7 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u6F14\u8BF4. <code>\u739B\u5C14\u51EF\u5362</code> \u548C <code>\u83F2\u6D1B\u514B\u62C9\u56FE</code> \u8BF4: &quot;<code>\u4FEE\u6614\u5E95\u5FB7</code>, <code>\u4F2F\u91CC\u514B\u5229</code> \u53CA\u5176\u60C5\u5987 <code>\u963F\u4E1D\u5E15\u5E0C\u4E9A</code>, <code>\u514B\u91CC\u5E95\u4E9A</code>, <code>\u963F\u5C14\u57FA\u6BD4\u4E9A\u5FB7</code> \u90FD\u4EF0\u6155 <code>\u9AD8\u5C14\u5409\u4E9A</code>; <code>\u67CF\u62C9\u56FE</code> \u548C <code>\u6B27\u91CC\u5E87\u5F97\u65AF</code> \u7684\u670B\u53CB <code>\u963F\u4F3D\u677E</code> \u751A\u81F3\u8FD8\u6479\u62DF <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u7528\u8BED\u548C\u4FEE\u8F9E\u98CE\u683C.&quot;</p><p>\u5927\u7EA6\u662F\u516C\u5143\u524D 420 \u5E74 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u5FB7\u5C14\u6590\u53D1\u8868\u6F14\u8BF4\u8868\u6F14\u4ED6\u7684\u7B54\u8FA9\u6280\u827A, <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u8BF4\u4ED6\u7684\u6F14\u8BF4\u53CA\u8868\u73B0\u5524\u8D77\u4EBA\u4EEC\u7684\u6FC0\u60C5, \u4EBA\u4EEC\u4E3A\u4ED6\u5EFA\u9020\u4E86\u4E00\u5EA7\u91D1\u8D28\u96D5\u50CF. <code>\u897F\u585E\u7F57</code> \u4E5F\u63D0\u5230\u8FD9\u4EF6\u4E8B, \u4E0D\u8FC7\u4ED6\u8BF4\u90A3\u5EA7\u96D5\u50CF\u662F\u9576\u91D1\u7684, \u800C\u4E14\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u81EA\u5DF1\u51FA\u7684\u94B1.</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u8FD8\u5728\u5965\u6797\u5339\u514B\u8D5B\u4F1A\u4E0A\u53D1\u8868\u8FC7\u91CD\u8981\u6F14\u8BF4, <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u548C <code>\u9C8D\u8428\u5C3C\u963F</code> \u90FD\u8BF4\u4ED6\u53D1\u8868\u4E86\u5173\u4E8E\u8981\u6C42\u5E0C\u814A\u5404\u90A6\u534F\u8C03\u7EDF\u4E00\u7684\u6F14\u8BF4. \u867D\u8BF4\u5F53\u65F6\u666E\u7ECF\u6709\u4EBA\u8BBD\u524C\u4ED6, <code>\u666E\u5362\u5854\u514B</code> \u8BF4\u96C5\u5178\u7684\u5267\u8BD7\u4EBA <code>\u7F8E\u5170\u82CF\u65AF</code> \u542C\u4E86\u4ED6\u7684\u6F14\u8BF4\u540E\u8BF4, &quot;<code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u5965\u6797\u5339\u514B\u7684\u6F14\u8BB2\u4E2D\u529D\u544A\u5E0C\u814A\u4EBA\u8981\u771F\u8BDA\u5408\u4F5C, \u4F46\u4ED6\u81EA\u5DF1\u5374\u4E0D\u80FD\u5B9E\u73B0\u540C\u59BB\u5B50\u513F\u5973\u7684\u8BDA\u5FC3\u5408\u4F5C.&quot; \u4F46\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u6F14\u8BF4\u7684\u4E3B\u9898\u662F\u503C\u5F97\u6CE8\u610F\u7684. \u4ECE <code>\u4F2F\u7F57\u5954\u5C3C\u6492\u6218\u4E89</code> \u5F00\u59CB\u76F4\u5230\u9A6C\u5176\u987F\u7EDF\u4E00\u5E0C\u814A\u4EE5\u524D, \u5E0C\u814A\u8BF8\u57CE\u90A6\u4E4B\u95F4\u7684\u6218\u4E89\u548C\u7EB7\u4E89\u8FDE\u7EED\u4E0D\u65AD, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5229\u7528\u8FD9\u4E2A\u6CDB\u5E0C\u814A\u7684\u8D5B\u4F1A\u53D1\u9762\u8FD9\u79CD\u5185\u5BB9\u7684\u6F14\u8BF4\u662F\u5F88\u503C\u5F97\u91CD\u89C6\u7684. \u5BF9\u4E8E\u8FD9\u4E2A\u95EE\u9898\u897F\u65B9\u5B66\u8005\u6709\u4E0D\u540C\u610F\u89C1, \u6211\u4EEC\u4EE5\u4E0B\u8FD8\u8981\u63D0\u5230\u8FD9\u5219\u8D44\u6599. <code>\u9AD8\u5C14\u5409\u4E9A</code> \u5728\u5965\u6797\u6BD4\u514B\u8D5B\u4F1A\u4E0A\u53D1\u8868\u6F14\u8BF4\u80AF\u5B9A\u662F\u6709\u7684, <code>\u9C8D\u8428\u5C3C\u963F</code> \u8BF4, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u6B7B\u540E\u4ED6\u7684\u5904\u7525 <code>\u6B27\u8C1F\u5C14\u666E</code> \u4E3A\u6B64\u7ED9\u4ED6\u5728\u5965\u6797\u6BD4\u4E9A\u5EFA\u7ACB\u4E86\u4E00\u5EA7\u96D5\u8C61.</p><p><code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u8457\u4F5C\u5927\u90E8\u5206\u5DF2\u7ECF\u4F5A\u5931, \u53EA\u6709\u300A\u6D77\u4F26\u9882\u300B\u548C\u300A\u4E3A\u5E15\u62C9\u58A8\u5F97\u8FA9\u62A4\u300B\u4E24\u7BC7\u57FA\u672C\u4E0A\u5B8C\u6574\u5730\u4FDD\u5B58\u4E0B\u6765. \u73B0\u5728\u4FDD\u7559\u7684\u4ED6\u7684\u6B8B\u7BC7\u5728\u667A\u8005\u4E2D\u662F\u6700\u591A\u7684. \u4ED6\u7684\u91CD\u8981\u8457\u4F5C\u300A\u8BBA\u5B58\u5728\u6216\u8BBA\u81EA\u7136\u300B\u5728 <code>\u585E\u514B\u65AF\u90FD.\u6069\u62AB\u91CC\u67EF</code> \u7684\u8457\u4F5C\u4E2D\u4E5F\u5927\u6BB5\u6458\u5F55\u4E86. <code>\u7B2C\u5C14\u65AF</code> \u7F16\u4E3A\u6B8B\u7BC7\u7B2C\u4E09. \u540E\u4EBA\u63D0\u5230\u4ED6\u7684\u6F14\u8BF4\u8F9E\u6709:</p><ol><li>\u300A\u5728\u76AE\u897F\u76AE\u7684\u6F14\u8BF4\u300B, \u662F <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u63D0\u5230\u7684, \u4F46\u5168\u90E8\u4F5A\u5931, \u4EC5\u7559\u7BC7\u540D.</li><li>\u300A\u5728\u5965\u6797\u5339\u4E9A\u7684\u6F14\u8BF4\u300B, <code>\u4E9A\u91CC\u58EB\u591A\u5FB7</code> \u5728\u4FEE\u8F9E\u5B66, <code>\u514B\u83B1\u95E8\u7279</code> \u7684\u300A\u6C47\u7F16\u300B\u7B2C 1 \u5377\u7B2C 51 \u8282\u548C <code>\u666E\u5362\u5854\u514B</code> \u7684\u300A\u529D\u6212\u7BC7\u300B\u7B2C 43 \u8282\u90FD\u63D0\u5230\u8FD9\u7BC7\u6F14\u8BF4, \u5185\u5BB9\u5927\u4F53\u548C <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u8BB2\u7684\u76F8\u540C, \u4E3B\u5F20\u5168\u5E0C\u814A\u5E94\u56E2\u7ED3\u534F\u8C03\u4E00\u81F4\u5BF9\u5916, \u6709\u4EBA\u8BA4\u4E3A\u53D1\u8868\u8FD9\u6B21\u6F14\u8BB2\u662F\u5728\u516C\u5143\u524D 408 \u5E74\u65AF\u5DF4\u8FBE\u4E0E\u96C5\u5178\u8BAE\u548C\u7684\u65F6\u5019, \u53E6\u5904\u6709\u4E9B\u4EBA\u8BA4\u4E3A\u662F\u5728\u79D1\u6797\u65AF\u6218\u4E89\u671F\u95F4, <code>\u9AD8\u5C14\u5409\u4E9A</code> \u53CD\u5BF9 <code>\u65AF\u5DF4\u8FBE</code> \u548C <code>\u6CE2\u65AF</code> \u8054\u76DF\u5E94\u662F\u516C\u5143\u524D 392 \u5E74.</li><li>\u300A\u5728\u96C5\u5178\u7684\u846C\u793C\u4E0A\u7684\u6F14\u8BF4\u300B, \u7531 <code>\u83F2\u6D1B\u7279\u62C9\u56FE</code> \u548C <code>\u666E\u62C9\u52AA\u5F97</code> \u4FDD\u5B58\u4E86\u4E00\u5927\u6BB5.</li><li>\u300A\u8D5E\u57C3\u5229\u65AF\u4EBA\u300B, <code>\u4E9A\u91CC\u58EB\u591A\u5FB7</code> \u5728\u300A\u4FEE\u8F9E\u5B66\u300B\u4E2D\u63D0\u5230\u5176\u4E2D\u4E00\u53E5\u8BDD: &quot;\u5E78\u798F\u7684\u57C3\u5229\u65AF\u57CE\u90A6\u554A.&quot;</li><li>\u300A\u6D77\u4F26\u9882\u300B, \u5927\u90E8\u5206\u4FDD\u5B58\u4E0B\u6765, \u5171\u4E8C\u5341\u4E00\u6BB5.</li><li>\u300A\u4E3A\u5E15\u58A8\u5F97\u8FA9\u62A4\u300B, \u8FD9\u662F\u76F8\u5F53\u5B8C\u6574\u7684, \u5171\u4E09\u5341\u4E03\u6BB5.</li></ol><p>\u540E\u4EBA\u63D0\u5230\u8FC7 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u8457\u4F5C\u8FD8\u6709\u300A\u963F\u5580\u7409\u65AF\u9882\u300B\u548C\u300A\u4FEE\u8F9E\u6280\u5DE7\u300B\u90FD\u5DF2\u4F5A\u5931.</p><p><code>\u7B2C\u5C14\u65AF</code> \u7F16\u7684\u6B8B\u7BC7\u7B2C\u5341\u4E8C -- \u4E09\u5341\u4E00\u4E3B\u8981\u6458\u81EA\u4E9A\u91CC\u58EB\u591A\u5FB7\u7684\u300A\u4FEE\u8F9E\u5B66\u300B\u548C\u67CF\u62C9\u56FE\u7684\u5BF9\u8BDD\u4E2D\u6240\u5F15\u4E2A\u522B\u8F9E\u53E5.</p><p>\u4EE5\u4E0A\u7531<code>\u7B2C\u5C14\u65AF</code> \u7F16\u4E3A <code>\u9AD8\u5C14\u5409\u4E9A</code> \u8457\u4F5C\u6B8B\u7BC7\u4E09\u5341\u4E00\u6761, \u5176\u4E2D\u5305\u62EC\u300A\u6D77\u4F26\u9882\u300B\u548C\u300A\u4E3A\u5E15\u62C9\u58A8\u5F97\u8FA9\u62A4\u300B\u4E24\u7BC7\u5927\u4F53\u5B8C\u6574\u7684\u6587\u7AE0, \u5728 <code>\u5F17\u91CC\u66FC</code> \u7684\u300A\u82CF\u683C\u62C9\u5E95\u4EE5\u524D\u54F2\u5B66\u5BB6\u7684\u8F85\u52A9\u8BFB\u7269\u300B\u4E2D\u5168\u90E8\u8BD1\u6210\u82F1\u6587.</p><p>\u5728 <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u8FD9\u4E9B\u8457\u4F5C\u4E2D, \u300A\u6D77\u4F26\u9882\u300B\u548C\u300A\u4E3A\u5E15\u62C9\u58A8\u5F97\u8FA9\u62A4\u300B\u4E24\u7BC7\u7684\u771F\u4F2A\u5386\u6765\u6709\u4E89\u8BAE. <code>\u7B56\u52D2</code> \u4ECB\u7ECD\u4E86\u5341\u4E5D\u4E16\u7EAA\u5B66\u8005\u4EEC\u7684\u770B\u6CD5, \u5927\u591A\u8BA4\u4E3A\u662F\u4F2A\u4F5C, <code>\u7B2C\u5C14\u65AF</code> \u5728\u7F16\u7E82\u8FD9\u90E8\u5206\u8D44\u6599\u65F6\u4F5C\u4E86\u8BF4\u660E, \u8BA4\u4E3A\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u8457\u4F5C. \u672C\u4E16\u7EAA\u4E0A\u534A\u610F\u5927\u5229\u7814\u7A76\u667A\u8005\u8FD0\u52A8\u7684\u8457\u540D\u5B66\u8005 <code>\u7FC1\u7279\u65AF\u6CF0\u7EB3</code> \u5217\u4E3E\u4E0D\u5C11\u8D44\u6599\u8BA4\u4E3A: &quot;\u5230\u4ECA\u5929\u53EF\u4EE5\u8BF4\u6BD4\u8F83\u4E00\u81F4\u540C\u610F\u4EE5\u4E3A\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u8457\u4F5C\u4E86, \u5C3D\u7BA1\u8FD9\u4E24\u7BC7\u8457\u4F5C\u7684\u98CE\u683C\u4E0D\u540C, \u300A\u4E3A\u5E15\u62C9\u58A8\u5F97\u8FA9\u62A4\u300B\u662F\u8FA9\u62A4\u8BCD, \u800C\u300A\u6D77\u4F26\u9882\u300B\u5374\u63A5\u8FD1\u8BD7\u6B4C\u6587\u4F53.&quot; \u6211\u4EEC\u63A5\u53D7\u8FD9\u4E2A\u610F\u89C1, \u5C06\u8FD9\u4E24\u7BC7\u8BA4\u4E3A\u662F <code>\u9AD8\u5C14\u5409\u4E9A</code> \u7684\u539F\u4F5C.</p>',15),u=[p];function i(l,_){return e(),c("div",null,u)}var a=o(t,[["render",i],["__file","\u9AD8\u5C14\u5409\u4E9A-\u56DB\u5377\u672C.html.vue"]]);export{a as default};
