import{_ as a,o as e,c as p,e as r}from"./app.6bf529a6.js";const h={},d=r('<h1 id="\u9AD8\u5C14\u5409\u4E9A\u7BC7\u8BF4\u4E86\u5565-\u4E8C" tabindex="-1"><a class="header-anchor" href="#\u9AD8\u5C14\u5409\u4E9A\u7BC7\u8BF4\u4E86\u5565-\u4E8C" aria-hidden="true">#</a> \u9AD8\u5C14\u5409\u4E9A\u7BC7\u8BF4\u4E86\u5565 ( \u4E8C )</h1><p>\u672C\u7BC7\u5BFC\u8BFB\u662F\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u7684\u7B2C\u4E8C\u90E8\u5206\u7684\u5BFC\u8BFB.</p><p>\u5728\u4E0A\u7BC7\u5BFC\u8BFB\u8BB2\u4E86\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u7684\u7B2C\u4E00\u90E8\u5206. \u90A3\u4E2A\u90E8\u5206\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u4E3B\u8981\u662F\u82CF\u683C\u62C9\u5E95\u4E0E\u9AD8\u5C14\u5409\u4E9A\u5BF9\u4FEE\u8F9E\u672F\u7684\u8BA8\u8BBA. \u5728\u8BA8\u8BBA\u7684\u6700\u540E, \u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u4E86\u4ED6\u5BF9\u4E8E\u4FEE\u8F9E\u672F\u7684\u89C2\u70B9: \u4FEE\u8F9E\u5B66\u4E0D\u662F\u4E00\u79CD\u6280\u827A, \u800C\u662F\u4E00\u79CD\u5949\u627F\u7684\u7A0B\u5E8F.</p><p>\u672C\u7BC7\u5BFC\u8BFB\u6240\u8BB2\u7684\u300A\u9AD8\u5C14\u5409\u4E9A\u7BC7\u300B\u7B2C\u4E8C\u90E8\u5206, \u82CF\u683C\u62C9\u5E95\u5BF9\u8BDD\u7684\u4EBA\u53D8\u6210\u4E86\u6CE2\u5362\u65AF, \u4ED6\u4EEC\u4ECE\u4FEE\u8F9E\u5B66\u5BB6\u662F\u5426\u5728\u57CE\u90A6\u4E2D\u62E5\u6709\u6700\u5927\u7684\u6743\u5229\u8BF4\u8D77, \u8BA8\u8BBA\u5230\u4F5C\u6076\u4E0E\u53D7\u6076, \u9003\u907F\u60E9\u7F5A\u4E0E\u63A5\u53D7\u60E9\u7F5A\u54EA\u4E2A\u66F4\u574F\u7684\u95EE\u9898.</p><h2 id="\u89C2\u70B9\u7684\u63D0\u51FA" tabindex="-1"><a class="header-anchor" href="#\u89C2\u70B9\u7684\u63D0\u51FA" aria-hidden="true">#</a> \u89C2\u70B9\u7684\u63D0\u51FA</h2><h3 id="\u4FEE\u8F9E\u5B66\u5BB6\u5728\u57CE\u90A6\u4E2D\u7684\u6743\u529B\u662F\u6700\u5C0F\u7684" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u8F9E\u5B66\u5BB6\u5728\u57CE\u90A6\u4E2D\u7684\u6743\u529B\u662F\u6700\u5C0F\u7684" aria-hidden="true">#</a> \u4FEE\u8F9E\u5B66\u5BB6\u5728\u57CE\u90A6\u4E2D\u7684\u6743\u529B\u662F\u6700\u5C0F\u7684</h3><p>\u5728\u82CF\u683C\u62C9\u5E95\u4E0E\u9AD8\u5C14\u5409\u4E9A\u7684\u8BA8\u8BBA\u4E2D, \u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u4FEE\u8F9E\u5B66\u662F\u4E00\u79CD\u5949\u627F\u7684\u7A0B\u5E8F. \u8FD9\u4E00\u70B9\u53D7\u5230\u4E86\u6CE2\u5362\u65AF\u7684\u8D28\u7591, \u56E0\u4E3A\u4ED6\u8BA4\u4E3A\u4FEE\u8F9E\u5B66\u5BB6\u5728\u57CE\u90A6\u91CC\u6700\u6709\u6743\u529B, \u4ED6\u4EEC\u53EF\u4EE5\u50CF\u50ED\u4E3B\u4E00\u6837, \u968F\u610F\u5904\u6B7B\u4ED6\u4EBA, \u5265\u593A\u4ED6\u4EBA\u7684\u8D22\u4EA7, \u8FD8\u80FD\u628A\u4F3C\u4E4E\u6700\u4F18\u79C0\u7684\u4EBA\u9A71\u9010\u51FA\u57CE\u90A6.</p><p>\u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u53CD\u9A73\u610F\u89C1, \u8BA4\u4E3A\u50ED\u4E3B\u4EEC\u5E76\u4E0D\u80FD\u968F\u5FC3\u6240\u6B32, \u800C\u53EA\u80FD\u505A\u4ED6\u4EEC\u770B\u8D77\u6765\u6700\u597D\u7684\u4E8B. \u800C\u6CA1\u6709\u7406\u667A\u7684\u4EBA\u770B\u8D77\u6765\u597D\u7684\u4E8B, \u5E76\u4E0D\u80FD\u79F0\u4E4B\u4E3A\u597D. \u6240\u4EE5, \u5982\u679C\u6211\u4EEC\u8BA4\u4E3A\u6240\u8C13\u4F1F\u5927\u7684\u6743\u529B\u6307\u7684\u662F\u505A\u5BF9\u81EA\u5DF1\u597D\u7684\u4E8B, \u90A3\u4E48\u50ED\u4E3B\u6240\u505A\u7684\u4E8B, \u5E76\u4E0D\u80FD\u79F0\u4E3A\u4F1F\u5927\u6743\u529B.</p><p>\u6CE2\u5362\u65AF\u8868\u793A\u4E0D\u80FD\u7406\u89E3\u82CF\u683C\u62C9\u5E95\u6240\u8BF4.</p><p>\u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u8BF4, \u4EBA\u7684\u884C\u4E3A\u662F\u4E3A\u4E86\u8FFD\u6C42\u76EE\u7684. \u5982: \u4EBA\u5403\u836F\u662F\u4E3A\u4E86\u5065\u5EB7, \u822A\u6D77\u662F\u4E3A\u4E86\u6323\u94B1. \u4E16\u4E0A\u7684\u4E8B\u5206\u4E3A\u4E09\u7C7B: \u597D\u7684\u4E8B, \u5982\u667A\u6167 / \u5065\u5EB7 / \u8D22\u5BCC; \u574F\u7684\u4E8B, \u5982\u4E0A\u9762\u6240\u8BF4\u8BF8\u4E8B\u7684\u5BF9\u7ACB\u9762; \u4E0D\u597D\u4E0D\u597D\u7684\u4E8B, \u5982\u5750 / \u8D70 / \u8DD1 / \u822A\u884C \u6216 \u77F3\u5934 / \u6728\u5934. \u4EBA\u7684\u76EE\u7684\u53EA\u80FD\u662F\u597D\u7684, \u800C\u4E0D\u4F1A\u662F\u574F\u7684\u6216\u4E0D\u597D\u4E0D\u574F\u7684.</p><p>\u5F53\u4EBA\u4EEC\u53BB\u6740\u4EBA, \u653E\u9010, \u5265\u593A\u4ED6\u4EBA\u8D22\u4EA7\u65F6, \u662F\u56E0\u4E3A\u4ED6\u4EEC\u8BA4\u4E3A\u8FD9\u6837\u505A\u6BD4\u4E0D\u8FD9\u6837\u505A\u597D. \u4F46\u50ED\u4E3B\u4EEC\u81EA\u8BA4\u4E3A\u8FD9\u505A\u8FD9\u4E9B\u5BF9\u81EA\u5DF1\u6709\u5229, \u4F46\u5B9E\u9645\u8FD9\u4E9B\u884C\u4E3A\u662F\u5BF9\u81EA\u5DF1\u6709\u5BB3\u7684.</p><p>\u6CE2\u5362\u65AF\u542C\u5230\u6B64\u5904, \u8D28\u95EE\u82CF\u683C\u62C9\u5E95, \u4ED6\u96BE\u9053\u4E0D\u7FA1\u6155\u50ED\u4E3B\u4E48? \u82CF\u683C\u62C9\u5E95\u8BF4, \u50ED\u4E3B\u6240\u884C\u7684\u90A3\u4E9B\u4E8B, \u662F\u4E0D\u53EF\u907F\u514D\u7684, \u4E0D\u5E78\u7684, \u503C\u5F97\u9057\u61BE\u800C\u975E\u503C\u5F97\u7FA1\u6155: \u56E0\u4E3A\u4F5C\u6076\u662F\u6700\u4E0D\u5E78\u7684\u4E8B.</p><h3 id="\u505A\u6076\u8981\u6BD4\u53D7\u6076\u66F4\u53EF\u60B2-\u66F4\u4E0D\u5E78" tabindex="-1"><a class="header-anchor" href="#\u505A\u6076\u8981\u6BD4\u53D7\u6076\u66F4\u53EF\u60B2-\u66F4\u4E0D\u5E78" aria-hidden="true">#</a> \u505A\u6076\u8981\u6BD4\u53D7\u6076\u66F4\u53EF\u60B2, \u66F4\u4E0D\u5E78</h3><p>\u6CE2\u5362\u65AF\u4E0D\u8BA4\u4E3A\u4F5C\u6076\u662F\u6700\u4E0D\u5E78\u7684\u4E8B, \u4ED6\u8BA4\u4E3A\u53D7\u6076\u624D\u662F\u6700\u4E0D\u5E78\u7684. \u4ED6\u95EE\u82CF\u683C\u62C9\u5E95, \u96BE\u9053\u4F60\u66F4\u613F\u610F\u53D7\u6076 ( \u63A5\u53D7\u522B\u4EBA\u5BF9\u4ED6\u4F5C\u6076 ), \u4E5F\u4E0D\u613F\u4F5C\u6076\u4E48? \u82CF\u683C\u62C9\u5E95\u8BF4\u53D7\u6076\u548C\u4F5C\u6076\u90FD\u4E0D\u503C\u5F97\u9009, \u4F46\u5982\u679C\u975E\u5F97\u9009\u4E00\u4E2A, \u90A3\u4ED6\u5B81\u53EF\u53D7\u6076, \u4E5F\u4E0D\u613F\u610F\u505A\u6076.</p><p>\u6CE2\u5362\u65AF\u7EE7\u7EED\u8D28\u7591, \u96BE\u9053\u82CF\u683C\u62C9\u5E95\u4E0D\u60F3\u505A\u4E00\u4E2A\u53EF\u4EE5\u968F\u610F\u6740\u4EBA, \u653E\u9010, \u5728\u4E00\u5207\u4E8B\u60C5\u4E0A\u968F\u5FC3\u6240\u6B32\u7684\u50ED\u4E3B\u4E48? \u82CF\u683C\u62C9\u5E95\u4E3E\u4E86\u4E00\u4E2A\u4F8B\u5B50, \u4E00\u4E2A\u6301\u5315\u9996\u4E0A\u8857\u7684\u4EBA, \u4E5F\u53EF\u505A\u4E0A\u8FF0\u968F\u5FC3\u6240\u6B32\u4E4B\u4E8B, \u4F46\u8FD9\u4E0D\u89C1\u5F97\u662F\u4EC0\u4E48\u4F1F\u5927\u6743\u529B. \u6CE2\u5362\u65AF\u8BF4, \u8FD9\u6837\u7684\u6743\u529B\u7684\u9519\u8BEF\u5728\u4E8E, \u5982\u679C\u884C\u4F7F\u4E86, \u5C31\u8981\u53D7\u5230\u60E9\u7F5A. \u4E8E\u662F\u82CF\u683C\u62C9\u5E95\u603B\u7ED3\u8BF4: \u4EBA\u505A\u4E8B\u5E26\u6765\u5229\u76CA, \u662F\u4EF6\u597D\u4E8B, \u5219\u662F\u4F1F\u5927\u6743\u529B, \u53CD\u4E4B, \u5982\u679C\u6240\u884C\u4E4B\u4E8B\u662F\u574F\u4E8B, \u5219\u5305\u542B\u6743\u529B\u4E5F\u5C0F. \u505A\u6B63\u4E49\u7684\u4E8B\u597D, \u800C\u505A\u9519\u8BEF\u7684\u4E8B\u5219\u574F.</p><p>\u6CE2\u5362\u65AF\u8BF4\u82CF\u683C\u62C9\u5E95\u7684\u89C2\u70B9\u867D\u7136\u4E0D\u597D\u9A73\u65A5, \u4F46\u4E00\u4F4D\u513F\u7AE5\u4E5F\u80FD\u8BC1\u660E\u8FD9\u8BF4\u6CD5\u662F\u9519\u7684. \u56E0\u4E3A\u5C31\u6709\u5F53\u65F6\u7684\u4F8B\u5B50, \u53EF\u4EE5\u7528\u6765\u8BC1\u660E\u82CF\u683C\u62C9\u5E95\u7684\u9519\u8BEF. \u63A5\u7740\u4ED6\u4E3E\u4E86\u9A6C\u5176\u987F\u50ED\u4E3B, \u963F\u51EF\u52B3\u65AF\u7684\u4F8B\u5B50. \u963F\u51EF\u52B3\u65AF\u4F7F\u7528\u90AA\u6076\u7684\u624B\u6BB5\u5F97\u53D6\u4E86\u50ED\u4E3B\u7684\u6743\u529B. \u6CE2\u5362\u65AF\u65AD\u5B9A\u963F\u51EF\u52B3\u65AF\u662F\u5E78\u798F\u7684. \u7531\u6B64\u4ED6\u65AD\u5B9A, \u4F5C\u6076\u8005\u662F\u5E78\u798F\u7684, \u4F46\u5982\u679C\u4ED6\u9047\u5230\u6B63\u4E49\u548C\u60E9\u7F5A, \u90A3\u4ED6\u4F1A\u4E0D\u5E78\u798F.</p><p>\u82CF\u683C\u62C9\u5E95\u542C\u5B8C\u6CE2\u5362\u65AF\u7684\u4F8B\u5B50, \u63D0\u51FA\u6F14\u8BF4\u662F\u4E0D\u80FD\u9A73\u5012\u8BBA\u8BC1\u7684. \u56E0\u4E3A\u4E3E\u4F8B\u5B50, \u5927\u53EF\u4E3E\u51FA\u865A\u5047\u7684\u4F8B\u5B50, \u5E76\u4F1A\u6709\u865A\u5047\u7684\u8BC1\u4EBA\u6765\u4E3A\u865A\u5047\u7684\u4F8B\u5B50\u4F5C\u8BC1. \u82CF\u683C\u62C9\u5E95\u8BF4, \u73B0\u5728\u7684\u8BA8\u8BBA\u51B3\u975E\u5FAE\u4E0D\u8DB3\u9053, \u800C\u662F\u4E00\u4E2A\u5173\u4E8E\u77E5\u8BC6\u6700\u9AD8\u5C1A, \u65E0\u77E5\u6700\u53EF\u803B\u7684\u95EE\u9898, \u8FD9\u5C31\u662F\u77E5\u8BC6\u7684\u4EBA\u8FD8\u662F\u65E0\u77E5\u7684\u4EBA\u662F\u5E78\u798F\u7684\u8FD9\u4E2A\u95EE\u9898\u7684\u603B\u548C\u4E0E\u672C\u8D28.</p><h3 id="\u82CF\u683C\u62C9\u5E95\u7684\u89C2\u70B9-\u4F5C\u6076\u5DF2\u7136\u4E0D\u5E78-\u4F46\u4ED6\u5F97\u8131\u60E9\u7F5A\u5219\u66F4\u4E3A\u4E0D\u5E78" tabindex="-1"><a class="header-anchor" href="#\u82CF\u683C\u62C9\u5E95\u7684\u89C2\u70B9-\u4F5C\u6076\u5DF2\u7136\u4E0D\u5E78-\u4F46\u4ED6\u5F97\u8131\u60E9\u7F5A\u5219\u66F4\u4E3A\u4E0D\u5E78" aria-hidden="true">#</a> \u82CF\u683C\u62C9\u5E95\u7684\u89C2\u70B9: \u4F5C\u6076\u5DF2\u7136\u4E0D\u5E78, \u4F46\u4ED6\u5F97\u8131\u60E9\u7F5A\u5219\u66F4\u4E3A\u4E0D\u5E78</h3><p>\u82CF\u683C\u62C9\u5E95\u63D0\u51FA\u4ED6\u7684\u89C2\u70B9: \u4EE5\u90AA\u6076\u7684\u624B\u6BB5\u6210\u4E3A\u50ED\u4E3B\u662F\u4E0D\u5E78\u7684, \u56E0\u6B64\u53D7\u5230\u60E9\u7F5A\u4E5F\u662F\u4E0D\u5E78\u7684, \u800C\u4ED6\u9003\u8131\u60E9\u7F5A\u5219\u662F\u66F4\u4E0D\u5E78\u7684.</p><p>\u6CE2\u5362\u65AF\u542C\u5230\u8FD9\u4E9B, \u5F00\u59CB\u5632\u7B11\u82CF\u683C\u62C9\u5E95. \u82CF\u683C\u62C9\u5E95\u8BF4, \u4ED6\u53BB\u5E74\u5728\u516C\u6C11\u5927\u4F1A\u4E0A\u7EC4\u7EC7\u6295\u7968, \u4F46\u56E0\u4E3A\u4ED6\u4E0D\u77E5\u9053\u8BE5\u600E\u4E48\u529E\u800C\u5F15\u6765\u5632\u7B11. \u4F46\u4E9B\u65F6\u4E0D\u662F\u8981\u8FDB\u884C\u6295\u7968, \u800C\u662F\u8981\u8FDB\u884C\u8BBA\u8BC1, \u56E0\u6B64\u6B64\u65F6\u4E0D\u9700\u8981\u4F7F\u591A\u6570\u4EBA\u540C\u610F, \u800C\u53EA\u8981\u4F7F\u4E00\u4EBA\u540C\u610F. \u6240\u4EE5\u4ED6\u8FD8\u662F\u8BF7\u6CE2\u5362\u65AF\u901A\u8FC7\u56DE\u7B54\u548C\u63D0\u95EE\u6765\u8FDB\u884C\u8003\u5BDF.</p><h2 id="\u89C2\u70B9\u7684\u8BBA\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u89C2\u70B9\u7684\u8BBA\u8BC1" aria-hidden="true">#</a> \u89C2\u70B9\u7684\u8BBA\u8BC1</h2><h3 id="\u4F5C\u6076\u597D\u4E8E\u53D7\u6076" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u6076\u597D\u4E8E\u53D7\u6076" aria-hidden="true">#</a> \u4F5C\u6076\u597D\u4E8E\u53D7\u6076</h3><p>\u82CF\u683C\u62C9\u5E95\u901A\u8FC7\u8BD8\u95EE, \u4F7F\u6CE2\u5362\u65AF\u627F\u8BA4, \u53D7\u6076\u574F\u800C\u4F5C\u6076\u53EF\u803B. \u8FD9\u663E\u7136\u662F\u4E0D\u628A\u5584\u548C\u7F8E\u5F53\u4F5C\u4E00\u56DE\u4E8B, \u4E5F\u4E0D\u628A\u6076\u4E0E\u53EF\u803B\u5F53\u4F5C\u4E00\u56DE\u4E8B.</p><p>\u7F8E\u4E4B\u4E8B\u7269\u4E4B\u6240\u4EE5\u7F8E, \u6216\u662F\u56E0\u4E3A\u5B83\u4EEC\u4EA7\u751F\u5FEB\u4E50, \u6216\u662F\u56E0\u4E3A\u5B83\u4EEC\u6709\u7528, \u6216\u662F\u56E0\u4E3A\u5B83\u4EEC\u65E2\u4EA7\u751F\u5FEB\u4E50, \u53C8\u6709\u7528\u5904. \u5982: \u58F0\u97F3\u548C\u97F3\u4E50, \u6CD5\u5F8B\u548C\u98CE\u4FD7, \u5404\u7070\u5F62\u5F0F\u7684\u77E5\u8BC6\u7684\u7F8E, \u83AB\u4E0D\u59CB\u6B64. \u56E0\u6B64\u4E8B\u7269\u7F8E\u7684\u6BD4\u8F83, \u5C31\u662F\u5728\u6BD4\u8F83\u5FEB\u4E50\u548C\u6709\u7528.</p><p>\u540C\u6837\u4E5F\u53EF\u4EE5\u7528\u75DB\u82E6\u548C\u6076\u6765\u5B9A\u4E49\u4E11. \u5F53\u4E24\u4E2A\u53EF\u803B\u7684\u4E8B\u7269\u5728\u5351\u52A3\u65B9\u9762\u4E00\u6837\u8D85\u8FC7\u53E6\u4E00\u6837, \u90A3\u4E48\u8FD9\u79CD\u8D85\u8FC7\u5E94\u5F53\u5F52\u4E8E\u75DB\u82E6\u6216\u90AA\u6076\u65B9\u9762. \u6211\u4EEC\u6BD4\u8F83\u4F5C\u6076\u548C\u53D7\u6076, \u4F5C\u6076\u4E0D\u6BD4\u53D7\u6076\u75DB\u82E6, ( \u5B83\u53C8\u6BD4\u53D7\u6076\u53EF\u803B ), \u90A3\u5B83\u8D85\u8FC7\u53D7\u6076\u7684\u65B9\u9762\u5C31\u4E00\u5B9A\u662F\u90AA\u6076\u65B9\u9762\u4E86. \u6240\u4EE5\u4F5C\u6076\u4E00\u5B9A\u662F\u6BD4\u53D7\u6076\u66F4\u90AA\u6076\u7684\u4E8B\u60C5.</p><p>\u4EFB\u4F55\u4EBA\u90FD\u4E0D\u4F1A\u9009\u62E9\u66F4\u52A0\u90AA\u6076\u548C\u66F4\u52A0\u53EF\u803B\u7684\u4E8B\u60C5, \u6240\u4EE5\u4EFB\u4F55\u4EBA\u4E5F\u4E0D\u4F1A\u9009\u62E9\u4F5C\u6076\u800C\u4E0D\u9009\u62E9\u53D7\u6076.</p><h3 id="\u63A5\u53D7\u60E9\u7F5A\u597D\u4E0E\u9003\u907F\u60E9\u7F5A" tabindex="-1"><a class="header-anchor" href="#\u63A5\u53D7\u60E9\u7F5A\u597D\u4E0E\u9003\u907F\u60E9\u7F5A" aria-hidden="true">#</a> \u63A5\u53D7\u60E9\u7F5A\u597D\u4E0E\u9003\u907F\u60E9\u7F5A</h3><p>\u9996\u5148, \u82CF\u683C\u62C9\u5E95\u8BA9\u6CE2\u5362\u5176\u627F\u8BA4, \u6B63\u4E49\u7684\u4E8B\u5728\u6B63\u4E49\u7684\u8303\u56F4\u5185\u90FD\u662F\u597D\u7684.</p><p>\u63A5\u7740\u4ED6\u8BA8\u8BBA\u4E86\u884C\u4E3A\u548C\u627F\u53D7\u8005\u7684\u5173\u7CFB. \u884C\u4E3A\u53D1\u751F\u65F6, \u5FC5\u6709\u4E00\u4E2A\u627F\u53D7\u8005. \u800C\u884C\u4E3A\u7684\u6027\u8D28\u4E0E\u627F\u53D7\u8005\u7684\u6027\u8D28\u662F\u76F8\u540C\u7684. \u5982\u4E00\u4E2A\u4EBA\u6253, \u5FC5\u6709\u88AB\u6253\u7684, \u4E14\u5B9E\u65BD\u6253\u7684\u4EBA, \u5982\u4F55\u5B9E\u65BD\u6253\u7684\u52A8\u4F5C ( \u72E0\u6216\u5FEB ), \u5219\u88AB\u6253\u7684\u4E00\u5B9A\u627F\u53D7\u540C\u6837\u7684\u6027\u8D28 ( \u72E0\u6216\u5FEB ). \u540C\u6837\u7684\u4F8B\u5B50\u8FD8\u6709\u70E7\u4E0E\u88AB\u70E7, \u5207\u4E0E\u88AB\u5207.</p><p>\u540C\u6837, \u5728\u60E9\u7F5A\u7684\u4F8B\u5B50\u91CC, \u60E9\u7F5A\u8005\u662F\u65BD, \u88AB\u7F5A\u8005\u662F\u53D7. \u60E9\u7F5A\u8005\u7684\u884C\u4E3A\u516C\u53F8, \u5219\u53D7\u7F5A\u8005\u627F\u62C5\u7684\u4E5F\u662F\u516C\u6B63. \u800C\u516C\u6B63\u7684\u4E8B\u7269\u662F\u597D\u7684\u548C\u5149\u8363\u7684, \u5149\u8363\u7684\u597D\u662F\u597D\u7684, \u4EE4\u4EBA\u6109\u60A6\u7684, \u6216\u8005\u6709\u5229\u7684. \u6240\u4EE5\u516C\u6B63\u7684\u53D7\u5230\u60E9\u7F5A\u7684\u4EBA\u662F\u5728\u627F\u53D7\u597D\u4E1C\u897F. \u800C\u8FD9\u79CD\u597D\u5904, \u6B63\u662F\u4F7F\u4EBA\u7684\u7075\u9B42\u6446\u8131\u90AA\u6076\u7684\u597D\u5904.</p><p>\u5728\u4E16\u95F4\u7684\u4E0D\u540C\u8303\u56F4\u5185\u5404\u6709\u4E0D\u540C\u7684\u6076: \u5728\u7269\u8D28\u8D22\u5BCC\u8303\u56F4\u5185, \u8D2B\u7A77\u662F\u6076; \u5728\u8EAB\u4F53\u7684\u72B6\u6001\u4E0A, \u865A\u5F31, \u60A3\u75C5, \u4E11\u964B\u662F\u6076; \u5728\u7075\u9B42\u7684\u72B6\u6001\u4E0A, \u4E0D\u4E49, \u65E0\u77E5, \u80C6\u602F\u662F\u6076.</p><p>\u6700\u53EF\u803B\u7684\u4E8B\u7269\u662F\u6700\u574F\u7684, \u800C\u4E0D\u4E49\u548C\u7075\u9B42\u7684\u6076\u662F\u6700\u53EF\u803B\u7684. \u4E0D\u6B63\u4E49, \u4E0D\u8282\u5236, \u80C6\u602F, \u65E0\u77E5\u5E76\u4E0D\u6BD4\u8D2B\u7A77\u548C\u75BE\u75C5\u66F4\u75DB\u82E6, \u56E0\u6B64\u4ED6\u7684 <strong>\u6700\u53EF\u803B</strong> \u5FC5\u5B9A\u5F52\u4E8E\u4F24\u5BB3\u548C\u4E00\u79CD\u76F4\u63A5\u7684 ( \u4E0D\u663E\u793A\u4E3A\u75DB\u82E6\u7684 ) \u66F4\u5927\u7684\u6076. \u4F46\u5728\u5F15\u8D77\u7684\u4F24\u5BB3\u8D85\u8FC7\u5176\u4ED6\u6076\u7684\u60C5\u51B5\u4E0B, \u5B83\u5728\u4E00\u5207\u53EF\u725F\u7684\u6076\u662F\u5FC5\u5B9A\u662F\u6700\u5927\u7684\u6076.</p><p>\u6323\u94B1\u53EF\u4EE5\u4F7F\u6211\u4EEC\u6446\u8131\u8D2B\u7A77; \u533B\u5B66\u4F7F\u6211\u4EEC\u6446\u8131\u75BE\u75C5, \u6240\u4EE5\u6211\u4EEC\u8EAB\u4F53\u751F\u75C5\u4E86\u8981\u770B\u533B\u751F. \u90A3\u4F7F\u6211\u4EEC\u6446\u8131\u4E0D\u4E49\u548C\u4E0D\u8282\u5236\u8981\u9760\u4EC0\u4E48\u5462?</p><p>\u82CF\u683C\u62C9\u5E95\u95EE\u6CE2\u5362\u65AF, \u6B63\u5982\u8EAB\u4F53\u751F\u75C5\u8981\u53BB\u89C1\u533B\u751F, \u90A3\u4E48\u7075\u9B42\u4E0D\u4E49\u4E86\u6211\u4EEC\u8981\u53BB\u89C1\u8C01\u5462? \u90A3\u5F53\u7136\u662F\u89C1\u6CD5\u5B98. \u533B\u751F\u65BD\u884C\u533B\u672F, \u6CD5\u5B98\u65BD\u884C\u60E9\u7F5A. \u56E0\u6B64\u53EF\u89C1\u6B63\u4E49\u6211\u4EEC\u6446\u8131\u4E0D\u8282\u5236\u548C\u4E0D\u4E49.</p><p>\u6323\u94B1, \u533B\u5B66, \u6B63\u4E49\u4E09\u8005\u95F4\u6B63\u4E49\u6700\u597D, \u56E0\u6B64\u5B83\u4EEC\u4EA7\u751F\u6700\u5927\u7684\u5FEB\u4E50, \u6700\u5927\u7684\u597D\u5904, \u6216\u8005\u540C\u65F6\u4EA7\u751F\u4E24\u8005. \u800C\u6B63\u5982\u6CBB\u75C5\u5E76\u4E0D\u4EA7\u751F\u5FEB\u4E50\u4F46\u6709\u76CA\u4E00\u6837, ( \u60E9\u7F5A\u4E0D\u5FEB\u4E50, \u4F46\u4EA7\u751F\u597D\u5904 ). \u4E0D\u751F\u75C5\u8981\u597D\u8FC7\u5F97\u75C5\u540E\u88AB\u6CBB\u597D, \u672A\u67D3\u6076\u4E5F\u6BD4\u6446\u8131\u6076\u8981\u5E78\u798F. \u4F46\u524D\u8FF0\u50ED\u4E3B\u5DF2\u7136\u72AF\u4E0B\u6781\u5927\u7684\u6076\u884C, \u800C\u53C8\u52AA\u548C\u9003\u907F\u544A\u8BEB, \u77EB\u6B63\u6216\u60E9\u7F5A, \u5C31\u5982\u90A3\u4E9B\u56E0\u4E3A\u5BB3\u6015\u6CBB\u7597\u7684\u75DB\u82E6\u800C\u8BB3\u75BE\u5FCC\u533B\u7684\u5C0F\u5B69.</p><h2 id="\u5C0F\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a> \u5C0F\u7ED3</h2><p>\u6700\u540E\u82CF\u683C\u62C9\u5E95\u5BF9\u8BBA\u8BC1\u4F5C\u4E86\u5C0F\u7ED3: \u4E0D\u4E49\u548C\u4F5C\u6076\u662F\u6700\u5927\u7684\u6076, \u60E9\u7F5A\u4F7F\u6211\u4EEC\u6446\u8131\u8FD9\u79CD\u6076, \u9003\u907F\u60E9\u7F5A\u5219\u4F7F\u7F6A\u6076\u4FDD\u7559. \u56E0\u6B64\u9003\u907F\u60E9\u7F5A\u662F\u6700\u5927\u7684\u6076, \u5176\u6B21\u7684\u6076\u5219\u662F\u6076\u884C\u672C\u8EAB</p><p>\u5982\u679C\u4E00\u4E2A\u4EBA\u901A\u8FC7\u4FEE\u8F9E\u672F\u4E3A\u81EA\u5DF1\u7684\u7F6A\u6076\u8FA9\u62A4, \u4E3A\u81EA\u5DF1\u7684\u7236\u6BCD, \u670B\u53CB, \u5B50\u5973, \u56FD\u5BB6\u6240\u72AF\u7684\u7F6A\u884C\u8FA9\u62A4, ( \u5C31\u662F\u4FDD\u7559\u7F6A\u6076, ) \u5219\u4FEE\u8F9E\u5B66\u6CA1\u6709\u4EFB\u4F55\u7528\u5904. \u4F46\u5982\u679C\u6211\u4EEC\u901A\u8FC7\u4FEE\u8F9E\u672F\u8D23\u5907\u81EA\u5DF1, \u8D23\u5907\u4F5C\u6076\u7684\u4EB2\u5C5E\u548C\u670B\u53CB, \u5C06\u7F6A\u6076\u516C\u4E4B\u4E8E\u4F17, \u4EE5\u4FBF\u7F6A\u72AF\u53D7\u60E9\u7F5A, \u7075\u9B42\u83B7\u5065\u5EB7, \u90A3\u4E48\u4FEE\u8F9E\u5B66\u5219\u662F\u6709\u7528\u7684. \u56E0\u6B64\u4FEE\u8F9E\u5B66\u6700\u5927\u7684\u7528\u5904\u5728\u4E8E\u4F7F\u6211\u4EEC\u4ECE\u6700\u5927\u7684\u6076\u662F\u6446\u8131\u51FA\u6765.</p><p>\u5982\u679C\u6211\u4EEC\u7684\u654C\u4EBA\u505A\u4E86\u6076, \u6211\u4EEC\u5E94\u5F53\u5C3D\u91CF\u4E0D\u4F7F\u4ED6\u53D7\u60E9\u7F5A, \u597D\u4F7F\u7F6A\u6076\u4FDD\u7559, \u4F7F\u4ED6\u4EEC\u53D7\u7F6A\u6076\u7684\u4F24\u5BB3. \u6240\u4EE5\u4FEE\u8F9E\u5B66\u6CA1\u6709\u4EC0\u4E48\u7528, \u7531\u5176\u662F\u5BF9\u90A3\u4E9B\u4E0D\u60F3\u505A\u6076\u7684\u4EBA\u6765\u8BF4\u6CA1\u6709\u4EC0\u4E48\u7528.</p>',39),i=[d];function n(t,c){return e(),p("div",null,i)}var o=a(h,[["render",n],["__file","\u9AD8\u5C14\u5409\u4E9A2.html.vue"]]);export{o as default};
